import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as t,f as e}from"./app.eee000b7.js";const r={},c=e('<h1 id="\u9EC4\u5DFE\u8D77\u4E49" tabindex="-1"><a class="header-anchor" href="#\u9EC4\u5DFE\u8D77\u4E49" aria-hidden="true">#</a> \u9EC4\u5DFE\u8D77\u4E49</h1><blockquote><p>\uFF08\u5F20\uFF09\u89D2\u7B49\u77E5\u4E8B\u5DF2\u9732\uFF0C\u6668\u591C\u9A70\u6555\u8BF8\u65B9\uFF0C\u4E00\u65F6\u4FF1\u8D77\uFF0C\u7686\u8457\u9EC4\u5DFE\u4EE5\u4E3A\u6807\u5E1C\uFF0C\u6545\u65F6\u4EBA\u8C13\u4E4B\u201C\u9EC4\u5DFE\u8D3C\u201D\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274 \xB7 \u5377\u4E94\u5341\u516B \xB7 \u6C49\u7EAA\u4E94\u5341\u300B</strong></p><p>\u5F20\u89D2\u7684\u540D\u5B57\uFF0C\u5728\u5386\u53F2\u4E0A\u9996\u73B0\u4E8E\u5149\u548C\u516D\u5E74\uFF08\u516C\u5143183\u5E74\uFF09\uFF0C\u4F46\u5176\u5B9E\u5728\u6B64\u4E4B\u524D\uFF0C\u4ED6\u5DF2\u7ECF\u9ED8\u9ED8\u7684\u8015\u8018\u4E86\u5341\u4F59\u5E74\u3002</p><p>\u5341\u51E0\u5E74\u95F4\uFF0C\u5F20\u89D2\u4EE5\u6CBB\u75C5\u4E3A\u540D\uFF0C\u884C\u4F20\u6559\u4E4B\u5B9E\u3002\u636E\u8BF4\u4ED6\u4FE1\u5949\u9EC4\u8001\uFF0C\u80FD\u4EE5\u7B26\u6C34\u5E2E\u4EBA\u6CBB\u75C5\uFF0C\u800C\u4E14\u6548\u679C\u8FD8\u4E0D\u9519\uFF0C\u56E0\u6B64\u5341\u51E0\u5E74\u95F4\uFF0C\u4FE1\u5F92\u8FBE\u5230\u51E0\u5341\u4E07\u4E4B\u4F17\u3002</p><blockquote><p>\u5F20\u89D2\u540E\u6765\u540D\u6C14\u4E4B\u5927\uFF0C\u5DF2\u7ECF\u5230\u4E86\u5F88\u591A\u8EAB\u60A3\u4E0D\u6CBB\u4E4B\u75C7\u7684\u4EBA\u53D8\u5356\u5BB6\u4EA7\uFF0C\u5343\u91CC\u8FE2\u8FE2\u53BB\u627E\u4ED6\u770B\u75C5\u7684\u5730\u6B65\u3002</p></blockquote><blockquote><p>\u4F46\u90A3\u4E2A\u65F6\u5019\u4E00\u6CA1\u6709\u7F51\u7EDC\u4E8C\u6CA1\u5E7F\u64AD\uFF0C\u8981\u627E\u5230\u5F20\u89D2\u5E76\u4E0D\u662F\u4E00\u4EF6\u6613\u4E8B\uFF0C\u5F88\u591A\u4EBA\u6700\u7EC8\u90FD\u6B7B\u5728\u4E86\u8DEF\u4E0A\u3002\u636E\u53F2\u6599\u8BB0\u8F7D\uFF0C\u5355\u662F\u8FD9\u6837\u7684\u6B7B\u8005\u5C31\u6709\u4E0A\u4E07\u4EBA\u3002</p></blockquote><p>\u8FD9\u8BA9\u5730\u65B9\u5B98\u5458\u89C9\u5F97\uFF0C\u8FD9\u4E2A\u5F20\u89D2\u7B80\u76F4\u5C31\u662F\u5F53\u4E16\u795E\u533B\u554A\uFF01\uFF08\u8BF7\u6CE8\u610F\uFF0C\u90A3\u65F6\u534E\u4F57\u867D\u7136\u5DF2\u7ECF\u51FA\u751F\uFF0C\u4F46\u662F\u8FD8\u6CA1\u6709\u51FA\u540D\uFF0C\u6240\u4EE5\u8FD9\u91CC\u4E0D\u80FD\u8BB2\u201C\u534E\u4F57\u518D\u751F\u201D\u8FD9\u79CD\u8BDD~\uFF09\u800C\u4E14\uFF0C\u5B98\u5458\u4EEC\u89C9\u5F97\u5F20\u89D2\u7684\u6240\u4F5C\u6240\u4E3A\u6709\u52A9\u4E8E\u529D\u5584\u6559\u5316\uFF0C\u56E0\u6B64\uFF0C\u5168\u529B\u652F\u6301\u4ED6\u7684\u533B\uFF08zong\uFF09\u5B66\uFF08jiao\uFF09\u4E8B\u4E1A\u3002</p><p>\u671D\u91CC\u96BE\u9053\u5C31\u6CA1\u6709\u660E\u767D\u4EBA\u5417\uFF1F\u5F53\u7136\u6709\u3002\u6768\u8D50\uFF08\u8FD9\u662F\u6768\u9707\u4E4B\u5B59\u3001\u6768\u79C9\u4E4B\u5B50\uFF09\u5C31\u4E13\u95E8\u4E0A\u4E66\uFF0C\u5E0C\u671B\u671D\u5EF7\u5BF9\u5F20\u89D2\u5927\u89C4\u6A21\u7684\u5B97\u6559\u6D3B\u52A8\u8FDB\u884C\u9650\u5236\u3002\u4F46\u53EF\u60DC\u6CA1\u8FC7\u591A\u4E45\uFF0C\u6768\u8D50\u88AB\u7F62\u5B98\u4E86\uFF0C\u671D\u5EF7\u4E5F\u5C31\u61D2\u5F97\u56DE\u590D\u548C\u642D\u7406\u8FD9\u4E8B\u4E86\u3002</p><p>\u4F46\u4E8B\u5B9E\u662F\uFF0C<strong>\u6768\u8D50\u771F\u7684\u731C\u5BF9\u4E86\uFF0C\u5F20\u89D2\u5C31\u662F\u5728\u641E\u4E8B\u60C5\u3002</strong></p><p>\u5F20\u89D2\u628A\u8DDF\u968F\u7684\u4FE1\u4F17\u4E0E\u5F1F\u5B50\u5206\u4E3A\u201C\u4E09\u5341\u516D\u65B9\u201D\uFF0C\u5927\u7684\u6709\u4E0A\u4E07\u4EBA\uFF0C\u5C0F\u7684\u4E5F\u6709\u4E03\u516B\u5343\u4EBA\uFF0C\u5404\u6709\u5934\u9886\uFF0C\u5206\u5E03\u5728\u5168\u56FD\u5404\u5730\u3002\u4F60\u60F3\u60F3\uFF0C\u8FD9\u4E9B\u4EBA\u53EA\u8981\u62FF\u8D77\u6B66\u5668\uFF0C\u5B8C\u5168\u5C31\u662F\u5F53\u5730\u7684\u9A7B\u519B\u4E86\u3002</p><p>\u4E0D\u4EC5\u5982\u6B64\uFF0C\u5F20\u89D2\u8FD8\u641E\u795E\u79D8\u4E3B\u4E49\u3002\u4ED6\u6D3E\u4EBA\u5230\u5904\u5531\u8BFB\u8BB2\u4F20\u201C<code>\u82CD\u5929\u5DF2\u6B7B\uFF0C\u9EC4\u5929\u5F53\u7ACB\uFF0C\u5C81\u5728\u7532\u5B50\uFF0C\u5929\u4E0B\u5927\u5409</code>\u201D\uFF08\u5F53\u5E74\u4E3A\u7678\u4EA5\u5E74\uFF0C\u6B21\u5E74\u5373\u4E3A\u7532\u5B50\uFF09\u7684\u53E3\u53F7\uFF1B\u5E76\u8D81\u7740\u5929\u9ED1\uFF0C\u5230\u5904\u5F80\u9AD8\u5B98\u663E\u8D35\u3001\u5BCC\u8C6A\u5927\u65CF\u7684\u95E8\u4E0A\u8D34\u5199\u7740\u201C\u7532\u5B50\u201D\u4E8C\u5B57\u7684\u5C0F\u5E7F\u544A\u3002</p><p>\u751A\u81F3\u4E8E\uFF0C\u5F20\u89D2\u5728\u5BAB\u91CC\u4E5F\u662F\u6709\u670B\u53CB\u7684\uFF0C\u5BA6\u5B98\u4E4B\u4E2D\u5C31\u6709\u4ED6\u7684\u4FE1\u4F17\u3002\u4ED6\u6D3E\u5F1F\u5B50\u4EEC\u548C\u8FD9\u4E9B\u4EBA\u4E00\u901A\u9F13\u6363\uFF0C\u51B3\u5B9A\u4E8E\u7532\u5B50\u5E74\u4E09\u6708\u4E94\u65E5\uFF0C\u5728\u5168\u56FD\u5404\u5730\u540C\u65F6\u8D77\u4E8B\u3002</p><p>\u4F60\u770B\uFF0C\u5F20\u89D2\u7684\u8FD9\u4E00\u756A\u201C\u4E8B\u4E1A\u201D\u6709\u7EC4\u7EC7\u6709\u7EAA\u5F8B\uFF0C\u6709\u89C4\u5212\u6709\u51C6\u5907\uFF0C\u8FD9\u67B6\u52BF\u5B8C\u5168\u7B80\u76F4\u662F\u53E4\u4EE3\u519C\u6C11\u8D77\u4E49\u7684\u5178\u8303\u3002</p><p>\u53EA\u662F\u6CA1\u60F3\u5230\uFF0C\u8FD9\u65F6\u5019\u9769\u547D\u961F\u4F0D\u91CC\u5374\u51FA\u4E86\u53DB\u5F92\u3002\u5C0F\u5C06\u4EEC\u9AD8\u5C1A\u7684\u9769\u547D\u60C5\u64CD\u88AB\u5730\u4E3B\u9636\u7EA7\u7684\u9AD8\u5B98\u539A\u7984\u6240\u6253\u8D25\uFF0C\u5F20\u89D2\u7684\u4E00\u4E2A\u5F1F\u5B50\u5411\u671D\u5EF7\u544A\u53D1\u4E86\u6B64\u4E8B\u3002</p><p>\u5F20\u89D2\u4E00\u770B\uFF0C\u51B3\u5B9A\u63D0\u524D\u66B4\u52A8\u3002<strong>\u9EC4\u5DFE\u4E4B\u4E71\uFF0C\u7531\u6B64\u6B64\u5F00\u59CB\u3002</strong></p><p><strong>\u56DE\u987E\u5386\u4EE3\u738B\u671D\u672B\u5E74\u7684\u519C\u6C11\u8D77\u4E49\uFF0C\u9EC4\u5DFE\u4E4B\u4E71\u662F\u52A8\u9759\u6700\u5927\u7684</strong>\uFF0C\u5B83\u4E0D\u50CF\u9648\u80DC\u5434\u5E7F\u90A3\u6837\uFF0C\u53EA\u505A\u4E86\u4E00\u9897\u71CE\u539F\u7684\u661F\u706B\uFF0C\u800C\u662F\u5728\u5168\u56FD\u8303\u56F4\u5185\uFF0C\u540C\u65F6\u7206\u53D1\u4E86\u5927\u89C4\u6A21\u7684\u66B4\u52A8\u3002</p><p>\u8FD9\u8BA9\u5F53\u65F6\u7684\u671D\u5EF7\u6781\u4E3A\u9707\u60CA\uFF0C\u672C\u6765\u8FD8\u60F3\u7740\u6715\u7684\u6C5F\u5C71\u5982\u94C1\u6876\u4E00\u822C\u5462\uFF0C\u7A81\u7136\u5374\u53D1\u73B0\u904D\u5730\u90FD\u662F\u72FC\u70DF\uFF0C\u6574\u4E2A\u4E2D\u67A2\u90FD\u614C\u4E86\u795E\uFF0C\u624B\u5FD9\u811A\u4E71\u7684\u5F00\u59CB\u6D3E\u519B\u56F4\u527F\u3002</p><p>\u597D\u5728\u5F20\u89D2\u7684\u8D77\u4E49\u867D\u7136\u770B\u4E0A\u53BB\u58F0\u52BF\u6D69\u5927\uFF0C\u4F46\u519B\u4E8B\u80FD\u529B\u5B9E\u5728\u592A\u5DEE\u3002\u53CD\u5012\u662F\u671D\u5EF7\u8FD9\u4E9B\u5E74\u4E00\u76F4\u5728\u548C\u897F\u7F8C\u4F5C\u6218\uFF0C\u5175\u7CBE\u5C06\u731B\u3002\u56E0\u6B64\uFF0C\u9EC4\u5DFE\u4E4B\u4E71\u5F88\u5FEB\u4FBF\u5F97\u4EE5\u5E73\u606F\u3002\u5F20\u89D2\u4E8E\u5F53\u5E7410\u6708\u75C5\u6B7B\uFF0C\u4E00\u4E2A\u6708\u4EE5\u540E\uFF0C\u5176\u5F1F\u5F20\u6881\u548C\u5F20\u5B9D\u88AB\u8BDB\uFF0C\u53DB\u4E71\u521D\u6B65\u5E73\u5B9A\u3002</p><p>\u53EA\u662F\uFF0C\u98CE\u96E8\u98D8\u6447\u7684\u4E1C\u6C49\u738B\u671D\u4E5F\u8D70\u5230\u4E86\u81EA\u5DF1\u7684\u5C3D\u5934\u3002\u867D\u8BF4\u9EC4\u5DFE\u4E4B\u4E71\u4EE5\u540E\uFF0C\u4E1C\u6C49\u738B\u671D\u53C8\u5B58\u7EED\u4E8636\u5E74\uFF0C\u4F46\u73B0\u4ECA\uFF0C\u6211\u4EEC\u4E00\u822C\u628A\u8FD936\u5E74\u5212\u5F52\u4E8E\u4E09\u56FD\u65F6\u4EE3\u3002</p><p>\u5BF9\u6BD4\u4E8E\u9648\u80DC\u3001\u5434\u5E7F\u7684\u4E34\u65F6\u8D77\u610F\uFF0C\u5F20\u89D2\u53EF\u8C13\u662F\u6DF1\u8C0B\u8FDC\u8651\u3002\u4ED6\u79EF\u6512\u540D\u671B\uFF0C\u57F9\u690D\u52BF\u529B\uFF0C\u5341\u51E0\u5E74\u95F4\u62DB\u7EB3\u4E86\u51E0\u5341\u4E07\u4FE1\u4F17\uFF0C\u5149\u662F\u613F\u610F\u548C\u4ED6\u4E00\u8D77\u8D77\u4E8B\u7684\u4EBA\uFF0C\u5C31\u6709\u8FD1\u4E09\u5341\u4E07\uFF01</p><p>\u8FD9\u662F\u4E00\u4E2A\u4EC0\u4E48\u6982\u5FF5\uFF1F\u7075\u5E1D\u5E74\u95F4\uFF0C\u5929\u4E0B\u4EBA\u53E3\u4E0D\u8D85\u8FC7\u516D\u5343\u4E07\uFF0C\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5168\u56FD\u4EBA\u53E3\u603B\u6570\u76845\u2030\u613F\u610F\u4E3A\u5F20\u89D2\u62FF\u8D77\u67AA\u53BB\u62FC\u547D\u3002\u6309\u6211\u4EEC\u73B0\u4ECA\u7684\u4EBA\u53E3\u6BD4\u4F8B\u6362\u7B97\u4E00\u4E0B\uFF0C\u5927\u7EA6\u76F8\u5F53\u4E8E650\u4E07\u4EBA\u7684\u89C4\u6A21\uFF01\u8981\u77E5\u9053\u4E2D\u56FD\u4EBA\u6C11\u89E3\u653E\u519B\u76EE\u524D\u4EBA\u6570\u4E5F\u5C31\u4E24\u767E\u4E07\u51FA\u5934\uFF0C\u800C\u4E14\u8FD9\u8FD8\u662F\u4E2D\u592E\u653F\u5E9C\u82B1\u4E86\u8FD1\u4E00\u4E07\u4EBF\u519B\u8D39\u7684\u6210\u679C\uFF01\u4F60\u770B\u5230\u5F20\u89D2\u82B1\u94B1\u4E86\u5417\uFF1F</p><blockquote><p>\u5F20\u89D2\u4E3A\u4EC0\u4E48\u4F1A\u6709\u8FD9\u4E48\u5927\u7684\u795E\u901A\uFF1F</p></blockquote><p>\u7B54\u6848\u7684\u5173\u952E\u5728\u4E8E\uFF1A</p><p>\u56E0\u4E3A\u4ED6\u6709\u628A\u6D41\u6C11\u7EC4\u7EC7\u8D77\u6765\u7684\u80FD\u529B\u3002</p><p>\u4ECE\u6625\u79CB\u65F6\u4EE3\u5F00\u59CB\uFF0C\u5404\u56FD\u4FBF\u9010\u6B65\u5BF9\u672C\u56FD\u5C45\u6C11\u8FDB\u884C\u201C\u7F16\u6237\u201D\uFF0C\u4E00\u5219\u65B9\u4FBF\u5F81\u7A0E\u53CA\u6218\u65F6\u52A8\u5458\uFF1B\u4E8C\u5219\u5C31\u662F\u5BF9\u6E38\u79BB\u4E8E\u6743\u529B\u63A7\u5236\u4E4B\u5916\u7684\u4EBA\u4F17\u6709\u6240\u62C5\u5FC3\u548C\u8B66\u60D5\u3002\u5230\u897F\u6C49\u65F6\uFF0C\u6B63\u5F0F\u53D1\u5C55\u4E3A\u201C<code>\u7F16\u6237\u9F50\u6C11</code>\u201D\u5236\u5EA6\uFF0C\u5E76\u4E3A\u4E2D\u56FD\u5386\u4EE3\u6240\u7EE7\u627F\u3002</p><p>\u4E00\u822C\u800C\u8A00\uFF0C\u5728\u738B\u671D\u521D\u5E74\uFF0C\u8FD9\u9879\u5236\u5EA6\u80FD\u5F97\u5230\u8F83\u597D\u6267\u884C\u3002\u4F46\u968F\u7740\u65F6\u95F4\u7684\u63A8\u79FB\uFF0C\u5386\u4EE3\u5BF9\u57FA\u5C42\u6C11\u4F17\u7684\u63A7\u5236\u4F1A\u9010\u6B65\u51CF\u5F31\uFF0C\u751A\u81F3\u4E8E\u5B8C\u5168\u6D88\u5931\u3002\u800C\u5230\u6B64\u65F6\uFF0C\u79BB\u738B\u671D\u7684\u8986\u706D\u4E5F\u5C31\u4E0D\u8FDC\u4E86\u3002</p><p>\u665A\u6E05\u5B98\u5458\u5F20\u96C6\u99A8\u5728\u300A\u9053\u54B8\u5BA6\u6D77\u89C1\u95FB\u5F55\u300B\u4E2D\uFF0C\u7EAA\u5F55\u4E86\u4ED6\u5728\u56DB\u5DDD\u5F53\u6309\u5BDF\u4F7F\u65F6\u7684\u89C1\u95FB\uFF1A</p><p>\u5F53\u65F6\uFF0C\u6210\u90FD\u5E9C\u5728\u6BCF\u5929\u65E9\u4E0A\u5F00\u57CE\u95E8\u7684\u65F6\u5019\uFF0C\u90FD\u4F1A\u6709\u4E24\u4E07\u591A\u7CBE\u58EE\u6C49\u5B50\u4ECE\u57CE\u5916\u4E00\u62E5\u800C\u5165\u3002\u8FD9\u4E9B\u4EBA\u90FD\u6709\u4E00\u4E2A\u7279\u5F81\uFF1A\u4E0D\u7A7F\u8863\u670D\u3002</p><p>\u8FD9\u4E0D\u662F\u4EC0\u4E48\u8DE8\u6027\u522B\u604B\u8005\u7684\u793A\u5A01\u6E38\u884C\uFF0C\u800C\u662F\u6210\u7FA4\u7684\u65E0\u4E1A\u6E38\u6C11\u51C6\u5907\u8FDB\u57CE\u627E\u5403\u7684\u3002\u8FD9\u4E9B\u4EBA\u57FA\u672C\u4E0A\u90FD\u662F\u4ECE\u6E56\u5317\u5B9C\u660C\u987A\u6D41\u800C\u4E0A\u7684\u7EA4\u592B\uFF0C\u5230\u4E86\u56DB\u5DDD\u4EE5\u540E\u56E0\u4E3A\u6CA1\u6709\u56DE\u4E61\u7684\u8DEF\u8D39\u4E86\uFF0C\u53EA\u80FD\u5728\u56DB\u5DDD\u5E73\u539F\u5230\u5904\u6E38\u8361\u4E5E\u8BA8\u3002\u800C\u4ED6\u4EEC\u4E4B\u6240\u4EE5\u4E0D\u7A7F\u8863\u670D\uFF0C\u662F\u56E0\u4E3A\u8863\u670D\u65E9\u5C31\u88AB\u5F53\u6389\u4E86\u3002</p><p>\u6210\u90FD\u5E9C\u6BCF\u4E2A\u767D\u5929\u90FD\u6709\u8FD9\u4E48\u591A\u7684\u884C\u4E3A\u827A\u672F\u5BB6\u5728\u51FA\u6CA1\uFF0C\u81EA\u7136\u826F\u5BB6\u5987\u5973\uFF0C\u751A\u81F3\u4E8E\u5A3C\u5993\uFF0C\u90FD\u4E0D\u6562\u5728\u767D\u5929\u51FA\u95E8\u4E86\u3002</p><p><strong>\u8FD9\u4E9B\u6E38\u79BB\u4E8E\u7EC4\u7EC7\u4E4B\u5916\u7684\u4EBA\uFF0C\u5C31\u662F\u6D41\u6C11\u3002\u4F46\u8BF7\u6CE8\u610F\uFF0C\u4ED6\u4EEC\u4E4B\u95F4\u662F\u76F8\u4E92\u72EC\u7ACB\u7684\u4E2A\u4F53\uFF0C\u800C\u4E14\u5E76\u6CA1\u6709\u4EC0\u4E48\u4F20\u8BF4\u4E2D\u7684\u4E10\u5E2E\u5B58\u5728</strong>\u3002\u56E0\u6B64\u5730\u65B9\u5B98\u4E5F\u5C31\u7741\u4E00\u53EA\u773C\u95ED\u4E00\u53EA\u773C\u4E86\uFF0C\u53EA\u8981\u4E0D\u662F\u592A\u8FC7\u5206\uFF0C\u4E5F\u61D2\u5F97\u9A71\u8D76\u3002</p><p><strong>\u4F46\u82E5\u662F\u6709\u7EC4\u7EC7\u7684\uFF0C\u53EF\u5C31\u4E0D\u4E00\u6837\u4E86\u3002</strong></p><p>\u4EE5\u660E\u672B\u4E3A\u4F8B\uFF0C\u9AD8\u8FCE\u7965\u3001\u674E\u81EA\u6210\u63ED\u7AFF\u800C\u8D77\u540E\uFF0C\u5341\u51E0\u5E74\u95F4\u51E0\u906D\u8D25\u7EE9\uFF0C\u65E0\u5904\u5BB9\u8EAB\u3002\u65F6\u9022\u6CB3\u5357\u8FDE\u5E74\u5927\u65F1\uFF0C\u904D\u5730\u9965\u6C11\u3002\u674E\u81EA\u6210\u8FDB\u5165\u6CB3\u5357\u4E0D\u5230\u4E00\u5E74\uFF0C\u4FBF\u88F9\u631F\u4E86\u51E0\u5341\u4E07\u6D41\u79BB\u5931\u6240\u7684\u9965\u6C11\uFF0C\u58F0\u52BF\u5927\u632F\uFF0C\u4E94\u5E74\u4EE5\u540E\uFF0C\u653B\u7834\u5317\u4EAC\u3002</p><p>\u6240\u4EE5\u6211\u4EEC\u770B\u5404\u79CD\u5173\u4E8E\u6E05\u4EE3\u7684\u5F71\u89C6\u4F5C\u54C1\u65F6\uFF0C\u4F1A\u53D1\u73B0\u671D\u5EF7\u5BF9\u4E8E\u8BF8\u5982\u5929\u5730\u4F1A\u3001\u767D\u83B2\u6559\u7B49\uFF0C\u603B\u662F\u89C6\u82E5\u5F3A\u654C\uFF0C\u52A1\u8981\u65A9\u5C3D\u6740\u7EDD\u3002\u4E0D\u662F\u56E0\u4E3A\u8FD9\u5176\u4E2D\u6709\u591A\u5C11\u50CF\u65B9\u4E16\u7389\u4E00\u6837\u7684\u6B66\u6797\u9AD8\u624B\uFF0C\u800C\u662F\u5728\u4E8E<strong>\u8FD9\u4E9B\u6559\u6D3E\u53EF\u4EE5\u628A\u8131\u79BB\u4E8E\u7F16\u6237\u7684\u6D41\u6C11\uFF0C\u901A\u8FC7\u5B97\u6559\u7684\u540D\u4E49\u7EDF\u4E00\u4EF7\u503C\u89C2\uFF0C\u7136\u540E\u7ED9\u7EC4\u7EC7\u8D77\u6765\u3002</strong></p><p>\u5230\u8FD9\u91CC\uFF0C\u6211\u4EEC\u4E5F\u5C31\u770B\u5F97\u6E05\u5386\u4EE3\u519C\u6C11\u8D77\u4E49\u80FD\u591F\u6210\u4E8B\u7684\u6761\u4EF6\u4E86\uFF1A</p><p><code>\u5176\u4E00</code>\uFF0C\u8981\u6709\u8131\u79BB\u4E8E\u653F\u5E9C\u63A7\u5236\u7684\u5927\u91CF\u6D41\u6C11\uFF1A</p><p><code>\u5176\u4E8C</code>\uFF0C\u8981\u6709\u80FD\u628A\u8FD9\u4E9B\u6D41\u6C11\u805A\u96C6\u8D77\u6765\u7684\u7EC4\u7EC7\u3002</p><p>\u4E00\u65E6\u8FD9\u4E24\u4E2A\u6761\u4EF6\u90FD\u6210\u719F\uFF0C\u738B\u671D\u5C31\u5F88\u96BE\u9003\u8FC7\u8986\u4EA1\u7684\u547D\u8FD0\u3002</p><p>\u5F53\u7136\uFF0C<strong>\u6B64\u9898\u867D\u96BE\uFF0C\u5374\u4E5F\u4E0D\u662F\u65E0\u89E3</strong>\u3002\u4E2D\u56FD\u7684\u6700\u540E\u4E00\u4E2A\u5723\u4EBA\u2014\u2014\u66FE\u56FD\u85E9\u5C31\u89E3\u8FC7\u3002\u4ED6\u5728\u5E73\u5B9A\u592A\u5E73\u5929\u56FD\u4E4B\u4E71\u65F6\uFF0C\u7A33\u624E\u7A33\u6253\uFF0C\u6B65\u6B65\u4E3A\u8425\uFF0C\u201C\u7ED3\u786C\u5BE8\u6253\u5446\u4ED7\u201D\uFF0C\u4E0D\u6C42\u6709\u529F\u4F46\u6C42\u65E0\u8FC7\uFF0C\u901A\u8FC7\u4E00\u70B9\u4E00\u70B9\u7684\u8695\u98DF\uFF0C\u6700\u7EC8\u56F4\u6B7B\u4E86\u592A\u5E73\u519B\u3002</p><p>\u79D8\u8BC0\u4F55\u5728\uFF1F\u7BA1\u4F60\u6709\u591A\u5F3A\u5927\u7684\u7EC4\u7EC7\u80FD\u529B\uFF0C\u6211\u53EA\u8981\u628A\u4F60\u548C\u6D41\u6C11\u5F7B\u5E95\u9694\u7EDD\uFF0C\u770B\u4F60\u8FD8\u6709\u4F55\u80FD\u8010\uFF01</p><p><a href="">\u6587\u6848</a></p>',42),n=[c];function s(a,d){return o(),t("div",null,n)}const _=p(r,[["render",s],["__file","1.html.vue"]]);export{_ as default};
