import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.21634df6.js";const c={},o=a('<h1 id="\u7B2C1\u96C6-\u77E5\u540D\u5211\u8FA9\u5F8B\u5E08\u6740\u4EBA\u7FFB\u4F9B-\u9B3C\u624D\u5211\u4FA6\u961F\u957F\u4E34\u5371\u53D7\u547D" tabindex="-1"><a class="header-anchor" href="#\u7B2C1\u96C6-\u77E5\u540D\u5211\u8FA9\u5F8B\u5E08\u6740\u4EBA\u7FFB\u4F9B-\u9B3C\u624D\u5211\u4FA6\u961F\u957F\u4E34\u5371\u53D7\u547D" aria-hidden="true">#</a> \u7B2C1\u96C6\uFF1A\u77E5\u540D\u5211\u8FA9\u5F8B\u5E08\u6740\u4EBA\u7FFB\u4F9B \u9B3C\u624D\u5211\u4FA6\u961F\u957F\u4E34\u5371\u53D7\u547D</h1><p>2010\u5E74\uFF0C\u6C5F\u6F6D\u5E02\u7684\u4E0A\u7A7A\u4E91\u5411\u9634\u6C89\uFF0C\u4F3C\u662F\u53D7\u5230\u5438\u5F15\u822C\u6C47\u6210\u4E00\u56E2\uFF0C\u7EE7\u800C\u7B3C\u7F69\u5728\u901A\u5F80\u5E02\u90CA\u7684\u5730\u94C1\u4E09\u53F7\u7EBF\uFF0C\u76F4\u81F3\u7AD9\u5916\u8D70\u6765\u4E00\u540D\u4E2D\u5E74\u7537\u5B50\uFF0C\u5373\u4FBF\u4ED6\u8863\u886B\u7834\u891B\uFF0C\u559D\u5B8C\u7F50\u88C5\u5564\u9152\u540E\u968F\u5730\u4E22\u7F6E\uFF0C\u4F46\u56E0\u5468\u56F4\u7199\u6518\uFF0C\u65E0\u4EBA\u6295\u53BB\u8D28\u7591\u76EE\u5149\u3002</p><p>\u7537\u5B50\u62D6\u7740\u5DE8\u5927\u7B28\u91CD\u7684\u884C\u674E\u7BB1\uFF0C\u6D51\u8EAB\u6563\u53D1\u51FA\u6D53\u70C8\u7684\u9152\u6C14\u4E0E\u6C57\u81ED\uFF0C\u4E00\u8DEF\u4E0A\u5934\u4E5F\u4E0D\u62AC\u5730\u5F80\u524D\u8D70\uFF0C\u4E34\u8FC7\u5B89\u68C0\u65F6\uFF0C\u8FD9\u624D\u653E\u6162\u811A\u6B65\uFF0C\u5F00\u59CB\u5DE6\u53F3\u5F20\u671B\uFF0C\u4F3C\u4E4E\u60F3\u8981\u6324\u8FDB\u4EBA\u7FA4\u4E2D\u8499\u6DF7\u8FC7\u5173\u3002\u5E78\u597D\u5B89\u68C0\u5458\u773C\u660E\u624B\u5FEB\uFF0C\u53CA\u65F6\u5C06\u4ED6\u62E6\u4E0B\uFF0C\u5C24\u5176\u770B\u5230\u7537\u5B50\u795E\u8272\u614C\u5F20\uFF0C\u8116\u95F4\u6293\u75D5\u660E\u663E\uFF0C\u4EE5\u53CA\u51F6\u72E0\u53C8\u8FDF\u7591\u7684\u773C\u795E\uFF0C\u5185\u5FC3\u4E0D\u7531\u54CD\u8D77\u8B66\u94C3\uFF0C\u672C\u80FD\u5730\u63E1\u7D27\u5BF9\u8BB2\u673A\u3002</p><p>\u4FE9\u4EBA\u5BF9\u89C6\u4E0D\u5230\u51E0\u79D2\u540E\uFF0C\u7537\u5B50\u77AC\u95F4\u63A8\u5F00\u5B89\u68C0\u5458\uFF0C\u62D4\u817F\u72C2\u5954\uFF0C\u773C\u89C1\u524D\u65B9\u65E0\u8DEF\uFF0C\u4E24\u9762\u5747\u88AB\u5305\u6284\uFF0C\u4E8E\u662F\u5F53\u573A\u62FF\u51FA\u70B8\u5F39\u9065\u63A7\uFF0C\u8FEB\u4F7F\u4FDD\u5B89\u7D27\u6025\u758F\u6563\u6240\u6709\u7FA4\u4F17\uFF0C\u5E76\u5C06\u9053\u8DEF\u5C01\u9501\u3002</p><p>\u5341\u5206\u949F\u540E\uFF0C\u4E03\u516B\u8F86\u8B66\u8F66\u706B\u901F\u8D76\u5230\uFF0C\u51E0\u5341\u4E2A\u9632\u66B4\u8B66\u5BDF\u624B\u6301\u76FE\u724C\uFF0C\u8FC5\u901F\u7EC4\u6210\u5F2F\u6708\u5F62\u6218\u961F\u56F4\u4F4F\u51FA\u53E3\u3002\u773C\u89C1\u7537\u5B50\u60C5\u7EEA\u9010\u6E10\u5931\u63A7\uFF0C\u5E02\u5C40\u5211\u8B66\u961F\u957F\u4EFB\u73A5\u5A77\u7ACB\u9A6C\u8FC7\u53BB\u4E0E\u4ED6\u5468\u65CB\uFF0C\u8D81\u5176\u4E0D\u5907\uFF0C\u4E00\u62DB\u5236\u670D\u5BF9\u65B9\u3002</p><p>\u968F\u7740\u884C\u674E\u7BB1\u8F6C\u79FB\u5230\u7A7A\u65F7\u5730\u70B9\uFF0C\u6392\u7206\u624B\u5F00\u59CB\u5BF9\u6B64\u8FDB\u884C\u63A2\u6D4B\uFF0C\u5E76\u5728\u51E0\u5341\u53CC\u773C\u775B\u7684\u6CE8\u89C6\u4E0B\uFF0C\u7D27\u5F20\u5730\u8D70\u5411\u884C\u674E\u7BB1\u524D\uFF0C\u7F13\u7F13\u62C9\u5F00\u9501\u94FE\uFF0C\u7ED3\u679C\u6620\u5165\u773C\u5E18\u7684\u5E76\u975E\u70B8\u5F39\uFF0C\u800C\u662F\u4E00\u5177\u5C38\u4F53\uFF0C\u4E00\u5177\u7626\u5F31\u4E14\u5E72\u51C0\u7684\u7537\u6027\u5C38\u4F53\uFF0C\u4ED6\u5728\u751F\u524D\u66FE\u662F\u5E73\u5EB7\u53BF\u6700\u5E74\u8F7B\u4F18\u79C0\u7684\u68C0\u5BDF\u5B98\uFF0C\u5374\u5728\u6B7B\u540E\u88AB\u5916\u754C\u79F0\u4E3A\u201C\u6B7B\u8005\u6C5F\u9633\u201D\uFF0C\u5E76\u4E14\u7559\u4E0B\u90A3\u6BB5\u5750\u7262\u4E24\u5E74\u7684\u9ED1\u6697\u7ECF\u5386\u3002</p><p>\u7537\u5B50\u540D\u53EB\u5F20\u8D85\uFF0C\u4ED6\u662F\u6C5F\u9633\u7684\u8001\u5E08\uFF0C\u4E5F\u662F\u6C5F\u6F6D\u5E02\u77E5\u540D\u5211\u8FA9\u5F8B\u5E08\uFF0C\u4E14\u4E0D\u8BF4\u5148\u524D\u5C65\u5386\u591A\u4E48\u8F89\u714C\uFF0C\u5C31\u4EE5\u4E09\u5341\u56DB\u5C81\u6210\u4E3A\u653F\u6CD5\u5B66\u9662\u526F\u9662\u957F\u7684\u8EAB\u4EFD\uFF0C\u8DB3\u591F\u4EE4\u4EBA\u77AA\u76EE\u7ED3\u820C\u3002\u53EF\u60DC\u5F20\u8D85\u62E5\u6709\u5927\u597D\u524D\u9014\uFF0C\u504F\u8981\u7231\u4E0A\u5973\u5B66\u751F\u674E\u9759\uFF0C\u751A\u81F3\u4E3A\u5979\u653E\u5F03\u4E00\u5207\uFF0C\u51C0\u8EAB\u51FA\u6237\uFF0C\u76F4\u5230\u4E09\u5341\u516D\u5C81\u767D\u624B\u8D77\u5BB6\uFF0C\u72EC\u81EA\u521B\u529E\u6C5F\u4E1C\u5F8B\u5E08\u4E8B\u52A1\u6240\uFF0C\u5341\u5E74\u91CC\u63A5\u624B\u8FC7\u4E0A\u5343\u8D77\u6848\u4EF6\uFF0C\u51E0\u4E4E\u4ECE\u672A\u5931\u624B\u3002</p><p>\u8C01\u4E5F\u6CA1\u60F3\u5230\uFF0C\u6B63\u662F\u8FD9\u4E48\u4E00\u4F4D\u540D\u9707\u5E02\u653F\u6CD5\u754C\u7684\u4F20\u5947\u4EBA\u7269\uFF0C\u7ADF\u7136\u4F1A\u7528\u6781\u5176\u5351\u52A3\u7684\u624B\u6BB5\u6740\u4EBA\u629B\u5C38\uFF0C\u9762\u5BF9\u8B66\u65B9\u5BA1\u8BAF\u9009\u62E9\u6001\u5EA6\u914D\u5408\uFF0C\u4F9B\u8BA4\u4E0D\u8BB3\uFF0C\u5982\u5B9E\u4EA4\u4EE3\u4ED6\u4E0E\u6C5F\u9633\u56E0\u4E3A\u503A\u52A1\u7EA0\u7EB7\u53D1\u751F\u4E89\u6267\uFF0C\u4EE5\u53CA\u4F5C\u6848\u7ECF\u8FC7\uFF0C\u5305\u62EC\u540E\u671F\u4E3A\u4F55\u4F1A\u9009\u4E58\u5750\u5730\u94C1\u524D\u5F80\u90CA\u533A\u6BC1\u5C38\u706D\u8FF9\u3002</p><p>\u4E0E\u6B64\u540C\u65F6\uFF0C\u7406\u79D1\u72B6\u5143\u65E0\u6545\u5931\u8054\uFF0C\u540E\u88AB\u51F6\u624B\u6740\u5BB3\u57CB\u5728\u6C5F\u8FB9\uFF0C\u7ED3\u679C\u8FD1\u6765\u51E0\u65E5\u8FDE\u964D\u5927\u96E8\uFF0C\u6070\u597D\u51F6\u624B\u906D\u9047\u8F66\u7978\u8EAB\u4EA1\uFF0C\u6839\u672C\u65E0\u6CD5\u51C6\u786E\u627E\u51FA\u57CB\u85CF\u4F4D\u7F6E\u3002\u7834\u6848\u9AD8\u624B\u4E25\u826F\u4EE5\u5947\u7279\u624B\u6CD5\u534F\u52A9\u8B66\u65B9\uFF0C\u679C\u7136\u5C31\u5728\u5F53\u5929\u591C\u91CC\u53D1\u73B0\u5C38\u4F53\u3002</p><p>\u6839\u636E\u5F20\u8D85\u4EB2\u53E3\u4F9B\u8FF0\u4E0E\u73B0\u573A\u6307\u8BA4\uFF0C\u518D\u52A0\u4E0A\u6CD5\u533B\u540E\u671F\u9274\u5B9A\uFF0C\u56E0\u6B64\u5750\u5B9E\u6C5F\u9633\u662F\u88AB\u7535\u7EBF\u52D2\u4F4F\uFF0C\u9020\u6210\u673A\u68B0\u6027\u7A92\u606F\u6B7B\u4EA1\u3002\u7136\u800C\u7834\u83B7\u8FC7\u7A0B\u592A\u8FC7\u987A\u5229\uFF0C\u8BC1\u636E\u94FE\u5B8C\u6574\uFF0C\u5BFC\u81F4\u6B64\u6B21\u6848\u4EF6\u663E\u5F97\u683C\u5916\u8BE1\u5F02\uFF0C\u5A92\u4F53\u4EEC\u4E0D\u53BB\u8BA1\u8F83\u771F\u5047\uFF0C\u53EA\u5728\u8FD9\u573A\u6848\u4EF6\u4E0A\u8FDB\u884C\u8DDF\u8E2A\u62A5\u9053\uFF0C\u77ED\u77ED\u51E0\u4E2A\u5C0F\u65F6\u91CC\u8D5A\u8DB3\u8206\u8BBA\u7126\u70B9\uFF0C\u7B49\u5F85\u5927\u4F17\u65B0\u9C9C\u611F\u6B86\u5C3D\uFF0C\u518D\u4F5C\u5176\u4ED6\u6587\u7AE0\u3002</p><p>\u4EFB\u73A5\u5A77\u5FC3\u4E2D\u5C1A\u6709\u7591\u8651\uFF0C\u4E3B\u52A8\u53BB\u89C1\u5F20\u8D85\u8BE2\u95EE\u771F\u76F8\uFF0C\u56E0\u4E3A\u5979\u4E86\u89E3\u5230\u5F20\u8D85\u5728\u4E0A\u5343\u8D77\u5211\u4E8B\u6848\u4EF6\u4E2D\uFF0C\u552F\u72EC\u5931\u624B\u5728\u6C5F\u9633\u7684\u65E0\u7F6A\u8FA9\u62A4\u4E0A\uFF0C\u5C24\u5176\u4E8B\u540E\u6CA1\u6709\u7EE7\u7EED\u4E0A\u8BC9\uFF0C\u5BFC\u81F4\u6C5F\u9633\u5165\u72F1\uFF0C\u4E22\u6389\u5DE5\u4F5C\u3002\u5948\u4F55\u5F20\u8D85\u6CA1\u6709\u6B63\u9762\u56DE\u5E94\uFF0C\u4ED6\u9009\u62E9\u907F\u4E4B\u4E0D\u8C08\uFF0C\u7528\u6C89\u9ED8\u63A9\u76D6\u771F\u76F8\uFF0C\u5374\u65E0\u6CD5\u63A9\u76D6\u5BF9\u4E8E\u6C5F\u9633\u6B7B\u8BAF\u7684\u60B2\u75DB\u3002</p><p>\u6CD5\u9662\u516C\u544A\u5F00\u5EAD\u65E5\u671F\u540E\uFF0C3.12\u5730\u94C1\u629B\u5C38\u6848\u518D\u5EA6\u53D1\u9175\uFF0C\u5404\u5927\u5A92\u4F53\u8BB0\u8005\u7EB7\u7EB7\u7533\u8BF7\u65C1\u542C\uFF0C\u5176\u4E2D\u5305\u62EC\u300A\u6C5F\u6F6D\u665A\u62A5\u300B\u6CD5\u5236\u680F\u76EE\u7684\u5973\u8BB0\u8005\u5F20\u6653\u5029\u3002\u5EAD\u5BA1\u73B0\u573A\uFF0C\u5F20\u8D85\u7A81\u7136\u7FFB\u4F9B\uFF0C\u62D2\u4E0D\u627F\u8BA4\u8C0B\u6740\u6307\u63A7\uFF0C\u968F\u5373\u62FF\u51FA\u5B8C\u7F8E\u7684\u4E0D\u5728\u573A\u8BC1\u660E\uFF0C\u4F7F\u5F97\u539F\u672C\u6E05\u6670\u660E\u4E86\u7684\u6848\u4EF6\u9877\u523B\u53D8\u5F97\u6251\u6714\u8FF7\u79BB\uFF0C\u53EA\u542C\u65C1\u542C\u5E2D\u5904\u4F20\u6765\u4E00\u7247\u550F\u5618\u3002</p><p>\u6B64\u4E8B\u5F71\u54CD\u6781\u5927\uFF0C\u773C\u4E0B\u5DF2\u5411\u96BE\u4EE5\u6536\u573A\u7684\u5730\u6B65\u53D1\u5C55\uFF0C\u7701\u516C\u5B89\u5385\u4F9D\u7167\u7591\u7F6A\u4ECE\u65E0\u539F\u5219\uFF0C\u8981\u6C42\u5E02\u5C40\u5728\u4E8C\u5341\u56DB\u5929\u5185\u91CD\u65B0\u67E5\u529E\u3002\u5C40\u957F\u8D75\u4F1F\u6C11\u7D27\u6025\u6210\u7ACB3.12\u4E13\u6848\u7EC4\uFF0C\u4E25\u826F\u4E34\u5371\u53D7\u547D\uFF0C\u4E13\u95E8\u5BF9\u5F20\u8D85\u8FDB\u884C\u63D0\u5BA1\u3002</p><p>\u7531\u4E8E\u4E25\u826F\u4E3A\u4EBA\u6B63\u76F4\uFF0C\u529E\u6848\u98CE\u683C\u72EC\u7279\uFF0C\u6240\u4EE5\u66FE\u5728\u4E1C\u5317\u7834\u83B7\u9707\u60CA\u5168\u56FD\u7684\u96EA\u4EBA\u6740\u4EBA\u6848\uFF0C\u7EB5\u7136\u7ACB\u4E0B\u5927\u529F\uFF0C\u5374\u4E5F\u75DB\u5931\u513F\u5B50\uFF0C\u59BB\u79BB\u5BB6\u7834\u3002\u5F20\u8D85\u5BF9\u4ED6\u6781\u5176\u6B23\u8D4F\u4E0E\u5C0A\u91CD\uFF0C\u4EA6\u662F\u770B\u5230\u5E0C\u671B\uFF0C\u671F\u5F85\u4ED6\u80FD\u5728\u7B2C\u4E8C\u5341\u56DB\u5929\u627E\u51FA\u771F\u76F8\u3002</p>',14),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","1.html.vue"]]);export{h as default};
