import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as t,f as a}from"./app.1218f0f6.js";const c={},o=a('<h1 id="\u7B2C1\u96C6-\u5468\u84C9\u4E3A\u7231\u81EA\u613F\u4E0B\u4E61\u5165\u6DF1\u5C71" tabindex="-1"><a class="header-anchor" href="#\u7B2C1\u96C6-\u5468\u84C9\u4E3A\u7231\u81EA\u613F\u4E0B\u4E61\u5165\u6DF1\u5C71" aria-hidden="true">#</a> \u7B2C1\u96C6\uFF1A\u5468\u84C9\u4E3A\u7231\u81EA\u613F\u4E0B\u4E61\u5165\u6DF1\u5C71</h1><p>1969\u5E74\u4E2D\u56FD\u51E0\u4E4E\u6240\u6709\u7684\u4EBA\u5BB6\u90FD\u5373\u5C06\u6216\u5373\u5C06\u9762\u4E34\u4E00\u79CD\u4E0A\u5C71\u4E0B\u4E61\u7684\u9009\u62E9\u3002\u5728\u8FBD\u5B81\u7701\u5149\u5B50\u7247\u6709\u4E00\u6237\u5468\u5BB6\u4EBA\uFF0C\u4E5F\u88AB\u8FD9\u79CD\u4E0A\u5C71\u4E0B\u4E61\u7684\u9009\u62E9\u7ED9\u7B3C\u7F69\u7740\u3002\u5468\u5FD7\u521A\u662F\u5927\u4E09\u7EBF\u5DE5\u4EBA\uFF0C\u5BB6\u91CC\u6709\u4E09\u4E2A\u5B50\u5973\uFF0C\u8001\u5927\u5468\u79C9\u4E49\u5DF2\u7ECF\u53C2\u52A0\u4E86\u5EFA\u8BBE\u5175\u56E2\uFF0C\u8001\u4E8C\u662F\u4E2A\u5973\u513F\u53EB\u5468\u84C9\uFF0C\u8001\u4E09\u513F\u5B50\u53EB\u5468\u79C9\u6606\u3002\u6309\u7167\u653F\u7B56\u7684\u89C4\u5B9A\uFF0C\u65E0\u8BBA\u7537\u5973\u90FD\u8981\u4E0A\u5C71\u4E0B\u4E61\uFF0C\u8857\u9053\u4E0A\u7684\u4E54\u5927\u5988\u6765\u505A\u5DE5\u4F5C\uFF0C\u529D\u8BF4\u518D\u51FA\u53BB\u4E00\u4E2A\u5B50\u5973\uFF0C\u4F46\u5728\u4E54\u5927\u5988\u770B\u6765\u5468\u84C9\u662F\u4E2A\u5973\u5B69\uFF0C\u800C\u4E14\u957F\u76F8\u975E\u5E38\u597D\u770B\uFF0C\u5728\u5149\u5B50\u7247\u5C31\u88AB\u6240\u6709\u7537\u5B69\u5B50\u60E6\u8BB0\u7740\uFF0C\u5982\u679C\u53BB\u4E86\u5916\u5730\u5C31\u66F4\u52A0\u4E0D\u5B89\u5168\u3002\u73B0\u5728\u8001\u4E09\u5468\u79C9\u6606\u5DF2\u7ECF\u521D\u4E2D\u6BD5\u4E1A\u4E86\uFF0C\u56E0\u6B64\u90FD\u5E0C\u671B\u5468\u79C9\u6606\u80FD\u53BB\u3002</p><p>\u5468\u84C9\u575A\u6301\u60F3\u8981\u53BB\u4E61\u4E0B\uFF0C\u5468\u5FD7\u521A\u7684\u59BB\u5B50\u4E00\u65F6\u62FF\u4E0D\u5B9A\u4E3B\u610F\uFF0C\u8BF4\u8BDD\u4E0D\u53CA\u5468\u5FD7\u521A\u53D6\u5B8C\u7167\u7247\u56DE\u6765\u4E86\uFF0C\u4E00\u5171\u6D17\u4E86\u4E94\u5F20\u7167\u7247\uFF0C\u6BCF\u4EBA\u4E00\u5F20\u7167\u7247\uFF0C\u5927\u5BB6\u90FD\u77E5\u9053\u8FD9\u6B21\u5206\u522B\u4E0D\u77E5\u9053\u8981\u591A\u4E45\u624D\u80FD\u89C1\u9762\u3002\u5468\u5FD7\u521A\u6700\u540E\u4E5F\u8BA9\u5468\u79C9\u6606\u4E0B\u4E61\uFF0C\u5468\u79C9\u6606\u672C\u4EBA\u5012\u662F\u975E\u5E38\u4E50\u610F\uFF0C\u4E00\u5BB6\u4EBA\u4E5F\u5C31\u5982\u6B64\u5546\u5B9A\u4E86\u3002</p><p>\u5468\u5BB6\u4E00\u5BB6\u4EBA\u6765\u8F66\u7AD9\u5148\u9001\u8D70\u4E86\u5468\u79C9\u4E49\uFF0C\u5468\u79C9\u4E49\u8868\u9762\u5E26\u7740\u5FAE\u7B11\uFF0C\u8F6C\u5934\u773C\u6CEA\u6ED1\u843D\uFF0C\u624B\u91CC\u8FD8\u6525\u7740\u6BCD\u4EB2\u5077\u5077\u585E\u7ED9\u4ED6\u7684\u94B1\u3002\u4E09\u5341\u5206\u949F\u4E4B\u540E\u9001\u8D70\u5468\u5FD7\u521A\uFF0C\u5468\u5FD7\u521A\u592B\u59BB\u6CEA\u773C\u76F8\u671B\uFF0C\u4F46\u662F\u90FD\u4E0D\u80FD\u8BF4\u51FA\u81EA\u5DF1\u60F3\u8981\u8BF4\u7684\u8BDD\uFF0C\u5468\u84C9\u4E3B\u52A8\u8BA9\u5468\u79C9\u6606\u5C06\u5979\u62B1\u8D77\u6765\uFF0C\u900F\u8FC7\u8F66\u7A97\u4EB2\u543B\u4E86\u5468\u5FD7\u521A\u7684\u8138\uFF0C\u8FDE\u7740\u4EB2\u4E86\u4E24\u4E0B\uFF0C\u4E00\u4E0B\u4EE3\u8868\u81EA\u5DF1\uFF0C\u4E00\u4E0B\u4EE3\u8868\u6BCD\u4EB2\u3002\u5468\u5FD7\u521A\u5FC3\u91CC\u820D\u4E0D\u5F97\u5BB6\u4EBA\uFF0C\u53EF\u662F\u4E0D\u5F97\u4E0D\u51FA\u53BB\u3002</p><p>\u5468\u5FD7\u521A\u4EBA\u751F\u4E2D\u7ECF\u8FC7\u4E86\u4E24\u6B21\u5927\u53D8\u9769\uFF0C\u7B2C\u4E00\u6B21\u6210\u4E3A\u4E86\u5927\u4E09\u7EBF\u7684\u5EFA\u7B51\u5DE5\u4EBA\uFF0C\u4ECE\u519C\u6751\u6237\u53E3\u53D8\u4E3A\u4E86\u57CE\u5E02\u6237\u53E3\uFF0C\u53EF\u662F\u73B0\u5728\u53C8\u4E0A\u5C71\u4E0B\u4E61\uFF0C\u4E24\u4E2A\u5B50\u5973\u5C06\u5931\u53BB\u57CE\u5E02\u6237\u53E3\u3002</p><p>\u5468\u79C9\u6606\u628A\u5468\u79C9\u4E49\u7559\u4E0B\u7684\u4E66\u7C4D\u90FD\u7ED9\u5468\u84C9\u4FDD\u7BA1\uFF0C\u91CC\u9762\u768461\u4E00\u672C\u4E66\u90FD\u4F1A\u662F\u7EDD\u7248\uFF0C\u5468\u84C9\u62C5\u5FC3\u653E\u5728\u5BB6\u91CC\u4E0D\u5B89\u5168\uFF0C\u5C31\u7528\u6CB9\u5E03\u5305\u8D77\u6765\u653E\u5728\u4E86\u767D\u83DC\u5806\u91CC\uFF0C\u5468\u79C9\u6606\u63D0\u51FA\u8981\u7528\u8FA3\u6912\u9632\u866B\uFF0C\u5468\u84C9\u72E0\u72E0\u5632\u7B11\u4E86\u5468\u79C9\u6606\u4E00\u987F\uFF0C\u63D0\u9192\u5468\u79C9\u6606\u4E00\u5B9A\u8981\u591A\u8BFB\u4E66\u521D\u4E2D\u6BD5\u4E1A\u5C31\u597D\u50CF\u662F\u5C0F\u5B66\u6BD5\u4E1A\u4E00\u6837\u3002</p><p>\u8521\u6653\u5149\u9001\u4E86\u5468\u84C9\u79BB\u5F00\u5BB6\u91CC\uFF0C\u5468\u84C9\u7792\u7740\u6240\u6709\u4EBA\u4E0A\u4E86\u8F66\uFF0C\u53EA\u7559\u4E0B\u4E00\u5C01\u4FE1\uFF0C\u53EE\u5631\u5468\u79C9\u6606\u597D\u597D\u7167\u987E\u6BCD\u4EB2\uFF0C\u5979\u53BB\u4E86\u8D35\u5DDE\u4E0B\u4E61\u3002\u8521\u6653\u5149\u7B2C\u4E09\u5929\u4E4B\u540E\u53BB\u544A\u8BC9\u4E86\u5468\u84C9\u6BCD\u4EB2\u539F\u56E0\uFF0C\u5468\u84C9\u559C\u6B22\u7684\u51AF\u5316\u6210\u5728\u8D35\u5DDE\uFF0C\u8521\u6653\u5149\u4E00\u76F4\u559C\u6B22\u5468\u84C9\uFF0C\u4F46\u662F\u5468\u84C9\u53EA\u559C\u6B22\u51AF\u5316\u6210\u3002\u8521\u6653\u5149\u8FD9\u4E9B\u5E74\u4E00\u76F4\u8868\u9762\u5145\u5F53\u5468\u84C9\u7684\u7537\u53CB\uFF0C\u5C31\u662F\u4E3A\u4E86\u6321\u4F4F\u90A3\u4E9B\u8FFD\u6C42\u8005\u3002\u8521\u6653\u5149\u773C\u4E2D\u5468\u84C9\u662F\u4F1F\u5927\u7684\uFF0C\u53EF\u4EE5\u4E3A\u4E86\u7231\u60C5\u53BB\u8D35\u5DDE\u7684\u5927\u5C71\u4E2D\uFF0C\u4F46\u8FD9\u8BA9\u5468\u6BCD\u4F24\u5FC3\u53C8\u62C5\u5FC3\u3002</p><p>\u51E0\u5E74\u4E4B\u540E\u5927\u5BB6\u90FD\u957F\u5927\u4E86\uFF0C\u5468\u79C9\u6606\u5728\u4E00\u4E2A\u5DE5\u5382\u505A\u5DE5\uFF0C\u8FD9\u5929\u5382\u91CC\u7A81\u7136\u901A\u77E5\u5468\u79C9\u6606\u53BB\u53C2\u52A0\u4ED6\u53D1\u5C0F\u67AA\u6BD9\u7684\u6D3B\u52A8\u3002\u5468\u79C9\u6606\u4E0D\u613F\u610F\u53BB\uFF0C\u58F0\u79F0\u81EA\u5DF1\u80C6\u5B50\u5C0F\uFF0C\u4F46\u5382\u91CC\u5374\u575A\u6301\u8BA9\u5468\u79C9\u6606\u5FC5\u987B\u53BB\uFF0C\u8FD8\u8BA4\u4E3A\u5468\u79C9\u6606\u4E5F\u5E94\u8BE5\u5F15\u4EE5\u4E3A\u6212\u7684\u53BB\u770B\u770B\u3002\u6D82\u5FD7\u5F3A\u662F\u5468\u79C9\u6606\u5F88\u597D\u7684\u670B\u53CB\uFF0C\u5468\u79C9\u6606\u4E00\u76F4\u5BF9\u4ED6\u90FD\u6BD4\u8F83\u7167\u987E\uFF0C\u4E24\u4EBA\u62AC\u6728\u5934\u5E72\u6D3B\uFF0C\u5468\u79C9\u6606\u90FD\u4F1A\u62AC\u7740\u7C97\u7684\uFF0C\u8BA9\u6D82\u5FD7\u5F3A\u62AC\u7740\u7EC6\u7684\u3002</p><p>\u770B\u5B88\u6240\u95E8\u53E3\u56F4\u4E86\u5F88\u591A\u4EBA\uFF0C\u5929\u4E0A\u4E0B\u7740\u96EA\uFF0C\u6BCF\u4E2A\u88AB\u6740\u7684\u4EBA\u90FD\u6234\u7740\u5E3D\u5B50\uFF0C\u53EA\u6709\u6D82\u5FD7\u5F3A\u6CA1\u6709\u3002\u5468\u79C9\u6606\u51E0\u6B21\u4E09\u756A\u60F3\u8981\u5C06\u81EA\u5DF1\u7684\u5E3D\u5B50\u9001\u4E0A\u53BB\uFF0C\u53EF\u662F\u90FD\u6CA1\u90A3\u4E2A\u52C7\u6C14\uFF0C\u7A81\u7136\u4E00\u4E2A\u7A7F\u7740\u897F\u88C5\u7684\u4EBA\u5C06\u81EA\u5DF1\u7684\u5E3D\u5B50\u7ED9\u4E86\u6D82\u5FD7\u5F3A\u3002\u6D82\u5FD7\u5F3A\u4F3C\u4E4E\u4E5F\u548C\u4ED6\u8BF4\u4E86\u4EC0\u4E48\uFF0C\u4F46\u6CA1\u4EBA\u542C\u5230\uFF0C\u6C34\u81EA\u6D41\u53EA\u662F\u544A\u8BC9\u770B\u5B88\uFF0C\u90A3\u662F\u6D82\u5FD7\u5F3A\u611F\u8C22\u4ED6\u7684\u8BDD\u3002</p><p>\u5230\u4E86\u67AA\u6BD9\u7684\u5730\u65B9\uFF0C\u56F4\u6EE1\u4E86\u89C2\u770B\u7684\u4EBA\uFF0C\u67AA\u54CD\u4E4B\u540E\uFF0C\u6BCF\u4E2A\u4EBA\u90FD\u77AA\u5927\u4E86\u773C\u775B\uFF0C\u53EA\u6709\u5468\u79C9\u6606\u5F53\u573A\u6655\u4E86\u8FC7\u53BB\u3002\u5927\u5BB6\u90FD\u56F4\u4E0A\u6765\u770B\uFF0C\u90A3\u4E2A\u6C34\u81EA\u6D41\u4E5F\u4E0A\u6765\u770B\uFF0C\u8FD9\u624D\u77E5\u9053\u6D82\u5FD7\u5F3A\u662F\u5468\u79C9\u6606\u7684\u670B\u53CB\uFF0C\u6240\u4EE5\u4ED6\u624D\u4F1A\u88AB\u5413\u6655\u8FC7\u53BB\u7684\uFF0C\u6C34\u81EA\u6D41\u4E5F\u9ED8\u9ED8\u5FF5\u53E8\u4E86\u5468\u79C9\u6606\u7684\u540D\u5B57\uFF0C\u5E76\u9644\u5728\u540C\u4F19\u8033\u7554\u8BF4\u4E86\u6D82\u5FD7\u5F3A\u5F53\u65F6\u8BF4\u8FC7\u7684\u8BDD\uFF0C\u4F3C\u4E4E\u548C\u5468\u79C9\u6606\u6709\u5173\u7CFB\u3002</p><p>\u4E54\u6625\u71D5\u6765\u627E\u5468\u79C9\u6606\uFF0C\u8BE2\u95EE\u5468\u79C9\u6606\u5728\u6CD5\u573A\u7684\u4E8B\u60C5\uFF0C\u4E54\u6625\u71D5\u4E00\u76F4\u559C\u6B22\u5468\u79C9\u6606\uFF0C\u4F46\u5468\u79C9\u6606\u5E76\u672A\u770B\u5728\u773C\u91CC\u3002\u5468\u6BCD\u8981\u53BB\u5012\u6CD4\u6C34\uFF0C\u5468\u79C9\u6606\u5FD9\u62A2\u7740\u6876\u8FC7\u6765\uFF0C\u4E54\u6625\u71D5\u4E3B\u52A8\u966A\u7740\u4E00\u8D77\u53BB\u3002\u5468\u6BCD\u81EA\u4ECE\u5468\u84C9\u79BB\u5F00\u4E4B\u540E\u5C31\u603B\u662F\u54ED\u6CE3\uFF0C\u73B0\u5728\u773C\u775B\u5DF2\u7ECF\u4F24\u7684\u5F88\u91CD\uFF0C\u770B\u4E0D\u6E05\u4E1C\u897F\u4E86\u3002</p><p>\u4E54\u6625\u71D5\u548C\u5468\u79C9\u6606\u6B63\u5728\u5012\u6CD4\u6C34\u7684\u65F6\u5019\uFF0C\u6C34\u81EA\u6D41\u548C\u540C\u4F19\u9A86\u58EB\u5BBE\u8DDF\u4E86\u8FC7\u6765\uFF0C\u4F46\u662F\u6C34\u81EA\u6D41\u770B\u5230\u4E54\u6625\u71D5\u540E\u6CA1\u6562\u4E0A\u524D\uFF0C\u4E54\u6625\u71D5\u66FE\u7ECF\u89C1\u4ED6\u548C\u6D82\u5FD7\u5F3A\u5728\u4E00\u8D77\u8FC7\u3002</p>',12),r=[o];function _(n,s){return p(),t("div",null,r)}const h=e(c,[["render",_],["__file","1.html.vue"]]);export{h as default};
