import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as t,f as a}from"./app.cd90e563.js";const c={},o=a('<h1 id="\u7B2C1\u96C6-\u5C0F\u5170\u82B1\u4E1C\u65B9\u9752\u82CD\u521D\u9047" tabindex="-1"><a class="header-anchor" href="#\u7B2C1\u96C6-\u5C0F\u5170\u82B1\u4E1C\u65B9\u9752\u82CD\u521D\u9047" aria-hidden="true">#</a> \u7B2C1\u96C6\uFF1A\u5C0F\u5170\u82B1\u4E1C\u65B9\u9752\u82CD\u521D\u9047</h1><p>\u4F20\u95FB\u4E2D\uFF0C\u6708\u65CF\u6708\u5C0A\u4E1C\u65B9\u9752\u82CD\u56E0\u4FEE\u4E60\u4E0A\u53E4\u79D8\u672F\uFF0C\u53D8\u6210\u4E86\u4E00\u4E2A\u6CA1\u6709\u4EFB\u4F55\u611F\u60C5\u7684\u602A\u7269\u3002\u4ED6\u624B\u5203\u751F\u7236\uFF0C\u593A\u53D6\u6708\u5C0A\u5B9D\u5EA7\uFF0C\u63A7\u5236\u5341\u4E07\u6708\u65CF\u58EB\u5175\uFF0C\u70E7\u6740\u62A2\u63A0\uFF0C\u4ED9\u754C\u6C34\u4E91\u5929\u3001\u6708\u65CF\u82CD\u76D0\u6D77\u3001\u4EBA\u95F4\u4E91\u68A6\u6CFD\u4E09\u754C\u5371\u5728\u65E6\u5915\u3002\u4ED9\u754C\u6218\u795E\u8D64\u6C34\u5973\u5B50\u4E3A\u62EF\u6551\u82CD\u751F\u4E0D\u60DC\u81EA\u7206\u5143\u795E\uFF0C\u4E0E\u4E1C\u65B9\u9752\u82CD\u540C\u5F52\u4E8E\u5C3D\uFF0C\u5C01\u5370\u4E86\u5341\u4E07\u6708\u65CF\u58EB\u5175\u3002\u7136\u800C\u4E1C\u65B9\u9752\u82CD\u7684\u5143\u795E\u4E0D\u6B7B\u4E0D\u706D\uFF0C\u4E00\u65E6\u590D\u6D3B\uFF0C\u5730\u72F1\u4E4B\u706B\u5C06\u518D\u6B21\u5E2D\u5377\u4E09\u754C\u3002\u4F20\u8BF4\u4E2D\uFF0C\u552F\u6709\u606F\u5C71\u795E\u5973\u624D\u80FD\u5316\u89E3\u8FD9\u573A\u6D69\u52AB\uFF0C\u4F46\u5979\u5374\u4E0D\u77E5\u6240\u8E2A\u3002</p><p>\u6C34\u4E91\u5929\u7389\u4EAC\uFF0C\u534E\u4E3D\u70AB\u76EE\uFF0C\u5341\u5206\u7F8E\u4E3D\u3002\u4ED9\u5973\u6C34\u5170\u82B1\u7F13\u7F13\u4ECE\u7761\u68A6\u4E2D\u9192\u6765\uFF0C\u671B\u7740\u5916\u9762\u7684\u67AB\u6811\u7A81\u7136\u60F3\u5230\u4E86\u4EC0\u4E48\uFF0C\u6025\u5FD9\u8D77\u8EAB\u8DD1\u4E86\u51FA\u53BB\u3002\u8FD9\u4E9B\u53F6\u5B50\u53EF\u4E0D\u662F\u666E\u901A\u7684\u53F6\u5B50\uFF0C\u800C\u662F\u547D\u7C3F\uFF0C\u6BCF\u4E00\u7247\u53F6\u5B50\u4E0A\u90FD\u5199\u7740\u4E00\u4E2A\u4EBA\u751F\u751F\u4E16\u4E16\u7684\u547D\u8FD0\u3002\u800C\u6C34\u5170\u82B1\u7684\u804C\u8D23\uFF0C\u5C31\u662F\u6574\u7406\u3001\u7167\u770B\u8FD9\u4E9B\u547D\u7C3F\uFF0C\u4F46\u4E0D\u4F1A\u5E72\u6270\u4EFB\u4F55\u4EBA\u7684\u547D\u8FD0\u3002</p><p>\u6C34\u4ED9\u82B1\u7684\u5E08\u7236\u53F8\u547D\uFF0C\u662F\u638C\u7BA1\u547D\u7C3F\u7684\u4E0A\u4ED9\u3002\u8981\u8BF4\u8D77\u4E24\u4EBA\u7684\u7F18\u5206\uFF0C\u8981\u4ECE\u8BB8\u4E45\u4E4B\u524D\u8BF4\u8D77\u3002\u90A3\u4F4D\u8C8C\u7F8E\u7684\u4E0A\u4ED9\uFF0C\u5927\u5927\u54A7\u54A7\u5750\u7740\u559D\u9152\uFF0C\u8138\u4E0A\u6EE1\u662F\u7EA2\u6655\uFF0C\u5634\u91CC\u7B11\u7740\u8BF4\u4E9B\u4EC0\u4E48\uFF0C\u9152\u74F6\u503E\u5012\u5979\u4E5F\u6D51\u7136\u4E0D\u89C9\u3002\u9152\u6C34\u6D12\u843D\u5230\u4E86\u4E00\u682A\u6C34\u5170\u82B1\u4E0A\uFF0C\u6D47\u574F\u4E86\u4ED9\u6839\uFF0C\u6240\u4EE5\u6C34\u5170\u82B1\u7684\u4FEE\u4E3A\u548C\u6CD5\u529B\u4E00\u76F4\u90FD\u5F88\u5DEE\u52B2\u3002</p><p>\u6559\u4F1A\u4E86\u6C34\u5170\u82B1\u4E00\u4E9B\u624B\u827A\u540E\uFF0C\u53F8\u547D\u4E0A\u4ED9\u5C31\u53BB\u4EBA\u95F4\u4E91\u6E38\u53BB\u4E86\uFF0C\u81F3\u4ECA\u5DF2\u6709\u4E94\u767E\u5E74\u3002\u4E0D\u8FC7\u6C34\u5170\u82B1\u4E5F\u81EA\u5F97\u5176\u4E50\uFF0C\u5C3D\u7BA1\u6709\u4E9B\u65F6\u5019\u611F\u5230\u6709\u4E9B\u5B64\u72EC\u3002\u81F3\u4E8E\u5176\u4ED6\u4ED9\u5973\uFF0C\u5BF9\u6C34\u5170\u82B1\u4F3C\u4E4E\u5E76\u4E0D\u5F85\u89C1\u3002\u4E5F\u8BB8\u662F\u56E0\u4E3A\u5979\u6253\u7B97\u53C2\u52A0\u4ED9\u8003\uFF0C\u53BB\u6D8C\u6CC9\u5BAB\uFF0C\u5411\u957F\u73E9\u4ED9\u541B\u62A5\u6551\u547D\u4E4B\u6069\u3002</p><p>\u6218\u795E\u957F\u73E9\u4E00\u56DE\u6765\uFF0C\u4FBF\u53BB\u62DC\u89C1\u4E86\u6C34\u4E91\u5929\u5E1D\u541B\u4E91\u4E2D\u541B\u3002\u53EA\u662F\u4ED6\u4E0D\u77E5\uFF0C\u4E3A\u4F55\u4E91\u4E2D\u541B\u4F1A\u8FD9\u4E48\u7740\u6025\u53EC\u81EA\u5DF1\u56DE\u6765\u3002\u6B64\u65F6\u660A\u5929\u5854\u5371\u673A\u56DB\u4F0F\uFF0C\u6697\u6D41\u6D8C\u52A8\uFF0C\u4F3C\u4E4E\u6B63\u9884\u544A\u7740\u4E00\u573A\u6D69\u52AB\u3002\u957F\u73E9\u8FD9\u624D\u77E5\u6653\uFF0C\u660A\u5929\u5854\u5F02\u52A8\uFF0C\u4E1C\u65B9\u9752\u82CD\u7684\u5143\u795E\u6B63\u51C6\u5907\u51B2\u7834\u5C01\u5370\u3002\u800C\u8FD9\u6B21\u4E91\u4E2D\u541B\u53EC\u4ED6\u56DE\u6765\uFF0C\u6B63\u662F\u8981\u52A0\u56FA\u5C01\u5370\u3002</p><p>\u8FD9\u5929\uFF0C\u51E0\u4F4D\u4ED9\u5973\u5230\u53F8\u547D\u6BBF\u8C03\u547D\u7C3F\uFF0C\u671F\u95F4\u8BF4\u8D77\u4E86\u957F\u73E9\u56DE\u6765\u7684\u4E8B\u60C5\u3002\u5C0F\u5170\u82B1\u542C\u95FB\u9AD8\u5174\u4E0D\u5DF2\uFF0C\u8D76\u5FD9\u53BB\u91C7\u6700\u597D\u7684\u82B1\u9001\u53BB\u6D8C\u6CC9\u5BAB\u3002\u770B\u5230\u957F\u73E9\u7684\u80CC\u5F71\uFF0C\u5C0F\u5170\u82B1\u5C31\u5DF2\u7ECF\u88AB\u8FF7\u5F97\u98A0\u4E09\u5012\u56DB\u3002\u6B63\u51C6\u5907\u5728\u4ED6\u9762\u524D\u9686\u91CD\u767B\u573A\uFF0C\u8C01\u6599\u7ADF\u4E00\u4E2A\u4E0D\u5C0F\u5FC3\u5361\u5728\u4E86\u6811\u679D\u4E0A\uFF0C\u5728\u4ED6\u9762\u524D\u6454\u4E86\u4E2A\u5927\u8DDF\u5934\u3002\u5C0F\u5170\u82B1\u5C0F\u5FC3\u7FFC\u7FFC\u5C06\u9C9C\u82B1\u9001\u7ED9\u957F\u73E9\uFF0C\u6FC0\u52A8\u7684\u548C\u4ED6\u9053\u8C22\u3002\u53EF\u5BF9\u65B9\u5E76\u4E0D\u8BB0\u5F97\u5F53\u521D\u7684\u4E8B\u60C5\uFF0C\u4E5F\u8BB8\u5BF9\u4ED6\u6765\u8BF4\u53EA\u662F\u4E00\u4EF6\u5BFB\u5E38\u4E4B\u4E8B\u3002</p><p>\u5C0F\u5170\u82B1\u4EF0\u9996\u633A\u80F8\u524D\u53BB\u62A5\u540D\u4ED9\u8003\uFF0C\u53EF\u5374\u906D\u5230\u4E86\u5176\u4ED6\u4ED9\u5973\u7684\u5632\u7B11\u3002\u90A3\u4E9B\u4ED9\u5973\u751A\u81F3\u6839\u672C\u4E0D\u8BB0\u5F97\u5979\u662F\u8C01\uFF0C\u53EA\u5F53\u662F\u4E00\u4E2A\u4E0D\u77E5\u5929\u9AD8\u5730\u539A\u7684\u5C0F\u82B1\u7CBE\u3002\u5C0F\u5170\u82B1\u671B\u7740\u90A3\u4E9B\u4ED9\u5973\uFF0C\u90D1\u91CD\u7684\u5411\u5979\u4EEC\u4ECB\u7ECD\u81EA\u5DF1\u7684\u540D\u53F7\uFF0C\u56E0\u4E3A\u5979\u4F1A\u72E0\u72E0\u6253\u8D25\u5979\u4EEC\u7684\u3002\u53EF\u662F\u5C0F\u5170\u82B1\u5FC3\u91CC\u4E5F\u53D1\u6101\uFF0C\u4FEE\u590D\u4ED9\u6839\u7684\u836F\u6750\u5B9E\u5728\u662F\u4E70\u4E0D\u8D77\u3002\u670B\u53CB\u4EEC\u5435\u5435\u95F9\u95F9\uFF0C\u53EF\u4E5F\u613F\u610F\u62FF\u51FA\u7075\u77F3\u652F\u6301\u5979\u3002</p><p>\u5C31\u5728\u5C0F\u5170\u82B1\u9AD8\u5174\u4E4B\u65F6\uFF0C\u957F\u73E9\u7684\u547D\u7C3F\u7A81\u7136\u6389\u4E86\u4E0B\u6765\uFF0C\u5979\u62FC\u5C3D\u5168\u529B\u4FEE\u590D\u53EF\u4E5F\u4E8E\u4E8B\u65E0\u8865\u3002\u800C\u8FD9\u4E5F\u5C31\u610F\u5473\u7740\uFF0C\u957F\u73E9\u4ECA\u665A\u6709\u5F62\u795E\u4FF1\u706D\u7684\u5371\u9669\u3002\u6B64\u65F6\uFF0C\u5929\u96F7\u6EDA\u6EDA\uFF0C\u4E4C\u4E91\u5BC6\u5E03\u3002\u4F17\u4ED9\u541B\u805A\u96C6\u660A\u5929\u5854\uFF0C\u4FEE\u590D\u5C01\u5370\u3002\u5FFD\u7136\uFF0C\u4E00\u9635\u70AB\u76EE\u7684\u5149\u4ECE\u5854\u91CC\u51B2\u51FA\uFF0C\u90A3\u4E9B\u7075\u529B\u4F4E\u7684\u5929\u5175\u88AB\u9707\u5F00\u3002\u957F\u73E9\u89C1\u72B6\u98DE\u5954\u4E0A\u524D\uFF0C\u53EF\u4E5F\u6CA1\u6709\u8D77\u5230\u4EFB\u4F55\u6548\u679C\u3002</p><p>\u5C0F\u5170\u82B1\u7740\u6025\u4E07\u5206\uFF0C\u5979\u4E0D\u7531\u5F97\u60F3\u5230\u4E86\u53F8\u547D\u66FE\u8BF4\u8FC7\uFF0C\u82E5\u662F\u6CC4\u9732\u4E86\u547D\u7C3F\u7684\u5929\u673A\uFF0C\u662F\u8981\u906D\u5230\u5929\u8C34\u7070\u98DE\u70DF\u706D\u7684\u3002\u53EF\u5C0F\u5170\u82B1\u4E0D\u80FD\u773C\u7741\u7741\u770B\u7740\u957F\u73E9\u6D88\u4EA1\uFF0C\u6240\u4EE5\u5979\u6234\u4E0A\u9762\u5177\uFF0C\u7528\u8089\u8EAB\u6321\u4F4F\u4ED6\u9762\u524D\u7684\u653B\u51FB\u3002\u5F85\u957F\u73E9\u53CD\u5E94\u8FC7\u6765\uFF0C\u5C0F\u5170\u82B1\u5DF2\u7ECF\u6389\u8FDB\u4E86\u5C01\u5370\u4E4B\u4E2D\u3002\u6B64\u65F6\u5C01\u5370\u5DF2\u6210\uFF0C\u4E91\u4E2D\u541B\u600E\u4E48\u53EF\u80FD\u518D\u6253\u5F00\u3002</p><p>\u6B64\u65F6\u5C0F\u5170\u82B1\u6025\u901F\u4E0B\u5760\uFF0C\u4E0D\u77E5\u80F8\u53E3\u4F55\u65F6\u95EA\u8000\u7740\u5149\u8292\uFF0C\u5979\u5FFD\u7136\u9002\u5E94\u4E86\u8FD9\u91CC\u3002\u5979\u88AB\u4E00\u80A1\u83AB\u540D\u7684\u529B\u91CF\u5438\u5F15\u5230\u4E1C\u65B9\u9752\u82CD\u7684\u8DDF\u524D\uFF0C\u751A\u81F3\u543B\u4E86\u4E0A\u53BB\u3002\u7B49\u5979\u9192\u6765\uFF0C\u53D1\u73B0\u81EA\u5DF1\u8EAB\u5904\u4E00\u7247\u96EA\u5730\u4E4B\u4E2D\uFF0C\u773C\u524D\u662F\u4E00\u68F5\u5E72\u67AF\u7684\u5927\u6811\u3002\u5C0F\u5170\u82B1\u8BD5\u63A2\u7684\u78B0\u4E86\u78B0\u6811\u5E72\uFF0C\u5E72\u67AF\u7684\u6811\u679D\u4E0A\u6389\u4E0B\u70B9\u70B9\u96EA\u6765\u3002\u5C0F\u5170\u82B1\u4E0D\u7531\u5F97\u6253\u4E86\u4E00\u4E2A\u55B7\u568F\uFF0C\u968F\u540E\u5C31\u53D1\u73B0\u81EA\u5DF1\u6765\u5230\u4E86\u4E00\u4E2A\u9ED1\u6697\u7684\u7A7A\u95F4\uFF0C\u524D\u9762\u5750\u7740\u7684\u5C11\u5973\u7ADF\u957F\u7740\u5979\u7684\u8138\u3002</p><p>\u5C0F\u5170\u82B1\u8BD5\u63A2\u7684\u8D70\u4E0A\u524D\u53BB\uFF0C\u53EF\u8C01\u77E5\u5C11\u5973\u7684\u773C\u91CC\u6EE1\u662F\u6740\u6C14\uFF0C\u5C06\u5979\u6254\u5230\u5730\u4E0A\u3002\u5C0F\u5170\u82B1\u540E\u77E5\u540E\u89C9\uFF0C\u81EA\u5DF1\u7ADF\u548C\u4E1C\u65B9\u9752\u82CD\u4EA4\u6362\u4E86\u8EAB\u4F53\u3002\u6B64\u65F6\u5C0F\u5170\u82B1\u8EAB\u4F53\u91CC\u85CF\u7740\u4E1C\u65B9\u9752\u82CD\u7684\u6CD5\u529B\uFF0C\u82E5\u662F\u5C3D\u529B\u5B9A\u80FD\u51B2\u7834\u5C01\u5370\u3002</p>',12),r=[o];function _(n,s){return p(),t("div",null,r)}const h=e(c,[["render",_],["__file","1.html.vue"]]);export{h as default};
