import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as a,c as e,f as r}from"./app.86bca16c.js";const h={},d=r('<h1 id="\u96F6\u6563\u9B3C\u6545\u4E8B" tabindex="-1"><a class="header-anchor" href="#\u96F6\u6563\u9B3C\u6545\u4E8B" aria-hidden="true">#</a> \u96F6\u6563\u9B3C\u6545\u4E8B</h1><h2 id="_1" tabindex="-1"><a class="header-anchor" href="#_1" aria-hidden="true">#</a> 1</h2><p>\u67D0\u6751\u6709\u4E2A\u6751\u6C11\uFF0C\u957F\u5F97\u8180\u5927\u8170\u5706\uFF0C\u813E\u6C14\u5F02\u5E38\u706B\u7206\uFF0C\u5F97\u7F6A\u8FC7\u6751\u91CC\u4E0D\u5C11\u4EBA\u3002</p><p>\u90A3\u4E00\u5E74\u590F\u5B63\uFF0C\u70ED\u6D6A\u6ED4\u5929\uFF0C\u4E3A\u4E86\u907F\u6691\uFF0C\u6751\u6C11\u4E70\u4E00\u53E3\u5927\u6C34\u7F38\u653E\u5728\u9662\u5B50\u91CC\uFF0C\u6BCF\u5230\u4E2D\u5348\uFF0C\u4FBF\u7ED9\u6C34\u7F38\u704C\u4E95\u6C34\uFF0C\u8212\u8212\u670D\u670D\u6D17\u4E0A\u4E00\u573A\u3002</p><p>\u6709\u4E00\u5929\u4E2D\u5348\uFF0C\u5929\u6C14\u4E00\u5982\u65E2\u5F80\u7684\u70ED\uFF0C\u6751\u6C11\u63D0\u6876\u6253\u6C34\uFF0C\u4ED6\u5728\u6C34\u4E95\u548C\u6C34\u7F38\u4E4B\u95F4\u7684\u4E00\u6761\u8DEF\u4E0A\u6765\u6765\u5F80\u5F80\uFF0C\u5FD9\u6D3B\u534A\u4E2A\u65F6\u8FB0\uFF0C\u7EC8\u4E8E\u704C\u6EE1\u6C34\u7F38\u3002</p><p>\u7136\u800C\uFF0C\u8C01\u4E5F\u6CA1\u60F3\u5230\uFF0C\u5F53\u4ED6\u4E00\u4E1D\u4E0D\u6302\u8E72\u8FDB\u6C34\u7F38\uFF0C\u6E05\u6F88\u89C1\u5E95\u7684\u4E95\u6C34\u77AC\u95F4\u7329\u7EA2\u4E86\u3002\u53EA\u542C\u201C\u554A\u201D\u5730\u4E00\u58F0\u60E8\u53EB\uFF0C\u6751\u6C11\u6323\u51FA\u6C34\u7F38\uFF0C\u4ED6\u6D51\u8EAB\u7684\u76AE\u80A4\u9CDE\u7247\u4E00\u6837\u6389\u843D\uFF0C\u4E0D\u4E00\u4F1A\u5C31\u762B\u5750\u5728\u6CE5\u5730\u4E0A\uFF0C\u53D8\u6210\u4E86\u4E00\u53EA\u5265\u76AE\u7684\u201C\u706B\u9F99\u679C\u201D\u3002</p><p>\u539F\u6765\uFF0C\u6709\u4EBA\u5077\u5077\u5728\u6C34\u7F38\u91CC\u53C2\u8FDB\u4E86\u6C22\u6C1F\u9178\u3002</p><h2 id="_2" tabindex="-1"><a class="header-anchor" href="#_2" aria-hidden="true">#</a> 2</h2><p>\u5C0F\u65F6\u5019\uFF0C\u6211\u5BB6\u6709\u4E2A\u5BF9\u5934\uFF0C\u90A3\u4E2A\u5BF9\u5934\u5C31\u4F4F\u6211\u5BB6\u9662\u540E\uFF0C\u4ED6\u51ED\u501F\u5BB6\u65CF\u4EBA\u591A\u52BF\u4F17\uFF0C\u5360\u8FC7\u6211\u4EEC\u4E0D\u5C11\u4FBF\u5B9C\u3002</p><p>\u66F4\u91CD\u8981\u7684\u662F\uFF0C\u6211\u53D4\u53D4\u548C\u4ED6\u6253\u8FC7\u67B6\uFF0C\u4ED6\u4E0B\u624B\u53C8\u9ED1\u53C8\u72E0\uFF0C\u53D4\u53D4\u5C06\u4ED6\u6253\u5012\u5728\u5730\uFF0C\u51C6\u5907\u79BB\u5F00\u7684\u65F6\u5019\uFF0C\u4ED6\u80CC\u540E\u5077\u88AD\uFF0C\u4E00\u68CD\u5B50\u6253\u65AD\u53D4\u53D4\u7684\u817F\u9AA8\uFF0C\u4ECE\u90A3\u4EE5\u540E\uFF0C\u53D4\u53D4\u8D70\u8DEF\u4E0D\u65B9\u4FBF\u4E86\uFF0C\u8EAB\u5B50\u6B6A\u6B6A\u659C\u659C\uFF0C\u4E00\u7638\u4E00\u62D0\uFF0C\u518D\u4E5F\u4E0D\u662F\u4ED6\u7684\u5BF9\u624B\u3002</p><p>2007\u5E74\u590F\u5929\uFF0C\u6211\u53D4\u53D4\u53BB\u4E16\u4E86\uFF0C\u533B\u751F\u8BF4\u662F\u80BE\u8870\u7AED\uFF0C\u7236\u4EB2\u8BF4\u4ED6\u662F\u88AB\u4EC7\u6068\u6467\u6BC1\u7684\u3002</p><p>\u53D4\u53D4\u53BB\u4E16\u7684\u524D\u4E09\u4E2A\u6708\uFF0C\u6211\u5BB6\u7684\u5BF9\u5934\u4E5F\u6B7B\u4E86\u3002\u4ED6\u540C\u6837\u5F97\u75C5\u6B7B\u7684\uFF0C\u53D4\u53D4\u4E0D\u8FD9\u4E48\u8BA4\u4E3A\uFF0C\u4ED6\u65F6\u4E0D\u65F6\u8F7B\u58F0\u8F7B\u8BED\u544A\u8BC9\u6211\uFF1A\u90A3\u4E2A\u5BF9\u5934\u662F\u88AB\u6211\u6740\u6B7B\u7684 \u3002</p><p>\u8BF4\u5B9E\u8BDD\uFF0C\u6211\u4E0D\u4FE1\uFF0C\u53D4\u53D4\u662F\u6B8B\u75BE\u4EBA\uFF0C\u8DEF\u90FD\u8D70\u4E0D\u7A33\uFF0C\u600E\u4E48\u80FD\u6740\u4EBA\uFF0C\u5BF9\u65B9\u8FD8\u662F\u4E2A\u5F88\u5389\u5BB3\u7684\u4EBA\u3002</p><p>\u53D4\u53D4\u8BF4\uFF0C\u4E0D\u4E45\u524D\uFF0C\u4ED6\u505A\u4E86\u4E00\u4E2A\u5F02\u5E38\u771F\u5B9E\u7684\u68A6\uFF0C\u68A6\u5883\u4E2D\u7684\u4ED6\u662F\u4E2A\u5B8C\u7F8E\u65E0\u7F3A\u7684\u4EBA\uFF0C\u8EAB\u4F53\u5065\u58EE\uFF0C\u56DB\u80A2\u4FF1\u5168\uFF0C\u72B9\u5982\u5E74\u8F7B\u7684\u65F6\u5019\u4E00\u6837\u3002</p><p>\u6062\u590D\u5065\u5EB7\u7684\u4ED6\uFF0C\u7B2C\u4E00\u4E2A\u60F3\u5230\u7684\u5C31\u662F\u62A5\u4EC7\uFF0C\u4EE5\u524D\u6CA1\u80FD\u529B\uFF0C\u73B0\u5728\u6709\u80FD\u529B\u4E86\uFF0C\u4ED6\u8981\u628A\u90A3\u4E2A\u7ED9\u4ED6\u6240\u6709\u75DB\u82E6\u7684\u6E90\u5934\u8FDE\u6839\u62D4\u6389\u3002</p><p>\u6284\u8D77\u4E00\u628A\u6740\u732A\u5200\uFF0C\u6C14\u51B2\u51B2\uFF0C\u4ED6\u5C31\u671D\u6211\u5BB6\u9662\u540E\u5954\u4E86\uFF0C\u901F\u5EA6\u5F88\u5FEB\uFF0C\u77AC\u95F4\u5C31\u51B2\u8FDB\u5BF9\u5934\u7684\u5BB6\u91CC\u3002</p><p>\u65F6\u503C\u6B63\u5348\uFF0C\u5BF9\u5934\u548C\u8001\u5A46\u5B69\u5B50\u56F4\u7740\u996D\u684C\u5403\u996D\uFF0C\u770B\u5230\u53D4\u53D4\u6525\u7740\u5200\u95EF\u8FDB\u6765\uFF0C\u4ED6\u6367\u7897\u7684\u624B\u90FD\u54C6\u55E6\u4E86\u3002</p><p>\u5BF9\u5934\u7D27\u5F20\u5730\u95EE\uFF1A\u4F60\u60F3\u5E72\u6CA1\u4EC0\u4E48\uFF1F</p><p>\u53D4\u53D4\u6293\u4F4F\u4ED6\u7684\u5DE6\u80A9\u8BA4\u771F\u7684\u8BF4\uFF1A\u6345\u6B7B\u4F60\uFF01</p><p>\u7D27\u63A5\u7740\uFF0C\u53D4\u53D4\u624B\u81C2\u53CD\u590D\u62BD\u9001\uFF0C\u5728\u5BF9\u5934\u809A\u5B50\u4E0A\u8FDE\u6345\u4E03\u4E0B\u3002</p><p>\u6345\u5B8C\u4E03\u4E0B\uFF0C\u4ED6\u5934\u4E5F\u4E0D\u56DE\u8F6C\u8EAB\u5C31\u8D70\uFF0C\u4ED6\u60F3\uFF0C\u6345\u4E86\u4E03\u5200\uFF0C\u8C01\u80FD\u6297\u4F4F\u4E03\u5200\uFF0C\u5FC5\u6B7B\u65E0\u7591\u3002\u7136\u800C\u521A\u8E0F\u4E0A\u95E8\u69DB\uFF0C\u53D4\u53D4\u60CA\u8BB6\u7684\u53D1\u73B0\uFF0C\u4ED6\u624B\u4E0A\u6CA1\u6709\u8840\u3002</p><p>\u88AB\u6345\u4E86\u5200\u5B50\uFF0C\u600E\u4E48\u4F1A\u6CA1\u6709\u8840\uFF1F\u96BE\u9053\uFF1F\u96BE\u9053\u6CA1\u6345\u5230\uFF1F\u60F3\u5230\u8FD9\u91CC\uFF0C\u95E8\u69DB\u4E00\u7ECA\uFF0C\u53D4\u53D4\u5FFD\u5730\u9192\u4E86\uFF0C\u810A\u80CC\u51B7\u6C57\u6D94\u6D94\uFF0C\u539F\u6765\u662F\u4E00\u573A\u68A6\u3002</p><p>\u8981\u8BF4\u662F\u4E00\u573A\u68A6\uFF0C\u5176\u5B9E\u53C8\u4E0D\u662F\u68A6\uFF0C\u7B2C\u4E8C\u5929\u65E9\u6668\u7238\u7238\u6765\u7ED9\u53D4\u53D4\u62A5\u559C\uFF0C\u7238\u7238\u8BF4\u5BB3\u4F60\u90A3\u4E2A\u5BF9\u5934\u6B7B\u4E86\u3002</p><p>\u53D4\u53D4\u8BE7\u5F02\u9053\uFF1A\u600E\u4E48\u6B7B\u7684\uFF1F</p><p>\u7238\u7238\u8BF4\uFF1A\u80C3\u6E83\u75A1\uFF0C\u809A\u5B50\u70C2\u4E86\u4E03\u4E2A\u6D1E\u3002</p><h2 id="_3" tabindex="-1"><a class="header-anchor" href="#_3" aria-hidden="true">#</a> 3</h2><p>\u5C0F\u65F6\u5019\uFF0C\u6211\u5BB6\u524D\u9762\u662F\u4E00\u6D3C\u6CE5\u6C34\u5858\uFF0C\u6CE5\u6C34\u5858\u91CC\u7684\u6C34\u53C8\u810F\u53C8\u9EC4\u3002</p><p>\u6211\u8BB0\u5F97\u90A3\u662F\u590F\u79CB\u4EA4\u9645\u6536\u7A3B\u8C37\u7684\u65F6\u5019\uFF0C\u6751\u91CC\u7684\u5927\u4EBA\u4EEC\u4E0B\u5730\u5272\u7A3B\u5B50\uFF0C\u7559\u4E0B\u61F5\u61F5\u61C2\u61C2\u7684\u5B69\u5B50\u770B\u5BB6\u3002</p><p>\u5B69\u5B50\u559C\u6B22\u73A9\uFF0C\u95F2\u4E0D\u4F4F\uFF0C\u8FD9\u662F\u4E0E\u751F\u4FF1\u6765\u7684\u5929\u6027\u3002\u5927\u4EBA\u4EEC\u4E00\u8D70\uFF0C\u6211\u5C31\u9501\u4E0A\u9662\u95E8\uFF0C\u6328\u5BB6\u5524\u73A9\u4F34\u53BB\u4E86\u3002</p><p>\u90A3\u5929\u771F\u662F\u8BE1\u5F02\uFF0C\u6211\u8FDE\u5524\u4E03\u516B\u5BB6\uFF0C\u95E8\u5185\u9759\u6084\u6084\u7684\uFF0C\u4E00\u4E2A\u56DE\u5E94\u4E5F\u6CA1\u6709\u3002\u5B69\u5B50\u4EEC\u90FD\u4E0D\u89C1\u4E86\u3002</p><p>\u6CA1\u4EBA\u966A\u73A9\uFF0C\u6211\u5782\u5934\u4E27\u6C14\u56DE\u5BB6\uFF0C\u8D70\u5230\u6CE5\u6C34\u5858\u7684\u65F6\u5019\uFF0C\u6211\u8FDC\u8FDC\u5730\u671B\u89C1\uFF0C\u4E00\u4E2A\u957F\u53D1\u5973\u4EBA\u9886\u7740\u4E00\u7FA4\u5C0F\u5B69\u5B50\u671D\u8FD9\u8FB9\u8D70\u6765\u3002</p><p>\u5973\u4EBA\u7684\u6B65\u4F10\u5F88\u5947\u602A\uFF0C\u626D\u626D\u634F\u634F\u3002\u76F4\u5230\u8D70\u8FDB\uFF0C\u6211\u624D\u53D1\u73B0\uFF0C\u5979\u6839\u672C\u6CA1\u6709\u817F\uFF0C\u8EAB\u4F53\u4E0B\u9762\u8FDE\u7740\u7684\u7ADF\u7136\u662F\u4E00\u6761\u86C7\u5C3E\u5DF4\u3002</p><p>\u8EAB\u540E\u7684\u5B69\u5B50\u6211\u90FD\u8BA4\u8BC6\uFF0C\u4E94\u4E2A\u5B69\u5B50\u4ECE\u5C0F\u548C\u6211\u73A9\u60EF\u4E86\u7684\uFF0C\u542D\u4E00\u58F0\uFF0C\u95ED\u4E0A\u773C\u775B\u4E5F\u80FD\u731C\u51FA\u8C01\u662F\u8C01\u3002</p><p>\u5F53\u65F6\u6211\u7684\u810A\u80CC\u6CA1\u6765\u7531\u53D1\u51C9\uFF0C\u6211\u4E00\u8FB9\u770B\u7740\u957F\u53D1\u5973\u4EBA\u4E00\u8FB9\u6162\u6162\u671D\u540E\u817F\uFF0C\u548C\u5979\u59CB\u7EC8\u4FDD\u6301\u5341\u4E03\u516B\u7C73\u7684\u8DDD\u79BB\u3002</p><p>\u5973\u4EBA\u8D70\u5230\u6CE5\u6C34\u5858\u5C31\u505C\u4E0B\uFF0C\u5979\u547D\u4EE4\u4E94\u4E2A\u5B69\u5B50\u4ECE\u9AD8\u5230\u77EE\u6392\u6210\u961F\uFF0C\u5979\u7ED9\u4ED6\u4EEC\u634F\u6CE5\u4EBA\u73A9\u3002</p><p>\u771F\u7684\uFF0C\u5973\u4EBA\u7684\u624B\u5F88\u7075\u5DE7\uFF0C\u5979\u634F\u51FA\u7684\u6CE5\u4EBA\u6829\u6829\u5982\u751F\uFF0C\u7B80\u76F4\u50CF\u771F\u7684\u4E00\u6837\u3002</p><p>\u6211\u539F\u4EE5\u4E3A\u634F\u597D\u4E4B\u540E\u5979\u4F1A\u9001\u7ED9\u8EAB\u540E\u7684\u5B69\u5B50\u4EEC\uFF0C\u8C01\u77E5\u9053\uFF0C\u5979\u521A\u4E00\u634F\u5B8C\u6CE5\u4EBA\u5C31\u62AC\u624B\u671D\u6C34\u5858\u4E00\u7529\uFF0C\u90A3\u53EA\u6CE5\u4EBA\u201C\u6251\u901A\u201D\u843D\u8FDB\u6CE5\u6C34\u91CC\u3002</p><p>\u6C34\u82B1\u5F02\u5E38\u7684\u5927\u3002</p><p>\u63A5\u4E0B\u6765\uFF0C\u5973\u4EBA\u9646\u7EED\u53C8\u634F\u4E86\u56DB\u4E2A\u6CE5\u4EBA\uFF0C\u4E00\u5982\u7B2C\u4E00\u4E2A\u6CE5\u4EBA\u90A3\u6837\uFF0C\u4F5C\u54C1\u5B8C\u6210\u90A3\u4E00\u523B\uFF0C\u5979\u7ACB\u5373\u5C06\u5B83\u4EEC\u6295\u8FDB\u6CE5\u6C34\u5858\u91CC\u3002</p><p>\u8FC7\u4E86\u4E0D\u4E45\uFF0C\u5973\u4EBA\u626D\u626D\u634F\u634F\u6E38\u8D70\u4E86\uFF0C\u6211\u75F4\u75F4\u5446\u5446\u770B\u5979\uFF0C\u76F4\u5230\u5979\u7684\u8EAB\u4F53\u6D88\u5931\u5728\u6811\u6797\u91CC\uFF0C\u6211\u624D\u53D1\u73B0\uFF0C\u521A\u624D\u90A3\u4E94\u4E2A\u5B69\u5B50\u4E0D\u89C1\u4E86\u3002</p><p>\u4E94\u4E2A\u5B69\u5B50\u5728\u6C60\u5858\u91CC\uFF0C\u8FD9\u662F\u6211\u540E\u6765\u77E5\u9053\u7684\uFF0C\u90A3\u65F6\u5168\u6751\u7684\u5927\u4EBA\u5954\u8D70\u5BFB\u627E\uFF0C\u7FFB\u904D\u6751\u5B50\u6BCF\u4E2A\u62D0\u89D2\uFF0C\u5C31\u662F\u4E0D\u89C1\u4ED6\u4EEC\u7684\u8E2A\u5F71\u3002\u6709\u4EBA\u60F3\u5230\u4E86\u5931\u8DB3\u843D\u6C34\uFF0C\u4ED6\u62FF\u7740\u6E14\u7F51\u6CBF\u6CE5\u6C34\u5858\u629B\u6492\uFF0C\u6492\u4E86\u5F88\u4E45\u5F88\u4E45\uFF0C\u7EC8\u4E8E\uFF0C\u4E94\u4E2A\u5B69\u5B50\u8BA9\u4ED6\u7F51\u4E86\u51FA\u6765\u3002</p><p>\u5B69\u5B50\u5168\u90E8\u65AD\u6C14\u4E86\uFF0C\u4ED6\u4EEC\u7684\u9F3B\u5B54\u3001\u8033\u773C\u3001\u5634\u5DF4\u3001\u773C\u775B\u91CC\u585E\u6EE1\u6C61\u6CE5\uFF0C\u8EAB\u4E0A\u7070\u810F\u516E\u516E\u7684\u3002\u6211\u7AD9\u5728\u8FDC\u5904\u9759\u9759\u5730\u770B\u7740\u5C38\u4F53\uFF0C\u8D8A\u770B\u8D8A\u89C9\u5F97\u8BE1\u5F02\uFF0C\u4ED6\u4EEC\u7684\u6A21\u6837\u7279\u50CF\u5973\u4EBA\u4E22\u8FDB\u6C60\u5858\u7684\u6CE5\u4EBA\u3002</p><h2 id="\u5927\u53A6\u6709\u9B3C" tabindex="-1"><a class="header-anchor" href="#\u5927\u53A6\u6709\u9B3C" aria-hidden="true">#</a> \u5927\u53A6\u6709\u9B3C</h2><p>\u6709\u4E9B\u5927\u53A6\uFF0C\u56E0\u4E3A\u56DB\u5B57\u4E0D\u5409\u5229\uFF0C\u6240\u4EE5\u6CA1\u6709\u56DB\u697C\uFF0C\u5C0F\u65F6\u5019\uFF0C\u6211\u5C31\u662F\u4F4F\u5728\u90A3\u79CD\u5927\u53A6\u7684\u4E94\u697C\u3002\u6709\u4E00\u6B21\uFF0C\u6211\u653E\u5B66\u56DE\u5BB6\uFF0C\u5728\u5750\u7535\u68AF\u65F6\uFF0C\u7535\u68AF\u7A81\u7136\u5728\u4E09\u697C\u81F3\u56DB\u697C\u505C\u4E86\u4E0B\u6765\uFF0C\u7136\u540E\u7535\u68AF\u6162\u6162\u7684\u6253\u5F00\u4E86\u3002\u6211\u671B\u51FA\u7535\u68AF\uFF0C\u770B\u89C1\u56DB\u697C\u7684\u5B57\u6837\uFF0C\u7ACB\u523B\u5173\u4E86\u7535\u68AF\u3002\u5230\u5BB6\u662F\uFF0C\u6211\u544A\u8BC9\u5988\u5988\uFF0C\u5988\u5988\u8BF4\u662F\u6211\u770B\u9519\u4E86\uFF0C\u4F46\u662F\u6211\u660E\u660E\u770B\u5230\u56DB\u697C\u7684\u3002\u7B2C\u4E8C\u5929\uFF0C\u5168\u68AF\u505C\u7535\uFF0C\u6240\u4EE5\u6211\u8D70\u697C\u68AF\u56DE\u5BB6\uFF0C\u8FC7\u4E86\u4E09\u697C\uFF0C\u6211\u7EE7\u7EED\u8D70\uFF0C\u4F46\u4E0A\u5C42\u7ADF\u7136\u662F\u56DB\u697C\u3002\u6211\u662F\u771F\u7684\u8D70\u5230\u4E86\u56DB\u697C\u4E86\u5417?\u6211\u6253\u5F00\u56DB\u697C\u7684\u9632\u70DF\u95E8\u4E00\u770B\uFF0C\u770B\u89C1\u4E86\u4E00\u4E2A\u5973\u5C38\u6302\u5728\u6C34\u7BA1\u4E0A\u3002</p><h2 id="\u5395\u6240\u91CC\u7684\u8001\u5A46\u5A46" tabindex="-1"><a class="header-anchor" href="#\u5395\u6240\u91CC\u7684\u8001\u5A46\u5A46" aria-hidden="true">#</a> \u5395\u6240\u91CC\u7684\u8001\u5A46\u5A46</h2><p>\u8BB8\u591A\u5B66\u6821\u591A\u662F\u4E71\u846C\u5C97\u6216\u662F\u5211\u573A\u7684\u540E\u8EAB,\u56E0\u6B64\u6709\u8BB8\u591A\u6050\u6016\u7684\u4F20\u95FB\u6D41\u4F20\u5728\u5E08\u751F\u4E4B\u95F4......</p><p>\u4F4D\u4E8E\u9AD8\u96C4\u7684\u4E00\u4E2A\u5C0F\u5B66,\u662F\u4E00\u6240\u6821\u53F2\u76F8\u5F53\u957F\u4E45\u7684\u5B66\u6837.\u6709\u4E00\u6392\u5395\u6240\u5EA7\u843D\u5728\u6821\u533A\u7684\u6700\u540E\u65B9,\u9664\u4E86\u4E00\u4E8C\u5E74\u7EA7\u7684\u5C0F\u670B\u53CB\u5916,\u6CA1\u6709\u5176\u5B83\u5E74\u7EA7\u7684\u5E08\u751F\u4F7F\u7528....\u603B\u662F\u5F25\u6F2B\u7740\u4E00\u80A1\u9634\u68EE\u68EE\u7684\u6C14\u606F.\u800C\u7B2C\u4E09\u95F4\u5395\u6240\u4E00\u76F4\u662F\u6DF1\u9501\u7740\u7684.</p><p>\u4E00\u5929\u4E0B\u5348,\u4E00\u4E2A\u9AD8\u5E74\u7EA7\u7684\u7537\u751F\u6025\u7740\u4E0A\u5927\u53F7,\u6B63\u597D\u6BCF\u95F4\u5395\u6240\u90FD\u6709\u4EBA,\u4ED6\u5B9E\u5728\u662F\u5FCD\u4E0D\u4F4F\u4E86,\u5C31\u7528\u529B\u62C9\u5F00\u7B2C\u4E09\u95F4\u7684\u95E8....\u8BF4\u4E5F\u5947\u602A,\u5E73\u5E38\u600E\u4E48\u62C9\u4E5F\u62C9\u4E0D\u5F00\u7684\u95E8,\u4F46\u4ECA\u5929\u600E\u4E48....\u7BA1\u4ED6\u7684,\u8D76\u5FEB\u89E3\u51B3\u518D\u8BF4....\u6B63\u5F53\u4ED6\u677E\u53E3\u6C14\u60F3\u5927\u558A\u4E00\u58F0\u75DB\u5FEB\u65F6,\u5E95\u4E0B\u5FFD\u7136\u6709\u4E00\u79CD\u51B0\u51B7\u7684\u611F\u89C9....\u4ED6\u731B\u7136\u5F80\u4E0B\u4E00\u770B....\u5929\u554A!\u4E00\u53EA\u67AF\u7626\u7684\u624B\u4ECE\u4E0B\u9762\u4F38\u51FA\u6765,\u4ED6\u5927\u53EB\u4E00\u58F0,\u4ECE\u53E3\u888B\u91CC\u62FF\u51FA\u4E00\u4E2A\u5C0F\u5200\u5F80\u90A3\u53EA\u602A\u624B\u4E0A\u5212\u4E86\u4E00\u5200\u4E4B\u540E,\u9A6C\u4E0A\u51B2\u4E86\u51FA\u53BB,\u81EA\u6B64\u4EE5\u540E\u4ED6\u518D\u4E5F\u4E0D\u6562\u518D\u8E0F\u8FDB\u90A3\u95F4\u5395\u6240\u4E00\u6B65.</p><p>\u8FC7\u4E86\u5F88\u4E45,\u8FD9\u4EF6\u4E8B\u6E10\u6E10\u5728\u90A3\u4F4D\u9AD8\u5E74\u7EA7\u5B66\u751F\u7684\u8111\u4E2D\u6DE1\u5FD8,\u6709\u4E00\u5929,\u4ED6\u4E0E\u4E09\u4E94\u4E2A\u597D\u53CB\u5728\u90A3\u6392\u5395\u6240\u9644\u8FD1\u7684\u7BEE\u7403\u573A\u6253\u7403,\u4E00\u4E2A\u5F80\u53CD\u65B9\u5411\u7684\u7403\u7ADF\u8F6C\u4E2A\u8EAB\u98DE\u8FDB\u4E86\u5395\u6240\u91CC.\u540C\u5B66\u4EEC\u602A\u4ED6\u4E71\u4F20,\u4FBF\u53EB\u4ED6\u8D76\u7D27\u53BB\u628A\u7403\u6361\u56DE\u6765.\u4ED6\u5634\u91CC\u5495\u54DD\u7740\u76F4\u8FDB\u5395\u6240.\u8FDC\u8FDC\u770B\u89C1\u4E00\u4E2A\u8001\u5A46\u5A46\u62FF\u7740\u90A3\u4E2A\u7403\u4ECE\u5395\u6240\u8D70\u4E86\u51FA\u6765,\u4ED6\u5C0F\u8DD1\u6B65\u5230\u8001\u5A46\u5A46\u90A3,\u60F3\u62FF\u56DE\u90A3\u4E2A\u7403....\u597D\u5947\u602A!\u8001\u5A46\u5A46\u7684\u8138\u59CB\u7EC8\u6CA1\u6709\u62AC\u8D77\u6765\u8FC7,\u4F46\u5979\u624B\u80CC\u4E0A\u7684\u5200\u75D5\u5438\u5F15\u4F4F\u4E86\u4ED6\u7684\u76EE\u5149,\u4ED6\u95EE:&quot;\u8001\u5A46\u5A46,\u60A8\u7684\u624B\u80CC\u4E0A\u600E\u4E48\u6709\u5200\u75D5\u554A.&quot;\u53EA\u89C1\u8001\u5A46\u5A46\u7F13\u7F13\u5730\u62AC\u8D77\u5934\u6765,\u5F20\u5927\u773C\u775B\u77AA\u7740\u4ED6,\u5E72\u7B11\u4E24\u58F0\u540E\u8BF4:&quot;\u90A3\u662F\u88AB\u4F60\u5272\u7684\u554A,\u4F60\u5FD8\u4E86\u5417?&quot;\u8BED\u6BD5\u4FBF\u5F20\u7259\u821E\u722A\u7684\u5411\u4ED6\u6251\u53BB.\u4ED6\u54C7\u7684\u5927\u53EB\u4E00\u58F0\u6655\u4E86\u8FC7\u53BB.</p><p>\u636E\u8BF4,\u90A3\u4F4D\u9AD8\u5E74\u7EA7\u7684\u540C\u5B66\u7ECF\u8FC7\u90A3\u4E48\u4E00\u5413\u4E4B\u540E,\u53D8\u5F97\u6709\u70B9\u75F4\u5446,\u800C\u90A3\u4E00\u6392\u5395\u6240\u4E0D\u4E45\u540E\u4E5F\u62C6\u9664\u4E86.</p><h2 id="\u624B" tabindex="-1"><a class="header-anchor" href="#\u624B" aria-hidden="true">#</a> \u624B</h2><p>\u963F\u65B9\u662F\u4E00\u4E2A\u5927\u6392\u6321\u7684\u8001\u677F\uFF0C\u4EE5\u524D\u4ED6\u7684\u751F\u610F\u4E0D\u662F\u5F88\u597D\uFF0C\u4F46\u662F\u81EA\u4ECE\u5F97\u5230\u4E86\u4E00\u4F4D\u9AD8\u4EBA\u7684\u6307\u70B9\u540E\uFF0C\u4ED6\u7684\u751F\u610F\u4E00\u4E0B\u5B50\u5C31\u7EA2\u706B\u8D77\u6765\u4E86.\u4E2A\u6027\u662F\u9171\u9E21\u722A\uFF0C\u4F46\u4ED6\u6BCF\u4E00\u5929\u90FD\u5511\u662F\u9650\u91CF\u4F9B\u5E94\u5341\u4EFD\uFF0C\u8C01\u6765\u4E86\u4E5F\u6CA1\u7684\u591A.\u8FD9\u53EF\u82E6\u4E86\u6211\u8FD9\u4E2A\u98DF\u5BA2\u4E86\uFF0C\u6709\u65F6\u5019\u53BB\u665A\u4E86\uFF0C\u5C31\u6CA1\u4E86\uFF0C\u90A3\u4E00\u5929\u6211\u662F\u7761\u90FD\u7761\u4E0D\u7740\uFF0C\u5C31\u4E3A\u4E86\u90A3\u4E00\u7897\u9E21\u722A\uFF0C\u8FD9\u4F46\u662F\u8BF4\u51FA\u53BB\u90FD\u6CA1\u6709\u610F\u601D.\u800C\u4E14\u4ED6\u6709\u4E00\u4E2A\u602A\u6BDB\u75C5\uFF0C\u4ED6\u7684\u53A8\u623F\u5468\u56F4\u90FD\u662F\u7528\u9ED1\u5E03\u7F69\u7740\u7684.\u6CA1\u6709\u4EBA\u660E\u767D\u4ED6\u662F\u600E\u6837\u505A\u7684\u83DC\u7684\uFF0C\u6700\u5947\u602A\u7684\u662F\uFF0C\u6211\u4ECE\u6765\u4E5F\u6CA1\u6709\u770B\u89C1\u4ED6\u5411\u8C01\u8D2D\u8FC7\u9E21\u722A\uFF0C\u4ED6\u4E5F\u6CA1\u6709\u9E21.\u90A3\u4ED6\u7684\u539F\u6599\u662F\u600E\u6837\u6765\u7684\u5462</p><p>\u90A3\u5929\u6211\u5B9E\u5728\u662F\u5FCD\u4E0D\u4F4F\u4E86\uFF0C\u5C31\u6084\u6084\u5730\u8EB2\u5728\u4E86\u4ED6\u7684\u5C4B\u9876\u4E0A\uFF0C\u6380\u5F00\u4E86\u5C4B\u74E6\u7684\u4E00\u89D2\uFF0C\u5FC3\u60F3\u5B66\u5230\u4E86\u6211\u5C31\u81EA\u5DF1\u505A.\u6211\u4ECE\u7EC6\u7F1D\u770B\u5230\uFF0C\u90A3\u771F\u662F\u4E00\u8F88\u5B50\u4E5F\u5FD8\u4E0D\u4E86\u7684\u60C5\u666F\uFF0C\u6211\u770B\u5230\u4E86\u53EA\u624B.\u90A3\u662F\u4EBA\u624B.\u8FD8\u8FDE\u5728\u4EBA\u7684\u8EAB\u4E0A\u7684\u624B\uFF0C\u4F46\u662F\u5DF2\u7ECF\u4E0D\u5168\u4E86\uFF0C\u90A3\u4E2A\u4EBA\u8FD8\u6D3B\u7740\uFF0C\u6211\u770B\u5230\u4ED6\u7684\u8138\u5728\u626D\u66F2\uFF0C\u4F46\u662F\u53EB\u4E0D\u51FA\u6765\uFF0C\u4ED6\u5168\u8EAB\u53EA\u662F\u76AE\u5305\u9AA8\u5934\uFF0C\u4F46\u662F\u624B\u5374\u662F\u8089\u8089\u7684\uFF0C\u90A3\u53EA\u624B\u662F\u88AB\u9489\u5728\u5899\u4E0A\u7684\uFF0C\u7070\u9EC4\u8272\u7684\uFF0C\u63BA\u7740\u4E00\u4E1D\u8840\u4E1D\uFF0C\u8FD8\u5728\u6296\u52A8\u7740\uFF0C\u8FD9\u65F6\u5916\u9762\u6709\u4EBA\u53EB\u4E00\u4EFD\u9E21\u722A\uFF0C\u53EA\u89C1\u963F\u65B9\u719F\u7EC3\u5730\u4ECE\u90A3\u4E2A\u624B\u4E0A\u65A9\u4E0B\u4E86\u4E00\u5757\uFF0C\u4ED6\u98DE\u5FEB\u5730\u5241\u7740\uFF0C\u7136\u540E\u4E0B\u9505\uFF0C\u52A0\u6599.....\u5F88\u5FEB\uFF0C\u4E00\u76D8\u9E21\u722A\u5C31\u9999\u55B7\u55B7\u7684\u51FA\u9505\u4E86\uFF0C\u963F\u65B9\u5C06\u5B83\u7AEF\u4E86\u51FA\u53BB.\u8FD9\u65F6\uFF0C\u6211\u53D1\u73B0\u4ED6\u51B2\u6211\u8FD9\u4E2A\u65B9\u5411\u7B11\u4E86\u4E00\u4E0B\uFF0C&quot;\u549A!&quot;\u6211\u5413\u5F97\u4ECE\u4E0A\u9762\u6389\u4E86\u4E0B\u6765\uFF0C\u6389\u8FDB\u4E86\u963F\u65B9\u7684\u53A8\u623F....</p><h2 id="\u533B\u9662\u9B3C\u8BDD" tabindex="-1"><a class="header-anchor" href="#\u533B\u9662\u9B3C\u8BDD" aria-hidden="true">#</a> \u533B\u9662\u9B3C\u8BDD</h2><p>\u4E00\u4E2A\u533B\u751F\u548C\u62A4\u58EB\u5728\u4E3A\u4E94\u697C\u75C5\u4EBA\u6025\u6551\u540E\u4ECE\u4E94\u697C\u5750\u7535\u68AF\u8981\u56DE\u4E00\u697C\uFF0C\u4F46\u662F\u7535\u68AF\u8FC7\u4E86\u4E00\u697C\u5374\u6CA1\u6709\u505C\u4E0B\u6765\u3002B1/B2/B3/\u7535\u68AF\u6700\u540E\u5728\u5730\u4E0B\u4E09\u697C\u505C\u4E86\u4E0B\u6765\uFF0C\u7535\u68AF\u7684\u95E8\u7F13\u7F13\u5730\u6253\u5F00\u4E86\uFF0C\u4E00\u80A1\u9634\u51B7\u4E4B\u6C14\u8FCE\u9762\u6251\u6765\u3002\u4E00\u4E2A\u5973\u5B69\u5B50\u51FA\u6B64\u523B\u9762\u524D\uFF0C\u8981\u8FDB\u6765\u642D\u7535\u68AF\u3002\u533B\u751F\u5413\u5F97\u9762\u65E0\u4EBA\u8272\uFF0C\u8D76\u5FEB\u628A\u7535\u68AF\u7684\u95E8\u5173\u4E0A\u4E86\u3002\u62A4\u58EB\u95EE\u533B\u751F\u4E3A\u4EC0\u4E48\u4E0D\u8BA9\u90A3\u4E2A\u5973\u5B69\u5B50\u8FDB\u6765\uFF0C\u533B\u751F\u60CA\u5413\u5F97\u8BF4\uFF1A\u201C\u4F60\u6CA1\u6709\u770B\u89C1\u90A3\u4E2A\u5973\u5B69\u5B50\u624B\u4E0A\u5E26\u7740\u4E00\u6761\u7EA2\u5E26\u4E86\uFF0C\u5730\u4E0B\u4E09\u697C\u662F\u533B\u9662\u7684\u592A\u5E73\u95F4\uFF0C\u6BCF\u4E00\u4E2A\u5C38\u4F53\u90FD\u4F1A\u5728\u624B\u4E0A\u7CFB\u4E00\u6761\u7EA2\u5E26\u5B50\u793A\u522B\u3002</p><p>\u533B\u751F\u4E0D\u505C\u5730\u5598\u6C14\u3002\u3002\u3002\u3002\u3002\u4E0D\u505C\u5730\u5598\u6C14\u3002\u3002\u3002\u3002\u3002</p><p>\u62A4\u58EB\u6162\u6162\u5730\u628A\u624B\u4F38\u5230\u533B\u751F\u9762\u524D\uFF1A\u201C\u662F\u4E0D\u662F\u50CF\u8FD9\u6837\u7684\u7EA2\u5E26\u5B50\u3002\u3002\u3002\u3002\u201D</p>',57),t=[d];function i(n,c){return a(),e("div",null,t)}const _=p(h,[["render",i],["__file","1.html.vue"]]);export{_ as default};
