import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.52cb12a8.js";const c={},o=a('<h1 id="\u7B2C1\u96C6-\u674E\u5927\u948A\u6177\u6168\u9648\u8BCD\u6B32\u6551\u56FD" tabindex="-1"><a class="header-anchor" href="#\u7B2C1\u96C6-\u674E\u5927\u948A\u6177\u6168\u9648\u8BCD\u6B32\u6551\u56FD" aria-hidden="true">#</a> \u7B2C1\u96C6\uFF1A\u674E\u5927\u948A\u6177\u6168\u9648\u8BCD\u6B32\u6551\u56FD</h1><p>1915\u5E74\uFF0C\u7B2C\u4E00\u6B21\u4E16\u754C\u5927\u6218\u7684\u7B2C\u4E8C\u5E74\uFF0C\u8D81\u7740\u5FB7\u56FD\u5728\u6B27\u6D32\u6218\u573A\u5206\u8EAB\u4E4F\u672F\uFF0C\u65E5\u672C\u52A0\u7D27\u4E86\u5BF9\u4E2D\u56FD\u7684\u4FB5\u7565\u3002\u7B2C\u4E8C\u6B21\u9769\u547D\u5931\u8D25\uFF0C\u5B59\u4E2D\u5C71\u7B49\u6D41\u4EA1\u6D77\u5916\uFF0C\u56FD\u5185\u653F\u5C40\u6DF7\u4E71\uFF0C\u4E2D\u56FD\u518D\u6B21\u8FDB\u5165\u98CE\u96E8\u98D8\u6447\u7684\u65F6\u4EE3\u3002\u5317\u4EAC\u57CE\u91CC\uFF0C\u6C11\u56FD\u5916\u4EA4\u90E8\u6B21\u957F\u66F9\u6C5D\u9716\u9762\u5E26\u7126\u707C\u5730\u544A\u8BC9\u8881\u4E16\u51EF\uFF0C\u65E5\u672C\u516C\u4F7F\u9001\u6765\u6700\u540E\u901A\u7252\u8981\u6C42\u6C11\u56FD\u653F\u5E9C\u572848\u5C0F\u65F6\u5185\u63A5\u53D7\u4E8C\u5341\u4E00\u6761\uFF0C\u5426\u5219\u5C06\u76F4\u63A5\u63A5\u7BA1\u5C71\u4E1C\u3002\u65E5\u672C\u6D77\u519B\u7F16\u961F\u5DF2\u6709\u9A76\u5F80\u4E1C\u6D77\u8FF9\u8C61\uFF0C\u65E5\u672C\u4EA1\u6211\u4E4B\u5FC3\u4E0D\u6B7B\uFF0C\u8881\u4E16\u51EF\u76F4\u547C\u7A9D\u56CA\u3002\u53EF\u65E5\u672C\u6001\u5EA6\u5F3A\u786C\uFF0C\u8FD9\u6761\u7EA6\u5FC5\u987B\u9A6C\u4E0A\u7B7E\u7F72\uFF0C\u8881\u4E16\u51EF\u8BE2\u95EE\u5916\u4EA4\u90E8\u7684\u610F\u601D\uFF0C\u66F9\u6C5D\u9716\u8D76\u7D27\u4F4E\u5934\u8BF4\u7740\u552F\u5927\u603B\u7EDF\u9A6C\u9996\u662F\u77BB\uFF0C\u591A\u4E48\u6ED1\u5934\u7684\u7B54\u590D\u3002\u8881\u4E16\u51EF\u540C\u610F\u548C\u65E5\u672C\u7B7E\u8BA2\u6761\u7EA6\uFF0C\u4F46\u7B2C\u4E94\u90E8\u5206\u90A3\u51E0\u6761\u4E0D\u80FD\u540C\u610F\uFF0C\u8FD9\u662F\u4ED6\u7684\u5E95\u7EBF\uFF0C\u5E76\u5B9A\u4E8E9\u53F7\u4E5F\u5C31\u662F\u65E5\u672C\u7ED9\u7684\u6700\u540E\u65F6\u95F4\u518D\u544A\u8BC9\u65E5\u672C\u3002</p><p>\u8857\u5934\u4E0A\u827A\u4EBA\u8BF4\u7740\u5FEB\u677F\uFF0C\u75DB\u65A5\u7740\u8881\u4E16\u51EF\u5356\u56FD\u7684\u5351\u52A3\u884C\u4E3A\uFF0C\u65C1\u8FB9\u5927\u6237\u4EBA\u5BB6\u51FA\u6BA1\u548C\u559C\u4E8B\u7684\u7684\u4EEA\u961F\u7ADF\u7136\u4EA4\u9519\u800C\u8FC7\uFF0C\u767D\u5E10\u548C\u7EA2\u5E06\u98D8\u6447\u5728\u4E00\u8D77\uFF0C\u4EFF\u4F5B\u5728\u5632\u8BBD\u7740\u6C11\u56FD\u653F\u5E9C\u7684\u61E6\u5F31\u65E0\u80FD\u548C\u4E2D\u56FD\u7684\u524D\u9014\u672A\u535C\u30021915\u5E745\u67089\u65E5\uFF0C\u5317\u6D0B\u653F\u5E9C\u7B7E\u8BA2\u4E8C\u5341\u4E00\u6761\uFF0C\u5141\u8BB8\u65E5\u672C\u7EE7\u627F\u5FB7\u56FD\u5728\u5C71\u4E1C\u7684\u4E00\u5207\u6743\u5229\uFF0C\u5317\u6D0B\u653F\u5E9C\u7684\u5356\u56FD\u884C\u5F84\u5F15\u8D77\u5168\u56FD\u4EBA\u6C11\u7684\u4E0D\u6EE1\uFF0C\u7FA4\u4F17\u7EB7\u7EB7\u8FDB\u884C\u793A\u5A01\u884C\u52A8\uFF0C\u6C49\u53E3\u3001\u957F\u6C99\u7B49\u5730\u5DE5\u4EBA\u751A\u81F3\u7F62\u5DE5\u6297\u8BAE\uFF0C\u62D2\u4E0D\u627F\u8BA4\u4E8C\u5341\u4E00\u6761\uFF0C\u8A93\u8840\u56FD\u803B\u3002\u6D41\u4EA1\u6D77\u5916\u7684\u5B59\u4E2D\u5C71\u3001\u9EC4\u5174\u7B49\u4EBA\u4E5F\u7EB7\u7EB7\u547C\u5401\u6D77\u5916\u6709\u5FD7\u4E4B\u58EB\u56DE\u56FD\u5012\u8881\u3002</p><p>\u65E5\u672C\u65E9\u7A3B\u7530\u5927\u5B66\u6821\u56ED\u5185\uFF0C\u4E00\u7FA4\u7559\u5B66\u751F\u6B63\u62FF\u7740\u62A5\u7EB8\u5BF9\u4E0A\u9762\u5199\u7740\u7684\u56FD\u5185\u60C5\u51B5\u6124\u6168\u4E0D\u5DF2\u3002\u65F6\u4E3A\u65E9\u7A3B\u7530\u5927\u5B66\u5B66\u751F\u7684\u674E\u5927\u948A\u548C\u660E\u6CBB\u5927\u5B66\u7684\u9AD8\u4E00\u6DB5\u5BF9\u5317\u6D0B\u653F\u5E9C\u5982\u6B64\u4E27\u6743\u8FB1\u56FD\u7684\u884C\u5F84\u6DF1\u6076\u75DB\u7EDD\uFF0C\u6C14\u7684\u8FDE\u996D\u90FD\u5403\u4E0D\u4E0B\u3002\u674E\u5927\u948A\u6BC5\u7136\u8D77\u8EAB\uFF0C\u547C\u5401\u8D77\u5728\u65E5\u7559\u5B66\u7684\u540C\u5B66\u4EEC\uFF0C\u56FD\u5BB6\u5DF2\u7ECF\u5230\u4E86\u6B64\u79CD\u5883\u5730\uFF0C\u4FBF\u518D\u65E0\u9700\u6295\u9F20\u5FCC\u5668\uFF0C\u5E94\u4E0B\u7834\u91DC\u6C89\u821F\u7684\u51B3\u5FC3\u3002\u7A83\u56FD\u5927\u76D7\u8881\u4E16\u51EF\u60F3\u8981\u81EA\u5DF1\u5F53\u7687\u5E1D\uFF0C\u4E2D\u534E\u6C11\u65CF\u5371\u5728\u65E6\u5915\uFF0C\u5171\u548C\u5C31\u8981\u4EA1\u4E86\uFF0C\u7559\u5B66\u751F\u7FA4\u60C5\u6FC0\u594B\u7EB7\u7EB7\u547C\u558A\u8981\u56DE\u56FD\u5012\u8881\u3002</p><p>\u5750\u5728\u4E00\u65C1\u770B\u7740\u62A5\u7EB8\u7684\u5F20\u4E30\u8F7D\u5374\u7ED9\u4ED6\u4EEC\u6CFC\u4E86\u51B7\u6C34\uFF0C\u4ED6\u8BA4\u4E3A\u4E2D\u56FD\u9677\u5165\u5982\u6B64\u5883\u5730\uFF0C\u5C31\u662F\u56E0\u4E3A\u6C11\u4E3B\u4E4B\u58EB\u975E\u8981\u6210\u7ACB\u6C11\u4E3B\u5171\u548C\uFF0C\u660E\u660E\u541B\u4E3B\u7ACB\u5BAA\u5236\u624D\u662F\u62EF\u6551\u4E2D\u56FD\u7684\u6B63\u9053\u3002\u674E\u5927\u948A\u9A73\u65A5\u5176\u601D\u60F3\u8352\u8C2C\u81F3\u6781\uFF0C\u9769\u547D\u56FA\u7136\u4F1A\u6D41\u8840\uFF0C\u4F46\u541B\u4E3B\u7ACB\u5BAA\u7167\u6837\u4F1A\u6D41\u8840\u3002\u8F9B\u4EA5\u9769\u547D\u4EE5\u6765\u56FD\u6C11\u601D\u60F3\u65E9\u5DF2\u7FFB\u5929\u8986\u5730\uFF0C\u6240\u8C13\u6C11\u667A\u672A\u5F00\u66F4\u662F\u65E0\u7A3D\u4E4B\u8C08\u3002\u9006\u65F6\u4EE3\u6F6E\u6D41\u800C\u884C\u7EDD\u4E0D\u53EF\u884C\uFF0C\u4E3A\u4E86\u4E2D\u534E\u6C11\u65CF\u4ED6\u4EEC\u5FC5\u987B\u5012\u8881\u6551\u56FD\u3002</p><p>\u88AB\u8FEB\u6D41\u4EA1\u65E5\u672C\u7684\u793E\u4F1A\u6D3B\u52A8\u5BB6\u7AE0\u58EB\u948A\u80CC\u7740\u624B\u8D70\u6765\u542C\u7740\u4E24\u65B9\u5B66\u751F\u7684\u4E89\u8BBA\uFF0C\u4ED6\u76DB\u8D5E\u674E\u5927\u948A\u7684\u8BDD\u5B57\u5B57\u73E0\u7391\uFF0C\u6177\u6168\u6FC0\u6602\uFF0C\u5E76\u51B2\u7740\u8FDC\u5904\u84EC\u5934\u57A2\u9762\u7684\u7537\u4EBA\u558A\u7740\u8BA9\u4ED6\u4E5F\u6765\u8BC4\u8BAE\u4E00\u756A\u3002\u6B64\u4EBA\u5B57\u4EF2\u752B\uFF0C\u5373\u662F\u540C\u7AE0\u58EB\u948A\u4E00\u5E76\u6D41\u4EA1\u65E5\u672C\u7684\u9648\u72EC\u79C0\u3002\u9648\u72EC\u79C0\u5403\u7740\u624B\u4E2D\u7684\u996D\u83DC\uFF0C\u6BEB\u65E0\u8868\u60C5\u7684\u987A\u53E3\u8BC4\u8BBA\u674E\u5927\u948A\u7684\u786E\u662F\u4E2A\u89C1\u8BC6\u6781\u9AD8\u7684\u9752\u5E74\u3002</p><p>\u5B66\u751F\u4EEC\u8BA9\u9648\u72EC\u79C0\u8BF4\u8BF4\u5982\u4F55\u6551\u56FD\uFF0C\u9648\u72EC\u79C0\u6DE1\u6DE1\u5730\u8BF4\u9053\u8FD9\u6837\u7684\u56FD\u65E0\u53EF\u6551\u836F\u3002\u4E0D\u5F85\u4ED6\u63A5\u7740\u8BF4\u4E0B\u53BB\uFF0C\u6124\u6168\u5730\u5B66\u751F\u5C31\u4E0A\u524D\u6454\u6389\u4ED6\u624B\u4E2D\u7684\u996D\u83DC\uFF0C\u8FD8\u553E\u5F03\u4ED6\u3002\u5F20\u4E30\u8F7D\u66F4\u662F\u8BF4\u9053\u9648\u72EC\u79C0\u4EA1\u56FD\u65E0\u6240\u60DC\u7684\u53D1\u8A00\uFF0C\u75DB\u65A5\u4ED6\u4EEC\u8FD9\u4E9B\u9769\u547D\u515A\u624D\u662F\u4EA1\u56FD\u5974\u3002\u6124\u6012\u5730\u5B66\u751F\u4E0A\u524D\u6BB4\u6253\u8FB1\u9A82\u9648\u72EC\u79C0\uFF0C\u53EA\u6709\u674E\u5927\u948A\u660E\u767D\u8FD9\u4F4D\u5148\u751F\u662F\u6709\u771F\u624D\u8BC6\u3002\u4ED6\u62E8\u5F00\u4F17\u4EBA\u62A4\u7740\u9648\u72EC\u79C0\u79BB\u5F00\uFF0C\u5F85\u5230\u4E00\u7247\u6811\u836B\u4E0B\uFF0C\u674E\u5927\u948A\u8C26\u865A\u5730\u540C\u9648\u72EC\u79C0\u8BE2\u95EE\u8D77\u5176\u6587\u7AE0\u4E2D\u7684\u601D\u60F3\u3002\u9648\u72EC\u79C0\u544A\u8BC9\u4ED6\uFF0C\u4E2D\u56FD\u7684\u95EE\u9898\u79EF\u91CD\u96BE\u8FD4\uFF0C\u9760\u6362\u4EBA\u6362\u653F\u5E9C\u4E0D\u80FD\u89E3\u51B3\uFF0C\u4ED6\u4EEC\u60F3\u8981\u6551\u56FD\u5FC5\u987B\u63A8\u7FFB\u91CD\u5EFA\u671D\u5EF7\u7684\u8001\u8DEF\uFF0C\u60F3\u8981\u627E\u51FA\u4E00\u6761\u65B0\u8DEF\u4ED6\u4EEC\u5C31\u9700\u8981\u50CF\u674E\u5927\u948A\u8FD9\u6837\u7684\u6709\u5FD7\u9752\u5E74\u3002</p><p>\u9648\u72EC\u79C0\u7EC8\u4E8E\u53EF\u4EE5\u518D\u5EA6\u56DE\u56FD\uFF0C\u4ED6\u770B\u4E86\u674E\u5927\u948A\u53D1\u8868\u7684\u6587\u7AE0\u7279\u610F\u5728\u4ECA\u65E5\u6765\u627E\u4ED6\u3002\u6613\u767D\u6C99\u7ED9\u4ED6\u9001\u6765\u8BC1\u4EF6\uFF0C\u7B11\u8BDD\u4ED6\u4E3A\u4E86\u5199\u6587\u7AE0\u628A\u81EA\u5DF1\u5F04\u5F97\u50CF\u4E2A\u4E5E\u4E10\u4E00\u6837\uFF0C\u8BA9\u4ED6\u6D17\u4E2A\u6FA1\u6362\u8EAB\u8863\u670D\u518D\u56DE\u56FD\u3002\u53EF\u9648\u72EC\u79C0\u5C31\u8FD9\u53EA\u6709\u8FD9\u4E00\u8EAB\u8863\u670D\u4E86\uFF0C\u674E\u5927\u948A\u62FF\u8D77\u81EA\u5DF1\u7684\u5927\u8863\u8D60\u4E88\u4ED6\uFF0C\u674E\u5927\u948A\u544A\u8BC9\u4ED6\u4ECA\u65E5\u4ED6\u6B20\u4E0B\u5176\u4E09\u4E2A\u4EBA\u60C5\uFF0C\u4E00\u5E2E\u4ED6\u8131\u56F0\uFF0C\u4E8C\u501F\u4ED6\u8863\u670D\uFF0C\u4E09\u5C31\u662F\u4ED6\u521A\u521A\u8E6D\u7684\u662F\u4ED6\u674E\u5927\u948A\u7684\u996D\u3002\u65E5\u540E\u5982\u6709\u9700\u8981\u4ED6\u53EF\u4EE5\u6765\u627E\u81EA\u5DF1\u8FD8\u8FD9\u4E09\u4E2A\u4EBA\u60C5\u3002</p><p>\u6E05\u6D17\u4E00\u756A\u7684\u9648\u72EC\u79C0\u5750\u5728\u56DE\u56FD\u7684\u8239\u4E0A\u671B\u7740\u7ECF\u8FC7\u7684\u5C71\u4E1C\uFF0C\u5FC3\u4E2D\u5F88\u662F\u6C14\u6124\u3002\u81EA\u9E26\u7247\u6218\u4E89\u540E\u5DF2\u7136\u4E03\u5341\u4E94\u5E74\uFF0C\u65E0\u6570\u4EBA\u5BFB\u627E\u8FC7\u6551\u56FD\u7684\u65B9\u6CD5\uFF0C\u53EF\u73B0\u5982\u4ECA\u5C31\u8FDE\u5B59\u4E2D\u5C71\u201C\u5F15\u8FDB\u201D\u7684\u5171\u548C\u5236\u5EA6\u4E5F\u5C8C\u5C8C\u53EF\u5371\uFF0C\u6881\u58EB\u8BD2\u7B49\u4EBA\u5230\u5904\u4E32\u8054\u4E0A\u8E7F\u4E0B\u8DF3\uFF0C\u5C31\u662F\u4E00\u7FA4\u6CA1\u6709\u9053\u5FB7\u5E95\u7EBF\u7684\u653F\u6CBB\u6D41\u6C13\u3002\u9648\u72EC\u79C0\u53EB\u8FD9\u7FA4\u4EBA\u4E3A\u201C\u793C\u4E49\u5EC9\u201D\uFF0C\u6613\u767D\u6C99\u4E0D\u660E\u6240\u4EE5\uFF0C\u5176\u5B9E\u8FD9\u5C31\u662F\u9648\u72EC\u79C0\u5728\u9A82\u4ED6\u4EEC\u65E0\u803B\u3002\u9648\u72EC\u79C0\u611F\u6168\u81EA\u5DF1\u8FD9\u4E9B\u5E74\u4E1C\u5954\u897F\u8D70\u4E5D\u6B7B\u4E00\u751F\uFF0C\u7ED3\u679C\u5230\u73B0\u5728\u8FD8\u662F\u4E00\u4E8B\u65E0\u6210\u3002\u4E2D\u56FD\u7684\u95EE\u9898\u5149\u9760\u9769\u547D\u89E3\u51B3\u4E0D\u4E86\uFF0C\u4ED6\u6253\u5B9A\u4E3B\u610F\u8FD9\u6B21\u56DE\u56FD\u4E8C\u5341\u5E74\u4E0D\u8C08\u653F\u6CBB\uFF0C\u8981\u8131\u80CE\u6362\u9AA8\u8F6C\u53D8\u81EA\u5DF1\u7684\u601D\u60F3\u3002\u800C\u4E8C\u7B49\u8231\u5185\u5F20\u4E30\u8F7D\u6B63\u753B\u7740\u4EAC\u5267\u8138\u8C31\uFF0C\u5728\u4E00\u4F17\u540C\u5B66\u7684\u5938\u8D5E\u4E2D\u4ED6\u5766\u8A00\uFF0C\u8881\u4E16\u51EF\u8981\u662F\u5F53\u4E86\u603B\u7EDF\uFF0C\u4ED6\u4E8C\u53D4\u5C31\u662F\u5927\u5185\u603B\u7BA1\u3002\u51E0\u4E2A\u6253\u7740\u724C\u7684\u5B66\u751F\u58F0\u79F0\u4E3A\u4E86\u5E2E\u8881\u4E16\u51EF\u4ED6\u4EEC\u90FD\u653E\u5F03\u4E86\u7559\u5B66\u540D\u989D\uFF0C\u5F20\u4E30\u8F7D\u627F\u8BFA\u53EA\u8981\u4E8B\u60C5\u6210\u4E86\u4ED6\u4EEC\u5C31\u80FD\u53BB\u5317\u5927\u8BFB\u4E66\u3002</p><p>\u540C\u76DF\u4F1A\u5143\u8001\u6E58\u8C6B\u8054\u519B\u7B2C\u4E09\u519B\u519B\u957F\u90B9\u6C38\u6210\u5C06\u519B\u6B63\u5728\u7532\u677F\u4E0A\u6253\u592A\u6781\uFF0C\u89C1\u9648\u72EC\u79C0\u8FC7\u6765\u4FBF\u4E3A\u4ED6\u8BA9\u8DEF\u3002\u53EF\u9648\u72EC\u79C0\u6839\u672C\u5C31\u4E0D\u4E88\u7406\u776C\uFF0C\u79F0\u81EA\u5DF1\u5BF9\u5B98\u50DA\u653F\u5BA2\u4E0D\u611F\u5174\u8DA3\u3002\u90B9\u6C38\u6210\u5012\u662F\u4E0D\u751A\u5728\u610F\u4E3B\u52A8\u6765\u540C\u4ED6\u6500\u8C08\uFF0C\u5F53\u521D\u5B8B\u6559\u4EC1\u88AB\u6740\uFF0C\u4ED6\u6DF1\u611F\u9769\u547D\u65E0\u671B\u7559\u7EDD\u547D\u8BD7\u4E00\u9996\u6124\u7136\u6295\u9EC4\u6D66\u6C5F\u81EA\u6740\uFF0C\u5E78\u5F97\u6E14\u6C11\u6551\u8D77\u624D\u6D3B\u4E86\u4E0B\u6765\u3002\u9648\u72EC\u79C0\u5766\u8A00\u6B64\u884C\u5F84\u5B9E\u5728\u7CCA\u6D82\uFF0C\u9769\u547D\u4E4B\u4EBA\u4E0D\u80FD\u8F7B\u6613\u653E\u5F03\u751F\u547D\uFF0C\u4ED6\u6B64\u751F\u7EDD\u4E0D\u4F1A\u505A\u81EA\u7EDD\u4E8E\u9769\u547D\u7684\u8822\u4E8B\u3002\u6613\u767D\u6C99\u5BF9\u4E8E\u4ED6\u7684\u5931\u6001\u5F88\u4E0D\u6EE1\uFF0C\u9648\u72EC\u79C0\u8868\u793A\u62B1\u6B49\uFF0C\u4F46\u4ED6\u5FC3\u60C5\u5B9E\u5728\u4E0D\u597D\u3002\u59BB\u5B50\u541B\u66FC\u60A3\u80BA\u75E8\u5DF2\u7ECF\u54B3\u8840\uFF0C\u5EF6\u5E74\u3001\u4E54\u5E74\u4E24\u4E2A\u5B69\u5B50\u4E5F\u8DD1\u53BB\u4E86\u4E0A\u6D77\u3002\u6613\u767D\u6C99\u6307\u8D23\u4ED6\u5BF9\u4E24\u4E2A\u513F\u5B50\u592A\u8FC7\u82DB\u523B\uFF0C\u5C0F\u5C0F\u5E74\u7EAA\u5C31\u8981\u81EA\u98DF\u5176\u529B\uFF0C\u53EF\u9648\u72EC\u79C0\u5C31\u662F\u8981\u8BA9\u4ED6\u4EEC\u53BB\u5403\u82E6\uFF0C\u51B5\u4E14\u81EA\u5DF1\u4E5F\u5B9E\u5728\u6CA1\u94B1\u8D34\u8865\u4ED6\u4EEC\u3002</p><p>\u4E0A\u6D77\u6CD5\u79DF\u754C\u642C\u8FD0\u7AD9\u91CC\uFF0C\u9648\u72EC\u79C0\u7684\u957F\u5B50\u9648\u5EF6\u5E74\u548C\u6B21\u5B50\u9648\u4E54\u5E74\u625B\u8D77\u5927\u5305\u7CAE\u98DF\u505A\u7740\u642C\u8FD0\u5DE5\u3002\u5E74\u8001\u7684\u5DE5\u4EBA\u8001\u8D3E\u817F\u4E0A\u6D41\u7740\u8840\uFF0C\u652F\u6491\u4E0D\u4F4F\uFF0C\u628A\u4E24\u5927\u5305\u8C46\u5B50\u7CAE\u5305\u6389\u5728\u4E86\u5730\u4E0A\u3002\u76D1\u5DE5\u4E0A\u524D\u7528\u97AD\u5B50\u62BD\u6253\u8001\u8D3E\uFF0C\u5EF6\u5E74\u4E54\u5E74\u770B\u4E0D\u8FC7\u4E0A\u524D\u963B\u6B62\uFF0C\u663E\u7136\u8FD9\u4E0D\u662F\u4ED6\u4EEC\u7B2C\u4E00\u6B21\u8FD9\u6837\u4ED7\u4E49\u51FA\u624B\u3002\u76D1\u5DE5\u8BA9\u4ED6\u4FE9\u628A\u8001\u8D3E\u7684\u6D3B\u513F\u5E72\u4E86\uFF0C\u5426\u5219\u8FDE\u4ED6\u4FE9\u4E00\u8D77\u6253\u3002\u5EF6\u5E74\u4E54\u5E74\u679C\u65AD\u625B\u8D77\u5927\u5305\uFF0C\u4E54\u5E74\u88E4\u888B\u65AD\u88C2\u5374\u76F4\u63A5\u62D6\u4E0B\u978B\u5B50\u5149\u811A\u5F80\u4E0A\u8D70\uFF0C\u786C\u662F\u6CA1\u6709\u653E\u4E0B\u80A9\u4E0A\u7684\u5927\u5305\u3002</p><p>\u822A\u8239\u4E0A\u4E24\u4E2A\u65E5\u672C\u8239\u5458\u62D6\u7740\u4E00\u7537\u4E00\u5973\u4E24\u4E2A\u5B66\u751F\u5728\u7532\u677F\u4E0A\u6BB4\u6253\u4FAE\u8FB1\uFF0C\u66F4\u662F\u52A8\u624B\u53BB\u62FD\u5973\u5B69\u7684\u8863\u670D\u3002\u5F20\u4E30\u8F7D\u7B49\u4EBA\u638F\u51FA\u9EBB\u5C06\u7838\u5230\u65E5\u672C\u8239\u5458\u8EAB\u4E0A\u5374\u53C8\u6CA1\u6709\u80C6\u91CF\u5149\u660E\u6B63\u5927\u7684\u7AD9\u51FA\u6765\uFF0C\u8FD8\u597D\u90B9\u6C38\u6210\u8D76\u6765\u51FA\u624B\u76F8\u52A9\u3002\u4E24\u4E2A\u5B66\u751F\u662F\u56E0\u4E3A\u8239\u7968\u88AB\u5077\u624D\u906D\u6B64\u5F85\u9047\u3002\u90B9\u6C38\u6210\u63D0\u51FA\u81EA\u5DF1\u4E3A\u4ED6\u4EEC\u8865\u7968\uFF0C\u9648\u72EC\u79C0\u63D0\u51FA\u8FD9\u662F\u516C\u6D77\uFF0C\u65E5\u672C\u8239\u5458\u5FC5\u987B\u4E3A\u4ED6\u4EEC\u7684\u884C\u5F84\u9053\u6B49\u3002\u9762\u5BF9\u9053\u6B49\u540E\u79BB\u5F00\u7684\u65E5\u672C\u8239\u5458\uFF0C\u5F20\u4E30\u8F7D\u539A\u989C\u65E0\u803B\u7684\u53EB\u56A3\u7740\u8981\u6253\u5C31\u8981\u63A5\u7740\u6253\u4E0B\u53BB\u8FD9\u6837\u624D\u80FD\u5F70\u663E\u4ED6\u4EEC\u7684\u7231\u56FD\u60C5\u6000\u3002\u9648\u72EC\u79C0\u8E29\u4F4F\u4ED6\u4EEC\u4ECD\u5728\u5730\u4E0A\u7684\u9EBB\u5C06\u724C\uFF0C\u75DB\u65A5\u56FD\u4E4B\u6240\u4EE5\u672A\u660C\u5C31\u662F\u56E0\u4E3A\u6709\u4ED6\u4EEC\u8FD9\u79CD\u6C11\u667A\u672A\u5F00\u7684\u4EBA\u3002</p><p>\u9648\u72EC\u79C0\u5411\u90B9\u6C38\u6210\u9053\u6B49\uFF0C\u800C\u90A3\u540D\u88AB\u6253\u7684\u7537\u5B66\u751F\u540D\u53EB\u5FC3\u521A\uFF0C\u4ED6\u5411\u9648\u72EC\u79C0\u97A0\u8EAC\u81F4\u8C22\u5E76\u611F\u8C22\u4ED6\u7684\u4E0D\u8BA1\u524D\u5ACC\uFF0C\u539F\u6765\u4ED6\u5C31\u662F\u90A3\u4E2A\u63A8\u6389\u9648\u72EC\u79C0\u996D\u83DC\u8FD8\u553E\u5F03\u4ED6\u7684\u5B66\u751F\u3002\u9648\u72EC\u79C0\u544A\u8BC9\u4ED6\u4EC5\u51ED\u8840\u6027\u8FD9\u4ED7\u662F\u6253\u4E0D\u5B8C\u7684\uFF0C\u4ED6\u9080\u8BF7\u5FC3\u521A\u53BB\u5F80\u81EA\u5DF1\u7684\u8239\u8231\u540C\u4ED6\u597D\u597D\u63A2\u8BA8\u6551\u56FD\u4E4B\u9053\u3002</p><p>\u5EF6\u5E74\u548C\u4E54\u5E74\u7528\u624B\u628A\u98DF\u7269\u4E0A\u7684\u9709\u70B9\u6263\u6389\uFF0C\u5C31\u7740\u6C34\u5403\u8D77\u6765\u3002\u770B\u7740\u722C\u4E0A\u7897\u91CC\u7684\u8682\u8681\uFF0C\u5EF6\u5E74\u7B11\u7740\u5C06\u5B83\u653E\u751F\uFF0C\u8FD9\u662F\u5BF9\u751F\u547D\u987D\u5F3A\u7684\u4E00\u79CD\u656C\u4F69\u3002\u59B9\u59B9\u9648\u5B50\u7F8E\u6765\u627E\u4E24\u4E2A\u54E5\u54E5\uFF0C\u7EE7\u6BCD\u9AD8\u541B\u66FC\u5E26\u7740\u5305\u5B50\u548C\u9E21\u86CB\u6C64\u6765\u7ED9\u5144\u5F1F\u4E8C\u4EBA\uFF0C\u53EF\u5144\u5F1F\u4E8C\u4EBA\u77E5\u9053\u59E8\u5988\u5BB6\u4E2D\u4E5F\u8D2B\u56F0\uFF0C\u4ED6\u4EEC\u4E0D\u5FCD\u5FC3\u8BA9\u59E8\u5988\u548C\u59B9\u59B9\u4EEC\u591A\u82B1\u8D39\u3002\u5F97\u77E5\u9648\u72EC\u79C0\u5373\u5C06\u56DE\u56FD\uFF0C\u5EF6\u5E74\u58F0\u79F0\u4ED6\u4EEC\u4E0D\u4F1A\u56DE\u53BB\u4E5F\u548C\u9648\u72EC\u79C0\u6CA1\u6709\u4EFB\u4F55\u5173\u7CFB\u3002\u4E34\u8D70\u524D\u5B50\u7F8E\u8BA9\u5EF6\u5E74\u8BB0\u5F97\u8981\u9001\u81EA\u5DF1\u8748\u8748\uFF0C\u5EF6\u5E74\u5543\u7740\u624B\u673A\u786C\u90A6\u90A6\u7684\u7389\u7C73\u997C\uFF0C\u7B54\u5E94\u4E00\u5B9A\u4F1A\u9001\u7ED9\u5979\u3002</p>',14),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","1.html.vue"]]);export{h as default};
