import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as t,f as r}from"./app.eee000b7.js";const n={},s=r('<h1 id="\u664B\u7EAA\u5341" tabindex="-1"><a class="header-anchor" href="#\u664B\u7EAA\u5341" aria-hidden="true">#</a> \u664B\u7EAA\u5341</h1><blockquote><p>\u806A\u4EE5\u9C7C\u87F9\u4E0D\u4F9B\uFF0C\u65A9\u5DE6\u90FD\u6C34\u4F7F\u8005\u8944\u9675\u738B\u6445\uFF1B\u4F5C\u6E29\u660E\u3001\u5FBD\u5149\u4E8C\u6BBF\u672A\u6210\uFF0C\u65A9\u5C06\u4F5C\u5927\u5320\u671B\u90FD\u516C\u9773\u9675\u3002\u89C2\u6E14\u4E8E\u6C7E\u6C34\uFF0C\u660F\u591C\u4E0D\u5F52\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\u2022\u5377\u516B\u5341\u516B\u2022\u664B\u7EAA\u5341\u300B</strong></p><p>\u4ECA\u5929\u6765\u4ECB\u7ECD\u4E00\u4F4D\u66B4\u541B\uFF1A<strong>\u5218\u806A</strong>\u3002</p><p>\u5982\u679C\u4EE5\u5308\u5974\u4EBA\u7684\u773C\u5149\u6765\u770B\u5386\u53F2\uFF0C\u90A3\u4E48\u5218\u806A\u662F\u4E00\u4E2A\u5F00\u5929\u8F9F\u5730\u7684\u4EBA\u7269\uFF1A\u4ED6\u4EE5\u5916\u65CF\u7687\u5E1D\u7684\u8EAB\u4EFD\uFF0C\u4FD8\u864F\u664B\u671D\u7687\u5E1D\uFF0C\u7B2C\u4E00\u6B21\u706D\u4EA1\u4E86\u6C49\u4EBA\u521B\u7ACB\u7684\u738B\u671D\u3002</p><p>\u5F53\u7136\uFF0C\u8FD9\u6837\u7684\u201C\u4E30\u529F\u4F1F\u7EE9\u201D\u5728\u6C49\u4EBA\u8BB0\u8F7D\u7684\u5386\u53F2\u4E2D\u662F\u4E0D\u4F1A\u88AB\u6B4C\u9882\u7684\u3002\u800C\u4E14\uFF0C<strong>\u5218\u806A\u4E5F\u4E0D\u662F\u4E00\u4E2A\u503C\u5F97\u88AB\u7EAA\u5FF5\u7684\u5E1D\u738B</strong>\u3002\u53F2\u4E66\u4E2D\u5173\u4E8E\u4ED6\u7684\u8BB0\u5F55\uFF0C\u5927\u591A\u843D\u7B14\u5728\u6B8B\u66B4\u65E0\u9053\u548C\u6E5B\u6E4E\u8352\u6DEB\u4E0A\u3002</p><p>\u53EA\u662F\u540A\u8BE1\u7684\u662F\uFF0C\u8FD9\u4E9B\u7F3A\u70B9\u5728\u540E\u6765\u7684\u5386\u53F2\u4E2D\u5374\u4E00\u800C\u518D\u518D\u800C\u4E09\u51FA\u73B0\u2014\u2014\u8FD9\u5012\u5F88\u503C\u5F97\u4E00\u8BF4\u3002</p><p><strong>\u6211\u4EEC\u6765\u4ECE\u5934\u8BB2\u8D77</strong>\u3002</p><p>\u5218\u806A\u662F\u524D\u8D75\u5F00\u56FD\u7687\u5E1D<strong>\u5218\u6E0A</strong>\u7684\u513F\u5B50\uFF0C\u4F46\u4ED6\u7684\u7687\u4F4D\u5374\u662F\u7BE1\u6765\u7684\u3002\u8FD9\u91CC\u9762\uFF0C\u6709\u4E00\u4E2A\u8001\u751F\u5E38\u8C08\u7684\u6545\u4E8B\u3002</p><p>\u5218\u6E0A\u5F53\u65F6\u7ACB\u7684\u592A\u5B50\u662F<strong>\u5218\u548C</strong>\u3002\u4F46\u5218\u6E0A\u75C5\u6B7B\u540E\u5C1A\u672A\u4E0B\u846C\uFF0C\u5175\u53D8\u5C31\u5DF2\u7ECF\u5C55\u5F00\u2014\u2014\u8FD9\u79CD\u5267\u672C\u6211\u4EEC\u53F8\u7A7A\u89C1\u60EF\uFF0C\u53EA\u662F\u8FD9\u6B21\u7A0D\u6709\u4E0D\u540C\uFF1A\u5175\u53D8\u7684\u53D1\u8D77\u8005\uFF0C\u4E0D\u662F\u56FE\u8C0B\u7BE1\u4F4D\u7684\u7687\u5B50\u4EEC\uFF0C\u800C\u662F\u5373\u5C06\u7EE7\u4F4D\u7684\u592A\u5B50\u5218\u548C\u672C\u4EBA\u3002</p><p>\u5218\u6E0A\u4E34\u7EC8\u524D\uFF0C\u7ED9\u5218\u548C\u7559\u4E86\u597D\u591A\u4E2A\u8F85\u653F\u5927\u81E3\uFF0C\u8FD9\u5176\u4E2D\uFF0C\u5C31\u5305\u62EC\u5218\u806A\u7B49\u5144\u5F1F\u3002\u6309\u7167\u4E2D\u539F\u7684\u653F\u6CBB\u4F20\u7EDF\uFF0C\u5218\u6E0A\u7684\u8FD9\u79CD\u5B89\u6392\u80AF\u5B9A\u662F\u4E0D\u5408\u7406\u7684\uFF0C\u5144\u5F1F\u4EEC\u79BB\u6743\u529B\u6838\u5FC3\u592A\u8FD1\uFF0C\u5BB9\u6613\u4EA7\u751F\u7978\u4E71\u3002</p><p>\u4F46\u5218\u6E0A\u5374\u6709\u4E0D\u5F97\u4EE5\u7684\u521D\u8877\u3002</p><p>\u524D\u8D75\u81EA\u521B\u7ACB\u4EE5\u6765\uFF0C\u56DB\u5904\u5F81\u6218\uFF0C\u9886\u519B\u7684\u5C06\u9886\uFF0C\u6709\u5F88\u591A\u5C31\u662F\u5218\u6E0A\u7684\u513F\u5B50\u4EEC\u3002\u8FD9\u662F\u8349\u539F\u5343\u767E\u5E74\u6765\u7684\u9EC4\u91D1\u6CD5\u5219\uFF1A\u6743\u529B\u662F\u9760\u519B\u961F\u7EF4\u6301\u7684\uFF0C\u800C\u519B\u961F\u53C8\u53EA\u80FD\u4EA4\u7ED9\u6700\u4FE1\u4EFB\u7684\u4EBA\uFF0C\u6B63\u6240\u8C13\u6253\u864E\u4EB2\u5144\u5F1F\uFF0C\u4E0A\u9635\u7236\u5B50\u5175\u3002</p><p>\u5218\u806A\u5C31\u662F\u8FD9\u4E9B\u513F\u5B50\u4E2D\u7684\u4F7C\u4F7C\u8005\u3002</p><p>\u4F46\u8BA9\u4EBA\u5C34\u5C2C\u7684\u662F\uFF0C\u7B49\u5230\u5218\u6E0A\u4E34\u7EC8\u524D\uFF0C<strong>\u51E0\u4E2A\u513F\u5B50\u624B\u91CC\u90FD\u5DF2\u7ECF\u6709\u4E86\u5404\u81EA\u7684\u52BF\u529B\uFF0C\u5C31\u53EA\u6709\u592A\u5B50\u6CA1\u6709</strong>\u2014\u2014\u56E0\u4E3A\u5728\u6C49\u4EBA\u7684\u653F\u6CBB\u4F20\u7EDF\u91CC\uFF0C\u592A\u5B50\u529F\u4E0D\u80FD\u8D4F\uFF0C\u8FC7\u4E0D\u80FD\u7F5A\u3002\u6240\u4EE5\uFF0C\u592A\u5B50\u662F\u4E0D\u9886\u5175\u7684\u3002</p><p>\u8FD9\u76F4\u63A5\u5BFC\u81F4\u5218\u6E0A\u8FC7\u4E16\u4EE5\u540E\uFF0C\u5218\u548C\u624B\u91CC\u53EA\u6709\u4E00\u4E2A\u7A7A\u8361\u8361\u7684\u671D\u5EF7\u3002</p><p>\u6240\u4EE5\u5218\u548C\u624D\u60F3\u501F\u7740\u4E27\u4E8B\uFF0C\u8D81\u4E71\u9664\u53BB\u51E0\u4E2A\u5F1F\u5F1F\uFF0C\u593A\u56DE\u5175\u6743\u3002\u7136\u800C\u4ED6\u5B9E\u529B\u5B9E\u5728\u4E0D\u6D4E\uFF0C\u5077\u9E21\u4E0D\u6210\uFF0C\u53CD\u5012\u628A\u81EA\u5DF1\u7684\u547D\u7ED9\u642D\u4E86\u8FDB\u53BB\u3002</p><p>\u5728\u8FD9\u573A\u5175\u53D8\u4E2D\uFF0C\u5218\u806A\u6700\u7EC8\u8D62\u5F97\u4E86\u80DC\u5229\uFF0C\u767B\u57FA\u4E3A\u5E1D\u3002</p><p>\u4E00\u5E74\u540E\uFF0C\u5218\u806A\u90E8\u5C06\u5218\u66DC\u3001\u738B\u5F25\u653B\u7834\u6D1B\u9633\uFF0C<strong>\u4FD8\u864F\u664B\u6000\u5E1D\uFF0C\u540E\u6740\u4E4B</strong>\u3002\u53C8\u8FC7\u4E86\u4E94\u5E74\uFF0C\u540C\u6837\u7684\u4E8B\u60C5\u53C8\u6765\u4E86\u4E00\u904D\uFF0C<strong>\u5218\u66DC\u4FD8\u864F\u664B\u610D\u5E1D\uFF0C\u897F\u664B\u6B63\u5F0F\u706D\u4EA1</strong>\u3002</p><p>\u5728\u5308\u5974\u4EBA\u773C\u91CC\uFF0C\u8FD9\u7EDD\u5BF9\u662F\u201C\u5F6A\u658C\u53F2\u518C\u201D\u7684\u5927\u529F\u52B3\u3002\u4F46\u6C49\u4EBA\u7684\u53F2\u4E66\u5F53\u7136\u4E0D\u613F\u610F\u591A\u63D0\u8FD9\u4E9B\uFF0C\u76F8\u53CD\uFF0C\u5012\u662F\u5BF9\u5218\u806A\u7684\u4E2A\u4EBA\u54C1\u5FB7\u7740\u58A8\u9887\u591A\uFF0C\u603B\u7ED3\u8D77\u6765\uFF0C\u4E3B\u8981\u662F\u56DB\u4E2A\u5B57\uFF1A</p><blockquote><p>\u597D\u8272\uFF0C\u55DC\u6740</p></blockquote><p><strong>\u9996\u5148\u662F<code>\u597D\u8272</code></strong>\u3002</p><p>\u5218\u806A\u6709\u5F88\u591A\u5983\u5AD4\uFF0C\u6BD4\u5982\u201C\u516D\u5218\u201D\uFF0C\u8FD9\u662F\u65F6\u4EFB\u592A\u4FDD\u5218\u6BB7\u7684\u4E24\u4E2A\u5973\u513F\u548C\u56DB\u4E2A\u5B59\u5973\u3002\u5218\u806A\u5C06\u5979\u4EEC\u4E00\u80A1\u8111\u513F\u5168\u90E8\u6536\u4E3A\u5DF1\u6709\uFF0C\u59D1\u59D1\u4EEC\u5C01\u4E3A\u8D35\u5AD4\uFF0C\u4F84\u5973\u4EEC\u5C01\u4E3A\u8D35\u4EBA\u3002\u540E\u6765\u6709\u4E00\u6B21\uFF0C\u5218\u806A\u8DDF\u88AB\u4FD8\u864F\u7684\u664B\u6000\u5E1D\u53F8\u9A6C\u70BD\u804A\u5F97\u5F00\u5FC3\uFF0C\u987A\u624B\u5C31\u5F88\u5927\u5EA6\u5730\u9001\u51FA\u53BB\u4E86\u4E00\u4E2A\u3002</p><p>\u5218\u806A\u8FD8\u5F88\u559C\u6B22\u4ED6\u7238\u7684\u5973\u4EBA\u3002\u5218\u6E0A\u6B7B\u540E\uFF0C\u7687\u540E\u5355\u6C0F\u88AB\u5C0A\u4E3A\u7687\u592A\u540E\uFF0C\u4F46\u4E0D\u4E45\uFF0C\u4E5F\u88AB\u6536\u5230\u4E86\u5218\u806A\u7684\u6000\u91CC\u3002\u8FD9\u4F4D\u5355\u6C0F\u7684\u513F\u5B50\u5218\u4E42\uFF0C\u6B64\u65F6\u8FD8\u662F\u7687\u592A\u5F1F\uFF0C\u4ED6\u4EEC\u4E4B\u95F4\u8FD9\u590D\u6742\u7684\u5173\u7CFB\uFF0C\u5B9E\u5728\u662F\u7406\u4E0D\u987A\u3002\u6700\u7EC8\uFF0C\u5728\u513F\u5B50\u7684\u6307\u8D23\u4E2D\uFF0C\u5355\u6C0F\u7F9E\u6127\u800C\u6B7B\u3002</p><p>\u6B7B\u4E86\u4E00\u4E2A\u5355\u6C0F\uFF0C\u5BF9\u5218\u806A\u6765\u8BF4\u5E76\u4E0D\u6253\u7D27\uFF0C\u4ED6\u6709\u7684\u662F\u5973\u4EBA\u3002\u9664\u4E86\u524D\u9762\u8BF4\u7684\u4E24\u4F4D\u5218\u8D35\u5AD4\u3001\u56DB\u4F4D\u5218\u8D35\u4EBA\u5916\uFF0C\u5218\u806A\u540E\u6765\u53C8\u7ACB\u4E86\u597D\u4E9B\u4E2A\u7687\u540E\u2014\u2014\u524D\u540E\u5927\u6982\u6709\u5341\u51E0\u4E2A\uFF0C\u800C\u4E14\u51E0\u4E4E\u90FD\u662F\u540C\u65F6\u5E76\u7ACB\u7684\u3002\u6BD4\u5982\uFF0C\u4E0A\u7687\u540E\u3001\u5DE6\u7687\u540E\u3001\u53F3\u7687\u540E......\u7B49\u7B49\u7B49\u7B49\u3002</p><p>\u6709\u540D\u6709\u4F4D\u7684\u90FD\u5982\u6B64\u4E4B\u591A\uFF0C\u81F3\u4E8E\u90A3\u4E9B\u65E0\u540D\u65E0\u59D3\u7684\uFF0C\u57FA\u6570\u6709\u591A\u5927\u5C31\u65E0\u6CD5\u60F3\u8C61\u4E86\u3002</p><p><strong>\u597D\u8272\u4E4B\u5916\uFF0C\u5218\u806A\u8FD8\u6709\u53E6\u4E00\u4E2A\u7279\u70B9\uFF1A<code>\u55DC\u6740</code></strong>\u3002</p><p>\u65E9\u5E74\u95F4\u5218\u806A\u8FD8\u662F\u7687\u5B50\u7684\u65F6\u5019\uFF0C\u9886\u5175\u51FA\u5F81\u5E76\u6CA1\u6709\u7559\u4E0B\u591A\u5C11\u55DC\u6740\u7684\u8BB0\u5F55\u3002\u4F46\u81EA\u4ECE\u505A\u4E86\u7687\u5E1D\u4EE5\u6765\uFF0C\u5C24\u5176\u662F\u4FD8\u864F\u4E86\u664B\u6000\u5E1D\u540E\uFF0C\u5218\u806A\u7684\u6076\u6027\u4FBF\u9010\u6E10\u88AB\u91CA\u653E\u51FA\u6765\uFF0C\u7ECF\u5E38\u662F\u4E0D\u95EE\u9752\u7EA2\u7682\u767D\u7684\u6740\u4EBA\u3002</p><p>\u6709\u4EBA\u56E0\u4E3A\u9635\u524D\u6740\u4EBA\u800C\u88AB\u6740\uFF0C\u6709\u4EBA\u56E0\u4E3A\u8FDB\u8D21\u9C7C\u867E\u4E0D\u591F\u800C\u88AB\u6740\uFF0C\u8FD8\u6709\u4EBA\u56E0\u4E3A\u5EFA\u9020\u5BAB\u6BBF\u4E0D\u80FD\u6309\u65F6\u5B8C\u5DE5\u800C\u88AB\u6740......\u81F3\u4E8E\u6000\u7591\u8C0B\u53CD\u800C\u88AB\u6740\u7684\uFF0C\u5C31\u66F4\u52A0\u4E0D\u53EF\u80DC\u6570\u4E86\u3002\u751A\u81F3\u5982\u592A\u5E08\u3001\u592A\u5085\u3001\u592A\u4FDD\u4E4B\u7C7B\u7684\u9AD8\u5B98\uFF0C\u4E5F\u7167\u6740\u4E0D\u8BEF\u3002</p><p>\u5230\u665A\u5E74\uFF0C\u5218\u806A\u56E0\u8352\u6DEB\u800C\u4E0D\u7406\u4E8B\uFF0C\u5C06\u653F\u4E8B\u4E00\u5E76\u4EA4\u7ED9\u5BA6\u5B98\u5904\u7406\u3002\u5047\u516C\u6D4E\u79C1\u4E4B\u4E0B\uFF0C\u53C8\u6709\u65E0\u6570\u9AD8\u5B98\u88AB\u6740\uFF0C\u5218\u806A\u4E5F\u4E00\u5E76\u9ED8\u8BB8\u3002</p><p>\u4E94\u80E1\u4E71\u534E\uFF0C\u4EE5\u53CA\u540E\u6765\u7684\u5357\u5317\u671D\u65F6\u671F\uFF0C\u6709\u4E00\u4E2A\u5F88\u5947\u7279\u7684\u73B0\u8C61\uFF1A</p><p><strong>\u76DB\u4EA7<code>\u53D8\u6001\u7687\u5E1D</code></strong>\u3002</p><p>\u524D\u6709\u5218\u806A\u3001\u82FB\u751F\uFF0C\u540E\u6709\u5317\u9F50\u9AD8\u6C0F\uFF0C\u751A\u81F3\u4E8E\u5357\u671D\u7684\u5F88\u591A\u7687\u5E1D\uFF0C\u4ED6\u4EEC\u867D\u7136\u53D8\u6001\u7684\u91CD\u70B9\u5404\u4E0D\u76F8\u540C\uFF0C\u4F46\u7EDD\u5927\u591A\u6570\u90FD\u6495\u4E0D\u6389\u597D\u8272\u3001\u55DC\u6740\u7684\u6807\u7B7E\u3002</p><p>\u5218\u806A\u53EF\u8C13\u662F\u5176\u4E2D\u7684\u4EE3\u8868\uFF0C\u5373\u4FBF\u4ED6\u7B97\u4E0D\u4E0A\u662F\u4E00\u4E2A\u65E0\u80FD\u7684\u7687\u5E1D\uFF0C\u4F46\u660F\u8069\u3001\u6DEB\u4E71\u3001\u66B4\u8650\u7B49\u8D1F\u9762\u884C\u4E3A\uFF0C\u5374\u4E5F\u4E00\u4E2A\u4E0D\u7F3A\u3002</p><p>\u4E3A\u4EC0\u4E48\uFF1F</p><p>\u5728\u6F2B\u957F\u7684\u5386\u53F2\u4E2D\uFF0C\u5982\u679C\u53EA\u51FA\u73B0\u4E00\u4E24\u4E2A\u8FD9\u6837\u7684\u7687\u5E1D\uFF0C\u90A3\u6211\u4EEC\u8FD8\u80FD\u7528\u8FD9\u662F\u5C0F\u6982\u7387\u4E8B\u4EF6\u6765\u89E3\u91CA\u3002\u4F46\u5728\u4E0D\u5230\u4E09\u767E\u5E74\u7684\u65F6\u95F4\u91CC\uFF0C\u96C6\u4E2D\u51FA\u73B0\u8FD9\u4E48\u591A\u7684\u6837\u672C\uFF0C\u800C\u5728\u5176\u4ED6\u7684\u5386\u53F2\u65F6\u671F\u4E4B\u5185\u53C8\u76F8\u5BF9\u8F83\u5C11\uFF0C\u90A3\u8FD9\u5C31\u80AF\u5B9A\u4E0D\u80FD\u7528\u968F\u673A\u4E8B\u4EF6\u6765\u89E3\u91CA\u4E86\u3002</p><p><strong>\u4E00\u5B9A\u662F\u6709\u4E00\u4E9B\u539F\u56E0\u5B58\u5728\uFF0C\u624D\u4F1A\u5BFC\u81F4\u8FD9\u4E48\u591A\u201C\u4E0D\u6B63\u5E38\u201D\u7684\u4EBA\u51FA\u73B0</strong>\u3002</p><p>\u800C\u60F3\u8981\u7406\u89E3\u8FD9\u79CD\u201C\u4E0D\u6B63\u5E38\u201D\u7684\u6210\u56E0\uFF0C\u5C31\u9700\u8981\u4ECE\u6E90\u5934\u8BF4\u8D77\uFF1A</p><p>\u5F53\u5E74\uFF0C\u5218\u6E0A\u4EE5\u5308\u5974\u4EBA\u7684\u8EAB\u4EFD\u79F0\u5E1D\uFF0C\u5F00\u521B\u4E86<strong>\u80E1\u6C49\u878D\u5408</strong>\u7684\u5148\u6CB3\u3002\u4F46\u5218\u6E0A\u968F\u5373\u8EAB\u6B7B\uFF0C\u5BFC\u81F4\u8FD9\u79CD\u878D\u5408\u8FC7\u7A0B\u5728\u4ED6\u8EAB\u540E\u4EA7\u751F\u4E86\u5DE8\u5927\u7684\u95EE\u9898\uFF0C\u5E76\u9010\u6E10\u66B4\u9732\u3002</p><p>\u53D1\u751F\u5728\u5218\u806A\u8EAB\u4E0A\u7684\u4E8B\uFF0C\u5176\u5B9E\u5C31\u662F\u8FD9\u4E9B\u95EE\u9898\u4E2D\u7684\u4E00\u90E8\u5206\uFF1A</p><blockquote><p>\u6E38\u79BB\u4E8E\u7EA6\u675F\u4E4B\u5916\u7684\u7687\u6743\u3002</p></blockquote><p>\u5218\u806A\u7EE7\u4F4D\u540E\uFF0C\u9762\u4E34\u7684\u5176\u5B9E\u662F\u8FD9\u6837\u4E00\u79CD\u5C40\u9762\uFF1A\u4ED6\u65E2\u662F\u5308\u5974\u4EBA\u7684\u9886\u8896\uFF0C\u53C8\u662F\u6C49\u4EBA\u7684\u7687\u5E1D\u3002<strong>\u4F46\u5308\u5974\u4EBA\u7684\u4E60\u60EF\u548C\u6C49\u4EBA\u7684\u4F20\u7EDF\uFF0C\u5374\u53C8\u7EDF\u7EDF\u7BA1\u4E0D\u4E86\u4ED6</strong>\u3002</p><p>\u65E2\u7136\u5DF2\u7ECF\u79F0\u5E1D\uFF0C\u90A3\u4E48\u5728\u9762\u5BF9\u671D\u5EF7\u548C\u540E\u5BAB\u7684\u95EE\u9898\u65F6\uFF0C\u5218\u806A\u5C31\u662F\u4EE5\u4E00\u4E2A\u6C49\u4EBA\u7687\u5E1D\u7684\u8EAB\u4EFD\u5728\u5904\u7406\u3002<strong>\u8FD9\u65F6\u5019\uFF0C\u5308\u5974\u4EBA\u7684\u4F20\u7EDF\u81EA\u7136\u5DF2\u7ECF\u65E0\u6CD5\u7EA6\u675F\u4ED6</strong>\u3002</p><p>\u90A3\u6C49\u4EBA\u7684\u7687\u5E1D\u662F\u60F3\u505A\u4EC0\u4E48\u5C31\u505A\u4EC0\u4E48\u7684\u5417\uFF1F</p><blockquote><p>\u5F53\u7136\u4E0D\u662F\u3002\u7687\u6743\u5E76\u4E0D\u50CF\u5F88\u591A\u4EBA\u60F3\u7684\u4E00\u6837\uFF0C\u662F\u81F3\u9AD8\u65E0\u4E0A\u3001\u4E0D\u53D7\u7EA6\u675F\u7684\u3002\u8BDA\u7136\uFF0C\u4EE5\u73B0\u4EE3\u4EBA\u7684\u773C\u5149\u6765\u770B\uFF0C\u7687\u5E1D\u5E76\u4E0D\u53D7\u53F8\u6CD5\u4F53\u7CFB\u7684\u7EA6\u675F\uFF0C\u4F46\u8FD9\u5E76\u4E0D\u4EE3\u8868\u7687\u5E1D\u80FD\u4E3A\u6240\u6B32\u4E3A\u3002</p><p>\u7687\u5E1D\u7684\u884C\u4E3A\uFF0C\u4F9D\u7136\u53D7\u5230\u7EA6\u675F\uFF0C\u800C\u7EA6\u675F\u4ED6\u7684\u529B\u91CF\uFF0C\u5219\u5305\u62EC\u6587\u5316\u3001\u7956\u5236\u3001\u5B97\u5BA4\uFF0C\u4EE5\u53CA\u6587\u5B98\u96C6\u56E2\u7684\u5236\u7EA6\u7B49\u7B49\u3002</p><p><strong>\u6587\u5316\u4E0A\u7684\u7EA6\u675F\uFF0C\u6BD4\u5982\u201C\u5723\u671D\u4EE5\u5B5D\u6CBB\u5929\u4E0B\u201D</strong>\u3002\u5F53\u5B5D\u662F\u4E00\u4E2A\u738B\u671D\u6700\u6839\u672C\u7684\u9053\u5FB7\u51C6\u5219\uFF0C\u90A3\u4E48\u5BF9\u4E8E\u7EDD\u5927\u591A\u6570\u7687\u5E1D\u6765\u8BF4\uFF0C\u8FD9\u5C31\u662F\u65E0\u6CD5\u903E\u8D8A\u7684\u5E95\u7EBF\u3002</p><p><strong>\u7956\u5236\u4E0A\u7684\u7EA6\u675F\uFF0C\u5219\u4EE5\u6C49\u671D\u7684\u201C\u975E\u5218\u4E0D\u5F97\u4E3A\u738B\u201D\u6700\u4E3A\u6709\u540D</strong>\u3002\u8FD9\u662F\u5218\u90A6\u8BA2\u4E0B\u7684\u89C4\u77E9\uFF0C\u5218\u6C0F\u5B50\u5B59\u57FA\u672C\u4E0A\u90FD\u6CA1\u6709\u8FDD\u53CD\u8FC7\u3002\u5373\u4F7F\u5230\u4E86\u897F\u6C49\u672B\u5E74\uFF0C\u6C49\u54C0\u5E1D\u518D\u600E\u4E48\u559C\u6B22\u4ED6\u7684\u5C0F\u4EB2\u4EB2\u8463\u8D24\uFF0C\u4E5F\u6CA1\u80FD\u7ED9\u4ED6\u5C01\u8FC7\u738B\u7235\uFF0C\u5C31\u662F\u8FD9\u6761\u7956\u5236\u7684\u7EA6\u675F\u4F5C\u7528\u3002</p><p><strong>\u5B97\u5BA4\u7684\u7EA6\u675F\uFF0C\u5386\u4EE3\u5404\u4E0D\u76F8\u540C</strong>\u3002\u8FD9\u4E00\u70B9\u897F\u664B\u4F53\u73B0\u5F97\u6BD4\u8F83\u660E\u663E\uFF0C\u81EA\u664B\u6B66\u5E1D\u5206\u5C01\u4EE5\u6765\uFF0C\u5B97\u5BA4\u5C31\u662F\u4E00\u652F\u91CD\u8981\u7684\u653F\u6CBB\u529B\u91CF\uFF0C\u5E76\u6700\u7EC8\u917F\u6210\u4E86\u516B\u738B\u4E4B\u4E71\u7684\u7978\u4E71\u3002</p><p><strong>\u800C\u6700\u4E3A\u5173\u952E\u7684\uFF0C\u5219\u662F\u6587\u5B98\u96C6\u56E2\u7684\u7EA6\u675F</strong>\u3002</p><p>\u4ECE\u9053\u7406\u4E0A\u6765\u770B\uFF0C\u7687\u5E1D\u4E0E\u5B98\u5458\u4E4B\u95F4\u662F\u541B\u81E3\u5173\u7CFB\uFF0C\u81E3\u5B50\u542C\u547D\u4E8E\u541B\u738B\u3002\u4F46\u8981\u6CE8\u610F\uFF0C\u542C\u547D\u5E76\u4E0D\u662F\u5B8C\u5168\u670D\u4ECE\u3002\u541B\u738B\u5982\u679C\u60F3\u8BA4\u771F\u6CBB\u7406\u56FD\u5BB6\uFF0C\u5C31\u5FC5\u987B\u4E0E\u81E3\u4E0B\u7EF4\u6301\u4E00\u4E2A\u826F\u597D\u7684\u4E92\u52A8\u5173\u7CFB\uFF0C\u800C\u5728\u8FD9\u4E2A\u4E92\u52A8\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u5F7C\u6B64\u90FD\u4F1A\u4EA7\u751F\u7EA6\u675F\u3002</p><p>\u8FD9\u7C7B\u4F3C\u4E8E\u4F01\u4E1A\u4E2D\u9AD8\u7BA1\u548C\u4E2D\u5C42\u9AA8\u5E72\u7684\u5173\u7CFB\u3002\u6709\u6210\u6548\u7684\u5DE5\u4F5C\uFF0C\u4E00\u5B9A\u662F\u53CC\u65B9\u76F8\u4E92\u534F\u8C03\u51FA\u6765\u7684\u3002</p><p>\u7687\u5E1D\u5728\u884C\u4E8B\u65F6\uFF0C\u9700\u8981\u63D0\u4F9B\u4E00\u5B9A\u7684\u5408\u7406\u6027\uFF0C\u5982\u6B64\u624D\u80FD\u7EF4\u6301\u4F4F\u4ED6\u7684\u6709\u6548\u7EDF\u6CBB\u3002</p></blockquote><p><strong>\u800C\u5BF9\u6BD4\u4E94\u80E1\u4E71\u534E\u65F6\u671F\uFF0C\u4F60\u4F1A\u53D1\u73B0\uFF0C\u4EE5\u4E0A\u8FD9\u4E9B\u9650\u5236\uFF0C\u51E0\u4E4E\u5168\u90FD\u4E0D\u5B58\u5728\u4E86</strong>\u3002</p><p>\u4ECE\u6587\u5316\u4E0A\u770B\uFF0C\u81EA\u5218\u6E0A\u4EE5\u964D\uFF0C\u541B\u4E3B\u4EEC\u5BF9\u4E8E\u6C49\u6587\u5316\u7684\u63A5\u89E6\u5E76\u4E0D\u591A\uFF0C\u63A5\u53D7\u5C31\u66F4\u8C08\u4E0D\u4E0A\u3002</p><p>\u81F3\u4E8E\u5176\u4ED6\u5982\u7956\u5236\u3001\u5B97\u5BA4\u4E4B\u7C7B\u7684\u7EA6\u675F\uFF0C\u4EE5\u5308\u5974\u4EBA\u7684\u6807\u51C6\u770B\uFF0C\u5C31\u66F4\u52A0\u65E0\u6CD5\u60F3\u8C61\u4E86\u3002</p><p>\u5728\u5308\u5974\u4EBA\u7684\u6743\u529B\u7EE7\u627F\u4E2D\uFF0C\u6740\u622E\u662F\u518D\u6B63\u5E38\u4E0D\u8FC7\u7684\u4E8B\u3002\u4ECE\u5308\u5974\u4EBA\u5F15\u4EE5\u4E3A\u8C6A\u7684\u7956\u5148\u5192\u987F\u5355\u4E8E\u4EE5\u6765\u4FBF\u662F\u5982\u6B64\u2014\u2014\u5F53\u5E74\uFF0C\u5192\u987F\u662F\u6740\u6389\u4EB2\u751F\u7236\u4EB2\u624D\u5F97\u4EE5\u7EE7\u4EFB\u5355\u4E8E\u4E4B\u4F4D\u7684\u3002\u5728\u8FD9\u79CD\u4F20\u7EDF\u4E4B\u4E0B\uFF0C\u7956\u5236\u548C\u5B97\u5BA4\u6839\u672C\u5C31\u65E0\u4ECE\u7EA6\u675F\u7687\u5E1D\u7684\u884C\u4E3A\u3002</p><p>\u6700\u540E\u5C31\u53EA\u5269\u4E0B\u6587\u5B98\u96C6\u56E2\u7EA6\u675F\u8FD9\u4E00\u6761\u8DEF\u4E86\u3002\u53EF\u8FD9\u4E2A\u7FA4\u4F53\uFF0C\u5728\u5308\u5974\u4EBA\u8FD9\u91CC\u4E5F\u662F\u4E0D\u5B58\u5728\u7684\u3002\u6B64\u65F6\u6C49\u65CF\u58EB\u5927\u592B\u5BF9\u4E8E\u5308\u5974\u4EBA\u8FD8\u666E\u904D\u5B58\u5728\u7740\u6392\u65A5\uFF0C\u5218\u6E0A\u7684\u671D\u5EF7\u4E0A\u5C31\u6CA1\u6709\u51E0\u4E2A\u58EB\u5927\u592B\u7684\u8EAB\u5F71\u3002</p><p>\u56E0\u6B64\uFF0C<strong>\u6C49\u4EBA\u4F20\u7EDF\u4E0A\u5BF9\u4E8E\u7687\u5E1D\u7684\u7EA6\u675F\uFF0C\u5728\u5218\u806A\u8FD9\u91CC\u5176\u5B9E\u90FD\u662F\u4E0D\u5B58\u5728\u7684</strong>\u3002\u6700\u540E\u7684\u7ED3\u679C\uFF0C\u5C31\u662F\u5218\u806A\u53EA\u7EE7\u627F\u4E86\u7687\u5E1D\u7684\u6743\u529B\uFF0C\u800C\u6CA1\u6709\u63A5\u53D7\u7687\u5E1D\u7684\u4E49\u52A1\u3002</p><p>\u4E0D\u53EA\u662F\u5218\u806A\uFF0C\u8FD9\u4E2A\u5C40\u9762\u5728\u5357\u5317\u5206\u88C2\u65F6\u671F\u53CD\u590D\u4E0A\u6F14\u3002\u5927\u90E8\u5206\u5F02\u65CF\u5EFA\u7ACB\u7684\u653F\u6743\uFF0C\u90FD\u5B58\u5728\u7740\u7C7B\u4F3C\u60C5\u5F62\u3002\u4ED6\u4EEC\u867D\u7136\u62AB\u7740\u6C49\u4EBA\u5E1D\u5236\u7684\u5916\u8863\uFF0C\u4F46\u5728\u9AA8\u5B50\u91CC\uFF0C\u8FD9\u79CD\u5E1D\u5236\u5374\u5E76\u4E0D\u5B8C\u5584\u3002\u540C\u65F6\uFF0C\u8349\u539F\u5F0F\u7684\u5F3A\u8005\u4E3A\u738B\u89C2\u5FF5\uFF0C\u53C8\u6781\u5927\u7684\u523A\u6FC0\u7740\u80E1\u4EBA\u7687\u5E1D\u4EEC\u4E0D\u65AD\u81A8\u80C0\u7684\u6B32\u671B\u3002</p><p>\u8FD9\u4E9B\u5F20\u529B\u6700\u7EC8\u663E\u73B0\u5728\u5386\u53F2\u4E2D\u7684\u5F62\u6001\uFF0C\u4FBF\u662F\u4E00\u4E2A\u53C8\u4E00\u4E2A\u66B4\u8650\u7687\u5E1D\u7684\u51FA\u73B0\u3002</p><p>\u8FD9\u5176\u4E2D\uFF0C\u7687\u5E1D\u4EEC\u7684\u4E2A\u4EBA\u7D20\u8D28\u56FA\u7136\u662F\u4E00\u4E2A\u91CD\u8981\u56E0\u7D20\uFF0C\u4F46\u66F4\u4E3A\u6839\u672C\u7684\u539F\u56E0\uFF0C\u8FD8\u662F\u4F53\u5236\u7684\u4E0D\u5B8C\u5584\u3002</p><p><strong>\u80E1\u4EBA\u8FD8\u6CA1\u6709\u5B66\u4F1A\u600E\u4E48\u5F53\u7687\u5E1D</strong>\u3002</p><blockquote><p>\u6709\u5173\u53D8\u9769\u4E0E\u8F6C\u578B\u3002</p></blockquote><p>\u6240\u8C13\u7684\u8F6C\u578B\uFF0C\u6216\u8005\u8BF4\u53D8\u9769\uFF0C\u90FD\u662F\u65E7\u6709\u7684\u4F53\u5236\u5F00\u59CB\u9010\u6E10\u74E6\u89E3\uFF0C\u5E76\u9010\u6B65\u5EFA\u7ACB\u8D77\u65B0\u4F53\u5236\u7684\u4E00\u4E2A\u8FC7\u7A0B\u3002</p><p>\u8FD9\u4E2A\u8FC7\u7A0B\u662F\u6F2B\u957F\u7684\uFF0C\u4E0D\u8BBA\u662F\u65E7\u6709\u4F53\u5236\u7684\u5B8C\u5168\u6D88\u89E3\u3001\u8FD8\u662F\u65B0\u5236\u5EA6\u7684\u9010\u6E10\u6210\u578B\uFF0C\u90FD\u9700\u8981\u65F6\u95F4\u3002</p><p><strong>\u800C\u8FD9\u6BB5\u65F6\u95F4\uFF0C\u5C31\u6210\u4E86\u6743\u529B\u7684<code>\u7A7A\u7A97\u671F</code></strong>\uFF1A\u65E7\u6709\u4F53\u5236\u65E0\u6CD5\u53D1\u6325\u4F5C\u7528\uFF0C\u65B0\u5174\u89C4\u5219\u4E5F\u6CA1\u6709\u80FD\u529B\u88AB\u6267\u884C\u3002</p><p>\u8FD9\u5176\u4E2D\uFF0C\u52BF\u5FC5\u4F1A\u51FA\u73B0\u5404\u79CD\u5404\u6837\u7684\u95EE\u9898\uFF0C\u6709\u7684\u95EE\u9898\u751A\u81F3\u662F\u5BF9\u5E95\u7EBF\u7684\u539F\u5219\u6027\u7A81\u7834\u3002\u4F46\u6211\u4EEC\u5E94\u8BE5\u8BA4\u8BC6\u5230\uFF0C<strong>\u8FD9\u4E0D\u662F\u8F6C\u578B\u65B9\u5411\u7684\u95EE\u9898\uFF0C\u800C\u662F\u8F6C\u578B\u672C\u8EAB\u7684\u95EE\u9898</strong>\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u53EA\u8981\u5B58\u5728\u6539\u53D8\uFF0C\u5C31\u4F1A\u4EA7\u751F\u95EE\u9898\uFF0C\u65E0\u8BBA\u53D8\u52A8\u7684\u65B9\u5411\u6B63\u786E\u4E0E\u5426\u3002</p><p>\u5927\u5230\u56FD\u5BB6\u3001\u7EC4\u7EC7\uFF0C\u5C0F\u5230\u56E2\u961F\u3001\u4E2A\u4EBA\uFF0C\u90FD\u4F1A\u5B58\u5728\u8FD9\u79CD\u60C5\u5F62\u3002\u6539\u9769\u5F00\u653E\u521D\u671F\u2014\u2014\u4EE5\u516B\u4E5D\u5341\u5E74\u4EE3\u4E3A\u4E3B\uFF0C\u4E2D\u56FD\u51FA\u73B0\u8FC7\u5404\u79CD\u5404\u6837\u7684\u7ECF\u6D4E\u3001\u793E\u4F1A\u95EE\u9898\uFF0C\u6709\u5F88\u591A\u4EBA\u56E0\u6B64\u800C\u53CD\u5BF9\u6539\u9769\u3002\u6240\u5E78\uFF0C\u6539\u9769\u7684\u9053\u8DEF\u8FD8\u662F\u575A\u6301\u4E86\u4E0B\u6765\uFF0C\u7136\u540E\u624D\u6709\u4E2D\u56FD\u4ECA\u65E5\u4E4B\u6210\u5C31\u3002</p><p>\u8FD9\u4E9B\u95EE\u9898\uFF0C\u90FD\u4E0D\u662F\u56E0\u4E3A\u6539\u9769\u65B9\u5411\u6709\u9519\u800C\u4EA7\u751F\uFF0C\u800C\u662F\u7531<strong>\u6539\u9769\u672C\u8EAB</strong>\u4EA7\u751F\u3002</p><p>\u4E2A\u4EBA\u4E5F\u662F\u5982\u6B64\u3002\u6BCF\u4E00\u4E2A\u8FC7\u6765\u4EBA\uFF0C\u90FD\u6DF1\u77E5\u81EA\u5DF1\u9752\u6625\u671F\u7684\u53DB\u9006\u5176\u5B9E\u5C31\u662F\u4E00\u4E2A\u4EBA\u5BF9\u81EA\u5DF1\u8FDB\u884C\u91CD\u5851\u7684\u95EE\u9898\u3002\u5728\u8FD9\u4E00\u4ECE\u5B69\u7AE5\u5230\u6210\u4EBA\u7684\u8F6C\u6298\u65F6\u671F\uFF0C\u9752\u5E74\u4EBA\u4E0D\u518D\u4EE5\u513F\u7AE5\u7684\u8EAB\u4EFD\u4E0E\u5916\u754C\u76F8\u5904\uFF0C\u800C\u5F00\u59CB\u4EE5\u5927\u4EBA\u7684\u773C\u5149\u770B\u5F85\u6574\u4E2A\u4E16\u754C\u3002\u8FD9\u5176\u4E2D\u7684\u7834\u88C2\u4E0E\u6784\u5EFA\uFF0C\u624D\u662F\u9020\u6210\u9752\u6625\u671F\u53DB\u9006\u7684\u4E3B\u8981\u539F\u56E0\u3002</p><p>\u9762\u5BF9\u8FD9\u4E2A\u6840\u9A9C\u4E0D\u9A6F\u7684\u65F6\u671F\uFF0C\u4E00\u4E2A\u8D1F\u8D23\u4EFB\u7684\u5BB6\u957F\u4E0D\u5E94\u8BE5\u4E00\u5473\u6253\u538B\uFF0C\u800C\u5E94\u8BE5\u6B63\u89C6\u95EE\u9898\uFF0C\u9010\u6B65\u5F15\u5BFC\u5B69\u5B50\u5EFA\u7ACB\u8D77\u6B63\u786E\u7684\u4E09\u89C2\u3002</p><p>\u4F60\u5FC5\u987B\u9996\u5148\u610F\u8BC6\u5230\uFF0C\u4ED6\u4E0D\u518D\u662F\u4F60\u542C\u8BDD\u7684\u597D\u5B9D\u5B9D\u4E86\u3002\u4F60\u5F97\u9002\u5E94\u8FD9\u79CD\u53D8\u5316\uFF0C\u5E76\u63A5\u53D7\u8FD9\u4E2A\u73B0\u5B9E\u3002</p><p><strong>\u518D\u56DE\u5230\u672C\u6587\u7684\u6545\u4E8B</strong>\uFF1A</p><p>\u5218\u806A\u5F53\u65F6\u7684\u60C5\u5F62\uFF0C\u5176\u5B9E\u4E5F\u662F\u5982\u6B64\u3002<strong>\u5308\u5974\u4EBA\u90E8\u843D\u8054\u76DF\u7684\u4F20\u7EDF\u7EA6\u675F\u4E0D\u518D\u751F\u6548\uFF0C\u6C49\u4EBA\u653F\u6743\u4E2D\u7684\u4E00\u7CFB\u5217\u7EA6\u675F\u6761\u4EF6\u53C8\u6CA1\u6709\u80FD\u591F\u53D1\u6325\u51FA\u6765</strong>\u3002\u6B63\u662F\u5728\u8FD9\u4E2A\u7A7A\u7A97\u671F\uFF0C\u7687\u6743\u7684\u529B\u91CF\u5F97\u4EE5\u65E0\u9650\u6269\u5927\u3002</p><p>\u800C\u5F53\u6743\u529B\u65E0\u6CD5\u88AB\u7EA6\u675F\uFF0C\u4EBA\u6027\u7684\u6076\u5C31\u4F1A\u88AB\u5F7B\u5E95\u91CA\u653E\u3002\u5176\u8868\u73B0\uFF0C\u5C31\u662F\u7687\u5E1D\u672C\u4EBA\u7684\u8352\u6DEB\u6B8B\u66B4\u3002</p><p>\u867D\u7136\u4ECE\u9053\u7406\u4E0A\u8BF4\uFF0C\u4ED6\u5E94\u8BE5\u63A5\u53D7\u4E24\u8005\u89C4\u5219\u7684\u5171\u540C\u7EA6\u675F\uFF0C\u4F46\u4E8B\u5B9E\u4E0A\u5374\u662F\uFF0C<strong>\u4E0D\u7BA1\u54EA\u5934\u90FD\u7BA1\u4E0D\u5230\u4ED6\uFF0C\u4ED6\u6210\u529F\u7684\u6E38\u79BB\u4E8E\u89C4\u5219\u7EA6\u675F\u4E4B\u5916</strong>\u3002</p><p>\u9020\u6210\u8FD9\u79CD\u7ED3\u679C\u7684\u539F\u56E0\uFF0C\u5C31\u662F\u8FD9\u79CD\u80E1\u6C49\u6DF7\u5408\u4F53\u5236\u7684\u5B58\u5728\uFF1A\u56E0\u4E3A\u5B58\u5728\u80E1\u6C49\u4E24\u79CD\u4F53\u5236\uFF0C\u6240\u4EE5\u8D85\u7136\u4E8E\u4E24\u79CD\u4F53\u5236\u4E4B\u4E0A\u7684\u7687\u5E1D\uFF0C\u81EA\u7531\u5E76\u4E0D\u662F\u8D8A\u6765\u8D8A\u5C0F\uFF0C\u800C\u662F\u8D8A\u6765\u8D8A\u5927\uFF0C\u76F4\u81F3\u6700\u7EC8\u6210\u529F\u9003\u79BB\u4E24\u79CD\u4F53\u5236\u7684\u7EA6\u675F\u3002</p><p>\u5386\u4EE3\u7687\u5E1D\u5C31\u7B97\u518D\u559C\u6B22\u5973\u4EBA\uFF0C\u4E5F\u4E0D\u4F1A\u540C\u65F6\u8BBE\u7ACB\u4E94\u516D\u4E2A\u7687\u540E\uFF1B\u5C31\u7B97\u518D\u6B8B\u5FCD\u55DC\u6740\uFF0C\u4E5F\u4E0D\u4F1A\u5927\u9762\u79EF\u8BDB\u6740\u671D\u81E3\u3002\u4F46\u5728\u5218\u806A\u8FD9\u91CC\uFF0C\u5F53\u5386\u4EE3\u7687\u5E1D\u7684\u6240\u6709\u7EA6\u675F\u90FD\u51E0\u4E4E\u4E0D\u5B58\u5728\uFF0C\u90A3\u4E48\u4ED6\u6709\u7684\uFF0C\u5C31\u53EA\u662F\u81EA\u5DF1\u7684\u968F\u5FC3\u6240\u6B32\u4E86\u3002</p><p><strong>\u9690\u85CF\u5728\u53D8\u6001\u884C\u4E3A\u4E4B\u4E0B\u7684\uFF0C\u662F\u88AB\u5236\u5EA6\u5F02\u5316\u4E86\u7684\u4EBA\u683C</strong>\u3002</p>',73),g=[s];function e(c,d){return o(),t("div",null,g)}const _=p(n,[["render",e],["__file","10.html.vue"]]);export{_ as default};
