import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as t,c as r,a as p,b as n,d as c,f as s,r as a}from"./app.cd90e563.js";const l={},d=s('<h1 id="\u5218\u8868" tabindex="-1"><a class="header-anchor" href="#\u5218\u8868" aria-hidden="true">#</a> \u5218\u8868</h1><blockquote><p>\u8BCF\u4EE5\u5317\u519B\u4E2D\u5019\u5218\u8868\u4E3A\u8346\u5DDE\u523A\u53F2\u3002\u65F6\u5BC7\u8D3C\u7EB5\u6A2A\uFF0C\u9053\u8DEF\u6897\u585E\uFF0C\u8868\u5355\u9A6C\u5165\u5B9C\u57CE\uFF0C\u8BF7\u5357\u90E1\u540D\u58EB\u84AF\u826F\u3001\u84AF\u8D8A\uFF0C\u4E0E\u4E4B\u8C0B\u66F0\uFF1A\u201C......\u201D\u4E43\u4F7F\u8D8A\u8BF1\u5B97\u8D3C\u5E05\uFF0C\u81F3\u8005\u4E94\u5341\u4E94\u4EBA\uFF0C\u7686\u65A9\u4E4B\u800C\u53D6\u5176\u4F17\u3002\u9042\u5F92\u6CBB\u8944\u9633\uFF0C\u9547\u629A\u90E1\u53BF\uFF0C\u6C5F\u5357\u6089\u5E73\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\u2022\u5377\u4E94\u5341\u4E5D\u2022\u6C49\u7EAA\u4E94\u5341\u4E00\u300B</strong></p><p><strong>\u5218\u8868\u751F\u547D\u4E2D\u7684\u8D35\u4EBA\u662F\u5B59\u575A\u3002</strong></p><p>\u5B59\u575A\u672C\u6765\u5728\u957F\u6C99\u505A\u592A\u5B88\uFF0C\u4F46\u662F\u8DDF\u8346\u5DDE\u523A\u53F2\u4E0D\u5BF9\u4ED8\u3002\u5B59\u575A\u6B66\u4EBA\u51FA\u8EAB\uFF0C\u8111\u5B50\u91CC\u5C11\u6839\u7B4B\uFF0C\u88AB\u523A\u53F2\u7684\u653F\u654C\u7ED9\u5229\u7528\u4E86\uFF0C\u4E00\u756A\u6311\u62E8\u4EE5\u540E\uFF0C\u5C45\u7136\u771F\u7684\u5C31\u53BB\u628A\u523A\u53F2\u7ED9\u903C\u6B7B\u4E86\u3002</p><p>\u5F53\u65F6\uFF0C\u8463\u5353\u5DF2\u7ECF\u628A\u6301\u671D\u653F\uFF0C\u4ED6\u81EA\u7136\u4E5F\u6CA1\u6709\u4EC0\u4E48\u4F38\u5F20\u6B63\u4E49\u7684\u5FC3\u601D\uFF0C\u53EA\u662F\u65E2\u7136\u8346\u5DDE\u523A\u53F2\u7684\u4F4D\u7F6E\u7A7A\u4E86\uFF0C\u90A3\u5C31\u6D3E\u4E2A\u4EBA\u8FC7\u53BB\u5427\u3002\u8463\u5353\u5DE6\u770B\u770B\u53F3\u770B\u770B\uFF0C\u89C9\u5F97\u5218\u8868\u4E0D\u9519\uFF0C\u5C31\u7ED9\u4E86\u5218\u8868\u4E00\u5C01\u59D4\u4EFB\u72B6\uFF0C\u8BA9\u4ED6\u53BB\u8346\u5DDE\u4E0A\u73ED\u3002</p><p>\u4F46\u8FD9\u538B\u6839\u5C31\u662F\u4E2A\u82E6\u6D3B\u7D2F\u6D3B\u3002\u8346\u5DDE\u5F53\u65F6\u904D\u5730\u72FC\u70DF\uFF0C\u5317\u90E8\u6700\u5BCC\u5EB6\u7684\u5357\u9633\u90E1\u5DF2\u7ECF\u88AB\u8881\u672F\u7ED9\u9738\u5360\u4E86\uFF0C\u5176\u4ED6\u5DDE\u90E1\u4E5F\u5DF2\u88AB\u5404\u79CD\u76D7\u8D3C\u6240\u5360\u636E\u3002\u5218\u8868\u867D\u7136\u624B\u91CC\u634F\u7740\u4E00\u7EB8\u4EFB\u547D\u4E66\uFF0C\u4F46\u4E00\u6CA1\u5175\u4E8C\u6CA1\u94B1\uFF0C\u5DE5\u4F5C\u8FD8\u771F\u4E0D\u597D\u5F00\u5C55\u3002</p><p>\u5218\u8868\u4E5F\u6CA1\u6307\u671B\u671D\u5EF7\u80FD\u518D\u7ED9\u4ED6\u4EC0\u4E48\u8D44\u6E90\u548C\u652F\u6301\uFF0C\u81EA\u5DF1\u60F3\u529E\u6CD5\u5427\uFF01\u5230\u4E86\u8346\u5DDE\u4EE5\u540E\uFF0C\u5218\u8868\u5148\u8BF7\u6765\u4E86\u8346\u5DDE\u5927\u65CF\u84AF\u6C0F\u5144\u5F1F\u548C\u8521\u7441\uFF0C\u4E00\u8D77\u5546\u91CF\u5BF9\u7B56\u3002\u4E00\u756A\u5BC6\u8C0B\u4EE5\u540E\uFF0C\u5927\u5BB6\u5EFA\u7ACB\u540C\u76DF\uFF1A\u7531\u84AF\u6C0F\u5144\u5F1F\u51FA\u9762\uFF0C\u9080\u8BF7\u7FA4\u76D7\u9996\u9886\u8BAE\u4E8B\u3002</p><p>\u7FA4\u76D7\u60F3\u7740\u5218\u8868\u662F\u5929\u4E0B\u6709\u540D\u7684\u5112\u751F\uFF0C\u53C8\u6709\u8346\u5DDE\u5927\u65CF\u84AF\u6C0F\u76F8\u9080\uFF0C\u7ADF\u7136\u90FD\u5E94\u9080\u5230\u4E86\u3002\u7ED3\u679C\uFF0C\u8BFB\u4E66\u4EBA\u4E00\u70B9\u90FD\u4E0D\u8BB2\u4FE1\u7528\uFF0C\u9152\u684C\u4E0A\u5C31\u76F4\u63A5\u628A\u8FD9\u4E9B\u4EBA\u7ED9\u6740\u4E86\u3002</p><p>\u8FD9\u65F6\u5019\u5C31\u4F53\u73B0\u51FA\u76D7\u8D3C\u7FA4\u4F53\u7684\u65E0\u7EC4\u7EC7\u65E0\u7EAA\u5F8B\u4E86\u2014\u2014\u9996\u9886\u88AB\u6740\uFF0C\u4ED6\u4EEC\u7684\u624B\u4E0B\u4EBA\u5C45\u7136\u6CA1\u5565\u5927\u52A8\u4F5C\uFF0C\u5F88\u5FEB\u4FBF\u4E00\u54C4\u800C\u6563\u3002</p><p><strong>\u5218\u8868\u4E8E\u662F\u987A\u5229\u6536\u56DE\u4E86\u8346\u5DDE\u7684\u5DDE\u90E1\u3002</strong></p><blockquote><p>\u8881\u672F\u6B64\u65F6\u4E5F\u5728\u6253\u8346\u5DDE\u7684\u4E3B\u610F\u3002\u773C\u770B\u7740\u5218\u8868\u8F7B\u800C\u6613\u4E3E\u7684\u5C31\u593A\u4E86\u8FD9\u4E48\u4E00\u5927\u5757\u5730\u65B9\uFF0C\u8881\u672F\u773C\u7EA2\u81F3\u6781\uFF0C\u4FBF\u6D3E\u5B59\u575A\u5357\u4E0B\u653B\u6253\u5218\u8868\u3002\u7ED3\u679C\u5B59\u575A\u4ED7\u8FD8\u6CA1\u6253\u51E0\u573A\uFF0C\u5C31\u5728\u6218\u573A\u4E0A\u88AB\u51B7\u7BAD\u7ED9\u5C04\u6B7B\u4E86\u3002\u4ECE\u6B64\uFF0C\u8881\u672F\u518D\u4E5F\u6CA1\u6709\u80FD\u529B\u548C\u5218\u8868\u5BF9\u6297\uFF0C\u9010\u6E10\u9000\u51FA\u4E86\u5357\u9633\u90E1\u3002</p></blockquote><blockquote><p>\u5C31\u5728\u5218\u8868\u5360\u636E\u8346\u5DDE\u7684\u540C\u65F6\uFF0C\u66F9\u64CD\u8FD8\u5728\u5F88\u82E6\u903C\u5730\u8DDF\u5415\u5E03\u62C9\u952F\u7740\u3002\u6700\u60E8\u7684\u65F6\u5019\uFF0C\u66F9\u64CD\u53EA\u5269\u4E0B\u4E86\u4E09\u4E2A\u53BF\u57CE\u7684\u5730\u76D8\u3002\u518D\u770B\u770B\u5218\u8868\u7684\u624B\u5230\u64D2\u6765\uFF0C\u771F\u662F\u4EBA\u6BD4\u4EBA\u6C14\u6B7B\u4EBA\u554A......</p></blockquote><p>\u5F53\u65F6\uFF0C\u5218\u8868\u5DF2\u7ECF\u662F\u5929\u4E0B\u6709\u540D\u7684\u5112\u751F\u3002\u65F6\u4EBA\u5BF9\u54C1\u884C\u9AD8\u5C1A\u7684\u58EB\u5927\u592B\u6709\u4E09\u541B\u3001\u516B\u4FCA\u3001\u516B\u987E\u3001\u516B\u53CA\u3001\u516B\u53A8\u7B49\u6392\u540D\uFF0C\u5218\u8868\u4F4D\u5728\u516B\u53CA\u4E4B\u5217\u3002</p><p>\u4E3A\u4EC0\u4E48\u5218\u8868\u80FD\u6709\u8FD9\u4E48\u5927\u7684\u540D\u6C14\uFF1F\u662F\u56E0\u4E3A\u4ED6\u4E43\u515A\u9522\u4E4B\u7978\u7684\u53D7\u5BB3\u8005\uFF0C\u662F\u88AB\u5BA6\u5B98\u5217\u5165\u8FC7\u9ED1\u540D\u5355\u7684\u4EBA\u3002\u515A\u9522\u4E4B\u7978\u5E73\u53CD\u4EE5\u540E\uFF0C\u8FD9\u4E9B\u4EBA\u81EA\u7136\u76DB\u540D\u5728\u5916\uFF0C\u53D7\u65F6\u4EBA\u656C\u4EF0\u3002</p><p>\u800C\u5218\u8868\u5728\u8346\u5DDE\u5B89\u5B9A\u4E0B\u6765\u4EE5\u540E\uFF0C\u4E5F\u5927\u529B\u5F18\u626C\u6587\u5316\u4E8B\u4E1A\uFF0C\u4F18\u5F85\u77E5\u8BC6\u5206\u5B50\u3002\u5728\u5F53\u65F6\u7684\u5929\u4E0B\u5927\u4E71\u4E2D\uFF0C\u8346\u5DDE\u7684\u786E\u662F\u4E00\u65B9\u4E50\u571F\u3002\u5218\u8868\u96C5\u597D\u6587\u5B66\uFF0C\u8BBE\u7ACB\u5B66\u6821\uFF0C\u62DB\u63FD\u5112\u751F\uFF0C\u53F7\u79F0\u201C\u7231\u6C11\u517B\u58EB\uFF0C\u4ECE\u5BB9\u81EA\u4FDD\u201D\u3002\u4E00\u65F6\u95F4\uFF0C\u56E0\u4E2D\u539F\u4E27\u4E71\u800C\u8FC1\u5165\u8346\u5DDE\u7684\u58EB\u5927\u592B\u4E0D\u53EF\u80DC\u6570\u3002\u6BD4\u5982\u5C71\u4E1C\u5927\u6C49\u8BF8\u845B\u4EAE\uFF0C\u5C31\u662F\u8FD9\u4E2A\u65F6\u5019\u8DDF\u968F\u53D4\u53D4\u5230\u4E86\u8346\u5DDE\u907F\u96BE\u3002</p><p>\u81EA\u4ECE\u5B59\u575A\u6B7B\u540E\uFF0C\u8881\u672F\u65E0\u6CD5\u62B5\u5FA1\u5218\u8868\uFF0C\u9010\u6E10\u9000\u51FA\u4E86\u8346\u5DDE\u6700\u5317\u90E8\u7684\u5357\u9633\u90E1\u3002\u5F20\u7EE3\u8FD9\u65F6\u6B63\u597D\u4ECE\u5173\u4E2D\u64A4\u51FA\uFF0C\u4E8E\u662F\u4FBF\u5360\u636E\u4E86\u8FD9\u5757\u5730\u65B9\u3002</p><p>\u8D3E\u8BE9\u4EE3\u8868\u5F20\u7EE3\uFF0C\u4E3B\u52A8\u53BB\u8DDF\u5218\u8868\u8054\u7EDC\uFF1A\u5218\u8868\u627F\u8BA4\u5F20\u7EE3\u5728\u5357\u9633\u7684\u519B\u4E8B\u5B58\u5728\uFF0C\u5F20\u7EE3\u5219\u8D1F\u8D23\u9632\u5FA1\u8346\u5DDE\u7684\u5317\u90E8\u5730\u533A\u2014\u2014\u9632\u5FA1\u7684\u5BF9\u8C61\uFF0C\u4E3B\u8981\u662F\u66F9\u64CD\u3002</p><p>\u8FD9\u79CD\u4E92\u60E0\u4E92\u5229\u7684\u5E73\u7B49\u6761\u7EA6\uFF0C\u8BA9\u8D3E\u8BE9\u8FD9\u79CD\u4EBA\u53BB\u8C08\uFF0C\u5F53\u7136\u4E0D\u5728\u8BDD\u4E0B\u3002**\u5173\u952E\u5728\u4E8E\uFF0C\u8D3E\u8BE9\u56DE\u53BB\u4EE5\u540E\uFF0C\u5BF9\u5218\u8868\u53D1\u8868\u4E86\u4E00\u756A\u8BC4\u8BBA\u3002**\u8FD9\u756A\u8BDD\uFF0C\u624D\u662F\u542B\u91D1\u91CF\u6700\u9AD8\u7684\u3002</p><p>\u8D3E\u8BE9\u8BF4\uFF1A</p><p><strong>\u8868\uFF0C\u5E73\u4E16\u4E09\u516C\u624D\u4E5F\uFF1B\u4E0D\u89C1\u4E8B\u53D8\uFF0C\u591A\u7591\u65E0\u51B3\uFF0C\u65E0\u80FD\u4E3A\u4E5F\u3002</strong></p><p>\u4EC0\u4E48\u610F\u601D\u5462\uFF1F\u8FD9\u53E5\u8BDD\u5F97\u62C6\u5F00\u4E86\u770B\u3002</p><p>**\u9996\u5148\uFF0C\u8D3E\u8BE9\u8BF4\u5218\u8868\u662F\u627F\u5E73\u4E4B\u4E16\u4E2D\u505A\u4E09\u516C\u7684\u6599\u3002**\u8FD9\u5176\u5B9E\u662F\u4E00\u53E5\u5F88\u9AD8\u7684\u8BC4\u4EF7\uFF0C\u5938\u4F60\u505A\u4E2A\u56FD\u52A1\u9662\u603B\u7406\u4E00\u70B9\u95EE\u9898\u90FD\u6CA1\u6709\u3002</p><p>\u8FD9\u53E5\u8BDD\u662F\u9488\u5BF9\u5218\u8868\u5728\u8346\u5DDE\u7684\u6CBB\u7406\u800C\u8A00\u7684\u3002\u5218\u8868\u505A\u5730\u65B9\u5B98\uFF0C\u786E\u5B9E\u505A\u7684\u5F88\u4E0D\u9519\u3002\u5230\u4EFB\u4EE5\u540E\uFF0C\u4ED6\u80FD\u524A\u5E73\u5730\u65B9\u53DB\u4E71\uFF0C\u5174\u529E\u5B66\u6821\uFF0C\u9F13\u52B1\u751F\u4EA7\uFF0C\u51E0\u5E74\u4E4B\u95F4\u4FBF\u5353\u6709\u6210\u6548\u3002\u8FD9\u5C31\u662F\u6E38\u620F\u91CC\u90A3\u79CD\u5178\u578B\u7684\u5185\u653F\u578B\u4EBA\u624D\u3002</p><p>\u4F46\u662F\uFF0C\u63A5\u4E0B\u6765\u8D3E\u8BE9\u8BDD\u950B\u4E00\u8F6C\uFF0C\u8BF4\uFF1A<strong>\u5218\u8868\u770B\u4E0D\u5230\u4E8B\u7269\u7684\u53D1\u5C55\u53D8\u5316\uFF0C\u7591\u5FC3\u91CD\u800C\u4E14\u72B9\u8C6B\u4E0D\u51B3\uFF0C\u662F\u4E0D\u4F1A\u6709\u4EC0\u4E48\u524D\u9014\u7684\u3002</strong></p><p>\u8FD9\u4E2A\u8BC4\u4EF7\u5C31\u5F88\u4F4E\u4E86\uFF0C\u800C\u4E14\uFF0C\u8FD8\u51C6\u5F97\u8981\u547D\u2014\u2014\u5218\u8868\u540E\u6765\u7684\u884C\u4E8B\uFF0C\u57FA\u672C\u4E0A\u6CA1\u6709\u8131\u79BB\u51FA\u8FD9\u4E2A\u6846\u67B6\u3002</p><p>\u66F9\u64CD\u8FCE\u5949\u6C49\u732E\u5E1D\u4EE5\u540E\uFF0C\u5218\u8868\u9063\u4F7F\u7EB3\u8D21\uFF0C\u8FD9\u672C\u65E0\u53EF\u975E\u8BAE\u3002\u4F46\u540C\u65F6\uFF0C\u4ED6\u5728\u8881\u7ECD\u90A3\u91CC\u53C8\u53E6\u6709\u4E00\u4EFD\u62A5\u6548\u3002\u4F60\u8BF4\u4ED6\u8FD9\u662F\u5728\u4E24\u5934\u4E0B\u6CE8\uFF0C\u90A3\u4E5F\u5C31\u7B97\u4E86\u3002\u4F46\u66F4\u4EE4\u4EBA\u7EB3\u95F7\u7684\u662F\uFF0C\u540E\u6765\u66F9\u64CD\u548C\u8881\u7ECD\u5F00\u6218\uFF0C\u5218\u8868\u7B54\u5E94\u4E86\u8981\u5E2E\u8881\u7ECD\u7684\u5FD9\uFF0C\u7ED3\u679C\u5374\u653E\u4E86\u4EBA\u5BB6\u9E3D\u5B50\u3002\u540C\u65F6\uFF0C\u5728\u66F9\u64CD\u90A3\u91CC\uFF0C\u5218\u8868\u4E5F\u6CA1\u6709\u4EC0\u4E48\u52A8\u9759\uFF0C\u53F7\u79F0\u662F\u201C\u4FDD\u6C5F\u6C49\u95F4\uFF0C\u89C2\u5929\u4E0B\u53D8\u201D\u3002</p><p>\u5F53\u65F6\u5C31\u6709\u4EBA\u529D\u4ED6\uFF0C\u73B0\u5728\u4E24\u5F3A\u76F8\u4E89\uFF0C\u5929\u4E0B\u7684\u6743\u91CD\u5728\u60A8\u8EAB\u4E0A\uFF0C\u8FD9\u662F\u4E89\u593A\u5929\u4E0B\u7684\u597D\u673A\u4F1A\u5440\uFF01\u800C\u60A8\u82E5\u662F\u4E0D\u6253\u7B97\u4E89\uFF0C\u90A3\u4E5F\u5DEE\u4E0D\u591A\u8BE5\u8868\u6001\u4E86\uFF0C\u5230\u5E95\u662F\u6295\u5411\u54EA\u4E00\u8FB9\u3002\u60A8\u8FD9\u4E48\u62E5\u5175\u81EA\u91CD\u5374\u4E0D\u8868\u6001\uFF0C\u6015\u662F\u4F1A\u628A\u4E24\u8FB9\u90FD\u7ED9\u5F97\u7F6A\u4E86\u3002\u5230\u65F6\u5019\u4E0D\u7BA1\u54EA\u8FB9\u80DC\u4E86\uFF0C\u5148\u5BF9\u4ED8\u7684\u90FD\u4E00\u5B9A\u4F1A\u662F\u4F60\u5662\u3002</p><p><strong>\u5218\u8868\u542C\u540E\uFF0C\u66F4\u52A0\u72D0\u7591</strong>\uFF0C\u5C31\u53BB\u54A8\u8BE2\u8C0B\u4E3B\u84AF\u8D8A\u7684\u610F\u89C1\u3002\u84AF\u8D8A\u7684\u60F3\u6CD5\u5DEE\u4E0D\u591A\u4E5F\u662F\u8FD9\u6837\uFF0C\u800C\u4E14\u8FD8\u66F4\u52A0\u660E\u786E\uFF1A\u5C31\u5E94\u8BE5\u6295\u5411\u66F9\u64CD\uFF0C\u56E0\u4E3A\u66F9\u64CD\u80DC\u7387\u66F4\u5927\uFF01</p><p><strong>\u4F46\u5218\u8868\u8FD8\u662F\u4E0D\u653E\u5FC3</strong>\uFF0C\u53C8\u6D3E\u4E86\u4E00\u4E2A\u53EB\u97E9\u5D69\u7684\uFF0C\u53BB\u671D\u4E2D\u4EB2\u81EA\u89C2\u5BDF\u4E00\u756A\u3002\u97E9\u5D69\u56DE\u6765\u540E\uFF0C\u76DB\u8D5E\u66F9\u64CD\u3002\u6CA1\u60F3\u5230<strong>\u5218\u8868\u4E0D\u7981\u6000\u7591</strong>\uFF1A\u4F60\u4E2B\u662F\u4E0D\u662F\u6536\u4E86\u66F9\u64CD\u7684\u597D\u5904\uFF1F\u7ED9\u6211\u5173\u8D77\u6765\uFF01</p><p>\u90A3\u5218\u8868\u662F\u4E0D\u662F\u60F3\u62BC\u5B9D\u8881\u7ECD\u5462\uFF1F</p><p>\u4E5F\u4E0D\u662F\u3002</p><p>\u540E\u6765\u5218\u5907\u6295\u9760\u5218\u8868\uFF0C\u4E86\u89E3\u5230\u66F9\u64CD\u4E3B\u529B\u6B63\u548C\u8881\u7ECD\u5728\u5B98\u6E21\u50F5\u6301\uFF0C\u4FBF\u6781\u529B\u529D\u8BEB\u5218\u8868\u8D81\u673A\u5077\u88AD\u8BB8\u660C\uFF0C<strong>\u4F46\u5218\u8868\u8FD8\u662F\u4E0D\u613F\u610F</strong>\u3002\u76F4\u5230\u540E\u6765\u66F9\u64CD\u6253\u8D25\u4E86\u8881\u7ECD\uFF0C\u5218\u8868\u624D\u5F00\u59CB\u540E\u6094\uFF0C\u4ED6\u8DDF\u5218\u5907\u8BF4\uFF0C\u5440\uFF0C\u5F53\u521D\u8981\u662F\u542C\u4E86\u4F60\u7684\u5C31\u597D\u4E86\u3002\u5218\u5907\u5F88\u662F\u65E0\u8BED\u3002</p><p>\u6253\u5B8C\u8881\u7ECD\uFF0C\u66F9\u64CD\u4FBF\u53BB\u5E73\u5B9A\u6CB3\u5317\u4E86\u3002\u6B64\u6218\u5F02\u5E38\u8270\u82E6\uFF0C\u4ECE\u516C\u5143200\u5E74\u81F3207\u5E74\uFF0C\u524D\u540E\u82B1\u4E86\u5DEE\u4E0D\u591A\u4E03\u5E74\u65F6\u95F4\u3002\u4E03\u5E74\u4E4B\u540E\uFF0C\u8881\u6C0F\u5144\u5F1F\u6388\u9996\uFF0C\u5218\u8868\u5728\u8346\u5DDE\u7684\u597D\u65E5\u5B50\u4E5F\u5230\u4E86\u5934\uFF0C\u66F9\u64CD\u7EC8\u4E8E\u6709\u673A\u4F1A\u80FD\u817E\u51FA\u624B\u6765\uFF0C\u4E0B\u6C5F\u5357\u89E3\u51B3\u5218\u8868\u7684\u95EE\u9898\u3002</p><p>\u4E0D\u8FC7\uFF0C\u66F9\u64CD\u51FA\u5F81\u524D\uFF0C\u5218\u8868\u4FBF\u75C5\u6B7B\u4E86\u3002\u4E4B\u540E\uFF0C\u5218\u8868\u90E8\u4E0B\u62E5\u7ACB\u5176\u5B50\u5218\u742E\u7EE7\u4EFB\u8346\u5DDE\u523A\u53F2\u3002</p><p>\u5728\u66F9\u64CD\u5175\u4E34\u8944\u9633\u65F6\uFF0C\u5218\u742E\u4EE5\u8346\u5DDE\u964D\u66F9\u3002</p><p>\u4EE5\u4E0A\u5173\u4E8E\u5218\u8868\u7684\u751F\u5E73\u9648\u8FF0\uFF0C\u8FD9\u5F53\u7136\u662F\u6709\u8D56\u4E8E\u300A\u4E09\u56FD\u6F14\u4E49\u300B\u7684\u666E\u53CA\u3002\u800C\u6211\u4EEC\u82E5\u8DF3\u51FA\u300A\u4E09\u56FD\u6F14\u4E49\u300B\u7684\u89C2\u70B9\uFF0C\u518D\u6765\u770B\u5218\u8868\u7684\u4E00\u751F\u4EE5\u53CA\u540E\u6765\u5218\u742E\u7684\u964D\u66F9\uFF0C\u5176\u5B9E\u8FD8\u4F1A\u6709\u5F88\u591A\u522B\u7684\u6536\u83B7\u3002</p><p>\u5218\u742E\u672C\u6765\u662F\u4E0D\u613F\u610F\u964D\u66F9\u7684\uFF0C\u8FD9\u4E2A\u5F88\u597D\u7406\u89E3\uFF1A\u7236\u4EB2\u4F20\u7ED9\u6211\u504C\u5927\u7684\u57FA\u4E1A\uFF0C\u5982\u4ECA\u4ED6\u5C38\u9AA8\u672A\u5BD2\uFF0C\u6211\u4E00\u4ED7\u672A\u63A5\u4FBF\u5F84\u81EA\u6295\u964D\uFF0C\u8FD9\u600E\u4E48\u80FD\u5BF9\u5F97\u8D77\u4ED6\u8001\u4EBA\u5BB6\u7684\u5728\u5929\u4E4B\u7075\uFF01</p><p>\u4F46\u5218\u742E\u5176\u65F6\u5E74\u7EAA\u5C1A\u5E7C\uFF0C\u51B3\u5B9A\u5E76\u4E0D\u662F\u4ED6\uFF08\u80FD\uFF09\u505A\u7684\uFF0C\u4ED6\u53EA\u80FD\u542C\u4ECE\u4E8E\u6574\u4E2A\u8346\u5DDE\u9AD8\u7EA7\u5B98\u540F\u96C6\u56E2\u7684\u4E00\u81F4\u610F\u89C1\u3002\u800C\u5927\u5BB6\u7684\u7406\u7531\u5219\u662F\uFF1A\u8346\u5DDE\u6839\u672C\u65E0\u529B\u62B5\u6297\u66F9\u64CD\u7684\u653B\u51FB\u3002\u4E0E\u5176\u6218\u8D25\u53D7\u8FB1\u800C\u964D\uFF0C\u8FD8\u4E0D\u5982\u4F53\u9762\u7684\u76F4\u63A5\u6295\u964D\u3002</p><p>\u5218\u742E\u53C8\u95EE\uFF0C\u6211\u4EEC\u4E0D\u662F\u8FD8\u6709\u5218\u5907\u4E48\uFF1F\u5C5E\u4E0B\u56DE\u7B54\uFF1A\u5218\u5907\u8981\u662F\u6321\u4E0D\u4F4F\u66F9\u64CD\uFF0C\u4F9D\u7136\u662F\u73B0\u5728\u7684\u7ED3\u5C40\uFF1B\u800C\u5218\u5907\u82E5\u662F\u80FD\u6321\u5F97\u4F4F\u66F9\u64CD\uFF0C\u90A3\u4F60\u5C31\u6321\u4E0D\u4F4F\u4ED6\u3002\u5218\u742E\u60F3\u6765\u60F3\u53BB\uFF0C\u679C\u7136\u662F\u8FD9\u4E2A\u9053\u7406\uFF0C\u4E8E\u662F\u6700\u540E\u51B3\u5B9A\u964D\u66F9\u3002</p><p><strong>\u300A\u4E09\u56FD\u6F14\u4E49\u300B\u662F\u4EE5\u5218\u5907\u7684\u7ACB\u573A\u4E3A\u7ACB\u573A\u7684\uFF0C\u4F46\u5982\u679C\u6211\u4EEC\u80FD\u7AD9\u5230\u5218\u742E\u7684\u7ACB\u573A\u6765\u60F3\u95EE\u9898\uFF0C\u5C31\u4F1A\u53D1\u73B0\u964D\u66F9\u7684\u786E\u662F\u6700\u4F18\u89E3\u3002</strong></p><p>\u800C\u4E14\uFF0C\u6295\u964D\u7684\u4E8B\u5176\u5B9E\u5E76\u4E0D\u80FD\u602A\u5218\u742E\u3002\u4ED6\u867D\u7136\u53E3\u53E3\u58F0\u58F0\u8BF4\u5148\u4EBA\u7684\u57FA\u4E1A\uFF0C\u4F46\u5176\u5B9E\uFF0C<code>\u5148\u4EBA\u6839\u672C\u5C31\u6CA1\u6709\u628A\u8FD9\u4EFD\u57FA\u4E1A\u5B8C\u6574\u7684\u4EA4\u5230\u4ED6\u624B\u4E2D\u3002</code></p><blockquote><p>\u5218\u8868\u5728\u8346\u5DDE\u8FD1\u4E8C\u5341\u5E74\uFF0C\u9664\u4E86\u5730\u65B9\u5E73\u53DB\u4EE5\u5916\uFF0C\u8346\u5DDE\u57FA\u672C\u4E0A\u4E0D\u7ECF\u5175\u6208\uFF1B</p></blockquote><blockquote><p>\u5F53\u65F6\u4E2D\u539F\u4E71\u54C4\u54C4\u7684\u4E89\u6597\uFF0C\u5218\u8868\u4E5F\u4ECE\u6765\u6CA1\u6709\u53C2\u4E0E\u8FC7\u3002</p></blockquote><blockquote><p>\u8FD9\u4E9B\u5E74\u5218\u8868\u8BF4\u662F\u5728\u9632\u5FA1\u66F9\u64CD\uFF0C\u4F46\u5176\u5B9E\u5E76\u4E0D\u662F\u9760\u81EA\u5DF1\u5B8C\u6210\u7684\uFF0C\u800C\u662F\u5148\u6709\u5F20\u7EE3\uFF0C\u540E\u9760\u5218\u5907\u3002</p></blockquote><blockquote><p>\u5218\u8868\u81EA\u5DF1\uFF0C\u53C8\u6CA1\u6709\u5B59\u6B66\u6216\u8005\u97E9\u4FE1\u4E00\u6837\u7684\u519B\u4E8B\u624D\u80FD\uFF0C\u80FD\u628A\u4E00\u652F\u4E0D\u6253\u4ED7\u7684\u519B\u961F\u5E26\u51FA\u6765\u3002</p></blockquote><p>\u7B49\u5230\u5218\u8868\u6B7B\u540E\uFF0C\u66F9\u64CD\u5357\u4E0B\uFF0C\u8346\u5DDE\u8868\u9762\u4E0A\u770B\u6709\u8FD1\u4E8C\u5341\u4E07\u4EBA\u7684\u519B\u961F\uFF0C\u4F46\u5B9E\u9645\u4E0A\uFF0C\u53EF\u6218\u4E4B\u5175\u57FA\u672C\u4E0A\u6CA1\u6709\u3002\u518D\u53CD\u89C2\u66F9\u64CD\uFF0C\u4ECE\u8BA8\u4F10\u8463\u5353\u4EE5\u6765\uFF0C\u4E00\u76F4\u5728\u4E0D\u505C\u5F81\u6218\uFF0C\u5175\u58EB\u7CBE\u826F\uFF0C\u731B\u5C06\u6797\u5217\u3002</p><p>\u4F60\u82E5\u662F\u5218\u742E\uFF0C\u4E5F\u4E00\u6837\u6CA1\u6709\u5176\u4ED6\u7684\u9009\u9879\u53EF\u4EE5\u9009\u3002</p><p>\u8346\u5DDE\u4E3A\u4EC0\u4E48\u4F1A\u53D8\u6210\u8FD9\u6837\u5462\uFF1F</p><p><strong>\u56E0\u4E3A\uFF0C\u5218\u8868\u5C31\u662F\u8FD9\u79CD\u4EBA</strong>\u2014\u2014\u201C\u4E09\u516C\u4E4B\u624D\u201D\u561B\uFF0C\u5C31\u662F\u80FD\u5B89\u6C11\u4FDD\u5883\uFF0C\u80FD\u5956\u52B1\u8015\u7EC7\uFF0C\u80FD\u4F18\u5F85\u58EB\u5B50\uFF0C\u80FD\u660C\u9686\u6587\u6559\u55BD\uFF0C\u4F46\u662F\uFF0C\u5C31\u662F\u4E0D\u4F1A\u4E89\u9738\u5929\u4E0B\uFF0C<strong>\u65E2\u6CA1\u6709\u90A3\u4E2A\u91CE\u5FC3\u548C\u89C6\u91CE\uFF0C\u4E5F\u6CA1\u6709\u90A3\u4EFD\u80FD\u529B\u548C\u51B3\u5FC3\u3002</strong></p><p>\u8FD9\u65F6\uFF0C\u6211\u4EEC\u518D\u770B\u8D3E\u8BE9\u5BF9\u5218\u8868\u7684\u8BC4\u4EF7\uFF1A\u201C\u4E0D\u89C1\u4E8B\u53D8\uFF0C\u591A\u7591\u65E0\u51B3\u201D\uFF0C\u4E5F\u5C31\u53EF\u4EE5\u641E\u6E05\u695A\u539F\u56E0\u4E86\uFF1A<code>\u5218\u8868\u662F\u4E0D\u61C2</code>\u3002</p><p>\u4ED6\u6CA1\u6709\u8FD9\u4E2A\u65B9\u9762\u7684\u80FD\u529B\uFF0C\u5BF9\u4E8E\u76F8\u5E94\u7684\u5EFA\u8BAE\u81EA\u7136\u4E5F\u5C31\u65E0\u6CD5\u505A\u51FA\u5224\u65AD\u3002<strong>\u9762\u5BF9\u4E00\u4E2A\u65E0\u6CD5\u505A\u800C\u4E14\u4E0D\u662F\u5FC5\u987B\u8981\u505A\u7684\u51B3\u5B9A\uFF0C\u4E00\u822C\u4EBA\u7684\u9009\u62E9\u90FD\u4F1A\u662F\uFF0C\u90A3\u5C31\u5148\u4E0D\u505A\u4E86</strong>\u3002\u4F53\u73B0\u5728\u5218\u8868\u8EAB\u4E0A\uFF0C\u5C31\u662F\u5728\u5927\u5C40\u4E0A\uFF0C\u770B\u4E0D\u89C1\u5929\u4E0B\u5F62\u52BF\u7684\u53D8\u5316\uFF1B\u5728\u5177\u4F53\u5F81\u6218\u51B3\u5B9A\u4E2D\uFF0C\u72B9\u8C6B\u4E0D\u51B3\uFF0C\u62D6\u6CE5\u5E26\u6C34\u3002</p><p>\u7EC8\u7A76\u4E0D\u662F\u4E00\u5757\u4E89\u9738\u5929\u4E0B\u7684\u6599\u3002</p>',53),g={href:"https://mp.weixin.qq.com/s?__biz=MzAxNzg4MDMzMg==&mid=2247484154&idx=1&sn=deb405997bbec76b3e7ea473e731113f&chksm=9bdf8699aca80f8fc4ae8a6ff2e593dc36af80e817c24798e5de4a4da6c879b7d96a120ea38a&cur_album_id=1572981576643215362&scene=189#wechat_redirect",target:"_blank",rel:"noopener noreferrer"};function b(_,u){const o=a("ExternalLinkIcon");return t(),r("div",null,[d,p("p",null,[p("a",g,[n("\u6587\u6848"),c(o)])])])}const f=e(l,[["render",b],["__file","10.html.vue"]]);export{f as default};
