import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.1218f0f6.js";const c={},o=a('<h1 id="\u7B2C10\u96C6-\u5468\u79C9\u6606\u5E26\u5468\u79C9\u4E49\u89C1\u90D1\u5A1F" tabindex="-1"><a class="header-anchor" href="#\u7B2C10\u96C6-\u5468\u79C9\u6606\u5E26\u5468\u79C9\u4E49\u89C1\u90D1\u5A1F" aria-hidden="true">#</a> \u7B2C10\u96C6\uFF1A\u5468\u79C9\u6606\u5E26\u5468\u79C9\u4E49\u89C1\u90D1\u5A1F</h1><p>\u9676\u4FCA\u4E66\u4E00\u76F4\u559C\u6B22\u5F39\u94A2\u7434\uFF0C\u5979\u8981\u4FDD\u62A4\u597D\u81EA\u5DF1\u7684\u8FD9\u53CC\u624B\u5C06\u6765\u5F39\u94A2\u7434\uFF0C\u6BCD\u4EB2\u4E5F\u66FE\u8BF4\u8FC7\u5979\u7684\u624B\u5C31\u662F\u4E3A\u4E86\u5F39\u94A2\u7434\u800C\u751F\u7684\uFF0C\u5728\u9676\u4FCA\u4E66\u7684\u62BD\u5C49\u91CC\u6709\u4E00\u5F20\u7EB8\uFF0C\u4E0A\u9762\u753B\u7740\u94A2\u7434\u7684\u7434\u952E\uFF0C\u5979\u6BCF\u5929\u5C31\u90A3\u4E48\u7EC3\u4E60\u7740\u5F39\u94A2\u7434\u3002</p><p>\u90DD\u51AC\u6885\u5E26\u7740\u9676\u4FCA\u4E66\u6765\u5230\u6742\u7269\u95F4\uFF0C\u5468\u79C9\u4E49\u65E9\u5C31\u7B49\u5728\u91CC\u9762\uFF0C\u91CC\u9762\u653E\u7740\u4E00\u67B6\u5C18\u5C01\u7684\u94A2\u7434\uFF0C\u9676\u4FCA\u4E66\u542C\u51FA\u94A2\u7434\u9700\u8981\u8C03\u97F3\u4E86\uFF0C\u4E3B\u52A8\u5C0F\u5FC3\u7684\u5F39\u594F\u7740\u94A2\u7434\u3002\u5979\u719F\u7EC3\u7684\u6307\u6CD5\u548C\u4F18\u7F8E\u7684\u65CB\u5F8B\u5438\u5F15\u4E86\u90DD\u51AC\u6885\u548C\u5468\u79C9\u4E49\u3002\u5728\u5468\u79C9\u4E49\u548C\u90DD\u51AC\u6885\u7684\u63A8\u8350\u4E4B\u4E0B\uFF0C\u8FD9\u624D\u8BA9\u9676\u4FCA\u4E66\u6709\u4E86\u673A\u4F1A\u79BB\u5F00\u77E5\u9752\u70B9\u3002\u4E34\u884C\u524D\uFF0C\u9676\u4FCA\u4E66\u5199\u4E86\u68C0\u4E3E\u4FE1\u68C0\u4E3E\u6234\u4E3B\u4EFB\uFF0C\u5468\u79C9\u4E49\u5728\u6B64\u4E4B\u524D\u6709\u8FC7\u4E00\u4E2A\u5973\u5B69\u7684\u4EB2\u7B14\u4FE1\u4E3E\u62A5\u6234\u4E3B\u4EFB\uFF0C\u5979\u4E3A\u6234\u4E3B\u4EFB\u6253\u8FC7\u4E09\u6B21\u80CE\uFF0C\u6700\u540E\u8FD8\u5927\u51FA\u8840\u6458\u9664\u4E86\u5B50\u5BAB\uFF0C\u53EF\u662F\u67E5\u65E0\u5B9E\u636E\uFF0C\u90A3\u4E9B\u68C0\u4E3E\u4FE1\u90FD\u88AB\u9000\u56DE\u6765\u4E86\u3002\u73B0\u5982\u4ECA\u6709\u4E86\u9676\u4FCA\u4E66\u7684\u68C0\u4E3E\u4FE1\u548C\u8BC1\u636E\uFF0C\u4E5F\u4E3A\u4E86\u90A3\u4E9B\u53D7\u8FC7\u59D4\u5C48\u7684\u4EBA\u8BA8\u56DE\u516C\u9053\u3002\u6234\u4E3B\u4EFB\u6700\u540E\u4E5F\u88AB\u5F00\u9664\u4E86\u516C\u804C\uFF0C\u4ECE\u6B64\u4E4B\u540E\u5468\u79C9\u4E49\u548C\u90DD\u51AC\u6885\u8FC7\u7740\u4E24\u4EBA\u751C\u871C\u7684\u5C0F\u65E5\u5B50\uFF0C\u6BCF\u5929\u4E0B\u73ED\u56DE\u6765\u770B\u89C1\u623F\u95F4\u91CC\u4EAE\u7740\u706F\uFF0C\u90DD\u51AC\u6885\u505A\u597D\u4E86\u996D\u83DC\u7B49\u7740\u4ED6\uFF0C\u4ED6\u5C31\u89C9\u5F97\u7279\u522B\u5E78\u798F\u3002</p><p>\u5468\u79C9\u4E49\u5C06\u81EA\u5DF1\u7684\u8FD9\u4E9B\u5FC3\u91CC\u8BDD\u90FD\u544A\u8BC9\u4E86\u6BCD\u4EB2\uFF0C\u5468\u6BCD\u4E3A\u5468\u79C9\u4E49\u592B\u5987\u611F\u5230\u5F00\u5FC3\uFF0C\u4E5F\u5E0C\u671B\u4E24\u4EBA\u5C3D\u5FEB\u6709\u5B69\u5B50\u3002\u5468\u6BCD\u8BA9\u5468\u79C9\u4E49\u6709\u65F6\u95F4\u591A\u529D\u8BF4\u5468\u79C9\u6606\uFF0C\u8BA9\u4ED6\u4E5F\u8D76\u7D27\u627E\u4E2A\u5BF9\u8C61\u7ED3\u5A5A\uFF0C\u5468\u6BCD\u8868\u8FBE\u7740\u81EA\u5DF1\u5BF9\u513F\u5B50\u7684\u601D\u5FF5\uFF0C\u6BCD\u5B50\u4FE9\u63E1\u7740\u624B\u6CEA\u773C\u76F8\u671B\uFF0C\u4F46\u5FC3\u91CC\u5145\u6EE1\u4E86\u751C\u871C\u548C\u6E29\u99A8\u3002</p><p>\u5E74\u4E09\u5341\u8FD9\u5929\uFF0C\u5927\u5BB6\u51D1\u5230\u4E00\u8D77\u8FC7\u56E2\u5706\u5E74\uFF0C\u5468\u6BCD\u5C06\u814A\u8089\u548C\u7F50\u5934\u90FD\u653E\u8D77\u6765\uFF0C\u5C31\u662F\u5E0C\u671B\u5468\u84C9\u56DE\u6765\u7684\u65F6\u5019\u80FD\u5E26\u8D70\uFF0C\u5927\u5BB6\u4E5F\u90FD\u6CA1\u6709\u610F\u89C1\u3002\u5403\u5B8C\u996D\u4E4B\u540E\uFF0C\u5468\u79C9\u6606\u770B\u7740\u5468\u5FD7\u521A\u592B\u5987\u4FE9\u6B22\u4E50\u804A\u5929\u7684\u6837\u5B50\uFF0C\u5C31\u6253\u7B97\u51FA\u53BB\u8EB2\u4E00\u4E0B\uFF0C\u8BA9\u592B\u5987\u4FE9\u4E5F\u80FD\u6709\u5355\u72EC\u76F8\u5904\u7684\u673A\u4F1A\u3002</p><p>\u53EF\u662F\u51FA\u4E86\u95E8\u4E4B\u540E\uFF0C\u5468\u79C9\u6606\u53BB\u627E\u5415\u5DDD\u60F3\u8981\u51D1\u5408\u4E00\u5BBF\uFF0C\u7ED3\u679C\u5BB6\u91CC\u5B69\u5B50\u90FD\u95F9\u7740\uFF0C\u8D76\u8D85\u4E5F\u5E26\u7740\u5BF9\u8C61\u4E8E\u8679\u56DE\u5BB6\u4E86\uFF0C\u66F9\u5FB7\u5B9D\u4E5F\u56E0\u4E3A\u559D\u591A\u4E86\u56DE\u53BB\u627E\u4E54\u6625\u71D5\u4E86\uFF0C\u54E5\u4EEC\u8BE5\u7ED3\u5A5A\u7684\u90FD\u7ED3\u5A5A\u4E86\uFF0C\u5468\u79C9\u6606\u4E00\u65F6\u534A\u4F1A\u513F\u6CA1\u6709\u5730\u65B9\u53BB\u53EA\u597D\u6765\u4E86\u90D1\u5A1F\u5BB6\u91CC\u3002\u90D1\u5149\u660E\u4E00\u770B\u89C1\u5468\u79C9\u6606\u5C31\u62B1\u7740\u4E0D\u6492\u624B\uFF0C\u5468\u79C9\u6606\u602F\u602F\u6073\u6C42\u7740\u5728\u90D1\u5A1F\u8FD9\u91CC\u4F4F\u4E00\u5BBF\uFF0C\u90D1\u5A1F\u660E\u663E\u6709\u4E9B\u7D27\u5F20\uFF0C\u4F46\u662F\u4E5F\u6709\u4E9B\u6C42\u4E4B\u4E0D\u5F97\u3002</p><p>\u665A\u4E0A\uFF0C\u7761\u5728\u8FD9\u91CC\uFF0C\u5468\u79C9\u6606\u5374\u7761\u4E0D\u7740\uFF0C\u8111\u6D77\u91CC\u60F3\u7740\u88AB\u7236\u4EB2\u8E39\u4E00\u811A\u7684\u6837\u5B50\u3002\u4ED6\u5FC3\u91CC\u662F\u60F3\u8981\u5A36\u90D1\u5A1F\u7684\uFF0C\u4F46\u662F\u65E0\u5948\u7236\u4EB2\u90A3\u4E00\u5173\u662F\u8FC7\u4E0D\u4E86\u7684\uFF0C\u90D1\u5A1F\u8F7B\u58F0\u8BE2\u95EE\u5468\u79C9\u6606\u662F\u5426\u7761\u7740\u4E86\uFF0C\u5468\u79C9\u6606\u7741\u7740\u773C\u775B\u9ED1\u6697\u4E2D\u5C31\u662F\u4E00\u773C\u4E0D\u53D1\uFF0C\u90D1\u5A1F\u4E5F\u53EA\u597D\u7761\u53BB\u3002</p><p>\u90DD\u51AC\u6885\u53EF\u4EE5\u5141\u8BB8\u53BB\u63A2\u671B\u7236\u6BCD\u4E86\uFF0C\u90DD\u51AC\u6885\u548C\u5468\u79C9\u4E49\u660E\u5929\u5C31\u8981\u8D70\u4E86\uFF0C\u5468\u6BCD\u9057\u61BE\u5468\u84C9\u8FD8\u6CA1\u56DE\u6765\uFF0C\u4E00\u5BB6\u4EBA\u6CA1\u6709\u56E2\u5706\u3002\u90DD\u51AC\u6885\u81EA\u8D23\u90FD\u662F\u56E0\u4E3A\u5979\u7684\u7F18\u6545\uFF0C\u5468\u5FD7\u521A\u5374\u8868\u793A\u7406\u89E3\uFF0C\u8BA4\u4E3A\u90DD\u51AC\u6885\u7684\u7236\u6BCD\u597D\u4E0D\u5BB9\u6613\u624D\u5141\u8BB8\u63A2\u671B\uFF0C\u90FD\u5E94\u8BE5\u5148\u53BB\u63A2\u671B\u518D\u56DE\u6765\uFF0C\u5E76\u4E14\u5E0C\u671B\u90DD\u51AC\u6885\u80FD\u4EE3\u66FF\u4ED6\u4EEC\u95EE\u597D\uFF0C\u8FD9\u8BA9\u90DD\u51AC\u6885\u5F88\u611F\u6FC0\u3002</p><p>\u5468\u6BCD\u8BA9\u5468\u79C9\u4E49\u5728\u4E34\u884C\u524D\u548C\u5468\u79C9\u6606\u597D\u597D\u8C08\u8C08\uFF0C\u5468\u79C9\u6606\u5E26\u7740\u5468\u79C9\u4E49\u6765\u60F3\u8BA9\u4ED6\u89C1\u4E00\u773C\u90D1\u5A1F\uFF0C\u6070\u597D\u90D1\u5A1F\u80CC\u7740\u5B69\u5B50\u6253\u9488\u56DE\u6765\u3002\u5468\u79C9\u6606\u672C\u60F3\u548C\u90D1\u5A1F\u5546\u8BAE\u4E00\u4E0B\uFF0C\u5982\u679C\u90D1\u5A1F\u4E0D\u613F\u610F\u89C1\u5C31\u4E0D\u89C1\uFF0C\u6CA1\u60F3\u5230\u4F1A\u5728\u8DEF\u53E3\u5076\u9047\uFF0C\u90D1\u5A1F\u5927\u65B9\u53BB\u89C1\u5468\u79C9\u4E49\uFF0C\u53CD\u5012\u8BA9\u5468\u79C9\u4E49\u529D\u8BF4\u4E00\u4E0B\u5468\u79C9\u6606\uFF0C\u5979\u4E0D\u503C\u5F97\u5468\u79C9\u6606\u7684\u4ED8\u51FA\u3002</p><p>\u5468\u79C9\u4E49\u4E5F\u5BF9\u90D1\u5A1F\u7684\u8BC4\u4EF7\u6BD4\u8F83\u9AD8\uFF0C\u77E5\u9053\u8FD9\u8FB9\u7684\u4EBA\u90FD\u6BD4\u4E0D\u4E0A\u90D1\u5A1F\u3002\u4F46\u662F\u5468\u79C9\u4E49\u5E76\u4E0D\u540C\u610F\u5468\u79C9\u6606\u7684\u5A5A\u4E8B\uFF0C\u800C\u4E14\u8BA4\u4E3A\u7236\u6BCD\u4E5F\u7EDD\u5BF9\u4E0D\u4F1A\u540C\u610F\uFF0C\u5728\u90A3\u4E2A\u9636\u7EA7\u5212\u5206\u660E\u786E\u7684\u5E74\u4EE3\uFF0C\u4E24\u4EBA\u5728\u4E00\u8D77\u662F\u4E0D\u53EF\u80FD\u7684\u4E8B\u60C5\u3002\u5468\u79C9\u4E49\u4E5F\u544A\u8BC9\u5468\u79C9\u6606\u90DD\u51AC\u6885\u4E00\u6B21\u6253\u6C34\u7684\u65F6\u5019\u4E0D\u614E\u6389\u5165\u4E86\u4E95\u6C34\u4E4B\u4E2D\uFF0C\u5F53\u65F6\u6B63\u662F\u7ECF\u671F\uFF0C\u81EA\u4ECE\u90A3\u4E4B\u540E\uFF0C\u90DD\u51AC\u6885\u5C31\u88AB\u8BCA\u65AD\u5F97\u4E86\u4E0D\u5B55\u75C7\u3002\u8FD9\u4EF6\u4E8B\u5468\u79C9\u4E49\u6CA1\u80FD\u544A\u8BC9\u7236\u6BCD\uFF0C\u5C06\u6240\u6709\u671F\u671B\u90FD\u5BC4\u6258\u5728\u4E86\u5468\u79C9\u6606\u8EAB\u4E0A\u3002</p><p>\u5468\u79C9\u4E49\u79BB\u5F00\u4E4B\u540E\uFF0C\u5468\u5FD7\u521A\u4E5F\u79BB\u5F00\u5BB6\u91CC\uFF0C\u5468\u6BCD\u5FC3\u75BC\u4E08\u592B\u4E0D\u505C\u7ED9\u4ED6\u5F80\u5305\u91CC\u585E\u5403\u7684\u3002\u5468\u5FD7\u521A\u770B\u5230\u59BB\u5B50\u843D\u6CEA\uFF0C\u4F9D\u504E\u5728\u59BB\u5B50\u540E\u80CC\u4E0A\uFF0C\u7B54\u5E94\u5C06\u59BB\u5B50\u7684\u7231\u90FD\u5E26\u7740\u3002\u5468\u79C9\u6606\u9001\u5468\u5FD7\u521A\u79BB\u5F00\uFF0C\u5728\u8F66\u4E0A\uFF0C\u5468\u5FD7\u521A\u81EA\u8D23\u4E0D\u8BE5\u6253\u4E86\u5468\u79C9\u6606\uFF0C\u4F46\u4E5F\u63D0\u9192\u5468\u79C9\u6606\u4E0D\u8981\u518D\u5F00\u90A3\u6837\u7684\u73A9\u7B11\uFF0C\u4EE5\u540E\u627E\u4E2A\u50CF\u4ED6\u6BCD\u4EB2\u4E00\u6837\u7684\u597D\u5973\u4EBA\u6210\u4EB2\uFF0C\u4E0D\u80FD\u5C06\u5C31\u3002</p><p>\u5468\u79C9\u6606\u56DE\u53BB\u4E4B\u540E\u9047\u5230\u4E86\u4E54\u6625\u71D5\uFF0C\u4E54\u6625\u71D5\u544A\u8BC9\u5468\u79C9\u6606\u5B9E\u9645\u4E0A\u90A3\u5929\u66F9\u5FB7\u5B9D\u94BB\u4E86\u5979\u88AB\u7A9D\uFF0C\u4F46\u4EC0\u4E48\u4E8B\u60C5\u90FD\u6CA1\u53D1\u751F\uFF0C\u662F\u5979\u559C\u6B22\u4E0A\u4E86\u66F9\u5FB7\u5B9D\u975E\u8981\u8D56\u7ED9\u4ED6\u7684\u3002\u5468\u79C9\u6606\u9001\u4E54\u6625\u71D5\u56DE\u53BB\u4E4B\u540E\u6765\u627E\u90D1\u5A1F\uFF0C\u4F46\u90D1\u5A1F\u5374\u5E76\u672A\u89C1\u5468\u79C9\u6606\uFF0C\u751A\u81F3\u4E8E\u8FD8\u8BA9\u90D1\u5149\u660E\u5C06\u94B1\u90FD\u9000\u56DE\u6765\u3002</p><p>\u5468\u79C9\u6606\u60F3\u8D77\u4E86\u54E5\u54E5\u4E34\u8D70\u65F6\u5019\u7684\u8BDD\uFF0C\u8BA9\u4ED6\u653E\u4E0B\u5BF9\u90D1\u5A1F\u7684\u611F\u60C5\uFF0C\u53EF\u4EE5\u5728\u7ECF\u6D4E\u4E0A\u5E2E\u52A9\u4E00\u4E0B\u5C31\u53EF\u4EE5\u4E86\uFF0C\u4F46\u4E0D\u80FD\u5728\u4E00\u8D77\uFF0C\u5426\u5219\u5410\u6CAB\u661F\u5B50\u4E5F\u80FD\u6DF9\u6B7B\u4EBA\u3002</p>',13),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","10.html.vue"]]);export{h as default};
