import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as t,f as a}from"./app.1226039b.js";const c={},o=a('<h1 id="\u7B2C12\u96C6-\u9648\u6C5F\u6CB3\u82B1\u6837\u767E\u51FA\u5077\u6280\u672F" tabindex="-1"><a class="header-anchor" href="#\u7B2C12\u96C6-\u9648\u6C5F\u6CB3\u82B1\u6837\u767E\u51FA\u5077\u6280\u672F" aria-hidden="true">#</a> \u7B2C12\u96C6\uFF1A\u9648\u6C5F\u6CB3\u82B1\u6837\u767E\u51FA\u5077\u6280\u672F</h1><p>\u7389\u73E0\u5728\u8F66\u7AD9\u95E8\u53E3\u6446\u4E86\u4E2A\u644A\u5B50\u5356\u8336\u53F6\u86CB\uFF0C\u90A3\u5E74\uFF0C\u7535\u89C6\u5267 \u300A\u7EA2\u697C\u68A6\u300B\u98CE\u9761\u4E00\u65F6\u3002\u5927\u8857\u5C0F\u5DF7\u90FD\u5728\u64AD\u653E\u3002\u738B\u5927\u5C71\u4E0B\u73ED\u6765\u63A5\u7389\u73E0\uFF0C\u4E00\u5BB6\u4EBA\u8D70\u5728\u56DE\u5BB6\u7684\u8DEF\u4E0A\uFF0C\u7389\u73E0\u548C\u5927\u5C71\u7684\u513F\u5B50\u5C0F\u65ED\u5531\u8D77\u7EA2\u697C\u68A6\u91CC\u5934\u7684\u6B4C\u513F\uFF0C\u6B22\u58F0\u7B11\u8BED\u6652\u6EE1\u56DE\u5BB6\u7684\u8DEF\u3002\u7389\u73E0\u5728\u6B64\u63D0\u8D77\u60F3\u53BB\u57CE\u91CC\u505A\u751F\u610F\uFF0C\u591A\u8D5A\u70B9\u94B1\u6539\u5584\u751F\u6D3B\uFF0C\u4F46\u662F\u5927\u5C71\u4F9D\u7136\u89C9\u5F97\u8D5A\u94B1\u662F\u7537\u4EBA\u7684\u804C\u8D23\uFF0C\u4E0D\u613F\u610F\u7389\u73E0\u8F9B\u82E6\u3002</p><p>\u7389\u73E0\u5728\u7AD9\u53F0\u4E0A\u5356\u8336\u53F6\u86CB\u65F6\uFF0C\u542C\u89C1\u4E24\u4E2A\u65C5\u5BA2\u804A\u5929\uFF0C\u5176\u4E2D\u4E00\u4E2A\u4EBA\u62FF\u51FA\u4E86\u4E00\u5F20\u6797\u9EDB\u7389\u7684\u7167\u7247\uFF0C\u8BF4\u662F\u5F53\u521D\u5728\u90E8\u961F\u641E\u6444\u5F71\u65F6\u7559\u4E0B\u7684\u3002\u7389\u73E0\u89C1\u5230\u540E\uFF0C\u5341\u5206\u60CA\u5947\uFF0C\u60F3\u8981\u4E70\u4E0B\u8FD9\u5F20\u7167\u7247\u3002\u4F46\u7167\u7247\u7684\u4E3B\u4EBA\u5374\u4E0D\u540C\u610F\u3002\u7389\u73E0\u4E0D\u7518\u5FC3\uFF0C\u4E8E\u662F\u6254\u4E0B\u644A\u5B50\uFF0C\u5E26\u7740\u513F\u5B50\u4E5F\u8DDF\u4E0A\u4E86\u706B\u8F66\uFF0C\u89C1\u5230\u90A3\u540D\u65C5\u5BA2\u540E\uFF0C\u82E6\u82E6\u6073\u6C42\uFF0C\u8FDE\u51E0\u5C81\u7684\u5C0F\u65ED\u90FD\u5E2E\u5FD9\u6C42\u60C5\uFF0C\u5E0C\u671B\u4E70\u4E0B\u7167\u7247\uFF0C\u6700\u7EC8\uFF0C\u90A3\u540D\u65C5\u5BA2\u770B\u5728\u5B69\u5B50\u7684\u4EFD\u4E0A\uFF0C\u5C06\u8FD9\u5F20\u7167\u7247\u9001\u7ED9\u4E86\u5979\u3002</p><p>\u7389\u73E0\u7275\u7740\u513F\u5B50\u8D70\u5728\u56DE\u5BB6\u7684\u8DEF\u4E0A\uFF0C\u9AD8\u5174\u7684\u5BF9\u5927\u5C71\u8BF4\uFF0C\u5979\u627E\u7740\u6447\u94B1\u6811\u4E86\u3002\u5927\u5C71\u4E00\u5934\u96FE\u6C34\u3002</p><p>\u889C\u5382\u95E8\u53E3\uFF0C\u9648\u6C5F\u6CB3\u6B63\u5728\u51C6\u5907\u9686\u91CD\u7684\u6B22\u8FCE\u4EEA\u5F0F\uFF0C\u6B22\u8FCE\u5C71\u4E0B\u5148\u751F\u7684\u5230\u6765\u3002\u539F\u6765\u662F\u4ED6\u4EEC\u4E70\u6765\u7684\u65E5\u672C\u63D0\u82B1\u673A\u51FA\u73B0\u4E86\u6545\u969C\uFF0C\u7279\u610F\u8BF7\u4E86\u5C71\u4E0B\u5148\u751F\u6765\u7EF4\u4FEE\u3002\u5973\u7FFB\u8BD1\u6768\u96EA\u81EA\u7136\u4E5F\u8DDF\u4E86\u8FC7\u6765\u3002\u6C5F\u6CB3\u548C\u5C0F\u848B\u51C6\u5907\u5077\u5B66\uFF0C\u5374\u88AB\u5C71\u4E0B\u4EE5\u5546\u4E1A\u673A\u5BC6\u4E3A\u7531\u6321\u4E86\u56DE\u53BB\u3002</p><p>\u9A86\u7389\u73E0\u4ECE\u7535\u89C6\u5267\u548C\u90A3\u5F20\u7167\u7247\u4E2D\u770B\u5230\u4E86\u5546\u673A\uFF0C\u5F00\u59CB\u5728\u5927\u8857\u4E0A\u515C\u552E\u6797\u9EDB\u7389\u7684\u7167\u7247\uFF0C\u679C\u7136\u751F\u610F\u5341\u5206\u706B\u7206\u3002</p><p>\u9648\u6C5F\u6CB3\u7279\u610F\u51C6\u5907\u4E86\u4E30\u76DB\u7684\u9152\u83DC\uFF0C\u5E0C\u671B\u80FD\u704C\u9189\u5C71\u4E0B\uFF0C\u8BA9\u4ED6\u9152\u540E\u5410\u771F\u8A00\u3002\u53EA\u53EF\u60DC\u53CD\u88AB\u5C71\u4E0B\u653E\u5230\u3002\u6768\u96EA\u4E00\u773C\u770B\u7A7F\u4E86\u9648\u6C5F\u6CB3\u7684\u628A\u620F\uFF0C\u9648\u6C5F\u6CB3\u5374\u628A\u5979\u62C9\u5230\u4E86\u81EA\u5DF1\u8FD9\u8FB9\u6765\u3002\u539F\u6765\u8FD9\u673A\u5668\u662F\u9648\u6C5F\u6CB3\u6545\u610F\u8BA9\u4EBA\u5F04\u574F\u7684\uFF0C\u4E3A\u7684\u5C31\u662F\u77E5\u9053\u8FD9\u673A\u5668\u7684\u5168\u90E8\u539F\u7406\u3002\u5C71\u4E0B\u6765\u7684\u8FD9\u51E0\u5929\uFF0C\u6240\u6709\u7684\u96F6\u90E8\u4EF6\u8F6E\u756A\u574F\u4E86\u4E2A\u904D\uFF0C\u5C71\u4E0B\u5341\u5206\u7591\u60D1\uFF0C\u4F46\u53C8\u767E\u601D\u4E0D\u5F97\u5176\u89E3\u3002</p><p>\u6700\u7EC8\uFF0C\u5728\u6768\u96EA\u7684\u5E2E\u52A9\u4E0B\uFF0C\u9648\u6C5F\u6CB3\u7EC8\u4E8E\u770B\u5230\u4E86\u5C71\u4E0B\u7EF4\u4FEE\u673A\u5668\u8BBE\u5907\u7684\u5168\u8FC7\u7A0B\u3002\u4E3A\u6B64\uFF0C\u4ED6\u7279\u610F\u51C6\u5907\u793C\u7269\u611F\u8C22\u6768\u96EA\uFF0C\u4F46\u6768\u96EA\u786E\u8BA4\u4E3A\u8FD9\u6837\u5E76\u6CA1\u6709\u4EC0\u4E48\u7528\uFF0C\u56E0\u4E3A\u673A\u5668\u6240\u6709\u7684\u4E1C\u897F\u90FD\u8981\u9760\u8FDB\u53E3\uFF0C\u4F9D\u7136\u8FD8\u662F\u5F97\u82B1\u94B1\u5411\u5916\u56FD\u4EBA\u4E70\uFF0C\u9648\u6C5F\u6CB3\u51B3\u5B9A\u5E26\u5979\u53BB\u770B\u4E00\u6837\u597D\u4E1C\u897F\u3002\u539F\u6765\u4ED6\u4EEC\u7ADF\u7136\u81EA\u5DF1\u7EC4\u88C5\u4E86\u81EA\u5DF1\u7684\u7535\u5B50\u63D0\u82B1\u673A\u3002\u6768\u96EA\u7EC8\u4E8E\u5BF9\u4ED6\u4EEC\u522E\u76EE\u76F8\u770B\u3002</p><p>\u9648\u6C5F\u6CB3\u7ED9\u81EA\u5DF1\u889C\u5382\u7684\u889C\u5B50\u53BB\u4E86\u4E00\u4E2A\u54CD\u4EAE\u7684\u540D\u5B57\uFF1A\u7389\u73E0\u724C</p><p>\u7389\u73E0\u8D5A\u4E86\u94B1\uFF0C\u7ED9\u5BB6\u91CC\u6DFB\u4E86\u4E00\u53F0\u7535\u89C6\u673A\u3002\u5BB6\u91CC\u7EC8\u4E8E\u6709\u4E86\u4E00\u53F0\u50CF\u6837\u7684\u7535\u5668\u3002\u665A\u4E0A\uFF0C\u7389\u73E0\u548C\u5927\u5C71\u6B63\u5728\u5BB6\u91CC\u4E00\u8FB9\u770B\u7535\u89C6\uFF0C\u4E00\u8FB9\u804A\u5929\u3002\u5927\u5C71\u5374\u53C8\u88AB\u540C\u4E8B\u8D75\u5BB6\u5E86\u62C9\u53BB\u9876\u73ED\uFF0C\u7ED3\u679C\u665A\u4E0A\u4E0B\u4E86\u6574\u6574\u4E00\u591C\u7684\u66B4\u96E8\uFF0C\u5927\u5C71\u76F4\u5230\u65E9\u4E0A\u4E5F\u6CA1\u6709\u56DE\u6765\u3002\u7389\u73E0\u51FA\u53BB\u5BFB\u4EBA\uFF0C\u5374\u542C\u8BF4\u5317\u7EBF\u6ED1\u5761\uFF0C\u503C\u73ED\u4EBA\u6389\u8FDB\u4E86\u6C9F\u91CC\u3002</p><p>\u533B\u751F\u5168\u529B\u62A2\u6551\uFF0C\u5927\u5C71\u5374\u4F9D\u65E7\u6210\u4E86\u690D\u7269\u4EBA\u3002\u7389\u73E0\u4E0D\u5F97\u4E0D\u518D\u6B21\u6491\u8D77\u6574\u4E2A\u5BB6\u3002</p>',11),r=[o];function _(n,s){return p(),t("div",null,r)}const h=e(c,[["render",_],["__file","12.html.vue"]]);export{h as default};
