import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.eee000b7.js";const c={},o=a('<h1 id="\u7B2C13\u96C6-\u5468\u79C9\u6606\u548C\u90D1\u5A1F\u559C\u7ED3\u826F\u7F18" tabindex="-1"><a class="header-anchor" href="#\u7B2C13\u96C6-\u5468\u79C9\u6606\u548C\u90D1\u5A1F\u559C\u7ED3\u826F\u7F18" aria-hidden="true">#</a> \u7B2C13\u96C6\uFF1A\u5468\u79C9\u6606\u548C\u90D1\u5A1F\u559C\u7ED3\u826F\u7F18</h1><p>\u5468\u79C9\u6606\u56DE\u6765\u5927\u5BB6\u90FD\u5F88\u9AD8\u5174\uFF0C\u4E00\u8D77\u6765\u5230\u5468\u79C9\u6606\u5BB6\u91CC\u5E86\u795D\uFF0C\u4E8E\u8679\u548C\u5434\u5029\u5305\u63FD\u4E86\u6240\u6709\u7684\u5BB6\u52A1\u6D3B\uFF0C\u5C31\u60F3\u8BA9\u90D1\u5A1F\u597D\u597D\u4F11\u606F\u3002\u5728\u5403\u996D\u7684\u65F6\u5019\uFF0C\u5927\u5BB6\u4E5F\u7EB7\u7EB7\u8981\u6C42\u5468\u79C9\u6606\u7ED9\u90D1\u5A1F\u8868\u4E00\u4E2A\u6001\uFF0C\u56E0\u4E3A\u66F9\u5FB7\u5B9D\u66FE\u7ECF\u770B\u5230\u8FC7\u4E24\u4EBA\u62B1\u5728\u4E00\u8D77\uFF0C\u5C24\u5176\u5BF9\u4E8E\u4E00\u4E2A\u5BE1\u5987\u6765\u8BF4\u5F71\u54CD\u5F88\u5927\uFF0C\u5468\u79C9\u6606\u4E5F\u5F53\u573A\u8868\u793A\u8981\u975E\u90D1\u5A1F\u4E0D\u5A36\u3002</p><p>\u5468\u79C9\u6606\u5F53\u4E2D\u6492\u8C0E\uFF0C\u8C0E\u79F0\u6960\u6960\u662F\u4ED6\u548C\u90D1\u5A1F\u7684\u5B69\u5B50\uFF0C\u5F53\u5E74\u90D1\u5A1F\u4E5F\u6CA1\u6709\u548C\u6D82\u5FD7\u5F3A\u7ED3\u5A5A\uFF0C\u662F\u4ED6\u5E2E\u7740\u6D82\u5FD7\u5F3A\u7684\u670B\u53CB\u7ED9\u90D1\u5A1F\u9001\u94B1\uFF0C\u4E00\u6765\u4E8C\u53BB\u7684\u5C31\u7231\u4E0A\u4E86\u90D1\u5A1F\u3002\u5415\u5DDD\u4E5F\u53D1\u73B0\u597D\u51E0\u6B21\u90FD\u5728\u592A\u5E73\u8857\u9047\u5230\u4E86\u5468\u79C9\u6606\uFF0C\u66F4\u52A0\u786E\u5B9A\u5468\u79C9\u6606\u7684\u8BDD\u4E0D\u5047\u3002</p><p>\u6240\u6709\u4EBA\u90FD\u5728\u4E3A\u5468\u79C9\u6606\u548C\u90D1\u5A1F\u9AD8\u5174\u7684\u540C\u65F6\uFF0C\u610F\u8BC6\u5230\u4E54\u6625\u71D5\u548C\u66F9\u5FB7\u5B9D\u6CA1\u6709\u6765\uFF0C\u4E54\u6625\u71D5\u90A3\u8FB9\u6B63\u5FD9\u7740\u642C\u5BB6\uFF0C\u672C\u6765\u5979\u597D\u597D\u7684\u5E72\u7740\u4FEE\u811A\u7684\u5DE5\u4F5C\uFF0C\u975E\u8981\u7ED9\u8BC4\u6807\u5175\u8BA9\u5979\u53BB\u505A\u4E86\u4E3B\u4EFB\uFF0C\u8FD8\u903C\u7740\u5979\u642C\u53BB\u4E86\u5E72\u90E8\u697C\uFF0C\u53EF\u662F\u73B0\u5728\u56DB\u4EBA\u5E2E\u7C89\u788E\u4E86\uFF0C\u73B0\u5728\u53C8\u8BF4\u4E54\u6625\u71D5\u662F\u548C\u56DB\u4EBA\u5E2E\u4E00\u8DEF\u7684\u4EBA\uFF0C\u4E54\u6625\u71D5\u4E0D\u4F46\u88AB\u514D\u804C\u8FD8\u88AB\u8D76\u51FA\u4E86\u4F4F\u5904\u3002\u8FD9\u8BA9\u4E54\u6625\u71D5\u6C14\u607C\u7684\u5F88\uFF0C\u5728\u5BB6\u91CC\u5230\u5904\u53D1\u813E\u6C14\uFF0C\u5176\u5B9E\u5979\u5C31\u60F3\u597D\u597D\u5E72\u6D3B\uFF0C\u6839\u672C\u5C31\u4E0D\u61C2\u4EC0\u4E48\u662F\u56DB\u4EBA\u5E2E\u3002</p><p>\u5468\u79C9\u6606\u4E3A\u4E86\u8BC1\u660E\u90D1\u5A1F\u662F\u672A\u5A5A\uFF0C\u8FD8\u8BA9\u90D1\u5A1F\u62FF\u7740\u6237\u53E3\u672C\u7ED9\u5927\u5BB6\u770B\uFF0C\u7ED3\u679C\u8BC1\u660E\u771F\u7684\u662F\u672A\u5A5A\uFF0C\u4E5F\u8BF4\u660E\u90D1\u5A1F\u548C\u6D82\u5FD7\u5F3A\u6CA1\u6709\u6210\u4EB2\uFF0C\u5468\u79C9\u6606\u5F53\u7740\u6240\u6709\u4EBA\u7684\u9762\u5411\u90D1\u5A1F\u6C42\u5A5A\u3002\u90D1\u5A1F\u671F\u76FC\u5DF2\u4E45\u7684\u6C42\u5A5A\uFF0C\u773C\u6CEA\u6D41\u4E0B\u6765\uFF0C\u70B9\u5934\u540C\u610F\u4E86\u8FD9\u4E2A\u5A5A\u4E8B\u3002</p><p>\u5468\u79C9\u6606\u5199\u4FE1\u7ED9\u5468\u79C9\u4E49\uFF0C\u8868\u8FBE\u4E86\u81EA\u5DF1\u8981\u5A36\u90D1\u5A1F\u7684\u51B3\u5FC3\uFF0C\u800C\u4E14\u4E5F\u5C06\u5BB6\u91CC\u53D1\u751F\u7684\u6240\u6709\u4E8B\u60C5\u90FD\u4E00\u4E94\u4E00\u5341\u7684\u544A\u8BC9\u4E86\u5468\u79C9\u4E49\uFF0C\u5BF9\u4E8E\u90D1\u5A1F\u8FD9\u79CD\u6709\u60C5\u6709\u4E49\u7684\u5973\u4EBA\u4ED6\u4E0D\u80FD\u8F9C\u8D1F\uFF0C\u5982\u679C\u5BB6\u91CC\u4EBA\u4E0D\u540C\u610F\uFF0C\u4ED6\u5C31\u548C\u5BB6\u91CC\u4EBA\u51B3\u88C2\u3002\u6B64\u65F6\uFF0C\u7531\u4E8E\u653F\u7B56\u6062\u590D\u4E86\u9AD8\u8003\uFF0C\u5468\u79C9\u4E49\u548C\u5468\u84C9\u90FD\u8003\u4E0A\u4E86\u5317\u5927\uFF0C\u90DD\u51AC\u6885\u4E5F\u8003\u4E0A\u4E86\u5409\u6625\u533B\u79D1\u5927\u5B66\u3002</p><p>1978\u5E74\u521A\u5F00\u6625\uFF0C\u5468\u79C9\u4E49\u5C31\u7279\u610F\u6765\u5230\u91CD\u5E86\u5927\u4E09\u7EBF\u627E\u5468\u5FD7\u521A\uFF0C\u5468\u5FD7\u521A\u8FD8\u4EE5\u4E3A\u5BB6\u91CC\u53D1\u751F\u4E86\u4EC0\u4E48\u91CD\u5927\u7684\u4E8B\u60C5\u614C\u5FD9\u6765\u5230\u65C5\u9986\u627E\u5468\u79C9\u4E49\u3002\u5468\u79C9\u4E49\u5148\u544A\u8BC9\u7236\u4EB2\u8003\u4E0A\u5927\u5B66\u7684\u597D\u6D88\u606F\uFF0C\u968F\u540E\u53C8\u62FF\u51FA\u4E86\u5468\u79C9\u6606\u5199\u7684\u4FE1\u3002</p><p>\u6960\u6960\u548C\u51AF\u73A5\u73A5\u6B63\u5728\u9662\u5B50\u91CC\u73A9\u800D\u7684\u65F6\u5019\u5468\u5FD7\u521A\u5FFD\u7136\u56DE\u6765\u4E86\uFF0C\u5468\u5FD7\u521A\u4EB2\u5207\u7684\u548C\u73A5\u73A5\u6253\u62DB\u547C\uFF0C\u5F53\u5F97\u77E5\u6960\u6960\u59D3\u5468\u7684\u65F6\u5019\u5468\u5FD7\u521A\u7709\u5934\u76B1\u4E86\u4E00\u4E0B\u3002\u6B64\u65F6\uFF0C\u90D1\u5A1F\u63D0\u7740\u6C34\u56DE\u6765\u4E86\uFF0C\u5468\u5FD7\u521A\u4E0A\u4E0B\u6253\u91CF\u90D1\u5A1F\uFF0C\u63D0\u9192\u5979\u8D76\u7D27\u5C06\u6C34\u5012\u4E86\uFF0C\u63D0\u7740\u4E5F\u91CD\u3002\u90D1\u5A1F\u610F\u8BC6\u5230\u5468\u5FD7\u521A\u7684\u8EAB\u4EFD\u6709\u4E9B\u7D27\u5F20\uFF0C\u5468\u5FD7\u521A\u523B\u610F\u63D0\u51FA\u770B\u770B\u90D1\u5A1F\u7684\u53CC\u624B\uFF0C\u5F53\u53D1\u73B0\u90D1\u5A1F\u53CC\u624B\u5E03\u6EE1\u8001\u8327\u7684\u65F6\u5019\uFF0C\u5468\u5FD7\u521A\u6709\u4E9B\u52A8\u5BB9\u3002\u542C\u95FB\u5468\u79C9\u6606\u4ECA\u5929\u4E0D\u56DE\u6765\uFF0C\u5468\u5FD7\u521A\u8BA9\u90D1\u5A1F\u5148\u5E26\u7740\u5B69\u5B50\u56DE\u5BB6\u53BB\u4E86\u3002</p><p>\u5F53\u5468\u79C9\u6606\u56DE\u6765\u7684\u65F6\u5019\u53D1\u73B0\u90D1\u5A1F\u4E0D\u89C1\u4E86\uFF0C\u8D23\u602A\u5468\u5FD7\u521A\u8D76\u8D70\u4E86\u90D1\u5A1F\uFF0C\u5468\u5FD7\u521A\u60F3\u8D77\u4FE1\u91CC\u7684\u5185\u5BB9\u5C31\u751F\u6C14\uFF0C\u8D23\u602A\u5468\u79C9\u6606\u4E0D\u8BE5\u52A8\u4E0D\u52A8\u5C31\u51B3\u88C2\uFF0C\u4E00\u6C14\u4E4B\u4E0B\u8D76\u5468\u79C9\u6606\u79BB\u5F00\u3002\u5468\u79C9\u6606\u8D4C\u6C14\u8981\u8D70\u7684\u65F6\u5019\uFF0C\u5374\u5FFD\u7136\u53D1\u73B0\u4E0D\u89C1\u4E86\u51AF\u73A5\u73A5\uFF0C\u5413\u5F97\u5468\u79C9\u6606\u548C\u5468\u5FD7\u521A\u5FD9\u53BB\u5BFB\u627E\u3002\u5468\u79C9\u6606\u4EE5\u4E3A\u6765\u4E86\u90D1\u5A1F\u8FD9\u91CC\uFF0C\u4F46\u90D1\u5A1F\u8FD9\u91CC\u4E5F\u6CA1\u6709\u4EBA\uFF0C\u6960\u6960\u60F3\u8D77\u51AF\u73A5\u73A5\u8BF4\u8FC7\u60F3\u56DE\u5BB6\u627E\u7236\u6BCD\u7684\u8BDD\uFF0C\u90D1\u5A1F\u548C\u5468\u79C9\u6606\u614C\u5FD9\u51FA\u95E8\u53BB\u627E\u3002\u5468\u79C9\u6606\u53BB\u4E86\u8F66\u7AD9\uFF0C\u90D1\u5A1F\u6765\u4E86\u5468\u79C9\u6606\u5BB6\u91CC\u3002\u51AF\u73A5\u73A5\u5374\u5728\u4E8C\u4EBA\u51FA\u95E8\u4E4B\u540E\u6765\u627E\u4E86\u6960\u6960\uFF0C\u4F24\u5FC3\u751F\u6C14\uFF0C\u751A\u81F3\u8BA4\u4E3A\u5468\u5FD7\u521A\u4E0D\u662F\u597D\u4EBA\uFF0C\u5C06\u6960\u6960\u548C\u90D1\u5A1F\u8D76\u8D70\u4E86\u3002\u6960\u6960\u5E26\u7740\u51AF\u73A5\u73A5\u6765\u5230\u4E86\u5468\u79C9\u6606\u5BB6\u91CC\uFF0C\u90D1\u5A1F\u5FD9\u7740\u7ED9\u4E24\u4E2A\u5B69\u5B50\u505A\u4E86\u996D\u5403\uFF0C\u8FD8\u6362\u6D17\u4E86\u5468\u6BCD\u7684\u8863\u7269\u3002\u5468\u79C9\u6606\u548C\u5468\u5FD7\u521A\u4E5F\u5728\u6B64\u65F6\u56DE\u6765\u4E86\u3002</p><p>\u5468\u5FD7\u521A\u77E5\u9053\u90D1\u5A1F\u5BF9\u5BB6\u91CC\u7684\u4ED8\u51FA\uFF0C\u4E5F\u5F97\u77E5\u4E86\u5979\u548C\u5468\u79C9\u6606\u5DF2\u7ECF\u6210\u4EB2\u7684\u4E8B\u60C5\uFF0C\u5468\u5FD7\u521A\u8D23\u602A\u5468\u79C9\u6606\u6CA1\u6709\u65E9\u70B9\u7ED9\u90D1\u5A1F\u4E00\u4E2A\u4EA4\u4EE3\uFF0C\u521A\u624D\u8BA9\u90D1\u5A1F\u56DE\u5BB6\u4E5F\u5E76\u975E\u662F\u8D76\u8D70\u5979\uFF0C\u800C\u662F\u5468\u5FD7\u521A\u4EE5\u4E3A\u513F\u5B50\u4E0D\u56DE\u6765\uFF0C\u513F\u5AB3\u5987\u548C\u4ED6\u4F4F\u4E00\u8D77\u4E0D\u5408\u9002\u3002</p><p>\u5468\u5FD7\u521A\u8BA9\u90D1\u5A1F\u51FA\u53BB\uFF0C\u4ED6\u5355\u72EC\u548C\u5468\u79C9\u6606\u8BF4\u8BDD\uFF0C\u5468\u5FD7\u521A\u8BA4\u4E3A\u90D1\u5A1F\u662F\u4ED6\u4EEC\u7684\u5927\u6069\u4EBA\uFF0C\u6CA1\u6709\u90D1\u5A1F\u7684\u4ED8\u51FA\u5C31\u6CA1\u6709\u5468\u84C9\u548C\u5468\u79C9\u4E49\u7684\u5927\u5B66\u3002\u5468\u5FD7\u521A\u5E0C\u671B\u5468\u79C9\u6606\u4EE5\u540E\u597D\u597D\u5BF9\u5F85\u90D1\u5A1F\uFF0C\u5468\u79C9\u6606\u4E5F\u8BA9\u7236\u4EB2\u5B88\u4F4F\u79D8\u5BC6\uFF0C\u5BF9\u5916\u5C31\u8BF4\u5B69\u5B50\u662F\u4ED6\u548C\u90D1\u5A1F\u4EB2\u751F\u7684\uFF0C\u5468\u5FD7\u521A\u4E5F\u90FD\u7B54\u5E94\u4E86\u3002</p><p>\u5468\u5FD7\u521A\u5F88\u5FEB\u79BB\u5F00\u5BB6\u91CC\u56DE\u53BB\u5927\u4E09\u7EBF\uFF0C\u4E5F\u53EE\u5631\u5468\u79C9\u6606\u8981\u597D\u597D\u8003\u5927\u5B66\uFF0C\u6293\u4F4F\u8FD9\u4E2A\u96BE\u5F97\u7684\u673A\u4F1A\u3002\u5468\u79C9\u6606\u5176\u5B9E\u4E5F\u65E9\u6709\u8FD9\u4E2A\u6253\u7B97\u3002</p><p>\u90D1\u5A1F\u548C\u5468\u79C9\u6606\u4E5F\u62CD\u4E86\u7ED3\u5A5A\u7167\uFF0C\u4E24\u4EBA\u5750\u5728\u5BB6\u91CC\u5403\u996D\u7684\u65F6\u5019\uFF0C\u7A81\u7136\u6709\u4EBA\u6572\u95E8\uFF0C\u90D1\u5A1F\u5FD9\u53BB\u5F00\u95E8\uFF0C\u6765\u4EBA\u5374\u662F\u8521\u6653\u5149\u3002</p>',13),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","13.html.vue"]]);export{h as default};
