import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.1218f0f6.js";const c={},o=a('<h1 id="\u7B2C13\u96C6-\u7389\u73E0\u627E\u5230\u5546\u673A" tabindex="-1"><a class="header-anchor" href="#\u7B2C13\u96C6-\u7389\u73E0\u627E\u5230\u5546\u673A" aria-hidden="true">#</a> \u7B2C13\u96C6\uFF1A\u7389\u73E0\u627E\u5230\u5546\u673A</h1><p>\u7389\u73E0\u5C06\u5927\u5C71\u4ECE\u533B\u9662\u63A5\u4E86\u56DE\u6765\uFF0C\u5B89\u7F6E\u5728\u5BB6\u91CC\u3002\u81EA\u5DF1\u4E00\u8FB9\u7167\u987E\u738B\u5927\u5C71\uFF0C\u4E00\u904D\u8D5A\u94B1\u517B\u5BB6\u3002\u8FDE\u5C0F\u65ED\u4E5F\u8FC5\u901F\u6210\u957F\u6210\u4E00\u4E2A\u5C0F\u5C0F\u7537\u5B50\u6C49\u3002</p><p>\u9648\u6C5F\u6CB3\u7684\u751F\u610F\u8D8A\u505A\u8D8A\u5927\uFF0C\u90B1\u82F1\u6770\u5E26\u7740\u51E0\u4E2A\u4E49\u4E4C\u8001\u5BB6\u7684\u889C\u5546\u6765\u889C\u5382\u8FDB\u8D27\u3002\u7389\u73E0\u724C\u7684\u889C\u5B50\u5982\u4ECA\u5DF2\u7ECF\u662F\u54CD\u5F53\u5F53\u7684\u54C1\u724C\u4E86\uFF0C\u8FDE\u5317\u4EAC\u4E0A\u6D77\u8FD9\u6837\u7684\u5927\u57CE\u5E02\u90FD\u5728\u75AF\u62A2\u3002\u9648\u6C5F\u6CB3\u8FD8\u89C1\u5230\u4E86\u67F1\u5B50\u53D4\uFF0C\u539F\u6765\u67F1\u5B50\u542C\u8BF4\u4E86\u6C5F\u6CB3\u7684\u7ECF\u5386\uFF0C\u4E5F\u60F3\u8981\u6765\u8DDF\u7740\u4ED6\u505A\u4E8B\u60C5\u3002\u90B1\u82F1\u6770\u8FD8\u5E26\u6765\u4E86\u81EA\u5DF1\u7684\u5973\u513F\u2014\u90B1\u5CA9\uFF0C\u8FD9\u624D\u77E5\u9053\uFF0C\u90B1\u82F1\u6770\u7684\u59BB\u5B50\u5728\u534A\u5E74\u524D\u79BB\u5F00\u4ED6\u4EEC\u81EA\u5DF1\u53BB\u4E86\u7F8E\u56FD\u3002\u9648\u6C5F\u6CB3\u529D\u8BF4\u82F1\u6770\u8FD9\u6837\u4E0D\u662F\u957F\u4E45\u4E4B\u8BA1\uFF0C\u90B1\u82F1\u6770\u5374\u4E0D\u613F\u591A\u63D0\u3002\u90B1\u82F1\u6770\u8BF4\u8D77\u4E49\u4E4C\u5927\u53D8\u6837\uFF0C\u95EE\u9648\u6C5F\u6CB3\u4EC0\u4E48\u65F6\u5019\u56DE\u53BB\u3002\u9648\u6C5F\u6CB3\u4E0D\u8BF4\uFF0C\u5374\u652F\u652F\u543E\u543E\u7684\u95EE\u8D77\u9648\u91D1\u6C34\u3002\u624D\u77E5\u9053\u9648\u91D1\u6C34\u5728\u6751\u5B50\u91CC\u5EFA\u4E86\u4E2A\u9E21\u6BDB\u5382\uFF0C\u4E13\u95E8\u56DE\u6536\u9E21\u6BDB\u505A\u9E21\u6BDB\u63B8\u5B50\u3002\u90B1\u82F1\u6770\u8BF4\u8D77\u9648\u91D1\u6C34\uFF0C\u76F4\u8BF4\u4F60\u4EEC\u8FD9\u7237\u4FE9\u771F\u662F\u4E00\u6837\u7684\u813E\u6C14\u3002</p><p>\u56E0\u4E3A\u8D75\u5BB6\u5E86\u4E0D\u80AF\u627F\u8BA4\u81EA\u5DF1\u7FD8\u73ED\uFF0C\u6240\u4EE5\u5927\u5C71\u7684\u5DE5\u4F24\u8865\u52A9\u4E00\u76F4\u529E\u4E0D\u4E0B\u6765\uFF0C\u5927\u5C71\u7684\u540C\u4E8B\u4EEC\u7ED9\u7389\u73E0\u6350\u4E86\u70B9\u94B1\uFF0C\u7389\u73E0\u6CA1\u6536\uFF0C\u53EA\u60F3\u4E3A\u5927\u5C71\u8BA8\u4E2A\u8BF4\u6CD5\u3002\u7389\u73E0\u5728\u8D75\u5BB6\u5E86\u5BB6\u95E8\u53E3\u6446\u4E86\u4E2A\u644A\u5B50\uFF0C\u5356\u4E9B\u5B69\u5B50\u4EEC\u559C\u6B22\u7684\u4E1C\u897F\u3002\u7B49\u5B69\u5B50\u4EEC\u805A\u96C6\u8D77\u6765\uFF0C\u5979\u5C31\u7EC4\u7EC7\u5B69\u5B50\u4EEC\u5728\u8D75\u5BB6\u5E86\u5BB6\u95E8\u53E3\u558A\uFF0C\u9003\u73ED\u6253\u724C\u7684\u662F\u8D75\u5BB6\u5E86\uFF0C\u503C\u73ED\u7684\u662F\u738B\u5927\u5C71\u3002\u8D75\u5BB6\u5E86\u8EB2\u5728\u5BB6\u91CC\u4E0D\u6562\u51FA\u95E8\uFF0C\u5750\u7ACB\u96BE\u5B89\u3002</p><p>\u9648\u6C5F\u6CB3\u5230\u4E0A\u6D77\u63A8\u9500\u81EA\u5DF1\u5382\u751F\u4EA7\u7684\u889C\u5B50\uFF0C\u53EF\u60DC\u5BF9\u65B9\u6839\u672C\u770B\u4E0D\u4E0A\u4ED6\u4EEC\u8FD9\u79CD\u5C0F\u5730\u65B9\u51FA\u6765\u7684\u4E1C\u897F\u3002\u5C31\u5728\u9648\u6C5F\u6CB3\u65E0\u8BA1\u53EF\u65BD\u65F6\uFF0C\u6768\u96EA\u6B63\u597D\u7ECF\u8FC7\uFF0C\u66FF\u4ED6\u8BF4\u4E86\u4E00\u53E5\u597D\u8BDD\uFF0C\u7ED3\u679C\u5C31\u8BA9\u5BF9\u65B9\u6536\u4E0B\u4E86\u4ED6\u4EEC\u7684\u4EA7\u54C1\u3002\u9648\u6C5F\u6CB3\u5927\u559C\u8FC7\u671B\uFF0C\u8981\u8BF7\u6768\u96EA\u5403\u996D\uFF0C\u6768\u96EA\u5374\u628A\u4ED6\u5E26\u5230\u4E86\u4E94\u661F\u7EA7\u9152\u5E97\u3002</p><p>\u9648\u6C5F\u6CB3\u770B\u7740\u83DC\u5355\u548B\u820C\uFF0C\u4E3A\u4E86\u7701\u94B1\uFF0C\u4ED6\u53EA\u7ED9\u81EA\u5DF1\u70B9\u4E86\u4E00\u74F6\u9152\u3002\u7ED3\u679C\u4E0D\u77E5\u4E0D\u89C9\u559D\u591A\u4E86\u3002\u8DB4\u5728\u5395\u6240\u5455\u5410\uFF0C\u8C01\u77E5\u7ADF\u5728\u5395\u6240\u91CC\u78B0\u89C1\u4E86\u719F\u4EBA\uFF0C\u9648\u5927\u5149\u3002</p><p>\u5927\u5149\u65E9\u5C31\u4E0D\u662F\u5F53\u521D\u7684\u6837\u5B50\uFF0C\u4ED6\u73B0\u5728\u5403\u4F4F\u5728\u4E94\u661F\u7EA7\u9152\u5E97\uFF0C\u7A7F\u7684\u662F\u4E0A\u6D77\u6700\u65F6\u9AE6\u7684\u8863\u670D\uFF0C\u624B\u91CC\u8FD8\u62FF\u7740\u5927\u54E5\u5927\u3002\u4FE8\u7136\u4E00\u526F\u66B4\u53D1\u6237\u7684\u6837\u5B50\u3002\u9648\u6C5F\u6CB3\u8D8A\u770B\u8D8A\u4E0D\u5BF9\u52B2\uFF0C\u53EA\u95EE\u5DE7\u59D1\u5462\uFF1F\u9648\u5927\u5149\u907F\u800C\u4E0D\u7B54\uFF0C\u8F6C\u8EAB\u8FDB\u4E86\u5395\u6240\u3002</p><p>\u6768\u96EA\u9001\u6C5F\u6CB3\u56DE\u65C5\u9986\uFF0C\u8DEF\u4E0A\uFF0C\u6C5F\u6CB3\u611F\u53F9\uFF0C\u4EBA\u662F\u4E0D\u662F\u90FD\u662F\u4F1A\u53D8\u5F97\u3002\u6768\u96EA\u770B\u7740\u4ED6\u8D70\u8FDB\u7B80\u964B\u7684\u5C0F\u65C5\u9986\u3002\u611F\u53F9\u4ED6\u8FD8\u771F\u662F\u8DDF\u522B\u4EBA\u4E0D\u4E00\u6837\u3002</p><p>\u4E3A\u4E86\u7EF4\u6301\u5927\u5C71\u7684\u751F\u547D\uFF0C\u9A86\u7389\u73E0\u5DF2\u7ECF\u82B1\u5149\u4E86\u5BB6\u5E95\uFF0C\u4E0D\u5F97\u4E0D\u518D\u6B21\u65E9\u51FA\u665A\u5F52\uFF0C\u8D5A\u94B1\u517B\u5BB6\u3002\u7389\u73E0\u5356\u6389\u4E86\u5BB6\u91CC\u7684\u7535\u89C6\u673A\uFF0C\u5C0F\u65ED\u5341\u5206\u4E0D\u820D\uFF0C\u7389\u73E0\u8FD8\u662F\u72E0\u4E0B\u5FC3\u6765\u5356\u6389\u4E86\u7535\u89C6\u3002\u5E76\u627F\u8BFA\u5C0F\u65ED\u4EE5\u540E\u7ED9\u4ED6\u4E70\u5927\u5F69\u7535\u3002</p><p>\u9648\u5927\u5149\u5411\u9648\u6C5F\u6CB3\u70AB\u8000\u81EA\u5DF1\u73B0\u5728\u7684\u5BCC\u8DB3\u751F\u6D3B\uFF0C\u539F\u6765\u9648\u5927\u5149\u8FD9\u4E9B\u5E74\u4E00\u76F4\u5728\u9760\u7740\u8D70\u540E\u95E8\uFF0C\u6500\u5173\u7CFB\u8D5A\u4E86\u5927\u94B1\u3002\u5374\u53EA\u5B57\u4E0D\u63D0\u5DE7\u59D1\u3002\u6C5F\u6CB3\u529D\u544A\u4ED6\u505A\u751F\u610F\u8FD8\u662F\u8981\u8D70\u6B63\u9053\uFF0C\u5927\u5149\u5374\u5BF9\u6B64\u55E4\u4E4B\u4EE5\u9F3B\u3002\u5927\u5149\u7EC8\u4E8E\u8BF4\u51FA\u5DE7\u59D1\u63A5\u53D7\u4E0D\u4E86\u4ED6\u73B0\u5728\u7684\u6240\u4F5C\u6240\u4E3A\uFF0C\u56DE\u4E86\u4E49\u4E4C\u3002\u6C5F\u6CB3\u5BF9\u5927\u5149\u611F\u5230\u5341\u5206\u5931\u671B\u3002</p><p>\u7ECF\u8FC7\u8C03\u67E5\uFF0C\u8D75\u5BB6\u5E86\u7EC8\u4E8E\u62DB\u51FA\u4E86\u5B9E\u60C5\uFF0C\u738B\u5927\u5C71\u7684\u5DE5\u4F24\u8865\u52A9\u4E5F\u7EC8\u4E8E\u6279\u4E86\u4E0B\u6765\u3002\u7389\u73E0\u5174\u594B\u7684\u51B2\u56DE\u5BB6\u53BB\uFF0C\u60F3\u8981\u544A\u8BC9\u5927\u5C71\u8FD9\u4E2A\u597D\u6D88\u606F\uFF0C\u6CA1\u60F3\u5230\u5374\u53D1\u73B0\u5927\u5C71\u5DF2\u7ECF\u505C\u6B62\u4E86\u547C\u5438\u3002\u9A86\u7389\u73E0\u60B2\u75DB\u6B32\u7EDD\u3002</p><p>\u5927\u5C71\u8D70\u4E86\uFF0C\u4EBA\u4EEC\u5374\u5F00\u59CB\u6307\u8D23\u7389\u73E0\u53EA\u8981\u94B1\u4E0D\u8981\u4E08\u592B\u3002\u7389\u73E0\u65E0\u5FC3\u8FA9\u9A73\uFF0C\u5E26\u7740\u5B69\u5B50\u79BB\u5F00\u4E86\u8FD9\u4E2A\u4F24\u5FC3\u5730\u3002</p><p>\u6C5F\u6CB3\u7684\u889C\u5382\u9500\u91CF\u6253\u5F00\u4E86\uFF0C\u673A\u5668\u7684\u751F\u4EA7\u901F\u5EA6\u5374\u9010\u6E10\u8DDF\u4E0D\u4E0A\u3002\u9648\u6C5F\u6CB3\u72AF\u6101\uFF0C\u5C0F\u848B\u8DD1\u904D\u4E86\u5404\u5730\uFF0C\u5C45\u7136\u5230\u5904\u90FD\u4E70\u4E0D\u5230\u673A\u5668\u3002</p>',13),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","13.html.vue"]]);export{h as default};
