import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as t,f as a}from"./app.86bca16c.js";const c={},o=a('<h1 id="\u7B2C14\u96C6-\u5C0F\u5170\u82B1\u5411\u9752\u82CD\u6492\u5A07" tabindex="-1"><a class="header-anchor" href="#\u7B2C14\u96C6-\u5C0F\u5170\u82B1\u5411\u9752\u82CD\u6492\u5A07" aria-hidden="true">#</a> \u7B2C14\u96C6\uFF1A\u5C0F\u5170\u82B1\u5411\u9752\u82CD\u6492\u5A07</h1><p>\u8FD9\u5929\uFF0C\u5C0F\u5170\u82B1\u6B63\u767E\u65E0\u804A\u8D56\u7684\u8E72\u5728\u82B1\u623F\u91CC\u6D47\u82B1\uFF0C\u6CA1\u6210\u60F3\u7ED3\u9ECE\u7ADF\u6765\u4E86\u3002\u5F97\u77E5\u662F\u4E1C\u65B9\u9752\u82CD\u6551\u4E86\u5C0F\u5170\u82B1\uFF0C\u7ED3\u9ECE\u4E0D\u7531\u5F97\u6D6E\u4E0A\u4E00\u62B9\u73A9\u5473\u7684\u7B11\u3002\u8FD9\u4E00\u8DEF\u8D70\u6765\uFF0C\u5979\u542C\u4E86\u592A\u591A\u5173\u4E8E\u5C0F\u5170\u82B1\u548C\u4E1C\u65B9\u9752\u82CD\u7684\u98CE\u6D41\u97F5\u4E8B\uFF0C\u6CA1\u60F3\u5230\u8FD9\u4F20\u95FB\u7ADF\u662F\u771F\u7684\u3002\u5C0F\u5170\u82B1\u6025\u5FD9\u89E3\u91CA\uFF0C\u53EF\u7ED3\u9ECE\u4F3C\u4E4E\u4E5F\u5E76\u4E0D\u662F\u5341\u5206\u5728\u610F\u8FD9\u4E9B\u4F20\u95FB\uFF0C\u5979\u8FD9\u6B21\u6765\u5012\u662F\u771F\u7684\u6709\u6C42\u4E8E\u5C0F\u5170\u82B1\u3002</p><p>\u81EA\u4E0A\u6B21\u5C0F\u5170\u82B1\u5728\u6D77\u5E02\u60F9\u51FA\u7978\u7AEF\u540E\uFF0C\u6574\u4E2A\u6D77\u5E02\u90FD\u5728\u8FFD\u6740\u7ED3\u9ECE\uFF0C\u5979\u65E0\u53EF\u5948\u4F55\u53EA\u597D\u6765\u82CD\u76D0\u6D77\u627E\u5C0F\u5170\u82B1\u3002\u6B63\u8BF4\u7740\uFF0C\u4E1C\u65B9\u9752\u82CD\u7A81\u7136\u8D76\u6765\u3002\u4E1C\u65B9\u9752\u82CD\u672C\u5C31\u662F\u60F3\u6765\u770B\u770B\u5C0F\u5170\u82B1\u7684\uFF0C\u53EF\u662F\u8BDD\u5230\u5634\u8FB9\u53C8\u53D8\u4E86\u4E00\u4E2A\u5473\u513F\uFF0C\u7ADF\u50AC\u4FC3\u5C0F\u5170\u82B1\u4FEE\u547D\u7C3F\u3002\u5C0F\u5170\u82B1\u6709\u4E9B\u5931\u843D\u548C\u82E6\u95F7\uFF0C\u81EA\u5632\u81EA\u5DF1\u662F\u81EA\u4F5C\u591A\u60C5\uFF0C\u5F53\u521D\u8FD8\u4EE5\u4E3A\u4E1C\u65B9\u9752\u82CD\u627E\u81EA\u5DF1\u4FEE\u547D\u7C3F\u662F\u4E3A\u4E86\u966A\u81EA\u5DF1\uFF0C\u5982\u4ECA\u770B\u6765\u90FD\u662F\u81EA\u5DF1\u60F3\u591A\u4E86\u3002</p><p>\u5C0F\u5170\u82B1\u767E\u822C\u62D2\u7EDD\u4FEE\u547D\u7C3F\uFF0C\u76F4\u8A00\u81EA\u5DF1\u6CA1\u6709\u8FD9\u4E2A\u672C\u4E8B\u3002\u53EF\u662F\u8C01\u77E5\u7ADF\u60F9\u6012\u4E86\u4E1C\u65B9\u9752\u82CD\uFF0C\u8FD8\u8FDE\u7D2F\u4E86\u7ED3\u9ECE\u3002\u4E00\u8DEF\u4E0A\uFF0C\u7ED3\u9ECE\u90FD\u5728\u5411\u6B87\u9619\u89E3\u91CA\u548C\u6C42\u60C5\uFF0C\u53EF\u662F\u5BF9\u65B9\u4ECD\u65E7\u94C1\u9762\u65E0\u79C1\u3002\u7ED3\u9ECE\u53EA\u597D\u544A\u8BC9\u6B87\u9619\uFF0C\u4EE5\u81EA\u5DF1\u7684\u6027\u547D\u8981\u631F\u5C0F\u5170\u82B1\u4FEE\u547D\u7C3F\u662F\u6CA1\u6709\u7528\u7684\uFF0C\u56E0\u4E3A\u4E24\u4E2A\u4EBA\u6839\u672C\u5C31\u4E0D\u719F\u3002\u6B87\u9619\u89C9\u5F97\u7ED3\u9ECE\u6B64\u756A\u8BDD\u5012\u662F\u6709\u51E0\u5206\u9053\u7406\uFF0C\u4FBF\u7EE7\u7EED\u8BE2\u95EE\u5979\u5E94\u5F53\u5982\u4F55\u3002\u7ED3\u9ECE\u60F3\u4E86\u4E00\u4F1A\u513F\uFF0C\u60F3\u5230\u4E86\u4E00\u4E2A\u6CD5\u5B50\u3002\u53EA\u8981\u5C06\u4ED9\u65CF\u7684\u4EBA\u6293\u6765\uFF0C\u5C0F\u5170\u82B1\u4E00\u5B9A\u4F1A\u6709\u6240\u53CD\u5E94\u3002</p><p>\u867D\u7136\u6B87\u9619\u4E5F\u89C9\u5F97\u8FD9\u662F\u4E00\u4E2A\u4E0D\u9519\u7684\u529E\u6CD5\uFF0C\u53EF\u4ED6\u66F4\u77E5\u9053\uFF0C\u8FD9\u6837\u4F1A\u8BA9\u5C0F\u5170\u82B1\u96BE\u8FC7\u3002\u5C0F\u5170\u82B1\u96BE\u8FC7\uFF0C\u90A3\u4E48\u4E1C\u65B9\u9752\u82CD\u4E5F\u4F1A\u96BE\u8FC7\uFF0C\u8FD9\u662F\u4ED6\u4E0D\u613F\u610F\u770B\u5230\u7684\u3002\u542C\u5230\u8FD9\u8BDD\uFF0C\u7ED3\u9ECE\u5012\u662F\u89C9\u5F97\u6709\u4E9B\u597D\u7B11\uFF0C\u4E1C\u65B9\u9752\u82CD\u600E\u4E48\u4F1A\u8FD9\u4E48\u5728\u610F\u5C0F\u5170\u82B1\u3002\u5C31\u8FD9\u6837\uFF0C\u4E24\u4EBA\u4E3A\u4E86\u5C0F\u5170\u82B1\u548C\u4E1C\u65B9\u9752\u82CD\u662F\u5426\u4E24\u60C5\u76F8\u60A6\u5435\u4E86\u8D77\u6765\u3002\u6700\u540E\uFF0C\u7ED3\u9ECE\u4E3A\u4E86\u6D3B\u547D\uFF0C\u53EA\u80FD\u987A\u7740\u6B87\u9619\u7684\u8BDD\u832C\u7EE7\u7EED\u8BF4\u4E0B\u53BB\uFF0C\u76F4\u8A00\u81EA\u5DF1\u6709\u529E\u6CD5\u4FC3\u8FDB\u4E1C\u65B9\u9752\u82CD\u548C\u5C0F\u5170\u82B1\u7684\u611F\u60C5\u3002</p><p>\u4E0D\u77E5\u4E0D\u89C9\uFF0C\u4E24\u4EBA\u5C31\u6765\u5230\u4E86\u5730\u7262\u3002\u6B87\u9619\u4E5F\u89C9\u5F97\u7ED3\u9ECE\u8BF4\u7684\u5F88\u6709\u9053\u7406\uFF0C\u53EF\u6700\u540E\u4ED6\u8FD8\u662F\u8BA9\u4EBA\u5C06\u5979\u5173\u8FDB\u7262\u91CC\u3002\u5E78\u800C\u5C0F\u5170\u82B1\u53CA\u65F6\u8D76\u5230\uFF0C\u8FD9\u624D\u6551\u4E86\u7ED3\u9ECE\u3002\u52AB\u540E\u4F59\u751F\u7684\u7ED3\u9ECE\u8D76\u5FD9\u8BE2\u95EE\u5C0F\u5170\u82B1\u548C\u4E1C\u65B9\u9752\u82CD\u7684\u5173\u7CFB\uFF0C\u4E00\u756A\u8C08\u8BDD\u4E0B\u6765\uFF0C\u7ED3\u9ECE\u5C31\u77E5\u9053\u4E86\u4E24\u4E2A\u4EBA\u7684\u611F\u60C5\u3002\u7ED3\u9ECE\u7ED9\u5C0F\u5170\u82B1\u51FA\u4E3B\u610F\uFF0C\u65E2\u7136\u4E1C\u65B9\u9752\u82CD\u559C\u6B22\u5979\uFF0C\u5979\u5C31\u53EF\u4EE5\u8BA8\u597D\u4E1C\u65B9\u9752\u82CD\u6210\u4E3A\u6708\u65CF\u7684\u5973\u4E3B\u4EBA\uFF0C\u8FD9\u6837\u4E0D\u662F\u60F3\u5E72\u4EC0\u4E48\u5E72\u4EC0\u4E48\u3002\u5C0F\u5170\u82B1\u4E00\u542C\u89C9\u5F97\u751A\u662F\u6709\u7406\uFF0C\u4FBF\u5411\u7ED3\u9ECE\u8BA8\u6559\u8BA8\u597D\u4E1C\u65B9\u9752\u82CD\u7684\u65B9\u6CD5\u3002</p><p>\u8FD9\u5929\uFF0C\u5C0F\u5170\u82B1\u5FFD\u7136\u5C06\u4E1C\u65B9\u9752\u82CD\u53EB\u53BB\u3002\u4E1C\u65B9\u9752\u82CD\u5634\u4E0A\u867D\u8BF4\u7740\u4E0D\u613F\u53BB\uFF0C\u53EF\u8EAB\u4F53\u8FD8\u662F\u5F88\u8001\u5B9E\u7684\u8D70\u8FDB\u4E86\u5C0F\u5170\u82B1\u7684\u9662\u5B50\uFF0C\u7136\u800C\u4E00\u89C1\u9762\u5C31\u95EE\u8D77\u547D\u7C3F\u7684\u4E8B\u60C5\u3002\u5C0F\u5170\u82B1\u6446\u5F04\u7740\u81EA\u5DF1\u7684\u53D1\u9970\uFF0C\u773C\u6CE2\u6D41\u8F6C\u5341\u5206\u5A07\u4FCF\u3002\u53EF\u5BF9\u9762\u7684\u4E1C\u65B9\u9752\u82CD\u5374\u663E\u5F97\u6709\u4E9B\u4E0D\u8010\u70E6\uFF0C\u50AC\u4FC3\u5C0F\u5170\u82B1\u8D76\u5FEB\u7ED9\u51FA\u7B54\u6848\u3002\u5C0F\u5170\u82B1\u8138\u4E0A\u7684\u7B11\u5BB9\u77AC\u95F4\u6536\u4E86\u8D77\u6765\uFF0C\u6709\u4E9B\u59D4\u5C48\u7684\u8BF4\u81EA\u5DF1\u8FD8\u6CA1\u6709\u5F04\u597D\u3002</p><p>\u89C1\u4E1C\u65B9\u9752\u82CD\u6709\u4E9B\u6120\u6012\uFF0C\u5C0F\u5170\u82B1\u7F13\u7F13\u4F38\u624B\u62C9\u4F4F\u4ED6\u7684\u8863\u670D\u5DE6\u53F3\u6447\u6446\uFF0C\u5F88\u662F\u5A07\u7F9E\u7684\u6A21\u6837\u3002\u7136\u800C\u4E1C\u65B9\u9752\u82CD\u4F3C\u4E4E\u5E76\u4E0D\u5403\u8FD9\u5957\uFF0C\u53CD\u5012\u63D0\u8D77\u957F\u73E9\u6765\u3002\u5C0F\u5170\u82B1\u6709\u4E9B\u5C34\u5C2C\uFF0C\u6025\u5FD9\u5938\u8D5E\u4E1C\u65B9\u9752\u82CD\u795E\u52C7\u76D6\u4E16\uFF0C\u5929\u4E0B\u65E0\u53CC\u3002\u65E0\u5948\u4E1C\u65B9\u9752\u82CD\u4F3C\u4E4E\u4ECD\u65E7\u6CA1\u6709\u9886\u60C5\u3002\u5C0F\u5170\u82B1\u9080\u8BF7\u4E1C\u65B9\u9752\u82CD\u559D\u8336\uFF0C\u548C\u4ED6\u804A\u8D77\u8FC7\u5F80\u7684\u4E8B\u60C5\u3002\u4E1C\u65B9\u9752\u82CD\u5FC3\u91CC\u8D77\u4E86\u6D9F\u6F2A\uFF0C\u53EF\u662F\u6708\u5C0A\u7684\u8EAB\u4EFD\u8BA9\u4ED6\u4E0D\u5F97\u4E0D\u6536\u8D77\u8FD9\u4EFD\u840C\u52A8\uFF0C\u8D76\u5FD9\u501F\u53E3\u79BB\u5F00\u3002\u5C0F\u5170\u82B1\u5FFD\u7136\u53EB\u4F4F\u4ED6\uFF0C\u8BF4\u81EA\u5DF1\u77E5\u9053\u4ED6\u5BF9\u81EA\u5DF1\u7684\u597D\u3002</p><p>\u4E1C\u65B9\u9752\u82CD\u544A\u8BC9\u5C0F\u5170\u82B1\uFF0C\u56E0\u4E3A\u4FEE\u4E60\u4E1A\u706B\uFF0C\u81EA\u5DF1\u5DF2\u7ECF\u65AD\u60C5\u7EDD\u7231\uFF0C\u7EDD\u4E0D\u53EF\u80FD\u5BF9\u5979\u52A8\u60C5\u3002\u8BB0\u5FC6\u5FFD\u7136\u56DE\u6EAF\uFF0C\u5C0F\u5C0F\u7684\u4E1C\u65B9\u9752\u82CD\u75DB\u82E6\u7684\u53EB\u558A\u7740\uFF0C\u53EF\u662F\u4E5F\u53EA\u80FD\u4EFB\u7531\u90A3\u4E9B\u4E1A\u706B\u5728\u81EA\u5DF1\u8EAB\u4F53\u5185\u5916\u4E71\u7A9C\u3002\u4ED6\u7684\u7236\u4EB2\u7AD9\u5728\u4E0D\u8FDC\u5904\uFF0C\u7136\u800C\u773C\u91CC\u6CA1\u6709\u4E1D\u6BEB\u5FC3\u75BC\uFF0C\u751A\u81F3\u6405\u52A8\u7740\u90A3\u4E9B\u4E1A\u706B\u66F4\u4E3A\u731B\u70C8\u3002\u5C0F\u5170\u82B1\u5FFD\u7136\u5C31\u660E\u767D\u4E86\uFF0C\u4E3A\u4F55\u90A3\u65E5\u5728\u660A\u5929\u5854\uFF0C\u81EA\u5DF1\u4F1A\u5728\u68A6\u4E2D\u770B\u5230\u90A3\u68F5\u5149\u79C3\u79C3\u7684\u6811\u3002\u4E5F\u8BB8\u662F\u540C\u60C5\u4E1C\u65B9\u9752\u82CD\u7684\u906D\u9047\uFF0C\u5C0F\u5170\u82B1\u5411\u4ED6\u627F\u8BFA\uFF0C\u81EA\u5DF1\u4E00\u5B9A\u4F1A\u4FEE\u590D\u597D\u4ED6\u7684\u4E03\u60C5\u6811\u3002</p><p>\u4ECE\u7ED3\u9ECE\u90A3\u91CC\u62FF\u5230\u4E86\u836F\uFF0C\u5C0F\u5170\u82B1\u9AD8\u5174\u4E07\u5206\uFF0C\u7136\u800C\u60F3\u5230\u81EA\u5DF1\u88AB\u4E91\u4E2D\u541B\u5224\u5B9A\u4E3A\u6708\u65CF\uFF0C\u5FC3\u4E2D\u53C8\u4E0D\u7531\u5F97\u4F24\u5FC3\u8D77\u6765\u3002\u6B64\u65F6\u6B63\u5728\u5BC2\u6708\u5BAB\u5546\u8BA8\u5927\u4E8B\u7684\u4E1C\u65B9\u9752\u82CD\uFF0C\u4E5F\u8DDF\u7740\u5979\u53D8\u5316\u7740\u60C5\u7EEA\u3002\u6234\u4E86\u5E7D\u7389\u6212\u4E4B\u540E\uFF0C\u4E1C\u65B9\u9752\u82CD\u8FD9\u624D\u52C9\u5F3A\u6062\u590D\u4E86\u5FC3\u7EEA\u3002\u4E3A\u4E86\u907F\u514D\u8FD9\u79CD\u4E8B\u60C5\u518D\u53D1\u751F\uFF0C\u4E1C\u65B9\u9752\u82CD\u4EE5\u7ED3\u9ECE\u7684\u547D\u76F8\u5A01\u80C1\uFF0C\u8BA9\u5C0F\u5170\u82B1\u597D\u597D\u5F85\u7740\u3002\u5C0F\u5170\u82B1\u5413\u5F97\u8FDE\u8FDE\u70B9\u5934\u7B54\u5E94\u3002</p><p>\u5BB9\u660A\u66FF\u957F\u73E9\u7597\u4F24\uFF0C\u867D\u4E0D\u613F\u4ED6\u53BB\u82CD\u76D0\u6D77\u5192\u9669\uFF0C\u53EF\u4ED6\u77E5\u9053\u5931\u53BB\u7684\u75DB\u82E6\uFF0C\u4FBF\u4E5F\u6CA1\u6709\u62E6\u7740\u4ED6\u3002</p>',11),r=[o];function _(n,s){return p(),t("div",null,r)}const h=e(c,[["render",_],["__file","14.html.vue"]]);export{h as default};
