import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.2ed33847.js";const c={},o=a('<h1 id="\u7B2C14\u96C6-\u5468\u79C9\u6606\u6BCD\u4EB2\u7A81\u7136\u9192\u6765" tabindex="-1"><a class="header-anchor" href="#\u7B2C14\u96C6-\u5468\u79C9\u6606\u6BCD\u4EB2\u7A81\u7136\u9192\u6765" aria-hidden="true">#</a> \u7B2C14\u96C6\uFF1A\u5468\u79C9\u6606\u6BCD\u4EB2\u7A81\u7136\u9192\u6765</h1><p>\u8521\u6653\u5149\u6765\u627E\u5468\u79C9\u6606\uFF0C\u62FF\u51FA\u4E86\u5317\u4EAC\u5E7F\u64AD\u5B66\u9662\u7684\u5F55\u53D6\u901A\u77E5\u4E66\uFF0C\u4F46\u7531\u4E8E\u5382\u91CC\u4E0D\u6279\u51C6\uFF0C\u4ED6\u7236\u4EB2\u653F\u5BA1\u6709\u95EE\u9898\uFF0C\u6240\u4EE5\u65E0\u6CD5\u53BB\u4E0A\u5927\u5B66\u3002\u8521\u6653\u5149\u5E0C\u671B\u5468\u79C9\u6606\u80FD\u53BB\u548C\u51AC\u6885\u7236\u4EB2\u8BF4\u4E00\u58F0\uFF0C\u56E0\u4E3A\u73B0\u5728\u90DD\u51AC\u6885\u7236\u4EB2\u5DF2\u7ECF\u6062\u590D\u4E86\u7701\u957F\u7684\u804C\u4F4D\u3002\u5468\u79C9\u6606\u8BA4\u4E3A\u81EA\u5DF1\u51FA\u9762\u53EF\u80FD\u4E0D\u5408\u9002\uFF0C\u627E\u5468\u79C9\u4E49\u6700\u5408\u9002\uFF0C\u5982\u679C\u4ED6\u548C\u5468\u84C9\u8FD8\u6709\u7EC3\u4E60\u7684\u8BDD\uFF0C\u627E\u5468\u84C9\u4E5F\u53EF\u4EE5\u7684\u3002</p><p>\u6B64\u65F6\uFF0C\u5468\u84C9\u548C\u5468\u79C9\u4E49\u5DF2\u7ECF\u8FDB\u5165\u4E86\u5317\u5927\uFF0C\u5728\u5927\u793C\u5802\u91CC\uFF0C\u8457\u540D\u8BD7\u4EBA\u51AF\u5316\u6210\u4E3A\u5927\u5BB6\u6717\u8BFB\u970D\u91D1\u7684\u8BD7\u8BCD\uFF0C\u5468\u84C9\u9F13\u638C\u6EE1\u8138\u5199\u7740\u9A84\u50B2\u3002</p><p>\u90DD\u51AC\u6885\u4E5F\u5C06\u8FD9\u4EF6\u4E8B\u544A\u8BC9\u4E86\u7236\u4EB2\uFF0C\u5E0C\u671B\u80FD\u8BA9\u7236\u4EB2\u5E2E\u5FD9\uFF0C\u53EF\u662F\u6BCD\u4EB2\u5374\u62A2\u8FC7\u53BB\u4E86\u8D44\u6599\u8981\u4EA4\u7ED9\u9EC4\u79D8\u4E66\u770B\u770B\uFF0C\u5982\u679C\u662F\u8FDD\u53CD\u653F\u7B56\u7684\u4E8B\u60C5\u575A\u51B3\u4E0D\u80FD\u505A\uFF0C\u4F46\u662F\u7236\u4EB2\u8FD8\u662F\u5E2E\u4E86\u8521\u6653\u5149\u3002\u8521\u6653\u5149\u4E5F\u5F97\u5230\u6D88\u606F\u53EF\u4EE5\u5C3D\u5FEB\u5A36\u62A5\u9053\u4E86\uFF0C\u5382\u957F\u8BD5\u63A2\u6027\u8BE2\u95EE\u8521\u6653\u5149\u548C\u90DD\u7701\u957F\u7684\u5173\u7CFB\uFF0C\u8521\u6653\u5149\u5FD9\u6447\u5934\u8868\u793A\u6CA1\u6709\u5173\u7CFB\u3002\u4F46\u5382\u957F\u8FD8\u662F\u8BF4\u4E86\u5F88\u591A\u5382\u5B50\u91CC\u7684\u95EE\u9898\uFF0C\u5E0C\u671B\u80FD\u6709\u673A\u4F1A\u8BA9\u7701\u91CC\u7ED9\u6279\u590D\u4E00\u4E9B\u8D44\u91D1\u3002</p><p>\u5468\u79C9\u6606\u5E26\u7740\u4E00\u4E2A\u5BA2\u6237\u6765\u6FA1\u5802\u5B50\u4FEE\u811A\uFF0C\u672C\u60F3\u8981\u7B7E\u5408\u540C\u7684\uFF0C\u4F46\u662F\u5BA2\u6237\u5BF9\u4E54\u6625\u71D5\u52A8\u624B\u52A8\u811A\uFF0C\u5468\u79C9\u6606\u5B9E\u5728\u770B\u4E0D\u4E0B\u53BB\u5C31\u548C\u5BA2\u6237\u7FFB\u8138\u4E86\u3002\u4E54\u6625\u71D5\u5FC3\u91CC\u4E5F\u751F\u6C14\u73B0\u5728\u4EBA\u7684\u7D20\u8D28\u592A\u5DEE\u4E86\uFF0C\u539F\u672C\u5DF4\u7ED3\u4E54\u6625\u71D5\u7684\u540C\u4E8B\uFF0C\u73B0\u5728\u4E5F\u5BF9\u5979\u6307\u624B\u753B\u811A\u7684\uFF0C\u4E54\u6625\u71D5\u5411\u5468\u79C9\u6606\u53D1\u7262\u9A9A\u73B0\u5728\u90FD\u662F\u72D7\u773C\u770B\u4EBA\u4F4E\u3002</p><p>\u4E54\u6625\u71D5\u7684\u59D0\u59D0\u56DE\u6765\u6CA1\u6709\u5730\u65B9\u4F4F\uFF0C\u4E54\u5927\u5A76\u5C31\u627E\u4E86\u5468\u79C9\u6606\u5E2E\u5FD9\uFF0C\u5E0C\u671B\u80FD\u4F4F\u5728\u90D1\u5A1F\u7684\u623F\u5B50\u91CC\u53BB\uFF0C\u5468\u79C9\u6606\u7ACB\u523B\u7B54\u5E94\u4E86\uFF0C\u5E76\u627F\u8BFA\u4EC0\u4E48\u65F6\u5019\u4E54\u6625\u71D5\u7684\u59D0\u59D0\u51FA\u5AC1\u4E86\uFF0C\u4EC0\u4E48\u65F6\u5019\u518D\u642C\u8D70\u3002</p><p>\u6C34\u81EA\u6D41\u548C\u9A86\u58EB\u5BBE\u4E5F\u51FA\u72F1\u4E86\uFF0C\u6C34\u81EA\u6D41\u63D0\u51FA\u53BB\u770B\u770B\u5B69\u5B50\uFF0C\u4F46\u662F\u9A86\u58EB\u5BBE\u5374\u5E76\u4E0D\u60F3\u53BB\uFF0C\u5728\u4ED6\u770B\u6765\u81EA\u5DF1\u73B0\u5728\u662F\u4EC0\u4E48\u90FD\u6CA1\u6709\uFF0C\u4E5F\u4E0D\u4F1A\u6709\u4EBA\u77A7\u5F97\u8D77\u3002</p><p>\u5468\u79C9\u6606\u6765\u706B\u8F66\u7AD9\u63A5\u4E86\u5468\u84C9\u548C\u51AF\u5316\u6210\u56DE\u53BB\u5BB6\u91CC\uFF0C\u5468\u79C9\u4E49\u548C\u90DD\u51AC\u6885\u4E5F\u4E00\u8D77\u56DE\u6765\u4E86\u3002\u5F53\u770B\u89C1\u6BCD\u4EB2\u8EBA\u5728\u5E8A\u4E0A\u660F\u8FF7\u4E0D\u9192\u7684\u6837\u5B50\uFF0C\u5468\u84C9\u4F24\u5FC3\u843D\u6CEA\uFF0C\u4E00\u76F4\u548C\u6BCD\u4EB2\u8BF4\u7740\u5BF9\u4E0D\u8D77\uFF0C\u548C\u90D1\u5A1F\u8BF4\u7740\u5BF9\u4E0D\u8D77\uFF0C\u8FD9\u4E9B\u5E74\u4ED6\u4EEC\u6CA1\u80FD\u56DE\u6765\uFF0C\u90D1\u5A1F\u4E0D\u4F46\u7167\u987E\u4E86\u6BCD\u4EB2\uFF0C\u8FD8\u7167\u987E\u4E86\u51AF\u73A5\u73A5\u3002</p><p>\u5468\u79C9\u4E49\u4E5F\u548C\u90DD\u51AC\u6885\u4E00\u8D77\u6765\u5230\u4E86\u90DD\u51AC\u6885\u5BB6\uFF0C\u90DD\u51AC\u6885\u4E5F\u5411\u7236\u6BCD\u8BF4\u8D77\u4E86\u90D1\u5A1F\u7684\u4E8B\u60C5\uFF0C\u6BCD\u4EB2\u53EE\u5631\u4ED6\u4EEC\u8BA4\u771F\u5B66\u4E60\uFF0C\u8FD9\u6837\u624D\u80FD\u5BF9\u5F97\u8D77\u90D1\u5A1F\u7684\u4ED8\u51FA\u3002\u5468\u6BCD\u5173\u5FC3\u5468\u5FD7\u521A\u4EC0\u4E48\u65F6\u5019\u56DE\u6765\uFF0C\u4E5F\u5E0C\u671B\u56DE\u6765\u7684\u65F6\u5019\u4E24\u5BB6\u4EBA\u80FD\u76F8\u805A\u4E00\u6B21\u3002</p><p>\u672C\u4EE5\u4E3A\u5468\u84C9\u4ED6\u4EEC\u4F1A\u56DE\u6765\u4F4F\uFF0C\u6240\u4EE5\u5BB6\u91CC\u5C31\u4E70\u4E86\u98CE\u6247\uFF0C\u4F46\u662F\u5468\u84C9\u548C\u51AF\u5316\u6210\u5E76\u672A\u4F4F\u5728\u5BB6\u91CC\uFF0C\u90D1\u5A1F\u5C31\u89C9\u5F97\u5FC3\u75BC\u7535\u8D39\uFF0C\u8FD8\u8981\u7ED9\u5468\u79C9\u6606\u6247\u6247\u5B50\u3002\u6B64\u65F6\uFF0C\u5468\u84C9\u548C\u51AF\u5316\u6210\u62B1\u7740\u51AF\u73A5\u73A5\u7ED9\u9001\u56DE\u6765\uFF0C\u51AF\u73A5\u73A5\u8FD9\u4E9B\u5E74\u8DDF\u7740\u90D1\u5A1F\uFF0C\u5BF9\u5468\u84C9\u6709\u4E9B\u964C\u751F\u4E86\uFF0C\u5230\u4E86\u665A\u4E0A\u5C31\u54ED\u7740\u95F9\u7740\u8981\u627E\u90D1\u5A1F\uFF0C\u5468\u84C9\u5FC3\u91CC\u65E0\u6BD4\u5931\u843D\u3002</p><p>\u5468\u79C9\u4E49\u6709\u4E9B\u81EA\u8D23\uFF0C\u89C9\u5F97\u4E0D\u8BE5\u627E\u90DD\u7701\u957F\u529E\u8521\u6653\u5149\u7684\u4E8B\u60C5\uFF0C\u6BD5\u7ADF\u662F\u59B9\u59B9\u7684\u540C\u5B66\uFF0C\u90DD\u51AC\u6885\u8BA4\u4E3A\u627E\u7236\u4EB2\u5E2E\u5FD9\u6CA1\u6709\u9519\u3002\u53EF\u5468\u79C9\u4E49\u89C9\u5F97\u5E2E\u5FD9\u6CA1\u6709\u9519\uFF0C\u4F46\u5B9E\u9645\u4E0A\u6E20\u9053\u9519\u4E86\u3002\u56E0\u4E3A\u4ECA\u5929\u5728\u996D\u684C\u4E0A\u5468\u79C9\u4E49\u63D0\u4E86\u5149\u5B57\u7247\u6539\u9020\u7684\u4E8B\u60C5\uFF0C\u90DD\u7701\u957F\u53EA\u662F\u4E00\u53E5\u767E\u5E9F\u5F85\u5174\u5C31\u7ED9\u6253\u53D1\u4E86\uFF0C\u5468\u79C9\u4E49\u89C9\u5F97\u53EF\u80FD\u548C\u4E4B\u524D\u627E\u90DD\u7701\u957F\u529E\u4E8B\u6709\u5173\u3002</p><p>\u5468\u79C9\u4E49\u548C\u5468\u84C9\u8981\u79BB\u5F00\u53BB\u4E0A\u5B66\u4E86\uFF0C\u90DD\u51AC\u6885\u53EE\u5631\u5468\u79C9\u6606\u6709\u4E8B\u5C3D\u7BA1\u627E\u5979\uFF0C\u53EF\u662F\u5468\u79C9\u4E49\u5374\u5E76\u4E0D\u653E\u5FC3\u770B\u7740\u5468\u79C9\u6606\uFF0C\u5468\u79C9\u6606\u8BFB\u61C2\u4E86\u5468\u79C9\u4E49\u7684\u610F\u601D\uFF0C\u4E5F\u8868\u793A\u4E0D\u4F1A\u53BB\u627E\u90DD\u51AC\u6885\u5E2E\u5FD9\u3002</p><p>\u90D1\u5A1F\u7ED9\u5468\u6BCD\u6309\u6469\u7684\u65F6\u5019\uFF0C\u5468\u6BCD\u5FFD\u7136\u7741\u5F00\u773C\u775B\uFF0C\u8BE2\u95EE\u90D1\u5A1F\u662F\u8C01\uFF1F\u90D1\u5A1F\u8D76\u7D27\u89E3\u91CA\u5468\u6BCD\u7761\u4E86\u4E24\u5E74\u591A\uFF0C\u5979\u662F\u6765\u8D1F\u8D23\u7167\u987E\u5468\u6BCD\u7684\u3002\u6960\u6960\u7740\u6025\u4E0A\u5395\u6240\u6765\u5230\u5C4B\u5B50\u91CC\uFF0C\u5468\u6BCD\u8FD8\u8BE2\u95EE\u6960\u6960\u662F\u8C01\uFF1F\u4F46\u968F\u540E\u5C31\u95ED\u4E0A\u773C\u4E86\uFF0C\u90D1\u5A1F\u6FC0\u52A8\u7684\u8DD1\u53BB\u627E\u5468\u79C9\u6606\uFF0C\u544A\u8BC9\u5468\u79C9\u6606\u5468\u6BCD\u9192\u4E86\uFF0C\u4F46\u53C8\u751F\u6015\u81EA\u5DF1\u773C\u775B\u82B1\u4E86\u770B\u9519\u4E86\u3002\u5468\u79C9\u6606\u4E00\u8DEF\u9A91\u7740\u81EA\u884C\u8F66\u5E26\u7740\u90D1\u5A1F\u56DE\u53BB\uFF0C\u53EA\u8981\u6BCD\u4EB2\u9192\u4E86\uFF0C\u90D1\u5A1F\u5C31\u662F\u6700\u5927\u7684\u529F\u81E3\u3002\u5468\u79C9\u6606\u56DE\u53BB\u7684\u65F6\u5019\u548C\u6BCD\u4EB2\u8BF4\u8BDD\uFF0C\u6BCD\u4EB2\u5E76\u672A\u56DE\u7B54\u3002</p><p>\u665A\u4E0A\u5468\u79C9\u6606\u4E3B\u52A8\u63D0\u51FA\u8981\u966A\u7740\u6BCD\u4EB2\u7761\uFF0C\u534A\u591C\u770B\u5230\u6BCD\u4EB2\u7741\u5F00\u773C\u775B\u770B\u7740\u81EA\u5DF1\uFF0C\u5468\u79C9\u6606\u8D76\u7D27\u51D1\u4E0A\u53BB\u6FC0\u52A8\u4E0D\u5DF2\u3002\u5468\u6BCD\u4E00\u53E5\u53E3\u6E34\u4E86\uFF0C\u5468\u79C9\u6606\u7740\u6025\u4E0B\u5730\u53BB\u7ED9\u6BCD\u4EB2\u5012\u6C34\uFF0C\u770B\u7740\u6BCD\u4EB2\u559D\u6C34\u7684\u6837\u5B50\uFF0C\u5468\u79C9\u6606\u5634\u89D2\u6291\u5236\u4E0D\u4F4F\u7684\u7B11\uFF0C\u4ED6\u751F\u6015\u6BCD\u4EB2\u4E0D\u8981\u4ED6\u4E86\uFF0C\u6BCD\u4EB2\u9732\u51FA\u7B11\u5BB9\uFF0C\u4F3C\u4E4E\u5F88\u75B2\u60EB\u53C8\u7761\u4E86\u4E00\u4F1A\u513F\uFF0C\u5468\u79C9\u6606\u6FC0\u52A8\u5730\u6D41\u4E0B\u773C\u6CEA\uFF0C\u7D27\u5FD9\u8DD1\u53BB\u544A\u8BC9\u90D1\u5A1F\u6BCD\u4EB2\u9192\u4E86\uFF0C\u5468\u79C9\u6606\u5FCD\u4E0D\u4F4F\u4E00\u4E2A\u52B2\u7684\u4EB2\u543B\u90D1\u5A1F\u7684\u989D\u5934\u3002</p><p>\u5468\u79C9\u6606\u8FD8\u4EA4\u4EE3\u4E24\u4E2A\u5B69\u5B50\u8981\u5F53\u6210\u5927\u4EBA\uFF0C\u4E07\u4E00\u5988\u5988\u4E0D\u5728\u5BB6\u7684\u65F6\u5019\uFF0C\u4E24\u4E2A\u5B69\u5B50\u8981\u62C5\u8D1F\u8D77\u7167\u987E\u5976\u5976\u7684\u8D23\u4EFB\u3002\u7B2C\u4E8C\u5929\uFF0C\u5468\u6BCD\u80FD\u505A\u8D77\u6765\u4E86\uFF0C\u8FD8\u548C\u4E24\u4E2A\u5B69\u5B50\u804A\u5929\uFF0C\u5468\u6BCD\u60F3\u8D77\u5973\u5B69\u662F\u5468\u84C9\u7684\u5973\u513F\u3002</p><p>\u90D1\u5A1F\u56DE\u6765\uFF0C\u6709\u4E9B\u7D27\u5F20\uFF0C\u7279\u610F\u5012\u4E86\u4E00\u676F\u767D\u7CD6\u6C34\u7ED9\u5468\u6BCD\u9001\u8FC7\u53BB\u3002</p>',16),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","14.html.vue"]]);export{h as default};
