import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.21634df6.js";const c={},o=a('<h1 id="\u7B2C15\u96C6-\u9752\u82CD\u53CA\u65F6\u6551\u4E0B\u5C0F\u5170\u82B1" tabindex="-1"><a class="header-anchor" href="#\u7B2C15\u96C6-\u9752\u82CD\u53CA\u65F6\u6551\u4E0B\u5C0F\u5170\u82B1" aria-hidden="true">#</a> \u7B2C15\u96C6\uFF1A\u9752\u82CD\u53CA\u65F6\u6551\u4E0B\u5C0F\u5170\u82B1</h1><p>\u957F\u73E9\u5DF2\u7ECF\u5230\u4E86\u5FD8\u5DDD\uFF0C\u53EF\u6CA1\u60F3\u5230\u4E91\u4E2D\u541B\u8FD9\u4E48\u5FEB\u5C31\u8FFD\u4E86\u4E0A\u6765\u3002\u9762\u5BF9\u4E91\u4E2D\u541B\u7684\u8BE2\u95EE\uFF0C\u957F\u73E9\u5766\u8A00\u81EA\u5DF1\u559C\u6B22\u5C0F\u5170\u82B1\u3002\u53EF\u4ED6\u7EC8\u7A76\u4E0D\u662F\u4E91\u4E2D\u541B\u7684\u5BF9\u624B\uFF0C\u6700\u540E\u88AB\u5173\u8FDB\u4E86\u660A\u5929\u5854\u3002</p><p>\u5DF2\u7ECF\u6DF1\u591C\uFF0C\u7ED3\u9ECE\u8FD8\u5728\u5916\u9762\u6E38\u8361\u3002\u6B87\u9619\u89C1\u72B6\uFF0C\u8D76\u5FD9\u4E0A\u524D\u76D8\u95EE\u3002\u7ED3\u9ECE\u8BF4\u7740\u5C31\u59D4\u5C48\u8D77\u6765\uFF0C\u8868\u793A\u81EA\u5DF1\u4ECA\u5929\u88AB\u5413\u7740\u4E86\uFF0C\u665A\u4E0A\u7761\u4E0D\u7740\uFF0C\u8FD9\u624D\u51FA\u6765\u6563\u5FC3\u3002\u542C\u95FB\u7ED3\u9ECE\u4E5F\u662F\u5B64\u513F\uFF0C\u6B87\u9619\u987F\u65F6\u8D77\u4E86\u540C\u60C5\u5FC3\u3002\u627E\u5230\u4E86\u5171\u540C\u8BDD\u9898\uFF0C\u4E24\u4EBA\u4FBF\u5750\u5728\u4E00\u8D77\u804A\u4E86\u8BB8\u4E45\u3002</p><p>\u6B87\u9619\u89C9\u5F97\u81EA\u5DF1\u5F88\u662F\u5E78\u8FD0\uFF0C\u9047\u5230\u4E86\u53EF\u4EE5\u5FE0\u8BDA\u4E00\u751F\u7684\u6708\u5C0A\u3002\u53EF\u7ED3\u9ECE\u5012\u662F\u89C9\u5F97\uFF0C\u4EBA\u5FC3\u590D\u6742\uFF0C\u4E0E\u5176\u7B49\u7740\u88AB\u4EBA\u9A97\uFF0C\u4E0D\u5982\u81EA\u5DF1\u53BB\u9A97\u522B\u4EBA\u3002\u6B87\u9619\u987F\u65F6\u6709\u4E9B\u751F\u6C14\uFF0C\u6307\u8D23\u7ED3\u9ECE\u7684\u8FD9\u79CD\u60F3\u6CD5\u7B80\u76F4\u8352\u8C2C\u81F3\u6781\u3002\u7ED3\u9ECE\u4E5F\u4E0D\u7518\u793A\u5F31\uFF0C\u53CD\u9A73\u9053\u8FD9\u4E16\u95F4\u4E4B\u4E8B\u672C\u5C31\u6CA1\u6709\u4EC0\u4E48\u660E\u663E\u7684\u5BF9\u9519\u3002\u521A\u521A\u6109\u5FEB\u7684\u8C08\u8BDD\u5230\u6B64\u7ED3\u675F\uFF0C\u6B87\u9619\u607C\u7F9E\u6210\u6012\u5C06\u5979\u8D76\u4E86\u56DE\u53BB\u3002</p><p>\u4E00\u5927\u65E9\uFF0C\u5C0F\u5170\u82B1\u5C31\u8DD1\u8FC7\u53BB\u7ED9\u7ED3\u9ECE\u9053\u6B49\u3002\u89C1\u5BF9\u65B9\u6CA1\u6709\u539F\u8C05\u81EA\u5DF1\u7684\u610F\u601D\uFF0C\u5979\u8D76\u5FD9\u638F\u51FA\u4E1C\u65B9\u9752\u82CD\u9001\u7ED9\u81EA\u5DF1\u7684\u9AA8\u5170\u3002\u7ED3\u9ECE\u9AD8\u5174\u6536\u4E0B\uFF0C\u8BA9\u5C0F\u5170\u82B1\u4E5F\u548C\u81EA\u5DF1\u4E00\u8D77\u9003\u51FA\u53BB\u3002\u53EF\u4E00\u60F3\u5230\u4E1C\u65B9\u9752\u82CD\u548C\u81EA\u5DF1\u5FC3\u8109\u76F8\u8FDE\uFF0C\u5C0F\u5170\u82B1\u53C8\u72B9\u8C6B\u4E86\uFF0C\u62C5\u5FC3\u81EA\u5DF1\u7684\u7D27\u5F20\u60C5\u7EEA\u4F1A\u8BEF\u4E8B\u3002\u7ED3\u9ECE\u638F\u51FA\u85CF\u5FC3\u7C2A\uFF0C\u8BA9\u5C0F\u5170\u82B1\u6234\u4E0A\u3002</p><p>\u5F53\u5E74\u6708\u65CF\u59CB\u7956\u76D0\u5973\u548C\u4ED9\u65CF\u59CB\u7956\u91CD\u534E\u5FC3\u610F\u60F3\u901A\uFF0C\u4E24\u60C5\u76F8\u60A6\uFF0C\u53EF\u540E\u6765\u6708\u65CF\u5374\u88AB\u4ED9\u65CF\u80CC\u5F03\u9A71\u9010\u3002\u76D0\u5973\u4F24\u5FC3\u4E0E\u91CD\u534E\u51B3\u88C2\uFF0C\u4E8E\u662F\u51B3\u5B9A\u4E0D\u518D\u8BA9\u6614\u65E5\u7684\u7231\u4EBA\u611F\u53D7\u5230\u81EA\u5DF1\u7684\u5FC3\u610F\uFF0C\u5C31\u4EB2\u624B\u6253\u9020\u4E86\u8FD9\u652F\u85CF\u5FC3\u7C2A\u3002\u9664\u4E86\u4E0A\u53E4\u795E\u5668\u5E7D\u7389\u6212\uFF0C\u85CF\u5FC3\u7C2A\u4E43\u662F\u552F\u4E00\u80FD\u591F\u85CF\u4F4F\u5FC3\u7EEA\u7684\u6CD5\u5668\u3002</p><p>\u5C0F\u5170\u82B1\u8DDF\u7740\u7ED3\u9ECE\u987A\u5229\u8D70\u4E86\u51FA\u53BB\uFF0C\u53EF\u5374\u9047\u5230\u4E86\u8776\u8863\u3002\u5C0F\u5170\u82B1\u8D76\u5FD9\u5C06\u9AA8\u5170\u4EA4\u7ED9\u7ED3\u9ECE\uFF0C\u5E76\u5C06\u5979\u62A4\u5728\u8EAB\u540E\u3002\u6B64\u65F6\uFF0C\u4E1C\u65B9\u9752\u82CD\u5FFD\u7136\u611F\u53D7\u5230\u4E86\u624B\u4E0A\u7684\u75BC\u75DB\uFF0C\u62C9\u5F00\u8896\u5B50\u53D1\u73B0\u4E86\u4E00\u9053\u65B0\u9C9C\u7684\u4F24\u53E3\u3002\u4ED6\u7684\u773C\u4E2D\u5347\u8D77\u4E00\u80A1\u6740\u6C14\uFF0C\u4F3C\u4E4E\u8981\u5C06\u6574\u4E2A\u4E09\u754C\u541E\u6CA1\u3002</p><p>\u547D\u7C3F\u4ECE\u5C0F\u5170\u82B1\u8EAB\u4E0A\u6389\u843D\uFF0C\u8776\u8863\u521A\u60F3\u6361\u8D77\uFF0C\u624B\u4E0A\u7684\u6CD5\u5668\u5FFD\u7136\u788E\u6210\u7070\u70EC\u3002\u957F\u73E9\u7A81\u7136\u51FA\u73B0\u5728\u5C0F\u5170\u82B1\u524D\u9762\uFF0C\u5C06\u5979\u6551\u8D70\u3002\u5F85\u5C0F\u5170\u82B1\u9192\u6765\uFF0C\u5C31\u770B\u5230\u957F\u73E9\u5750\u5728\u81EA\u5DF1\u8EAB\u65C1\u3002\u5979\u5DE6\u53F3\u770B\u770B\uFF0C\u53D1\u73B0\u81EA\u5DF1\u7ADF\u771F\u7684\u56DE\u5230\u4E86\u6C34\u4E91\u5929\u7684\u53F8\u547D\u6BBF\u3002\u5C0F\u5170\u82B1\u544A\u8BC9\u957F\u73E9\uFF0C\u4E1C\u65B9\u9752\u82CD\u4E4B\u6240\u4EE5\u5C06\u81EA\u5DF1\u5E26\u5230\u82CD\u76D0\u6D77\uFF0C\u4E3A\u7684\u5C31\u662F\u4FEE\u590D\u4E00\u679A\u547D\u7C3F\uFF0C\u53EF\u5979\u4E0D\u77E5\u8FD9\u547D\u7C3F\u4E4B\u4E2D\u6709\u4F55\u7384\u673A\u3002\u957F\u73E9\u5766\u8A00\u81EA\u5DF1\u4E5F\u4E0D\u6E05\u695A\uFF0C\u53EA\u6709\u4FEE\u597D\u4E86\u547D\u7C3F\u624D\u80FD\u77E5\u6653\u4E00\u4E8C\u3002\u5C0F\u5170\u82B1\u987E\u4E0D\u4E0A\u4F11\u606F\uFF0C\u8D76\u5FD9\u5F00\u59CB\u4FEE\u547D\u7C3F\u3002</p><p>\u957F\u73E9\u8D70\u51FA\u53F8\u547D\u6BBF\uFF0C\u8138\u4E0A\u6D6E\u73B0\u51FA\u4E00\u62B9\u8010\u4EBA\u5BFB\u5473\u7684\u7B11\uFF0C\u968F\u540E\u98DE\u51FA\u7ED3\u754C\u6765\u5230\u6D77\u5E02\u3002\u539F\u6765\u8FD9\u4E2A\u957F\u73E9\u662F\u5BB9\u660A\u5047\u626E\uFF0C\u4E3A\u7684\u5C31\u662F\u5C0F\u5170\u82B1\u80FD\u591F\u5FC3\u7518\u60C5\u613F\u4FEE\u597D\u547D\u7C3F\u3002\u968F\u540E\uFF0C\u4E1C\u65B9\u9752\u82CD\u8D76\u5230\u6D77\u5E02\u3002\u5BB9\u660A\u62C5\u5FC3\u4E8B\u6709\u53D8\u5316\uFF0C\u8D76\u7D27\u53D8\u6210\u957F\u73E9\u56DE\u5230\u5E7B\u5883\u4E4B\u4E2D\u3002\u53EA\u4E00\u4F1A\u513F\uFF0C\u4E1C\u65B9\u9752\u82CD\u5C31\u8FDB\u5165\u4E86\u5E7B\u5883\u3002\u53EA\u662F\u8FD9\u5E7B\u5883\u6709\u592A\u591A\u5C42\uFF0C\u6BCF\u4E00\u5C42\u90FD\u6709\u4E00\u4E2A\u5C0F\u5170\u82B1\u3002\u82E5\u60F3\u51B2\u7834\u5E7B\u5883\uFF0C\u53EA\u80FD\u627E\u5230\u771F\u6B63\u7684\u5C0F\u5170\u82B1\u3002</p><p>\u5C0F\u5170\u82B1\u4FEE\u597D\u547D\u7C3F\uFF0C\u53D1\u73B0\u4E0A\u9762\u6709\u4E00\u4E2A\u51E1\u4EBA\u7684\u540D\u5B57\u2014\u2014\u8C22\u60CB\u537F\u3002\u5979\u5FFD\u7136\u60F3\u5230\u4E9B\u4EC0\u4E48\uFF0C\u795E\u8272\u53D8\u5F97\u614C\u5F20\u8D77\u6765\u3002\u5F97\u77E5\u547D\u7C3F\u5DF2\u7ECF\u4FEE\u597D\uFF0C\u957F\u73E9\u8D76\u5FD9\u5411\u5C0F\u5170\u82B1\u8BE2\u8981\u547D\u7C3F\u3002\u53EF\u5C0F\u5170\u82B1\u5374\u5E76\u4E0D\u7740\u6025\uFF0C\u800C\u662F\u89E3\u91CA\u8BF4\u81EA\u5DF1\u5F53\u65F6\u88AB\u4E1C\u65B9\u9752\u82CD\u5173\u4E86\u8D77\u6765\uFF0C\u6240\u4EE5\u624D\u6CA1\u6709\u56DE\u590D\u4ED6\uFF0C\u8FD8\u95EE\u4ED6\u662F\u5426\u751F\u81EA\u5DF1\u7684\u6C14\u3002\u957F\u73E9\u4F3C\u4E4E\u5E76\u4E0D\u5728\u610F\u8FD9\u4EF6\u4E8B\u60C5\uFF0C\u76F4\u8A00\u81EA\u5DF1\u5E76\u4E0D\u751F\u6C14\uFF0C\u53EA\u662F\u62C5\u5FC3\u3002\u5C0F\u5170\u82B1\u5FFD\u7136\u660E\u767D\uFF0C\u6B64\u65F6\u7684\u957F\u73E9\u662F\u5047\u626E\u7684\uFF0C\u8D76\u5FD9\u8D77\u8EAB\u51C6\u5907\u8DD1\u3002\u89C1\u8EAB\u4EFD\u8D25\u9732\uFF0C\u5047\u957F\u73E9\u4F7F\u51FA\u795F\u6C14\uFF0C\u56F0\u4F4F\u4E86\u5C0F\u5170\u82B1\u3002</p><p>\u89C1\u5C0F\u5170\u82B1\u6709\u5371\u9669\uFF0C\u4E1C\u65B9\u9752\u82CD\u52A0\u5FEB\u4E86\u901F\u5EA6\uFF0C\u5728\u6D77\u5E02\u4E3B\u9003\u8DD1\u7684\u524D\u4E00\u79D2\u51B2\u7834\u4E86\u5E7B\u5883\u3002\u5C0F\u5170\u82B1\u8D81\u6D77\u5E02\u4E3B\u4E0D\u6CE8\u610F\uFF0C\u8D76\u7D27\u5C06\u547D\u7C3F\u53EC\u4E86\u8FC7\u6765\u3002\u4E1C\u65B9\u9752\u82CD\u60F3\u8981\u63ED\u5F00\u6D77\u5E02\u4E3B\u7684\u9762\u5177\uFF0C\u4E0D\u6599\u8776\u8863\u7A81\u7136\u8D76\u6765\uFF0C\u4E8C\u8BDD\u4E0D\u8BF4\u5C31\u8981\u5BF9\u5C0F\u5170\u82B1\u52A8\u624B\u3002\u4E1C\u65B9\u9752\u82CD\u54EA\u91CC\u8FD8\u987E\u5F97\u4E0A\u4EC0\u4E48\u9762\u5177\uFF0C\u8D76\u5FD9\u8D77\u8EAB\u66FF\u5C0F\u5170\u82B1\u6321\u4E0B\u653B\u51FB\u3002</p><p>\u5C0F\u5170\u82B1\u6DF1\u77E5\u81EA\u5DF1\u6709\u9519\u5728\u5148\uFF0C\u5C0F\u5FC3\u7FFC\u7FFC\u8DDF\u5728\u4E1C\u65B9\u9752\u82CD\u8EAB\u540E\u3002\u5230\u4E86\u5316\u9B42\u589F\uFF0C\u4E1C\u65B9\u9752\u82CD\u7A81\u7136\u8F6C\u8FC7\u8EAB\u634F\u4F4F\u5C0F\u5170\u82B1\u7684\u80A9\u8180\uFF0C\u6C14\u6025\u8D25\u574F\u8D28\u95EE\u5979\u4E3A\u4F55\u4E0D\u542C\u81EA\u5DF1\u7684\u8BDD\u79C1\u81EA\u4E71\u8DD1\u3002\u867D\u7136\u9762\u524D\u7684\u5927\u9B54\u5934\u5F88\u51F6\uFF0C\u53EF\u662F\u5C0F\u5170\u82B1\u80FD\u591F\u611F\u53D7\u5230\u4ED6\u5BF9\u81EA\u5DF1\u7684\u62C5\u5FC3\uFF0C\u4FBF\u4E56\u4E56\u70B9\u5934\u7B54\u5E94\u81EA\u5DF1\u4E0D\u4F1A\u518D\u4E71\u8DD1\u3002\u4E1C\u65B9\u9752\u82CD\u8FD9\u624D\u7F13\u7F13\u677E\u5F00\u624B\uFF0C\u968F\u540E\u4E00\u63E1\u62F3\u5C06\u5316\u9B42\u9F0E\u53D8\u6210\u7070\u70EC\u3002\u4E4C\u4E91\u548C\u8FF7\u96FE\u77AC\u95F4\u6563\u53BB\uFF0C\u5468\u56F4\u5E03\u6EE1\u4E86\u7EDA\u4E3D\u6E29\u6696\u7684\u5149\u3002</p><p>\u65E0\u5948\u5C0F\u5170\u82B1\u5FFD\u7136\u6655\u5012\uFF0C\u8EAB\u4F53\u5341\u5206\u865A\u5F31\uFF0C\u4E1C\u65B9\u9752\u82CD\u53EA\u597D\u5E26\u5979\u5230\u6797\u5B50\u91CC\u4F11\u606F\u3002\u4E1C\u65B9\u9752\u82CD\u8BA9\u5C0F\u5170\u82B1\u53D6\u4E0B\u85CF\u5FC3\u7C2A\uFF0C\u53EF\u5BF9\u65B9\u5374\u6B7B\u6D3B\u4E0D\u80AF\u3002\u5C0F\u5170\u82B1\u544A\u8BC9\u4E1C\u65B9\u9752\u82CD\uFF0C\u81EA\u5DF1\u4E0D\u60F3\u88AB\u4ED6\u7AA5\u63A2\u5FC3\u4E8B\u3002\u8BF4\u7740\uFF0C\u4E24\u4EBA\u4FBF\u4E89\u6267\u8D77\u6765\u3002</p><p>\u8776\u8863\u8DCC\u8DCC\u649E\u649E\u5C06\u5BB9\u660A\u5E26\u5230\u4E00\u4E2A\u5C71\u6D1E\u4E2D\uFF0C\u51F6\u795E\u592A\u5C81\u5F97\u77E5\u662F\u4E1C\u65B9\u9752\u82CD\u4F24\u4E86\u4ED6\uFF0C\u89C9\u5F97\u4E8B\u60C5\u5F88\u662F\u6709\u8DA3\u3002\u4E3A\u4E86\u7B54\u8C22\u592A\u5C81\u7684\u6551\u547D\u4E4B\u6069\uFF0C\u5BB9\u660A\u627F\u8BFA\uFF0C\u5B9A\u5C06\u795E\u5973\u606F\u82B8\u5E26\u6765\u52A9\u4ED6\u7834\u754C\u51FA\u5173\u3002</p>',14),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","15.html.vue"]]);export{h as default};
