import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as t,f as r}from"./app.33477645.js";const n={},s=r('<h1 id="\u6C38\u5609\u4E4B\u4E71" tabindex="-1"><a class="header-anchor" href="#\u6C38\u5609\u4E4B\u4E71" aria-hidden="true">#</a> \u6C38\u5609\u4E4B\u4E71</h1><blockquote><p>\u8BF8\u540D\u58EB\u76F8\u4E0E\u767B\u65B0\u4EAD\u6E38\u5BB4\uFF0C\u5468\u9857\u4E2D\u5750\u53F9\u66F0\uFF1A\u201C\u98CE\u666F\u4E0D\u6B8A\uFF0C\u4E3E\u76EE\u6709\u6C5F\u6CB3\u4E4B\u5F02\uFF01\u201D\u56E0\u76F8\u89C6\u6D41\u6D95\u3002\u738B\u5BFC\u6100\u7136\u53D8\u8272\u66F0\uFF1A\u201C\u5F53\u5171\u622E\u529B\u738B\u5BA4\uFF0C\u514B\u590D\u795E\u5DDE\uFF0C\u4F55\u81F3\u4F5C\u695A\u56DA\u5BF9\u6CE3\u90AA\uFF01\u201D\u4F17\u7686\u6536\u6CEA\u8C22\u4E4B\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274 \u2022 \u5377\u516B\u5341\u4E03 \u2022 \u664B\u7EAA\u4E5D\u300B</strong></p><p>\u8FD9\u4E2A\u573A\u666F\uFF0C\u662F\u4E2D\u56FD\u5386\u53F2\u4E0A\u7684\u7ECF\u5178\u4E00\u5E55\uFF1A</p><p>\u6C38\u5609\u5E74\u95F4\uFF0C\u4E2D\u539F\u4E27\u4E71\uFF0C\u540D\u58EB\u5357\u6C34\u6E21\uFF0C\u5BC4\u5BD3\u4E8E\u6C5F\u4E1C\u3002</p><p>\u540D\u58EB\u4EEC\u95F2\u7740\u6CA1\u4E8B\uFF0C\u7ECF\u5E38\u76F8\u7EA6\u51FA\u95E8\u6E38\u5BB4\u3002\u67D0\u6B21\u5BB4\u4F1A\u4E2D\uFF0C\u5468\u9857\u611F\u6168\u8BF4\uFF0C\u98CE\u666F\u4E00\u5982\u6628\u65E5\uFF0C\u53EA\u662F\u6C5F\u5C71\u4E0D\u5728\u554A\uFF01</p><p>\u4F17\u4EBA\u90FD\u88AB\u52FE\u8D77\u4E86\u201C\u56FD\u7834\u5C71\u6CB3\u5728\u201D\u7684\u4F24\u611F\uFF0C\u76F8\u5BF9\u6D41\u6D95\u3002</p><p>\u6B64\u65F6\uFF0C\u5728\u5EA7\u7684<code>\u738B\u5BFC</code>\u6100\u7136\u53D8\u8272\uFF0C\u5389\u58F0\u8BF4\uFF0C\u8FD9\u79CD\u65F6\u5019\uFF0C\u66F4\u5E94\u8BE5\u5171\u540C\u5408\u529B\u6548\u5FE0\u671D\u5EF7\uFF0C\u5149\u590D\u6CB3\u5C71\uFF01\u600E\u4E48\u53EF\u4EE5\u76F8\u5BF9\u54ED\u6CE3\uFF0C\u5982\u540C\u4EA1\u56FD\u5974\u4E00\u6837\uFF01</p><p>\u8FD9\u5C31\u662F\u5386\u53F2\u4E0A\u6709\u540D\u7684\u201C<strong>\u65B0\u4EAD\u5BF9\u6CE3</strong>\u201D\u5178\u6545\u3002</p><p>\u53EA\u662F\u6545\u4E8B\u867D\u597D\uFF0C\u5F80\u91CC\u518D\u6DF1\u7A76\u4E00\u5C42\uFF0C\u5185\u56CA\u5C31\u8FDC\u6CA1\u6709\u8868\u9762\u770B\u4E0A\u53BB\u7684\u90A3\u4E48\u4F1F\u5149\u6B63\u4E86\uFF1A</p><blockquote><p>\u201C\u514B\u590D\u795E\u5DDE\u201D\uFF0C\u5230\u5E95\u505A\u4E86\u6CA1\u6709\uFF1F</p></blockquote><p>\u6211\u4EEC\u5148\u6765\u770B\u53F2\u6599\u4E2D\u7684\u4E24\u5219\u8BB0\u8F7D\u3002</p><p>\u5EFA\u5174\u5143\u5E74\uFF08\u516C\u5143313\u5E74\uFF09\uFF0C\u88AB\u5308\u5974\u4EBA\u4FD8\u864F\u7684\u897F\u664B\u7B2C\u4E09\u4F4D\u7687\u5E1D\u2014\u2014\u664B\u6000\u5E1D\u88AB\u6740\uFF0C\u8EAB\u5728\u957F\u5B89\u7684\u4EB2\u738B\u53F8\u9A6C\u90BA\u968F\u5373\u7EE7\u4F4D\uFF0C\u662F\u4E3A\u664B\u610D\u5E1D\u3002</p><p>\u6B64\u65F6\uFF0C\u897F\u664B\u5728\u5317\u65B9\u7684\u52BF\u529B\u5DF2\u7ECF\u652F\u79BB\u7834\u788E\uFF0C\u671D\u5EF7\u56E0\u6B64\u5F88\u662F\u6307\u671B\u5DF2\u7ECF\u5728\u5EFA\u4E1A\u5446\u4E86\u4E94\u5E74\u7684\u7405\u90AA\u738B<code>\u53F8\u9A6C\u777F</code>\u3002\u88AB\u56F0\u5728\u957F\u5B89\u5B64\u57CE\u7684\u615C\u5E1D\u4E13\u95E8\u6D3E\u4F7F\u8005\u81F3\u5EFA\u4E1A\uFF0C\u5E0C\u671B\u53F8\u9A6C\u777F\u80FD\u51FA\u5175\u5317\u4F10\uFF0C\u652F\u63F4\u671D\u5EF7\u3002</p><p>\u4F46\u53F8\u9A6C\u777F\u5374\u8F9E\u4EE5\u6C5F\u4E1C\u521D\u5B9A\uFF0C\u65E0\u529B\u5317\u4E0A\u3002</p><p>\u5F53\u7136\uFF0C\u7687\u5E1D\u7684\u547D\u4EE4\u4E5F\u4E0D\u597D\u62D2\u7EDD\u5F97\u8FD9\u4E48\u76F4\u767D\uFF0C\u6001\u5EA6\u8FD8\u662F\u8981\u505A\u4E00\u4E0B\u7684\u3002\u53F8\u9A6C\u777F\u4E8E\u662F\u4E5F\u6D3E\u4E86\u4E00\u4E2A\u4EBA\u51FA\u53BB\u5317\u4F10\u3002\u8FD9\u4E2A\u4EBA\uFF0C\u5C31\u662F<code>\u7956\u9016</code>\u3002</p><p>\u6C38\u5609\u5E74\u95F4\uFF0C\u7956\u9016\u5373\u5DF2\u8FC7\u6C5F\uFF0C\u4E4B\u540E\u4E00\u76F4\u5E0C\u671B\u5317\u4F10\uFF0C\u4F46\u82E6\u4E8E\u5B9E\u529B\u4E0D\u6D4E\u3002\u8FD9\u6B21\uFF0C\u53F8\u9A6C\u777F\u7EC8\u4E8E\u7ED9\u4E86\u7956\u9016\u673A\u4F1A\uFF0C\u8BA9\u4ED6\u80FD\u591F\u540D\u6B63\u8A00\u987A\u7684\u6325\u5175\u5317\u4E0A\u3002</p><p><strong>\u4F46\u7956\u9016\u5E26\u51FA\u53BB\u5317\u4F10\u7684\u519B\u961F\u6709\u591A\u5C11\u5462\uFF1F</strong></p><p>\u53F2\u6599\u8BB0\u8F7D\uFF0C\u201C\u4EE5\u9016\u4E3A\u594B\u5A01\u5C06\u519B\u3001\u8C6B\u5DDE\u523A\u53F2\uFF0C<strong>\u7ED9\u5343\u4EBA\u5EEA\uFF0C\u5E03\u4E09\u5343\u5339\uFF0C\u4E0D\u7ED9\u94E0\u4ED7\uFF0C\u4F7F\u81EA\u53EC\u52DF</strong>\u3002\u9016\u5C06\u5176\u90E8\u66F2\u767E\u9980\u5BB6\u6E21\u6C5F\u201D\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5728\u653F\u7B56\u65B9\u9762\uFF0C\u7956\u9016\u80FD\u5F97\u5230\u7684\u652F\u6301\u662F\u8FD9\u6837\u7684\uFF1A\u53F8\u9A6C\u777F\u4EFB\u547D\u7956\u9016\u4E3A\u594B\u5A01\u5C06\u519B\u3001\u8C6B\u5DDE\u523A\u53F2\u2014\u2014\u8C6B\u5DDE\u65E9\u5DF2\u5931\u9677\uFF0C\u8FD9\u4E2A\u804C\u52A1\u5C31\u662F\u4E00\u5F20\u7A7A\u5934\u652F\u7968\u3002\u9664\u975E\u4F60\u6253\u5F97\u4E0B\u6765\uFF0C\u624D\u662F\u4F60\u7684\u3002</p><p>\u8D22\u653F\u65B9\u9762\u7684\u652F\u6301\uFF0C\u5219\u6709\u201C\u5343\u4EBA\u5EEA\uFF0C\u4E09\u5343\u5339\u5E03\u201D\u3002\u5E03\uFF0C\u5728\u5F53\u65F6\u662F\u8D27\u5E01\uFF1B\u5343\u4EBA\u5EEA\uFF0C\u5C31\u662F\u4E00\u5343\u4EBA\u7684\u53E3\u7CAE\u3002</p><p>\u7136\u540E\u5C31\u6CA1\u6709\u7136\u540E\u4E86\uFF0C\u7956\u9016\u5C31\u8FD9\u4E48\u5E26\u7740\u81EA\u5DF1\u7684\u4E00\u767E\u591A\u5BB6\u90E8\u66F2\uFF0C\u5317\u4F10\u53BB\u4E86\u3002</p><blockquote><p>\u518D\u6765\u770B\u7B2C\u4E8C\u5219\u53F2\u6599\uFF1A</p></blockquote><p>\u51E0\u5E74\u4EE5\u540E\uFF0C\u957F\u5B89\u5931\u9677\uFF0C\u664B\u610D\u5E1D\u88AB\u4FD8\u3002\u6D88\u606F\u4F20\u5230\u5EFA\u4E1A\u540E\uFF0C\u53F8\u9A6C\u777F\u7ACB\u5373\u51B3\u5B9A\u51FA\u5F81\u5317\u4F10\u3002<strong>\u4F46\u8FD9\u6B21\uFF0C\u53C8\u6CA1\u6709\u6210\u884C\uFF1A\u56E0\u4E3A\u6F15\u7CAE\u903E\u671F\u3002</strong></p><p>\u4E3A\u6B64\uFF0C\u53F8\u9A6C\u777F\u4E13\u95E8\u6740\u4E86\u7763\u7CAE\u7684\u5B98\u5458\u3002\u738B\u5BFC\u7B49\u4EBA\u4E5F\u56E0\u4E3A\u65E0\u6CD5\u51FA\u5F81\uFF0C\u7EB7\u7EB7\u4E0A\u4E66\u5F15\u548E\u8F9E\u804C\uFF0C\u53EA\u4E0D\u8FC7\u53F8\u9A6C\u777F\u5E76\u6CA1\u6709\u5E94\u5141\u4ED6\u4EEC\uFF0C\u53CD\u800C\u8BF4\u90FD\u662F\u56E0\u4E3A\u81EA\u5DF1\u4E3B\u653F\u5931\u5F53\u6240\u81F4\u3002</p><p>\u7529\u5F97\u4E00\u624B\u597D\u9505\u3002</p><p>\u8FD9\u756A\u5B9E\u9645\u884C\u4E3A\uFF0C\u8DDF\u738B\u5BFC\u5F53\u521D\u4E49\u6B63\u8A00\u8F9E\u5531\u7684\u9AD8\u8C03\uFF0C\u5DEE\u8DDD\u5B9E\u5728\u4E0D\u662F\u4E00\u822C\u7684\u5927\u3002</p><p>\u672C\u7BC7\uFF0C\u6211\u4EEC\u5C31\u6765\u56DE\u987E\u4E00\u4E0B\u6C5F\u4E1C\u5F53\u65F6\u7684\u73B0\u72B6\uFF0C\u770B\u770B\u738B\u5BFC\u8FD9\u4E48\u5F00\u5634\u70AE\uFF0C\u7A76\u7ADF\u662F\u57FA\u4E8E\u4E00\u79CD\u4EC0\u4E48\u6837\u7684\u8003\u8651\u3002</p><p>**\u7405\u740A\u738B\u53F8\u9A6C\u777F\u4E4B\u4E8E\u897F\u664B\u7687\u5BA4\uFF0C\u7B97\u662F\u758F\u5B97\u3002**\u4ED6\u7684\u7956\u7236\u53F8\u9A6C\u4F37\u662F\u53F8\u9A6C\u61FF\u7684\u513F\u5B50\uFF0C\u4E0E\u53F8\u9A6C\u5E08\u3001\u53F8\u9A6C\u662D\u662F\u5144\u5F1F\u3002\u4ED6\u4EEC\u8FD9\u4E00\u652F\uFF0C\u56E0\u4E3A\u5728\u671D\u4E2D\u51E0\u4E4E\u6CA1\u6709\u52BF\u529B\uFF0C\u6240\u4EE5\u4E4B\u524D\u516B\u738B\u4E4B\u4E71\u65F6\uFF0C\u8840\u7F18\u758F\u8FDC\u4E14\u540D\u58F0\u4E0D\u663E\u7684\u53F8\u9A6C\u777F\uFF0C\u51E0\u4E4E\u6CA1\u6709\u7275\u6D89\u5176\u4E2D\u2014\u2014\u4E5F\u5C31\u662F\u8BF4\uFF0C\u4ED6\u8FDE\u9171\u6CB9\u90FD\u6CA1\u5F97\u6253\u3002</p><p>\u4E0D\u8FC7\u547D\u8FD0\u5374\u5F88\u7737\u987E\u4ED6\uFF0C\u516B\u738B\u4E4B\u4E71\u7684\u6700\u7EC8\u83B7\u80DC\u8005\u2014\u2014\u4E1C\u6D77\u738B\u53F8\u9A6C\u8D8A\u5728\u6267\u653F\u540E\uFF0C\u4EFB\u547D\u53F8\u9A6C\u777F\u4E3A\u5B89\u4E1C\u5C06\u519B\u3001\u9A7B\u5EFA\u4E1A\uFF0C\u7B97\u662F\u7ED9\u4E86\u53F8\u9A6C\u777F\u767B\u4E0A\u4E71\u4E16\u821E\u53F0\u7684\u673A\u4F1A\u3002</p><p><strong>\u53F8\u9A6C\u777F\u5230\u5EFA\u4E1A\u4EE5\u540E\uFF0C\u4EBA\u5FAE\u8A00\u8F7B\uFF0C\u5E76\u4E0D\u88AB\u6C5F\u5357\u58EB\u4EBA\u6240\u770B\u91CD\u3002\u5802\u5802\u4E00\u4E2A\u738B\u7237</strong>\uFF0C\u5728\u5EFA\u4E1A\u5F85\u4E86\u597D\u4E45\uFF0C\u7ADF\u7136\u90FD\u6CA1\u6709\u540D\u58EB\u6765\u767B\u95E8\u62DC\u8BBF\uFF08\u5F53\u7136\uFF0C\u7ECF\u8FC7\u516B\u738B\u4E4B\u4E71\uFF0C\u897F\u664B\u5B97\u5BA4\u7684\u540D\u58F0\u5DF2\u7ECF\u81ED\u4E86\u5927\u8857\uFF0C\u8FD9\u4E5F\u662F\u4E0D\u53EF\u5FFD\u89C6\u7684\u56E0\u7D20\uFF09\u3002</p><p>\u8FD8\u662F\u738B\u5BFC\u7ED9\u53F8\u9A6C\u777F\u51FA\u4E86\u4E2A\u4E3B\u610F\u3002\u67D0\u6B21\u51FA\u6E38\uFF0C\u53F8\u9A6C\u777F\u4E58\u80A9\u8206\uFF0C\u738B\u5BFC\u7B49\u4EBA\u9A91\u9A6C\u8DDF\u968F\u3002\u738B\u5BFC\u4E43\u4E2D\u539F\u7B2C\u4E00\u7B49\u7684\u540D\u58EB\uFF0C\u6C5F\u5357\u540D\u58EB\u89C1\u5230\u8FD9\u6837\u7684\u573A\u9762\uFF0C\u5927\u4E3A\u60CA\u5F02\uFF0C\u65E0\u4E0D\u62DC\u670D\u3002</p><p>\u6B64\u540E\uFF0C**\u6C5F\u5357\u540D\u58EB<code>\u987E\u8363</code>\u3001<code>\u8D3A\u5FAA</code>**\u7B49\u4E5F\u8BA4\u53EF\u4E86\u53F8\u9A6C\u777F\u5728\u6C5F\u4E1C\u7684\u5B58\u5728\uFF0C\u76F8\u7EE7\u8FDB\u5165\u53F8\u9A6C\u777F\u7684\u5B89\u4E1C\u5C06\u519B\u5E9C\u3002</p><blockquote><p><strong>\u987E\u8363\u8FD9\u4E2A\u4EBA\uFF0C\u9700\u8981\u5355\u72EC\u7740\u91CD\u8BF4\u4E00\u4E0B\uFF1A</strong></p></blockquote><p>\u987E\u8363\u51FA\u81EA\u5434\u90E1\u987E\u6C0F\uFF0C\u5176\u7956\u7236\u662F\u5B59\u6743\u65F6\u671F\u7684\u4E1E\u76F8\u987E\u96CD\u3002\u5434\u4EA1\u540E\uFF0C\u987E\u8363\u5317\u4E0A\uFF0C\u5165\u671D\u4F9B\u804C\uFF0C\u51E0\u4E4E\u5B8C\u6574\u7ECF\u5386\u4E86\u516B\u738B\u4E4B\u4E71\u521D\u671F\u7684\u5404\u6B21\u653F\u53D8\u3002\u597D\u5728\u987E\u8363\u8FD0\u6C14\u4E0D\u9519\uFF0C\u4FA5\u5E78\u6D3B\u547D\uFF0C\u540E\u6765\u8F9E\u5B98\u56DE\u4E61\u3002</p><p>\u56DE\u5230\u6C5F\u4E1C\u540E\uFF0C\u987E\u8363\u53C8\u7ECF\u5386\u4E86<strong>\u9648\u654F\u4E4B\u4E71</strong>\u3002</p><p>\u9648\u654F\u672C\u6765\u662F\u671D\u5EF7\u5B98\u5458\uFF0C\u540E\u6765\u773C\u89C1\u5929\u4E0B\u5927\u4E71\uFF0C\u5FC3\u6000\u5F02\u5FD7\uFF0C\u5C31\u60F3\u5B66\u7740\u5B59\u6743\u5272\u636E\u6C5F\u4E1C\u3002\u4F46\u4ED6\u5E76\u4E0D\u88AB\u6C5F\u4E1C\u58EB\u65CF\u6240\u62E5\u62A4\uFF0C\u4E00\u65F6\u6000\u6068\u5728\u5FC3\uFF0C\u4FBF\u60F3\u4E00\u4E3E\u6740\u6389\u6C5F\u5357\u58EB\u65CF\uFF0C\u4EE5\u7EDD\u540E\u60A3\u3002</p><p>\u987E\u8363\u5728\u5F97\u77E5\u9648\u654F\u7684\u5FC3\u601D\u540E\uFF0C\u6DF1\u529D\u9648\u654F\uFF0C\u9648\u654F\u6700\u7EC8\u653E\u5F03\u3002\u4F46\u6C5F\u5357\u58EB\u65CF\u8DDF\u9648\u654F\u4E4B\u95F4\u7684\u6881\u5B50\u5374\u7B97\u662F\u7ED3\u4E0B\u4E86\u3002\u540E\u6765\uFF0C\u987E\u8363\u7B49\u58EB\u65CF\u8054\u5408\u8D77\u5175\u653B\u6253\u9648\u654F\uFF0C\u9648\u654F\u6700\u7EC8\u88AB\u6740\u3002<strong>\u987E\u8363\u7531\u4E8E\u671F\u95F4\u5C45\u529F\u751A\u4F1F\uFF0C\u9042\u6210\u58EB\u65CF\u9886\u8896\u3002</strong></p><p>\u800C\u6B63\u662F\u56E0\u4E3A\u9648\u654F\u4E4B\u4E71\u88AB\u5E73\u5B9A\u540E\uFF0C\u671D\u5EF7\u9700\u8981\u5B97\u5BA4\u9547\u5B88\u6C5F\u4E1C\uFF0C\u53F8\u9A6C\u777F\u624D\u5230\u4E86\u5EFA\u4E1A\u3002</p><p>\u4E1C\u6D77\u738B\u8D8A\u5728\u6267\u653F\u671F\u95F4\uFF0C\u66FE\u5F81\u53EC\u987E\u8363\u518D\u6B21\u51FA\u4ED5\u3002\u987E\u8363\u4E5F\u53BB\u4E86\uFF0C\u4F46\u8D70\u5230\u5F90\u5DDE\u65F6\uFF0C\u53D1\u73B0\u5C40\u52BF\u51F6\u9669\uFF0C\u5C31\u53C8\u679C\u65AD\u63A8\u8F9E\uFF0C\u518D\u6B21\u9000\u56DE\u5434\u90E1\u3002</p><p>\u800C\u987E\u8363\u7684\u5224\u65AD\u4E5F\u662F\u51C6\u786E\u7684\uFF0C\u4E0D\u4E45\u4EE5\u540E\uFF0C\u4E1C\u6D77\u738B\u8D8A\u75C5\u6B7B\uFF0C\u9001\u846C\u961F\u4F0D\u88AB\u77F3\u52D2\u5168\u6B7C\uFF0C\u968F\u884C\u7684\u516C\u537F\u51E0\u4E4E\u5168\u90E8\u88AB\u6740\u3002</p><p>\u987E\u8363\u6709\u7740\u8FD9\u6837\u7684\u7ECF\u5386\u548C\u5730\u4F4D\uFF0C\u5982\u4ECA\u613F\u610F\u5165\u5E55\uFF0C\u7B97\u662F\u610F\u5473\u7740\u6C5F\u4E1C\u7684\u58EB\u65CF\u5F00\u59CB\u9010\u6B65\u63A5\u7EB3\u53F8\u9A6C\u777F\u4E86\u3002</p><p><strong>\u800C\u8FD9\uFF0C\u5176\u5B9E\u4E5F\u662F\u4E00\u4E2A\u4E92\u5229\u4E92\u60E0\u7684\u5C40\u9762</strong>\uFF1A\u5F53\u65F6\u5929\u4E0B\u5927\u4E71\uFF0C\u5982\u9648\u654F\u8FD9\u822C\u5FC3\u6000\u5F02\u5FD7\u8005\u4E0D\u53EF\u80DC\u6570\uFF0C\u52A8\u8F84\u5E26\u6765\u7978\u4E71\u3002\u4E0E\u5176\u5982\u6B64\uFF0C\u6C5F\u4E1C\u58EB\u65CF\u8FD8\u4E0D\u5982\u60F3\u529E\u6CD5\u63A5\u7EB3\u4E00\u4E2A\u671D\u5EF7\u8BA4\u53EF\u7684\u52BF\u529B\uFF0C\u5927\u5BB6\u5F7C\u6B64\u5408\u4F5C\uFF0C\u7EF4\u6301\u4F4F\u76F8\u5BF9\u5B89\u5B9A\u7684\u5C40\u9762\u3002</p><p>\u8FD9\u624D\u662F\u6C5F\u5357\u58EB\u65CF\u5728\u89C1\u5230\u738B\u5BFC\u7B49\u4E2D\u539F\u5927\u65CF\u613F\u610F\u652F\u6301\u53F8\u9A6C\u777F\u540E\uFF0C\u80FD\u629B\u5F03\u6210\u89C1\uFF0C\u4E3B\u52A8\u4E0E\u53F8\u9A6C\u777F\u8FD9\u4E2A\u758F\u5B97\u63A5\u89E6\u7684\u771F\u6B63\u539F\u56E0\u3002</p><p>\u800C\u53F8\u9A6C\u777F\u4E5F\u786E\u5B9E\u662F\u4E00\u4E2A\u503C\u5F97\u4FE1\u8D56\u7684\u76DF\u53CB\u3002\u5728\u63A5\u6536\u5230\u6C5F\u4E1C\u58EB\u65CF\u7684\u793A\u597D\u4FE1\u53F7\u540E\uFF0C\u4ED6\u4E3B\u52A8\u505A\u51FA\u56DE\u5E94\uFF0C\u5927\u91CF\u5438\u7EB3\u672C\u5730\u58EB\u65CF\u8FDB\u5165\u5E55\u5E9C\u3002</p><p>\u540C\u65F6\uFF0C<strong>\u53F8\u9A6C\u777F\u7684\u5E55\u5E9C\u91CC\uFF0C\u8FD8\u6709\u5927\u91CF\u6E21\u6C5F\u5357\u6765\u7684<code>\u4E2D\u539F\u4EBA\u58EB</code></strong>\u3002</p><p>\u5982\u4ECA\u4F60\u8981\u662F\u5728\u4E0A\u6D77\u8BF4\u201C\u6C5F\u5317\u4EBA\u201D\uFF0C\u90A3\u7EDD\u5BF9\u662F\u6B67\u89C6\u3002\u4F46\u5728\u5F53\u65F6\uFF0C\u8FD9\u6761\u9119\u89C6\u94FE\u5374\u662F\u53CD\u7740\u7684\uFF0C\u662F\u4E2D\u539F\u58EB\u4EBA\u770B\u4E0D\u8D77\u6C5F\u4E1C\u571F\u8457\u3002\u800C\u53F8\u9A6C\u777F\u5C3D\u7BA1\u4E0E\u6C5F\u4E1C\u58EB\u65CF\u8FBE\u6210\u4E86\u5408\u4F5C\uFF0C\u5374\u4E5F\u5E76\u6CA1\u6709\u8981\u6539\u53D8\u8FD9\u79CD\u6B67\u89C6\u94FE\u7684\u5FC3\u601D\uFF0C\u56E0\u4E3A\u4ED6\u540C\u6837\u4E5F\u9700\u8981\u8FD9\u4E9B\u4E2D\u539F\u58EB\u65CF\u7684\u652F\u6301\u3002</p><p>\u8FD9\u4E9B\u5357\u6765\u7684\u4E2D\u539F\u58EB\u4EBA\uFF0C\u57FA\u672C\u4E0A\u90FD\u6709\u4E00\u4E2A\u5171\u540C\u7684\u7279\u70B9\uFF1A<strong>\u5728\u671D\u5EF7\u6709\u4EFB\u804C\u7ECF\u5386</strong>\u3002\u8FD9\u4E9B\u4EBA\u5728\u5357\u4E0B\u4EE5\u540E\uFF0C\u51E0\u4E4E\u88AB\u53F8\u9A6C\u777F\u5168\u90E8\u5438\u7EB3\u8FDB\u5165\u5E55\u5E9C\uFF0C\u4EE5\u81F3\u4E8E\u5F53\u65F6\u751A\u81F3\u6709\u201C\u767E\u516D\u63BE\u201D\u7684\u8BF4\u6CD5\u3002</p><p>\u6240\u8C13\u201C\u63BE\u201D\uFF0C\u662F\u6307\u5E55\u5E9C\u4E2D\u7684\u9AD8\u7EA7\u516C\u52A1\u4EBA\u5458\u3002\u4E4B\u524D\u4E1C\u6C49\u7684\u4E09\u516C\u5E9C\uFF0C\u5927\u7EA6\u5404\u6709\u4E09\u5341\u4EBA\u5DE6\u53F3\u7684\u201C\u63BE\u5C5E\u201D\u3002\u5982\u4ECA\u53F8\u9A6C\u777F\u4E00\u4E2A\u5C0F\u5C0F\u7684\u5B89\u4E1C\u5C06\u519B\u5E9C\uFF0C\u7ADF\u7136\u6709\u4E00\u767E\u591A\u540D\u63BE\u5C5E\uFF0C\u53EF\u89C1\u4ED6\u7A76\u7ADF\u5EF6\u63FD\u4E86\u591A\u5C11\u5357\u6765\u58EB\u65CF\u3002</p><p>\u800C\u8FD9\u4E5F\u662F\u6CA1\u6709\u529E\u6CD5\u7684\u4E8B\u3002\u5927\u5BB6\u5FC3\u5411\u671D\u5EF7\uFF0C\u5982\u4ECA\u5929\u4E0B\u5927\u4E71\uFF0C\u53EA\u6709\u6B64\u5730\u5C1A\u5C5E\u548C\u5E73\uFF0C\u81EA\u7136\u5C31\u90FD\u6765\u4E86\u3002\u800C\u6765\u90FD\u6765\u4E86\uFF0C\u53F8\u9A6C\u777F\u4E5F\u5C31\u6CA1\u6709\u62D2\u7EDD\u7684\u9053\u7406\u3002</p><p><strong>\u66F4\u4F55\u51B5\uFF0C\u53F8\u9A6C\u777F\u8FD8\u85CF\u4E86\u70B9\u5176\u4ED6\u7684\u5C0F\u5FC3\u601D\u3002</strong></p><p>\u6211\u4EEC\u5728\u4E4B\u524D\u7684\u6587\u7AE0\u4E2D\u66FE\u7ECF\u63D0\u8FC7\uFF0C\u51FA\u9547\u5E76\u5DDE\u7684<code>\u5218\u7428</code>\u4E0E\u7FAF\u65CF\u77F3\u52D2\u4EA4\u6218\uFF0C\u5931\u8D25\u4EE5\u540E\uFF0C\u6295\u5954\u5E7D\u5DDE\u7684\u9C9C\u5351\u6BB5\u5339\u78FE\u3002\u518D\u540E\u6765\uFF0C\u664B\u6000\u5E1D\u88AB\u4FD8\uFF0C<strong>\u5218\u7428\u66FE\u7ECF\u5411\u6C5F\u4E1C\u6D3E\u4F7F\u8005\u529D\u8FDB</strong>\u2014\u2014\u6240\u8C13\u529D\u8FDB\uFF0C\u5176\u5B9E\u5C31\u662F\u529D\u53F8\u9A6C\u777F\u5F53\u7687\u5E1D\u3002</p><p>\u5F7C\u65F6\u664B\u610D\u5E1D\u8FD8\u6D3B\u7740\uFF0C\u53F8\u9A6C\u777F\u5F53\u7136\u4E0D\u53EF\u80FD\u592A\u7334\u6025\uFF0C\u663E\u5F97\u5403\u76F8\u592A\u96BE\u770B\u3002\u4F46\u5728\u4ED6\u7684\u5185\u5FC3\u6DF1\u5904\uFF0C\u559C\u60A6\u4E00\u5B9A\u662F\u65E0\u6CD5\u6291\u5236\u7684\uFF0C\u591C\u6DF1\u4EBA\u9759\u7684\u65F6\u5019\uFF0C\u53EA\u6015\u60F3\u60F3\u90FD\u4F1A\u7B11\u51FA\u58F0\u6765\u3002</p><blockquote><p>\u4E00\u5B9A\u6709\u4EBA\u4F1A\u95EE\uFF1A\u5218\u7428\u5F53\u65F6\u6295\u5954\u6BB5\u5339\u78FE\u5BC4\u4EBA\u7BF1\u4E0B\uFF0C\u51E0\u4E4E\u5DF2\u7ECF\u6CA1\u6709\u5B9E\u529B\u53EF\u8A00\uFF0C\u4E3A\u4EC0\u4E48\u4ED6\u7684\u529D\u8FDB\u8FD8\u4F1A\u8BA9\u53F8\u9A6C\u777F\u8FD9\u4E48\u5F00\u5FC3\uFF1F</p></blockquote><p>\u8FD9\u662F\u56E0\u4E3A\u5218\u7428\u8FD8\u6709\u4E00\u4E2A\u8EAB\u4EFD\u5F88\u91CD\u8981\uFF0C<strong>\u4ED6\u662F\u5F53\u65F6\u671D\u5EF7\u7684\u4E09\u516C\u4E4B\u4E00\uFF0C\u53F8\u7A7A</strong>\u3002</p><p>\u5218\u7428\u7684\u6B64\u756A\u529D\u8FDB\uFF0C\u610F\u5473\u7740\u53F8\u9A6C\u777F\u5982\u679C\u8981\u79F0\u5E1D\uFF0C\u90A3\u4E48\u5DF2\u7ECF\u5F97\u5230\u4E86\u6765\u81EA\u4E8E\u4E09\u516C\u7684\u652F\u6301\u3002\u66F4\u4F55\u51B5\uFF0C\u8FD9\u4E2A\u53F8\u7A7A\u8FD8\u5E76\u4E0D\u662F\u4ED6\u81EA\u5DF1\u59D4\u4EFB\u7684\uFF0C\u662F\u4E4B\u524D\u7684\u671D\u5EF7\u6B63\u5F0F\u4EFB\u547D\u8FC7\u7684\u3002</p><p>\u6309\u7167\u60EF\u4F8B\uFF0C<strong>\u7687\u5E1D\u5982\u679C\u9A7E\u5D29\uFF0C\u4E09\u516C\u662F\u53EF\u4EE5\u51B3\u5B9A\u7687\u5E1D\u4EBA\u9009\u7684\u51B3\u7B56\u5C42\u4E4B\u4E00</strong>\u3002\u56E0\u6B64\uFF0C\u5218\u7428\u7684\u652F\u6301\uFF0C\u5BF9\u4E8E\u53F8\u9A6C\u777F\u5C06\u6765\u7684\u79F0\u5E1D\u6781\u4E3A\u91CD\u8981\uFF0C\u8FD9\u80FD\u7ED9\u4ED6\u589E\u52A0\u83AB\u5927\u7684\u5408\u6CD5\u6027\u3002</p><p>\u7B49\u800C\u4E0B\u4E4B\uFF0C<strong>\u4E09\u516C\u4EE5\u4E0B\u7684\u671D\u81E3\uFF0C\u4E5F\u662F\u53F8\u9A6C\u777F\u53D6\u5F97\u6B63\u7EDF\u6027\u7684\u91CD\u8981\u4FDD\u969C</strong>\u3002\u5982\u4ECA\u4EBA\u5BB6\u6B63\u597D\u7A77\u9014\u6765\u6295\uFF0C\u5F53\u7136\u90FD\u5F97\u63A5\u7EB3\u3002\u5B9E\u5728\u6CA1\u6709\u804C\u4F4D\u4E86\uFF0C\u90A3\u5C31\u5148\u5F00\u5DE5\u8D44\u517B\u7740\u5457\u3002</p><p>\u800C\u8FD9\u4E48\u591A\u95F2\u4EBA\u4E5F\u786E\u5B9E\u6CA1\u6709\u4EC0\u4E48\u4E8B\u60C5\u53EF\u4EE5\u505A\uFF0C\u5C31\u53EA\u80FD\u662F\u7ECF\u5E38\u7EA6\u5728\u4E00\u8D77\u559D\u559D\u9152\u53D1\u53D1\u7262\u9A9A\u4E86\u3002</p><p>\u81F3\u4E8E\u7262\u9A9A\u7684\u5185\u5BB9\uFF0C\u80AF\u5B9A\u90FD\u662F\u5BB6\u56FD\u5929\u4E0B\u4E00\u5366\u7684\uFF0C\u54C0\u53F9\u4E00\u4E0B\u795E\u5DDE\u677F\u8361\uFF0C\u4E2D\u539F\u9646\u6C89\uFF0C\u8D8A\u6FC0\u70C8\u8D8A\u60B2\u58EE\uFF0C\u5C31\u8D8A\u80FD\u6FC0\u8D77\u5927\u5BB6\u7684\u5171\u9E23\uFF0C\u540C\u65F6\u53C8\u662D\u793A\u4E86\u6B63\u786E\u7684\u4EF7\u503C\u89C2\uFF0C\u7B80\u76F4\u80FD\u4E3A\u8FD9\u987F\u9152\u5212\u4E0A\u4E00\u4E2A\u5706\u6EE1\u7684\u53E5\u53F7\u3002</p><p>\u5982\u6B64\uFF0C\u624D\u6709\u4E86\u6587\u4E2D\u5F00\u5934\u7684\u90A3\u4E00\u5E55\u3002</p><p>\u7ECF\u8FC7\u4E0A\u8FF0\u5206\u6790\uFF0C\u6211\u4EEC\u5927\u4F53\u80FD\u5BF9\u6C5F\u4E1C\u5F53\u65F6\u7684\u5C40\u52BF\u6709\u4E00\u4E2A\u4E86\u89E3\u3002\u66F4\u8FDB\u4E00\u6B65\u7684\uFF0C\u4E5F\u5C31\u4E0D\u96BE\u53D1\u73B0\uFF0C<strong>\u4E1C\u664B\u7684\u504F\u5B89\u867D\u7136\u8DDF\u4E00\u767E\u591A\u5E74\u524D\u7684\u4E1C\u5434\u60C5\u5F62\u7C7B\u4F3C\uFF0C\u4F46\u5B9E\u9645\u4E0A\u5374\u6709\u672C\u8D28\u4E0D\u540C</strong>\u3002</p><p>\u4E1C\u5434\u7684\u7586\u571F\uFF0C\u662F\u4E09\u4EE3\u4EBA\u52AA\u529B\u594B\u6597\u7684\u7ED3\u679C\u3002\u800C\u4E1C\u664B\u5728\u6C5F\u5DE6\u7684\u504F\u5B89\uFF0C\u5374\u66F4\u50CF\u662F\u4E00\u573A\u56E0\u7F18\u9645\u4F1A\uFF1A\u746F\u90AA\u738B\u53F8\u9A6C\u777F\u88AB\u6D3E\u5230\u4E86\u626C\u5DDE\uFF1B\u8DDF\u968F\u7740\u7684\u746F\u90AA\u738B\u6C0F\u4E4B\u4E2D\uFF0C\u6B63\u597D\u6709\u738B\u6566\u3001\u738B\u5BFC\u8FD9\u6837\u771F\u624D\u5B9E\u5B66\u7684\u540D\u58EB\uFF1B\u800C\u6C5F\u4E1C\u53C8\u8FDC\u5728\u4E2D\u539F\u6218\u4E71\u4E4B\u5916......\u51E1\u6B64\u79CD\u79CD\uFF0C\u624D\u9020\u5C31\u4E86\u4E1C\u664B\u671D\u5EF7\u7684\u5B58\u5728\u3002</p><p>\u8FD9\u5176\u4E2D\uFF0C<code>\u8FD0\u6C14</code>\u6210\u5206\u5176\u5B9E\u5360\u4E86\u5F88\u5927\u6BD4\u4F8B\u3002</p><p>\u53EA\u662F\u597D\u8FD0\u6C14\u4E4B\u4E0B\u4E5F\u6DF1\u85CF\u9690\u5FE7\u3002\u5BF9\u4E8E\u6C5F\u4E1C\u4E4B\u5730\u800C\u8A00\uFF0C\u7405\u90AA\u738B\u53F8\u9A6C\u777F\u59CB\u7EC8\u7B97\u662F\u4E00\u4E2A\u5916\u6765\u8005\uFF0C\u4E0D\u7136\u6C5F\u4E1C\u58EB\u4EBA\u4E00\u5F00\u59CB\u4E5F\u4E0D\u4F1A\u5982\u6B64\u6392\u65A5\u3002</p><p>\u8BF4\u5F97\u66F4\u901A\u4FD7\u4E00\u70B9\u5C31\u662F\uFF0C<strong>\u5730\u76D8\u4E0D\u662F\u6253\u4E0B\u6765\u7684\uFF0C\u56E0\u6B64\u5BF9\u539F\u6709\u653F\u6CBB\u52BF\u529B\u7684\u6574\u5408\uFF0C\u5C31\u76F8\u5BF9\u8981\u56F0\u96BE\u5F97\u591A</strong>\u3002</p><p>\u800C\u5F53\u5730\u58EB\u4EBA\u7684\u8FD9\u79CD\u4E0D\u5408\u4F5C\uFF0C\u8FD8\u53EA\u662F\u4E1C\u664B\u671D\u5EF7\u6240\u9762\u4E34\u7684\u7B2C\u4E00\u5927\u56F0\u96BE\u3002</p><p>\u4E1C\u664B\u671D\u5EF7\u9762\u4E34\u7684\u7B2C\u4E8C\u4E2A\u95EE\u9898\uFF0C<strong>\u662F\u90A3\u4E9B\u5357\u4E0B\u7684\u58EB\u5927\u592B\u4EEC\u8981\u600E\u4E48\u529E\uFF1F</strong></p><p>\u57FA\u4E8E\u5404\u79CD\u4E3B\u89C2\u548C\u5BA2\u89C2\u7684\u539F\u56E0\uFF0C\u53F8\u9A6C\u777F\u6536\u5BB9\u4E86\u5927\u91CF\u6765\u81EA\u4E8E\u5317\u65B9\u7684\u58EB\u65CF\u3002\u4F46\u8FD9\u4E9B\u4EBA\u5357\u6E21\u4EE5\u540E\uFF0C\u5982\u4F55\u6536\u5F52\u5DF1\u7528\uFF0C\u53C8\u662F\u4E00\u4E2A\u95EE\u9898\u3002</p><p><strong>\u5927\u5BB6\u5404\u6709\u5404\u7684\u60F3\u6CD5\uFF0C\u5404\u6709\u5404\u7684\u5FC3\u601D\u3002</strong></p><p>\u56E0\u6B64\uFF0C\u6211\u4EEC\u624D\u4F1A\u8BF4\uFF1A</p><p><strong>\u4ECE\u8868\u9762\u4E0A\u770B\uFF0C\u4E1C\u664B\u7684\u5F00\u56FD\u4E0E\u4E1C\u5434\u76F8\u4F3C\uFF0C\u4F46\u5B9E\u9645\u4E0A\uFF0C\u4E8C\u8005\u7EDD\u5BF9\u4E0D\u540C\u3002</strong></p><p>\u4E1C\u5434\u5728\u7ACB\u56FD\u7684\u51E0\u5341\u5E74\u4E2D\uFF0C\u66F4\u591A\u65F6\u5019\u89C6\u91CE\u662F\u5411\u5916\u7684\uFF0C\u4E0D\u4EC5\u6253\u8D62\u8FC7\u8D64\u58C1\u4E4B\u6218\uFF0C\u5B59\u6743\u8FD8\u66FE\u56DB\u6B21\u4EB2\u5F81\u5408\u80A5\uFF0C\u8BF8\u845B\u606A\u4E5F\u66FE\u7387\u4F17\u5317\u4F10\u3002</p><p>\u4F46\u4E1C\u664B\u5374\u4E0D\u540C\uFF0C\u7EDD\u5927\u591A\u6570\u65F6\u5019\uFF0C\u5B83\u7684\u5185\u90E8\u77DB\u76FE\u90FD\u8981\u8FDC\u8FDC\u5927\u4E8E\u5916\u90E8\u538B\u529B\u3002\u56E0\u6B64\uFF0C\u671D\u653F\u7684\u7740\u773C\u70B9\u4E5F\u4E3B\u8981\u662F\u5411\u5185\u7684\uFF0C\u9664\u4E86\u540E\u671F\u7684\u4E24\u4E2A\u6743\u81E3\u2014\u2014\u6853\u6E29\u548C\u5218\u88D5\uFF0C\u4E1C\u664B\u57FA\u672C\u4E0A\u6CA1\u6709\u4E3B\u52A8\u671D\u5317\u65B9\u7528\u8FC7\u5175\u3002</p><p>\u800C\u8FD9\uFF0C\u53C8\u5F15\u51FA\u4E86\u66F4\u5927\u7684\u7591\u95EE\uFF1A\u65E2\u7136\u4E1C\u664B\u6CA1\u6709\u80FD\u529B\u5317\u4F10\uFF0C\u90A3\u738B\u5BFC\u4E3A\u4F55\u8FD8\u8981\u65F6\u65F6\u523B\u523B\u53E3\u53E3\u58F0\u58F0\u628A\u201C\u514B\u590D\u795E\u5DDE\u201D\u6302\u5728\u5634\u4E0A\u5462\uFF1F</p><p>\u5BF9\u6B64\uFF0C\u5728\u4E0B\u7684\u7406\u89E3\u662F\uFF1A</p><p><strong>\u5317\u4F10\u4E0D\u662F\u76EE\u6807\uFF0C\u662F<code>\u613F\u666F</code>\u3002</strong></p><p>\u800C\u4E14\uFF0C\u662F\u90A3\u79CD\u53EF\u671B\u5374\u4E0D\u53EF\u53CA\uFF0C\u6C38\u8FDC\u90FD\u770B\u5F97\u89C1\u6478\u4E0D\u7740\u7684\u613F\u666F\u3002</p><p>\u6709\u4EBA\u6216\u8BB8\u4F1A\u55E4\u4E4B\u4EE5\u9F3B\uFF0C\u8FD9\u79CD\u7C7B\u4F3C\u4E8E\u7A7A\u8BDD\u7684\u613F\u666F\uFF0C\u6811\u5728\u90A3\u91CC\u8FD8\u6709\u4EC0\u4E48\u610F\u4E49\uFF1F</p><p>\u6709\uFF0C\u5F53\u7136\u6709\u3002</p><p>\u5373\u4FBF\u6240\u6709\u4EBA\u90FD\u77E5\u9053\u5317\u4F10\u662F\u53E5\u7A7A\u8BDD\uFF0C\u4F46\u201C\u514B\u590D\u795E\u5DDE\u201D\u4F9D\u7136\u6709\u5B83\u5B58\u5728\u7684\u610F\u4E49\uFF1A<strong>\u53EA\u6709\u901A\u8FC7\u8FD9\u4E2A\u9065\u8FDC\u7684\u76EE\u6807\uFF0C\u624D\u80FD\u628A\u4E1C\u664B\u652F\u79BB\u7834\u788E\u7684\u5404\u65B9\u52BF\u529B\u6574\u5408\u8D77\u6765\u3002</strong></p><p><strong>\u4EFB\u4F55\u5DE5\u4F5C\uFF0C\u90FD\u9700\u8981\u88AB\u8D4B\u4E88\u610F\u4E49\u3002</strong></p><p>\u738B\u5BFC\u53E3\u53E3\u58F0\u58F0\u8BF4\u8981\u201C\u514B\u590D\u795E\u5DDE\u201D\uFF0C\u4F46\u5B9E\u9645\u4E0A\u5927\u5BB6\u90FD\u77E5\u9053\uFF0C\u4E1C\u664B\u7684\u57FA\u56E0\u51B3\u5B9A\u4E86\u5B83\u53EA\u80FD\u662F\u4E00\u4E2A\u504F\u5B89\u6C5F\u5DE6\u7684\u738B\u671D\uFF0C\u65E2\u6CA1\u6709\u80FD\u529B\uFF0C\u4E5F\u6CA1\u6709\u5B9E\u529B\u53BB\u5317\u4F10\u3002</p><p>\u201C\u514B\u590D\u795E\u5DDE\u201D\u4ECE\u4E00\u5F00\u59CB\uFF0C\u5C31\u53EA\u662F\u4E00\u4E2A\u53E3\u53F7\u3002</p><p>\u4F46\u8FD9\u4E2A\u53E3\u53F7\u4F9D\u7136\u5F88\u91CD\u8981\uFF0C<strong>\u5B83\u63D0\u4F9B\u4E86\u738B\u671D\u7EDF\u6CBB\u7684\u5408\u6CD5\u6027\uFF0C\u4E3A\u5404\u7EA7\u5B98\u5458\u5851\u9020\u7740\u6B63\u786E\u7684\u4EF7\u503C\u89C2</strong>\u3002\u540C\u65F6\uFF0C\u5B83\u8FD8\u80FD\u5C3D\u53EF\u80FD\u5730\u628A\u66F4\u591A\u6C49\u4EBA\u52BF\u529B\u56E2\u7ED3\u5230\u81EA\u5DF1\u9EBE\u4E0B\u3002</p><p>\u8FD9\u5C31\u662F\u8FD9\u4E2A\u53E3\u53F7\u7684\u610F\u4E49\u3002</p><p>\u7C7B\u4F3C\u7684\u60C5\u5F62\uFF0C\u4E00\u5343\u5E74\u4EE5\u540E\u4E5F\u6709\u8FC7\u3002\u5357\u5B8B\u5E74\u95F4\uFF0C\u81EA\u4ECE\u201C\u7ECD\u5174\u548C\u8BAE\u201D\u4EE5\u540E\uFF0C\u5357\u5B8B\u671D\u5EF7\u4ECE\u4E0A\u5230\u4E0B\u90FD\u77E5\u9053\uFF0C\u518D\u6B21\u7EDF\u6CBB\u5317\u65B9\u5DF2\u7ECF\u4E0D\u53EF\u80FD\u3002</p><p>\u4F46\u662F\uFF0C\u5B8B\u9AD8\u5B97\u4F9D\u7136\u628A\u676D\u5DDE\u4F5C\u4E3A\u201C\u884C\u5728\u201D\uFF0C\u800C\u975E\u201C\u4EAC\u5E08\u201D\uFF0C\u5E76\u53EA\u79F0\u676D\u5DDE\u4E3A\u201C\u4E34\u5B89\u5E9C\u201D\uFF0C\u53D6\u201C\u4E34\u65F6\u5B89\u987F\u201D\u4E4B\u610F\u3002</p><p>\u8FD9\u5176\u4E2D\uFF0C\u4E00\u6837\u662F\u57FA\u4E8E\u4EF7\u503C\u89C2\u7684\u8003\u8651\uFF1A\u5373\u4FBF\u505A\u4E0D\u5230\uFF0C\u4E5F\u7EDD\u4E0D\u80FD\u627F\u8BA4\u3002</p>',89),e=[s];function g(c,d){return o(),t("div",null,e)}const _=p(n,[["render",g],["__file","15.html.vue"]]);export{_ as default};
