import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as t,f as a}from"./app.1218f0f6.js";const c={},o=a('<h1 id="\u7B2C16\u96C6-\u9752\u82CD\u7684\u4E03\u60C5\u6811\u53D1\u82BD" tabindex="-1"><a class="header-anchor" href="#\u7B2C16\u96C6-\u9752\u82CD\u7684\u4E03\u60C5\u6811\u53D1\u82BD" aria-hidden="true">#</a> \u7B2C16\u96C6\uFF1A\u9752\u82CD\u7684\u4E03\u60C5\u6811\u53D1\u82BD</h1><p>\u5C0F\u5C0F\u7684\u6751\u5B50\u5374\u5341\u5206\u70ED\u95F9\uFF0C\u5973\u4EBA\u4EEC\u6709\u7684\u80CC\u7740\u5F13\u7BAD\uFF0C\u62FF\u7740\u957F\u77DB\uFF0C\u6709\u7684\u6765\u6765\u5F80\u5F80\u5E72\u7740\u5BB6\u52A1\u6D3B\u3002\u4E00\u4F4D\u5927\u59D0\u77A7\u89C1\u5C0F\u5170\u82B1\u624B\u4E0A\u7684\u82B1\uFF0C\u4FBF\u544A\u8BC9\u5979\u8FD9\u82B1\u4E0D\u80FD\u968F\u610F\u4E71\u6234\u3002\u8FD9\u82B1\u540D\u53EB\u971C\u76D0\u82B1\uFF0C\u5728\u90E8\u843D\u91CC\uFF0C\u53EA\u6709\u5BE1\u5987\u624D\u4F1A\u6234\u3002\u5C0F\u5170\u82B1\u770B\u5230\u5927\u5BB6\u5934\u4E0A\u90FD\u6234\u7740\u971C\u76D0\u82B1\uFF0C\u4FBF\u660E\u4E86\u6574\u4E2A\u6751\u5B50\u90FD\u662F\u5BE1\u5987\u3002\u4E00\u65C1\u7684\u4E1C\u65B9\u9752\u82CD\u4F3C\u4E4E\u542C\u5230\u4E86\u5979\u4EEC\u7684\u5BF9\u8BDD\uFF0C\u4F4E\u7740\u5934\u50CF\u662F\u5728\u60F3\u4E9B\u4EC0\u4E48\u3002</p><p>\u4E00\u4F4D\u8001\u5A46\u5A46\u773C\u5C16\u8BA4\u51FA\u4E86\u4E1C\u65B9\u9752\u82CD\uFF0C\u968F\u540E\u4F17\u4EBA\u7EB7\u7EB7\u8DEA\u4E0B\u884C\u793C\u62DC\u89C1\uFF0C\u53EA\u6709\u8001\u5A46\u5A46\u4F9D\u65E7\u5750\u7740\u3002\u5A46\u5A46\u8BE2\u95EE\u4E1C\u65B9\u9752\u82CD\uFF0C\u8FD9\u4E09\u4E07\u5E74\u4ED6\u7A76\u7ADF\u53BB\u4E86\u54EA\u91CC\u3002\u8FD9\u4E09\u4E07\u5E74\uFF0C\u82CD\u76D0\u6D77\u6CA6\u4E3A\u5E9F\u589F\uFF0C\u65CF\u4EBA\u53D7\u5C3D\u82E6\u695A\uFF0C\u5BB6\u5BB6\u6237\u6237\u6D41\u79BB\u5931\u6240\uFF0C\u4E27\u5931\u4EB2\u4EBA\uFF0C\u53EF\u6708\u65CF\u6708\u5C0A\u5374\u4E0D\u77E5\u53BB\u5411\u3002\u5A46\u5A46\u8BF4\u7740\u8BF4\u7740\u4FBF\u54ED\u4E86\uFF0C\u4ED6\u7684\u513F\u5B50\u539F\u672C\u5E94\u8BE5\u548C\u4E1C\u65B9\u9752\u82CD\u4E00\u4E2A\u5E74\u7EAA\uFF0C\u53EF\u662F\u5982\u4ECA\u4E1C\u65B9\u9752\u82CD\u56DE\u6765\u4E86\uFF0C\u4ED6\u7684\u513F\u5B50\u5374\u6CA1\u6709\u56DE\u6765\u3002\u5A46\u5A46\u8BF7\u6C42\u4E1C\u65B9\u9752\u82CD\uFF0C\u4ED6\u5F53\u521D\u662F\u600E\u4E48\u628A\u90A3\u4E9B\u7537\u4EBA\u5E26\u51FA\u53BB\u7684\uFF0C\u5C31\u628A\u4ED6\u4EEC\u5E26\u56DE\u6765\u3002\u4E1C\u65B9\u9752\u82CD\u76EF\u7740\u5A46\u5A46\uFF0C\u53EF\u5374\u6CA1\u6709\u7ED9\u51FA\u56DE\u7B54\u3002</p><p>\u5C0F\u5170\u82B1\u8DDF\u7740\u4E1C\u65B9\u9752\u82CD\u6765\u5230\u4E00\u5904\u60AC\u5D16\uFF0C\u8010\u5FC3\u529D\u6170\u4ED6\u3002\u4E1C\u65B9\u9752\u82CD\u4E0D\u7531\u5F97\u60F3\u5230\u4E86\u81EA\u5DF1\u7684\u5C0F\u65F6\u5019\uFF0C\u90A3\u65F6\u7236\u5C0A\u4F1A\u4EB2\u81EA\u6559\u5BFC\u4ED6\u7EC3\u5251\u4FEE\u6CD5\uFF0C\u4F1A\u5BBD\u5BB9\u4ED6\u7684\u8C03\u76AE\uFF0C\u4F1A\u548C\u4ED6\u4E00\u8D77\u8E22\u8E74\u97A0\uFF0C\u4F3C\u4E4E\u65E5\u5B50\u5C31\u5E94\u8BE5\u662F\u8FD9\u6837\u5FEB\u4E50\u7684\u3002\u60F3\u5230\u8FD9\u91CC\uFF0C\u4E1C\u65B9\u9752\u82CD\u8138\u4E0A\u9732\u51FA\u4E86\u4E0D\u4E00\u6837\u7684\u795E\u8272\uFF0C\u5C0F\u5170\u82B1\u8FD8\u662F\u7B2C\u4E00\u6B21\u5728\u4E1C\u65B9\u9752\u82CD\u7684\u8138\u4E0A\u770B\u5230\u8FD9\u79CD\u8868\u60C5\uFF0C\u4E0D\u514D\u611F\u5230\u6709\u4E9B\u6709\u8DA3\u3002</p><p>\u521A\u5230\u82CD\u76D0\u6D77\u65F6\uFF0C\u6240\u6709\u4EBA\u90FD\u8BF4\u6708\u5C0A\u7684\u5730\u4F4D\u81F3\u9AD8\u65E0\u4E0A\uFF0C\u4EFB\u4F55\u4EBA\u4EE5\u4EFB\u4F55\u7406\u7531\u5192\u72AF\u4E86\u4E1C\u65B9\u9752\u82CD\uFF0C\u90FD\u662F\u6B7B\u8DEF\u4E00\u6761\u3002\u53EF\u662F\u521A\u521A\uFF0C\u90A3\u4E2A\u8001\u5A46\u5A46\u90A3\u6837\u8BAD\u65A5\u4E1C\u65B9\u9752\u82CD\uFF0C\u4ED6\u90FD\u6CA1\u6709\u4E3A\u96BE\uFF0C\u5C0F\u5170\u82B1\u89C9\u5F97\u5B9A\u662F\u8001\u5A46\u5A46\u5BF9\u4EB2\u4EBA\u7684\u611F\u60C5\u548C\u7275\u6302\u6253\u52A8\u4E86\u4ED6\u3002\u4E1C\u65B9\u9752\u82CD\u575A\u51B3\u5426\u8BA4\uFF0C\u76F4\u8A00\u81EA\u5DF1\u53EA\u662F\u4E0D\u5FCD\u5979\u4E00\u4E2A\u5E74\u8001\u5C06\u6B7B\u4E4B\u4EBA\u7F62\u4E86\u3002\u53EF\u8FD9\u4E0D\u5FCD\u4E5F\u662F\u4E03\u60C5\u4E2D\u7684\u540C\u60C5\u548C\u60B2\u60AF\uFF0C\u4E1C\u65B9\u9752\u82CD\u660E\u660E\u4E03\u60C5\u5C3D\u65AD\uFF0C\u600E\u8FD8\u4F1A\u751F\u51FA\u8FD9\u6837\u7684\u60C5\u7EEA\uFF0C\u5C0F\u5170\u82B1\u4E00\u65F6\u95F4\u60F3\u4E0D\u660E\u767D\u3002\u60F3\u6765\u60F3\u53BB\uFF0C\u4E5F\u53EA\u6709\u4E00\u4E2A\u7B54\u6848\u3002</p><p>\u4E24\u4EBA\u968F\u540E\u6765\u5230\u4E1C\u65B9\u9752\u82CD\u7684\u5FC3\u6D77\uFF0C\u832B\u832B\u5927\u96EA\u4E2D\u7684\u4E03\u60C5\u6811\u679C\u771F\u53D1\u82BD\u4E86\u3002\u5C0F\u5170\u82B1\u6B22\u547C\u96C0\u8DC3\uFF0C\u53EF\u662F\u4E00\u65C1\u7684\u4E1C\u65B9\u9752\u82CD\u9762\u8272\u51DD\u91CD\uFF0C\u4F3C\u4E4E\u662F\u5728\u62C5\u5FE7\u4EC0\u4E48\u3002\u56DE\u53BB\u4E4B\u540E\uFF0C\u4E1C\u65B9\u9752\u82CD\u5C31\u68A6\u5230\u4E86\u7236\u5C0A\u3002\u53EA\u662F\u7236\u5C0A\u4E0D\u518D\u6E29\u67D4\uFF0C\u800C\u662F\u53D8\u5F97\u4E25\u5389\uFF0C\u4EE4\u4EBA\u754F\u60E7\u3002\u4E1C\u65B9\u9752\u82CD\u4ECE\u5669\u68A6\u4E2D\u9192\u6765\uFF0C\u4E45\u4E45\u65E0\u6CD5\u6062\u590D\u5E73\u9759\u3002</p><p>\u82E5\u662F\u611F\u53D7\u5230\u4E03\u60C5\uFF0C\u4E1A\u706B\u4E5F\u5C06\u6D88\u5931\uFF0C\u90A3\u65F6\u5019\u7684\u4E1C\u65B9\u9752\u82CD\u4FBF\u4E0D\u518D\u662F\u4E09\u754C\u6700\u5F3A\u3002\u53EF\u662F\u4E1C\u65B9\u9752\u82CD\u8EAB\u4E0A\u80CC\u8D1F\u7740\u6574\u4E2A\u82CD\u76D0\u6D77\uFF0C\u4ED6\u4E0D\u80FD\u5BB9\u8BB8\u8FD9\u79CD\u60C5\u51B5\u51FA\u73B0\u3002\u6B64\u65F6\u7684\u4ED6\u65E0\u6CD5\u63A7\u5236\u81EA\u5DF1\u7684\u60C5\u7EEA\uFF0C\u5C06\u6240\u6709\u6012\u706B\u53D1\u6CC4\u5230\u5C0F\u5170\u82B1\u8EAB\u4E0A\uFF0C\u53EF\u4ED6\u81EA\u5DF1\u7684\u5FC3\u4E2D\u4E5F\u96BE\u53D7\u4E07\u5206\u3002\u8C01\u6599\u7ED3\u9ECE\u6B63\u649E\u5230\u4E86\u4E1C\u65B9\u9752\u82CD\u7684\u67AA\u53E3\u4E0A\uFF0C\u7ADF\u5728\u8FD9\u65F6\u5077\u76D7\u8D22\u5B9D\uFF0C\u51C6\u5907\u51FA\u9003\u3002\u7ED3\u9ECE\u8C0E\u79F0\u81EA\u5DF1\u62FF\u8FD9\u4E9B\u4E1C\u897F\u662F\u4E3A\u4E86\u8BA8\u5C0F\u5170\u82B1\u7684\u6B22\u5FC3\uFF0C\u8FD9\u624D\u9003\u8FC7\u4E00\u52AB\u3002</p><p>\u5C31\u5728\u7ED3\u9ECE\u957F\u8212\u4E00\u53E3\u6C14\u65F6\uFF0C\u4E1C\u65B9\u9752\u82CD\u5728\u5305\u88F9\u4E2D\u53D1\u73B0\u4E86\u4E00\u4E2A\u6728\u96D5\uFF0C\u773C\u4E2D\u77AC\u95F4\u5347\u8D77\u6740\u610F\u3002\u5728\u7236\u5C0A\u53D8\u5F97\u4E25\u5389\u65F6\uFF0C\u5C0F\u9752\u82CD\u5E38\u5E38\u5FCD\u4E0D\u4F4F\u59D4\u5C48\u843D\u6CEA\u3002\u4E00\u4F4D\u5973\u5B50\u62FF\u7740\u4E00\u4E2A\u672A\u5B8C\u6210\u7684\u6728\u96D5\u8FC7\u6765\uFF0C\u548C\u4ED6\u8BF4\u8D77\u4E86\u4E00\u4E2A\u6545\u4E8B\u3002\u8FD9\u6728\u96D5\u96D5\u7684\u662F\u7B2C\u4E00\u4EE3\u6708\u5C0A\u76D0\u5973\uFF0C\u5979\u5E26\u9886\u7740\u6708\u65CF\u7956\u5148\u6E21\u5FD8\u5DDD\uFF0C\u5728\u82CD\u76D0\u6D77\u624E\u6839\uFF0C\u4ECE\u6B64\u751F\u751F\u4E0D\u606F\u3002\u5979\u7684\u8EAB\u4E0A\u6709\u5F88\u591A\u4F24\u75D5\uFF0C\u6BCF\u4E00\u9053\u90FD\u662F\u5149\u8363\u7684\u89C1\u8BC1\u3002\u5C0F\u9752\u82CD\u5FFD\u7136\u60F3\u660E\u767D\u4E86\uFF0C\u82E5\u60F3\u6210\u4E3A\u6708\u5C0A\uFF0C\u4FDD\u62A4\u5343\u4E07\u6708\u65CF\u5B50\u6C11\uFF0C\u5C31\u8981\u627F\u53D7\u4F24\u75D5\u3002</p><p>\u6B87\u9619\u770B\u51FA\u4E1C\u65B9\u9752\u82CD\u8138\u4E0A\u7684\u5F02\u6837\uFF0C\u8BBE\u6CD5\u6551\u8D70\u7ED3\u9ECE\uFF0C\u53EF\u662F\u4ED6\u4F4E\u4F30\u4E86\u4E1C\u65B9\u9752\u82CD\u7684\u72E0\u8FA3\u3002\u5C31\u5728\u5927\u5200\u780D\u4E0B\u7684\u4E00\u77AC\u95F4\uFF0C\u5C0F\u5170\u82B1\u5FFD\u7136\u95EA\u51FA\uFF0C\u6321\u5728\u4E86\u7ED3\u9ECE\u8EAB\u524D\u3002\u4E1C\u65B9\u9752\u82CD\u6025\u5FD9\u505C\u4E0B\uFF0C\u53EF\u5374\u53D1\u73B0\u81EA\u5DF1\u624B\u8155\u4E0A\u591A\u4E86\u4E00\u9053\u4F24\u3002\u5C0F\u5170\u82B1\u6B7B\u6D3B\u4E0D\u80AF\u8BA9\u5F00\uFF0C\u60F9\u5F97\u4E1C\u65B9\u9752\u82CD\u6012\u706B\u4E2D\u70E7\uFF0C\u9752\u7B4B\u66B4\u8D77\uFF0C\u773C\u4E2D\u7684\u6740\u6C14\u903C\u5F97\u4EBA\u5FEB\u8981\u70E7\u8D77\u6765\u3002\u5E78\u800C\u6709\u4EBA\u6B64\u65F6\u6765\u7980\u544A\uFF0C\u8BF4\u5DFD\u98CE\u5435\u7740\u8981\u53BB\u796D\u5960\u8001\u6708\u5C0A\u3002</p><p>\u867D\u7136\u6028\u6068\u54E5\u54E5\uFF0C\u53EF\u5DFD\u98CE\u4E5F\u4E0D\u5BB9\u8BB8\u82CD\u76D0\u6D77\u53D7\u5230\u4F24\u5BB3\u3002\u4ED6\u544A\u8BC9\u4E1C\u65B9\u9752\u82CD\uFF0C\u6D77\u5E02\u4E3B\u5E76\u672A\u544A\u77E5\u81EA\u5DF1\u795F\u6C14\u4F55\u6765\u3002\u4E1C\u65B9\u9752\u82CD\u672C\u4E0D\u60F3\u91CD\u7F5A\u5DFD\u98CE\uFF0C\u53EF\u662F\u4ED6\u5374\u51FA\u8A00\u4E0D\u900A\u3002\u88AB\u60F9\u6012\u7684\u4E1C\u65B9\u9752\u82CD\u54EA\u91CC\u8FD8\u987E\u5F97\u4E0A\u4EC0\u4E48\u5144\u5F1F\u4E4B\u60C5\uFF0C\u4E0B\u4EE4\u5C06\u5DFD\u98CE\u96F7\u5211\u5904\u6B7B\u3002\u6B87\u9619\u63D0\u9192\u4E1C\u65B9\u9752\u82CD\uFF0C\u4E09\u65E5\u540E\u4FBF\u662F\u8001\u6708\u5C0A\u7684\u5FCC\u65E5\u3002\u53EF\u8C01\u6599\u4E1C\u65B9\u9752\u82CD\u6839\u672C\u4E0D\u5728\u610F\uFF0C\u751A\u81F3\u8981\u5C06\u5FCC\u65E5\u63D0\u524D\uFF0C\u597D\u6210\u5168\u5DFD\u98CE\u6240\u8C13\u7684\u7236\u5B50\u60C5\u6DF1\u3002</p><p>\u5F97\u77E5\u4E1C\u65B9\u9752\u82CD\u8981\u5C06\u5DFD\u98CE\u5904\u6B7B\uFF0C\u5C0F\u5170\u82B1\u8D76\u5FD9\u53BB\u627E\u4ED6\u3002\u5C0F\u5170\u82B1\u66FF\u5DFD\u98CE\u6C42\u60C5\uFF0C\u53C8\u95EE\u5230\u4E86\u8001\u6708\u5C0A\u3002\u600E\u6599\u4E1C\u65B9\u9752\u82CD\u7A81\u7136\u53D1\u6012\uFF0C\u63A7\u8BC9\u7236\u5C0A\u5BF9\u4ED6\u7684\u6298\u78E8\u3002\u800C\u5F53\u521D\u8001\u6708\u5C0A\u8BA9\u4E1C\u65B9\u9752\u82CD\u6740\u4E86\u81EA\u5DF1\uFF0C\u4EE5\u751F\u547D\u4E3A\u4EE3\u4EF7\u6BC1\u6389\u7684\u4E03\u60C5\u6811\uFF0C\u5982\u4ECA\u5374\u53C8\u8981\u751F\u6839\u53D1\u82BD\u3002\u53EF\u5C0F\u5170\u82B1\u53EA\u89C9\u5F97\u4E8B\u6709\u8E4A\u8DF7\uFF0C\u8001\u6708\u5C0A\u7EDD\u4E0D\u4F1A\u4E0D\u7231\u81EA\u5DF1\u7684\u513F\u5B50\uFF0C\u8FD9\u82CD\u76D0\u6D77\u548C\u4E1C\u65B9\u9752\u82CD\u4F3C\u4E4E\u5E76\u4E0D\u50CF\u4F17\u4EBA\u773C\u4E2D\u7684\u6837\u5B50\uFF0C\u8FD9\u4E2D\u95F4\u4E00\u5B9A\u6709\u4EC0\u4E48\u9690\u60C5\u3002</p>',11),r=[o];function _(n,s){return p(),t("div",null,r)}const h=e(c,[["render",_],["__file","16.html.vue"]]);export{h as default};
