import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as t,f as a}from"./app.2ed33847.js";const c={},o=a('<h1 id="\u7B2C17\u96C6-\u5C0F\u5170\u82B1\u5524\u9192\u8001\u6708\u5C0A\u5143\u795E" tabindex="-1"><a class="header-anchor" href="#\u7B2C17\u96C6-\u5C0F\u5170\u82B1\u5524\u9192\u8001\u6708\u5C0A\u5143\u795E" aria-hidden="true">#</a> \u7B2C17\u96C6\uFF1A\u5C0F\u5170\u82B1\u5524\u9192\u8001\u6708\u5C0A\u5143\u795E</h1><p>\u4E1C\u65B9\u9752\u82CD\u7684\u4E03\u60C5\u6811\u5DF2\u7ECF\u53D1\u82BD\uFF0C\u53EF\u662F\u4ED6\u5374\u53EA\u89C9\u5F97\u96BE\u53D7\u548C\u614C\u4E71\u3002\u8FD8\u8BB0\u5F97\u5C0F\u7684\u65F6\u5019\uFF0C\u4ED6\u7279\u610F\u5B66\u4E86\u4E00\u652F\u66F2\u5B50\u60F3\u8981\u732E\u7ED9\u7236\u5C0A\uFF0C\u53EF\u662F\u5374\u906D\u5230\u4E86\u65A5\u8D23\u3002\u4E0D\u4EC5\u5982\u6B64\uFF0C\u7236\u5C0A\u8FD8\u5C06\u4ED6\u8EAB\u8FB9\u7684\u4F8D\u5973\u4EEC\u5168\u90E8\u5904\u6B7B\u3002\u4E1C\u65B9\u9752\u82CD\u6028\u6068\u7236\u5C0A\uFF0C\u51B2\u52A8\u4E4B\u4E0B\u62D4\u4E86\u5251\u5C31\u51B2\u5230\u7236\u5C0A\u9762\u524D\uFF0C\u53EF\u662F\u4ECD\u65E7\u4E0D\u5FCD\u5FC3\u52A8\u624B\u3002\u90A3\u4E4B\u540E\uFF0C\u7236\u5C0A\u5BF9\u5F85\u4E1C\u65B9\u9752\u82CD\uFF0C\u53EA\u5269\u4E0B\u65E0\u5C3D\u7684\u6298\u78E8\u3002\u4E4B\u540E\u7684\u4ED6\uFF0C\u7EC8\u4E8E\u65A9\u65AD\u4E86\u6240\u6709\u7684\u60C5\u4E1D\uFF0C\u6740\u4E86\u7236\u5C0A\u6210\u4E3A\u65B0\u7684\u6708\u5C0A\u3002</p><p>\u5176\u5B9E\u4E1C\u65B9\u9752\u82CD\u5F11\u7236\u4E4B\u4E8B\uFF0C\u6B87\u9619\u4E5F\u4E0D\u662F\u5F88\u6E05\u695A\uFF0C\u4ED6\u53EA\u77E5\u9053\u548C\u4E1C\u65B9\u9752\u82CD\u627F\u53D7\u62D4\u9664\u4E03\u60C5\u7684\u7981\u672F\u6709\u5173\u3002\u6B64\u7981\u672F\u8BB0\u8F7D\u4E8E\u4E0A\u53E4\u79D8\u518C\uFF0C\u65BD\u672F\u56F0\u96BE\uFF0C\u81F3\u4ECA\u53EA\u6709\u4E1C\u65B9\u9752\u82CD\u4E00\u4E2A\u6210\u529F\u8005\u3002\u53EA\u662F\u8FD9\u79D8\u518C\u5DF2\u7ECF\u88AB\u70E7\u6BC1\u4E86\u3002\u53EF\u5C0F\u5170\u82B1\u4E0D\u80AF\u6B7B\u5FC3\uFF0C\u5979\u4E0D\u987E\u6B87\u9619\u548C\u7ED3\u9ECE\u7684\u53CD\u5BF9\uFF0C\u575A\u6301\u8981\u53BB\u8001\u6708\u5C0A\u7684\u5BDD\u5BAB\uFF0C\u5F04\u6E05\u695A\u4E8B\u60C5\u7684\u539F\u59D4\uFF0C\u8BA9\u4E1C\u65B9\u9752\u82CD\u80FD\u591F\u5B89\u5FC3\u7761\u4E2A\u597D\u89C9\u3002\u6B87\u9619\u542C\u5230\u8FD9\u8BDD\uFF0C\u4E5F\u987E\u4E0D\u4E0A\u8FD9\u91CC\u662F\u7981\u5730\uFF0C\u8DDF\u7740\u5C0F\u5170\u82B1\u5C31\u8FDB\u53BB\u4E86\u3002\u7ED3\u9ECE\u65E0\u5948\uFF0C\u53EA\u597D\u7D27\u968F\u5176\u540E\u3002</p><p>\u5728\u8001\u6708\u5C0A\u7684\u5BDD\u5BAB\u91CC\u627E\u4E86\u8BB8\u4E45\uFF0C\u4E5F\u6CA1\u6709\u53D1\u73B0\u4EC0\u4E48\u6709\u4EF7\u503C\u7684\u4E1C\u897F\u3002\u5C0F\u5170\u82B1\u53EA\u597D\u5C0F\u5FC3\u7FFC\u7FFC\u6309\u52A8\u8001\u6708\u5C0A\u5EA7\u6905\u4E0A\u7684\u5B9D\u77F3\uFF0C\u8C01\u77E5\u7ADF\u89E6\u53D1\u4E86\u5F00\u5173\uFF0C\u627E\u5230\u4E86\u79D8\u518C\u3002\u53EA\u662F\u8FD9\u79D8\u518C\u5DF2\u7ECF\u6210\u4E86\u7070\u3002\u5C0F\u5170\u82B1\u5FFD\u7136\u60F3\u5230\uFF0C\u6216\u8BB8\u81EA\u5DF1\u53EF\u4EE5\u6062\u590D\u8FD9\u4E2A\u8349\u6728\u5236\u4F5C\u7684\u79D8\u518C\u3002\u8BF4\u7740\uFF0C\u5979\u4FBF\u5C1D\u8BD5\u4FEE\u590D\uFF0C\u7EC8\u4E8E\u77E5\u9053\u4E86\u4E1C\u65B9\u9752\u82CD\u7684\u8FC7\u53BB\u3002</p><p>\u5C0F\u5170\u82B1\u5FC3\u75BC\u6781\u4E86\uFF0C\u51E0\u4E4E\u5FEB\u8981\u6655\u53A5\u8FC7\u53BB\u3002\u53EF\u662F\u5979\u8FD8\u662F\u4E0D\u77E5\u9053\uFF0C\u4E00\u4E2A\u7236\u4EB2\u4E3A\u4F55\u8981\u90A3\u6837\u5BF9\u81EA\u5DF1\u7684\u513F\u5B50\u3002\u5979\u7EE7\u7EED\u7FFB\u627E\u7740\u5BDD\u5BAB\uFF0C\u7EC8\u4E8E\u5728\u4E00\u7247\u5E9F\u589F\u4E2D\u627E\u5230\u4E86\u4E00\u53EA\u7434\uFF0C\u90A3\u662F\u4E1C\u65B9\u9752\u82CD\u8D60\u4E0E\u8001\u6708\u5C0A\u7684\u3002\u8FD9\u53EA\u7434\u6709\u4FEE\u8865\u7684\u75D5\u8FF9\uFF0C\u8FD8\u88AB\u8001\u6708\u5C0A\u653E\u5728\u4E86\u79BB\u5E8A\u5934\u6700\u8FD1\u7684\u5BF9\u65B9\uFF0C\u5C0F\u5170\u82B1\u89C9\u5F97\u8001\u6708\u5C0A\u5FC3\u91CC\u662F\u7231\u81EA\u5DF1\u7684\u513F\u5B50\u7684\u3002</p><p>\u4ED6\u4EEC\u8FD8\u627E\u5230\u4E86\u4E00\u4E2A\u6728\u97A0\u3002\u5C0F\u5170\u82B1\u542C\u5BAB\u91CC\u7684\u8001\u4EBA\u8BF4\u8FC7\uFF0C\u4E1C\u65B9\u9752\u82CD\u5C0F\u65F6\u5019\u559C\u6B22\u73A9\u6728\u97A0\uFF0C\u5DFD\u98CE\u5374\u4E0D\u559C\u6B22\u3002\u800C\u8FD9\u4E2A\u6728\u97A0\u548C\u7434\u653E\u5728\u4E00\u8D77\uFF0C\u8FD8\u88AB\u597D\u597D\u6536\u7740\uFF0C\u8FD9\u8DB3\u4EE5\u8BF4\u660E\u7236\u4EB2\u5F88\u75BC\u7231\u8FD9\u4E2A\u513F\u5B50\u3002\u968F\u540E\u5C0F\u5170\u82B1\u6253\u5F00\u8BB0\u5F55\u6708\u5C0A\u4E00\u8A00\u4E00\u884C\u7684\u8D77\u5C45\u6CE8\uFF0C\u5F97\u77E5\u4E1C\u65B9\u9752\u82CD\u662F\u552F\u4E00\u53EF\u4EE5\u4F7F\u7528\u4E1A\u706B\u5251\u7684\u4EBA\u3002\u800C\u8981\u4FEE\u70BC\u4E1A\u706B\uFF0C\u4E00\u8981\u5929\u8D4B\u5F02\u7980\uFF0C\u4E8C\u8981\u62D4\u53BB\u4E03\u60C5\u3002\u4E09\u754C\u4E07\u4E16\uFF0C\u53EA\u6709\u76D0\u5973\u548C\u4E1C\u65B9\u9752\u82CD\u6709\u8FD9\u4E2A\u8D44\u8D28\u3002</p><p>\u5F53\u5E74\u76D0\u5973\u88AB\u6C34\u4E91\u5929\u7684\u91CD\u534E\u6240\u8D1F\uFF0C\u65AD\u60C5\u7EDD\u7231\u5B66\u4F1A\u4E86\u4E1A\u706B\uFF0C\u800C\u4E1C\u65B9\u9752\u82CD\u5219\u662F\u88AB\u8FEB\u65A9\u65AD\u4E86\u4E03\u60C5\u3002\u60F3\u5230\u8FD9\u91CC\uFF0C\u5C0F\u5170\u82B1\u4E0D\u7531\u5F97\u5FC3\u75BC\u4E1C\u65B9\u9752\u82CD\uFF0C\u4E5F\u5FFD\u7136\u660E\u767D\uFF0C\u5F53\u5E74\u8001\u6708\u5C0A\u4E00\u5B9A\u5F88\u96BE\u8FC7\uFF0C\u81EA\u5DF1\u6700\u75BC\u7231\u7684\u513F\u5B50\u7ADF\u662F\u552F\u4E00\u53EF\u4EE5\u4F7F\u7528\u4E1A\u706B\u5251\u7684\u4EBA\u3002\u4E3A\u4E86\u632F\u5174\u4FDD\u62A4\u6574\u4E2A\u82CD\u76D0\u6D77\uFF0C\u4E00\u4F4D\u7236\u4EB2\u4E0D\u5F97\u4E0D\u5B66\u4F1A\u5BF9\u81EA\u5DF1\u7684\u513F\u5B50\u51B7\u6F20\u3002</p><p>\u5C0F\u5170\u82B1\u4ECE\u7ED3\u9ECE\u90A3\u91CC\u62FF\u5230\u4E86\u71C3\u7280\u82B1\uFF0C\u53EF\u600E\u4E48\u4E5F\u4FEE\u590D\u4E0D\u4E86\u3002\u592A\u9633\u843D\u4E0B\uFF0C\u6708\u4EAE\u5347\u8D77\uFF0C\u5C0F\u5170\u82B1\u7684\u624B\u4E0A\u4E0D\u77E5\u4F55\u65F6\u7559\u4E0B\u4E86\u9C9C\u8840\uFF0C\u53EF\u5979\u4ECD\u672A\u653E\u5F03\u3002\u5C31\u5728\u5979\u5FEB\u8981\u5012\u4E0B\u7684\u65F6\u5019\uFF0C\u71C3\u7280\u82B1\u7EFD\u5F00\u4E86\u3002\u968F\u540E\uFF0C\u5979\u5E26\u7740\u90A3\u53EA\u7434\u548C\u6728\u97A0\u524D\u5F80\u5F52\u589F\u4E4B\u5883\u3002</p><p>\u4E1C\u65B9\u9752\u82CD\u5FFD\u7136\u611F\u53D7\u5230\u624B\u8155\u4E0A\u4F20\u6765\u7684\u75BC\u75DB\uFF0C\u4ED6\u6380\u5F00\u8863\u8896\uFF0C\u679C\u7136\u53D1\u73B0\u4E86\u4F24\u53E3\u3002\u8FD9\u4F24\u53E3\u4F3C\u4E4E\u662F\u88AB\u4EC0\u4E48\u5C16\u9510\u7684\u4E1C\u897F\u5212\u8FC7\u76AE\u80A4\u6240\u81F4\uFF0C\u9732\u51FA\u7329\u7EA2\u7684\u9C9C\u8840\u3002\u4ED6\u4E0D\u7531\u5F97\u63EA\u7D27\u4E86\u5FC3\uFF0C\u8138\u4E0A\u4E5F\u9732\u51FA\u62C5\u5FE7\u3002</p><p>\u7ED3\u9ECE\u7D27\u5F20\u7684\u770B\u7740\u4E0B\u9762\u7684\u6F29\u6DA1\uFF0C\u5C0F\u5170\u82B1\u8FDB\u53BB\u5DF2\u7ECF\u6709\u4E24\u4E2A\u65F6\u8FB0\u4E86\uFF0C\u5979\u771F\u6015\u5C0F\u5170\u82B1\u51FA\u73B0\u4EC0\u4E48\u610F\u5916\u3002\u4E1C\u65B9\u9752\u82CD\u8D76\u6765\uFF0C\u7EB5\u8EAB\u4E00\u8DC3\u8FDB\u5165\u6F29\u6DA1\u4E4B\u4E2D\u3002\u6B64\u65F6\u5C0F\u5170\u82B1\u7A7F\u68AD\u5728\u9ED1\u6697\u7684\u752C\u9053\u4E2D\uFF0C\u4E00\u4E2A\u4E2A\u53D1\u7740\u5149\u4EAE\u7684\u5143\u795E\u4ECE\u5979\u8EAB\u8FB9\u98DE\u8FC7\u3002\u5C0F\u5170\u82B1\u9AD8\u5174\u6781\u4E86\uFF0C\u5979\u7EC8\u4E8E\u5230\u4E86\u5F52\u589F\u4E4B\u5883\u3002\u5979\u5C0F\u5FC3\u7FFC\u7FFC\u8D70\u5165\u6BBF\u4E2D\uFF0C\u91CC\u9762\u6709\u4E00\u53EA\u5143\u795E\u73AF\u7ED5\u5728\u5979\u5468\u56F4\u3002\u5C0F\u5170\u82B1\u8D76\u5FD9\u62FF\u51FA\u71C3\u7280\u82B1\uFF0C\u51C6\u5907\u53EC\u5524\u51FA\u8001\u6708\u5C0A\u7684\u5143\u795E\u3002\u5C31\u5728\u5979\u51C6\u5907\u52A8\u624B\u65F6\uFF0C\u4E1C\u65B9\u9752\u82CD\u7A81\u7136\u51FA\u73B0\u963B\u6B62\u4E86\u5979\u3002</p><p>\u4E0D\u987E\u4E1C\u65B9\u9752\u82CD\u7684\u53CD\u5BF9\u548C\u6012\u706B\uFF0C\u5C0F\u5170\u82B1\u575A\u6301\u8981\u5524\u9192\u8001\u6708\u5C0A\u7684\u5143\u795E\u3002\u6240\u5E78\uFF0C\u5979\u6210\u529F\u4E86\u3002\u8001\u6708\u5C0A\u4E00\u5F00\u53E3\uFF0C\u4FBF\u95EE\u8D77\u4E1C\u65B9\u9752\u82CD\u662F\u5426\u626B\u5E73\u4E86\u4ED9\u754C\uFF0C\u91CD\u632F\u4E86\u6708\u65CF\u3002\u5C0F\u5170\u82B1\u5E76\u672A\u56DE\u7B54\uFF0C\u800C\u662F\u4E3E\u8D77\u7434\u8BA9\u8001\u6708\u5C0A\u8FA8\u8BA4\u3002\u8001\u6708\u5C0A\u5F53\u7136\u8BB0\u5F97\u8FD9\u53EA\u7434\uFF0C\u8FD9\u662F\u4ED6\u4EB2\u624B\u6467\u6BC1\u7684\uFF0C\u4E5F\u662F\u4ED6\u4EB2\u624B\u4FEE\u597D\u7684\u3002</p>',11),r=[o];function _(n,s){return p(),t("div",null,r)}const h=e(c,[["render",_],["__file","17.html.vue"]]);export{h as default};
