import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as t,f as a}from"./app.f4897be6.js";const c={},o=a('<h1 id="\u7B2C18\u96C6-\u9752\u82CD\u4E3A\u5C0F\u5170\u82B1\u70B9\u6731\u7802" tabindex="-1"><a class="header-anchor" href="#\u7B2C18\u96C6-\u9752\u82CD\u4E3A\u5C0F\u5170\u82B1\u70B9\u6731\u7802" aria-hidden="true">#</a> \u7B2C18\u96C6\uFF1A\u9752\u82CD\u4E3A\u5C0F\u5170\u82B1\u70B9\u6731\u7802</h1><p>\u4E1C\u65B9\u9752\u82CD\u8BE2\u95EE\u7236\u5C0A\uFF0C\u5982\u679C\u91CD\u6765\u4E00\u6B21\uFF0C\u4ED6\u662F\u5426\u8FD8\u662F\u4E00\u6837\u7684\u9009\u62E9\u3002\u8001\u6708\u5C0A\u72B9\u8C6B\u4E86\u5F88\u4E45\uFF0C\u6700\u540E\u7ED9\u4E86\u4ED6\u4E00\u4E2A\u80AF\u5B9A\u7684\u7B54\u6848\u3002\u4E1C\u65B9\u9752\u82CD\u6216\u8BB8\u65E9\u5DF2\u7ECF\u731C\u5230\u7236\u5C0A\u7684\u7B54\u6848\uFF0C\u53EF\u662F\u4EB2\u8033\u542C\u5230\u8FD9\u53E5\u8BDD\u65F6\uFF0C\u4ED6\u5FC3\u91CC\u8FD8\u662F\u514D\u4E0D\u4E86\u5931\u843D\u3002\u8001\u6708\u5C0A\u7684\u773C\u7736\u4E0D\u7531\u5F97\u5659\u6EE1\u4E86\u6CEA\u6C34\uFF0C\u4ED6\u60F3\u8981\u89E3\u91CA\uFF0C\u53EF\u662F\u5374\u53D1\u73B0\u81EA\u5DF1\u597D\u50CF\u4EC0\u4E48\u90FD\u65E0\u6CD5\u633D\u56DE\u3002</p><p>\u5F53\u5E74\u8D64\u5730\u5973\u5B50\u7387\u519B\u8FDB\u653B\uFF0C\u6708\u65CF\u591A\u5730\u63A5\u8FDE\u6CA6\u9677\u3002\u4ED6\u60F3\u8981\u505A\u597D\u4E00\u4E2A\u7236\u4EB2\uFF0C\u53EF\u662F\u4ED6\u4E0D\u80FD\u629B\u5F03\u82CD\u76D0\u6D77\u7684\u5B50\u6C11\u3002\u5728\u4E1C\u65B9\u9752\u82CD\u773C\u91CC\uFF0C\u81EA\u5DF1\u5C31\u662F\u7236\u5C0A\u62B5\u5FA1\u4ED9\u754C\u7684\u5DE5\u5177\uFF0C\u4ED6\u672C\u53EF\u4EE5\u6740\u4E86\u81EA\u5DF1\uFF0C\u53EF\u662F\u5374\u8BA9\u81EA\u5DF1\u751F\u4E0D\u5982\u6B7B\u3002\u5176\u5B9E\u8001\u6708\u5C0A\u5FC3\u91CC\u4E5F\u6709\u8BF8\u591A\u4E0D\u5FCD\uFF0C\u6240\u4EE5\u4ED6\u9009\u62E9\u8BA9\u513F\u5B50\u6740\u4E86\u81EA\u5DF1\u3002\u53EF\u4ED6\u4F3C\u4E4E\u5FD8\u4E86\uFF0C\u4E1C\u65B9\u9752\u82CD\u4E5F\u4F1A\u96BE\u8FC7\u3002</p><p>\u8001\u6708\u5C0A\u77E5\u9053\u60C5\u4E1D\u96BE\u65AD\uFF0C\u6BCF\u6BCF\u6B7B\u800C\u590D\u751F\uFF0C\u9700\u91CD\u590D\u5343\u5E74\u624D\u80FD\u5F7B\u5E95\u62D4\u9664\u3002\u800C\u79D8\u672F\u4E4B\u5916\uFF0C\u82E5\u80FD\u624B\u5203\u81F3\u4EB2\uFF0C\u987B\u81FE\u4E4B\u95F4\uFF0C\u60C5\u4E1D\u5C3D\u65AD\u3002\u6240\u4EE5\u90A3\u65E5\uFF0C\u4ED6\u624D\u4F1A\u8BA9\u4E1C\u65B9\u9752\u82CD\u4EB2\u624B\u6740\u4E86\u81EA\u5DF1\u3002\u5982\u4ECA\uFF0C\u4E1C\u65B9\u9752\u82CD\u7684\u60C5\u6839\u5DF2\u6709\u6240\u6062\u590D\uFF0C\u4ED6\u53EF\u4EE5\u9009\u62E9\u662F\u505A\u4E00\u4E2A\u6240\u5411\u62AB\u9761\u7684\u6708\u5C0A\uFF0C\u5E26\u9886\u6708\u65CF\u91CD\u56DE\u8363\u8000\uFF0C\u8FD8\u662F\u8981\u5C06\u4E4B\u524D\u7ECF\u5386\u7684\u6240\u6709\u75DB\u82E6\u767D\u8D39\u3002\u5982\u82E5\u4ED6\u7EE7\u7EED\u6C89\u6EBA\u5728\u60C5\u7231\u4E4B\u4E2D\uFF0C\u4FBF\u4F1A\u7EE7\u7EED\u8EAB\u9677\u75DB\u82E6\u65E0\u529B\u81EA\u62D4\u3002\u800C\u8001\u6708\u5C0A\u5E0C\u671B\u4E1C\u65B9\u9752\u82CD\u80FD\u591F\u9009\u62E9\u524D\u8005\uFF0C\u50CF\u5F53\u5E74\u90A3\u6837\u6740\u4E86\u4ED6\uFF0C\u65A9\u65AD\u60C5\u6839\u3002\u53EF\u662F\u4E1C\u65B9\u9752\u82CD\u5374\u505A\u4E0D\u5230\u3002</p><p>\u6700\u8FD1\u4E00\u6BB5\u65E5\u5B50\uFF0C\u4E1C\u65B9\u9752\u82CD\u65E5\u591C\u60F3\u8D77\u68A6\u5230\u7684\uFF0C\u90FD\u662F\u548C\u7236\u5C0A\u5728\u4E00\u8D77\u7684\u5FEB\u4E50\uFF0C\u6240\u4EE5\u4ED6\u5B81\u613F\u627F\u53D7\u5F11\u7236\u5E26\u6765\u7684\u75DB\u82E6\uFF0C\u4E5F\u8981\u8BB0\u4F4F\u90A3\u4E9B\u611F\u53D7\u3002\u8001\u6708\u5C0A\u653E\u4E0B\u6728\u97A0\uFF0C\u50CF\u8FC7\u53BB\u90A3\u6837\u5C06\u5B83\u8E22\u7ED9\u4E1C\u65B9\u9752\u82CD\u3002\u71C3\u7280\u82B1\u5FEB\u8981\u71C3\u5C3D\uFF0C\u53EF\u662F\u4E1C\u65B9\u9752\u82CD\u5374\u8FDF\u8FDF\u6CA1\u6709\u52A8\u9759\u3002\u4E00\u76F4\u5230\u71C3\u7280\u82B1\u70E7\u6210\u7070\u70EC\uFF0C\u4E1C\u65B9\u9752\u82CD\u8FD9\u624D\u53CD\u5E94\u8FC7\u6765\uFF0C\u53EF\u662F\u7236\u5C0A\u65E9\u5DF2\u6D88\u6563\u800C\u53BB\u3002</p><p>\u5DFD\u98CE\u53EA\u906D\u53D7\u4E86\u4E00\u534A\u96F7\u5211\uFF0C\u4FBF\u5DF2\u7ECF\u5FEB\u8981\u652F\u6491\u4E0D\u4E0B\u53BB\u3002\u4E1C\u65B9\u9752\u82CD\u7A81\u7136\u7AD9\u8D77\u6765\uFF0C\u66FF\u4ED6\u5B8C\u6210\u5269\u4E0B\u7684\u96F7\u5211\u3002\u5C0F\u5170\u82B1\u60F3\u8981\u963B\u6B62\uFF0C\u53EF\u5979\u77E5\u9053\uFF0C\u4E1C\u65B9\u9752\u82CD\u5FC3\u610F\u5DF2\u51B3\u3002\u884C\u5211\u5B8C\u6BD5\uFF0C\u4E1C\u65B9\u9752\u82CD\u7684\u5634\u89D2\u6D41\u51FA\u9C9C\u8840\u6765\u3002\u53EF\u4E00\u65C1\u7684\u5DFD\u98CE\u5374\u89C9\u5F97\u4E1C\u65B9\u9752\u82CD\u662F\u60FA\u60FA\u4F5C\u6001\uFF0C\u81EA\u5DF1\u4ECD\u65E7\u4F1A\u627E\u4ED6\u62A5\u4EC7\u3002\u5C0F\u5170\u82B1\u4E3A\u4E1C\u65B9\u9752\u82CD\u6253\u62B1\u4E0D\u5E73\uFF0C\u8D23\u5907\u5DFD\u98CE\u4F5C\u4E3A\u4E1C\u65B9\u9752\u82CD\u7684\u5144\u5F1F\uFF0C\u5374\u4ECE\u672A\u4E86\u89E3\u8FC7\u771F\u76F8\u3002\u8BF4\u7740\uFF0C\u5979\u5C06\u88C5\u7740\u771F\u76F8\u7684\u6CD5\u5668\u7559\u4E0B\uFF0C\u6400\u6276\u7740\u4E1C\u65B9\u9752\u82CD\u56DE\u53BB\u3002</p><p>\u521A\u60F3\u5582\u4E1C\u65B9\u9752\u82CD\u559D\u836F\uFF0C\u53EF\u4ED6\u5374\u7A81\u7136\u4E0D\u89C1\u4E86\u3002\u5C0F\u5170\u82B1\u5728\u8001\u6708\u5C0A\u7684\u5BDD\u5BAB\u53D1\u73B0\u4E86\u5931\u9B42\u843D\u9B44\u7684\u4E1C\u65B9\u9752\u82CD\uFF0C\u8FD9\u4E2A\u66FE\u7ECF\u76EE\u7A7A\u4E00\u5207\u7684\u5927\u9B54\u5934\uFF0C\u6B64\u523B\u6B63\u62B1\u7740\u4E00\u53EA\u5C0F\u5C0F\u7684\u6728\u97A0\u6CE3\u4E0D\u6210\u58F0\u3002\u5C0F\u5170\u82B1\u7F13\u7F13\u4E0A\u524D\uFF0C\u7D27\u7D27\u62B1\u4F4F\u4E86\u4ED6\u3002</p><p>\u8FD9\u51E0\u65E5\uFF0C\u4E1C\u65B9\u9752\u82CD\u7A9D\u5728\u5E8A\u4E0A\uFF0C\u4E00\u4E3E\u4E00\u52A8\u90FD\u9700\u8981\u5C0F\u5170\u82B1\u7167\u6599\u3002\u53EF\u5F53\u5DFD\u98CE\u6765\u65F6\uFF0C\u4ED6\u5374\u5FFD\u7136\u6765\u4E86\u7CBE\u795E\uFF0C\u8D76\u5FD9\u5C06\u81EA\u5DF1\u6536\u62FE\u5E72\u51C0\u3002\u5DFD\u98CE\u8FD9\u6B21\u662F\u771F\u5FC3\u7684\u884C\u793C\uFF0C\u4E5F\u662F\u771F\u5FC3\u7684\u62C5\u5FE7\u4E1C\u65B9\u9752\u82CD\u7684\u4F24\u52BF\u3002\u4ED6\u5C06\u4E4B\u524D\u548C\u6D77\u5E02\u4E3B\u89C1\u9762\u7684\u4E8B\u60C5\u544A\u77E5\u4E1C\u65B9\u9752\u82CD\uFF0C\u5E76\u8BDA\u5FC3\u8BA4\u9519\u3002\u7ECF\u8FC7\u51E0\u5929\u7684\u89C2\u5BDF\uFF0C\u5C0F\u5170\u82B1\u770B\u5F97\u51FA\uFF0C\u5176\u5B9E\u5DFD\u98CE\u662F\u771F\u7684\u5728\u4E4E\u4E1C\u65B9\u9752\u82CD\u7684\uFF0C\u800C\u4E1C\u65B9\u9752\u82CD\u4E5F\u5728\u4E4E\u8FD9\u4E2A\u5F1F\u5F1F\u3002\u53EA\u662F\u4E1C\u65B9\u9752\u82CD\u4E0D\u77E5\u9053\uFF0C\u8BE5\u5982\u4F55\u8868\u8FBE\u8FD9\u79CD\u60C5\u7EEA\u3002</p><p>\u5C0F\u5170\u82B1\u544A\u8BC9\u4E1C\u65B9\u9752\u82CD\uFF0C\u5982\u679C\u4E0B\u6B21\u518D\u89C1\u5230\u5DFD\u98CE\uFF0C\u5C31\u4E0D\u8981\u677F\u7740\u8138\uFF0C\u8981\u7B11\u4E00\u7B11\u3002\u8BF4\u7740\uFF0C\u5979\u5C31\u8BA9\u4E1C\u65B9\u9752\u82CD\u7B11\u4E00\u4E2A\u770B\u770B\u3002\u5C1D\u8BD5\u4E86\u534A\u5929\uFF0C\u4E1C\u65B9\u9752\u82CD\u4E5F\u53EA\u80FD\u52C9\u5F3A\u6324\u51FA\u4E00\u4E2A\u5047\u6A21\u5047\u6837\u7684\u7B11\u3002\u5C0F\u5170\u82B1\u770B\u7740\u6324\u7709\u5F04\u773C\u7684\u4E1C\u65B9\u9752\u82CD\uFF0C\u5FCD\u4E0D\u4F4F\u7B11\u51FA\u58F0\u6765\u3002\u65E0\u5948\uFF0C\u5979\u53EA\u597D\u52A8\u624B\u6446\u5F04\u4E1C\u65B9\u9752\u82CD\u7684\u8138\uFF0C\u8FD9\u624D\u5728\u4ED6\u8138\u4E0A\u770B\u5230\u4E00\u4E2A\u597D\u770B\u7684\u7B11\u5BB9\u3002\u53EF\u5C0F\u5170\u82B1\u8FD8\u662F\u89C9\u5F97\u4E0D\u591F\uFF0C\u6BD5\u7ADF\u4E1C\u65B9\u9752\u82CD\u548C\u5DFD\u98CE\u662F\u4EB2\u5144\u5F1F\uFF0C\u5F97\u518D\u4EB2\u70ED\u4E00\u4E9B\u624D\u884C\uFF0C\u53EF\u4EE5\u8BD5\u7740\u62E5\u62B1\u5BF9\u65B9\u3002\u8BF4\u7740\uFF0C\u5C0F\u5170\u82B1\u5C31\u5F20\u5F00\u53CC\u81C2\u731B\u5730\u6251\u4E0A\u53BB\uFF0C\u5411\u4E1C\u65B9\u9752\u82CD\u793A\u610F\u600E\u4E48\u62E5\u62B1\u3002\u4E24\u4EBA\u4E00\u65F6\u95F4\u9760\u5F97\u6781\u8FD1\uFF0C\u7A7A\u6C14\u4E2D\u5F25\u6F2B\u7740\u66A7\u6627\u7684\u6C14\u606F\u3002</p><p>\u8FD9\u5929\uFF0C\u5DFD\u98CE\u6765\u627E\u4E1C\u65B9\u9752\u82CD\u5546\u8BA8\u89E3\u9664\u5341\u4E07\u5C06\u58EB\u5C01\u5370\u4E00\u4E8B\u3002\u4E1C\u65B9\u9752\u82CD\u59CB\u7EC8\u677F\u7740\u4E00\u5F20\u8138\uFF0C\u4F3C\u4E4E\u662F\u5FD8\u4E86\u5C0F\u5170\u82B1\u4E4B\u524D\u7684\u8BDD\u3002\u5C31\u5728\u5DFD\u98CE\u8981\u8D70\u65F6\uFF0C\u4E1C\u65B9\u9752\u82CD\u5FFD\u7136\u53EB\u4F4F\u4ED6\uFF0C\u8FD8\u5F20\u5F00\u53CC\u624B\u60F3\u8981\u4E0A\u524D\u62E5\u62B1\u3002\u53EF\u5F53\u624B\u4E3E\u8D77\u6765\u65F6\uFF0C\u4E1C\u65B9\u9752\u82CD\u53C8\u89C9\u5F97\u522B\u626D\uFF0C\u53EA\u597D\u62CD\u62CD\u5DFD\u98CE\u7684\u80A9\u8180\uFF0C\u53EE\u5631\u4ED6\u5C0F\u5FC3\u3002</p><p>\u6309\u7167\u6708\u65CF\u7684\u89C4\u77E9\uFF0C\u611F\u8C22\u4E00\u4E2A\u4EBA\uFF0C\u5C31\u8981\u53D6\u4E0B\u81EA\u5DF1\u7684\u8840\u6D82\u5728\u90A3\u4E2A\u4EBA\u7684\u8138\u4E0A\u3002\u4E1C\u65B9\u9752\u82CD\u77E5\u9053\u5C0F\u5170\u82B1\u6015\u8840\uFF0C\u4FBF\u6362\u6210\u4E86\u6731\u7802\u3002</p>',11),r=[o];function _(n,s){return p(),t("div",null,r)}const h=e(c,[["render",_],["__file","18.html.vue"]]);export{h as default};
