import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.cd90e563.js";const c={},o=a('<h1 id="\u7B2C18\u96C6-\u90DD\u6BCD\u6012\u6253\u90DD\u51AC\u6885" tabindex="-1"><a class="header-anchor" href="#\u7B2C18\u96C6-\u90DD\u6BCD\u6012\u6253\u90DD\u51AC\u6885" aria-hidden="true">#</a> \u7B2C18\u96C6\uFF1A\u90DD\u6BCD\u6012\u6253\u90DD\u51AC\u6885</h1><p>\u53D1\u73B0\u793C\u7269\u9001\u9519\u4E86\uFF0C\u90DD\u6BCD\u4E5F\u81EA\u8D23\u8D76\u7D27\u5C06\u90DD\u51AC\u6885\u53EB\u51FA\u6765\uFF0C\u8FD8\u4E3B\u52A8\u63D0\u51FA\u8981\u627E\u62A5\u793E\u7684\u6444\u50CF\u8BB0\u8005\u53BB\u62CD\u7167\uFF0C\u6BD4\u7167\u76F8\u9986\u8FD8\u8981\u4E13\u4E1A\u3002\u4F46\u662F\u90DD\u51AC\u6885\u5374\u56E0\u6B64\u5927\u53D1\u813E\u6C14\uFF0C\u8BA4\u4E3A\u7236\u6BCD\u6CA1\u6709\u4ECE\u5FC3\u5E95\u91CC\u5C0A\u91CD\u5468\u79C9\u4E49\u7684\u5BB6\u4EBA\uFF0C\u90DD\u51AC\u6885\u5FCD\u4E0D\u4F4F\u843D\u6CEA\uFF0C\u58F0\u79F0\u81EA\u5DF1\u7231\u5468\u79C9\u4E49\uFF0C\u4F46\u662F\u5468\u79C9\u4E49\u7231\u4ED6\u4EEC\u7684\u5BB6\u4EBA\uFF0C\u5468\u79C9\u4E49\u662F\u597D\u4EBA\uFF0C\u5468\u79C9\u4E49\u7684\u5BB6\u4EBA\u66F4\u662F\u597D\u4EBA\u4E4B\u4E2D\u7684\u597D\u4EBA\u3002\u90DD\u51AC\u6885\u6307\u8D23\u6BCD\u4EB2\u5C31\u662F\u6234\u7740\u6709\u8272\u773C\u955C\u770B\u4EBA\uFF0C\u5F53\u521D\u51FA\u4E8B\u7684\u65F6\u5019\u5982\u679C\u4E0D\u662F\u5468\u79C9\u4E49\u5979\u4E5F\u6D3B\u4E0D\u5230\u73B0\u5728\uFF0C\u5F53\u65F6\u7236\u6BCD\u4E5F\u4E0D\u662F\u8FD9\u79CD\u6001\u5EA6\u3002</p><p>\u90DD\u51AC\u6885\u6307\u8D23\u6BCD\u4EB2\u751F\u6015\u5468\u79C9\u4E49\u5BB6\u4EBA\u627E\u4ED6\u4EEC\u529E\u4E8B\uFF0C\u6240\u4EE5\u624D\u4F1A\u4E00\u76F4\u907F\u800C\u4E0D\u89C1\uFF0C\u5B9E\u9645\u4E0A\u4ED6\u4EEC\u624B\u4E2D\u7684\u6743\u5229\u4E5F\u662F\u515A\u7ED9\u7684\u3002\u4ED6\u4EEC\u4E5F\u4E0D\u4F1A\u8086\u610F\u5229\u7528\u624B\u4E2D\u7684\u6743\u5229\uFF0C\u4E00\u5207\u7684\u4E0D\u89C1\u9762\u90FD\u662F\u627E\u501F\u53E3\uFF0C\u5B9E\u9645\u4E0A\u5C31\u662F\u9AA8\u5B50\u91CC\u7684\u77A7\u4E0D\u8D77\u3002\u90DD\u6BCD\u6C14\u5F97\u4E00\u5DF4\u638C\u72E0\u72E0\u6253\u5728\u4E86\u90DD\u51AC\u6885\u7684\u8138\u4E0A\uFF0C\u6BCD\u5973\u4FE9\u90FD\u6697\u81EA\u843D\u6CEA\u3002</p><p>\u665A\u4E0A\uFF0C\u5468\u5FD7\u521A\u548C\u5468\u84C9\u804A\u5929\uFF0C\u5468\u5FD7\u521A\u89C9\u5F97\u4E0D\u5E94\u8BE5\u9001\u8336\u53F6\uFF0C\u5982\u679C\u4E0D\u9001\u8336\u53F6\u90DD\u51AC\u6885\u7236\u6BCD\u53EF\u80FD\u4E5F\u4E0D\u4F1A\u6765\u3002\u5468\u5FD7\u521A\u89C9\u5F97\u59D4\u5C48\u4E86\u90DD\u51AC\u6885\uFF0C\u5176\u5B9E\u5B69\u5B50\u4EEC\u6210\u4EB2\u4E4B\u540E\u90FD\u662F\u81EA\u5DF1\u7684\u5C0F\u65E5\u5B50\uFF0C\u7236\u6BCD\u4E4B\u95F4\u6709\u673A\u4F1A\u89C1\u9762\u5C31\u89C1\u9762\uFF0C\u6CA1\u6709\u673A\u4F1A\u89C1\u9762\u5C31\u53EF\u4EE5\u5F53\u4E0D\u8BA4\u8BC6\u3002\u5468\u84C9\u89C9\u5F97\u90DD\u51AC\u6885\u5E94\u8BE5\u628A\u8336\u53F6\u7ED9\u6627\u4E0B\uFF0C\u8FD9\u6837\u4EC0\u4E48\u6BDB\u75C5\u90FD\u4E0D\u4F1A\u6709\u4E86\uFF0C\u4F46\u662F\u5468\u5FD7\u521A\u8BF4\u51AC\u6885\u662F\u5FC3\u773C\u5B9E\u5728\uFF0C\u5176\u5B9E\u4E5F\u662F\u7406\u89E3\u90DD\u51AC\u6885\u7684\u3002\u90DD\u51AC\u6885\u54ED\u7740\u56DE\u5230\u623F\u95F4\u91CC\uFF0C\u5468\u79C9\u4E49\u5B89\u6170\u90DD\u51AC\u6885\uFF0C\u4ED6\u4EEC\u5206\u5F00\u4EFB\u52A1\u5404\u81EA\u5904\u7406\u597D\u5404\u81EA\u5BB6\u5EAD\u7684\u95EE\u9898\uFF0C\u80FD\u5C3D\u5FEB\u7684\u7F13\u548C\u4E24\u5BB6\u7684\u5173\u7CFB\u8FD9\u624D\u662F\u6700\u91CD\u8981\u7684\u3002\u90DD\u51AC\u6885\u70B9\u5934\u540C\u610F\uFF0C\u4E5F\u8868\u793A\u5BF9\u5468\u79C9\u4E49\u7684\u8BDD\u5F88\u8D5E\u540C\u3002</p><p>\u9A86\u58EB\u5BBE\u5E26\u7740\u840C\u840C\u6765\u5230\u5409\u6625\uFF0C\u5927\u8857\u4E0A\u770B\u5230\u4E86\u81EA\u5DF1\u7684\u513F\u5B50\uFF0C\u9A86\u58EB\u5BBE\u60F3\u8D77\u4E86\u90A3\u4E2A\u5A74\u513F\uFF0C\u4F46\u662F\u601D\u6765\u60F3\u53BB\u5E76\u6CA1\u6709\u4E0A\u524D\u53BB\u95EE\u8BE2\u3002</p><p>\u6B21\u65E5\uFF0C\u90DD\u51AC\u6885\u4E3B\u52A8\u548C\u6BCD\u4EB2\u6C42\u548C\uFF0C\u90DD\u6BCD\u5FC3\u91CC\u81EA\u8D23\u770B\u4E86\u4E00\u4E0B\u5973\u513F\u7684\u8138\u988A\uFF0C\u773C\u770B\u7740\u90DD\u7701\u957F\u5C31\u8981\u4E0B\u697C\u4E86\uFF0C\u90DD\u51AC\u6885\u751F\u6015\u88AB\u7236\u4EB2\u770B\u51FA\u773C\u775B\u80BF\u7740\u3002\u90DD\u6BCD\u5B89\u6170\u7740\u5973\u513F\uFF0C\u90DD\u51AC\u6885\u4E5F\u8FDE\u8FDE\u5411\u6BCD\u4EB2\u8BA4\u9519\uFF0C\u6BCD\u5973\u4FE9\u7ADF\u7136\u62B1\u5934\u75DB\u54ED\u8D77\u6765\uFF0C\u4E3A\u4E86\u4E0D\u8BA9\u90DD\u7701\u957F\u770B\u51FA\u6765\u4E24\u4EBA\u90FD\u8D76\u7D27\u64E6\u5E72\u773C\u6CEA\u9732\u51FA\u7B11\u8138\u3002</p><p>\u5468\u79C9\u4E49\u56DE\u5230\u5BB6\u91CC\u5305\u997A\u5B50\uFF0C\u5468\u6BCD\u60E6\u8BB0\u90DD\u51AC\u6885\uFF0C\u5468\u79C9\u4E49\u8D76\u7D27\u89E3\u91CA\u56E0\u4E3A\u51AC\u6885\u5BB6\u91CC\u662F\u72EC\u751F\u5973\uFF0C\u6240\u4EE5\u6CA1\u6709\u56DE\u6765\u3002\u5468\u6BCD\u4E5F\u90FD\u8868\u793A\u7406\u89E3\uFF0C\u4F46\u5B9E\u9645\u4E0A\u6BCF\u4E2A\u4EBA\u5FC3\u91CC\u90FD\u6709\u70B9\u7EA0\u7ED3\u3002\u90DD\u51AC\u6885\u4E5F\u5C06\u81EA\u5DF1\u4E0D\u80FD\u751F\u80B2\u7684\u4E8B\u60C5\u544A\u8BC9\u4E86\u7236\u6BCD\uFF0C\u90DD\u7701\u957F\u63D0\u51FA\u8981\u53BB\u5927\u533B\u9662\u68C0\u67E5\u770B\u770B\uFF0C\u4F46\u90DD\u51AC\u6885\u5374\u544A\u8BC9\u7236\u4EB2\u5317\u4EAC\u4E13\u5BB6\u6BCD\u4EB2\u90FD\u8054\u7CFB\u8FC7\u4E86\uFF0C\u4E5F\u90FD\u770B\u8FC7\u4E86\uFF0C\u662F\u4E00\u76F4\u770B\u4E0D\u597D\u3002\u90DD\u7701\u957F\u4E0D\u5F00\u5FC3\u7684\u5750\u5230\u4E00\u8FB9\u53BB\uFF0C\u90DD\u6BCD\u8D23\u602A\u90DD\u51AC\u6885\u4E0D\u8BE5\u5728\u4ECA\u5929\u63D0\u8D77\u8FD9\u4EF6\u4E8B\uFF0C\u90DD\u7701\u957F\u751F\u6015\u90DD\u51AC\u6885\u4E0D\u5F00\u5FC3\u8D76\u7D27\u8FC7\u53BB\u5B89\u6170\uFF0C\u4E5F\u81EA\u8D23\u662F\u4ED6\u4EEC\u7684\u4E8B\u60C5\u8FDE\u7D2F\u4E86\u90DD\u51AC\u6885\u3002</p><p>\u90DD\u51AC\u6885\u544A\u8BC9\u7236\u6BCD\uFF0C\u5468\u79C9\u4E49\u5BB6\u91CC\u5E76\u4E0D\u77E5\u9053\u8FD9\u4EF6\u4E8B\uFF0C\u800C\u4E14\u5468\u79C9\u4E49\u5BB6\u91CC\u5DF2\u7ECF\u6709\u4E24\u4E2A\u5B69\u5B50\u4E86\uFF0C\u4E0D\u5DEE\u4ED6\u8FD9\u4E00\u4E2A\u3002\u5982\u679C\u6709\u4E00\u5929\u771F\u7684\u88AB\u95EE\u8D77\u6765\uFF0C\u5468\u79C9\u4E49\u4E5F\u4F1A\u544A\u8BC9\u5BB6\u91CC\u662F\u4ED6\u7684\u6BDB\u75C5\u4E0D\u4F1A\u751F\u80B2\uFF0C\u8FD9\u4EF6\u4E8B\u5BF9\u4E8E\u90DD\u7701\u957F\u592B\u5987\u7684\u89E6\u52A8\u6BD4\u8F83\u5927\uFF0C\u5E74\u4E09\u5341\u90FD\u662F\u5728\u6C89\u9ED8\u4E2D\u5EA6\u8FC7\u7684\u3002</p><p>\u5927\u5E74\u521D\u4E00\uFF0C\u5468\u79C9\u4E49\u5E26\u7740\u90DD\u51AC\u6885\u56DE\u53BB\uFF0C\u5468\u5FD7\u521A\u5E26\u7740\u5168\u5BB6\u4EBA\u53BB\u7ED9\u8857\u574A\u90BB\u5C45\u62DC\u5E74\uFF0C\u5927\u5BB6\u5BF9\u5468\u5BB6\u7684\u5B69\u5B50\u90FD\u79F0\u8D5E\u6709\u52A0\uFF0C\u4E5F\u90FD\u79F0\u8D5E\u5468\u5FD7\u521A\u662F\u6559\u5B50\u6709\u65B9\uFF0C\u8FD9\u8BA9\u5468\u5FD7\u521A\u592B\u5987\u5F88\u6709\u9762\u5B50\u3002\u53EF\u662F\u4E54\u5927\u5A76\u5374\u53E3\u4E0D\u62E9\u8A00\u8BF4\u90D1\u5A1F\u662F\u672A\u5A5A\u5148\u5B55\uFF0C\u4E00\u53E5\u8BDD\u60F9\u6012\u4E86\u5468\u5FD7\u521A\uFF0C\u5468\u5FD7\u521A\u8F6C\u8FC7\u5934\u53BB\u72E0\u72E0\u8BAD\u65A5\u4E86\u4E54\u5927\u5A76\u3002\u5468\u79C9\u6606\u56E0\u4E3A\u6CA1\u6709\u592A\u5927\u51FA\u606F\uFF0C\u4E5F\u6210\u4E3A\u4E86\u5927\u5BB6\u6570\u843D\u7684\u5BF9\u8C61\uFF0C\u5468\u84C9\u5B89\u6170\u5468\u79C9\u6606\u4E0D\u8981\u548C\u90A3\u4E9B\u5C0F\u5E02\u6C11\u8BA1\u8F83\uFF0C\u5468\u84C9\u4E5F\u81EA\u8D23\u90FD\u662F\u4ED6\u4EEC\u62D6\u7D2F\u4E86\u5468\u79C9\u6606\uFF0C\u5468\u79C9\u6606\u5BF9\u6B64\u5012\u662F\u4E0D\u4ECB\u610F\u3002</p><p>\u5468\u79C9\u4E49\u548C\u90DD\u7701\u957F\u6253\u4E52\u4E53\u7403\uFF0C\u4E24\u4EBA\u73A9\u5F97\u90FD\u5F88\u5C3D\u5174\u3002\u56DE\u53BB\u7684\u8DEF\u4E0A\uFF0C\u5468\u79C9\u4E49\u4E5F\u8BF4\u4E86\u81EA\u5DF1\u6253\u7B97\u8FB9\u5DE5\u4F5C\u8FB9\u8BFB\u7814\u7A76\u751F\uFF0C\u90DD\u7701\u957F\u5BF9\u6B64\u5F88\u8D5E\u540C\uFF0C\u540C\u65F6\u90DD\u7701\u957F\u4E5F\u81EA\u8D23\u90FD\u662F\u56E0\u4E3A\u4ED6\u4EEC\u5BFC\u81F4\u90DD\u51AC\u6885\u4E0D\u4F1A\u751F\u80B2\u3002\u5468\u79C9\u4E49\u5BF9\u6B64\u8868\u793A\u4E0D\u4ECB\u610F\uFF0C\u5373\u4FBF\u6CA1\u6709\u5B69\u5B50\u4E5F\u4E0D\u4F1A\u5F71\u54CD\u4E24\u4EBA\u7684\u611F\u60C5\uFF0C\u90DD\u7701\u957F\u5BF9\u5468\u79C9\u4E49\u591A\u4E86\u4E00\u4E9B\u6B49\u610F\u3002</p><p>\u56FD\u5E86\u548C\u5434\u5029\u3001\u5468\u79C9\u6606\u7B49\u4EBA\u90FD\u805A\u96C6\u5728\u4E86\u4E54\u6625\u71D5\u8FD9\u91CC\uFF0C\u6BCF\u5E74\u805A\u4E00\u6B21\u7684\u65F6\u5019\u672C\u6765\u5E94\u8BE5\u8BF4\u4E00\u4E9B\u5F00\u5FC3\u7684\u4E8B\u60C5\u3002\u53EF\u662F\u8BF4\u51FA\u6765\u90FD\u662F\u7CDF\u5FC3\u7684\u4E8B\u60C5\uFF0C\u5434\u5029\u8BA4\u4E3A\u4E00\u5BB6\u516B\u53E3\u4EBA\u4F4F\u5728\u5DF4\u638C\u5927\u7684\u5730\u65B9\u8FD8\u4E0D\u5982\u6536\u5BB9\u6240\uFF0C\u5434\u5029\u7FA1\u6155\u4E54\u6625\u71D5\u73B0\u5728\u7684\u4F4F\u5904\u3002\u4F46\u4E54\u6625\u71D5\u5374\u8BF4\u51FA\u4E86\u81EA\u5DF1\u7684\u8F9B\u9178\uFF0C\u5979\u88AB\u4EBA\u6B3A\u8D1F\u7684\u65F6\u5019\u4E8E\u8679\u8FD8\u5E2E\u7740\u6253\u67B6\uFF0C\u4ECE\u4E3B\u4EFB\u7684\u4F4D\u7F6E\u4E0B\u6765\u53BB\u4FEE\u811A\uFF0C\u90A3\u79CD\u6ECB\u5473\u522B\u63D0\u4E86\u3002\u73B0\u5728\u505A\u4E86\u533A\u5987\u8054\u7684\u526F\u4E3B\u4EFB\uFF0C\u4E54\u6625\u71D5\u8BA4\u4E3A\u81EA\u5DF1\u968F\u65F6\u90FD\u4F1A\u88AB\u8E29\u4E0B\u53BB\u3002</p><p>\u56FD\u5E86\u5E0C\u671B\u80FD\u8BA4\u8BC6\u4E00\u4E9B\u6709\u80FD\u529B\u7684\u4EBA\uFF0C\u6700\u597D\u6CD5\u9662\u80FD\u6709\u4EBA\uFF0C\u518D\u6709\u8BA4\u8BC6\u50CF\u662F\u9F9A\u4F1F\u5219\u90A3\u6837\u7684\u8B66\u5BDF\uFF0C\u8FD8\u6709\u5468\u84C9\u90A3\u6837\u6709\u6587\u5316\u7684\u4EBA\uFF0C\u5982\u679C\u80FD\u518D\u6709\u4E00\u4E2A\u533B\u9662\u7684\u9662\u957F\u5C31\u66F4\u597D\u4E86\uFF0C\u80FD\u7ED3\u8BC6\u4E00\u4E2A\u4E07\u5143\u6237\u66F4\u597D\u4E86\uFF0C\u5927\u5BB6\u7B11\u8BDD\u56FD\u5E86\u4E5F\u53EA\u80FD\u662F\u5728\u68A6\u91CC\u60F3\u60F3\u3002\u66F9\u5FB7\u5B9D\u8BA4\u4E3A\u4EE5\u524D\u516D\u541B\u5B50\u5F88\u597D\uFF0C\u53EF\u662F\u73B0\u5728\u5415\u5DDD\u3001\u5411\u9633\u4E0A\u4E86\u5927\u5B66\u6BCF\u5E74\u7684\u805A\u4F1A\u90FD\u4E0D\u6765\u4E86\u3002</p><p>\u5468\u79C9\u6606\u770B\u770B\u5927\u5BB6\u90FD\u60C5\u7EEA\u4E0D\u5BF9\uFF0C\u5E72\u8106\u63D0\u51FA\u53BB\u770B\u770B\u66F2\u79C0\u73CD\u3002</p>',13),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","18.html.vue"]]);export{h as default};
