import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as t,f as r}from"./app.86bca16c.js";const n={},s=r('<h1 id="\u664B\u7EAA\u4E00-\u670B\u515A" tabindex="-1"><a class="header-anchor" href="#\u664B\u7EAA\u4E00-\u670B\u515A" aria-hidden="true">#</a> \u664B\u7EAA\u4E00 \u670B\u515A</h1><blockquote><p>\u5145\u4E0E\u4F8D\u4E2D\u4EFB\u607A\u7686\u4E3A\u5E1D\u6240\u5BA0\u4EFB\uFF0C\u5145\u6B32\u4E13\u540D\u52BF\uFF0C\u800C\u5FCC\u607A\uFF0C\u4E8E\u662F\u671D\u58EB\u5404\u6709\u6240\u9644\uFF0C\u670B\u515A\u7EB7\u7136\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\u2022\u5377\u4E03\u5341\u4E5D\u2022\u664B\u7EAA\u4E00\u300B</strong></p><p>\u4ECA\u5929\u6765\u8BB2\u4E00\u4E2A\u5B98\u573A\u503E\u8F67\u7684\u6545\u4E8B\u3002</p><p>\u5386\u53F2\u4E2D\u7684\u771F\u5B9E\u4E8B\u4EF6\uFF0C\u770B\u4E0A\u53BB\u8981\u201C\u6734\u5B9E\u65E0\u534E\u201D\u5F97\u591A\u3002\u4F46\u6070\u6070\u8FD9\u79CD\u4E0D\u52A8\u58F0\u8272\u7684\u9759\u6C34\u6DF1\u6D41\uFF0C<strong>\u624D\u662F\u90A3\u4E9B\u8EAB\u5728\u98CE\u66B4\u4E2D\u5FC3\u7684\u4EBA\uFF0C\u5B9E\u5B9E\u5728\u5728\u7528\u8840\u4E0E\u547D\u6597\u51FA\u6765\u7684</strong>\u3002</p><p>\u6545\u4E8B\u7684\u4E3B\u89D2\uFF0C\u662F\u664B\u6B66\u5E1D\u53F8\u9A6C\u708E\u7684\u4E24\u4E2A\u4EB2\u4FE1\u5927\u81E3\uFF1A<strong>\u4F8D\u4E2D\u3001\u5C1A\u4E66\u4EE4\u3001\u8F66\u9A91\u5C06\u519B<code>\u8D3E\u5145</code></strong>\uFF0C\u4EE5\u53CA<strong>\u4F8D\u4E2D<code>\u4EFB\u607A</code></strong>\u3002</p><p>\u4E4D\u770B\u4E4B\u4E0B\uFF0C\u8D3E\u5145\u5B98\u804C\u4F17\u591A\uFF0C\u5E94\u8BE5\u5B8C\u5168\u78BE\u538B\u4EFB\u607A\u624D\u5BF9\u3002\u4F46\u4E8B\u5B9E\u662F\uFF0C\u65E9\u4E9B\u5E74\u95F4\u4E24\u4EBA\u5404\u6709\u670B\u515A\uFF0C\u6597\u5F97\u65D7\u9F13\u76F8\u5F53\uFF0C\u4EFB\u607A\u8FD8\u4E00\u5EA6\u5360\u636E\u4E0A\u98CE\uFF0C\u5DEE\u70B9\u5C31\u5C06\u8D3E\u5145\u6392\u6324\u51FA\u4E86\u671D\u5EF7\u3002</p><p>\u800C\u4E24\u4EBA\u4E4B\u6240\u4EE5\u4F1A\u6597\u8D77\u6765\uFF0C\u8FD8\u6597\u5F97\u96BE\u89E3\u96BE\u5206\uFF0C\u5F88\u5927\u7A0B\u5EA6\u4E0A\u5C31\u662F\u56E0\u4E3A\u4ED6\u4EEC\u62C5\u4EFB\u4E86\u76F8\u540C\u7684\u804C\u4F4D\uFF1A</p><blockquote><p>\u4F8D\u4E2D</p></blockquote><p>\u5173\u4E8E\u4F8D\u4E2D\u7684\u804C\u8D23\uFF0C\u300A\u664B\u4E66\u300B\u662F\u8FD9\u4E48\u89E3\u91CA\u7684\uFF1A\u201C\u5907\u5207\u95EE\u8FD1\u5BF9\uFF0C\u62FE\u9057\u8865\u9619\u201D\u3002\u8FD9\u4E5D\u4E2A\u5B57\uFF0C\u6709\u4E03\u4E2A\u5B57\u90FD\u662F\u5E9F\u8BDD\uFF0C\u53EA\u6709\u201C<code>\u8FD1\u5BF9</code>\u201D\u4E24\u4E2A\u5B57\u624D\u662F\u6838\u5FC3\u3002\u6240\u8C13\u201C\u8FD1\u5BF9\u201D\uFF0C\u5C31\u662F\u8BF4\u4F8D\u4E2D\u662F\u53EF\u4EE5\u5728\u7687\u5E1D\u8EAB\u8FB9\u8DDF\u7687\u5E1D\u8BA8\u8BBA\u671D\u653F\u7684\u4EBA\u3002</p><p>\u8DDD\u79BB\u6743\u529B\u6838\u5FC3\u8D8A\u8FD1\uFF0C\u672C\u8EAB\u5C31\u610F\u5473\u7740\u6743\u529B\u3002\u8D3E\u5145\u4E4B\u6240\u4EE5\u4F4D\u9AD8\u6743\u91CD\uFF0C\u4EFB\u607A\u4E4B\u6240\u4EE5\u80FD\u8DDF\u8D3E\u5145\u5E73\u8D77\u5E73\u5750\uFF0C\u90FD\u5728\u4E8E\u4E24\u4EBA\u540C\u65F6\u62C5\u4EFB\u4E86\u4F8D\u4E2D\u7684\u804C\u4F4D\u3002</p><p><strong>\u4F46\u8FD9\u4E24\u4F4D\u4F8D\u4E2D\u5374\u4E92\u76F8\u770B\u4E0D\u987A\u773C</strong>\u3002</p><blockquote><p>\u8FD9\u8DDF\u4E24\u4E2A\u4EBA\u4E0D\u540C\u7684\u664B\u5347\u8DEF\u5F84\u6709\u5173\uFF1A</p><p>\u8D3E\u5145\u4ECE\u53F8\u9A6C\u5E08\u65F6\u4EE3\u5F00\u59CB\u5C31\u8DDF\u7740\u53F8\u9A6C\u6C0F\uFF0C\u4E4B\u540E\u53C8\u7ECF\u5386\u53F8\u9A6C\u662D\u3001\u53F8\u9A6C\u708E\u4E24\u4EE3\uFF0C\u59CB\u7EC8\u4EFB\u52B3\u4EFB\u6028\uFF0C\u5FE0\u5FC3\u803F\u803F\u3002\u800C\u4E14\uFF0C\u8FD8\u6709\u5F11\u6740\u9B4F\u5E1D\u66F9\u9AE6\u8FD9\u6837\u7684\u201C\u5149\u8F89\u6218\u7EE9\u201D\u52A0\u6301\uFF0C\u53EF\u4EE5\u8BF4\u662F\u53F8\u9A6C\u6C0F\u4EB2\u4FE1\u4E2D\u7684\u4EB2\u4FE1\u3002</p><p>\u4EFB\u607A\u5219\u662F\u9B4F\u660E\u5E1D\u66F9\u53E1\u7684\u5973\u5A7F\uFF0C\u7B97\u8D77\u6765\u8DDF\u66F9\u9AE6\u662F\u4E00\u8F88\u3002\u4F46\u4EFB\u607A\u6709\u7ECF\u56FD\u4E4B\u5FD7\uFF0C\u773C\u770B\u65F6\u5C40\u5DF2\u7136\u5982\u6B64\uFF0C\u5E76\u4E0D\u613F\u610F\u56E0\u4E3A\u59FB\u4EB2\u7684\u5173\u7CFB\u5C31\u4E3A\u65E7\u653F\u6743\u6B89\u846C\uFF0C\u800C\u662F\u6BC5\u7136\u6295\u5165\u4E86\u53F8\u9A6C\u6C0F\u7684\u9635\u8425\uFF0C\u88AB\u59D4\u4EE5\u91CD\u4EFB\u3002</p><p>\u4EFB\u607A\u80FD\u83B7\u5F97\u53F8\u9A6C\u662D\u3001\u53F8\u9A6C\u708E\u7236\u5B50\u7684\u770B\u4E2D\uFF0C\u4E3B\u8981\u662F\u56E0\u4E3A\u624D\u5E72\u3002\u5F53\u65F6\u7684\u98CE\u8BC4\u8BA4\u4E3A\uFF0C\u4EFB\u607A\u6709\u201C\u4F50\u4E16\u5668\u5C40\u201D\uFF0C\u5177\u5907\u5321\u4F50\u65F6\u4E8B\u7684\u80FD\u529B\u548C\u6C14\u5EA6\u3002</p></blockquote><p>\u672C\u6765\uFF0C\u8D3E\u5145\u5BF9\u4E8E\u4EFB\u607A\u7684\u5B58\u5728\uFF0C\u65E0\u53EF\u65E0\u4E0D\u53EF\u3002\u53CD\u6B63\u5927\u5BB6\u90FD\u662F\u7ED9\u53F8\u9A6C\u5BB6\u6253\u5DE5\u7684\uFF0C\u53EA\u8981\u9762\u5B50\u4E0A\u8FC7\u5F97\u53BB\u5C31\u884C\u4E86\uFF0C\u54EA\u91CC\u4F1A\u6709\u4EC0\u4E48\u6DF1\u4EC7\u5927\u6068\u3002</p><p><strong>\u4F46\u4EFB\u607A\u5374\u770B\u8D3E\u5145\u788D\u773C\u2014\u2014\u4E3B\u8981\u662F\u89C9\u5F97\u4ED6\u9053\u5FB7\u5815\u843D\uFF0C\u4EBA\u54C1\u8D25\u574F</strong>\u3002\u5F53\u7136\uFF0C\u8FD9\u5E76\u4E0D\u662F\u4EFB\u607A\u4E00\u4E2A\u4EBA\u7684\u89C2\u70B9\uFF0C\u5F53\u65F6\u8DDF\u4ED6\u6301\u76F8\u540C\u770B\u6CD5\u7684\u5927\u6709\u4EBA\u5728\u3002\u6709\u4EBA\u751A\u81F3\u76F4\u63A5\u5BF9\u664B\u6B66\u5E1D\u8BF4\uFF0C\u60A8\u77E5\u9053\u5982\u4ECA\u6BD4\u5C27\u821C\u4E4B\u4E16\u5DEE\u5728\u54EA\u91CC\u5417\uFF1F\u5C31\u5728\u4E8E\u6709\u8D3E\u5145\u4E00\u6837\u7684\u5978\u81E3\u5F53\u9053\uFF0C\u624D\u8BA9\u6B63\u76F4\u7684\u4EBA\u6CA1\u6709\u4E86\u664B\u5347\u7684\u673A\u4F1A\u3002</p><p>\u4EFB\u607A\u5F88\u8D5E\u540C\u8FD9\u79CD\u8BC4\u4EF7\uFF0C\u6B64\u65F6\u7684\u4ED6\u53EF\u80FD\u8FD8\u6CA1\u6709\u610F\u8BC6\u5230\uFF0C\u8FD9\u53E5\u8BDD\u6700\u540E\u4F1A\u53D8\u6210\u4E00\u9053\u53EF\u6015\u7684\u8C36\u8BED\uFF0C\u51C6\u51C6\u7684\u624E\u5230\u81EA\u5DF1\u8EAB\u4E0A\u3002</p><p>\u603B\u4E4B\u4EFB\u607A\u6CA1\u5C11\u5728\u664B\u6B66\u5E1D\u9762\u524D\u8BF4\u8D3E\u5145\u7684\u4E0D\u662F\uFF0C\u5177\u4F53\u8BF4\u4EC0\u4E48\u4E86\uFF0C\u53F2\u6599\u6CA1\u6709\u8BB0\u8F7D\uFF0C\u4F46\u8FD9\u4E9B\u8BAE\u8BBA\u4E0D\u65AD\u7D2F\u79EF\uFF0C\u7EC8\u4E8E\u5F15\u8D77\u4E86\u8D3E\u5145\u7684\u60F6\u6050\uFF1A\u6709\u4EBA\u4E00\u5929\u5230\u665A\u5728\u9886\u5BFC\u9762\u524D\u8BF4\u4F60\u7684\u4E0D\u662F\uFF0C\u65F6\u95F4\u4E00\u957F\uFF0C\u5C31\u7B97\u662F\u5047\u7684\u4E5F\u4F1A\u53D8\u6210\u771F\u7684\u3002</p><p>\u4EBA\u65E0\u4F24\u864E\u610F\uFF0C\u864E\u6709\u5BB3\u4EBA\u5FC3\u3002\u4E8B\u5DF2\u81F3\u6B64\uFF0C\u8D3E\u5145\u53EA\u80FD\u8BBE\u6CD5\u89E3\u51B3\u8FD9\u4E2A\u9EBB\u70E6\uFF0C<strong>\u4ED6\u60F3\u5230\u7684\u529E\u6CD5\uFF0C\u662F\u628A\u4EFB\u607A\u8D76\u8D70</strong>\u2014\u2014\u53EA\u8981\u80FD\u89E3\u53BB\u4EFB\u607A\u7684\u4F8D\u4E2D\u4E00\u804C\uFF0C\u8BA9\u4ED6\u4E0D\u80FD\u7ECF\u5E38\u89C1\u5230\u7687\u5E1D\uFF0C\u90A3\u4E48\u81EA\u7136\u4E5F\u5C31\u6D88\u505C\u4E86\u3002</p><p>\u56E0\u6B64\uFF0C\u5728\u67D0\u6B21\u664B\u6B66\u5E1D\u8DDF\u8D3E\u5145\u804A\u8D77\u592A\u5B50\u2014\u2014\u4E5F\u5C31\u662F\u90A3\u4E2A\u50BB\u513F\u5B50\u53F8\u9A6C\u8877\u2014\u2014\u7684\u65F6\u5019\uFF0C\u8D3E\u5145\u8D81\u673A\u8DDF\u664B\u6B66\u5E1D\u8BF4\uFF0C\u4EFB\u607A\u5FE0\u8D1E\uFF0C\u53EF\u4EE5\u8F85\u4F50\u592A\u5B50\u3002</p><p>\u8D3E\u5145\u7684\u5982\u610F\u7B97\u76D8\u662F\uFF0C\u4E0D\u52A8\u58F0\u8272\u7684\u7ED9\u4EFB\u607A\u632A\u4E2A\u7A9D\u3002</p><p>\u8FD9\u756A\u516C\u5FE0\u4F53\u56FD\u7684\u8BDD\uFF0C\u5F88\u5BF9\u664B\u6B66\u5E1D\u7684\u80C3\u53E3\u3002\u4E0D\u4E45\uFF0C\u4EFB\u607A\u4FBF\u88AB\u4EFB\u547D\u4E3A\u592A\u5B50\u5C11\u5085\uFF0C\u4F46\u8BA9\u8D3E\u5145\u50BB\u773C\u7684\u662F\uFF0C\u201C<strong>\u800C\u4F8D\u4E2D\u5982\u6545</strong>\u201D\u3002\u4EFB\u607A\u53EA\u662F\u517C\u4E86\u592A\u5B50\u5C11\u5085\u4E00\u804C\uFF0C\u5E76\u4E0D\u662F\u4E13\u804C\uFF0C\u4F8D\u4E2D\u7684\u5DE5\u4F5C\u8FD8\u7167\u65E7\u5E72\u3002</p><p>\u8D3E\u5145\u7B2C\u4E00\u56DE\u5408\u51FA\u51FB\u5931\u8D25\u3002</p><p>\u8F6E\u5230\u4EFB\u607A\u8FD8\u51FB\u4E86\u3002</p><p>\u6765\u800C\u4E0D\u5F80\u975E\u793C\u4E5F\uFF0C\u4EFB\u607A\u60F3\u5230\u7684\u529E\u6CD5\u4E5F\u662F\u628A\u8D3E\u5145\u7ED9\u8D76\u8D70\u3002\u5F53\u65F6\uFF0C\u96CD\u51C9\u4E00\u5E26\u6709\u9C9C\u5351\u4F5C\u4E71\uFF0C\u671D\u5EF7\u5F88\u662F\u5934\u75BC\u3002\u4EFB\u607A\u56E0\u6B64\u5EFA\u8BAE\u8BF4\uFF0C\u201C<strong>\u5B9C\u5F97\u5A01\u671B\u91CD\u81E3\u6709\u667A\u7565\u8005\u4EE5\u9547\u629A\u4E4B</strong>\u201D\uFF0C\u5F97\u6D3E\u4E00\u4E2A\u53C8\u6709\u5A01\u671B\u53C8\u667A\u8C0B\u51FA\u4F17\u7684\u91CD\u81E3\u53BB\u9547\u5B88\u624D\u884C\u3002</p><p>\u664B\u6B66\u5E1D\u6DF1\u4EE5\u4E3A\u7136\uFF0C\u7136\u540E\u8FFD\u95EE\uFF0C\u8C01\u5408\u9002\u5462\uFF1F</p><p>\u4EFB\u607A\u8BF4\uFF0C\u5F53\u7136\u662F\u8D3E\u5145\u4E86\u3002</p><p>\u6B66\u5E1D\u8BF4\uFF0C\u597D\uFF01</p><p>\u8D3E\u5145\u8FD9\u4E0B\u5F7B\u5E95\u61F5\u4E86\u3002\u672C\u6765\u662F\u60F3\u7740\u628A\u4EFB\u607A\u7ED9\u6324\u8D70\u7684\uFF0C\u8C01\u77E5\u9053\u4EBA\u5BB6\u529B\u5927\u8EAB\u6C89\uFF0C\u6324\u4E86\u6324\u6CA1\u6324\u52A8\uFF0C\u53CD\u5012\u81EA\u5DF1\u88AB\u6324\u51FA\u5C40\u4E86\u3002</p><p>\u614C\u4E86\u795E\u7684\u8D3E\u5145\u50CF\u6CA1\u5934\u82CD\u8747\u4E00\u6837\u5230\u5904\u60F3\u529E\u6CD5\uFF0C\u5E0C\u671B\u664B\u6B66\u5E1D\u80FD\u6536\u56DE\u6210\u547D\u3002\u4F46\u6B66\u5E1D\u5FC3\u610F\u5DF2\u51B3\uFF0C\u4E00\u5FC3\u8BA4\u4E3A\u6B64\u884C\u975E\u8D3E\u5145\u83AB\u5C5E\uFF0C\u4E00\u5B9A\u8981\u4ED6\u53BB\u957F\u5B89\u9547\u5B88\u3002</p><p>\u6700\u540E\uFF0C\u8FD8\u662F\u522B\u4EBA\u7ED9\u8D3E\u5145\u51FA\u4E86\u4E2A\u4E3B\u610F\uFF1A\u4F60\u8981\u4E0D\u628A\u5973\u513F\u5AC1\u7ED9\u50BB\u592A\u5B50\u5427\u3002\u603B\u4E4B\uFF0C\u8D3E\u5145\u8FD8\u662F\u60F3\u529E\u6CD5\u7559\u5728\u4E86\u6D1B\u9633\u3002</p><p>\u8FD9\u4E00\u56DE\u5408\u4EA4\u950B\uFF0C\u5E73\u624B\u3002</p><p>\u8D3E\u5145\u548C\u4EFB\u607A\u7684\u8FD9\u79CD\u660E\u4E89\u6697\u6597\uFF0C\u641E\u5F97\u6574\u4E2A\u671D\u5EF7\u90FD\u4E4C\u70DF\u7634\u6C14\uFF0C\u5927\u81E3\u4EEC\u53EA\u80FD\u4E3B\u52A8\u6216\u88AB\u8FEB\u7684\u9009\u62E9\u7AD9\u961F\uFF0C\u515A\u540C\u4F10\u5F02\u3002</p><p>\u664B\u6B66\u5E1D\u5934\u75BC\u4E0D\u5DF2\uFF0C\u60F3\u7F13\u548C\u4E00\u4E0B\u4E24\u4EBA\u7684\u77DB\u76FE\u3002\u4ED6\u7684\u505A\u6CD5\u5F88\u6709\u4EBA\u60C5\u5473\uFF1A\u6512\u4E86\u4E2A\u996D\u5C40\u2014\u2014\u6CA1\u6709\u5973\u5B66\u751F\u4F5C\u966A\u7684\u90A3\u79CD\uFF0C\u8BF7\u8D3E\u5145\u548C\u4EFB\u607A\u4E00\u8D77\u6765\u5403\u996D\u3002</p><p>\u996D\u5C40\u4E0A\uFF0C\u6B66\u5E1D\u548C\u989C\u60A6\u8272\u7684\u7AEF\u8D77\u4E86\u9152\u676F\uFF0C\u8DDF\u4E24\u4EBA\u8BF4\uFF1A<strong>\u671D\u5EF7\u5E94\u8BE5\u56E2\u7ED3\u4E00\u81F4\uFF0C\u5927\u81E3\u4E4B\u95F4\u8FD8\u662F\u8981\u4EE5\u548C\u4E3A\u8D35\u7684</strong>\u3002</p><p>\u7687\u4E0A\u90FD\u8BF4\u5230\u8FD9\u4E2A\u4EFD\u4E0A\u4E86\uFF0C\u4E24\u4EBA\u5F53\u7136\u53EA\u80FD\u8D77\u8EAB\u544A\u7F6A\uFF0C\u4E00\u81F4\u8868\u793A\u8C28\u9075\u7687\u4E0A\u7684\u65E8\u610F\uFF0C\u4EE5\u540E\u4E00\u5B9A\u56E2\u7ED3\u4E00\u81F4\u5411\u524D\u770B\uFF0C\u5FC3\u5F80\u4E00\u5904\u60F3\u52B2\u5F80\u4E00\u5904\u4F7F\uFF0C\u52AA\u529B\u5EFA\u8BBE\u98CE\u6E05\u6C14\u6B63\u7684\u653F\u6CBB\u73AF\u5883\uFF0C\u4E3A\u5927\u664B\u7684\u957F\u6CBB\u4E45\u5B89\u4E0D\u61C8\u594B\u6597\u3002</p><p>\u8BDD\u8BF4\u5F97\u597D\u597D\u7684\uFF0C\u7136\u800C\u6563\u4E86\u5C40\uFF0C\u4E24\u4E2A\u4EBA\u5374\u6597\u5F97\u66F4\u72E0\u4E86\u3002\u8FD9\u987F\u996D\u8BA9\u4E24\u4E2A\u4EBA\u9886\u4F1A\u5230\u7684\uFF0C\u5E76\u4E0D\u662F\u56E2\u7ED3\u594B\u6597\u7684\u603B\u8981\u6C42\uFF0C\u800C\u662F\u7687\u5E1D\u7684\u5FC3\u6148\u624B\u8F6F\u2014\u2014<strong>\u660E\u77E5\u4E24\u4EBA\u515A\u4E89\uFF0C\u7ADF\u7136\u6CA1\u6709\u8D23\u7F5A</strong>\u3002</p><p>\u7687\u5E1D\u662F\u8FD9\u4E48\u4E2A\u4E3B\u65E8\uFF0C\u90A3\u63A5\u4E0B\u6765\u5C31\u597D\u529E\u4E86\u3002\u53F2\u8F7D\uFF0C\u4E24\u4EBA\u201C<strong>\u6108\u65E0\u6240\u60EE\uFF0C\u5916\u76F8\u5D07\u91CD\uFF0C\u5185\u6028\u76CA\u6DF1</strong>\u201D\u3002</p><p>\u6700\u7EC8\uFF0C\u8D3E\u5145\u8FD8\u662F\u60F3\u51FA\u4E86\u8D76\u8D70\u4EFB\u607A\u7684\u529E\u6CD5\u2014\u2014\u800C\u4E14\u8FD9\u4E2A\u601D\u8DEF\uFF0C\u5F88\u53EF\u80FD\u8FD8\u662F\u4EFB\u607A\u81EA\u5DF1\u63D0\u4F9B\u7684\u3002</p><blockquote><p><strong>\u4E0A\u6B21\u4E24\u4EBA\u4E92\u6324\uFF0C\u4EFB\u607A\u5DEE\u70B9\u6210\u529F\u800C\u8D3E\u5145\u5B8C\u5168\u5931\u8D25\uFF0C\u4E3A\u4EC0\u4E48\uFF1F</strong></p><p>\u7EC6\u770B\u4E24\u4EBA\u7684\u51FA\u62DB\uFF0C\u5176\u5B9E\u9AD8\u4E0B\u6709\u522B\uFF1A</p><p>\u8D3E\u5145\u7ED9\u4EFB\u607A\u627E\u7684\u5751\uFF0C\u662F\u592A\u5B50\u5C11\u5085\u3002\u8FD9\u4E2A\u804C\u4F4D\u4EFB\u607A\u53EF\u4EE5\u505A\uFF0C\u522B\u4EBA\u4E5F\u53EF\u4EE5\u505A\u3002\u800C\u4E14\u8FD9\u4E0D\u662F\u4EC0\u4E48\u7D2F\u6210\u72D7\u7684\u5FD9\u6D3B\uFF0C\u517C\u804C\u505A\u4E5F\u53EF\u4EE5\u3002</p><p>\u4F46\u4EFB\u607A\u7ED9\u8D3E\u5145\u627E\u7684\u5751\u5374\u4E0D\u540C\uFF0C\u4E0D\u4EC5\u5F97\u79BB\u5F00\u6D1B\u9633\uFF0C\u800C\u4E14\u770B\u4E0A\u53BB\u8FD8\u975E\u8D3E\u5145\u83AB\u5C5E</p></blockquote><p>\u56E0\u6B64\u8D3E\u5145\u53D1\u73B0\uFF0C\u5F97\u627E\u7C7B\u4F3C\u7684\u6D3B\u7ED9\u4EFB\u607A\u5E72\u624D\u884C\uFF0C\u8981\u4E48\u8BA9\u4ED6\u79BB\u5F00\u6D1B\u9633\uFF0C\u8981\u4E48\u8BA9\u4ED6996.ICU\u3002\u603B\u4E4B\uFF0C\u5C31\u662F\u4E0D\u80FD\u8BA9\u4ED6\u6210\u5929\u6709\u65F6\u95F4\u6709\u7CBE\u529B\u6709\u673A\u4F1A\u8D34\u5728\u7687\u5E1D\u8EAB\u8FB9\u3002</p><p>\u8FD9\u79CD\u804C\u4F4D\u8FD8\u771F\u8BA9\u8D3E\u5145\u7ED9\u627E\u5230\u4E86\uFF1A</p><p><strong>\u540F\u90E8\u5C1A\u4E66</strong></p><p>\u540F\u90E8\u5C1A\u4E66\u4E3B\u7BA1\u5168\u56FD\u4E00\u5207\u4E2D\u4F4E\u5C42\u5B98\u540F\u7684\u4EFB\u804C\u5347\u8FC1\u3002<strong>\u4EBA\u4E8B\u6743\u662F\u4E00\u5207\u6743\u529B\u7684\u6838\u5FC3</strong>\uFF0C\u8D3E\u5145\u7ADF\u7136\u820D\u5F97\u628A\u8FD9\u79CD\u522B\u4EBA\u773C\u4E2D\u7684\u80A5\u7F3A\u90FD\u9001\u7ED9\u4EFB\u607A\uFF0C\u771F\u7684\u662F\u4E0B\u4E86\u8840\u672C\u3002</p><p>\u800C\u4EE5\u6B64\u4E3A\u9A71\u8D76\u653F\u654C\u7684\u5DE5\u5177\uFF0C\u8D3E\u5145\u7684\u601D\u8DEF\u5176\u5B9E\u662F\u6B63\u786E\u7684\uFF1A\u9996\u5148\uFF0C\u8FD9\u4E2A\u6D3B\u513F\u53EF\u4EE5\u8017\u8D39\u6389\u4EFB\u607A\u7EDD\u5927\u90E8\u5206\u7684\u7CBE\u529B\uFF0C\u8BA9\u4ED6\u6CA1\u6709\u65F6\u95F4\u53BB\u63A5\u89E6\u6B66\u5E1D\u3002\u5176\u6B21\uFF0C\u4EBA\u4E8B\u5DE5\u4F5C\u7EB7\u7E41\u590D\u6742\uFF0C\u5F88\u5BB9\u6613\u51FA\u7EB0\u6F0F\uFF0C\u5230\u65F6\u5019\u53C8\u80FD\u627E\u5230\u79BB\u95F4\u7684\u673A\u4F1A\u3002</p><p>\u8D3E\u5145\u7EC8\u4E8E\u6273\u56DE\u4E00\u5C40\u3002</p><blockquote><p>\u968F\u7740\u4EFB\u607A\u4ECE\u4F8D\u4E2D\u7684\u804C\u4F4D\u4E0A\u9000\u4E0B\u6765\uFF0C\u5C40\u9762\u5F00\u59CB\u53D1\u751F\u53D8\u5316\u3002</p><p>\u81EA\u6B64\uFF0C\u4EFB\u607A\u5F00\u59CB\u9010\u6E10\u5931\u5229\uFF0C\u5E76\u6700\u7EC8\u5F7B\u5E95\u5931\u52BF\u3002</p></blockquote><p>\u8D3E\u5145\u8DDF\u515A\u7FBD\u4EEC\u9996\u5148\u505A\u7684\uFF0C\u662F\u5728\u6B66\u5E1D\u9762\u524D\u8FDB\u884C\u75AF\u72C2\u7684\u6784\u9677\u3002\u6700\u7EC8\uFF0C\u4ED6\u4EEC\u56E0\u4E3A\u4E00\u4EF6\u5C0F\u4E8B\u800C\u6210\u529F\uFF1A<strong>\u6709\u4EBA\u4E3E\u62A5\uFF0C\u4EFB\u607A\u5BB6\u91CC\u6709<code>\u5FA1\u7528</code>\u4E4B\u7269</strong>\u3002</p><p>\u4E00\u67E5\u4E4B\u4E0B\uFF0C\u679C\u7136\u6709\u3002\u8FD9\u4E9B\u4E1C\u897F\uFF0C\u5176\u5B9E\u5E76\u4E0D\u662F\u4EFB\u607A\u5077\u5077\u7528\u7684\uFF0C\u800C\u662F\u4ED6\u7684\u592B\u4EBA\u2014\u2014\u9B4F\u660E\u5E1D\u7684\u5973\u513F\u9F50\u957F\u516C\u4E3B\u5F53\u5E74\u5E26\u8FC7\u6765\u7684\u3002</p><p>\u6B64\u4E8B\u56FA\u7136\u60C5\u6709\u53EF\u539F\uFF0C\u4F46\u662F\u5BB6\u91CC\u6709\u524D\u671D\u516C\u4E3B\u7684\u5FA1\u7528\u4E4B\u7269\uFF0C\u603B\u5F52\u4E0D\u592A\u59A5\u5F53\u3002\u518D\u52A0\u4E0A\u8D3E\u5145\u7684\u79BB\u95F4\uFF0C\u4EFB\u607A\u5C31\u6B64\u88AB\u514D\u804C\u3002</p><p><strong>\u800C\u8FD9\u53EA\u662F\u4EFB\u607A\u5384\u8FD0\u7684\u5F00\u59CB</strong>\u3002</p><p>\u514D\u5B98\u540E\u4E0D\u4E45\uFF0C\u4EFB\u607A\u88AB\u91CD\u65B0\u8D77\u590D\u4E3A\u6CB3\u5357\u5C39\u3002\u8FD9\u662F\u4EFB\u607A\u7684\u540E\u4EFB\u540F\u90E8\u5C1A\u4E66\u5C71\u6D9B\u7684\u529F\u52B3\uFF0C\u5C71\u6D9B\u660E\u767D\u4EFB\u607A\u624D\u80FD\u51FA\u4F17\uFF0C\u56E0\u6B64\u627E\u4E86\u4E2A\u673A\u4F1A\uFF0C\u91CD\u65B0\u5411\u6B66\u5E1D\u63A8\u8350\u4ED6\u3002</p><p><strong>\u4F46\u4EFB\u607A\u8FD9\u6B21\u7684\u5B98\u53C8\u6CA1\u6709\u505A\u4E45</strong>\u3002\u8FD9\u6B21\u88AB\u514D\u5B98\u7684\u7406\u7531\uFF0C\u662F\u201C\u8D3C\u53D1\u4E0D\u83B7\u201D\uFF0C\u8F96\u533A\u5185\u51FA\u73B0\u4E86\u76D7\u8D3C\u5374\u6CA1\u6709\u529E\u6CD5\u527F\u706D\uFF0C\u56E0\u6B64\u88AB\u514D\u5B98\u3002</p><p><strong>\u4E4B\u540E\uFF0C\u5982\u6B64\u7684\u60C5\u5F62\u4E00\u518D\u91CD\u590D</strong>\u3002\u4EFB\u607A\u540E\u6765\u88AB\u8D77\u590D\u4E3A\u5149\u7984\u52CB\uFF0C\u4F46\u53C8\u88AB\u8D3E\u5145\u7684\u515A\u7FBD\u6293\u4F4F\u4E86\u628A\u67C4\uFF0C\u88AB\u514D\u3002\u518D\u8D77\u590D\uFF0C\u4E3A\u592A\u4EC6\u3001\u592A\u5E38\uFF0C\u6B64\u540E\u788C\u788C\u800C\u7EC8\u3002</p><p>\u5BF9\u4E8E\u4EFB\u607A\u7684\u7ED3\u5C40\uFF0C\u5F53\u65F6\u4FBF\u6709\u5F88\u591A\u8BAE\u8BBA\u3002\u4EFB\u607A\u624D\u5177\u4E0A\u4F73\uFF0C\u672C\u6765\u662F\u4E09\u516C\u7684\u4E0D\u4E8C\u4EBA\u9009\uFF0C\u4F46\u6700\u7EC8\u5374\u6B62\u6B65\u4E8E\u537F\u5927\u592B\u4E00\u7EA7\uFF0C\u800C\u4E14\u4EFB\u7684\u90FD\u662F\u4E9B\u865A\u804C\u3002</p><p>\u8FD9\u5F53\u7136\u4E0D\u662F\u56E0\u4E3A\u8FD0\u6C14\u4E0D\u597D\uFF0C<strong>\u5728\u6BCF\u4E00\u6B21\u8D2C\u5B98\u7684\u80CC\u540E\uFF0C\u90FD\u9690\u7EA6\u80FD\u770B\u5230\u8D3E\u5145\u7684\u5F71\u5B50</strong>\u3002\u4ED6\u603B\u80FD\u5728\u4EFB\u607A\u4E0D\u6CE8\u610F\u7684\u5730\u65B9\u6765\u4E00\u8BB0\u91CD\u51FB\uFF0C\u5C06\u521A\u521A\u722C\u8D77\u6765\u7684\u4EFB\u607A\u53C8\u91CD\u65B0\u6253\u7FFB\u5728\u5730\u3002</p><p>\u4EFB\u607A\u540E\u6765\u5176\u5B9E\u4E5F\u63A5\u89E6\u8FC7\u664B\u6B66\u5E1D\uFF0C\u6B66\u5E1D\u4E5F\u5F88\u53EF\u601C\u4ED6\u7684\u906D\u9047\uFF0C\u4F46\u8FD9\u53C8\u80FD\u600E\u4E48\u6837\u5462\uFF1F\u4EFB\u607A\u4E00\u518D\u88AB\u8D77\u590D\uFF0C\u5374\u603B\u4F1A\u56E0\u4E3A\u4E00\u4E9B\u83AB\u540D\u5176\u5999\u7684\u4E8B\u88AB\u514D\u5B98\u3002</p><p>\u80FD\u88AB\u53CD\u590D\u8D77\u590D\uFF0C\u8DB3\u89C1\u6B66\u5E1D\u5BF9\u4ED6\u7684\u4FE1\u4EFB\u3002\u7136\u800C\u5728\u4EFB\u4E0A\uFF0C\u4EFB\u607A\u603B\u4F1A\u88AB\u5404\u79CD\u7410\u4E8B\u6240\u62D6\u7D2F\u3002\u8FD9\u4E9B\u7410\u4E8B\uFF0C\u660E\u663E\u5C31\u662F\u8D3E\u5145\u6307\u4F7F\u4ED6\u4EBA\u5BF9\u4E8E\u4EFB\u607A\u7684\u9677\u5BB3\uFF0C<strong>\u4F46\u5B83\u4EEC\u504F\u504F\u5C31\u80FD\u8BA9\u4EFB\u607A\u7684\u5B98\u573A\u4E4B\u8DEF\u4E3E\u6B65\u7EF4\u8270</strong>\u3002</p><p><strong>\u4E00\u800C\u518D\u518D\u800C\u4E09</strong>\u3002</p><p>\u5982\u4F55\u624D\u7B97\u662F\u6253\u8D25\u5BF9\u624B\uFF1F\u5230\u8FD9\u91CC\uFF0C\u8D3E\u5145\u7EC8\u4E8E\u7ED9\u51FA\u4E86\u81EA\u5DF1\u7684\u7B54\u5377\uFF1A</p><blockquote><p>\u4EC5\u4EC5\u5728\u6BD4\u8D5B\u4E2D\u8D62\uFF0C\u90A3\u662F\u6700\u4F4E\u4E00\u7EA7\u7684\u80DC\uFF1B</p><p>\u771F\u6B63\u81F4\u547D\u7684\u6253\u51FB\uFF0C\u5728\u4E8E\u6D88\u78E8\u6389\u5BF9\u624B\u7684\u610F\u5FD7\uFF0C\u8BA9\u5BF9\u65B9\u518D\u4E5F\u65E0\u6CD5\u63D0\u8D77\u6597\u5FD7\u3002</p></blockquote><p>\u8D3E\u5145\u662F\u8FD9\u4E48\u60F3\u7684\uFF0C\u4E5F\u662F\u8FD9\u4E48\u505A\u7684\uFF0C\u4ED6\u4E00\u6B21\u6B21\u5730\u628A\u4EFB\u607A\u7F62\u5B98\uFF0C\u800C\u4E14\u51E0\u4E4E\u90FD\u662F\u901A\u8FC7\u4E00\u4E9B\u5FAE\u4E0D\u8DB3\u9053\u7684\u5C0F\u4E8B\u3002<strong>\u4F46\u6B63\u662F\u8FD9\u4E9B\u770B\u4E0A\u53BB\u5371\u5BB3\u4E0D\u663E\u3001\u5B9E\u9645\u4E0A\u9488\u5BF9\u6027\u6781\u5F3A\u7684\u635F\u8017\uFF0C\u6700\u7EC8\u771F\u6B63\u51FB\u57AE\u4E86\u4EFB\u607A</strong>\u3002</p><p>\u53F2\u8F7D\uFF0C\u201C\u607A\u65E2\u5931\u804C\uFF0C\u4E43\u7EB5\u9152\u803D\u4E50\uFF0C\u6781\u6ECB\u5473\u4EE5\u81EA\u5949\u517B\u201D\u3002\u4EFB\u607A\u5728\u88AB\u7F62\u5B98\u540E\uFF0C\u7EB5\u60C5\u58F0\u4E50\uFF0C\u751F\u6D3B\u6781\u5C3D\u5962\u534E\u3002\u7136\u800C\uFF0C\u5373\u4FBF\u662F\u7269\u8D28\u4E0A\u7684\u6781\u5927\u6EE1\u8DB3\uFF0C\u4E5F\u65E0\u6CD5\u586B\u8865\u5185\u5FC3\u7684\u96BE\u53D7\u4E0E\u82E6\u695A\u3002\u9762\u5BF9\u7740\u201C\u503C\u4E07\u94B1\u201D\u7684\u73CD\u9990\u7F8E\u98DF\uFF0C\u4EFB\u607A\u4F9D\u7136\u98DF\u4E4B\u65E0\u5473\uFF0C\u201C\u4E00\u98DF\u4E07\u94B1\uFF0C\u72B9\u4E91\u65E0\u53EF\u4E0B\u7BB8\u5904\u201D\u3002</p><p><strong>\u800C\u5728\u7F62\u5B98\u540E\u89D0\u89C1\u664B\u6B66\u5E1D\u65F6\u7684\u884C\u4E3A\u59FF\u6001\uFF0C\u4E5F\u5145\u5206\u66B4\u9732\u4E86\u4EFB\u607A\u7684\u8106\u5F31</strong>\u3002\u965B\u89C1\u540E\uFF0C\u664B\u6B66\u5E1D\u6E29\u8A00\u529D\u8BEB\u4EFB\u607A\uFF0C\u4EFB\u607A\u53EA\u662F\u542C\u7740\u4E0D\u505A\u58F0\u3002\u53EF\u542C\u7740\u542C\u7740\uFF0C\u4ED6\u7ADF\u7136\u54ED\u5F97\u4E00\u584C\u7CCA\u6D82\u3002</p><p>\u5728\u8FD9\u4E2A\u573A\u666F\u91CC\uFF0C\u6211\u4EEC\u5DF2\u7ECF\u770B\u4E0D\u5230\u65E9\u5E74\u90A3\u4E2A\u610F\u6C14\u98CE\u53D1\uFF0C\u4EE4\u8D3E\u5145\u8272\u53D8\u7684\u4EFB\u607A\u3002\u80FD\u770B\u5230\u7684\uFF0C\u53EA\u6709\u4E00\u4E2A\u8D25\u519B\u4E4B\u5C06\uFF0C\u4E00\u4E2A\u88AB\u8D3E\u5145\u53CD\u590D\u6298\u78E8\u4E4B\u540E\u7684\u5782\u5782\u8001\u8005\u3002</p><p>\u6D77\u660E\u5A01\u5728\u300A\u8001\u4EBA\u4E0E\u6D77\u300B\u4E2D\u66FE\u8BF4\uFF0C\u4F60\u53EA\u80FD\u6D88\u706D\u6211\uFF0C\u5374\u4E0D\u80FD\u5C06\u6211\u6253\u8D25\u3002\u53EF\u5728\u4EFB\u607A\u8FD9\u91CC\uFF0C\u6211\u4EEC\u770B\u5230\u7684\u5374\u662F\u76F8\u53CD\u7684\u4E00\u9762\uFF1A</p><blockquote><p>\u8D3E\u5145\u5E76\u6CA1\u6709\u5C06\u4EFB\u607A\u6D88\u706D\u6389\uFF0C\u4EC5\u4EC5\u662F\u628A\u4ED6\u6253\u8D25\u4E86\u3002</p><p>\u4F46\u4E8E\u4EFB\u607A\u800C\u8A00\uFF0C\u8FD9\u79CD\u5931\u8D25\u5374\u8BA9\u4ED6\u751F\u4E0D\u5982\u6B7B\u3002</p></blockquote><blockquote><p>\u6211\u4EEC\u4E0D\u59A8\u6362\u4E2A\u89D2\u5EA6\uFF0C\u60F3\u60F3\u5982\u679C\u4F60\u662F\u7AD9\u5728\u8D3E\u5145\u7684\u7ACB\u573A\u4E0A\uFF0C\u90A3\u4E48\u4EC0\u4E48\u6837\u7684\u80DC\u5229\u6700\u80FD\u8BA9\u4F60\u89C9\u5F97\u7545\u5FEB\u6DCB\u6F13\uFF1F</p></blockquote><p>\u662F\u4ECE\u8089\u4F53\u4E0A\u6D88\u706D\u5BF9\u624B\u5417\uFF1F\u5E76\u4E0D\u662F\u3002<strong>\u800C\u662F\u4ECE\u7CBE\u795E\u4E0A\u74E6\u89E3\u5BF9\u624B</strong>\uFF0C\u628A\u5BF9\u624B\u4EE5\u524D\u6240\u73CD\u89C6\u7684\u4E00\u5207\u90FD\u4ECE\u4ED6\u624B\u91CC\u593A\u8D70\uFF0C\u8BA9\u4ED6\u5931\u53BB\u5BF9\u751F\u6D3B\u7684\u4FE1\u5FC3\uFF0C\u4F46\u53C8\u4E0D\u5F97\u4E0D\u82DF\u6D3B\u5728\u8FD9\u4E2A\u4E16\u754C\u4E0A\u3002</p><p>\u5F53\u4F60\u6BCF\u6B21\u4ECE\u4ED6\u7684\u8EAB\u8FB9\u8D70\u8FC7\uFF0C\u770B\u5230\u4ED6\u530D\u5310\u5728\u5730\u4E0A\u6218\u6218\u53D1\u6296\uFF0C\u4F60\u90FD\u4F1A\u7A81\u7136\u60F3\u8D77\uFF0C\u8FD9\u5C31\u662F\u66FE\u7ECF\u8DDF\u4F60\u7EA0\u7F20\u4E0D\u4F11\u7684\u5BF9\u5934\u554A\uFF0C\u5982\u4ECA\u5374\u53EA\u80FD\u8DB4\u5728\u5730\u4E0A\u4E5E\u6C42\u4F60\u7684\u5782\u601C\uFF01</p><p>\u8FD9\u4E16\u4E0A\u8FD8\u6709\u6BD4\u8FD9\u66F4\u7545\u5FEB\u7684\u4E8B\u5417\uFF1F</p><h2 id="\u756A\u5916-\u8D3E\u5145\u548C\u4EFB\u607A" tabindex="-1"><a class="header-anchor" href="#\u756A\u5916-\u8D3E\u5145\u548C\u4EFB\u607A" aria-hidden="true">#</a> \u756A\u5916-\u8D3E\u5145\u548C\u4EFB\u607A</h2><p>\u5173\u4E8E\u8D3E\u5145\u548C\u4EFB\u607A\u7684\u6597\u6CD5\uFF0C\u8FD9\u91CC\u8FD8\u6709\u4E00\u5219\u5177\u4F53\u6545\u4E8B\u3002</p><p>\u8FD9\u573A\u6218\u6597\uFF0C\u53D1\u751F\u5728<code>\u8D3E\u5145</code>\u548C<code>\u5EBE\u7EAF</code>\u4E4B\u95F4\u2014\u2014\u540E\u8005\u65E9\u5C31\u662F\u8D3E\u5145\u7684\u773C\u4E2D\u523A\uFF0C\u5F53\u521D\u5EFA\u8BAE\u8D3E\u5145\u51FA\u9547\u957F\u5B89\u7684\uFF0C\u5C31\u662F\u4EFB\u607A\uFF0C\u4EE5\u53CA\u65F6\u4EFB\u6CB3\u5357\u5C39\u7684\u5EBE\u7EAF\u3002</p><p>\u4E8B\u60C5\u53D1\u751F\u5728\u4E00\u6B21\u5BB4\u4F1A\u4E0A\uFF0C\u8D3E\u5145\u5BB4\u8BF7\u671D\u81E3\uFF0C\u4F46\u5EBE\u7EAF\u8FDF\u5230\u4E86\u3002</p><p>\u5C31\u4E8B\u8BBA\u4E8B\uFF0C\u5EBE\u7EAF\u8FD9\u4E48\u505A\u7684\u786E\u6709\u70B9\u8FC7\u5206\u3002\u8D3E\u5145\u6BD5\u7ADF\u662F\u4E0A\u5B98\uFF0C\u9886\u5BFC\u8BF7\u5BA2\u4F60\u8FDF\u5230\uFF0C\u8FD9\u4E0D\u662F\u660E\u663E\u4E0D\u628A\u9886\u5BFC\u653E\u5728\u773C\u91CC\u4E48\uFF1F</p><p>\u8D3E\u5145\u56E0\u6B64\u5F88\u4E0D\u723D\uFF0C\u4FBF\u95EE\u5EBE\u7EAF\uFF0C\u4F60\u884C\u4E8B\u4E0D\u662F\u603B\u5728\u4EBA\u524D\u5417\uFF1F\u5982\u4ECA\u600E\u4E48\u843D\u540E\u4E86\uFF1F</p><p>\u5EBE\u7EAF\u8BF4\uFF0C\u88AB\u4E00\u4E9B\u5E02\u4E95\u5C0F\u4E8B\u803D\u6401\u4E86\u3002</p><p>\u8D3E\u5145\u52C3\u7136\u5927\u6012\u3002</p><blockquote><p>\u8FD9\u91CC\u6709\u4E2A\u5C0F\u80CC\u666F\u8981\u63D2\u64AD\u4E00\u4E0B\uFF1A</p><p>\u5EBE\u7EAF\u7684\u7956\u4E0A\u662F\u884C\u4F0D\u51FA\u8EAB\uFF0C\u800C\u4E14\u505A\u8FC7\u201C\u4F0D\u4F2F\u201D\uFF0C\u5927\u7EA6\u5C31\u7C7B\u4F3C\u4E8E\u73ED\u957F\uFF0C\u800C\u73ED\u957F\u603B\u662F\u8D70\u5728\u961F\u5217\u7684\u6700\u524D\u5217\u7684\u3002\u56E0\u6B64\u8D3E\u5145\u624D\u4F1A\u5632\u8BBD\u5EBE\u7EAF\u8BF4\u201C\u884C\u4E8B\u603B\u5728\u4EBA\u524D\u201D\u3002</p><p>\u800C\u5EBE\u7EAF\u7684\u53CD\u5507\u76F8\u8BA5\u4E5F\u523A\u75DB\u4E86\u8D3E\u5145\u7684\u8F6F\u808B\u3002\u8D3E\u5145\u7684\u7956\u4E0A\uFF0C\u636E\u8BF4\u6709\u505A\u8FC7\u201C\u5E02\u9B41\u201D\u7684\uFF0C\u5C31\u662F\u53E4\u4EE3\u7BA1\u7406\u5E02\u573A\u7684\u5F79\u540F\u3002\u56E0\u6B64\u5EBE\u7EAF\u4F1A\u8BF4\u81EA\u5DF1\u662F\u88AB\u201C\u5E02\u4E95\u5C0F\u4E8B\u201D\u803D\u6401\u4E86\u3002</p></blockquote><p>\u8FD9\u4E24\u79CD\u804C\u4E1A\uFF0C\u5728\u5F53\u65F6\u90FD\u4E0D\u751A\u4F53\u9762\uFF0C\u56E0\u6B64\u4E24\u4EBA\u5C5E\u4E8E\u4E92\u76F8\u63ED\u77ED\u3002\u4F46\u4E0D\u540C\u7684\u662F\uFF0C<strong>\u8D3E\u5145\u4F4D\u5C0A\u800C\u4E14\u5148\u51FA\u62DB\uFF0C\u8FD8\u53C8\u88AB\u4EBA\u7ED9\u603C\u4E86\u56DE\u6765</strong>\uFF0C\u5176\u5B9E\u5DF2\u7ECF\u8F93\u4E86\u3002</p><p>\u4E22\u4E86\u5148\u624B\uFF0C\u8D3E\u5145\u7684\u8FD9\u987F\u996D\u81EA\u7136\u8D8A\u5403\u8D8A\u4E0D\u987A\u5FC3\uFF0C\u603B\u60F3\u7740\u8981\u627E\u8865\u56DE\u6765\u3002\u6B63\u597D\u8FD9\u65F6\u5019\uFF0C\u5EBE\u7EAF\u504F\u504F\u53C8\u8FC7\u6765\u656C\u9152\u4E86\u3002</p><p>\u8D3E\u5145\u7AEF\u7740\u9152\u676F\u6CA1\u6709\u559D\u3002</p><p>\u8FD9\u4E00\u4E0B\u5B50\u6FC0\u6012\u4E86\u5EBE\u7EAF\uFF0C\u4ED6\u5927\u58F0\u65A5\u8D23\u9053\uFF0C\u201C\u957F\u8005\u4E3A\u5BFF\uFF0C\u4F55\u6562\u5C14\u4E4E\uFF01\u201D\u6211\u5E74\u7EAA\u6BD4\u4F60\u5927\uFF0C\u4E8E\u4F60\u6765\u8BF4\u662F\u957F\u8005\uFF0C\u4F60\u4E00\u4E2A\u5C0F\u8F88\u600E\u4E48\u6562\u8FD9\u4E48\u6CA1\u793C\u8C8C\uFF01</p><p>\u8D3E\u5145\u51B7\u7B11\u4E00\u58F0\uFF0C\u201C\u7236\u8001\u4E0D\u5F52\u4F9B\u517B\uFF0C\u5C06\u4F55\u8A00\u4E5F\uFF01\u201D\u4F60\u6709\u8001\u7236\u5728\u5BB6\uFF0C\u4E0D\u5F52\u53BB\u4F9B\u517B\u5374\u8D2A\u604B\u6743\u4F4D\uFF0C\u65E0\u884C\u65E0\u5B5D\uFF0C\u4E5F\u914D\u79F0\u957F\u8005\uFF1F</p><p>\u5EBE\u7EAF\u66F4\u52A0\u751F\u6C14\uFF1A\u201C<strong>\u8D3E\u5145\uFF01\u5929\u4E0B\u51F6\u51F6\uFF0C\u7531\u5C14\u4E00\u4EBA</strong>\u3002\u201D\u8D3E\u5145\uFF01\u600E\u662F\u6211\u65E0\u884C\u65E0\u5B5D\uFF1F\u5982\u4ECA\u5929\u4E0B\u793C\u4E49\u5EC9\u803B\u6CA6\u4E27\uFF0C\u5B8C\u5168\u662F\u56E0\u4E3A\u4F60\u4E00\u4E2A\u4EBA\u9020\u6210\u7684\uFF01\u2014\u2014\u5728\u8FD9\u91CC\uFF0C\u5EBE\u7EAF\u76F4\u547C\u8D3E\u5145\u540D\u59D3\uFF0C\u5DF2\u7ECF\u662F\u5B8C\u5168\u4E0D\u7ED9\u8D3E\u5145\u7559\u8138\u9762\u4E86\u3002</p><p>\u8D3E\u5145\u8BF4\uFF0C\u6211\u6709\u4EC0\u4E48\u7F6A\uFF01\u6211\u4E24\u4E16\u529F\u81E3\uFF0C\u8361\u5E73\u5DF4\u8700\uFF0C\u4F55\u7F6A\u4E4B\u6709\uFF01</p><p>\u7136\u540E\uFF0C\u5EBE\u7EAF\u53D1\u51FA\u4E86\u60CA\u5929\u4E00\u95EE\uFF1A</p><p>\u201C<strong>\u9AD8\u8D35\u4E61\u516C\u66F9\u9AE6\u4F55\u5728\uFF01</strong>\u201D</p><p>\u8FD9\u8BDD\u4E00\u51FA\u53E3\uFF0C\u8FD9\u987F\u996D\u5C31\u6CA1\u6CD5\u518D\u5403\u4E0B\u53BB\u4E86\u3002\u8D3E\u5145\u7684\u5DE6\u53F3\u4EB2\u4FE1\u8DF3\u51FA\u6765\u8981\u6293\u5EBE\u7EAF\u53BB\u95EE\u7F6A\uFF0C\u5EBE\u7EAF\u5219\u5728\u4F17\u4EBA\u7684\u4FDD\u62A4\u4E4B\u4E0B\u9000\u51FA\u4E86\u8D3E\u5E9C\u3002</p><blockquote><p><strong>\u9152\u9192\u4EE5\u540E\uFF0C\u4E24\u4EBA\u540C\u65F6\u4E0A\u4E66\u664B\u6B66\u5E1D\uFF0C\u800C\u4E14\u5185\u5BB9\u4E5F\u90FD\u76F8\u540C\uFF1A\u6211\u8981\u8F9E\u804C</strong>\u3002</p></blockquote><p>\u8D3E\u5145\u5728\u4E0A\u4E66\u4E2D\u8BF4\uFF0C\u6709\u4EBA\u6307\u8D23\u6211\u9020\u6210\u4E86\u5982\u4ECA\u7684\u9053\u5FB7\u6CA6\u4E27\uFF0C\u65E2\u7136\u5982\u6B64\uFF0C\u6211\u5B9E\u5728\u65E0\u6CD5\u518D\u5F85\u5728\u671D\u5EF7\u4E86\uFF0C\u56E0\u6B64\u8BF7\u6C42\u8F9E\u804C\u3002</p><p>\u5EBE\u7EAF\u7684\u4E0A\u4E66\u5219\u66F4\u7B80\u5355\uFF1A\u6211\u9152\u540E\u5931\u8A00\uFF0C\u8BF4\u9519\u8BDD\u4E86\u3002</p><p>\u5E73\u5FC3\u800C\u8BBA\uFF0C\u5EBE\u7EAF\u8BF4\u7684\u5F53\u7136\u662F\u4E8B\u5B9E\u3002\u8D3E\u5145\u7684\u5F11\u541B\u884C\u4E3A\uFF0C\u4E8B\u524D\u4E8B\u540E\u90FD\u6CA1\u6709\u6E05\u7B97\uFF0C\u8FD9\u5176\u5B9E\u662F\u7ED9\u897F\u664B\u7684\u653F\u6CBB\u98CE\u6C14\u7684\u6076\u5316\u5F00\u4E86\u4E00\u4E2A\u5F88\u574F\u7684\u5934\u3002</p><p>\u4F46\u95EE\u9898\u662F\uFF0C<strong>\u5927\u664B\u671D\u53C8\u65E0\u6CD5\u627F\u8BA4\u8FD9\u4E2A\u4E8B\u5B9E</strong>\uFF0C\u5426\u5219\u4ED6\u4EEC\u53F8\u9A6C\u6C0F\u5C31\u662F\u4E71\u81E3\u8D3C\u5B50\u4E86\uFF1A\u6740\u4E86\u4EBA\u5BB6\u7684\u7687\u5E1D\uFF0C\u62A2\u4E86\u4EBA\u5BB6\u7684\u7687\u4F4D\uFF0C\u8FD9\u624D\u662F\u771F\u6B63\u7684\u9053\u5FB7\u6CA6\u4E27\u3002</p><p>\u5BF9\u7740\u8FD9\u4E24\u4EFD\u8F9E\u5448\uFF0C\u664B\u6B66\u5E1D\u6CA1\u6CD5\u89C6\u800C\u4E0D\u89C1\u3002\u8D3E\u5145\u80AF\u5B9A\u662F\u8981\u6170\u7559\u7684\uFF0C\u5426\u5219\u5C31\u771F\u7684\u662F\u6B64\u5730\u65E0\u94F6\u4E86\uFF1B\u4F46\u5EBE\u7EAF\u7684\u8BDD\u8981\u600E\u4E48\u642D\uFF0C\u5374\u662F\u4E2A\u95EE\u9898\uFF0C\u671D\u5EF7\u65E2\u4E0D\u80FD\u8BF4\u4ED6\u5BF9\uFF0C\u4E5F\u4E0D\u80FD\u8BF4\u4ED6\u4E0D\u5BF9\u3002</p><p>\u5B9E\u5728\u662F\u5DE6\u53F3\u4E3A\u96BE\u554A\uFF01</p><p>\u53EF\u8981\u4E0D\u600E\u4E48\u8BF4\u5B98\u5458\u7684\u667A\u6167\u662F\u65E0\u7A77\u7684\u5462\uFF0C\u8FD9\u79CD\u4E24\u96BE\u5C40\u9762\uFF0C\u6700\u540E\u5C45\u7136\u8FD8\u771F\u7684\u627E\u5230\u4E86\u89E3\u51B3\u529E\u6CD5\uFF1A<strong>\u5EBE\u7EAF\u88AB\u514D\u5B98\u3002\u4F46\u7406\u7531\uFF0C\u662F\u56E0\u4E3A\u4ED6\u51B2\u649E\u4E86\u4E0A\u7EA7</strong>\u3002</p><p>\u4E3A\u4E86\u6015\u8FD9\u4E2A\u7406\u7531\u770B\u4E0A\u53BB\u4E0D\u591F\u5145\u5206\uFF0C\u5B98\u5458\u4EEC\u5728\u5199\u6750\u6599\u7684\u65F6\u5019\uFF0C\u8FD8\u7279\u5730\u627E\u4E86\u8D75\u5E7F\u6C49\u548C\u704C\u592B\u4E24\u4E2A\u6C49\u671D\u7684\u4F8B\u5B50\u6765\u505A\u4F5C\u8BC1\u3002\u8FD9\u4E24\u4EBA\u90FD\u66FE\u56E0\u4E3A\u51B2\u649E\u5BB0\u76F8\u800C\u88AB\u7F5A\uFF0C\u6240\u4EE5\u8FD9\u4E48\u5224\u662F\u6709\u524D\u4F8B\u53EF\u5FAA\u7684\uFF0C\u5C06\u5EBE\u7EAF\u514D\u804C\u6709\u7406\u6709\u636E\u3002</p><p><strong>\u800C\u5728\u5C06\u5EBE\u7EAF\u514D\u804C\u540E\uFF0C\u4E8B\u60C5\u4E5F\u6CA1\u7B97\u5B8C</strong>\uFF0C\u671D\u5EF7\u5B98\u5458\u53C8\u5F00\u59CB\u8BA8\u8BBA\u8D77\u8D3E\u5145\u5BF9\u5EBE\u7EAF\u7684\u4E3E\u62A5\u662F\u5426\u5C5E\u5B9E\u7684\u95EE\u9898\u2014\u2014\u4E5F\u5C31\u662F\u5728\u8BA8\u8BBA\uFF0C\u5EBE\u7EAF\u662F\u4E0D\u662F\u8D2A\u604B\u6743\u4F4D\u800C\u4E0D\u613F\u610F\u5F52\u517B\u8001\u7236\u7684\u95EE\u9898\u3002</p><p>\u6574\u4E2A\u671D\u8BAE\u7684\u8FC7\u7A0B\u5341\u5206\u590D\u6742\uFF0C\u5728\u6B64\u6211\u4EEC\u5C31\u4E0D\u505A\u5C55\u5F00\u4E86\u3002\u603B\u4E4B\u8FD9\u573A\u8BA8\u8BBA\u7684\u7ED3\u679C\u662F\uFF0C\u5EBE\u7EAF\u5E76\u975E\u4E0D\u613F\u610F\u5F52\u517B\u8001\u7236\uFF0C\u56E0\u6B64\u8D3E\u5145\u7684\u6307\u63A7\u4E5F\u5C31\u4E0D\u6210\u7ACB\u3002</p><p>\u90A3\u5982\u679C\u6CA1\u6709\u4E0D\u5B5D\u7684\u884C\u4E3A\uFF0C\u4EC5\u4EC5\u662F\u56E0\u4E3A\u8FB1\u9A82\u5BB0\u76F8\u5C31\u628A\u4E00\u4E2A\u4E5D\u537F\u7EA7\u7684\u9AD8\u5B98\u514D\u804C\uFF0C\u662F\u4E0D\u662F\u53C8\u592A\u8FC7\u5206\u4E86\u5462\uFF1F</p><p>\u664B\u6B66\u5E1D\u8BA4\u4E3A\u6709\u70B9\u8FC7\u5206\u4E86\u3002\u56E0\u6B64\uFF0C\u5728\u628A\u5EBE\u7EAF\u514D\u5B98\u540E\u4E0D\u4E45\uFF0C\u4ED6\u53C8\u4EFB\u547D\u5EBE\u7EAF\u4E3A\u56FD\u5B50\u796D\u9152\u3002</p><p>\u6574\u4F53\u4E0A\u6765\u770B\uFF0C\u5EBE\u7EAF\u8FD8\u662F\u7B97\u964D\u4E86\u804C\u7684\uFF0C\u4ECE\u5317\u4EAC\u5E02\u59D4\u4E66\u8BB0\u53D8\u6210\u4E86\u4E2D\u592E\u515A\u6821\u6821\u957F\u3002<strong>\u4F46\u5173\u952E\u7684\u662F\uFF0C\u7ECF\u8FC7\u8FD9\u4E48\u4E00\u756A\u5904\u7406\uFF0C\u9AD8\u8D35\u4E61\u516C\u662F\u4E0D\u662F\u88AB\u8D3E\u5145\u6240\u6740\uFF0C\u8FD9\u4E8B\u5C31\u88AB\u5F7B\u5E95\u63A9\u8FC7\u53BB\u4E86</strong>\u3002\u4E0D\u7BA1\u662F\u539F\u544A\u3001\u88AB\u544A\u8FD8\u662F\u88C1\u5224\uFF0C\u5927\u5BB6\u90FD\u5341\u5206\u9ED8\u5951\u7684\u8DF3\u5F00\u4E86\u8FD9\u4E00\u70B9\u3002</p><p>\u6709\u65F6\u5019\uFF0C\u4F60\u4E0D\u5F97\u4E0D\u4F69\u670D\u8FD9\u79CD\u8BA8\u8BBA\u95EE\u9898\u7684\u96C6\u4F53\u667A\u6167\uFF0C\u660E\u660E\u6700\u91CD\u8981\u7684\u4E8B\u5C31\u5728\u90A3\u91CC\uFF0C\u4F46\u8C01\u90FD\u5047\u88C5\u6CA1\u770B\u89C1\uFF0C\u5FC3\u7167\u4E0D\u5BA3\u3001\u5C0F\u5FC3\u7FFC\u7FFC\u7684\u7ED5\u7740\u8D70\u3002</p><p>\u8FD9\u5927\u6982\u53EF\u4EE5\u7B97\u4F5C\u662F\u53E4\u4EE3\u7248\u7684\u201C\u623F\u95F4\u91CC\u7684\u5927\u8C61\u201D\u3002</p><p>\u6709\u7684\u8BFB\u8005\u53EF\u80FD\u8FD8\u6709\u7591\u95EE\uFF1A<strong>\u4E3A\u4EC0\u4E48\u8D3E\u5145\u5230\u540E\u6765\u80FD\u4E00\u800C\u518D\u518D\u800C\u4E09\u5730\u6253\u538B\u4EFB\u607A\uFF0C\u800C\u4E4B\u524D\u5374\u4E0D\u80FD\uFF1F</strong></p>',106),g=[s];function e(c,l){return o(),t("div",null,g)}const u=p(n,[["render",e],["__file","2.html.vue"]]);export{u as default};
