import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.cb44c984.js";const c={},o=a('<h1 id="\u7B2C2\u96C6-\u5468\u79C9\u6606\u627E\u8521\u6653\u5149\u6C42\u804C" tabindex="-1"><a class="header-anchor" href="#\u7B2C2\u96C6-\u5468\u79C9\u6606\u627E\u8521\u6653\u5149\u6C42\u804C" aria-hidden="true">#</a> \u7B2C2\u96C6\uFF1A\u5468\u79C9\u6606\u627E\u8521\u6653\u5149\u6C42\u804C</h1><p>\u6C34\u81EA\u6D41\u751F\u6015\u88AB\u8BA4\u51FA\u6765\uFF0C\u548C\u9A86\u58EB\u5BBE\u8D76\u7D27\u79BB\u5F00\u4E86\u8FD9\u4E2A\u5730\u65B9\uFF0C\u5468\u79C9\u6606\u4E5F\u5E76\u672A\u6CE8\u610F\u5230\u81EA\u5DF1\u5DF2\u7ECF\u88AB\u4EBA\u7ED9\u76EF\u4E0A\u4E86\u3002</p><p>\u6B21\u65E5\uFF0C\u5468\u79C9\u6606\u4E0A\u73ED\u7684\u65F6\u5019\u548C\u540C\u4E8B\u4E00\u8D77\u62AC\u6728\u5934\uFF0C\u53C8\u60F3\u8D77\u4E86\u548C\u6D82\u5FD7\u5F3A\u5728\u4E00\u8D77\u65F6\u5019\u7684\u6837\u5B50\uFF0C\u6D82\u5FD7\u5F3A\u603B\u662F\u5BF9\u5468\u79C9\u6606\u7167\u987E\u6709\u52A0\u3002\u4F46\u6D82\u5FD7\u5F3A\u4E5F\u559C\u6B22\u6363\u4E71\uFF0C\u603B\u662F\u7ED9\u6728\u5934\u4E0A\u62E7\u9489\u5B50\uFF0C\u5F53\u91CC\u9762\u952F\u6728\u5934\u7684\u65F6\u5019\u4E5F\u603B\u80FD\u542C\u5230\u952F\u5B50\u62C9\u6728\u5934\u7684\u58F0\u97F3\uFF0C\u6BCF\u5F53\u8FD9\u4E2A\u65F6\u5019\u6D82\u5FD7\u5F3A\u5C31\u5F88\u5F00\u5FC3\u3002\u5468\u79C9\u6606\u5F53\u770B\u89C1\u6709\u4EBA\u518D\u6B21\u5F80\u91CC\u952F\u6728\u5934\uFF0C\u5468\u79C9\u6606\u7ADF\u7136\u770B\u6210\u4E86\u6D82\u5FD7\u5F3A\uFF0C\u5413\u5F97\u5468\u79C9\u6606\u8D76\u7D27\u5173\u95ED\u4E86\u7535\u95F8\uFF0C\u7ED3\u679C\u88AB\u4EBA\u8BEF\u4F1A\u662F\u53D7\u4E86\u6D82\u5FD7\u5F3A\u67AA\u6BD9\u7684\u523A\u6FC0\u6709\u70B9\u795E\u7ECF\u3002\u5468\u79C9\u6606\u6765\u627E\u9886\u5BFC\u8BF7\u5047\uFF0C\u7ED3\u679C\u4E5F\u88AB\u8BF4\u6210\u662F\u5BF9\u6D82\u5FD7\u5F3A\u88AB\u67AA\u6BD9\u5FC3\u6709\u6028\u603C\uFF0C\u6C14\u5F97\u5468\u79C9\u6606\u5C06\u9886\u5BFC\u4E5F\u9A82\u4E86\u3002</p><p>\u4E5F\u56E0\u6B64\u5BFC\u81F4\u4E86\u5468\u79C9\u6606\u4E22\u4E86\u5DE5\u4F5C\uFF0C\u751F\u6015\u88AB\u6BCD\u4EB2\u77E5\u9053\u4E4B\u540E\u4F24\u5FC3\uFF0C\u56E0\u6B64\u5468\u79C9\u6606\u6B21\u65E5\u8FD8\u662F\u5047\u88C5\u4E0A\u73ED\u79BB\u5F00\u5BB6\u95E8\u3002\u51FA\u53BB\u4E4B\u540E\u53C8\u627E\u5230\u540C\u4E8B\u8981\u6C42\u5E2E\u5FD9\u6253\u63A9\u62A4\uFF0C\u540C\u4E8B\u63D0\u9192\u5468\u79C9\u6606\u53EF\u4EE5\u53BB\u627E\u51AC\u6885\u60F3\u529E\u6CD5\uFF0C\u867D\u7136\u51AC\u6885\u7236\u6BCD\u88AB\u6253\u5012\u4E86\uFF0C\u4F46\u662F\u7626\u6B7B\u9A86\u9A7C\u6BD4\u9A6C\u5927\uFF0C\u53EF\u4EE5\u627E\u5230\u5176\u4ED6\u6CA1\u6709\u88AB\u6253\u5012\u7684\u4EBA\u60F3\u529E\u6CD5\u3002\u5468\u79C9\u6606\u5C31\u7ED9\u5728\u5EFA\u8BBE\u5175\u56E2\u7684\u54E5\u54E5\u5468\u79C9\u4E49\u6253\u53BB\u4E86\u7535\u8BDD\uFF0C\u6070\u597D\u4F5C\u4E3A\u4E0B\u4E61\u77E5\u9752\u7684\u90DD\u51AC\u6885\u6765\u627E\u5468\u79C9\u4E49\u3002\u90DD\u51AC\u6885\u5FC3\u91CC\u62C5\u5FE7\u7236\u6BCD\uFF0C\u4E5F\u4E0D\u77E5\u9053\u7236\u6BCD\u662F\u6B7B\u662F\u6D3B\uFF0C\u6240\u4EE5\u6839\u672C\u5C31\u65E0\u5FC3\u7BA1\u5468\u79C9\u6606\u7684\u4E8B\u60C5\u3002</p><p>\u5468\u79C9\u4E49\u63D0\u51FA\u8981\u548C\u90DD\u51AC\u6885\u5728\u4E00\u8D77\uFF0C\u73B0\u5728\u5468\u84C9\u5DF2\u7ECF\u7ED3\u5A5A\u4E86\uFF0C\u4ED6\u4E5F\u60F3\u8D76\u7D27\u628A\u5927\u4E8B\u4E86\u4E86\uFF0C\u6625\u8282\u65F6\u5019\u5468\u79C9\u4E49\u5E0C\u671B\u80FD\u5E26\u7740\u51AC\u6885\u56DE\u53BB\u89C1\u89C1\u516C\u5A46\uFF0C\u90DD\u51AC\u6885\u5FAE\u7B11\u7740\u70B9\u5934\u3002\u5468\u79C9\u4E49\u63D0\u51FA\u660E\u5929\u4E0B\u73ED\u8FC7\u6765\u627E\u51AC\u6885\uFF0C\u4E24\u4EBA\u4E00\u8D77\u53BB\u767D\u6866\u6797\uFF0C\u51AC\u6885\u5BB3\u7F9E\u70B9\u5934\u7B54\u5E94\u3002</p><p>\u665A\u4E0A\uFF0C\u4E54\u6625\u71D5\u6765\u7ED9\u5468\u6BCD\u9001\u4E86\u4E00\u74F6\u773C\u836F\uFF0C\u5468\u6BCD\u8981\u7559\u4E0B\u4E54\u6625\u71D5\u5403\u996D\uFF0C\u88AB\u4E54\u6625\u71D5\u62D2\u7EDD\u4E86\u3002\u5468\u79C9\u6606\u9001\u4E54\u6625\u71D5\u56DE\u53BB\u7684\u8DEF\u4E0A\uFF0C\u4E54\u6625\u71D5\u5C06\u6BCD\u4EB2\u7ED9\u5979\u627E\u5BF9\u8C61\u7684\u76DB\u60C5\u544A\u8BC9\u4E86\u5468\u79C9\u6606\uFF0C\u8FD8\u7279\u610F\u8BF4\u8FD9\u4E2A\u4EBA\u662F\u6728\u6750\u5382\u7684\uFF0C\u6BCF\u5929\u8DDD\u79BB\u5979\u4E0A\u73ED\u5730\u65B9\u6BD4\u8F83\u8FD1\uFF0C\u6070\u597D\u6BCF\u5929\u9001\u5979\u4E0A\u4E0B\u73ED\uFF0C\u65F6\u95F4\u957F\u4E86\u5C31\u6709\u611F\u60C5\u4E86\u3002\u4E54\u6625\u71D5\u5C31\u662F\u60F3\u8981\u70B9\u9192\u5468\u79C9\u6606\uFF0C\u4F46\u5468\u79C9\u6606\u5C31\u662F\u4E00\u4E2A\u6986\u6728\u7599\u7629\u4E0D\u5F00\u7A8D\uFF0C\u4E54\u6625\u71D5\u53EA\u597D\u8868\u660E\u4E86\u5FC3\u610F\u5C31\u60F3\u627E\u5468\u79C9\u6606\u8FD9\u6837\u7684\u3002\u5468\u79C9\u6606\u53EA\u597D\u8BF4\u51FA\u81EA\u5DF1\u5E76\u4E0D\u5728\u6728\u6750\u5382\u7684\u4E8B\u60C5\uFF0C\u8FD8\u5E0C\u671B\u4E54\u6625\u71D5\u80FD\u5E2E\u5FD9\u4ECB\u7ECD\u5230\u5979\u5DE5\u4F5C\u7684\u6FA1\u5802\u5B50\u91CC\u53BB\u4FEE\u811A\uFF0C\u53EF\u4E54\u6625\u71D5\u8BA4\u4E3A\u8FD9\u662F\u6CA1\u6709\u51FA\u606F\u7684\u4E8B\u60C5\u3002</p><p>\u4E54\u6625\u71D5\u603B\u559C\u6B22\u5468\u79C9\u6606\uFF0C\u662F\u56E0\u4E3A\u5979\u89C9\u5F97\u5468\u79C9\u6606\u548C\u522B\u4EBA\u4E0D\u4E00\u6837\uFF0C\u6709\u60F3\u6CD5\u548C\u89C1\u8BC6\u3002\u6700\u540E\uFF0C\u4E54\u6625\u71D5\u4E5F\u8BA4\u4E3A\u5373\u4FBF\u662F\u6FA1\u5802\u5B50\u4E5F\u603B\u6BD4\u6CA1\u6709\u5DE5\u4F5C\u7684\u5F3A\uFF0C\u6240\u4EE5\u5C31\u7B54\u5E94\u4E00\u5B9A\u4F1A\u597D\u597D\u548C\u4E3B\u4EFB\u8BF4\u8BF4\uFF0C\u8FD9\u8BA9\u5468\u79C9\u6606\u5F88\u611F\u6FC0\u3002\u4E54\u6625\u71D5\u8D81\u673A\u633D\u4F4F\u4E86\u5468\u79C9\u6606\u7684\u80F3\u818A\uFF0C\u5468\u79C9\u6606\u5374\u4E00\u8138\u7684\u5ACC\u5F03\u6837\u5B50\u6323\u8131\u4E86\u4E54\u6625\u71D5\u3002</p><p>\u5468\u79C9\u4E49\u548C\u90DD\u51AC\u6885\u94BB\u4E86\u767D\u6866\u6797\uFF0C\u51FA\u6765\u7684\u65F6\u5019\u5FFD\u7136\u53D1\u73B0\u5934\u53D1\u76AE\u7B4B\u5C11\u4E86\u4E00\u4E2A\uFF0C\u4E00\u8F86\u8F66\u8DEF\u8FC7\uFF0C\u5468\u79C9\u4E49\u751F\u6015\u88AB\u4EBA\u53D1\u73B0\uFF0C\u8D76\u7D27\u8BA9\u90DD\u51AC\u6885\u7684\u53D1\u8FAB\u624E\u8D77\u6765\uFF0C\u5E76\u4E14\u4EB2\u624B\u7528\u624B\u7EE2\u4E3A\u90DD\u51AC\u6885\u624E\u597D\uFF0C\u5468\u79C9\u4E49\u7D27\u7D27\u62B1\u7740\u90DD\u51AC\u6885\uFF0C\u4E24\u4EBA\u5E78\u798F\u751C\u871C\u3002\u4E24\u4EBA\u4ECE\u767D\u6866\u6797\u51FA\u6765\u78B0\u89C1\u4E86\u8DEF\u8FC7\u7684\u5361\u8F66\uFF0C\u4E0A\u9762\u8DF3\u4E0B\u4E00\u4E2A\u5973\u5B69\u5B50\u548C\u90DD\u51AC\u6885\u8BF4\u8BDD\uFF0C\u4E5F\u63D0\u9192\u90DD\u51AC\u6885\u8863\u670D\u7CFB\u9519\u4E86\u6263\u5B50\u3002</p><p>\u5468\u79C9\u6606\u53BB\u6D17\u6FA1\u5802\u7B49\u7740\u4E54\u6625\u71D5\u7684\u6D88\u606F\uFF0C\u6070\u597D\u770B\u5230\u4E00\u4E2A\u53D7\u4F24\u7684\u4EBA\u88AB\u4EBA\u62AC\u4E0A\u4E86\u5E73\u677F\u8F66\uFF0C\u4F46\u662F\u5374\u6CA1\u4EBA\u9001\u8001\u4EBA\u53BB\u533B\u9662\u3002\u5468\u79C9\u6606\u4E3B\u52A8\u63D0\u51FA\u9001\u8001\u4EBA\u4E0A\u533B\u9662\uFF0C\u5728\u533B\u9662\u7684\u65F6\u5019\u5468\u79C9\u6606\u88AB\u95EE\u53CA\u662F\u4ECE\u54EA\u91CC\u4E0A\u73ED\u7684\uFF0C\u5468\u79C9\u6606\u5934\u4E5F\u4E0D\u56DE\u5C31\u79BB\u5F00\u4E86\u3002</p><p>\u56DE\u5230\u5BB6\u91CC\u7684\u65F6\u5019\uFF0C\u4E54\u6625\u71D5\u5DF2\u7ECF\u7B49\u5728\u80E1\u540C\u8DEF\u53E3\u4E86\uFF0C\u4E54\u6625\u71D5\u544A\u8BC9\u5468\u79C9\u6606\u4ED6\u4EEC\u5355\u4F4D\u7684\u6307\u6807\u5DF2\u7ECF\u6EE1\u4E86\uFF0C\u4ED6\u4E5F\u53EA\u6709\u4E00\u4E2A\u6279\u6761\u5B50\u7684\u6743\u5229\uFF0C\u4F46\u5728\u4E54\u6625\u71D5\u7684\u201C\u5A01\u80C1\u201D\u4E4B\u4E0B\uFF0C\u4E3B\u4EFB\u540C\u610F\u8BA9\u5468\u79C9\u6606\u53BB\uFF0C\u4E54\u6625\u71D5\u613F\u610F\u548C\u5468\u79C9\u6606\u5171\u8FDB\u9000\uFF0C\u5982\u679C\u4E0D\u8BA9\u5468\u79C9\u6606\u5979\u5C31\u8D70\uFF0C\u4E3B\u4EFB\u4E5F\u5BB3\u6015\u5931\u53BB\u8FD9\u4E2A\u4FEE\u811A\u597D\u7684\u6280\u5DE5\u3002\u4E54\u6625\u71D5\u5F00\u5FC3\u6781\u4E86\uFF0C\u53EE\u5631\u5468\u79C9\u6606\u660E\u5929\u6765\u63A5\u5979\u4E00\u8D77\u4E0A\u4E0B\u73ED\uFF0C\u6070\u597D\u53EF\u4EE5\u4E00\u8D77\u5207\u78CB\u4E00\u4E0B\u4FEE\u811A\u6280\u672F\u3002</p><p>\u5468\u79C9\u6606\u56DE\u53BB\u7684\u65F6\u5019\uFF0C\u6BCD\u4EB2\u9762\u524D\u6446\u7740\u4E00\u5C01\u7535\u62A5\uFF0C\u5468\u6BCD\u4EE5\u4E3A\u662F\u5468\u79C9\u4E49\u51FA\u4E8B\u4E86\uFF0C\u7740\u6025\u7684\u7B49\u7740\u5468\u79C9\u6606\u56DE\u53BB\u3002\u5468\u79C9\u6606\u770B\u5230\u7535\u62A5\u4E0A\u5199\u7740\u8BA9\u5468\u79C9\u6606\u627E\u8521\u6653\u5149\u529E\u7406\u5DE5\u4F5C\u7684\u4E8B\u60C5\u3002\u751F\u6015\u6BCD\u4EB2\u62C5\u5FC3\uFF0C\u5468\u79C9\u6606\u8C0E\u79F0\u662F\u5468\u79C9\u4E49\u79F0\u8D5E\u6BCD\u4EB2\u7EC7\u6BDB\u8863\u597D\u770B\u3002</p><p>\u7B2C\u4E8C\u5929\uFF0C\u4E54\u6625\u71D5\u7740\u6025\u7B49\u7740\u5468\u79C9\u6606\u63A5\u5979\u53BB\u4E0A\u73ED\uFF0C\u5374\u6CA1\u60F3\u5230\u5468\u79C9\u6606\u5DF2\u7ECF\u79BB\u5F00\u5BB6\u53BB\u627E\u8521\u6653\u5149\u4E86\u3002\u5230\u4E86\u5382\u5B50\u95E8\u53E3\u8C0E\u79F0\u662F\u8521\u6653\u5149\u7684\u5C0F\u8205\u5B50\uFF0C\u8FD9\u624D\u6DF7\u4E86\u8FDB\u53BB\uFF0C\u8521\u6653\u5149\u7ADF\u7136\u8FD8\u90FD\u627F\u8BA4\u4E86\uFF0C\u800C\u4E14\u5DF4\u4E0D\u5F97\u505A\u5468\u79C9\u6606\u7684\u59D0\u592B\u3002\u770B\u89C1\u5468\u79C9\u6606\u652F\u652F\u543E\u543E\u7684\u6837\u5B50\uFF0C\u8521\u6653\u5149\u5012\u662F\u5927\u65B9\u7684\u5F88\uFF0C\u5C31\u51B2\u7740\u90A3\u4E9B\u5E74\u53BB\u627E\u5468\u84C9\uFF0C\u5468\u79C9\u6606\u6CA1\u5C11\u7AEF\u8336\u5012\u6C34\uFF0C\u4ED6\u4E5F\u4F1A\u5BF9\u5468\u79C9\u6606\u7684\u4E8B\u60C5\u4E0D\u4F1A\u4E0D\u7BA1\u3002</p><p>\u4E54\u6D77\u71D5\u8BA9\u6BCD\u4EB2\u7ED9\u5468\u6BCD\u9001\u6765\u6D17\u6FA1\u7968\uFF0C\u5468\u6BCD\u4E5F\u5411\u4E54\u5927\u5A76\u8BF4\u660E\u4E86\u5F88\u559C\u6B22\u4E54\u6D77\u71D5\uFF0C\u4E24\u4E2A\u6BCD\u4EB2\u4E00\u5408\u8BA1\u8981\u7ED9\u5B69\u5B50\u4EEC\u521B\u9020\u673A\u4F1A\u3002\u7B49\u5230\u5468\u79C9\u4E49\u79BB\u5F00\u5BB6\u4E4B\u540E\uFF0C\u5C31\u7ED9\u5468\u79C9\u6606\u548C\u4E54\u6D77\u71D5\u5B89\u6392\u8BA1\u5212\u3002</p><p>\u8521\u6653\u5149\u8BE2\u95EE\u5468\u79C9\u6606\u60F3\u8981\u53BB\u54EA\u91CC\u4E0A\u73ED\uFF1F\u5F53\u5468\u79C9\u6606\u8BF4\u51FA\u60F3\u8981\u6765\u4ED6\u7684\u5382\u5B50\u65F6\u5019\uFF0C\u8521\u6653\u5149\u53C8\u8BF4\u4E86\u4E0D\u53EF\u80FD\u7684\u8BDD\uFF0C\u58F0\u79F0\u8FD9\u91CC\u5F88\u591A\u5DE5\u79CD\u90FD\u662F\u6280\u672F\u5DE5\u79CD\uFF0C\u6CA1\u6709\u6280\u672F\u6839\u672C\u8FDB\u4E0D\u6765\u3002\u5468\u79C9\u6606\u5931\u671B\u7684\u4F4E\u4E0B\u5934\uFF0C\u8521\u6653\u5149\u6765\u5230\u5468\u79C9\u6606\u9762\u524D\u5C06\u8336\u676F\u9012\u8FC7\u53BB\u3002</p>',14),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","2.html.vue"]]);export{h as default};
