import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as t,f as a}from"./app.1218f0f6.js";const c={},o=a('<h1 id="\u7B2C2\u96C6-\u4E1C\u65B9\u9752\u82CD\u7FFB\u8EAB\u51FA\u5854" tabindex="-1"><a class="header-anchor" href="#\u7B2C2\u96C6-\u4E1C\u65B9\u9752\u82CD\u7FFB\u8EAB\u51FA\u5854" aria-hidden="true">#</a> \u7B2C2\u96C6\uFF1A\u4E1C\u65B9\u9752\u82CD\u7FFB\u8EAB\u51FA\u5854</h1><p>\u5C0F\u5170\u82B1\u6B64\u65F6\u62E5\u6709\u4E1C\u65B9\u9752\u82CD\u7684\u6CD5\u529B\uFF0C\u6709\u53EF\u80FD\u51B2\u51FA\u5C01\u5370\u9003\u51FA\u53BB\u3002\u53EF\u5979\u77E5\u6653\uFF0C\u5BF9\u65B9\u662F\u660A\u5929\u5854\u5173\u62BC\u7684\u7F6A\u4ED9\uFF0C\u5979\u4E0D\u80FD\u653E\u4ED6\u51FA\u53BB\u3002\u5C0F\u5170\u82B1\u62C5\u5FC3\u8FD9\u4E2A\u7F6A\u4ED9\u4F1A\u6740\u4E86\u81EA\u5DF1\uFF0C\u4FBF\u63D0\u9192\u4ED6\u9053\uFF0C\u82E5\u662F\u81EA\u5DF1\u6B7B\u4E86\uFF0C\u90A3\u4E5F\u662F\u4ED6\u6B7B\u4E86\u3002\u8FD9\u53E5\u8BDD\u53CD\u5012\u63D0\u9192\u4E86\u4E1C\u65B9\u9752\u82CD\uFF0C\u53EA\u89C1\u4ED6\u624B\u8D77\u5200\u843D\uFF0C\u5C0F\u5170\u82B1\u8089\u8EAB\u7684\u5934\u53D1\u98D8\u98D8\u7136\u843D\u4E0B\u3002\u5C0F\u5170\u82B1\u6C14\u6025\uFF0C\u7528\u529B\u63EA\u4E86\u63EA\u5934\u53D1\uFF0C\u53EF\u662F\u5374\u75BC\u5F97\u53EB\u82E6\u8FDE\u8FDE\u3002\u8C01\u77E5\u5BF9\u9762\u7684\u4E1C\u65B9\u9752\u82CD\u4E5F\u80FD\u611F\u53D7\u5230\u75BC\u75DB\u3002\u4E1C\u65B9\u9752\u82CD\u5C31\u5FEB\u8981\u53D1\u6012\uFF0C\u7136\u800C\u5C0F\u5170\u82B1\u4E5F\u4E0D\u662F\u597D\u60F9\u7684\u3002\u53EA\u662F\u5979\u73B0\u5728\u75DB\u82E6\u4E07\u5206\uFF0C\u5B9E\u5728\u5FCD\u4E0D\u4F4F\u5C31\u9876\u7740\u4E1C\u65B9\u9752\u82CD\u8FD9\u5177\u8089\u8EAB\u5927\u54ED\u8D77\u6765\u3002\u4E1C\u65B9\u9752\u82CD\u53D7\u4E0D\u4F4F\u5979\u7684\u54ED\u58F0\uFF0C\u8D76\u5FD9\u6062\u590D\u4E86\u5BB9\u8C8C\u3002</p><p>\u773C\u4E0B\uFF0C\u8FD8\u662F\u51FA\u53BB\u8981\u7D27\u3002\u53EF\u5C0F\u5170\u82B1\u5374\u600E\u4E48\u4E5F\u4F7F\u4E0D\u51FA\u4E1C\u65B9\u9752\u82CD\u4F53\u5185\u7684\u6CD5\u529B\uFF0C\u5C3D\u7BA1\u4F7F\u51FA\u4E86\u4E5F\u63A7\u5236\u4E0D\u4F4F\uFF0C\u53EA\u80FD\u6454\u5012\u5728\u5730\u3002\u5982\u4ECA\u4E5F\u53EA\u6709\u60F3\u529E\u6CD5\u6362\u56DE\u8089\u8EAB\uFF0C\u53EF\u8FD9\u4E5F\u5C31\u610F\u5473\u7740\u8981\u91CD\u590D\u4E00\u904D\u6362\u8EAB\u524D\u7684\u64CD\u4F5C\u3002\u5C0F\u5170\u82B1\u614C\u5F20\u7684\u6478\u4E86\u6478\u5634\u5DF4\uFF0C\u773C\u770B\u7740\u4E1C\u65B9\u9752\u82CD\u5C31\u8981\u903C\u8FD1\u5979\u3002</p><p>\u542C\u95FB\u5BB9\u660A\u56DE\u6765\u4E86\uFF0C\u957F\u73E9\u8D76\u5FD9\u51C6\u5907\u4E86\u9152\u98DE\u8FDB\u4E86\u753B\u4E2D\u666F\u3002\u4E24\u4EBA\u5750\u5728\u5C0F\u821F\u4E0A\uFF0C\u968F\u6C34\u800C\u53BB\u3002\u957F\u73E9\u548C\u4ED6\u8BF4\u8D77\u4ECA\u5929\u7684\u4E8B\u60C5\uFF0C\u4E0D\u89E3\u4E91\u4E2D\u541B\u4E3A\u4F55\u4E0D\u613F\u610F\u6551\u90A3\u5C0F\u4ED9\u3002\u5BB9\u660A\u5012\u662F\u4E1D\u6BEB\u4E0D\u60CA\u8BB6\uFF0C\u4ED6\u89C1\u60EF\u4E86\u6C34\u4E91\u5929\u90A3\u7FA4\u4F2A\u541B\u5B50\u7684\u5634\u8138\u3002\u4E24\u4EBA\u4E3E\u676F\u540C\u996E\uFF0C\u4E0D\u7531\u5F97\u8BF4\u8D77\u4E86\u606F\u5C71\u7684\u795E\u5973\u3002\u4E0D\u77E5\u8FD9\u795E\u5973\u7A76\u7ADF\u662F\u4F55\u6A21\u6837\uFF0C\u8BA9\u4E16\u4EBA\u90FD\u79F0\u4E4B\u4E3A\u7EDD\u8272\u3002</p><p>\u5BB9\u660A\u65E0\u610F\u4E2D\u77A5\u89C1\u4E86\u957F\u73E9\u624B\u4E2D\u7684\u7D20\u5E15\uFF0C\u53EF\u5BF9\u65B9\u5374\u5E76\u4E0D\u613F\u610F\u5206\u4EAB\u3002\u5BB9\u660A\u65BD\u4E86\u6CD5\u672F\uFF0C\u90A3\u7D20\u5E15\u4FBF\u7EFD\u653E\u51FA\u4E00\u682A\u6C34\u5170\u82B1\u3002\u957F\u73E9\u60F3\u8981\u593A\u56DE\uFF0C\u53EF\u5374\u65E0\u6D4E\u4E8E\u4E8B\u3002\u89C1\u957F\u73E9\u53F9\u6C14\uFF0C\u5BB9\u660A\u4F3C\u4E4E\u662F\u731C\u51FA\u4ED6\u5FC3\u4E2D\u7684\u987E\u8651\uFF0C\u63D0\u9192\u4ED6\u9053\uFF0C\u4ED6\u4E0E\u90A3\u795E\u5973\u4E0D\u8FC7\u53EA\u6709\u5A5A\u7EA6\uFF0C\u66F4\u4F55\u51B5\u90A3\u795E\u5973\u5DF2\u7ECF\u8D70\u4E86\u4E09\u4E07\u5E74\u3002\u53EF\u5BF9\u4E8E\u957F\u73E9\u6765\u8BF4\uFF0C\u5C31\u7B97\u795E\u5973\u8D70\u4E86\u4E09\u4E07\u591A\u5E74\uFF0C\u4ED6\u4E5F\u8981\u5BFB\u5230\u5979\u7684\u8E2A\u8FF9\u3002</p><p>\u4E1C\u65B9\u9752\u82CD\u538B\u4F4F\u5C0F\u5170\u82B1\u5C31\u8981\u543B\u4E0A\u53BB\uFF0C\u5C0F\u5170\u82B1\u7EC8\u4E8E\u5FCD\u4E0D\u4F4F\u4F7F\u51FA\u5168\u529B\u62B5\u6297\uFF0C\u8C01\u77E5\u7ADF\u5C06\u4E1C\u65B9\u9752\u82CD\u9707\u5F00\u3002\u5C0F\u5170\u82B1\u89C1\u72B6\u5FC3\u91CC\u7740\u6025\uFF0C\u60F3\u8981\u4E0A\u53BB\u5E2E\u5FD9\uFF0C\u53EF\u5374\u63A7\u5236\u4E0D\u4F4F\u81EA\u5DF1\u7684\u6CD5\u529B\u3002\u53EA\u89C1\u5979\u903C\u8FD1\u4E1C\u65B9\u9752\u82CD\uFF0C\u543B\u4E86\u4E0A\u53BB\u3002\u5E78\u800C\u8FD9\u6CD5\u5B50\u6709\u7528\uFF0C\u4ED6\u4EEC\u53C8\u6362\u56DE\u4E86\u8EAB\u4F53\u3002\u4E1C\u65B9\u9752\u82CD\u6062\u590D\u6CD5\u529B\uFF0C\u53D8\u56DE\u771F\u8EAB\uFF0C\u4E0D\u4E00\u4F1A\u513F\u4FBF\u5C06\u660A\u5929\u5854\u7684\u5C01\u5370\u571F\u5D29\u74E6\u89E3\u3002</p><p>\u5C0F\u5170\u82B1\u5413\u5F97\u8FDE\u8FDE\u540E\u9000\uFF0C\u53EF\u5374\u8FD8\u662F\u88AB\u4E1C\u65B9\u9752\u82CD\u6293\u4F4F\u3002\u5FFD\u7136\uFF0C\u4E1C\u65B9\u9752\u82CD\u53D1\u89C9\u81EA\u5DF1\u4E5F\u80FD\u611F\u53D7\u5230\u5C0F\u5170\u82B1\u76F8\u540C\u7684\u75DB\u82E6\uFF0C\u6025\u5FD9\u653E\u5F00\u4E86\u5979\u3002\u8D81\u4E1C\u65B9\u9752\u82CD\u4E0D\u5907\uFF0C\u5C0F\u5170\u82B1\u8D76\u5FD9\u9003\u4E86\u51FA\u53BB\u3002\u4E1C\u65B9\u9752\u82CD\u6765\u4E0D\u53CA\u591A\u60F3\uFF0C\u4ED6\u51FA\u53BB\u540E\u4FEE\u8865\u4E86\u660A\u5929\u5854\u3002\u660A\u5929\u5854\u77AC\u95F4\u6062\u590D\u539F\u6837\uFF0C\u50CF\u662F\u8FD9\u4E9B\u4E8B\u60C5\u4ECE\u672A\u53D1\u751F\u8FC7\u4E00\u822C\u3002</p><p>\u4E00\u51FA\u6765\uFF0C\u5C0F\u5170\u82B1\u4FBF\u7740\u6025\u67E5\u770B\u957F\u73E9\u7684\u547D\u7C3F\u3002\u53D1\u73B0\u547D\u7C3F\u5DF2\u7ECF\u6062\u590D\u539F\u6837\uFF0C\u5C0F\u5170\u82B1\u5FC3\u4E2D\u9AD8\u5174\uFF0C\u4E00\u8DEF\u90FD\u54FC\u7740\u5C0F\u66F2\u3002\u6CA1\u8D70\u51E0\u6B65\uFF0C\u5C31\u77A7\u89C1\u4E86\u957F\u73E9\u548C\u5BB9\u660A\uFF0C\u5C0F\u5170\u82B1\u8D76\u5FD9\u8EB2\u5230\u5927\u6811\u540E\u9762\u3002\u5C31\u5728\u4E24\u4EBA\u5FEB\u8981\u903C\u8FD1\u65F6\uFF0C\u4E0D\u77E5\u54EA\u91CC\u95EA\u51FA\u4E24\u4E2A\u7F6A\u4ED9\uFF0C\u5438\u5F15\u4E86\u4ED6\u4EEC\u7684\u6CE8\u610F\u3002\u4F17\u4EBA\u79BB\u5F00\u540E\uFF0C\u957F\u73E9\u8FD8\u662F\u89C9\u5F97\u4E0D\u59A5\uFF0C\u4ED6\u7F13\u7F13\u9760\u8FD1\u5927\u6811\uFF0C\u53EF\u5374\u5BFB\u4E0D\u5230\u4EFB\u4F55\u8E2A\u5F71\u3002\u548C\u4E91\u4E2D\u541B\u6C47\u62A5\u4E4B\u540E\uFF0C\u957F\u73E9\u5FFD\u7136\u5BDF\u89C9\u81EA\u5DF1\u6389\u4E86\u624B\u5E15\uFF0C\u8D76\u5FD9\u56DE\u53BB\u5BFB\u627E\u3002\u53EF\u627E\u904D\u4E86\u6574\u4E2A\u6797\u5B50\uFF0C\u4E5F\u5BFB\u4E0D\u5230\u4EFB\u4F55\u5E15\u5B50\u7684\u8E2A\u8FF9\u3002</p><p>\u4E1C\u65B9\u9752\u82CD\u6765\u5230\u5FD8\u5DDD\uFF0C\u5524\u9192\u4E86\u81EA\u5DF1\u7684\u526F\u5C06\u6B87\u9619\uFF0C\u51C6\u5907\u524D\u5F80\u7384\u865A\u4E4B\u5883\u3002\u671B\u7740\u90A3\u4E9B\u88AB\u5C01\u5370\u7740\u7684\u6708\u65CF\u58EB\u5175\uFF0C\u4E09\u4E07\u5E74\u524D\u90A3\u573A\u4ED7\u4E0D\u7531\u5F97\u6D6E\u73B0\u773C\u524D\u3002\u4E09\u4E07\u5E74\u524D\uFF0C\u8D64\u6C34\u5973\u5B50\u4E0E\u4E1C\u65B9\u9752\u82CD\u4E00\u51B3\u9AD8\u4E0B\uFF0C\u53CC\u65B9\u4E00\u65F6\u96BE\u5206\u4F2F\u4EF2\u3002\u53EF\u6700\u540E\uFF0C\u4E1C\u65B9\u9752\u82CD\u8FD8\u662F\u8F7B\u654C\u4E86\u3002</p><p>\u6B64\u65F6\uFF0C\u5C0F\u5170\u82B1\u6B63\u5728\u6D77\u5E02\u6E38\u73A9\u3002\u7559\u82B3\u9601\u9601\u4E3B\u8776\u8863\u6B63\u5728\u5BFB\u627E\u4E3B\u4E0A\u8981\u627E\u7684\u82B1\u7CBE\uFF0C\u53EF\u5374\u6CA1\u6709\u4EFB\u4F55\u6536\u83B7\u3002\u5C0F\u5170\u82B1\u627E\u5230\u7ED3\u9ECE\uFF0C\u60F3\u8981\u548C\u5979\u4E70\u7075\u836F\uFF0C\u53EF\u5374\u65E0\u5948\u7075\u77F3\u4E0D\u591F\u3002\u6B63\u5DE7\uFF0C\u7ED3\u9ECE\u8981\u9001\u8863\u88F3\u5230\u7559\u82B3\u9601\u53BB\uFF0C\u4FBF\u60F3\u8BA9\u5C0F\u5170\u82B1\u5E2E\u5FD9\u8DD1\u817F\u62B5\u8D26\u3002</p><p>\u521A\u5230\u7559\u82B3\u9601\uFF0C\u5C0F\u5170\u82B1\u5C31\u770B\u5230\u4E86\u88AB\u6293\u6765\u7684\u5C0F\u82B1\u7CBE\u3002\u4E3A\u4E86\u5C06\u5979\u5E26\u51FA\u53BB\uFF0C\u5C0F\u5170\u82B1\u628A\u5979\u53D8\u56DE\u4E86\u771F\u8EAB\u3002\u53EF\u4EE4\u5C0F\u5170\u82B1\u600E\u4E48\u4E5F\u6CA1\u6709\u60F3\u5230\u7684\u662F\uFF0C\u8FD9\u7559\u82B3\u9601\u91CC\u7ADF\u85CF\u7740\u547D\u7C3F\u3002\u5979\u5FAA\u7740\u624B\u4E0A\u7075\u77F3\u7684\u611F\u5E94\uFF0C\u627E\u5230\u4E86\u90A3\u679A\u547D\u7C3F\uFF0C\u53EF\u5374\u65E0\u6CD5\u9760\u8FD1\u3002\u65E0\u5948\uFF0C\u5979\u53EA\u597D\u501F\u7528\u7075\u77F3\u7684\u529B\u91CF\uFF0C\u5C06\u547D\u7C3F\u53EC\u4E86\u8FC7\u6765\u3002\u53EF\u90A3\u547D\u7C3F\u5374\u662F\u4E00\u679A\u65E0\u540D\u547D\u7C3F\u3002</p><p>\u4E1C\u65B9\u9752\u82CD\u5FFD\u7136\u611F\u53D7\u5230\u76AE\u80A4\u5E26\u6765\u7684\u75DB\u82E6\uFF0C\u77E5\u6653\u662F\u5C0F\u5170\u82B1\u9047\u9669\u3002\u6B64\u65F6\uFF0C\u5C0F\u5170\u82B1\u548C\u90A3\u5C0F\u82B1\u7CBE\u88AB\u5E26\u5230\u4E86\u5316\u9B42\u589F\u3002\u8FD8\u6CA1\u7B49\u5C0F\u5170\u82B1\u53CD\u5E94\u8FC7\u6765\uFF0C\u5C0F\u82B1\u7CBE\u5DF2\u7ECF\u88AB\u63A8\u5230\u5D16\u4E0B\uFF0C\u6D88\u5931\u5728\u718A\u718A\u5927\u706B\u4E2D\u3002\u968F\u540E\uFF0C\u5C0F\u5170\u82B1\u4E5F\u88AB\u4E00\u638C\u51FB\u4E2D\uFF0C\u6454\u5012\u4E0B\u53BB\u3002</p>',12),r=[o];function _(n,s){return p(),t("div",null,r)}const h=e(c,[["render",_],["__file","2.html.vue"]]);export{h as default};
