import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.86bca16c.js";const c={},o=a('<h1 id="\u7B2C2\u96C6-\u795E\u79D8\u4EBA\u53D1\u52A8\u4E5D\u5BAB\u683C\u6E38\u620F" tabindex="-1"><a class="header-anchor" href="#\u7B2C2\u96C6-\u795E\u79D8\u4EBA\u53D1\u52A8\u4E5D\u5BAB\u683C\u6E38\u620F" aria-hidden="true">#</a> \u7B2C2\u96C6\uFF1A\u795E\u79D8\u4EBA\u53D1\u52A8\u4E5D\u5BAB\u683C\u6E38\u620F</h1><p>\u6848\u53D1\u5F53\u5929\uFF0C\u5F20\u8D85\u4EBA\u5728\u5F02\u5730\uFF0C\u53E3\u4F9B\u5374\u4E0E\u6848\u60C5\u6781\u5176\u543B\u5408\uFF0C\u5C24\u5176\u4ED6\u5148\u627F\u8BA4\u6740\u4EBA\u7D27\u63A5\u63A8\u7FFB\uFF0C\u4E25\u826F\u627E\u4E0D\u51FA\u5176\u4E2D\u5173\u8054\u52A8\u673A\uFF0C\u5176\u4ED6\u4EBA\u4E5F\u90FD\u6BEB\u65E0\u5934\u7EEA\u3002\u6B63\u5F53\u4E13\u6848\u7EC4\u9677\u5165\u8FF7\u832B\u72B6\u6001\u4E4B\u9645\uFF0C\u5F20\u6653\u5029\u5FFD\u7136\u6536\u5230\u4E00\u5C01\u533F\u540D\u4FE1\u4EF6\uFF0C\u91CC\u9762\u653E\u6709\u4E00\u5F20\u5C40\u90E8\u7167\u7247\u548C\u51E0\u5341\u5B57\u5185\u5BB9\uFF0C\u843D\u6B3E\u201CHGP\u201D\u3002</p><p>\u901A\u8FC7\u5185\u5BB9\u4E86\u89E3\uFF0C\u5BC4\u4FE1\u4EBA\u6253\u7B97\u53D1\u52A8\u4E5D\u5BAB\u683C\u6E38\u620F\uFF0C\u4ECE\u5373\u523B\u8D77\u6BCF\u9694\u4E09\u5929\u90AE\u5BC4\u4E00\u5F20\u7167\u7247\uFF0C\u8FDE\u7EED\u4E8C\u5341\u56DB\u5929\u51D1\u8DB3\u4E5D\u5F20\u7167\u7247\u516C\u5F00\u6740\u5BB3\u6C5F\u9633\u7684\u51F6\u624B\uFF0C\u5E76\u4E14\u8981\u6C42\u300A\u6C5F\u6F6D\u665A\u62A5\u300B\u52A1\u5FC5\u5C06\u7167\u7247\u520A\u767B\u5934\u7248\uFF0C\u5426\u5219\u4FBF\u4F1A\u5F15\u7206\u5B89\u88C5\u5728\u57CE\u5E02\u67D0\u5904\u7684\u70B8\u5F39\u3002</p><p>\u4E3B\u7F16\u9648\u660E\u751F\u5F97\u77E5\u6B64\u4E8B\u540E\u60CA\u559C\u4EA4\u52A0\uFF0C\u7ACB\u9A6C\u5429\u5490\u5F20\u6653\u5029\u8BA9\u91C7\u7F16\u5BA4\u51C6\u5907\u77ED\u8BAF\uFF0C\u987A\u5E26\u5411\u6C5F\u8C2D\u5E02\u5C40\u6C47\u62A5\u4EA4\u63A5\u3002\u7531\u4E8E\u5F20\u6653\u5029\u5148\u524D\u64B0\u5199\u8FC7\u5173\u4E8E\u5367\u5E95\u5211\u8B66\u5218\u660E\u6D0B\u7684\u62A5\u9053\uFF0C\u7EE7\u800C\u5F15\u53D1\u70ED\u8BAE\uFF0C\u5BFC\u81F4\u5979\u540D\u6C14\u5927\u6DA8\uFF0C\u6781\u5177\u53F7\u53EC\u529B\u3002</p><p>\u6216\u8BB8\u520A\u767B\u7167\u7247\u5C06\u4F1A\u5F15\u8D77\u793E\u4F1A\u6050\u614C\uFF0C\u4F46\u662F\u4E25\u826F\u654F\u9510\u53D1\u73B0\u5BC4\u4FE1\u4EBA\u540C\u6837\u63D0\u53CA\u4E8C\u5341\u56DB\u5929\uFF0C\u8FD9\u4E0E\u5F20\u8D85\u6240\u8A00\u4E0D\u8C0B\u800C\u5408\u3002\u4E13\u6848\u7EC4\u7ECF\u8FC7\u8BE6\u7EC6\u8BA8\u8BBA\u540E\uFF0C\u51B3\u5B9A\u5C06\u8BA1\u5C31\u8BA1\uFF0C\u91C7\u7528\u9650\u91CF\u53D1\u552E\u3001\u5B9A\u70B9\u6295\u653E\u62A5\u7EB8\u7684\u65B9\u5F0F\uFF0C\u5F15\u86C7\u51FA\u6D1E\u3002</p><p>\u867D\u7136\u5F20\u8D85\u5426\u8BA4\u4E5D\u5BAB\u683C\u7167\u7247\u8DDF\u4ED6\u6709\u5173\uFF0C\u4F46\u662F\u66B4\u9732\u4E00\u4E2A\u91CD\u8981\u7EC6\u8282\uFF0C\u4ED6\u4F5C\u4E3A\u9AD8\u5EA6\u8FD1\u89C6\u773C\u60A3\u8005\uFF0C\u4ECE\u672A\u5750\u8FC7\u5730\u94C1\u4E09\u53F7\u7EBF\uFF0C\u5374\u5728\u629B\u5C38\u5F53\u5929\u4E0D\u5E26\u773C\u955C\uFF0C\u51C6\u786E\u627E\u5230\u5357\u95E8\u51FA\u53E3\uFF0C\u9664\u975E\u4E8B\u5148\u65E9\u5DF2\u8E29\u70B9\u3002</p><p>\u5C5E\u4E0B\u5F00\u8F66\u8F7D\u7740\u4E25\u826F\u6765\u5230\u6848\u53D1\u73B0\u573A\u590D\u67E5\uFF0C\u62B5\u8FBE\u65F6\u5DF2\u8FD1\u534A\u591C\uFF0C\u623F\u5B50\u9762\u79EF\u4E0D\u5927\uFF0C\u5BA2\u5385\u8FDE\u63A5\u9633\u53F0\uFF0C\u7ECF\u7531\u6708\u5149\u7167\u5C04\uFF0C\u7AD9\u5728\u95E8\u53E3\u4FBF\u5C06\u5168\u8C8C\u6253\u91CF\u6E05\u695A\u3002\u4FE9\u4EBA\u52D8\u9A8C\u5C38\u4F53\u6240\u5728\u4F4D\u7F6E\uFF0C\u6CA1\u60F3\u5230\u4E00\u540D\u5973\u5B50\u7ADF\u4ECE\u7A97\u5E18\u540E\u9762\u51FA\u6765\uFF0C\u6B64\u4EBA\u6B63\u662F\u674E\u9759\uFF0C\u4F34\u968F\u706F\u5149\u4EAE\u8D77\uFF0C\u9010\u6E10\u9732\u51FA\u59E3\u597D\u9762\u5BB9\u3002</p><p>\u674E\u9759\u4E0D\u4EC5\u6F02\u4EAE\u8FD8\u6709\u6C14\u8D28\uFF0C\u9762\u5BF9\u4E25\u826F\u76D8\u95EE\u6BEB\u65E0\u754F\u60E7\uFF0C\u53CD\u800C\u5927\u65B9\u56DE\u7B54\u76F8\u5173\u95EE\u9898\uFF0C\u5305\u62EC\u5979\u5BF9\u6C5F\u9633\u7684\u4E0D\u582A\u5370\u8C61\u3002\u5373\u4FBF\u6C5F\u9633\u66FE\u662F\u5F20\u8D85\u5F97\u610F\u95E8\u751F\uFF0C\u53EF\u4ED6\u672C\u4EBA\u58F0\u540D\u72FC\u85C9\uFF0C\u4E0D\u4F46\u5728\u68C0\u5BDF\u5B98\u671F\u95F4\u6536\u53D7\u8D3F\u8D42\uFF0C\u8FD8\u5411\u5F20\u8D85\u501F\u8D70\u4E09\u5341\u4E07Du\u535A\uFF0C\u5E76\u4E14\u548C\u522B\u4EBA\u4FDD\u6301\u4E0D\u6B63\u5F53\u7537\u5973\u5173\u7CFB\uFF0C\u81F4\u4F7F\u524D\u59BB\u9009\u62E9\u79BB\u5A5A\u3002</p><p>\u4E13\u6848\u7EC4\u53D6\u56DE\u7269\u8BC1\u9274\u5B9A\u79D1\u7684\u7ED3\u679C\uFF0C\u518D\u7ED3\u5408\u5211\u8B66\u8001\u5218\u5728\u6C5F\u6F6D\u665A\u62A5\u6240\u91C7\u96C6\u7684\u4FE1\u606F\uFF0C\u56E0\u6B64\u786E\u5B9A\u7167\u7247\u53EA\u6709\u5F20\u6653\u5029\u548C\u9648\u660E\u751F\u7684\u6307\u7EB9\uFF0C\u800C\u4FE1\u5C01\u4E0A\u7684\u6307\u7EB9\u5219\u662F\u6536\u53D1\u5BA4\u8001\u5468\uFF0C\u4EE5\u53CA\u90AE\u5C40\u5DE5\u4F5C\u4EBA\u5458\u3002\u6307\u7EB9\u6CA1\u80FD\u63D0\u4F9B\u6709\u7528\u7EBF\u7D22\uFF0C\u5BC4\u4FE1\u4EBA\u4E5F\u5177\u6709\u53CD\u4FA6\u5BDF\u80FD\u529B\uFF0C\u4E13\u6848\u7EC4\u552F\u6709\u5BF9\u6BD4\u7167\u7247\u80CC\u666F\uFF0C\u786E\u5B9A\u80CC\u666F\u51FA\u81EA\u5E73\u5EB7\u53BF\u7684\u5CF0\u5C71\u8DEF\u3002</p><p>\u4E25\u826F\u548C\u5218\u660E\u6D0B\u65E2\u540C\u4E8B\u4EA6\u540C\u5B66\uFF0C\u4FE9\u4EBA\u5173\u7CFB\u751A\u597D\uFF0C\u53EF\u60DC\u5728\u90A3\u6B21\u4EFB\u52A1\u4E2D\uFF0C\u4E25\u826F\u56E0\u9611\u5C3E\u708E\u624B\u672F\u65E0\u6CD5\u53C2\u52A0\uFF0C\u6700\u7EC8\u6362\u6210\u5218\u660E\u6D0B\uFF0C\u4ECE\u800C\u9020\u6210\u4ED6\u7684\u610F\u5916\uFF0C\u5982\u4ECA\u53EA\u80FD\u5F85\u5728\u7CBE\u795E\u75C5\u9662\u3002\u5F20\u6653\u5029\u5BF9\u5218\u660E\u6D0B\u5341\u5206\u4E86\u89E3\uFF0C\u540C\u6837\u4E5F\u8DDF\u8E2A\u91C7\u8BBF\u8FC7\u6C5F\u9633\uFF0C\u4E24\u4E2A\u4EBA\u62E5\u6709\u76F8\u540C\u7684\u60B2\u5267\u6027\uFF0C\u53EA\u4E0D\u8FC7\u5218\u660E\u6D0B\u6210\u4E3A\u82F1\u96C4\uFF0C\u88AB\u4F17\u4EBA\u5C0A\u656C\uFF0C\u800C\u6C5F\u9633\u5728\u671F\u6EE1\u91CA\u653E\u540E\u9760\u7740\u7EF4\u4FEE\u624B\u673A\u5EA6\u65E5\uFF0C\u76F4\u5230\u9047\u5BB3\u3002</p><p>\u8C03\u67E5\u6BEB\u65E0\u8FDB\u5C55\uFF0C\u4E25\u826F\u6253\u7B97\u4ECE\u6C5F\u9633\u5904\u5165\u624B\uFF0C\u4ED6\u5728\u9057\u7269\u91CC\u53D1\u73B0\u4FAF\u8D35\u5E73\u7684\u8EAB\u4EFD\u8BC1\u590D\u5370\u4EF6\uFF0C\u654F\u9510\u5BDF\u89C9\u4FAF\u8D35\u5E73\u7684\u62FC\u97F3\u9996\u4F4D\u5B57\u6BCD\u4E0E\u533F\u540D\u4FE1\u843D\u6B3E\u76F8\u540C\u3002\u4EFB\u73A5\u5A77\u770B\u5230\u6C5F\u9633\u5728\u68C0\u5BDF\u5B98\u624B\u518C\u91CC\u7559\u4E0B\u7684\u540D\u5B57\uFF0C\u7ACB\u9A6C\u8C03\u53D6\u4FAF\u8D35\u5E73\u6863\u6848\uFF0C\u679C\u7136\u5730\u5740\u6B63\u662F\u5E73\u5EB7\u53BF\uFF0C\u53EA\u4E0D\u8FC7\u4EBA\u65E9\u57282000\u5E74\u5C31\u5DF2\u53BB\u4E16\u3002</p><p>\u5F20\u6653\u5029\u5EFA\u8BAE\u4E25\u826F\u53BB\u627E\u5434\u7231\u53EF\u4E86\u89E3\u60C5\u51B5\uFF0C\u5979\u662F\u6C5F\u9633\u524D\u5973\u53CB\uFF0C\u4E5F\u662F\u5F71\u54CD\u6539\u53D8\u6C5F\u9633\u4E00\u751F\u7684\u4EBA\u3002\u4E25\u826F\u548C\u4EFB\u73A5\u5A77\u6839\u636E\u5730\u5740\u6765\u5230\u5434\u7231\u53EF\u5BB6\u4E2D\uFF0C\u6B64\u65F6\u5434\u7231\u53EF\u5DF2\u4E3A\u4EBA\u5987\uFF0C\u4F46\u5979\u63D0\u53CA\u6C5F\u9633\u65F6\uFF0C\u4ECD\u662F\u5E26\u7740\u6EE1\u6EE1\u7231\u610F\u3002</p><p>\u4E03\u5E74\u524D\u7684\u6C5F\u9633\uFF0C\u6B63\u503C\u610F\u6C14\u98CE\u53D1\uFF0C\u5728\u5916\u4EBA\u773C\u4E2D\u770B\u6765\uFF0C\u4ED6\u662F\u8DB3\u591F\u51FA\u8272\u4E14\u4EE4\u4EBA\u9A84\u50B2\uFF0C\u4E0D\u4EC5\u4EE5\u4F18\u5F02\u6210\u7EE9\u8003\u4E0A\u7814\u7A76\u751F\uFF0C\u8FDB\u5165\u68C0\u5BDF\u9662\uFF0C\u751A\u81F3\u88AB\u5355\u4F4D\u5217\u4E3A\u91CD\u70B9\u57F9\u517B\u5BF9\u8C61\uFF0C\u76F4\u63A5\u63D0\u62D4\u4E3A\u4FA6\u67E5\u76D1\u7763\u79D1\u79D1\u957F\u3002\u5F53\u65F6\u5434\u7231\u53EF\u7684\u7236\u4EB2\u4EFB\u5E02\u68C0\u5BDF\u9662\u526F\u68C0\u5BDF\u957F\uFF0C\u975E\u5E38\u652F\u6301\u5973\u513F\u8DDF\u6C5F\u9633\u4EA4\u5F80\uFF0C\u4E24\u4E2A\u4EBA\u6069\u7231\u751C\u871C\uFF0C\u751A\u81F3\u8FDB\u5C55\u5230\u8C08\u5A5A\u8BBA\u5AC1\uFF0C\u5948\u4F55\u56E0\u4E3A\u674E\u9759\u7684\u51FA\u73B0\uFF0C\u5F7B\u5E95\u6253\u7834\u8FD9\u4EFD\u5B89\u7A33\u751F\u6D3B\u3002</p><p>\u674E\u9759\u4E0E\u6C5F\u9633\u662F\u540C\u5B66\uFF0C\u56E0\u76F8\u8C8C\u51FA\u4F17\u6210\u4E3A\u73ED\u82B1\uFF0C\u7EB5\u7136\u8FFD\u6C42\u8005\u6570\u4E0D\u80DC\u6570\uFF0C\u53EF\u5979\u552F\u72EC\u7231\u4E0A\u7A77\u5C0F\u5B50\u4FAF\u8D35\u5E73\u3002\u7136\u800C\u4FAF\u8D35\u5E73\u652F\u6559\u65F6\u7A81\u7136\u6B7B\u4EA1\uFF0C\u5B66\u6821\u5BF9\u5916\u5BA3\u79F0\u4ED6\u5916\u51FA\u6E38\u6CF3\u65F6\u6EBA\u6CB3\u6240\u81F4\uFF0C\u4F46\u662F\u8B66\u65B9\u7684\u7ED3\u6848\u6750\u6599\u91CC\u5374\u5199\u7740\u4FAF\u8D35\u5E73\u5F3AJ\u5987\u5973\uFF0C\u56E0\u4E3A\u62D2\u7EDD\u902E\u6355\u9003\u8DD1\uFF0C\u610F\u5916\u5760\u6CB3\u3002</p><p>\u6C5F\u9633\u4FE1\u4EFB\u4FAF\u8D35\u5E73\u4EBA\u54C1\uFF0C\u674E\u9759\u4E5F\u5728\u4E24\u5E74\u5185\u627E\u5230\u5F88\u591A\u53EF\u7591\u7EBF\u7D22\uFF0C\u5176\u4E2D\u5305\u62EC\u4FAF\u8D35\u5E73\u652F\u6559\u73ED\u7EA7\u7684\u4E00\u540D\u5973\u5B69\u559D\u519C\u836F\u81EA\u6740\uFF0C\u6B7B\u524D\u66FE\u906D\u6027\u4FB5\uFF0C\u540E\u6765\u4FAF\u8D35\u5E73\u4E3A\u5979\u5C61\u6B21\u5411\u4E0A\u7EA7\u9886\u5BFC\u4E3E\u62A5\u8C03\u67E5\u7533\u8BF7\uFF0C\u7ED3\u679C\u5168\u90FD\u77F3\u6C89\u5927\u6D77\u3002</p><p>\u674E\u9759\u8BA4\u4E3A\u4FAF\u8D35\u5E73\u6B7B\u4E8E\u8C0B\u6740\uFF0C\u6240\u4EE5\u60F3\u8BA9\u6C5F\u9633\u5E2E\u5FD9\u7FFB\u6848\uFF0C\u627E\u51FA\u771F\u76F8\u3002\u6C5F\u9633\u4ECE\u4E8B\u68C0\u5BDF\u5B98\u5DE5\u4F5C\u5DF2\u6709\u65F6\u65E5\uFF0C\u65E9\u5C31\u89C1\u60EF\u8BB8\u591A\u7591\u96BE\u60AC\u6848\uFF0C\u66F4\u77E5\u9053\u5B9E\u9645\u529E\u6848\u7684\u56F0\u96BE\u5BFC\u81F4\u6700\u540E\u59A5\u534F\u3002\u59A5\u534F\uFF0C\u662F\u4E00\u4E2A\u4EBA\u6210\u719F\u7684\u6807\u5FD7\uFF0C\u53EF\u60DC\u5434\u7231\u53EF\u5E76\u672A\u6DF1\u601D\u719F\u8651\uFF0C\u5979\u5BF9\u8FD9\u8D77\u6848\u4EF6\u62B1\u6253\u4E0D\u5E73\uFF0C\u8981\u6C42\u6C5F\u9633\u575A\u6301\u6B63\u4E49\uFF0C\u5F7B\u67E5\u5230\u5E95\u3002\u6B63\u662F\u56E0\u4E3A\u5F53\u521D\u7684\u5E74\u8F7B\u51B2\u52A8\uFF0C\u5434\u7231\u53EF\u4FBF\u628A\u6C5F\u9633\u63A8\u5411\u4E00\u6761\u5B64\u72EC\u7684\u8DEF\uFF0C\u5E76\u4E14\u8D8A\u884C\u8D8A\u8FDC\u3002</p>',16),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","2.html.vue"]]);export{h as default};
