import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.e2de568c.js";const c={},o=a('<h1 id="\u7B2C2\u96C6-\u9648\u6C5F\u6CB3\u8131\u80CE\u6362\u9AA8" tabindex="-1"><a class="header-anchor" href="#\u7B2C2\u96C6-\u9648\u6C5F\u6CB3\u8131\u80CE\u6362\u9AA8" aria-hidden="true">#</a> \u7B2C2\u96C6\uFF1A\u9648\u6C5F\u6CB3\u8131\u80CE\u6362\u9AA8</h1><p>\u5C0F\u9A86\u505A\u597D\u4E86\u997C\uFF0C\u6765\u5230\u9662\u5B50\u91CC\u3002\u9648\u6C5F\u6CB3\u6B63\u5728\u79CD\u6811\uFF0C\u5C0F\u9A86\u6EE1\u5FC3\u6B22\u559C\uFF0C\u89C9\u5F97\u81EA\u5DF1\u7EC8\u4E8E\u6709\u4E86\u4E00\u4E2A\u5BB6\uFF0C\u5979\u5C06\u997C\u9012\u5230\u9648\u6C5F\u6CB3\u5634\u8FB9\uFF0C\u51C6\u5907\u5582\u7ED9\u4ED6\u5403\u3002\u9648\u6C5F\u6CB3\u5374\u8FD8\u6CA1\u9002\u5E94\u5C0F\u5144\u5F1F\u53D8\u6210\u4E86\u59D1\u5A18\u3002\u795E\u8272\u9887\u6709\u4E9B\u5C34\u5C2C\u3002</p><p>\u6B64\u65F6\uFF0C\u8FDC\u5728\u9648\u5BB6\u6751\uFF0C\u5F53\u5E74\u88AB\u6293\u8D70\u7684\u91D1\u6C34\u53D4\u7EC8\u4E8E\u56DE\u6765\u4E86\uFF0C\u4F46\u662F\uFF0C\u4ED6\u8981\u6C42\u6751\u6C11\u4EEC\u4E0D\u5F97\u518D\u4ECE\u4E8B\u9E21\u6BDB\u6362\u7CD6\u8FD9\u79CD\u884C\u4E3A\uFF0C\u53EA\u80FD\u8001\u8001\u5B9E\u5B9E\u5728\u5BB6\u79CD\u5730\u3002\u4E3A\u6B64\uFF0C\u4ED6\u751A\u81F3\u4E0B\u72E0\u5FC3\uFF0C\u5C06\u5404\u5BB6\u5404\u6237\u5BB6\u91CC\u7684\u5DE5\u5177\u90FD\u641C\u4E86\u51FA\u6765\uFF0C\u51C6\u5907\u4E00\u628A\u706B\u70E7\u6389\u3002\u4F17\u4EBA\u82E6\u82E6\u54C0\u6C42\uFF0C\u9648\u91D1\u6C34\u4E0D\u4E3A\u6240\u52A8\u3002\u4E3A\u4E86\u65AD\u6389\u5927\u5BB6\u4F19\u7684\u540E\u8DEF\u3002\u4ED6\u751A\u81F3\u72E0\u5FC3\uFF0C\u70E7\u6389\u4E86\u7956\u5E08\u7237\u7684\u753B\u50CF\u3002</p><p>\u4ECE\u6B64\uFF0C\u9648\u5BB6\u6751\u518D\u4E5F\u542C\u4E0D\u89C1\u62E8\u6D6A\u9F13\u58F0\u548C\u9E21\u6BDB\u6362\u7CD6\u7684\u5406\u559D\u58F0\u3002</p><p>\u51E0\u5E74\u540E\u7684\u4E00\u5929\uFF0C\u60F3\u8D77\u591A\u5E74\u6765\u6773\u65E0\u97F3\u4FE1\u7684\u9648\u6C5F\u6CB3\uFF0C\u9648\u91D1\u6C34\u5341\u5206\u62C5\u5FC3\u3002\u6B63\u5728\u6B64\u65F6\uFF0C\u5FFD\u7136\u542C\u89C1\u62E8\u6D6A\u9F13\u7684\u6E05\u8106\u54CD\u58F0\uFF0C\u4F17\u4EBA\u4E0D\u89E3\u3002\u8FD9\u58F0\u97F3\uFF0C\u9648\u5BB6\u6751\u4EBA\u5DF2\u6709\u591A\u5E74\u672A\u66FE\u542C\u5230\u4E86\u3002\u800C\u9648\u91D1\u6C34\u795E\u60C5\u604D\u60DA\uFF0C\u50CF\u662F\u9677\u5165\u4E86\u67D0\u79CD\u56DE\u5FC6\u3002</p><p>\u4F17\u4EBA\u5BFB\u58F0\u800C\u6765\uFF0C\u539F\u6765\u6B63\u662F\u5C0F\u9A86\uFF0C\u6B63\u56DB\u5904\u6253\u542C\u9648\u6C5F\u6CB3\u7684\u4E0B\u843D\u3002\u9648\u91D1\u6C34\u4EE4\u4F17\u4EBA\u62A2\u53BB\u5C0F\u9A86\u624B\u4E0A\u7684\u62E8\u6D6A\u9F13\uFF0C\u5C06\u5979\u8D76\u51FA\u6751\u53BB\u3002\u5C0F\u9A86\u4EE5\u6B7B\u76F8\u903C\uFF0C\u624D\u6700\u7EC8\u7559\u4E0B\u4E86\u8FD9\u4E2A\u62E8\u6D6A\u9F13\u3002\u90A3\u4E48\uFF0C\u9648\u6C5F\u6CB3\u53BB\u54EA\u513F\u4E86\u5462\uFF1F</p><p>\u4ECE\u4E49\u4E4C\u6765\u5F80\u5317\u4EAC\u7684\u706B\u8F66\u4E0A\uFF0C\u4E00\u4E2A\u5E74\u8F7B\u4EBA\uFF0C\u6000\u91CC\u63E3\u7740\u9E21\u86CB\u548C\u7CD6\uFF0C\u5411\u706B\u8F66\u4E0A\u7684\u4E58\u5BA2\u515C\u552E\u3002\u539F\u6765\u8FD9\u4EBA\u6B63\u662F\u9648\u6C5F\u6CB3\u3002\u540C\u6837\u662F\u4E49\u4E4C\u4EBA\u7684\u90B1\u82F1\u6770\u4E70\u4E86\u4E00\u5757\u513F\u7CD6\uFF0C\u9648\u6C5F\u6CB3\u5374\u6CA1\u6709\u6536\u94B1\uFF0C\u53CD\u800C\u662F\u8981\u4E86\u90B1\u82F1\u6770\u624B\u4E2D\u7684\u94A2\u7B14\u3002\u90B1\u82F1\u6770\u5BF9\u4ED6\u5341\u5206\u611F\u5174\u8DA3\uFF0C\u4FBF\u548C\u4ED6\u6500\u8C08\u8D77\u6765\u3002\u751A\u81F3\u8BA4\u4E3A\u9648\u6C5F\u6CB3\u9E21\u6BDB\u6362\u7CD6\u7684\u5C0F\u751F\u610F\uFF0C\u8FD0\u7528\u7684\u6B63\u662F\u7ECF\u6D4E\u5B66\u7684\u9053\u7406\u3002</p><p>\u706B\u8F66\u505C\u9760\u5FB7\u5DDE\u7AD9\uFF0C\u4E0B\u8F66\u4F11\u606F\u7684\u90B1\u82F1\u6770\u53C8\u9047\u4E0A\u4E86\u5356\u5B8C\u4E86\u4E1C\u897F\u7684\u9648\u6C5F\u6CB3\uFF0C\u94C1\u8F68\u8FB9\u4E24\u4EBA\u53C8\u804A\u4E86\u8D77\u6765\uFF0C\u624D\u77E5\u9053\u90B1\u82F1\u6770\u662F\u6062\u590D\u9AD8\u8003\u540E\u5230\u5317\u4EAC\u53BB\u4E0A\u5927\u5B66\u7684\u5927\u5B66\u751F\u3002\u9648\u6C5F\u6CB3\u542C\u8BF4\uFF0C\u5BF9\u4ED6\u5341\u5206\u5D07\u62DC\u3002\u53C8\u542C\u4ED6\u8BB2\u8BDD\u9887\u6709\u51E0\u5206\u9053\u7406\uFF0C\u4E5F\u6CA1\u6709\u4E1D\u6BEB\u8F7B\u89C6\u81EA\u5DF1\u8FD9\u4E2A\u9E21\u6BDB\u6362\u7CD6\u7684\u5C0F\u5546\u8D29\u7684\u610F\u601D\uFF0C\u4E0D\u7531\u5F97\u5BF9\u4ED6\u597D\u611F\u5927\u589E\u3002</p><p>\u8F66\u5F00\u4E86\uFF0C\u90B1\u82F1\u6770\u53D1\u73B0\u672C\u6765\u51C6\u5907\u79BB\u5F00\u7684\u9648\u6C5F\u6CB3\u53C8\u4E0A\u4E86\u706B\u8F66\u3002\u8FD9\u6B21\uFF0C\u9648\u6C5F\u6CB3\u662F\u7279\u5730\u4E3A\u5411\u90B1\u82F1\u6770\u8BF7\u6559\u800C\u6765\u3002\u4E24\u4EBA\u8A00\u8BED\u6295\u673A\u3002\u8D8A\u804A\u5174\u81F4\u8D8A\u9AD8\u3002\u7ADF\u4E00\u8DEF\u804A\u5230\u4E86\u5317\u4EAC\u3002\u5206\u79BB\u5728\u5373\uFF0C\u90B1\u82F1\u6770\u5631\u5490\u9648\u6C5F\u6CB3\u8981\u7ED9\u81EA\u5DF1\u5199\u4FE1\u3002\u53C8\u9F13\u52B1\u9648\u6C5F\u6CB3\u51FA\u53BB\u8D70\u8D70\u770B\u770B\u5916\u9762\u7684\u4E16\u754C\u3002\u9648\u6C5F\u6CB3\u542C\u540E\uFF0C\u82E5\u6709\u6240\u601D\u3002</p><p>\u591A\u5E74\u6CA1\u6709\u9648\u6C5F\u6CB3\u97F3\u8BAF\u7684\u9648\u5BB6\u6751\u7684\u6751\u6C11\u4EEC\u7EC8\u4E8E\u6536\u5230\u4E86\u9648\u6C5F\u6CB3\u7684\u6765\u4FE1\u3002\u539F\u6765\uFF0C\u8FD9\u51E0\u5E74\uFF0C\u4ED6\u51E0\u4E4E\u8E0F\u904D\u4E86\u6574\u4E2A\u4E2D\u56FD\u3002\u4ECE\u4E1C\u5230\u897F\uFF0C\u7531\u5357\u81F3\u5317\u3002\u4ED6\u53D1\u73B0\u5404\u5730\u7684\u98CE\u571F\u4EBA\u60C5\u4E0D\u540C\uFF0C\u5BF9\u4E0D\u540C\u5546\u54C1\u7684\u9700\u6C42\u4E5F\u4E0D\u540C\u3002\u4ECE\u4E2D\u53D1\u73B0\u4E86\u4E0D\u5C11\u7684\u5546\u673A\u3002</p><p>\u706B\u8F66\u4E0A\uFF0C\u4E00\u4E2A\u5E74\u9752\u59D1\u5A18\u5C06\u51E0\u4E2A\u7855\u5927\u7684\u5305\u88F9\u4ECE\u7A97\u53E3\u6254\u4E86\u4E0B\u53BB\uFF0C\u94C1\u9053\u8FB9\u51E0\u4E2A\u5987\u5973\u8D76\u5FD9\u5C06\u5305\u88F9\u6297\u8D70\u3002\u5979\u6B63\u662F\u5F53\u5E74\u7684\u5C0F\u9A86\uFF0C\u9A86\u7389\u73E0\u3002\u9A86\u7389\u73E0\u7A7A\u624B\u5927\u6447\u5927\u6446\u7684\u4E0B\u4E86\u706B\u8F66\u3002\u539F\u6765\u5979\u6B64\u6B21\u6B63\u662F\u4ECE\u91D1\u534E\u8FDB\u8D27\u56DE\u6765\uFF0C\u8C01\u77E5\u9053\u5374\u88AB\u6293\u4E2A\u6B63\u7740\u3002\u8981\u6293\u5979\u7684\u6B63\u662F\u5982\u4ECA\u5DF2\u7ECF\u5F53\u4E86\u9547\u957F\uFF0C\u4E25\u5389\u6253\u51FB\u6295\u673A\u5012\u628A\u7684\u9648\u91D1\u6C34\u3002</p><p>\u9A86\u7389\u73E0\u8D27\u7269\u88AB\u6263\uFF0C\u5341\u5206\u7740\u6025\uFF0C\u542C\u8BF4\u8C22\u53BF\u957F\u8981\u6765\uFF0C\u7279\u610F\u8DD1\u5230\u9547\u653F\u5E9C\u95E8\u53E3\u5927\u5435\u5927\u95F9\u3002\u88AB\u9648\u91D1\u6C34\u62E6\u4F4F\u3002\u9A86\u7389\u73E0\u4E00\u8BA1\u4E0D\u6210\uFF0C\u53C8\u751F\u4E00\u8BA1\u3002\u5979\u8054\u5408\u51E0\u4E2A\u5987\u5973\u51B2\u5230\u9A6C\u8DEF\u4E0A\u62E6\u4F4F\u4E86\u53BF\u957F\u7684\u8F66\u3002\u8F66\u4E0A\u5750\u7740\u7684\uFF0C\u8FD8\u6709\u521A\u521A\u5927\u5B66\u6BD5\u4E1A\u56DE\u5230\u5BB6\u4E61\u5DE5\u4F5C\u7684\u90B1\u82F1\u6770\u3002</p><p>\u8C22\u53BF\u957F\u548C\u90B1\u82F1\u6770\u542C\u8BF4\u4E86\u4E8B\u60C5\u539F\u59D4\uFF0C\u5E76\u6CA1\u6709\u5F53\u65F6\u8868\u6001\u3002\u51AF\u59D0\u4E0D\u653E\u5FC3\uFF0C\u529D\u8BF4\u7389\u73E0\u8DDF\u9648\u91D1\u6C34\u670D\u8F6F\u3002\u7389\u73E0\u60F3\u5230\u81EA\u5DF1\u548C\u5927\u5BB6\u7684\u8D27\u90FD\u88AB\u9648\u91D1\u6C34\u6263\u62BC\uFF0C\u65E0\u5948\u540C\u610F\u3002</p><p>\u665A\u4E0A\uFF0C\u7389\u73E0\u6765\u5230\u9648\u91D1\u6C34\u5BB6\uFF0C\u597D\u8A00\u76F8\u6C42\u5374\u88AB\u9648\u91D1\u6C34\u7F9E\u8FB1\u3002\u7389\u73E0\u63D0\u8D77\u9648\u6C5F\u6CB3\uFF0C\u5374\u5F15\u5F97\u9648\u91D1\u6C34\u66B4\u6012\u3002\u66F4\u662F\u8BF4\u7389\u73E0\u4E0D\u77E5\u7F9E\u803B\u3002\u7389\u73E0\u4E00\u6012\u4E4B\u4E0B\uFF0C\u6124\u7136\u79BB\u53BB\u3002</p><p>\u8C22\u53BF\u957F\u4E3B\u6301\u4F1A\u8BAE\u3002\u4E13\u95E8\u8BA8\u8BBA\u8FD9\u6B21\u7684\u4E8B\u60C5\u3002\u4F1A\u4E0A\u6709\u7684\u4EBA\u8BA4\u4E3A\u505A\u751F\u610F\u662F\u6295\u673A\u5012\u628A\u7684\u884C\u4E3A\uFF0C\u662F\u8D44\u672C\u4E3B\u4E49\u7684\u5C3E\u5DF4\u3002\u5E94\u8BE5\u575A\u51B3\u62B5\u5236\u3002\u800C\u90B1\u82F1\u6770\u5219\u63D0\u8D77\u4E86\u4ED6\u548C\u9648\u6C5F\u6CB3\u7684\u5F80\u4E8B\u3002\u8BA4\u4E3A\u505A\u751F\u610F\u5E76\u4E0D\u662F\u574F\u4E8B\uFF0C\u800C\u662F\u597D\u4E8B\u3002\u8C22\u53BF\u957F\u542C\u8BF4\u4E86\u8FD9\u4E2A\u4EBA\uFF0C\u5341\u5206\u611F\u5174\u8DA3\u3002\u800C\u9648\u91D1\u6C34\u548C\u95E8\u5916\u7684\u9A86\u7389\u73E0\u542C\u5230\u9648\u6C5F\u6CB3\u7684\u540D\u5B57\uFF0C\u5219\u5927\u4E3A\u60CA\u8BB6\u3002\u4F1A\u8BAE\u4E3E\u624B\u8868\u51B3\u662F\u5426\u5F52\u8FD8\u9A86\u7389\u73E0\u7B49\u4EBA\u7684\u8D27\u7269\uFF0C\u4F17\u4EBA\u5728\u542C\u5B8C\u8C22\u53BF\u957F\u53D1\u8A00\u540E\uFF0C\u7EB7\u7EB7\u4E3E\u624B\u3002\u9648\u91D1\u6C34\u65E0\u5948\u540C\u610F\u3002</p><p>\u653F\u5E9C\u8D34\u51FA\u544A\u793A\uFF0C\u4EE5\u540E\u505A\u751F\u610F\uFF0C\u4E0D\u7528\u518D\u62C5\u5FC3\u4E86\uFF0C\u767E\u59D3\u4E00\u7247\u6B22\u817E\u3002\u9A86\u7389\u73E0\u5728\u4EBA\u7FA4\u4E2D\u8BA4\u51FA\u4E86\u90B1\u82F1\u6770\uFF0C\u51B2\u4E0A\u524D\u53BB\u62E6\u4F4F\u5176\u53BB\u8DEF\uFF0C\u60F3\u8981\u6253\u63A2\u9648\u6C5F\u6CB3\u7684\u4E0B\u843D\u3002</p>',16),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","2.html.vue"]]);export{h as default};
