import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as t,f as e}from"./app.8a7d9ef5.js";const r={},c=e('<h1 id="\u8463\u5353\u7684\u72FC\u5B50\u91CE\u5FC3" tabindex="-1"><a class="header-anchor" href="#\u8463\u5353\u7684\u72FC\u5B50\u91CE\u5FC3" aria-hidden="true">#</a> \u8463\u5353\u7684\u72FC\u5B50\u91CE\u5FC3</h1><blockquote><p>\u5F20\u6E29\u4EE5\u8BCF\u4E66\u53EC\uFF08\u8463\uFF09\u5353\uFF0C\u5353\u826F\u4E45\u4E43\u8BE3\u6E29\uFF1B\u6E29\u8D23\u8BA9\u5353\uFF0C\u5353\u5E94\u5BF9\u4E0D\u987A\u3002\u5B59\u575A\u524D\u8033\u8BED\u8C13\u6E29\u66F0\uFF1A\u201C\u5353\u4E0D\u6016\u7F6A\u800C\u9E31\u5F20\u5927\u8BED\uFF0C\u5B9C\u4EE5\u53EC\u4E0D\u65F6\u81F3\uFF0C\u9648\u519B\u6CD5\u65A9\u4E4B\u3002\u201D\u2026\u2026\u6E29\u4E0D\u5FCD\u53D1\uFF0C\u4E43\u66F0\uFF1A\u201C\u541B\u4E14\u8FD8\uFF0C\u5353\u5C06\u7591\u4EBA\u3002\u201D\u575A\u9042\u51FA\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274 \xB7 \u5377\u4E94\u5341\u516B \xB7 \u6C49\u7EAA\u4E94\u5341\u300B</strong></p><p>\u8463\u5353\u7684\u72FC\u5B50\u91CE\u5FC3\uFF0C\u65E9\u5E74\u95F4\u5C31\u5DF2\u663E\u9732\u65E0\u7591\u3002</p><p>\u8463\u5353\u5728\u6853\u5E1D\u5E74\u95F4\u5373\u4ECE\u519B\uFF0C\u4E3B\u8981\u662F\u5728\u51C9\u5DDE\u4E00\u5E26\u548C\u7F8C\u4EBA\u4F5C\u6218\u3002\u51E0\u5341\u5E74\u95F4\uFF0C\u53CC\u65B9\u4E92\u6709\u80DC\u8D1F\uFF0C\u5B98\u4F4D\u4E5F\u968F\u4E4B\u8D77\u8D77\u4F0F\u4F0F\u3002\u8FD9\u6837\u7684\u8FB9\u5C06\uFF0C\u5728\u5927\u6C49\u671D\u8FD1\u56DB\u767E\u5E74\u7684\u5386\u53F2\u4E2D\u4E0D\u77E5\u51E1\u51E0\uFF0C\u5927\u90E8\u5206\u6700\u540E\u90FD\u6DF9\u6CA1\u5728\u4E86\u5386\u53F2\u7684\u5C18\u57C3\u4E2D\u3002<strong>\u53EA\u6709\u6781\u5C11\u6570\u56E0\u7F18\u9645\u4F1A\uFF0C\u80FD\u7559\u4E0B\u81EA\u5DF1\u7684\u540D\u5B57\u2014\u2014\u8463\u5353\uFF0C\u5C31\u662F\u8FD9\u6781\u5C11\u6570\u4E2D\u7684\u4E00\u4E2A\u3002</strong></p><p>\u8463\u5353\u56E0\u9EC4\u5DFE\u4E4B\u4E71\u800C\u53D1\u8FF9\uFF0C\u4F46\u4ED6\u7684\u4EBA\u751F\u6545\u4E8B\u548C\u5E73\u53DB\u5374\u6CA1\u6709\u592A\u5927\u5173\u7CFB\u3002\u5F53\u65F6\u671D\u5EF7\u7684\u786E\u66FE\u7ECF\u5F81\u53EC\u8FC7\u4ED6\u8BA8\u4F10\u9EC4\u5DFE\u519B\uFF0C\u4F46\u4ED6\u5374\u6253\u8D25\u4E86\uFF0C\u5E76\u56E0\u6B64\u800C\u88AB\u514D\u5B98\u3002</p><p>\u540E\u6765\uFF0C\u8FD8\u662F\u56E0\u4E3A\u4ED6\u957F\u671F\u4E0E\u7F8C\u4EBA\u4F5C\u6218\uFF0C\u5728\u5F53\u5730\u6709\u5A01\u671B\uFF0C\u624D\u91CD\u65B0\u83B7\u5F97\u8D77\u590D\u2014\u2014\u4ED6\u867D\u7136\u6253\u9EC4\u5DFE\u519B\u6253\u4E0D\u8FC7\uFF0C\u4F46\u5BF9\u4ED8\u7F8C\u65CF\u4EBA\u5374\u662F\u4E00\u6253\u4E00\u4E2A\u51C6\u3002</p><p>\u8463\u5353\u65F6\u4EFB\u7834\u864F\u5C06\u519B\uFF0C\u53D7\u592A\u5C09<strong>\u5F20\u6E29</strong>\u6240\u8F96\u3002\u53EA\u662F\uFF0C\u5F20\u6E29\u4EE5\u4E00\u4ECB\u6587\u4EBA\u5178\u5175\uFF0C\u6839\u672C\u5C31\u53EB\u4E0D\u52A8\u8463\u5C06\u519B\u3002</p><p>\u8463\u5353\u5BF9\u5F85\u4E0A\u5B98\u7684\u6001\u5EA6\uFF0C\u51E0\u4E4E\u53EF\u4EE5\u7528\u76EE\u4E2D\u65E0\u4EBA\u6765\u5F62\u5BB9\u3002\u5F20\u6E29\u7528\u7687\u5E1D\u7684\u8BCF\u4E66\u53EC\u8463\u5353\uFF0C\u7ED3\u679C\u8463\u5353\u8FC7\u4E86\u5F88\u4E45\u624D\u5230\u3002\u800C\u4E14\uFF0C\u5E94\u5BF9\u5931\u793C\uFF0C\u6001\u5EA6\u6781\u4E3A\u4E0D\u7AEF\u6B63\u3002</p><p>\u53C2\u519B<code>\u5B59\u575A</code>\u5BF9\u8463\u5353\u7684\u8FD9\u79CD\u98DE\u626C\u8DCB\u6248\u5F88\u662F\u4E0D\u723D\u3002\u4ED6\u9644\u5728\u5F20\u6E29\u8033\u8FB9\u5EFA\u8BAE\uFF0C<strong>\u5F53\u4EE5\u519B\u6CD5\u65A9\u4E4B</strong>\uFF01\u53EF\u5F20\u6E29\u4E0D\u540C\u610F\uFF0C\u4ED6\u8BA4\u4E3A\u8463\u5353\u5728\u6CB3\u9647\u4E4B\u95F4\u5F88\u6709\u5A01\u671B\uFF0C\u5982\u679C\u6740\u4E86\uFF0C\u897F\u5F81\u4FBF\u65E0\u4EBA\u53EF\u4F9D\u9760\u4E86\u3002</p><p>**\u5B59\u575A\u5E76\u4E0D\u540C\u610F\u5F20\u6E29\u7684\u770B\u6CD5\u3002**\u5B59\u575A\u8BA4\u4E3A\uFF0C\u8463\u5353\u8F7B\u89C6\u957F\u5B98\uFF0C\u6B64\u4E43\u4E00\u7F6A\uFF1B\u8FB9\u7AE0\u548C\u97E9\u9042\u53DB\u4E71\u4E00\u5E74\u591A\u4E86\uFF0C\u4ED6\u90FD\u6309\u5175\u4E0D\u52A8\uFF0C\u6B64\u4E43\u4E8C\u7F6A\uFF1B\u518D\u8005\uFF0C\u4ED6\u51FA\u5F81\u65E0\u529F\u800C\u8FD4\uFF0C\u4E0D\u4EC5\u4E0D\u8C22\u7F6A\u8BA4\u9519\uFF0C\u6001\u5EA6\u8FD8\u975E\u5E38\u5028\u50B2\uFF0C\u6B64\u4E43\u4E09\u7F6A\u3002\u8FD9\u79CD\u4EBA\u8FD8\u7559\u7740\u4E0D\u6740\uFF0C\u60A8\u4EE5\u540E\u5175\u5C31\u4E0D\u597D\u5E26\u4E86\u3002</p><p>\u4F46\u5F20\u6E29\u4F9D\u7136\u4E0D\u5FCD\u5FC3\u3002\u4ED6\u62D2\u7EDD\u4E86\u5B59\u575A\u7684\u5EFA\u8BAE\uFF0C\u8FD8\u548C\u5B59\u575A\u8BF4\uFF0C\u4F60\u5148\u51FA\u53BB\u5427\uFF0C\u4F60\u8FD9\u4E48\u8DDF\u6211\u5600\u5600\u5495\u5495\uFF0C\u8463\u5353\u4F1A\u8D77\u7591\u5FC3\u7684\uFF01\u5B59\u575A\u53EA\u5F97\u544A\u9000\u3002</p><p><strong>\u4EE5\u4E0A\uFF0C\u4FBF\u662F\u8463\u5353\u5728\u300A\u8D44\u6CBB\u901A\u9274\u300B\u91CC\u7684\u9996\u79C0\u3002</strong></p><p>\u8FD9\u6BB5\u8BB0\u5F55\uFF0C\u662F\u53F8\u9A6C\u5149\u6545\u610F\u653E\u5728\u8FD9\u91CC\u7684\uFF0C\u56E0\u4E3A\u4EE5\u4E0A\u7684\u5185\u5BB9\u5E76\u4E0D\u5728\u8463\u5353\u81EA\u5DF1\u7684\u4F20\u8BB0\u91CC\uFF0C\u800C\u662F\u5728\u5B59\u575A\u7684\u4F20\u4E2D\u3002</p><p>\u5386\u53F2\u90FD\u662F\u4E8B\u540E\u7531\u53F2\u5BB6\u6574\u7406\u7684\uFF0C\u800C\u51E0\u4E4E\u6240\u6709\u5199\u53F2\u4E66\u7684\u4EBA\uFF0C\u5BF9\u8463\u5353\u7684\u5370\u8C61\u90FD\u4E0D\u592A\u597D\u3002\u901A\u8FC7\u4EE5\u4E0A\u7684\u7247\u6BB5\uFF0C\u6211\u4EEC\u4E5F\u80FD\u770B\u51FA\uFF0C\u8463\u5353\u7684\u5927\u4F53\u7279\u5F81\u786E\u5B9E\u62DB\u4EBA\u53CD\u611F\uFF1A\u98DE\u626C\u8DCB\u6248\u3001\u76EE\u4E2D\u65E0\u4EBA\uFF0C\u4ED7\u7740\u519B\u529F\u770B\u4E0D\u8D77\u6587\u5B98\u3002</p><p>\u6709\u4E86\u8FD9\u4E2A\u5F00\u573A\u505A\u94FA\u57AB\uFF0C\u8463\u5353\u540E\u9762\u505A\u51FA\u90A3\u4E9B\u4E8B\uFF0C\u4E5F\u5C31\u663E\u5F97\u7406\u6240\u5F53\u7136\u4E86\u3002</p><blockquote><p>\u5E76\u4E0D\u662F\u8BF4\uFF0C\u8463\u5353\u56E0\u4E3A\u8FD9\u79CD\u6840\u9A9C\u4E0D\u9A6F\u7684\u6027\u683C\uFF0C\u5C31\u80AF\u5B9A\u4F1A\u8C0B\u6743\u7BE1\u653F\u3002\u800C\u662F\u5728\u5B59\u575A\u7684\u5EFA\u8BAE\u4E2D\uFF0C\u6211\u4EEC\u5DF2\u7ECF\u80FD\u591F\u5145\u5206\u660E\u767D\u5230\u8463\u5353\u7684\u62E5\u5175\u81EA\u91CD\u3002</p></blockquote><blockquote><p>\u53DB\u519B\u5DF2\u7ECF\u4E71\u4E86\u4E00\u5E74\u591A\u4E86\uFF0C\u8463\u5353\u5374\u6309\u5175\u4E0D\u52A8\uFF0C\u51B5\u4E14\u5BF9\u4E0A\u5B98\u7684\u6001\u5EA6\u6781\u4E3A\u56A3\u5F20\u3002\u7EFC\u5408\u8003\u91CF\u8FD9\u4E24\u70B9\uFF0C\u518D\u8054\u7CFB\u5F53\u65F6\u7684\u4E71\u4E16\u683C\u5C40\uFF0C\u8463\u5353\u7684\u5C0F\u5FC3\u601D\u5230\u5E95\u662F\u4EC0\u4E48\uFF0C\u4E5F\u5C31\u547C\u4E4B\u6B32\u51FA\u4E86\u3002</p></blockquote><p>\u53EF\u5F20\u6E29\u8FD9\u79CD\u4F4E\u59FF\u6001\u771F\u7684\u6709\u7528\u5417\uFF1F</p><p>\u5F88\u660E\u663E\uFF0C\u6CA1\u7528\u3002\u8463\u5353\u5C31\u662F\u6D3B\u8131\u8131\u7684\u4F8B\u5B50\u3002\u9762\u5BF9\u53DB\u519B\uFF0C\u4ED6\u7684\u505A\u6CD5\u662F\u4FDD\u5B58\u5B9E\u529B\u3002\u8FD9\u79CD\u5FC3\u6001\uFF0C\u662F\u4E00\u4E2A\u613F\u610F\u4E3A\u5927\u6C49\u671D\u4ED8\u51FA\u7684\u4EBA\u5417\uFF1F</p><p>\u5F53\u65F6\uFF0C\u6709\u4E00\u6B21\uFF0C\u5929\u8C61\u793A\u8B66\uFF0C\u636E\u8BF4\u5E94\u8BE5\u6709\u5BF9\u5E94\u7684\u5927\u81E3\u88AB\u6740\u624D\u80FD\u6D88\u9664\u707E\u7978\u3002\u8463\u5353\u65E9\u5C31\u770B\u5F20\u6E29\u4E0D\u987A\u773C\u4E86\uFF0C\u4FBF\u4EE5\u5BF9\u5E94\u5929\u8C61\u4E3A\u540D\uFF0C\u628A\u5F20\u6E29\u62C9\u5230\u4E86\u95F9\u5E02\uFF0C\u786C\u751F\u751F\u7528\u6728\u68CD\u6253\u6B7B\u3002\uFF08\u592A\u53F2\u671B\u6C14\uFF0C\u8A00\u5F53\u6709\u5927\u81E3\u622E\u6B7B\u8005\u3002\u6545\u592A\u5C09\u5F20\u6E29\u65F6\u4E3A\u536B\u5C09\uFF0C\u7D20\u4E0D\u5584\u5353\uFF0C\u5353\u5FC3\u6028\u4E4B\uFF0C\u56E0\u5929\u6709\u53D8\uFF0C\u6B32\u4EE5\u585E\u548E\uFF0C\u4F7F\u4EBA\u8A00\u6E29\u4E0E\u8881\u672F\u4EA4\u5173\uFF0C\u9042\u7B1E\u6740\u4E4B\u3002\uFF09</p><p>\u5F20\u6E29\u6B64\u4EBA\uFF0C\u65E9\u5E74\u53D7\u60E0\u4E8E\u66F9\u817E\uFF08\u5C31\u662F\u66F9\u64CD\u7684\u5BA6\u5B98\u7956\u7236\uFF09\u3002\u540E\u6765\u6C49\u7075\u5E1D\u5F00\u59CB\u5356\u5B98\uFF0C\u5F20\u6E29\u5C31\u7ED9\u81EA\u5DF1\u4E5F\u4E70\u4E86\u4E00\u4E2A\u4E09\u516C\u7684\u4F4D\u7F6E\u3002</p><p>\u4F46\u662F\uFF0C\u5728\u5F53\u65F6\u7684\u6E05\u8BAE\u4E2D\uFF0C\u5F20\u6E29\u5E76\u4E0D\u662F\u4EC0\u4E48\u65E0\u803B\u4E4B\u8F88\uFF0C\u53CD\u800C\u4EE5\u6709\u201C\u6770\u624D\u201D\u800C\u8457\u79F0\u3002\u53EA\u662F\uFF0C\u8FD9\u6837\u7684\u5B98\u540F\u60F3\u8981\u5347\u8FC1\uFF0C\u4E5F\u53EA\u80FD\u4ECE\u7687\u5E1D\u90A3\u91CC\u4E70\uFF0C\u60F3\u60F3\u8FD8\u771F\u662F\u8BA9\u4EBA\u5FC3\u9178\u3002</p><p>\u5927\u6C49\u671D\u7684\u6B7B\uFF0C\u771F\u7684\u662F\u81EA\u5DF1\u4F5C\u51FA\u6765\u7684\u3002</p><p>\u4E00\u5B9A\u6709\u4EBA\u4F1A\u95EE\uFF0C\u5982\u679C\u5F53\u5E74\u5F20\u6E29\u771F\u7684\u6740\u4E86\u8463\u5353\uFF0C\u662F\u4E0D\u662F\u5C31\u4E0D\u4F1A\u6709\u540E\u6765\u8FD9\u8FD9\u4E48\u591A\u7684\u4E71\u8C61\u4E86\uFF1F</p><p><strong>\u5F88\u53EF\u80FD\uFF0C\u8FD8\u662F\u4F1A\u6709\u3002</strong></p><p>\u6C49\u671D\u7684\u7EB7\u4E71\u867D\u7136\u7531\u8463\u5353\u8087\u59CB\uFF0C<code>\u4F46\u5173\u952E\u5374\u5728\u4E8E\u4E2D\u67A2\u5DF2\u7ECF\u6DF7\u4E71</code>\u2014\u2014\u5426\u5219\uFF0C\u4E0D\u8BBA\u8463\u5353\u80FD\u529B\u518D\u5F3A\uFF0C\u91CE\u5FC3\u518D\u5927\uFF0C\u4E5F\u662F\u65E0\u80FD\u4E3A\u529B\u7684\u3002\u56E0\u6B64\uFF0C\u5373\u4F7F\u6CA1\u6709\u8463\u5353\uFF0C\u63A5\u4E0B\u6765\u4E8B\u60C5\u4E5F\u53EF\u80FD\u4F1A\u5982\u6B64\u53D1\u5C55\u3002</p><p>\u671D\u653F\u5DF2\u7ECF\u4E71\u4E86\uFF0C\u8463\u5353\u53EA\u4E0D\u8FC7\u6070\u9022\u5176\u4F1A\u800C\u5DF2\u3002\u6CA1\u6709\u8463\u5353\uFF0C\u4E5F\u4F1A\u6709\u4E0B\u4E00\u4E2A\u519B\u9600\u6765\u505A\u8FD9\u4E9B\u4E8B\u7684\u3002\u4E8B\u5B9E\u4E0A\uFF0C\u5F53\u65F6\u5DF2\u7ECF\u6709\u4E0D\u5C11\u4EBA\u8822\u8822\u6B32\u52A8\u4E86\u3002</p>',28),n=[c];function s(a,_){return o(),t("div",null,n)}const i=p(r,[["render",s],["__file","2.html.vue"]]);export{i as default};
