import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as t,f as r}from"./app.1218f0f6.js";const n={},s=r('<h1 id="\u738B\u5BFC" tabindex="-1"><a class="header-anchor" href="#\u738B\u5BFC" aria-hidden="true">#</a> \u738B\u5BFC</h1><blockquote><p>\u5E9A\u7533\uFF0C\u59CB\u5174\u6587\u732E\u516C\u738B\u5BFC\u85A8\uFF0C\u4E27\u846C\u4E4B\u793C\u89C6\u6C49\u535A\u9646\u5019\u53CA\u5B89\u5E73\u732E\u738B\u6545\u4E8B\uFF0C\u53C2\u7528\u5929\u5B50\u4E4B\u793C\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274 \u2022 \u5377\u4E5D\u5341\u516D \u2022 \u664B\u7EAA\u5341\u516B\u300B</strong></p><p><strong><code>\u738B\u5BFC</code>\u5728\u5386\u53F2\u4E0A\u7684\u540D\u58F0\uFF0C\u5E76\u4E0D\u662F\u7279\u522B\u597D\u3002</strong></p><p>\u5373\u4FBF\u4E0E\u8C22\u5B89\u9F50\u540D\uFF0C\u540E\u4EBA\u5728\u63D0\u53CA\u65F6\u4E5F\u603B\u662F\u4F69\u670D\u8C22\u5B89\u66F4\u591A\u4E00\u4E9B\u3002\u8FD9\u4E00\u65B9\u9762\u4E0E\u8C22\u5B89\u8D85\u9038\u8131\u4FD7\u7684\u6C14\u8D28\u6709\u5173\uFF0C\u4E00\u65B9\u9762\u4E5F\u56E0\u4E3A\u300A\u4E16\u8BF4\u65B0\u8BED\u300B\u5BF9\u4E24\u4EBA\u7684\u8BB0\u8F7D\u5F88\u4E0D\u76F8\u540C\uFF1A\u8C22\u5B89\u51E0\u4E4E\u6CA1\u7559\u4E0B\u4EC0\u4E48\u8D1F\u9762\u8BB0\u5F55\uFF0C\u738B\u5BFC\u5374\u9ED1\u6599\u4E0D\u65AD\u3002</p><p>\u800C\u4E14\uFF0C<strong>\u8FD9\u4E2A\u4E1C\u664B\u5F00\u56FD\u7684\u6700\u5927\u529F\u81E3\uFF0C\u7ADF\u7136\u5E76\u6CA1\u6709\u5728\u53F2\u6599\u4E2D\u7559\u4E0B\u591A\u5C11\u5177\u4F53\u4E8B\u8FF9\u3002</strong></p><p>\u5386\u671D\u7684\u5F00\u56FD\u91CD\u81E3\uFF0C\u6BD4\u5982\u201C\u6C49\u521D\u4E09\u6770\u201D\u2014\u2014\u8427\u4F55\u3001\u5F20\u826F\u3001\u97E9\u4FE1\uFF0C\u6BD4\u5982\u5510\u671D\u7684\u201C\u51CC\u70DF\u9601\u4E8C\u5341\u56DB\u529F\u81E3\u201D\uFF0C\u90FD\u5404\u6709\u529F\u7EE9\u4F20\u4E16\u3002\u504F\u504F\u53EA\u6709\u738B\u5BFC\uFF0C\u51E0\u4E4E\u6CA1\u6709\u4EC0\u4E48\u503C\u5F97\u4E66\u5199\u7684\u529F\u4E1A\u7559\u4E0B\u3002</p><p>\u8FD9\u4E2A\u95EE\u9898\uFF0C\u65E9\u5728\u6E05\u671D\u5C31\u88AB\u4EBA\u63D0\u51FA\u8FC7\u3002\u6E05\u4EBA\u738B\u9E23\u76DB\u5728\u300A\u5341\u4E03\u53F2\u5546\u69B7\u300B\u4E2D\u8BA4\u4E3A\uFF1A\u300A\u738B\u5BFC\u4F20\u300B\u5168\u7BC7\u516D\u5343\u591A\u5B57\uFF0C\u5927\u90E8\u5206\u5185\u5BB9\u90FD\u662F\u6EA2\u7F8E\u4E4B\u8BCD\u3002\u738B\u5BFC\u53F7\u79F0\u4E00\u4EE3\u540D\u81E3\uFF0C\u5374\u6CA1\u6709\u4EC0\u4E48\u5177\u4F53\u7684\u529F\u7EE9\u53EF\u8A00\u3002\u800C\u4E14\u6587\u4E2D\u53CD\u5012\u5BF9\u95E8\u7B2C\u9AD8\u5927\u3001\u5B50\u5B59\u5BA6\u9014\u901A\u8FBE\u4E0D\u541D\u7B14\u58A8\u3002\u56E0\u6B64\uFF0C\u4E0E\u5176\u8BF4\u662F\u738B\u5BFC\u529F\u52B3\u5927\uFF0C\u8FD8\u4E0D\u5982\u8BF4\u662F\u7405\u740A\u738B\u6C0F\u95E8\u7B2C\u9AD8\uFF0C\u624D\u663E\u5F97\u738B\u5BFC\u5982\u6B64\u51FA\u4F17\u3002</p><p>\u738B\u9E23\u76DB\u7684\u8FD9\u756A\u8D28\u7591\uFF0C\u4E4D\u770B\u4F3C\u4E4E\u5408\u7406\uFF0C\u4F46\u5374\u89E3\u91CA\u4E0D\u4E86\u53E6\u5916\u4E00\u4E2A\u95EE\u9898\uFF1A<strong>\u65E2\u7136\u738B\u5BFC\u5982\u6B64\u201C\u65E0\u80FD\u201D\uFF0C\u90A3\u4ED6\u4E3A\u4F55\u5728\u4E1C\u664B\u80FD\u5907\u53D7\u5C0A\u5D07\uFF1F</strong></p><p>\u664B\u5143\u5E1D\u53F8\u9A6C\u777F\u79F0\u5E1D\u65F6\uFF0C\u7279\u522B\u9080\u8BF7\u738B\u5BFC\u540C\u5347\u5FA1\u5EA7\uFF0C\u4E00\u8D77\u63A5\u53D7\u4F17\u81E3\u7684\u671D\u62DC\u3002\u5230\u664B\u6210\u5E1D\u65F6\uFF0C\u671D\u5EF7\u53C8\u4E13\u95E8\u8BA8\u8BBA\u7687\u5E1D\u8DDF\u738B\u5BFC\u4E4B\u95F4\u7684\u793C\u4EEA\u3002\u8FD9\u4E9B\u8FF9\u8C61\uFF0C\u90FD\u8BF4\u660E\u5728\u4E1C\u664B\u65F6\uFF0C\u738B\u5BFC\u7684\u5730\u4F4D\u548C\u529F\u4E1A\u662F\u88AB\u8BA4\u53EF\u7684\u3002</p><p>\u5373\u4FBF\u7ECF\u8FC7\u738B\u6566\u4E4B\u4E71\u4EE5\u540E\uFF0C\u7405\u740A\u738B\u6C0F\u518D\u6CA1\u6709\u8FDC\u8FC8\u540C\u4FAA\u7684\u5B9E\u529B\uFF0C\u738B\u5BFC\u4E5F\u4F9D\u7136\u662F\u81E3\u5B50\u4E2D\u4E00\u4E2A\u7279\u6B8A\u7684\u5B58\u5728\u2014\u2014\u7687\u5E1D\u7ED9\u738B\u5BFC\u4E0B\u8BCF\uFF0C\u8981\u7528\u201C\u656C\u95EE\u201D\u8FD9\u6837\u7684\u8BF4\u6CD5\uFF0C\u8FD9\u53EF\u662F\u8FDE\u8463\u5353\u3001\u66F9\u64CD\u3001\u53F8\u9A6C\u662D\u7B49\u6743\u81E3\u90FD\u6CA1\u6709\u8FC7\u7684\u5F85\u9047\u3002</p><p>\u8FD9\u6837\u4E00\u4E2A\u738B\u5BFC\uFF0C\u4E0D\u53EF\u80FD\u5982\u738B\u9E23\u76DB\u6240\u8BF4\u4E00\u822C\uFF0C\u662F\u65E0\u6240\u8D21\u732E\u4E4B\u4EBA\u3002</p><p>\u56E0\u6B64\uFF0C\u4ECA\u5929\u6211\u4EEC\u4E0D\u59A8\u518D\u505A\u4E00\u70B9\u641C\u5BFB\uFF0C\u4ECE\u4E1C\u664B\u5F00\u56FD\u65F6\u671F\u7684\u60C5\u5F62\uFF0C\u6765\u7AA5\u89C6\u738B\u5BFC\u7684\u751F\u5E73\u3002</p><p>\u4ECE<code>\u53F8\u9A6C\u777F</code>\u548C<code>\u738B\u5BFC</code>\u8FDB\u9A7B\u5EFA\u90BA\u4EE5\u524D\u8BF4\u8D77\u3002</p><p>\u5728\u664B\u5143\u5E1D\u53F8\u9A6C\u777F\u4EE5\u9547\u4E1C\u5C06\u519B\u7684\u540D\u4E49\u9547\u5B88\u6C5F\u4E1C\u4E4B\u524D\uFF0C\u6C5F\u4E1C\u5176\u5B9E\u7ECF\u5386\u8FC7\u4E00\u573A\u6DF7\u4E71\u3002</p><p>\u5F53\u65F6\u6B63\u503C\u516B\u738B\u4E4B\u4E71\u7684\u540E\u671F\uFF0C\u5217\u738B\u7EB7\u4E89\uFF0C\u6D1B\u9633\u671D\u5EF7\u5DF2\u7ECF\u5B8C\u5168\u4E27\u5931\u5BF9\u5730\u65B9\u7684\u63A7\u5236\u3002\u8FD9\u4E2A\u5C40\u9762\uFF0C\u5BF9\u4E8E\u5F53\u65F6\u7684\u4EBA\u4EEC\u6765\u8BF4\u5E76\u4E0D\u964C\u751F\uFF0C\u4E00\u767E\u4E94\u5341\u5E74\u524D\uFF0C\u5929\u4E0B\u4E5F\u662F\u7C7B\u4F3C\u60C5\u5F62\uFF1A\u4E1C\u6C49\u672B\u5E74\uFF0C\u9EC4\u5DFE\u4E4B\u4E71\uFF0C\u6B64\u540E\u7684\u5929\u4E0B\u5C40\u52BF\u4E0E\u6B64\u65F6\u5982\u51FA\u4E00\u8F99\u3002</p><p>\u65E2\u7136\u5386\u53F2\u5728\u91CD\u6F14\uFF0C\u90A3\u5C31\u514D\u4E0D\u4E86\u6709\u4EBA\u52A8\u8D77\u5FC3\u601D\u3002\u6709\u4EBA\u5C31\u770B\u51C6\u4E86\u5929\u4E0B\u5927\u4E71\u7684\u597D\u65F6\u673A\uFF0C\u60F3\u5B66\u5B59\u6C0F\u7236\u5B50\uFF0C\u5272\u636E\u6C5F\u4E1C\u81EA\u7ACB\u2014\u2014\u8FD9\u4E2A\u4EBA\u53EB<code>\u9648\u654F</code>\u3002</p><p>\u4E00\u5F00\u59CB\uFF0C\u4ED6\u7684\u8FD9\u6761\u8DEF\u7ADF\u7136\u8FD8\u8D70\u5F97\u9887\u4E3A\u987A\u7545\u3002</p><p>\u9648\u654F\u539F\u672C\u662F\u5E90\u6C5F\u4EBA\uFF0C\u5728\u90E1\u91CC\u505A\u4E2A\u5C0F\u540F\u3002\u56E0\u4E3A\u5EC9\u6D01\uFF0C\u88AB\u8C03\u5230\u4E86\u6D1B\u9633\uFF0C\u4EFB\u201C\u4ED3\u90E8\u4EE4\u53F2\u201D\uFF0C\u4E5F\u5C31\u662F\u4E3B\u7BA1\u56FD\u5E93\u5B58\u7CAE\u7684\u4E66\u8BB0\u5458\u3002</p><p>\u5F53\u65F6\u6D1B\u9633\u4E00\u5E26\u6218\u706B\u7EB7\u98DE\uFF0C\u7CAE\u98DF\u5947\u7F3A\uFF0C\u9648\u654F\u56E0\u6B64\u5EFA\u8BAE\uFF1A\u6C5F\u5357\u4E00\u5E26\u51E0\u5341\u5E74\u6765\u4E00\u76F4\u6709\u5B58\u7CAE\uFF0C\u8FD9\u4E9B\u5B58\u7CAE\u82E5\u80FD\u8D70\u6C34\u8DEF\u8FD0\u5165\u6D1B\u9633\uFF0C\u53EF\u89E3\u671D\u5EF7\u71C3\u7709\u4E4B\u6025\u3002</p><p>\u671D\u5EF7\u542C\u53D6\u4E86\u9648\u654F\u7684\u5EFA\u8BAE\uFF0C\u5E76\u4EFB\u547D\u9648\u654F\u4E3B\u7BA1\u6B64\u4E8B\u3002\u501F\u6B64\u673A\u4F1A\uFF0C\u9648\u654F\u56DE\u5230\u4E86\u626C\u5DDE\uFF0C\u56E0\u4E3A\u653F\u7EE9\u5353\u8457\uFF0C\u4E0D\u4E45\u4FBF\u5347\u81F3\u5E7F\u9675\u76F8\u2014\u2014\u8FD9\u5DF2\u7ECF\u662F\u90E1\u5B88\u4E00\u7EA7\u7684\u9AD8\u5B98\u3002</p><p>\u4F46\u9648\u654F\u5E76\u4E0D\u6EE1\u8DB3\u3002\u773C\u89C1\u4E2D\u539F\u8D8A\u6765\u8D8A\u4E71\uFF0C\u9648\u654F\u7684\u91CE\u5FC3\u4E5F\u8D8A\u6765\u8D8A\u5927\u3002\u5F53\u65F6\u5929\u4E0B\u5927\u4E71\uFF0C\u6C5F\u4E1C\u4E0E\u4E2D\u539F\u4E4B\u95F4\u901A\u4FE1\u963B\u9694\uFF0C\u9648\u654F\u4E8E\u662F\u5047\u4F20\u671D\u5EF7\u65E8\u610F\uFF0C\u4EFB\u547D\u81EA\u5DF1\u4E3A\u626C\u5DDE\u523A\u53F2\uFF0C\u5E76\u8D81\u673A\u5360\u636E\u4E86\u6C5F\u5DDE\u2014\u2014\u5176\u7EDF\u6CBB\u6B64\u65F6\u57FA\u672C\u4E0A\u5DF2\u7ECF\u56CA\u62EC\u4E1C\u5434\u65E9\u671F\u7684\u5730\u57DF\u3002</p><p>\u53EA\u662F\uFF0C\u4ED6\u7684\u8FD9\u79CD\u7EDF\u6CBB\u5E76\u4E0D\u7262\u56FA\u3002<strong>\u6700\u5927\u7684\u95EE\u9898\uFF0C\u5728\u4E8E\u65E0\u6CD5\u53D6\u5F97<code>\u5434\u5730\u5730\u65B9\u8C6A\u65CF</code>\u7684\u652F\u6301\u3002</strong></p><p>\u8FD9\u4E9B\u8C6A\u65CF\u7684\u4EE3\u8868\u4EBA\u7269\uFF0C\u5C31\u662F<code>\u987E\u8363</code>\u3002</p><p>\u4ED6\u662F\u4E1C\u5434\u7B2C\u4E8C\u4EFB\u5BB0\u76F8\u987E\u96CD\u7684\u5B59\u5B50\uFF0C\u4E1C\u5434\u706D\u4EA1\u540E\uFF0C\u66FE\u5165\u6D1B\u9633\u5728\u671D\u4E2D\u4EFB\u804C\uFF0C\u540E\u6765\u56E0\u4E3A\u4E2D\u539F\u5927\u4E71\u800C\u8F9E\u5B98\u56DE\u4E61\u3002</p><p>\u663E\u8D6B\u7684\u8EAB\u4E16\uFF0C\u4EE5\u53CA\u5728\u6D1B\u9633\u4EFB\u804C\u7684\u7ECF\u5386\uFF0C\u8BA9\u4ED6\u6210\u4E3A\u4E86\u5434\u4E2D\u8C6A\u95E8\u7684\u4EE3\u8868\u3002</p><p>\u4F46\u9648\u654F\u5374\u8DDF\u8FD9\u4E9B\u8C6A\u95E8\u5904\u7406\u4E0D\u597D\u5173\u7CFB\u3002\u53F2\u8F7D\u201C\uFF08\u9648\u654F\uFF09<strong>\u5211\u653F\u65E0\u7AE0\uFF0C\u4E0D\u4E3A\u82F1\u4FCA\u6240\u670D\uFF0C\u5B50\u5F1F\u51F6\u66B4\uFF0C\u6240\u5728\u4E3A\u60A3\u201D\u3002</strong></p><p>\u4E00\u65B9\u9762\uFF0C\u9762\u5BF9\u6240\u8C13\u7684\u201C\u82F1\u4FCA\u201D\uFF0C\u4E5F\u5C31\u662F\u5730\u65B9\u4E0A\u7684\u5B9E\u529B\u6D3E\uFF0C\u9648\u654F\u5F97\u4E0D\u5230\u8FD9\u4E9B\u4EBA\u7684\u652F\u6301\u3002\u53E6\u4E00\u65B9\u9762\uFF0C\u9648\u654F\u53C8\u7EA6\u675F\u4E0D\u4E86\u81EA\u5DF1\u7684\u4EB2\u621A\u3002\u4E24\u76F8\u5408\u529B\u4E4B\u4E0B\uFF0C\u9648\u654F\u4E0E\u8C6A\u95E8\u4E4B\u95F4\u7684\u77DB\u76FE\u8D8A\u6765\u8D8A\u5927\u3002</p><p>\u9648\u654F\u7684\u5F1F\u5F1F\u9648\u6636\u66FE\u529D\u9648\u654F\u6740\u6389\u987E\u8363\u7ACB\u5A01\u3002\u7136\u800C\u50CF\u987E\u8363\u8FD9\u79CD\u540D\u671B\u7684\u5730\u65B9\u540D\u58EB\uFF0C\u5DF2\u7ECF\u4E0D\u662F\u9648\u654F\u60F3\u6740\u5C31\u80FD\u6740\u5F97\u4E86\u7684\u3002\u6700\u7EC8\uFF0C\u9648\u654F\u8FD8\u662F\u51B3\u5B9A\u5B89\u629A\uFF0C\u4ED6\u4EFB\u547D\u4E86\u987E\u8363\u5B98\u804C\uFF0C\u60F3\u501F\u673A\u7B3C\u7EDC\u3002</p><p>\u4F46\u987E\u8363\u6839\u672C\u5C31\u770B\u4E0D\u4E0A\u9648\u654F\u8FD9\u79CD\u51FA\u8EAB\u548C\u505A\u6D3E\u7684\u4EBA\uFF0C**\u6700\u7EC8\uFF0C\u987E\u8363\u4E0E\u4EBA\u79C1\u4E0B\u4E32\u8054\uFF0C\u8D77\u5175\u51FB\u8D25\u4E86\u9648\u654F\u3002**\u9648\u654F\u7684\u5BB6\u65CF\u5168\u90E8\u88AB\u6740\uFF0C\u4E5F\u7B97\u662F\u4E3A\u9648\u6C0F\u4E4B\u524D\u7684\u201C\u5B50\u5F1F\u51F6\u66B4\uFF0C\u6240\u5728\u4E3A\u60A3\u201D\u4ED8\u51FA\u4E86\u4EE3\u4EF7\u3002</p><p>\u800C\u5C31\u5728\u9648\u654F\u6B7B\u540E\u4E0D\u4E45\uFF0C\u5728\u671D\u4E2D\u6267\u653F\u7684\u4E1C\u6D77\u738B\u53F8\u9A6C\u8D8A\u4EFB\u547D\u5B89\u4E1C\u5C06\u519B\u3001<strong>\u7405\u740A\u738B\u53F8\u9A6C\u777F\u2014\u2014\u4E5F\u5C31\u662F\u540E\u6765\u7684\u664B\u5143\u5E1D</strong>\uFF0C\u6E21\u6C5F\u8FDB\u5165\u5EFA\u90BA\u3002</p><p>\u968F\u53F8\u9A6C\u777F\u4E00\u540C\u6E21\u6C5F\u7684\uFF0C\u5C31\u6709\u5B89\u4E1C\u53F8\u9A6C\u738B\u5BFC\u3002</p><p>\u968F\u7740\u9648\u654F\u88AB\u6D88\u706D\uFF0C\u4E0D\u8BBA\u662F\u6E21\u6C5F\u5357\u6765\u7684\u53F8\u9A6C\u777F\uFF0C\u8FD8\u662F\u4EE5\u987E\u8363\u4E3A\u4EE3\u8868\u7684\u4E1C\u5357\u540D\u58EB\uFF0C\u90FD\u610F\u8BC6\u5230\u4E86\u4E00\u4E2A\u95EE\u9898\uFF1A</p><blockquote><p><strong>\u5C06\u6765\u8BE5\u600E\u4E48\u529E\uFF1F</strong></p></blockquote><p>\u4E1C\u5357\u4E00\u5E26\u8C6A\u95E8\u7684\u60F3\u6CD5\u662F\uFF0C\u5929\u4E0B\u5927\u4E71\u81F3\u6B64\uFF0C\u518D\u56DE\u5230\u4EE5\u524D\u7684\u592A\u5E73\u76DB\u4E16\u5DF2\u4E0D\u53EF\u80FD\u3002\u65E2\u5982\u6B64\uFF0C\u90A3\u4E48\u6C5F\u4E1C\u4E00\u5E26\u6700\u597D\u7684\u7ED3\u679C\uFF0C\u5C31\u662F\u5F62\u6210\u7C7B\u4F3C\u4E8E\u5F53\u5E74\u7684\u5C40\u9762\uFF1A</p><blockquote><p><strong>\u51FA\u73B0\u4E00\u4E2A\u672C\u571F\u5316\u7684\u653F\u6743\uFF0C\u65E2\u80FD\u591F\u7A33\u5B9A\u5C40\u52BF\u907F\u514D\u6218\u4E71\uFF0C\u540C\u65F6\u53C8\u627F\u8BA4\u4ED6\u4EEC\u8FD9\u4E9B\u5730\u65B9\u8C6A\u65CF\u7684\u65E2\u6709\u6743\u529B\u3002</strong></p></blockquote><p>\u6B64\u65F6\u5728\u6C5F\u5357\u5982\u679C\u51FA\u73B0\u4E00\u4E2A\u5982\u5B59\u6743\u4E00\u822C\u7684\u4EBA\u7269\uFF0C\u53EA\u8981\u4ED6\u80FD\u591F\u5C0A\u91CD\u6C5F\u4E1C\u540D\u58EB\u7684\u65E2\u6709\u5229\u76CA\uFF0C\u90A3\u4E48\u6C5F\u4E1C\u8C6A\u65CF\u5C31\u4F1A\u5F88\u4E50\u4E8E\u652F\u6301\u4ED6\u7684\u7EDF\u6CBB\u3002\u5C4A\u65F6\u53CC\u65B9\u5404\u53D6\u6240\u9700\uFF0C\u672C\u5730\u52BF\u529B\u8FFD\u6C42\u5B89\u5B9A\u7684\u73AF\u5883\uFF0C\u5916\u6765\u52BF\u529B\u8FFD\u6C42\u5EFA\u7ACB\u8D77\u653F\u6743\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u9648\u654F\u4E00\u5F00\u59CB\u201C\u5272\u636E\u81EA\u7ACB\u201D\u7684\u5C0F\u7B97\u76D8\uFF0C\u5E76\u6CA1\u6709\u6253\u9519\u3002\u53EA\u4E0D\u8FC7\u4ED6\u5728\u8FFD\u6C42\u81EA\u8EAB\u5229\u76CA\u7684\u540C\u65F6\uFF0C\u6CA1\u6709\u517C\u987E\u5230\u5730\u65B9\u5927\u65CF\u7684\u5229\u76CA\uFF0C\u56E0\u6B64\u624D\u60E8\u906D\u5931\u8D25\u3002</p><p>\u800C\u5C31\u5728\u9648\u654F\u88AB\u6D88\u706D\u540E\uFF0C\u53F8\u9A6C\u777F\u548C\u738B\u5BFC\u5230\u4E86\u6C5F\u4E1C\u3002</p><p><strong>\u90A3\u53F8\u9A6C\u777F\u80FD\u4E0D\u80FD\u505A\u5B59\u6743\u5462\uFF1F</strong></p><p><strong>\u7B54\u6848\u662F\u5F88\u53EF\u60DC\uFF0C<code>\u4E0D\u80FD</code>\u3002</strong></p><p>\u4E1C\u5434\u7ECF\u5386\u4E09\u4E16\uFF0C\u4ECE\u5B59\u7B56\u8FC7\u6C5F\u5230\u5B59\u6743\u79F0\u5E1D\uFF0C\u82B1\u4E86\u4E09\u5341\u591A\u5E74\u7684\u65F6\u95F4\u3002\u800C\u4E14\u4E0D\u7BA1\u662F\u626C\u5DDE\u8FD8\u662F\u8346\u5DDE\uFF0C\u5B59\u6C0F\u5144\u5F1F\u90FD\u662F\u771F\u5200\u771F\u67AA\u6253\u4E0B\u6765\u7684\u3002</p><p>\u5728\u8FD9\u4E00\u70B9\u4E0A\uFF0C\u53F8\u9A6C\u777F\u5B8C\u5168\u6CA1\u6709\u53EF\u6BD4\u6027\u3002\u4ED6\u4ECE\u6E21\u6C5F\u5230\u79F0\u5E1D\uFF0C\u53EA\u7528\u4E86\u5341\u5E74\uFF0C\u800C\u4E14\u671F\u95F4\u4E1C\u664B\u5C31\u6CA1\u6709\u7ECF\u5386\u8FC7\u4EC0\u4E48\u50CF\u6837\u7684\u5927\u6218\u3002\u4ED6\u5012\u662F\u4E5F\u60F3\u5F81\u670D\u8DBE\u9AD8\u6C14\u626C\u7684\u5730\u65B9\u52BF\u529B\uFF0C\u4F46\u5C34\u5C2C\u7684\u662F\u81EA\u5DF1\u5E76\u6CA1\u6709\u8FD9\u4E2A\u5B9E\u529B\u3002</p><p><strong>\u800C\u4E14\uFF0C\u53F8\u9A6C\u777F\u672C\u8EAB\u4E5F<code>\u4E0D\u582A\u5927\u7528</code>\u3002</strong></p><p>\u300A\u4E16\u8BF4\u65B0\u8BED\u300B\u91CC\uFF0C\u6709\u8FD9\u6837\u4E00\u6BB5\u8BB0\u5F55\uFF1A\u7531\u4E8E\u4E2D\u539F\u5927\u4E71\uFF0C\u8863\u51A0\u58EB\u65CF\u7EB7\u7EB7\u5357\u6E21\u3002\u6853\u5F5D\u6E21\u6C5F\u540E\uFF0C\u4E0E\u516C\u537F\u4EA4\u8C08\uFF0C\u53D1\u73B0\u8BF8\u4EBA\u6C89\u6E4E\u4E8E\u9152\u4E50\uFF0C\u9887\u89C9\u5931\u671B\u3002\u76F4\u5230\u540E\u6765\u89C1\u5230\u738B\u5BFC\uFF0C\u6853\u5F5D\u624D\u4E00\u626B\u6CAE\u4E27\u4E4B\u60C5\uFF0C\u5927\u8D5E\u738B\u5BFC\u662F\u6C5F\u5DE6\u7684\u201C\u7BA1\u5937\u543E\u201D\u3002</p><p>\u7BA1\u5937\u543E\u5C31\u662F\u7BA1\u4EF2\uFF0C\u8FD9\u4E2A\u6BD4\u55BB\uFF0C\u5F53\u7136\u662F\u8BF4\u738B\u5BFC\u624D\u80FD\u51FA\u4F17\uFF0C\u80FD\u5321\u6D4E\u65F6\u4E8B\u3002\u4F46\u8D5E\u8A89\u4E4B\u4E0B\uFF0C\u53C8\u53E6\u6709\u53F0\u8BCD\uFF1A\u7BA1\u4EF2\u8F85\u4F50\u7684\u5BF9\u8C61\u662F\u9F50\u6853\u516C\u3002\u4ED6\u867D\u7136\u662F\u6625\u79CB\u4E94\u9738\u4E4B\u9996\uFF0C\u4F46\u6BD5\u751F\u7684\u529F\u4E1A\u5374\u5B8C\u5168\u9760\u7BA1\u4EF2\u4E00\u4EBA\u3002\u4EE5\u81F3\u4E8E\u7BA1\u4EF2\u6B7B\u540E\uFF0C\u9F50\u56FD\u7684\u9738\u4E1A\u5F88\u5FEB\u4FBF\u6D88\u6563\uFF0C\u9F50\u6853\u516C\u672C\u4EBA\u4E5F\u51C4\u51C9\u800C\u6B7B\uFF0C\u96BE\u4EE5\u5584\u7EC8\u3002</p><p>\u56E0\u6B64\uFF0C\u5728\u6853\u5F5D\u7684\u8FD9\u4E2A\u6BD4\u55BB\u91CC\uFF0C\u5BF9\u664B\u5143\u5E1D\u53F8\u9A6C\u777F\u7684\u8BC4\u4EF7\uFF0C\u7740\u5B9E\u4E0D\u9AD8\u3002</p><p>\u81F3\u4E8E\u300A\u664B\u4E66\u300B\u5BF9\u53F8\u9A6C\u777F\u76D6\u68FA\u5B9A\u8BBA\u7684\u8BC4\u4EF7\u5219\u662F\u8FD9\u6837\u7684\uFF1A\u201C<strong>\u606D\u4FED\u4E4B\u5FB7\u867D\u5145\uFF0C\u96C4\u6B66\u4E4B\u91CF\u4E0D\u8DB3\u3002</strong>\u201D\u4E2A\u4EBA\u64CD\u5B88\u5FB7\u884C\u56FA\u7136\u503C\u5F97\u79F0\u9882\uFF0C\u4F46\u662F\u7687\u5E1D\u7684\u96C4\u6B66\u5374\u8FDC\u8FDC\u4E0D\u591F\u3002</p><p><strong>\u8FD9\u79CD\u65F6\u5019\uFF0C\u738B\u5BFC\u7684\u4F5C\u7528\u5C31\u663E\u73B0\u51FA\u6765\u4E86\u3002</strong></p><p>\u4ED6\u773C\u524D\u9762\u5BF9\u7740\u7684\uFF0C\u662F\u8FD9\u6837\u4E00\u4E2A\u5C40\u9762\uFF1A\u4E00\u65B9\u9762\uFF0C\u81EA\u5DF2\u4E00\u65B9\u4EE5\u53F8\u9A6C\u777F\u4E3A\u4EE3\u8868\uFF0C\u6709\u79F0\u5E1D\u7684\u91CE\u5FC3\uFF0C\u4F46\u6CA1\u6709\u54CD\u5E94\u7684\u6B66\u529B\u652F\u6301\u3002\u53E6\u4E00\u65B9\u9762\uFF0C\u6C5F\u4E1C\u8C6A\u65CF\u5E0C\u671B\u6709\u7A33\u5B9A\u7684\u5C40\u52BF\uFF0C\u4F46\u53C8\u4E0D\u5E0C\u671B\u8FC7\u5EA6\u4FB5\u8680\u81EA\u8EAB\u7684\u65E2\u6709\u5229\u76CA\u3002</p><p>\u56E0\u6B64\uFF0C\u738B\u5BFC\u7684\u4EFB\u52A1\u5C31\u662F\uFF1A</p><blockquote><p><strong>\u628A\u4E24\u8005\u7684\u9700\u6C42\u7ED3\u5408\u8D77\u6765\uFF0C\u8BA9\u53CC\u65B9\u5404\u53D6\u6240\u9700\uFF0C\u76F8\u4E92\u652F\u6301\u3002</strong></p></blockquote><p>\u57FA\u4E8E\u8FD9\u4E00\u8BFE\u9898\uFF0C\u738B\u5BFC\u505A\u4E86\u4E0D\u5C11\u5DE5\u4F5C\u3002\u521D\u5230\u5EFA\u90BA\u65F6\uFF0C\u7405\u740A\u738B\u5E9C\u95E8\u5EAD\u51B7\u843D\uFF0C\u5C11\u6709\u540D\u58EB\u4E0A\u95E8\u3002\u53F8\u9A6C\u777F\u5185\u5FC3\u7126\u6025\uFF0C\u4F46\u82E6\u65E0\u826F\u65B9\u3002\u6700\u540E\uFF0C\u8FD8\u662F\u738B\u5BFC\u5144\u5F1F\u51FA\u6765\u89E3\u56F4\uFF1A</p><p>\u67D0\u6B21\u53F8\u9A6C\u777F\u51FA\u6E38\uFF0C\u738B\u6566\u3001\u738B\u5BFC\u4E24\u5144\u5F1F\u4EB2\u81EA\u966A\u540C\u3002\u8DEF\u65C1\u7684\u6C5F\u5357\u540D\u58EB\u987E\u8363\u548C\u7EAA\u77BB\u7B49\u89C1\u72B6\uFF0C\u5927\u60CA\u5931\u8272\uFF0C\u4E3A\u53F8\u9A6C\u777F\u80FD\u7EDF\u5FA1\u738B\u6C0F\u5144\u5F1F\u7684\u5A01\u4E25\u6240\u6298\u670D\uFF0C\u62DC\u5012\u5728\u5730\uFF0C\u8868\u793A\u5F52\u987A\u3002</p><p>\u8FD9\u4E2A\u4F8B\u5B50\u88AB\u7530\u4F59\u5E86\u5148\u751F\u8003\u8BC1\uFF0C\u8BA4\u4E3A\u662F\u4F2A\u9020\u3002\u4F46\u5176\u4E2D\u6C5F\u4E1C\u58EB\u65CF\u5BF9\u53F8\u9A6C\u777F\u7684\u6001\u5EA6\uFF0C\u4EE5\u53CA\u738B\u5BFC\u7684\u5904\u7406\u5E94\u5BF9\u65B9\u5F0F\uFF0C\u5374\u9887\u4E3A\u771F\u5B9E\u3002\u6700\u7EC8\uFF0C\u4E5F\u662F\u5728\u738B\u5BFC\u7684\u5EFA\u8BAE\u3001\u5904\u7406\u4E4B\u4E0B\uFF0C\u987E\u8363\u3001\u7EAA\u77BB\u7B49\u6C5F\u4E1C\u540D\u58EB\u5148\u540E\u88AB\u53EC\u5165\u53F8\u9A6C\u777F\u7684\u9547\u4E1C\u5C06\u519B\u5E9C\uFF0C\u53CC\u65B9\u5F00\u59CB\u5408\u4F5C\u3002</p><p>\u8FD8\u6709\u53E6\u5916\u4E00\u4E9B\u4E8B\u8FF9\uFF0C\u4E5F\u53EF\u4EE5\u4F53\u73B0\u51FA\u738B\u5BFC\u8FD9\u79CD\u7B3C\u7EDC\u6C5F\u4E1C\u58EB\u65CF\u7684\u6267\u653F\u601D\u8DEF\u3002\u8FD9\u7C7B\u4E8B\u8FF9\u5728\u300A\u664B\u4E66\u300B\u4E2D\u867D\u7136\u4E0D\u591A\u89C1\uFF0C\u4F46\u5728\u300A\u4E16\u8BF4\u65B0\u8BED\u300B\u91CC\u5374\u7ECF\u5E38\u51FA\u73B0\u3002\u6BD4\u5982\uFF0C<strong>\u738B\u5BFC\u8EAB\u4E3A\u4E2D\u539F\u9876\u7EA7\u58EB\u65CF\uFF0C\u7ADF\u7136\u5728\u805A\u4F1A\u4E2D\u523B\u610F\u5B66\u4E60\u8BF4\u5434\u5730\u65B9\u8A00</strong>\u2014\u2014\u5728\u5F53\u65F6\u7684\u8BED\u5883\u4E0B\uFF0C\u540E\u8005\u53EA\u662F\u4E0D\u767B\u5927\u96C5\u4E4B\u5802\u7684\u4FDA\u8BED\u3002</p><p>\u53C8\u6BD4\u5982\uFF0C\u738B\u5BFC\u884C\u653F\uFF0C\u4EE5\u5B89\u5B9A\u7701\u4E8B\u4E3A\u8981\uFF0C\u5230\u4E86\u665A\u5E74\uFF0C\u751A\u81F3\u51E0\u4E4E\u90FD\u4E0D\u7BA1\u4E8B\u3002\u5F53\u65F6\u5C31\u6709\u4EBA\u8BA4\u4E3A\u738B\u5BFC\u660F\u8069\uFF0C\u4F46\u738B\u5BFC\u542C\u540E\u4E5F\u4E0D\u751F\u6C14\uFF0C\u53EA\u8BF4\uFF1A<strong>\u4F60\u4EEC\u73B0\u5728\u8BF4\u6211\u660F\u8069\uFF0C\u53EA\u6015\u4EE5\u540E\u4F1A\u60F3\u5FF5\u6211\u7684\u8FD9\u79CD\u660F\u8069\u3002</strong></p><p>\u738B\u5BFC\u7684\u8FD9\u79CD\u7741\u4E00\u53EA\u773C\u95ED\u4E00\u53EA\u773C\uFF0C\u4ECE\u6765\u5C31\u4E0D\u662F\u5185\u5FC3\u7CCA\u6D82\uFF0C\u800C\u662F\u4E3A\u4E86\u5F97\u5230\u6C5F\u4E1C\u52BF\u529B\u7684\u652F\u6301\uFF0C\u4E0D\u5F97\u4E0D\u505A\u51FA\u7684\u59A5\u534F\u4E0E\u9000\u8BA9\u3002</p><p>\u6BD5\u7ADF\uFF0C\u4E1C\u664B\u7684\u5F00\u56FD\u8FC7\u7A0B\u4E0E\u5386\u4EE3\u90FD\u4E0D\u76F8\u540C\u3002\u8FD9\u4E0D\u662F\u4E00\u4E2A\u4EE5\u6B66\u529B\u5F81\u670D\u5B9E\u73B0\u7ACB\u56FD\u7684\u671D\u4EE3\uFF0C<strong>\u800C\u662F\u7ECF\u8FC7\u4E86\u76F8\u5F53\u591A\u7684\u5229\u76CA\u59A5\u534F\u4E4B\u540E\uFF0C\u5728\u5404\u79CD\u52BF\u529B\u7684\u5E73\u8861\u4E2D\u8BDE\u751F\u7684\u4E00\u4E2A\u865A\u5F31\u671D\u5EF7\u3002</strong></p><p>\u867D\u7136\u4ECE\u8868\u9762\u4E0A\u770B\uFF0C\u4E1C\u664B\u5EFA\u7ACB\u4EE5\u540E\u7684\u60C5\u5F62\u8DDF\u4E00\u767E\u591A\u5E74\u524D\u7684\u4E1C\u5434\u7C7B\u4F3C\uFF0C\u90FD\u662F\u4F9D\u4ED7\u7740\u957F\u6C5F\u5929\u9669\uFF0C\u5360\u6709\u8346\u5DDE\u3001\u626C\u5DDE\u4E0E\u5317\u65B9\u6297\u8861\u3002\u4F46\u76F8\u4F3C\u7684\u4EC5\u4EC5\u662F\u8868\u9762\uFF0C\u4E1C\u664B\u7684\u6574\u4E2A\u5EFA\u7ACB\u8FC7\u7A0B\uFF0C\u8FDC\u6CA1\u6709\u6253\u4E0B\u5982\u4E1C\u5434\u4E00\u6837\u624E\u5B9E\u7684\u6839\u57FA\u3002</p><p>\u5F00\u56FD\u4E0D\u8FC7\u5341\u51E0\u5E74\uFF0C\u4E1C\u664B\u5C31\u5148\u540E\u53D1\u751F\u738B\u6566\u4E4B\u4E71\u548C\u82CF\u5CFB\u4E4B\u4E71\u4E24\u6B21\u53DB\u4E71\uFF0C\u8FD9\u5728\u5386\u4EE3\u5F00\u56FD\u4E4B\u65F6\u90FD\u7EDD\u65E0\u4EC5\u6709\u3002\u5176\u80CC\u540E\u6298\u5C04\u7684\uFF0C\u5C31\u662F\u8FD9\u4E2A\u738B\u671D\u5728\u519B\u4E8B\u80FD\u529B\u4E0A\u7684\u5B71\u5F31\u3002</p><blockquote><p><strong>\u4F46\u53CD\u8FC7\u6765\u770B\uFF0C\u5728\u8FD9\u79CD\u5B71\u5F31\u7684\u5B9E\u529B\u4E0B\uFF0C\u7ADF\u7136\u8FD8\u80FD\u591F\u5EFA\u7ACB\u4E00\u4E2A\u56FD\u5BB6\uFF0C\u8FD9\u4E5F\u624D\u66F4\u5F70\u663E\u738B\u5BFC\u7684\u8D21\u732E\u3002</strong></p></blockquote><p>\u738B\u5BFC\u6240\u9762\u4E34\u7684\u7684\u95EE\u9898\uFF0C\u5168\u4E0D\u662F\u4F20\u7EDF\u7684\u5F00\u56FD\u65F6\u671F\u7684\u95EE\u9898\u3002\u56E0\u6B64\u4ED6\u7684\u8D21\u732E\uFF0C\u65E2\u4E0D\u662F\u5982\u8427\u4F55\u4E00\u822C\u7A33\u5B9A\u540E\u65B9\uFF0C\u8DB3\u5175\u8DB3\u98DF\u4E8E\u524D\u7EBF\uFF1B\u4E5F\u4E0D\u662F\u50CF\u5F20\u826F\u4E00\u6837\u51FA\u8C0B\u5212\u7B56\uFF0C\u5B9A\u8BA1\u4E8E\u767E\u6B65\u4E4B\u5916\uFF0C\u800C\u662F\u6574\u5408\u5404\u65B9\u653F\u6CBB\u52BF\u529B\uFF0C\u8BA9\u4E1C\u5357\u65E2\u6709\u7684\u5730\u65B9\u52BF\u529B\u652F\u6301\u53F8\u9A6C\u777F\u7684\u7EDF\u6CBB\u3002</p><p>\u8FD9\u4E9B\u8BDD\u8BF4\u6765\u7B80\u5355\uFF0C\u505A\u6765\u5374\u5341\u5206\u590D\u6742\u3002\u4E24\u4E2A\u653F\u6CBB\u5B9E\u4F53\uFF0C\u5373\u4FBF\u6709\u5C40\u90E8\u7684\u8054\u76DF\uFF0C\u4E5F\u4F1A\u751F\u51FA\u65E0\u6570\u9F83\u9F89\uFF0C\u751A\u81F3\u4E8E\u53CD\u76EE\u6210\u4EC7\u3002<strong>\u66F4\u4F55\u51B5\u738B\u5BFC\u662F\u901A\u8FC7\u59A5\u534F\u62C9\u62E2\uFF0C\u5C31\u628A\u6C5F\u4E1C\u7684\u5730\u65B9\u52BF\u529B\u6574\u5408\u8FDB\u4E86\u53F8\u9A6C\u777F\u7684\u6267\u653F\u73ED\u5E95\u4E4B\u5185\u3002</strong></p><p>\u8FD9\u79CD\u8D44\u6E90\u6574\u5408\u7684\u80FD\u529B\uFF0C\u4E1C\u664B\u9664\u4E86\u738B\u5BFC\uFF0C\u4E0D\u505A\u7B2C\u4E8C\u4EBA\u60F3\u3002</p><p>\u8FD9\u624D\u662F\u738B\u5BFC\u4E0D\u53EF\u6216\u7F3A\u7684\u4EF7\u503C\u3002</p><p><strong>\u8D8A\u5927\u7684\u7EC4\u7EC7\uFF0C\u8D8A\u5BB9\u6613\u7AD6\u8D77\u58C1\u5792\uFF0C\u4E5F\u5C31\u8D8A\u9700\u8981\u534F\u8C03</strong>\uFF0C\u53EA\u6709\u534F\u8C03\u597D\u4E86\uFF0C\u624D\u80FD\u4EA7\u751F\u5408\u529B\u2014\u2014\u800C\u8FD9\u79CD\u5408\u529B\u7684\u7ED3\u679C\uFF0C\u662F\u5C0F\u7EC4\u7EC7\u7684\u529B\u91CF\u65E0\u6CD5\u6BD4\u62DF\u7684\u3002\u56E0\u6B64\u5728\u8FD9\u6837\u7684\u7EC4\u7EC7\u4E2D\uFF0C\u534F\u8C03\u80FD\u529B\uFF0C\u53EF\u80FD\u624D\u662F\u6700\u91CD\u8981\u7684\u80FD\u529B\u3002</p><p>\u4ECE\u8FD9\u4E2A\u89D2\u5EA6\u6765\u7406\u89E3\u738B\u5BFC\uFF0C\u4F60\u5C31\u77E5\u9053\u8FD9\u4E2A\u4EBA\u7684\u534F\u8C03\u80FD\u529B\u6709\u591A\u4E48\u5F3A\u5927\u4E86\u3002<strong>\u4ED6\u662F\u7528\u81EA\u5DF1\u7684\u534F\u8C03\u529B\uFF0C\u4ECE\u65E0\u5230\u6709\uFF0C\u786C\u751F\u751F\u521B\u9020\u51FA\u4E86\u4E00\u4E2A\u56FD\u5BB6\u3002</strong></p><p>\u53F2\u8F7D\u738B\u5BFC\u8EAB\u540E\uFF0C\u6781\u5C3D\u54C0\u8363\uFF0C\u201C\u8C25\u6587\u732E\uFF0C\u4E27\u846C\u4E4B\u793C\u89C6\u5B89\u5E73\u732E\u738B\u6545\u4E8B\uFF0C\u53C2\u7528\u5929\u5B50\u4E4B\u793C\u201D\u3002\u4E4B\u524D\u5B89\u5E73\u732E\u738B\u53F8\u9A6C\u5B5A\u7684\u4E27\u846C\u793C\u4EEA\uFF0C\u5DF2\u7ECF\u662F\u6709\u664B\u4EE5\u6765\u6700\u9AD8\u7EA7\u522B\u7684\u793C\u4EEA\uFF0C\u4F46\u7528\u5728\u738B\u5BFC\u8EAB\u4E0A\uFF0C\u5C24\u4E14\u4E0D\u591F\uFF0C\u53C8\u201C\u53C2\u7528\u5929\u5B50\u4E4B\u793C\u201D\u3002</p><blockquote><p><strong>\u5728\u4E00\u5B9A\u610F\u4E49\u4E0A\uFF0C\u738B\u5BFC\u624D\u662F\u4E1C\u664B\u7684<code>\u5F00\u56FD\u4E4B\u541B</code>\u3002</strong></p></blockquote>',70),e=[s];function c(g,d){return o(),t("div",null,e)}const u=p(n,[["render",c],["__file","21.html.vue"]]);export{u as default};
