import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.d69c5e30.js";const c={},o=a('<h1 id="\u7B2C22\u96C6-\u5047\u5192\u4F2A\u52A3\u5546\u54C1\u98CE\u6CE2" tabindex="-1"><a class="header-anchor" href="#\u7B2C22\u96C6-\u5047\u5192\u4F2A\u52A3\u5546\u54C1\u98CE\u6CE2" aria-hidden="true">#</a> \u7B2C22\u96C6\uFF1A\u5047\u5192\u4F2A\u52A3\u5546\u54C1\u98CE\u6CE2</h1><p>\u6C5F\u6CB3\u548C\u5DE7\u59D1\u89C1\u5927\u5149\u7239\u5728\u5356\u8D27\uFF0C\u5341\u5206\u60CA\u8BB6\uFF0C\u5FD9\u5C06\u4ED6\u529D\u8D70\u3002\u665A\u4E0A\uFF0C\u6C5F\u6CB3\u529D\u7389\u73E0\u4E0D\u8981\u8FD9\u6837\uFF0C\u7389\u73E0\u544A\u8BC9\u5979\uFF0C\u90A3\u4E9B\u5047\u7684\u70ED\u6C34\u5668\u5C31\u662F\u5927\u5149\u7239\u5728\u80CC\u540E\u6363\u7684\u9B3C \u3002</p><p>\u6C5F\u6CB3\u53BB\u91D1\u571F\u5BB6\u6253\u542C\u90A3\u4E9B\u5236\u9020\u5047\u8D27\u7684\u4EBA\u7684\u6D88\u606F\u3002\u4E00\u8FDB\u95E8\uFF0C\u771F\u5DE7\u9047\u89C1\uFF0C\u6709\u4EBA\u8981\u5F3A\u884C\u642C\u8D70\u91D1\u571F\u53D4\u5BB6\u7684\u7535\u89C6\u673A\uFF0C\u8BF4\u662F\u91D1\u571F\u6B20\u4E86\u4ED6\u4EEC\u4E94\u767E\u5143\u94B1\uFF0C\u6362\u4E0D\u4E86 \u3002\u9648\u6C5F\u6CB3\u8BA9\u4ED6\u4EEC\u653E\u4E0B\u7535\u89C6\u673A\uFF0C\u66FF\u91D1\u571F\u53D4\u8FD8\u4E0A\u4E86\u94B1\u3002\u91D1\u571F\u5341\u5206\u540E\u6094\u81EA\u5DF1\u7684\u6240\u4F5C\u6240\u4E3A \u3002\u4F46\u4ED6\u4E5F\u4E0D\u77E5\u9053\u90A3\u4E9B\u5236\u5047\u7684\u4EBA\u5728\u54EA\u91CC\uFF0C\u53EA\u77E5\u9053\u4ED6\u4EEC\u4EC0\u4E48\u597D\u5356\u505A\u4EC0\u4E48 \u3002</p><p>\u7389\u73E0\u6709\u610F\u8C03\u6559\u5DE7\u59D1\uFF0C\u8BA9\u5DE7\u59D1\u81EA\u5DF1\u53BB\u548C\u987E\u5BA2\u8C08\u4EF7\uFF0C\u4F46\u5DE7\u59D1\u6027\u683C\u592A\u8F6F\uFF0C\u6709\u6CA1\u6709\u4EC0\u4E48\u4E3B\u89C1 \u3002\u770B\u7684\u7389\u73E0\u5341\u5206\u7740\u6025\uFF0C\u53EA\u6709\u81EA\u5DF1\u4EB2\u81EA\u51FA\u624B\uFF0C\u5DE7\u59D1\u5341\u5206\u94A6\u4F69\uFF0C\u7389\u73E0\u8D81\u673A\u6559\u80B2\u5DE7\u59D1\uFF0C\u62FF\u91D1\u6C34\u53D4\u5F53\u5E74\u9E21\u6BDB\u6362\u7CD6\u7684\u7ECF\u9A8C\u4E3E\u4F8B\u5B50 \u3002</p><p>\u6C5F\u6CB3\u5E26\u91D1\u571F\u56DE\u6765\uFF0C\u8BA9\u7389\u73E0\u7ED9\u91D1\u571F\u4E00\u6279\u8D27\u62FF\u53BB\u96F6\u552E\uFF0C\u5356\u7684\u5229\u6DA6\u518D\u7528\u6765\u8FD8\u9648\u6C5F\u6CB3\u7684\u4E94\u767E\u5757\u94B1 \u3002\u7389\u73E0\u672C\u4E0D\u60C5\u613F\uFF0C\u4F46\u6C5F\u6CB3\u5374\u8BF4\u4E0D\u7136\u8FDE\u4E94\u767E\u5757\u90FD\u6536\u4E0D\u56DE\uFF0C\u7389\u73E0\u65E0\u5948\u540C\u610F \u3002</p><p>\u5DE7\u59D1\u5C06\u7389\u73E0\u7684\u8BDD\u5B66\u7ED9\u91D1\u6C34\u542C\uFF0C\u91D1\u6C34\u542C\u7684\u5341\u5206\u53D7\u7528\uFF0C\u8BA4\u4E3A\u7389\u73E0\u5012\u662F\u6709\u809A\u91CF \u3002</p><p>\u968F\u7740\u6539\u9769\u5F00\u653E\u7684\u6DF1\u5165\uFF0C\u4EBA\u4EEC\u5F00\u59CB\u8FFD\u6C42\u5404\u79CD\u56FD\u5916\u7684\u540D\u724C\uFF0C\u4E8E\u662F\uFF0C\u4E49\u4E4C\u7684\u4EFF\u5192\u4E4B\u98CE\u5174\u8D77\uFF0C\u5404\u5BB6\u5546\u6237\u90FD\u79F0\u81EA\u5DF1\u7684\u4E1C\u897F\u662F\u56FD\u5916\u8FDB\u53E3\u7684\uFF0C\u7531\u4E8E\u4EF7\u683C\u4F4E\u5EC9\uFF0C\u53C8\u53F7\u79F0\u662F\u540D\u724C \u3002\u751F\u610F\u5341\u5206\u706B\u7206 \u3002\u800C\u53EA\u6709\u7389\u73E0\u4E00\u5BB6\uFF0C\u7531\u4E8E\u9648\u6C5F\u6CB3\u4E4B\u524D\u7ACB\u4E0B\u7684\u89C4\u77E9\uFF0C\u751F\u610F\u4E00\u4E0B\u5B50\u53D8\u5F97\u5341\u5206\u6E05\u6DE1\uFF0C\u800C\u4E14\u5229\u6DA6\u4E5F\u65E0\u6CD5\u548C\u90A3\u4E9B\u5047\u5192\u7684\u5546\u54C1\u76F8\u6BD4\u3002\u7389\u73E0\u5BF9\u6B64\u5341\u5206\u53D1\u6101 \u3002\u5C31\u8FDE\u91D1\u571F\u53D4\u4E5F\u9000\u56DE\u4E86\u6C5F\u6CB3\u7ED9\u4ED6\u7684\u8D27\u7269\uFF0C\u8BF4\u662F\u8981\u53BB\u8FDB\u4E00\u4E9B\u597D\u5356\u7684\u8D27 \u3002\u7389\u73E0\u542C\u4E86 \uFF0C\u66F4\u662F\u5FC3\u91CC\u4E00\u56E2\u4E71\u9EBB\u3002</p><p>\u800C\u5F53\u65F6\u7684\u7ECF\u6D4E\u5F62\u52BF\u4E0B\uFF0C\u4E09\u89D2\u503A\u7684\u60C5\u51B5\u5341\u5206\u4E25\u91CD \uFF0C\u4E0A\u4E2D\u4E0B\u6E38\u4E09\u65B9\u4E92\u76F8\u6B20\u94B1\uFF0C\u6700\u4E0B\u6E38\u7684\u96F6\u552E\u5546\u4E1C\u897F\u5356\u4E0D\u51FA\u53BB\uFF0C\u8FD8\u4E0D\u4E86\u94B1\uFF0C\u540C\u65F6\u4E2D\u95F4\u5546\u53C8\u6B20\u7740\u4E0A\u6E38\u5382\u5BB6\u7684\u8D27\u6B3E \u3002\u800C\u9648\u6C5F\u6CB3\u591A\u5E74\u6765\u575A\u6301\u4E0D\u6B20\u522B\u4EBA\u7684\u94B1 \u3002\u4F46\u501F\u51FA\u53BB\u7684\u94B1\u53C8\u90FD\u6536\u4E0D\u56DE\u6765 \u3002\u4E00\u4E0B\u5B50\u8BA9\u81EA\u5DF1\u7684\u8D44\u91D1\u53D8\u5F97\u5341\u5206\u7D27\u5F20 \u3002</p><p>\u7389\u73E0\u63A5\u5230\u7535\u8BDD\uFF0C\u6709\u4EBA\u8981\u7ED9\u4ED6\u4ECB\u7ECD\u4E00\u6279\u597D\u5356\u7684\u8D27\uFF0C\u7389\u73E0\u5230\u4E86\u624D\u77E5\u9053\uFF0C\u539F\u6765\u662F\u4EFF\u9020\u56FD\u5916\u540D\u724C\u7684\u7BB1\u5305 \u3002\u7389\u73E0\u672C\u6765\u575A\u6301\u4E0D\u8981\uFF0C\u4F46\u770B\u5230\u6709\u597D\u51E0\u5BB6\u7684\u8001\u677F\u90FD\u5728\u7B49\u7740\u8FDB\u8D27 \u3002\u7ADF\u7136\u4E00\u54AC\u7259\uFF0C\u5C06\u6240\u6709\u7684\u94B1\u90FD\u6295\u4E86\u8FDB\u53BB\uFF0C\u8D2D\u4E70\u4E86\u4E00\u5927\u6279\u5047\u5192\u7BB1\u5305 \u3002</p><p>\u7389\u73E0\u521A\u521A\u5C06\u8D27\u7269\u8FD0\u56DE\u5BB6\uFF0C\u9648\u6C5F\u6CB3\u56DE\u6765\u4E86\uFF0C\u8FD8\u5E26\u7740\u521A\u4ECE\u56FD\u5916\u56DE\u6765\u7684\u90B1\u82F1\u6770 \u3002\u7389\u73E0\u4E00\u4E0B\u5B50\u614C\u4E86\u795E \u3002</p><p>\u90B1\u82F1\u6770\u8FD8\u5E26\u56DE\u6765\u4E00\u4E2A\u6D88\u606F\uFF0C\u4E49\u4E4C\u8981\u5F00\u59CB\u6574\u987F\u5E02\u573A\uFF0C\u72E0\u72E0\u6253\u51FB\u5047\u5192\u4F2A\u52A3\u5546\u54C1 \u3002\u540C\u65F6\u4E5F\u8981\u5F00\u59CB\u6574\u6CBB\u4E09\u89D2\u503A\u7684\u95EE\u9898\u3002\u7389\u73E0\u542C\u5230\u8FD9\u4E2A\u6D88\u606F\uFF0C\u4EFF\u4F5B\u4E00\u4E2A\u70B8\u96F7\u5728\u8033\u8FB9\u70B8\u54CD\u3002\u9648\u6C5F\u6CB3\u4ECE\u8FDB\u95E8\u5F00\u59CB\u5C31\u89C9\u5F97\u7389\u73E0\u4E0D\u5BF9\u52B2 \u3002\u9001\u8D70\u90B1\u82F1\u6770\u548C\u90B1\u5CA9\u540E\uFF0C\u9648\u6C5F\u6CB3\u624D\u77E5\u9053\uFF0C\u7389\u73E0\u5C06\u624B\u4E0A\u6240\u6709\u7684\u73B0\u91D1\u90FD\u62FF\u53BB\u8FDB\u4E86\u5047\u8D27 \u3002\u9648\u6C5F\u6CB3\u6C14\u6025\u5374\u4E0D\u77E5\u8BF4\u4EC0\u4E48\u597D\uFF0C\u53EA\u80FD\u8BF4\uFF0C\u5C31\u5F53\u8FD9\u6279\u8D27\u6CA1\u6709\u8FDB\u8FC7\uFF0C\u4E0D\u80FD\u62FF\u51FA\u53BB\u5356 \u3002</p><p>\u4E49\u4E4C\u5E02\u573A\u679C\u7136\u5F00\u59CB\u6253\u51FB\u5047\u5192\u4F2A\u52A3\uFF0C\u6BCF\u5929\u90FD\u6709\u5F88\u591A\u4EBA\u7684\u8D27\u7269\u88AB\u6536\u7F34\u3002\u7389\u73E0\u5219\u6BCF\u5929\u90FD\u9B42\u4E0D\u5B88\u820D \u3002</p><p>\u9648\u6C5F\u6CB3\u6CA1\u6709\u4E86\u529E\u6CD5\uFF0C\u53EA\u6709\u53BB\u6C42\u90B1\u82F1\u6770 \u3002\u89C1\u5230\u90B1\u82F1\u6770\u6B63\u5FD9\u7740\u6574\u6CBB\u5047\u8D27\uFF0C\u540C\u65F6\u8FD8\u5728\u8054\u7CFB\u94F6\u884C\uFF0C\u5E0C\u671B\u80FD\u5E2E\u52A9\u5546\u6237\u89E3\u51B3\u4E09\u89D2\u503A\u52A1\uFF0C\u5E2E\u4ED6\u4EEC\u6E21\u8FC7\u96BE\u5173 \u3002\u9648\u6C5F\u6CB3\u5B9E\u5728\u4E0D\u77E5\u9053\u8BE5\u5982\u4F55\u5F00\u53E3 \u3002\u90B1\u82F1\u6770\u542C\u8BF4\u4E86\u9648\u6C5F\u6CB3\u7684\u6765\u610F\uFF0C\u597D\u597D\u7684\u7ED9\u9648\u6C5F\u6CB3\u4E0A\u4E86\u4E00\u8BFE\uFF0C\u544A\u8BC9\u4ED6\uFF0C\u5546\u4EBA\u6700\u91CD\u8981\u7684\u4E0D\u662F\u8D5A\u94B1\uFF0C\u800C\u662F\u4FE1\u4EFB \u3002</p>',13),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","22.html.vue"]]);export{h as default};
