import{_ as o}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as e,f as c}from"./app.b5163196.js";const t="/myNotes/assets/img/base/640(9).jpeg",s="/myNotes/assets/img/base/640(10).jpeg",d={},r=c('<h1 id="\u9C9C\u5351\u6155\u5BB9\u4F20\u5947-\u7B2C\u4E00\u7BC7" tabindex="-1"><a class="header-anchor" href="#\u9C9C\u5351\u6155\u5BB9\u4F20\u5947-\u7B2C\u4E00\u7BC7" aria-hidden="true">#</a> \u9C9C\u5351\u6155\u5BB9\u4F20\u5947 \u7B2C\u4E00\u7BC7</h1><blockquote><p>\u71D5\u7FA4\u50DA\u5171\u4E0A\u5C0A\u53F7\u4E8E\u71D5\u738B\u5101\uFF0C\u5101\u8BB8\u4E4B\u3002\u2026\u2026\u620A\u8FB0\uFF0C\u5101\u5373\u7687\u5E1D\u4F4D\uFF0C\u5927\u8D66\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274 \u2022 \u5377\u4E5D\u5341\u4E5D \u2022 \u664B\u7EAA\u4E8C\u5341\u4E00\u300B</strong></p><p>\u4E1C\u664B\u5341\u516D\u56FD\u671F\u95F4\uFF0C\u5929\u4E0B\u5927\u4E71\uFF0C\u5148\u540E\u6709<strong>\u5308\u5974\u3001\u7FAF\u3001\u7F8C\u3001\u6C10\u3001\u9C9C\u5351</strong>\u4E94\u4E2A\u5F02\u65CF\u767B\u4E0A\u9010\u9E7F\u4E2D\u539F\u7684\u821E\u53F0\u3002\u8FD9\u5176\u4E2D\u529F\u4E1A\u6700\u5927\u7684\uFF0C\u5E94\u8BE5\u662F\u521B\u7ACB\u4E86\u524D\u79E6\u653F\u6743\u7684\u6C10\u65CF\uFF0C\u4EE5\u53CA\u5EFA\u7ACB\u8D77\u5317\u9B4F\u7684\u9C9C\u5351\u62D3\u8DCB\u90E8\u3002</p><p>**\u81F3\u4E8E\u9C9C\u5351\u6155\u5BB9\u90E8\uFF0C\u529F\u4E1A\u5176\u5B9E\u5E76\u4E0D\u663E\u8D6B\u3002**\u5728\u5386\u53F2\u7684\u957F\u6CB3\u4E2D\uFF0C\u6155\u5BB9\u6C0F\u6240\u5EFA\u7ACB\u7684\u738B\u671D\u5982\u5F57\u661F\u822C\u9AA4\u73B0\u9AA4\u9690\uFF0C\u4E0D\u4EC5\u5386\u65F6\u4E0D\u4E45\uFF0C\u4E5F\u66F4\u65E0\u5236\u5EA6\u5178\u7AE0\u5782\u6CD5\u4E8E\u540E\u4E16\u3002\u5373\u4FBF\u53EA\u662F\u76F8\u5BF9\u4E8E\u9B4F\u664B\u5357\u5317\u671D\u65F6\u671F\u7684\u5176\u4ED6\u671D\u4EE3\uFF0C\u4E5F\u6CA1\u6709\u7559\u4E0B\u592A\u591A\u4EAE\u70B9\u3002</p><p>\u897F\u5143352\u5E74\uFF0C<code>\u6155\u5BB9\u5101</code>\u79F0\u5E1D\uFF0C\u56FD\u53F7\u4E3A\u71D5\uFF0C\u53F2\u79F0 <code>\u524D\u71D5</code>\uFF0C\u5386 <code>18</code> \u5E74\uFF0C\u4F20\u4E8C\u4E16\u800C\u4EA1\u3002\u5982\u679C\u4ECE\u6155\u5BB9\u5101\u4E4B\u7236\u6155\u5BB9\u769D\u79F0\u71D5\u738B\u7B97\u8D77\uFF0C\u5171 <code>33</code> \u5E74\u3002</p><p>\u897F\u5143384\u5E74\uFF0C\u6155\u5BB9\u769D\u4E4B\u5B50<code>\u6155\u5BB9\u5782</code>\u79F0\u5E1D\uFF0C\u56FD\u53F7\u4E3A\u71D5\uFF0C\u53F2\u79F0 <code>\u540E\u71D5</code>\uFF0C\u5386 <code>23</code> \u5E74\uFF0C\u5386\u4E03\u4E3B\u800C\u4EA1\u3002</p><p>\u897F\u5143384\u5E74\uFF0C\u6155\u5BB9\u5101\u4E4B\u5B50<code>\u6155\u5BB9\u6CD3</code>\u79F0\u5E1D\uFF0C\u56FD\u53F7\u4E3A\u71D5\uFF0C\u53F2\u79F0 <code>\u897F\u71D5</code>\uFF0C\u5386 <code>10</code> \u5E74\uFF0C\u5386\u4E03\u4E3B\u800C\u4EA1\u3002</p><p>\u897F\u5143398\u5E74\uFF0C\u6155\u5BB9\u769D\u4E4B\u5B50<code>\u6155\u5BB9\u5FB7</code>\u79F0\u5E1D\uFF0C\u56FD\u53F7\u4E3A\u71D5\uFF0C\u53F2\u79F0 <code>\u5357\u71D5</code>\uFF0C\u5386 <code>12</code> \u5E74\uFF0C\u4F20\u4E8C\u4E16\u800C\u4EA1\u3002</p><p>\u9762\u5BF9\u8FD9\u6BB5\u7834\u788E\u79BB\u6563\u7684\u5386\u53F2\uFF0C\u6216\u8BB8\u6211\u4EEC\u53EA\u6709\u6362\u4E00\u4E2A\u89D2\u5EA6\uFF0C\u624D\u80FD\u770B\u5230\u53E6\u4E00\u5E45\u5149\u666F\uFF1A</p><p><img src="'+t+'" alt="demo" loading="lazy"></p><p>\u6155\u5BB9\u6C0F\u4ECE\u5174\u8D77\u5230\u79F0\u5E1D\uFF0C\u5386\u7ECF\u4E09\u4EE3\u4EBA\u7269\uFF0C\u5206\u522B\u662F\uFF1A<strong>\u6155\u5BB9<code>\u5EC6</code></strong>\u3001<strong>\u6155\u5BB9<code>\u769D</code></strong>\u3001<strong>\u6155\u5BB9<code>\u5101</code></strong>\u3002</p><p>\u6155\u5BB9\u5EC6\u7EE7\u4EFB <strong><code>\u9C9C\u5351</code>\u6155\u5BB9\u90E8</strong>\u5934\u9886\u7684\u90A3\u4E00\u5E74\uFF0C\u662F\u592A\u5EB7\u516D\u5E74\uFF08\u897F\u5143285\u5E74\uFF09\uFF0C\u6B64\u65F6\u8DDD\u79BB\u897F\u664B\u706D\u5434\u3001\u7EDF\u4E00\u4E2D\u56FD\u521A\u521A\u8FC7\u53BB\u4E94\u5E74\uFF0C\u5927\u664B\u671D\u6B63\u5904\u4E8E\u5168\u76DB\u65F6\u671F\u3002\u6155\u5BB9\u5EC6\u867D\u7136\u53F7\u79F0\u662F\u9C9C\u5351\u5355\u4E8E\uFF0C\u4F46\u6574\u4E2A\u9C9C\u5351\u6155\u5BB9\u90E8\u4E8E\u664B\u738B\u671D\u800C\u8A00\uFF0C\u4E5F\u4E0D\u8FC7\u662F\u585E\u5916\u4E00\u4E2A\u5FAE\u4E0D\u8DB3\u9053\u7684\u5C0F\u90E8\u843D\u800C\u5DF2\u3002</p><p>\u6155\u5BB9\u5EC6\u66FE\u501F\u53E3\u4E0E <strong><code>\u9C9C\u5351</code>\u5B87\u6587\u90E8</strong>\u6709\u4E16\u4EC7\uFF0C\u53D1\u52A8\u8FC7\u4E00\u573A\u6218\u4E89\uFF0C\u4F46\u5374\u88AB\u664B\u671D\u519B\u961F\u5927\u8D25\u3002\u6B64\u540E\u6155\u5BB9\u5EC6\u610F\u8BC6\u5230\u81EA\u8EAB\u5B9E\u529B\u96BE\u4EE5\u4E0E\u4E2D\u539F\u738B\u671D\u6297\u4E89\uFF0C\u4FBF\u8F6C\u800C\u9009\u62E9\u5F52\u987A\uFF0C\u63A5\u53D7\u4E2D\u539F\u738B\u671D\u7684\u518C\u5C01\u2014\u2014\u6B64\u65F6\u7684\u6155\u5BB9\u5EC6\uFF0C\u53EA\u670921\u5C81\u3002</p><p>\u800C\u6155\u5BB9\u90E8\u4E0E\u5B87\u6587\u90E8\u518D\u6B21\u4F5C\u6218\uFF0C\u5DF2\u7ECF\u662F\u5341\u51E0\u5E74\u4EE5\u540E\u7684\u4E8B\u4E86\u3002\u8FD9\u4E00\u6B21\u662F\u5B87\u6587\u90E8\u9996\u5148\u6311\u8845\uFF0C\u6155\u5BB9\u90E8\u53D1\u8D77\u8FD8\u51FB\uFF0C\u6700\u7EC8\uFF0C\u6155\u5BB9\u90E8\u5927\u8D25\u5B87\u6587\u90E8\u3002</p><p>\u4E0E\u4E0A\u6B21\u4E0D\u540C\u7684\u662F\uFF0C\u5B87\u6587\u90E8\u8FD9\u56DE\u518D\u4E5F\u6CA1\u6709\u529E\u6CD5\u5411\u664B\u671D\u6C42\u63F4\u4E86\u3002\u7531\u4E8E\u516B\u738B\u4E4B\u4E71\u6B63\u9163\uFF0C\u671D\u653F\u65E9\u5DF2\u5927\u4E71\uFF0C\u671D\u5EF7\u6839\u672C\u5C31\u6CA1\u6709\u7CBE\u529B\u6765\u987E\u53CA\u8FB9\u7586\u5730\u533A\u7684\u8FD9\u4E9B\u829D\u9EBB\u5C0F\u4E8B\u3002</p><p>\u6B64\u524D\u4E2D\u539F\u738B\u671D\u5728\u5F3A\u5927\u65F6\uFF0C\u5BF9\u5F85\u585E\u5916\u6C11\u65CF\u7684\u65B9\u9488\u662F\u7B3C\u7EDC\u79BB\u95F4\uFF0C\u5206\u800C\u6CBB\u4E4B\u3002\u7B80\u5355\u6765\u8BF4\uFF0C\u5C31\u662F\u62C9\u4E00\u4E2A\u6253\u4E00\u4E2A\uFF0C\u4E0D\u8BA9\u67D0\u4E2A\u90E8\u843D\u8FC7\u5206\u5F3A\u76DB\uFF0C\u5F7C\u6B64\u95F4\u7EF4\u6301\u5E73\u8861\uFF0C\u7531\u671D\u5EF7\u5C45\u4E2D\u8C03\u8282\u3002\u5982\u4ECA\u671D\u5EF7\u81EA\u987E\u4E0D\u6687\uFF0C\u6155\u5BB9\u6C0F\u4E5F\u7EC8\u4E8E\u5F97\u5230\u4E86\u62AC\u5934\u7684\u673A\u4F1A\uFF0C\u5F00\u59CB\u653E\u624B\u6269\u5927\u81EA\u5DF1\u7684\u52BF\u529B\u3002</p><blockquote><p><strong>\u6155\u5BB9\u5EC6\u7684\u65B9\u6CD5\uFF0C\u4E3B\u8981\u6709\u4E24\u6761\uFF1A</strong><strong>\u7B2C\u4E00\uFF0C\u6253\u51FB\u5468\u56F4\u7684\u654C\u5BF9\u52BF\u529B\u3002</strong><strong>\u7B2C\u4E8C\uFF0C\u5927\u91CF\u6536\u5BB9\u6C49\u4EBA\u3002</strong></p></blockquote><p>\u8FD9\u91CC\u91CD\u70B9\u503C\u5F97\u4E00\u8BF4\u7684\u662F\u7B2C\u4E8C\u70B9\uFF0C\u6155\u5BB9\u6C0F\u6536\u5BB9\u6C49\u4EBA\u7684\u65B9\u6CD5\uFF0C\u5E76\u4E0D\u50CF\u5343\u5E74\u540E\u7684\u5973\u771F\u4EBA\u90A3\u6837\uFF0C\u8BA9\u4FD8\u864F\u7684\u6C49\u4EBA\u505A\u5305\u8863\uFF0C**\u800C\u662F\u76F4\u63A5\u4F9D\u7167\u6C49\u4EBA\u7684\u65B9\u6CD5\u6765\u6CBB\u7406\u6C49\u4EBA\u3002**\u53F2\u8F7D\uFF0C\u201C\uFF08\u6155\u5BB9\uFF09\u5EC6\u4E43\u7ACB\u90E1\u4EE5\u7EDF\u6D41\u4EBA\uFF0C\u5180\u5DDE\u4EBA\u4E3A\u5180\u9633\u90E1\uFF0C\u8C6B\u5DDE\u4EBA\u4E3A\u6210\u5468\u90E1\uFF0C\u9752\u5DDE\u4EBA\u4E3A\u8425\u4E18\u90E1\uFF0C\u5E76\u5DDE\u4EBA\u4E3A\u5510\u56FD\u90E1\u201D\u3002</p><p>**\u8FD9\u4E2A\u624B\u6BB5\uFF0C\u5DF2\u7ECF\u6709\u76F8\u5F53\u7684\u653F\u6CBB\u667A\u6167\u5728\u5176\u4E2D\u3002**\u5F53\u65F6\uFF0C\u5317\u65B9\u7684\u5404\u4E2A\u52BF\u529B\u90FD\u8FD8\u53EA\u5FD9\u4E8E\u7528\u6D41\u6C11\u5145\u5B9E\u519B\u961F\uFF0C\u800C\u6155\u5BB9\u5EC6\uFF0C\u5DF2\u7ECF\u80FD\u591F\u8003\u8651\u6D41\u6C11\u5B89\u5C45\u7684\u95EE\u9898\u4E86\u3002</p><p>\u4E5F\u6B63\u56E0\u4E3A\u5982\u6B64\uFF0C\u5411\u6155\u5BB9\u90E8\u6295\u5954\u7684\u6C49\u4EBA\u8D8A\u6765\u8D8A\u591A\uFF0C\u5E76\u7EC8\u4E8E\u5F15\u8D77\u522B\u4EBA\u7684\u5FCC\u6068\u2014\u2014\u8FD9\u4E2A\u5FCC\u6068\u6155\u5BB9\u6C0F\u7684\u4EBA\uFF0C\u662F\u5F53\u65F6\u7684<strong>\u5E73\u5DDE\u523A\u53F2\u3001\u4E1C\u5937\u6821\u5C09<code>\u5D14\u6BD6</code>\u3002</strong></p><p>\u4E71\u4E16\u4E4B\u4E2D\uFF0C\u4EBA\u53E3\u5C31\u610F\u5473\u7740\u5B9E\u529B\u3002\u773C\u89C1\u6D41\u6C11\u65E0\u6570\uFF0C\u5D14\u6BD6\u81EA\u7136\u4E5F\u60F3\u62DB\u63FD\uFF0C\u4F46\u65E0\u5948\u8FD9\u4E9B\u4EBA\u5374\u62A2\u7740\u5F80\u6155\u5BB9\u5EC6\u90A3\u91CC\u8DD1\uFF0C\u8C01\u4E5F\u4E0D\u6295\u5954\u4ED6\uFF0C\u8FD9\u8BA9\u5D14\u6BD6\u6068\u6068\u4E0D\u5DF2\uFF0C\u5F00\u59CB\u6253\u6155\u5BB9\u5EC6\u7684\u4E3B\u610F\u3002</p><p>\u968F\u7740\u6155\u5BB9\u90E8\u7684\u5B9E\u529B\u4E0D\u65AD\u6269\u5927\uFF0C\u5468\u56F4\u7684\u52BF\u529B\u5F00\u59CB\u6709\u6240\u62C5\u5FE7\uFF0C\u5D14\u6BD6\u4E8E\u662F\u501F\u673A\u9F13\u52A8\u3002\u6700\u7EC8\uFF0C<strong>\u9C9C\u5351\u5B87\u6587\u90E8\u3001\u9C9C\u5351\u6BB5\u90E8\uFF0C\u4EE5\u53CA\u9AD8\u53E5\u4E3D\u4E09\u65B9\u8054\u5408\u8BA8\u4F10\u6155\u5BB9\u5EC6\u3002</strong></p><p>\u4F46\u6155\u5BB9\u5EC6\u5E76\u4E0D\u60E7\u6015\u3002\u4ED6\u9488\u5BF9\u4E09\u65B9\u52BF\u529B\u4E4B\u95F4\u7F3A\u4E4F\u4FE1\u4EFB\u7684\u5F31\u70B9\uFF0C\u5F00\u59CB\u79BB\u95F4\u4ED6\u4EEC\u5F7C\u6B64\u7684\u5173\u7CFB\u3002\u5F53\u5927\u519B\u6765\u653B\u65F6\uFF0C\u4ED6\u9996\u5148\u95ED\u95E8\u4E0D\u6218\uFF0C\u7136\u540E\u539A\u8D60\u5B87\u6587\u90E8\u793C\u54C1\u8D22\u7269\uFF0C\u5F15\u8D77\u5176\u4F59\u4E24\u90E8\u731C\u7591\u3002</p><p>\u7531\u4E8E\u6000\u7591\u5B87\u6587\u90E8\u4E0E\u6155\u5BB9\u90E8\u4E4B\u95F4\u6709\u4EA4\u6613\uFF0C\u5176\u4F59\u4E24\u90E8\u5BB3\u6015\u88AB\u7B97\u8BA1\uFF0C\u5F15\u5175\u9000\u8D70\u3002\u968F\u540E\uFF0C\u6155\u5BB9\u5EC6\u5C3D\u8D77\u7CBE\u9510\uFF0C\u5B87\u6587\u90E8\u5168\u519B\u8986\u6CA1\u3002</p><p>\u8FD9\u5176\u4E2D\u662F\u4E0D\u662F\u5F88\u6709\u300A\u70DB\u4E4B\u6B66\u9000\u79E6\u5E08\u300B\u7684\u610F\u5473\uFF1F</p><blockquote><p><strong>\u6155\u5BB9\u5EC6\u53E6\u5916\u4E00\u4E2A\u62DB\u63FD\u6C49\u4EBA\u7684\u529E\u6CD5\uFF0C\u5219\u662F\u5411\u671D\u5EF7\u8981\u5C01\u8D4F\u3002</strong></p></blockquote><p>\u6155\u5BB9\u5EC6\u7684\u5B98\u7235\uFF0C\u90FD\u4E0D\u662F\u81EA\u5C01\u7684\uFF0C\u800C\u662F\u51FA\u81EA\u4E8E\u671D\u5EF7\u7684\u4EFB\u547D\u3002\u5373\u4FBF\u5F53\u65F6\u671D\u5EF7\u5DF2\u7ECF\u98A0\u6C9B\u6D41\u79BB\uFF0C\u5B8C\u5168\u5931\u53BB\u4E86\u5BF9\u56DB\u65B9\u7684\u7EDF\u6CBB\uFF0C\u6155\u5BB9\u5EC6\u4E5F\u4F9D\u7136\u575A\u6301\u5BFB\u6C42\u671D\u5EF7\u7684\u518C\u5C01\u3002</p><p>\u5F53\u65F6<code>\u738B\u6D5A</code>\u4EFB\u5E7D\u5DDE\u523A\u53F2\uFF0C\u5FC3\u6000\u4E0D\u8F68\uFF0C\u5984\u56FE\u79F0\u5E1D\uFF0C\u66FE\u7ECF\u5C01\u6155\u5BB9\u5EC6\u4E3A\u201C\u6563\u9A91\u5E38\u4F8D\u3001\u524D\u950B\u5927\u90FD\u7763\u3001\u5927\u5355\u4E8E\u201D\uFF0C\u4EE5\u6C42\u7B3C\u7EDC\u6155\u5BB9\u5EC6\uFF0C\u4F46\u6155\u5BB9\u5EC6\u5374\u575A\u51B3\u63A8\u8F9E\uFF0C\u4E0D\u613F\u9886\u53D7\u3002\u800C\u7B49\u5230\u8EAB\u5728\u957F\u5B89\u7684<code>\u664B\u615C\u5E1D</code>\u4EFB\u547D\u6155\u5BB9\u5EC6\u4E3A\u201C\u9547\u519B\u5C06\u519B\u3001\u660C\u9ECE\u3001\u8FBD\u4E1C\u4E8C\u56FD\u516C\u201D\u65F6\uFF0C\u6155\u5BB9\u5EC6\u5374\u6B23\u7136\u63A5\u53D7\u3002</p><p>\u5F7C\u65F6\u664B\u615C\u5E1D\u6240\u80FD\u7BA1\u8F96\u7684\u571F\u5730\uFF0C\u867D\u7136\u4E0D\u51FA\u957F\u5B89\u4E00\u57CE\uFF0C**\u4F46\u9664\u4E86\u4EE5\u664B\u5EF7\u4E3A\u654C\u7684\u5308\u5974\u4EBA\u5916\uFF0C\u664B\u615C\u5E1D\u4F9D\u7136\u662F\u5929\u4E0B\u5404\u65B9\u52BF\u529B\u6240\u516C\u8BA4\u7684\u7687\u5E1D\u3002**\u56E0\u6B64\uFF0C\u664B\u615C\u5E1D\u7684\u518C\u5C01\u4F9D\u65E7\u610F\u5473\u7740\u662F\u6B63\u5F0F\u7684\u4EFB\u547D\u3002\u800C\u8FD9\uFF0C\u6070\u662F\u6155\u5BB9\u5EC6\u7528\u4EE5\u5438\u7EB3\u6C49\u4EBA\u7684\u540D\u5206\u5927\u4E49\u6240\u5728\u3002</p><blockquote><p>\u540E\u6765\u53F8\u9A6C\u777F\u5728\u5EFA\u5EB7\u79F0\u5E1D\uFF0C\u5EFA\u7ACB\u4E1C\u664B\u3002\u6155\u5BB9\u5EC6\u53C8\u6D3E\u4EBA\u671D\u8D3A\uFF0C\u8868\u793A\u652F\u6301\uFF0C\u81EA\u7136\u53C8\u6362\u4E86\u4E00\u5806\u5B98\u804C\u56DE\u6765\u3002</p></blockquote><p>\u9E21\u8D3C\u7684\u6155\u5BB9\u5EC6\u5230\u665A\u5E74\u65F6\uFF0C\u751A\u81F3\u8FD8\u66FE\u7ED9\u4E1C\u664B\u7684\u5B9E\u529B\u6D3E\u4EBA\u7269<code>\u9676\u4F83</code>\u5199\u4FE1\uFF0C<strong>\u5E0C\u671B\u80FD\u4E0E\u5BF9\u65B9\u540C\u65F6\u51FA\u5175\uFF0C\u5939\u51FB\u57FA\u672C\u4E0A\u5DF2\u7ECF\u7EDF\u4E00\u5317\u65B9\u7684\u540E\u8D75\u3002</strong></p><blockquote><p>\u5728\u4FE1\u91CC\uFF0C\u6155\u5BB9\u5EC6\u8DDF\u9676\u4F83\u5927\u8C08\u7231\u56FD\u60C5\u64CD\u3002\u4ED6\u8BF4\uFF0C\u6D1B\u9633\u7684\u90A3\u4E9B\u6587\u6B66\u767E\u5B98\u7A7A\u8D1F\u56FD\u6069\uFF0C\u5374\u201C\u4E0D\u80FD\u706D\u4E2D\u539F\u4E4B\u5BC7\uFF0C\u5237\u5929\u4E0B\u4E4B\u803B\u201D\u3002 \u7136\u540E\uFF0C\u53C8\u62CD\u9676\u4F83\u7684\u9A6C\u5C41\uFF0C\u8BF4\u4ED6\u201C\u629A\u5B81\u65B9\u590F\uFF0C\u52B3\u5FC3\u6587\u6B66\u201D\uFF0C\u81EA\u5DF1\u5BF9\u4ED6\u94A6\u4F69\u5F02\u5E38\uFF0C\u201C\u94A6\u9AD8\u4EF0\u6B62\uFF0C\u6CE8\u60C5\u5F25\u4E45\u201D\u3002 \u7D27\u63A5\u7740\uFF0C\u6155\u5BB9\u5EC6\u5F00\u59CB\u57CB\u6C70\u5728\u671D\u4E2D\u6267\u653F\u7684\u738B\u5BFC\u4E0E\u5EBE\u4EAE\uFF0C\u8BF4\u738B\u5BFC\u201C\u6E05\u865A\u5BE1\u6B32\uFF0C\u5584\u4E8E\u5168\u5DF1\u201D\uFF0C\u5EBE\u4EAE\u201C\u8D85\u7136\u9AD8\u8E48\uFF0C\u660E\u667A\u4E4B\u6743\u201D\uFF0C\u603B\u800C\u8A00\u4E4B\uFF0C\u5C31\u662F\u5404\u79CD\u4E0D\u4F5C\u4E3A\u3002 \u76F4\u5230\u6700\u540E\uFF0C\u6155\u5BB9\u5EC6\u624D\u70B9\u9898\uFF1A\u5E0C\u671B\u9676\u4F83\u80FD\u591F\u51FA\u5175\u5317\u4F10\uFF0C\u5C4A\u65F6\u81EA\u5DF1\u5728\u540E\u65B9\u63A5\u5E94\u3002\u5982\u6B64\u4E24\u76F8\u5408\u529B\uFF0C\u4FBF\u53EF\u4EE5\u8BA9\u540E\u8D75\u9996\u5C3E\u4E0D\u80FD\u76F8\u987E\uFF0C\u8FBE\u5230\u4E24\u9762\u5939\u51FB\u7684\u6548\u679C\u3002</p></blockquote><p>\u5E73\u5FC3\u800C\u8BBA\uFF0C\u8FD9\u5C31\u662F\u4E00\u756A\u5927\u8BDD\u3002\u56E0\u4E3A\u8FD9\u5C01\u4FE1\u6839\u672C\u6CA1\u80FD\u9001\u5230\u9676\u4F83\u624B\u91CC\uFF0C\u534A\u8DEF\u5C31\u4E22\u4E86\uFF1A**\u5F53\u65F6\u5357\u5317\u76F8\u901A\u7684\u9646\u8DEF\u5DF2\u7ECF\u5B8C\u5168\u5728\u540E\u8D75\u7684\u5730\u76D8\u5185\uFF0C\u6155\u5BB9\u5EC6\u8DDF\u4E1C\u664B\u901A\u97F3\u4FE1\u53EA\u80FD\u8D70\u6D77\u8DEF\u3002**\u504F\u504F\u8FD9\u652F\u9001\u4FE1\u7684\u961F\u4F0D\u8FD0\u6C14\u4E0D\u597D\uFF0C\u88AB\u6D77\u6D6A\u7ED9\u5377\u8D70\u4E86\u3002</p><p>\u8054\u4FE1\u606F\u90FD\u901A\u4E0D\u4E86\uFF0C\u8FD8\u8C08\u4EC0\u4E48\u524D\u540E\u5939\u51FB\u5462\uFF1F</p><p>\u800C\u4E14\u9000\u4E00\u4E07\u6B65\u8BB2\uFF0C\u8FD9\u5C01\u5531\u9AD8\u8C03\u7684\u4FE1\u5C31\u7B97\u80FD\u5FFD\u60A0\u5230\u9676\u4F83\u5317\u4F10\uFF0C\u4E5F\u4E0D\u8FC7\u662F\u4FBF\u4E8E\u6155\u5BB9\u5EC6\u81EA\u5DF1\u5728\u540E\u65B9\u5360\u70B9\u4FBF\u5B9C\u7F62\u4E86\u3002</p><p>\u4F46\u6155\u5BB9\u5EC6\u5E76\u672A\u56E0\u4E3A\u5FEB\u9012\u7684\u4E22\u4EF6\u5C31\u6B7B\u5FC3\uFF0C\u4ED6\u53C8\u7ED9\u9676\u4F83\u5199\u4E86\u4E00\u5C01\u4FE1\uFF0C\u8FD9\u5C01\u4FE1\u7684\u5185\u5BB9\u66F4\u52A0\u76F4\u63A5\uFF1A</p><blockquote><p>\u54CE\uFF0C\u624B\u4E0B\u90FD\u529D\u6211\u79F0\u71D5\u738B\uFF0C\u4F46\u662F\u6211\u89C9\u5F97\u4E0D\u662F\u5F88\u5408\u9002\u8036\uFF0C\u4F60\u89C9\u5F97\u600E\u4E48\u6837\u5462\uFF1F\u671D\u5EF7\u613F\u610F\u5C01\u6211\u4E3A\u71D5\u738B\u5417\uFF1F\u8981\u4E0D\u4F60\u5E2E\u6211\u95EE\u95EE\uFF1F</p></blockquote><p>\u8FD9\u6B21\u9676\u4F83\u6536\u5230\u4E86\u4FE1\uFF0C\u5E76\u4E14\u56DE\u590D\u5F97\u4E5F\u5F88\u76F4\u767D\uFF1A</p><blockquote><p>\u50CF\u4F60\u8FD9\u6837\u7684\u8EAB\u4EFD\uFF0C\u5C01\u71D5\u738B\u4E5F\u672A\u5C1D\u4E0D\u53EF\u3002\u4F46\u4ECE\u6765\u90FD\u662F\u56E0\u529F\u5C01\u7235\uFF0C\u56E0\u6B64\u4F60\u82E5\u771F\u7684\u60F3\u83B7\u5C01\uFF0C\u5C31\u5F97\u6709\u70B9\u62FF\u5F97\u51FA\u624B\u7684\u529F\u52B3\uFF0C\u4E5F\u597D\u5835\u522B\u4EBA\u7684\u5634\u3002</p></blockquote><p>\u6CA1\u8FC7\u591A\u4E45\uFF0C\u6155\u5BB9\u5EC6\u4FBF\u53BB\u4E16\u4E86\uFF0C\u8FD9\u4EF6\u4E8B\u4E5F\u5C31\u4E0D\u4E86\u4E86\u4E4B\u3002</p><p>\u6B64\u540E\uFF0C\u6155\u5BB9\u5EC6\u4E4B\u5B50<code>\u6155\u5BB9\u769D</code>\u7EE7\u4F4D\u3002</p><p>\u6155\u5BB9\u769D\u5728\u4F4D\u4E00\u5171\u5341\u4E94\u5E74\uFF0C\u4E8E\u6155\u5BB9\u6C0F\u7684\u529F\u4E1A\uFF0C\u5927\u7EA6\u6709\u4E09\u4EF6\u4E8B\u503C\u5F97\u4E00\u8BF4\uFF1A</p><blockquote><p><strong>\u5176\u4E00 \uFF0C\u57FA\u672C\u4E0A\u6D88\u706D\u4E86\u538B\u5236\u6155\u5BB9\u6C0F\u53D1\u5C55\u7684\u4E09\u5927\u52BF\u529B\u2014\u2014\u9C9C\u5351\u5B87\u6587\u90E8\u3001\u9C9C\u5351\u6BB5\u90E8\u3001\u9AD8\u53E5\u4E3D\u3002</strong></p></blockquote><p>\u6155\u5BB9\u769D\u7684\u519B\u4E8B\u80FD\u529B\uFF0C\u5F88\u660E\u663E\u8981\u9AD8\u51FA\u5176\u7236\u4E00\u5927\u622A\u3002\u5728\u4ED6\u7EDF\u6CBB\u671F\u95F4\uFF0C\u4E09\u5927\u52BF\u529B\u7684\u5A01\u80C1\u57FA\u672C\u4E0A\u88AB\u6D88\u9664\u3002</p><p>\u9C9C\u5351\u6BB5\u90E8\u66FE\u7ECF\u5411\u7EDF\u6CBB\u5317\u65B9\u540E\u8D75\u6C42\u63F4\uFF0C\u5171\u540C\u653B\u6253\u6155\u5BB9\u90E8\uFF0C\u4F46\u5374\u88AB\u6155\u5BB9\u769D\u51FB\u8D25\u3002\u6B64\u540E\uFF0C<strong>\u6155\u5BB9\u90E8\u57FA\u672C\u5960\u5B9A\u4E86\u5728\u4E1C\u5317\u4E9A\u7684\u7EDF\u6CBB\u5B9E\u529B\u3002</strong></p><blockquote><p><strong>\u5176\u4E8C\uFF0C\u662F\u8FEB\u4F7F\u4E1C\u664B\u671D\u5EF7\u627F\u8BA4\u4E86\u81EA\u5DF1\u7684\u201C\u71D5\u738B\u201D\u5C01\u53F7\u3002</strong></p></blockquote><p>\u6155\u5BB9\u769D\u5728\u5373\u4F4D\u540E\u4E0D\u4E45\u4FBF\u81EA\u79F0\u71D5\u738B\uFF0C\u8FD9\u6B63\u662F\u4ED6\u7684\u7236\u4EB2\u6155\u5BB9\u5EC6\u5F53\u5E74\u5E0C\u671B\u9676\u4F83\u80FD\u5411\u671D\u5EF7\u4E3E\u8350\u7684\u5C01\u53F7\u3002\u4F46\u5F53\u65F6\u9676\u4F83\u7ED9\u6155\u5BB9\u5EC6\u7684\u56DE\u590D\u662F\uFF0C\u4F60\u5F97\u62FF\u529F\u4E1A\u6765\u6362\uFF0C\u5F88\u660E\u663E\uFF0C\u6155\u5BB9\u5EC6\u6CA1\u6709\u90A3\u4E2A\u80FD\u529B\u3002</p><p>\u53EF\u5982\u4ECA\u6155\u5BB9\u769D\u6709\u3002\u6155\u5BB9\u769D\u901A\u8FC7\u51E0\u6B21\u6218\u4E89\uFF0C\u5DF2\u5C06\u4E1C\u5317\u4E9A\u7EB3\u5165\u7EDF\u6CBB\u4E4B\u5185\u3002\u5728\u8FD9\u79CD\u60C5\u5F62\u4E0B\uFF0C\u4ED6\u518D\u6B21\u6D3E\u4EBA\u53BB\u5EFA\u5EB7\uFF0C\u5E0C\u671B\u5F97\u5230\u5C01\u7235\u3002\u6700\u7EC8\uFF0C\u5EFA\u5EB7\u671D\u5EF7\u540C\u610F\u4E86\u6155\u5BB9\u769D\u8BF7\u6C42\uFF0C\u5E76\u6D3E\u4EBA\u518C\u5C01\u3002</p><p>\u5F88\u591A\u4EBA\u53EF\u80FD\u4F1A\u62B1\u6709\u7591\u95EE\uFF1A</p><p><strong>\u8FD9\u79CD\u534E\u800C\u4E0D\u5B9E\u7684\u5C01\u738B\u6709\u4EC0\u4E48\u7528\u5417\uFF1F</strong></p><p>\u7B54\u6848\u662F\u5F53\u7136\u6709\uFF0C\u800C\u4E14\u4F5C\u7528\u8FD8\u5F88\u5927\uFF1A</p><p><strong>\u9996\u5148</strong>\uFF0C\u4E0D\u7BA1\u662F\u62DB\u63FD\u6C49\u4EBA\uFF0C\u8FD8\u662F\u4E0E\u540E\u8D75\u4F5C\u6218\uFF0C\u6155\u5BB9\u90E8\u90FD\u9700\u8981\u6253\u51FA\u4E00\u4E2A\u671D\u5EF7\u7ED9\u7684\u5C01\u53F7\uFF0C\u8FD9\u6837\u624D\u540D\u6B63\u8A00\u987A\u3002</p><p>\u5C31\u50CF\u5982\u4ECA\u7684\u8054\u5408\u56FD\uFF0C\u867D\u7136\u53EA\u662F\u4E2A\u6A61\u76AE\u56FE\u7AE0\uFF0C\u4F46\u7F8E\u56FD\u5982\u679C\u8981\u8DDF\u522B\u56FD\u5F00\u6218\uFF0C\u4E5F\u8FD8\u662F\u5E0C\u671B\u80FD\u4ECE\u8054\u5408\u56FD\u5F04\u5230\u4E00\u4E2A\u51B3\u8BAE\u7684\uFF0C\u5982\u6B64\u624D\u7B97\u5E08\u51FA\u6709\u540D\u3002</p><p><strong>\u5176\u6B21</strong>\uFF0C\u671D\u5EF7\u5BF9\u6155\u5BB9\u769D\u8FDB\u884C\u518C\u5C01\uFF0C\u4E5F\u80FD\u663E\u793A\u51FA\u534E\u590F\u6B63\u6714\u670D\u81BA\u8FDC\u4EBA\u7684\u6C14\u5EA6\u4E0E\u8363\u5149\u3002</p><p>\u53CC\u65B9\u5404\u53D6\u6240\u9700\uFF0C\u4E00\u62CD\u5373\u5408\u3002</p><blockquote><p><strong>\u6B64\u5916\uFF0C\u8FD8\u6709\u7B2C\u4E09\u4EF6\u4E8B\uFF0C\u5C31\u662F\u6155\u5BB9\u769D\u751F\u4E86\u4E00\u7FA4\u597D\u513F\u5B50\u3002</strong></p></blockquote><p>\u8FD9\u7FA4\u513F\u5B50\uFF0C\u5305\u62EC\u4E86\u540E\u6765\u79F0\u5E1D\u7684<code>\u6155\u5BB9\u5101</code>\uFF0C\u4EE5\u53CA<code>\u6155\u5BB9\u606A</code>\u3001<code>\u6155\u5BB9\u5782</code>\u3002\u6155\u5BB9\u769D\u5728\u4EFB\u671F\u95F4\u6240\u53D1\u751F\u7684\u8FD9\u4E9B\u6218\u4E89\uFF0C\u57FA\u672C\u4E0A\u90FD\u662F\u7531\u4ED6\u7684\u513F\u5B50\u4EEC\u6253\u7684\u3002</p><p>\u6155\u5BB9\u769D\u7EDF\u5171\u5728\u4F4D\u5341\u4E94\u5E74\uFF0C\u53BB\u4E16\u540E\uFF0C\u5176\u5B50<code>\u6155\u5BB9\u5101</code>\u7EE7\u4EFB\u71D5\u738B\u3002</p><p>\u5373\u4F4D\u540E\u7684\u7B2C\u4E8C\u5E74\uFF0C\u6155\u5BB9\u5101\u5373\u4FBF\u5357\u5F81\uFF0C\u51C6\u5907\u4E0E\u540E\u8D75\u5F00\u6218\uFF0C\u4E89\u593A\u5730\u76D8\u3002<strong>\u8FD9\u6B21\u5386\u53F2\u7ED9\u4E86\u6155\u5BB9\u6C0F\u4E00\u4E2A\u597D\u65F6\u673A\uFF1A\u540E\u8D75\u671D\u653F\u6B64\u65F6\u6B63\u9677\u5165\u5927\u4E71\u3002</strong></p><p>\u540E\u8D75\u7687\u5E1D\u77F3\u864E\u6B7B\u540E\uFF0C\u56FD\u5185\u53D1\u751F\u5185\u4E71\u3002\u6700\u7EC8\uFF0C\u6C49\u4EBA<code>\u5189\u95F5</code>\u6740\u5C3D\u7FAF\u65CF\u4EBA\uFF0C\u81EA\u7ACB\u79F0\u5E1D\uFF0C\u77F3\u52D2\u5EFA\u7ACB\u7684\u540E\u8D75\u51E0\u4E4E\u4E00\u591C\u4E4B\u95F4\u4FBF\u8F70\u7136\u5012\u584C\u3002</p><p><strong>\u800C\u6B64\u65F6\u7684\u6155\u5BB9\u6C0F\u6B63\u597D\u5904\u4E8E\u5168\u76DB\u671F</strong>\uFF1A\u6155\u5BB9\u769D\u867D\u5DF2\u53BB\u4E16\uFF0C\u4F46\u6155\u5BB9\u5101\u4E5F\u582A\u79F0\u4E00\u4EE3\u82F1\u4E3B\uFF0C\u518D\u52A0\u4E0A\u6B64\u524D\u4E24\u4EE3\u4EBA\u7684\u79EF\u7D2F\uFF0C\u6155\u5BB9\u6C0F\u5982\u4ECA\u7EC8\u4E8E\u6709\u4E86\u8DB3\u591F\u7684\u52BF\u529B\u4E89\u9738\u5929\u4E0B\u3002</p><p>\u66F4\u4F55\u51B5\uFF0C\u6155\u5BB9\u5101\u6B63\u597D\u8FD8\u6709\u51E0\u4E2A\u597D\u5144\u5F1F\uFF1A\u6155\u5BB9\u606A\u3001\u6155\u5BB9\u5782\u3002\u5176\u4E2D\uFF0C<strong>\u6155\u5BB9\u606A\u66F4\u662F\u88AB\u79F0\u4F5C\u5341\u516D\u56FD\u65F6\u671F\u7B2C\u4E00\u540D\u5C06</strong>\uFF0C\u5176\u4E00\u6218\u64D2\u5189\u95F5\u5E76\u6740\u4E4B\uFF0C\u4E4B\u540E\u53C8\u9010\u6E10\u5C06\u540E\u8D75\u4E1C\u65B9\u7684\u9886\u571F\u7EB3\u5165\u7EDF\u6CBB\u4E4B\u4E0B\u3002</p><p><strong>\u897F\u5143352\u5E74\uFF0C<code>\u6155\u5BB9\u5101</code>\u4E8E\u90BA\u57CE\u79F0\u5E1D\uFF0C\u53F2\u79F0<code>\u524D\u71D5</code>\u3002</strong></p><p>\u8FD9\u4E00\u5E74\uFF0C\u662F\u664B\u7A46\u5E1D\u6C38\u548C\u516B\u5E74\u3002\u4E00\u5E74\u540E\uFF0C\u738B\u7FB2\u4E4B\u5728\u5C71\u9634\u5170\u4EAD\u5199\u4E0B\u4E86\u90A3\u5E45\u6D41\u4F20\u5343\u8F7D\u7684\u201C\u5929\u4E0B\u7B2C\u4E00\u884C\u4E66\u201D\u3002</p><p><img src="'+s+'" alt="demo" loading="lazy"></p>',66),n=[r];function g(l,a){return p(),e("div",null,n)}const k=o(d,[["render",g],["__file","22.html.vue"]]);export{k as default};
