import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as t,c as p,f as a}from"./app.913fb917.js";const c={},o=a('<h1 id="\u7B2C23\u96C6-\u5C0F\u5170\u82B1\u9752\u82CD\u5047\u626E\u592B\u59BB" tabindex="-1"><a class="header-anchor" href="#\u7B2C23\u96C6-\u5C0F\u5170\u82B1\u9752\u82CD\u5047\u626E\u592B\u59BB" aria-hidden="true">#</a> \u7B2C23\u96C6\uFF1A\u5C0F\u5170\u82B1\u9752\u82CD\u5047\u626E\u592B\u59BB</h1><p>\u7ED3\u9ECE\u548C\u6B87\u9619\u597D\u4E0D\u5BB9\u6613\u5C06\u5168\u57CE\u7684\u753B\u50CF\u6495\u4E86\uFF0C\u4E00\u56DE\u6765\u5C31\u8981\u542C\u7740\u4E1C\u65B9\u9752\u82CD\u548C\u5C0F\u5170\u82B1\u6597\u5634\u3002\u7ED3\u9ECE\u89C9\u5F97\u6709\u4E9B\u5C34\u5C2C\uFF0C\u8D76\u5FD9\u793A\u610F\u6B87\u9619\u529D\u529D\u4E1C\u65B9\u9752\u82CD\u3002\u53EF\u6CA1\u60F3\u5230\u6B87\u9619\u5374\u53CD\u8FC7\u6765\u8D23\u602A\u7ED3\u9ECE\uFF0C\u8BF4\u5979\u7684\u5FC3\u90FD\u653E\u5728\u66F2\u6C34\u8EAB\u4E0A\u4E86\u3002\u90A3\u8FB9\u8FD8\u6CA1\u6709\u5435\u5B8C\uFF0C\u8FD9\u8FB9\u53C8\u5F00\u59CB\u5435\u4E86\u3002\u4E1C\u65B9\u9752\u82CD\u548C\u5C0F\u5170\u82B1\u53EA\u89C9\u5F97\u5435\u95F9\u65E0\u6BD4\uFF0C\u5F02\u53E3\u540C\u58F0\u8BA9\u4E24\u4EBA\u522B\u5435\u4E86\u3002</p><p>\u601D\u6765\u60F3\u53BB\uFF0C\u7ED3\u9ECE\u60F3\u5230\u4E86\u4E00\u4E2A\u65B9\u6CD5\u3002\u5979\u6545\u610F\u7ED9\u5C0F\u5170\u82B1\u626E\u4E11\uFF0C\u5E0C\u671B\u8427\u6DA6\u770B\u5230\u540E\u80FD\u6B7B\u5FC3\u3002\u5C0F\u5170\u82B1\u770B\u7740\u955C\u5B50\u91CC\u7684\u81EA\u5DF1\uFF0C\u786E\u5B9E\u6709\u4E9B\u4E11\u3002\u53EF\u4E1C\u65B9\u9752\u82CD\u5374\u89C9\u5F97\u4E0D\u591F\uFF0C\u8BF4\u7740\u81EA\u5DF1\u5C31\u52A8\u8D77\u624B\u6765\uFF0C\u5728\u5C0F\u5170\u82B1\u8138\u4E0A\u4E00\u987F\u6298\u817E\u3002\u4E00\u756A\u64CD\u4F5C\u4E0B\u6765\uFF0C\u5C0F\u5170\u82B1\u7B80\u76F4\u88AB\u81EA\u5DF1\u4E11\u5230\u5FEB\u8981\u54ED\u51FA\u6765\u3002\u4E3A\u4E86\u8BA9\u8427\u6DA6\u5BF9\u5C0F\u5170\u82B1\u6620\u8C61\u5DEE\uFF0C\u4E1C\u65B9\u9752\u82CD\u8BA9\u5C0F\u5170\u82B1\u62D6\u5EF6\u89C1\u9762\u65F6\u95F4\uFF0C\u8FD8\u4EA4\u4EE3\u5C0F\u5170\u82B1\u8981\u5C3D\u91CF\u7C97\u4FD7\u3002</p><p>\u770B\u5230\u5C0F\u5170\u82B1\u8FD9\u6A21\u6837\uFF0C\u8427\u6DA6\u7ADF\u89C9\u5F97\u5979\u8FD9\u662F\u5728\u8003\u9A8C\u81EA\u5DF1\uFF0C\u8D76\u5FD9\u5938\u8D5E\u5979\u7684\u5986\u5BB9\u6E05\u65B0\u8131\u4FD7\u3002\u5C0F\u5170\u82B1\u4E00\u65F6\u8BED\u585E\uFF0C\u4E0D\u77E5\u63A5\u4E0B\u6765\u8BE5\u5982\u4F55\u6F14\u4E0B\u53BB\u3002\u542C\u5230\u8427\u6DA6\u8BF4\u8D77\u753B\u50CF\uFF0C\u5C0F\u5170\u82B1\u8D76\u5FD9\u987A\u7740\u4ED6\u7684\u8BDD\u832C\uFF0C\u8D23\u602A\u4ED6\u6CA1\u6709\u753B\u51FA\u81EA\u5DF1\u7684\u534A\u5206\u7F8E\u8C8C\u3002\u53EF\u8427\u6DA6\u542C\u5230\u8FD9\u8BDD\u4E0D\u4EC5\u4E0D\u751F\u6C14\uFF0C\u8FD8\u8981\u5411\u5C0F\u5170\u82B1\u627F\u8BFA\u65E5\u540E\u5B9A\u52A0\u5F3A\u7EC3\u4E60\u3002\u5C0F\u5170\u82B1\u65E0\u5948\uFF0C\u53EA\u597D\u62FF\u51FA\u6CFC\u5987\u7684\u6C14\u52BF\u6765\uFF0C\u731B\u5730\u5750\u5728\u5730\u4E0A\u634F\u811A\u3002\u8C01\u6599\u8427\u6DA6\u7ADF\u4E5F\u8131\u4E86\u978B\u5B50\uFF0C\u8E72\u5750\u5728\u5979\u8EAB\u65C1\u3002</p><p>\u8427\u6DA6\u5938\u8D5E\u5C0F\u5170\u82B1\u4E0E\u4F17\u4E0D\u540C\uFF0C\u5B9B\u82E5\u4E16\u5916\u5929\u4ED9\u3002\u8BF4\u7740\uFF0C\u8FD8\u7F13\u7F13\u9760\u8FD1\u5C0F\u5170\u82B1\uFF0C\u76F4\u8A00\u81EA\u5DF1\u4E0E\u5979\u6709\u5BBF\u4E16\u7684\u59FB\u7F18\u3002\u8C01\u6599\u8BDD\u97F3\u521A\u843D\uFF0C\u4E1C\u65B9\u9752\u82CD\u7684\u58F0\u97F3\u4FBF\u51FA\u73B0\u5728\u8EAB\u540E\u3002\u5C0F\u5170\u82B1\u6084\u58F0\u8BE2\u95EE\u4E1C\u65B9\u9752\u82CD\u600E\u4E48\u8DDF\u8FC7\u6765\u4E86\uFF0C\u4E0D\u6599\u4ED6\u7ADF\u7A81\u7136\u5728\u5979\u9762\u524D\u8E72\u4E0B\uFF0C\u6E29\u67D4\u5730\u66FF\u5979\u7A7F\u4E0A\u978B\uFF0C\u8FD8\u79F0\u5979\u4E3A\u5A18\u5B50\u3002\u5C0F\u5170\u82B1\u6709\u4E9B\u7591\u60D1\uFF0C\u4E5F\u6709\u4E9B\u607C\u6012\uFF0C\u53EF\u773C\u4E0B\u8BA9\u8427\u6DA6\u6B7B\u5FC3\u624D\u662F\u9996\u8981\uFF0C\u53EA\u597D\u8BA9\u4E1C\u65B9\u9752\u82CD\u66FF\u5979\u7A7F\u4E86\u978B\u3002</p><p>\u8427\u6DA6\u4ECE\u9707\u60CA\u4E4B\u4E2D\u62BD\u56DE\u795E\uFF0C\u8D28\u95EE\u4E1C\u65B9\u9752\u82CD\u8FD9\u662F\u4F55\u610F\u3002\u4E1C\u65B9\u9752\u82CD\u8138\u4E0D\u7EA2\u5FC3\u4E0D\u8DF3\u7684\u5411\u4ED6\u89E3\u91CA\uFF0C\u5C0F\u5170\u82B1\u4E0E\u81EA\u5DF1\u5DF2\u6210\u5A5A\u4E09\u5E74\uFF0C\u800C\u4E14\u5C0F\u5170\u82B1\u5DF2\u7ECF\u8EAB\u6000\u516D\u7532\u3002\u8BDD\u6BD5\uFF0C\u4E1C\u65B9\u9752\u82CD\u4FBF\u62C9\u7740\u5C0F\u5170\u82B1\u8FDC\u53BB\uFF0C\u4E34\u8D70\u8FD8\u4E0D\u5FD8\u4ECE\u5931\u9B42\u843D\u9B44\u7684\u8427\u6DA6\u90A3\u91CC\u8981\u56DE\u753B\u50CF\u3002</p><p>\u6708\u8272\u6C89\u8C27\uFF0C\u5C0F\u5170\u82B1\u548C\u4E1C\u65B9\u9752\u82CD\u4E00\u8D77\u53BB\u653E\u6CB3\u706F\u3002\u4E1C\u65B9\u9752\u82CD\u53EA\u5E0C\u671B\u51ED\u501F\u81EA\u5DF1\u7684\u529B\u91CF\u94F2\u5E73\u6C34\u4E91\u5929\uFF0C\u82CD\u76D0\u6D77\u7684\u5B50\u6C11\u4ECE\u6B64\u5BCC\u5EB6\u5B89\u5EB7\u3002\u800C\u5C0F\u5170\u82B1\u5374\u5E0C\u671B\u5FD8\u5DDD\u6CB3\u7554\u518D\u65E0\u7EB7\u4E89\uFF0C\u56E0\u4E3A\u5979\u6E05\u695A\uFF0C\u6218\u4E8B\u4E00\u8D77\uFF0C\u65E0\u8BBA\u662F\u4ED9\u65CF\u8FD8\u662F\u6708\u65CF\uFF0C\u90FD\u5C06\u9762\u4E34\u4E00\u573A\u6D69\u52AB\u3002\u4E24\u4EBA\u90FD\u77E5\u9053\u8FD9\u4E2A\u8BDD\u9898\u662F\u65E0\u89E3\u7684\uFF0C\u4FBF\u6CA1\u6709\u7EE7\u7EED\u4E0B\u53BB\u3002\u6CB3\u706F\u7FE9\u8DF9\u800C\u8D77\uFF0C\u98DE\u5411\u591C\u7A7A\uFF0C\u604D\u5982\u767D\u663C\u3002\u4E1C\u65B9\u9752\u82CD\u9ED8\u9ED8\u795D\u613F\u5C0F\u5170\u82B1\u80FD\u4E00\u76F4\u966A\u4F34\u81EA\u5DF1\uFF0C\u7136\u800C\u5C0F\u5170\u82B1\u5374\u6DF1\u77E5\u81EA\u5DF1\u65E0\u6CD5\u4F34\u4ED6\u5DE6\u53F3\uFF0C\u53EA\u5E0C\u671B\u4ED6\u4E00\u5207\u90FD\u597D\u3002</p><p>\u8C22\u60CB\u537F\u62FF\u51FA\u5341\u4F59\u5E74\u94B1\u8D22\u4E3A\u81EA\u5DF1\u8D4E\u8EAB\uFF0C\u4ECE\u6B64\u4EE5\u540E\u662F\u81EA\u7531\u8EAB\u7684\u5979\u53EA\u613F\u5BFB\u5F97\u826F\u7F18\u4E4B\u4EBA\u3002\u6B21\u65E5\uFF0C\u8427\u6DA6\u6765\u5230\u5E9C\u4E0A\uFF0C\u5411\u4E1C\u65B9\u9752\u82CD\u9053\u6B49\u5E76\u9053\u522B\u3002\u4ED6\u5766\u8A00\u81EA\u5DF1\u6B64\u751F\u53EA\u5FC3\u7CFB\u753B\u4E2D\u4ED9\u4E00\u4EBA\uFF0C\u4F46\u7EDD\u4E0D\u4F1A\u593A\u670B\u53CB\u4E4B\u7231\uFF0C\u7D22\u6027\u79BB\u5F00\u8FD9\u4F24\u5FC3\u5730\u3002\u53EF\u5982\u6B64\u4E00\u6765\uFF0C\u82B1\u671D\u8282\u7684\u5927\u5A5A\u5C82\u4E0D\u662F\u65E0\u6CD5\u8FDB\u884C\u3002\u4E1C\u65B9\u9752\u82CD\u8D76\u5FD9\u53EB\u4E0A\u5C0F\u5170\u82B1\uFF0C\u4E00\u8D77\u529D\u8BF4\u8427\u6DA6\u3002\u53EF\u6CA1\u60F3\u5230\u8427\u6DA6\u53BB\u610F\u5DF2\u51B3\uFF0C\u600E\u4E48\u4E5F\u4E0D\u80AF\u56DE\u5934\u3002\u60C5\u6025\u4E4B\u4E0B\uFF0C\u7ED3\u9ECE\u53EA\u597D\u79F0\u5C0F\u5170\u82B1\u662F\u4E1C\u65B9\u9752\u82CD\u7684\u59B9\u59B9\uFF0C\u6B64\u756A\u53EA\u662F\u4E3A\u4E86\u8003\u9A8C\u4ED6\u3002\u4E00\u65C1\u7684\u4E1C\u65B9\u9752\u82CD\u6C14\u5F97\u706B\u5192\u4E09\u4E08\uFF0C\u53EF\u4E3A\u4E86\u8D64\u5730\u5973\u5B50\u7684\u5143\u795E\uFF0C\u4ED6\u4E5F\u53EA\u80FD\u54AC\u7259\u627F\u8BA4\uFF0C\u5E76\u4EE5\u54E5\u54E5\u7684\u8EAB\u4EFD\u7B54\u5E94\u5C0F\u5170\u82B1\u548C\u8427\u6DA6\u7684\u5A5A\u4E8B\u3002</p><p>\u603B\u7B97\u662F\u529D\u56DE\u4E86\u8427\u6DA6\uFF0C\u53EF\u4ED6\u548C\u8C22\u60CB\u537F\u7684\u5A5A\u4E8B\u8BE5\u5982\u4F55\u8FDB\u884C\u3002\u5C31\u5728\u5927\u5BB6\u4E00\u7B79\u83AB\u5C55\u4E4B\u65F6\uFF0C\u7ED3\u9ECE\u63D0\u51FA\u4E86\u4E00\u4E2A\u4E24\u5168\u5176\u7F8E\u7684\u65B9\u6CD5\u3002\u773C\u4E0B\u65E0\u6CD5\u8BA9\u8C22\u60CB\u537F\u4E0E\u8427\u6DA6\u76F8\u7231\uFF0C\u4FBF\u53EA\u80FD\u5728\u5A5A\u793C\u90A3\u5929\u5077\u6881\u6362\u67F1\u3002\u5C0F\u5170\u82B1\u8D5E\u6210\u7ED3\u9ECE\u7684\u60F3\u6CD5\uFF0C\u53EF\u4E1C\u65B9\u9752\u82CD\u600E\u4E48\u4E5F\u4E0D\u80AF\u7B54\u5E94\u53BB\u5411\u8C22\u60CB\u537F\u63D0\u4EB2\u3002</p>',9),r=[o];function _(n,s){return t(),p("div",null,r)}const h=e(c,[["render",_],["__file","23.html.vue"]]);export{h as default};
