import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.eee000b7.js";const c={},o=a('<h1 id="\u7B2C23\u96C6-\u5468\u79C9\u6606\u4E8B\u4E1A\u6709\u6210\u4E70\u65B0\u623F" tabindex="-1"><a class="header-anchor" href="#\u7B2C23\u96C6-\u5468\u79C9\u6606\u4E8B\u4E1A\u6709\u6210\u4E70\u65B0\u623F" aria-hidden="true">#</a> \u7B2C23\u96C6\uFF1A\u5468\u79C9\u6606\u4E8B\u4E1A\u6709\u6210\u4E70\u65B0\u623F</h1><p>\u770B\u5230\u4E0D\u662F\u5468\u79C9\u6606\u5199\u6765\u7684\u4FE1\u5468\u5FD7\u521A\u5FC3\u91CC\u6709\u4E9B\u5931\u671B\uFF0C\u5F92\u5F1F\u529D\u8BF4\u5468\u5FD7\u521A\u4E3B\u52A8\u7ED9\u5468\u79C9\u6606\u5199\u4E00\u5C01\u4FE1\uFF0C\u53EF\u662F\u5468\u5FD7\u521A\u5374\u5014\u5F3A\u4E0D\u80AF\u5199\uFF0C\u4ED6\u4E5F\u60F3\u770B\u770B\u5468\u79C9\u6606\u4EC0\u4E48\u65F6\u5019\u80FD\u4E3B\u52A8\u7ED9\u4ED6\u5199\u4FE1\u3002</p><p>\u5B81\u513F\u770B\u7740\u5468\u79C9\u6606\u8BF4\u8BDD\u5934\u5934\u662F\u9053\u7684\u6837\u5B50\u5F88\u662F\u559C\u6B22\uFF0C\u8FD8\u4E3B\u52A8\u70ED\u60C5\u8868\u793A\u8981\u4E00\u76F4\u8DDF\u968F\u5468\u79C9\u6606\u7684\u811A\u6B65\uFF0C\u5468\u79C9\u6606\u53BB\u54EA\u91CC\u5979\u5C31\u53BB\u54EA\u91CC\uFF0C\u5434\u5029\u65C1\u8FB9\u770B\u7740\u5B81\u513F\u7684\u4E3E\u52A8\u5FC3\u4E2D\u6709\u4E9B\u4E0D\u8212\u670D\u3002</p><p>\u5468\u84C9\u56DE\u5230\u4E86\u8D35\u5DDE\uFF0C\u6765\u5230\u4E86\u9ED8\u9ED8\u7684\u5893\u5730\uFF0C\u60F3\u8D77\u90A3\u4E2A\u591C\u665A\uFF0C\u9ED8\u9ED8\u4E3A\u4E86\u5979\u548C\u72FC\u640F\u6597\uFF0C\u6700\u7EC8\u9ED8\u9ED8\u6B7B\u5728\u4E86\u72FC\u7684\u53E3\u4E2D\uFF0C\u800C\u5468\u84C9\u5F97\u4EE5\u6D3B\u547D\u3002</p><p>\u59DA\u7ACB\u677E\u6765\u627E\u5468\u79C9\u4E49\uFF0C\u8BC9\u8BF4\u8FD9\u4E9B\u5E74\u7684\u4E0D\u5BB9\u6613\uFF0C\u4ED6\u5DE5\u4F5C\u5162\u5162\u4E1A\u4E1A\u3002\u672C\u60F3\u8FD9\u8001\u5385\u957F\u5347\u804C\u4E4B\u540E\u4F1A\u63D0\u62D4\u4ED6\uFF0C\u4F46\u662F\u6CA1\u60F3\u5230\u8001\u5385\u957F\u53BB\u4E0A\u4EFB\u7684\u8DEF\u4E0A\u51FA\u8F66\u7978\u6B7B\u4E86\u3002\u59DA\u7ACB\u677E\u4E5F\u6CA1\u80FD\u5982\u613F\u7684\u5347\u804C\uFF0C\u59DA\u7ACB\u677E\u8BA4\u4E3A\u662F\u5468\u79C9\u4E49\u6210\u5168\u4E86\u5468\u79C9\u6606\uFF0C\u4F46\u5468\u79C9\u4E49\u5374\u544A\u8BC9\u59DA\u7ACB\u677E\u5468\u79C9\u6606\u4E0D\u662F\u4ED6\u6210\u5168\u7684\u3002\u59DA\u7ACB\u677E\u671F\u76FC\u7740\u90DD\u7701\u957F\u6D3B\u7740\uFF0C\u53EF\u80FD\u5F88\u591A\u4E8B\u90FD\u80FD\u89E3\u51B3\u4E86\uFF0C\u4F46\u5468\u79C9\u4E49\u77E5\u9053\u5982\u679C\u7701\u957F\u6D3B\u7740\uFF0C\u4ED6\u90FD\u4E0D\u80FD\u5728\u7701\u5385\u5DE5\u4F5C\u3002</p><p>\u90DD\u51AC\u6885\u548C\u5468\u79C9\u4E49\u804A\u5929\uFF0C\u8BF4\u8D77\u4E86\u81EA\u5DF1\u89E3\u5256\u7684\u4E8B\u60C5\uFF0C\u770B\u5230\u4E86\u88AB\u89E3\u5256\u7684\u4EBA\u4E00\u5207\u5185\u90E8\u5668\u5B98\uFF0C\u672C\u4EE5\u4E3A\u4ED6\u53EA\u662F\u4E00\u4E2A\u6559\u5177\uFF0C\u4F46\u662F\u5F53\u770B\u89C1\u6B7B\u8005\u624B\u6307\u7532\u7684\u6CE5\u571F\uFF0C\u624D\u77E5\u9053\u4ED6\u4E5F\u662F\u4E00\u4E2A\u548C\u5927\u5BB6\u4E00\u6837\u7684\u4EBA\u3002\u5468\u79C9\u4E49\u60F3\u8D77\u8FD9\u4E9B\u5E74\u5468\u79C9\u6606\u7684\u4ED8\u51FA\u548C\u8F9B\u82E6\uFF0C\u7A81\u7136\u6709\u4E9B\u5FC3\u75BC\u4ED6\u3002</p><p>\u5468\u84C9\u56DE\u5230\u5317\u4EAC\u4E4B\u540E\uFF0C\u53D1\u73B0\u4F4F\u5904\u4E71\u4E03\u516B\u7CDF\u7684\u90FD\u662F\u9152\u74F6\u5B50\uFF0C\u6C14\u5F97\u5468\u84C9\u548C\u51AF\u5316\u6210\u5927\u5435\u4E00\u67B6\u3002\u51AF\u5316\u6210\u89E3\u91CA\u662F\u56E0\u4E3A\u8FD9\u6B21\u7684\u9009\u62D4\u4ED6\u4EC0\u4E48\u4E5F\u6CA1\u6709\u5F53\u4E0A\uFF0C\u4E0D\u7BA1\u662F\u7406\u4E8B\u8FD8\u662F\u4F5C\u534F\u4E3B\u5E2D\u90FD\u548C\u81EA\u5DF1\u6CA1\u6709\u5173\u7CFB\uFF0C\u5468\u84C9\u8BA4\u4E3A\u5317\u4EAC\u4EBA\u624D\u592A\u591A\u4E86\uFF0C\u8FD8\u4E0D\u5982\u627E\u90B5\u656C\u6587\u5462\uFF0C\u542C\u5468\u79C9\u6606\u8BF4\u8FC7\u6742\u5FD7\u793E\u662F\u6C42\u8D24\u82E5\u6E34\uFF0C\u5C24\u5176\u559C\u6B22\u50CF\u662F\u51AF\u5316\u6210\u8FD9\u6837\u7684\u4EBA\u624D\u3002\u51AF\u5316\u6210\u867D\u7136\u4E0D\u820D\u5F97\u5317\u4EAC\u7684\u7E41\u534E\u548C\u91CD\u5FC3\u5730\u5E26\uFF0C\u4E5F\u6700\u7EC8\u8FD8\u662F\u7B54\u5E94\u4E86\u3002</p><p>\u5468\u79C9\u6606\u4E70\u4E86\u4E00\u5957\u5927\u623F\u5B50\uFF0C\u8BA9\u5927\u5BB6\u4E00\u8D77\u4F4F\u8FDB\u53BB\uFF0C\u90D1\u5A1F\u6FC0\u52A8\u6389\u4E0B\u773C\u6CEA\uFF0C\u4ECE\u6765\u6CA1\u60F3\u8FC7\u53EF\u4EE5\u4F4F\u8FD9\u4E48\u5927\u7684\u623F\u5B50\u3002\u8FD9\u91CC\u7684\u8BBE\u65BD\u4E00\u5E94\u4FF1\u5168\uFF0C\u90D1\u5A1F\u62C5\u5FC3\u4F4F\u4E0D\u957F\uFF0C\u5468\u79C9\u6606\u544A\u8BC9\u90D1\u5A1F\u5408\u540C\u5199\u660E\u4E86\u53EF\u4EE5\u4F4F\u4E00\u8F88\u5B50\uFF0C\u8FD9\u8BA9\u90D1\u5A1F\u66F4\u662F\u6FC0\u52A8\u3002\u5468\u6BCD\u4E5F\u6765\u5230\u4E86\u8FD9\u4E2A\u5927\u623F\u5B50\u91CC\uFF0C\u62C9\u7740\u5468\u79C9\u6606\u7684\u624B\u66F4\u662F\u6FC0\u52A8\uFF0C\u4ECE\u672A\u60F3\u8FC7\u513F\u5B50\u80FD\u8FD9\u4E48\u6709\u51FA\u606F\u3002</p><p>\u665A\u4E0A\uFF0C\u5468\u79C9\u6606\u8D77\u5E8A\u6328\u4E2A\u623F\u95F4\u91CC\u68C0\u67E5\u4E00\u4E0B\uFF0C\u751F\u6015\u6BCD\u4EB2\u7761\u4E0D\u4E60\u60EF\uFF0C\u770B\u7740\u6BCD\u4EB2\u7761\u7740\u7684\u6837\u5B50\u5468\u79C9\u6606\u8FD9\u624D\u653E\u5FC3\uFF0C\u5468\u79C9\u6606\u7ED9\u806A\u806A\u76D6\u597D\u88AB\u5B50\uFF0C\u806A\u806A\u8FD8\u61C2\u793C\u8C8C\u7684\u611F\u8C22\u7236\u4EB2\u7684\u8F9B\u82E6\u4ED8\u51FA\u3002</p><p>1988\u5E74\uFF0C\u7ECF\u6D4E\u98DE\u901F\u53D1\u5C55\uFF0C\u9A86\u58EB\u5BBE\u53C8\u4E0A\u4E86\u4E00\u4E2A\u53F0\u9636\u3002\u56DE\u5230\u5409\u6625\u7B2C\u4E00\u4EF6\u4E8B\u5C31\u662F\u53BB\u770B\u770B\u81EA\u5DF1\u7684\u513F\u5B50\uFF0C\u5B66\u6821\u95E8\u53E3\uFF0C\u6960\u6960\u9A91\u7740\u81EA\u884C\u8F66\u7B49\u7740\u73A5\u73A5\u51FA\u6765\u3002\u4E00\u4E2A\u5973\u5B69\u559C\u6B22\u6960\u6960\u5E0C\u671B\u80FD\u4E58\u5750\u6960\u6960\u7684\u81EA\u884C\u8F66\uFF0C\u53EF\u662F\u88AB\u6960\u6960\u62D2\u7EDD\u4E86\uFF0C\u5973\u5B69\u7FA1\u6155\u73A5\u73A5\u6709\u4E00\u4E2A\u597D\u54E5\u54E5\u3002</p><p>\u9A86\u58EB\u5BBE\u770B\u7740\u6960\u6960\u51FA\u843D\u5F97\u4E00\u8868\u4EBA\u624D\uFF0C\u4E5F\u6CA1\u6562\u4E0A\u524D\u6253\u62DB\u547C\uFF0C\u770B\u7740\u4E24\u4EBA\u9A91\u81EA\u884C\u8F66\u79BB\u5F00\u7684\u80CC\u5F71\uFF0C\u9A86\u58EB\u5BBE\u7684\u5634\u89D2\u9732\u51FA\u4E86\u7B11\u610F\u3002</p><p>\u73A5\u73A5\u770B\u5230\u5973\u5B69\u653E\u5728\u6960\u6960\u4E66\u5305\u91CC\u9762\u7684\u60C5\u4E66\uFF0C\u8FFD\u95EE\u6960\u6960\u5BF9\u5973\u5B69\u7684\u770B\u6CD5\uFF0C\u6960\u6960\u5374\u8868\u793A\u81EA\u5DF1\u5E76\u4E0D\u559C\u6B22\u90A3\u4E2A\u5973\u5B69\uFF0C\u5973\u5B69\u867D\u7136\u957F\u5F97\u5728\u73ED\u7EA7\u91CC\u7B97\u662F\u597D\u770B\u7684\uFF0C\u4F46\u662F\u6960\u6960\u5E76\u4E0D\u559C\u6B22\u3002\u73A5\u73A5\u8FFD\u95EE\u6960\u6960\u5973\u5B69\u6709\u6CA1\u6709\u5979\u6F02\u4EAE\uFF0C\u6960\u6960\u7B11\u8BDD\u73A5\u73A5\u8FD8\u4E0D\u5982\u4EBA\u5BB6\uFF0C\u6C14\u5F97\u73A5\u73A5\u6376\u6253\u6960\u6960\u3002</p><p>\u5468\u5FD7\u521A\u5FFD\u7136\u56DE\u6765\u4E86\uFF0C\u4F46\u662F\u5374\u53D1\u73B0\u5BB6\u91CC\u9762\u5DF2\u7ECF\u4F4F\u4E86\u56FD\u5E86\u548C\u5434\u5029\uFF0C\u56FD\u5E86\u6253\u7535\u8BDD\u544A\u8BC9\u5468\u79C9\u6606\u8FD9\u4EF6\u4E8B\uFF0C\u5468\u79C9\u6606\u521A\u8981\u8D76\u56DE\u53BB\u7684\u65F6\u5019\u9152\u5E97\u91CC\u5BA2\u4EBA\u5374\u6253\u8D77\u6765\u3002\u5468\u79C9\u6606\u5FD9\u7740\u5904\u7406\u5E97\u94FA\u7684\u4E8B\u60C5\u6CA1\u80FD\u53CA\u65F6\u8D76\u56DE\u53BB\uFF0C\u56FD\u5E86\u53EA\u597D\u6253\u7535\u8BDD\u7ED9\u4E86\u5468\u84C9\u548C\u5468\u79C9\u4E49\uFF0C\u5468\u84C9\u5C06\u5468\u5FD7\u521A\u5E26\u53BB\u4E86\u65B0\u623F\u5B50\u91CC\uFF0C\u5468\u79C9\u6606\u751F\u6D3B\u548C\u5DE5\u4F5C\u4E0A\u90FD\u53D1\u751F\u4E86\u8FD9\u4E48\u5927\u7684\u53D8\u5316\u8BA9\u5468\u5FD7\u521A\u5F88\u9707\u60CA\uFF0C\u7ADF\u7136\u6CA1\u6709\u4EBA\u544A\u8BC9\u4ED6\u4E00\u58F0\u3002</p><p>\u5468\u84C9\u89E3\u91CA\u5468\u79C9\u6606\u4E00\u5B9A\u662F\u60F3\u8981\u81EA\u5DF1\u544A\u8BC9\u7236\u4EB2\uFF0C\u4ED6\u4EEC\u591A\u5634\u7684\u8BDD\u5E76\u4E0D\u597D\uFF0C\u5468\u5FD7\u521A\u4E5F\u4E0D\u5728\u591A\u8BF4\u3002\u5F53\u5468\u79C9\u6606\u63E3\u7740\u5FD0\u5FD1\u7684\u5FC3\u60C5\u56DE\u5230\u5BB6\u91CC\u7684\u65F6\u5019\uFF0C\u5468\u5FD7\u521A\u8FD8\u6CA1\u6709\u7761\u89C9\uFF0C\u4E00\u76F4\u62BD\u7740\u70DF\u7B49\u7740\u5468\u79C9\u6606\uFF0C\u4ED6\u4E0D\u653E\u5FC3\u3002</p><p>\u542C\u5468\u5FD7\u521A\u8BF4\u4E0D\u653E\u5FC3\u81EA\u5DF1\uFF0C\u5468\u79C9\u6606\u843D\u6CEA\uFF0C\u4E3A\u4E86\u63A9\u9970\u773C\u6CEA\uFF0C\u5468\u79C9\u6606\u5FD9\u8EB2\u53BB\u4E86\u536B\u751F\u95F4\uFF0C\u5468\u5FD7\u521A\u4E5F\u8DDF\u7740\u7EA2\u4E86\u773C\u7736\u3002\u8C03\u6574\u4E86\u60C5\u7EEA\u4E4B\u540E\u5468\u79C9\u6606\u56DE\u53BB\u623F\u95F4\u91CC\uFF0C\u5468\u5FD7\u521A\u5DF2\u7ECF\u8EBA\u4E0B\u4E86\uFF0C\u5468\u79C9\u6606\u9ED8\u9ED8\u8EBA\u5728\u7236\u4EB2\u65C1\u8FB9\u3002\u5468\u5FD7\u521A\u5FC3\u75BC\u5468\u79C9\u6606\u6BCF\u5929\u56DE\u6765\u8FD9\u4E48\u665A\uFF0C\u5982\u679C\u5929\u5929\u8FD9\u4E48\u71AC\u4E0B\u53BB\u751F\u6015\u8EAB\u4F53\u53D7\u4E0D\u4E86\u3002\u5468\u79C9\u6606\u89E3\u91CA\u4ECA\u5929\u63A5\u5230\u7535\u8BDD\u5C31\u60F3\u8D76\u56DE\u6765\uFF0C\u53EF\u662F\u6709\u4EBA\u6253\u67B6\u8FD8\u60CA\u52A8\u4E86\u8B66\u5BDF\uFF0C\u6240\u4EE5\u5904\u7406\u7684\u6BD4\u8F83\u665A\u3002\u5468\u5FD7\u521A\u8D23\u602A\u5468\u79C9\u6606\u591A\u5E74\u6765\u4E0D\u5199\u4FE1\uFF0C\u5468\u79C9\u6606\u58F0\u79F0\u7236\u4EB2\u662F\u52BF\u5229\u773C\uFF0C\u6CA1\u6709\u51FA\u606F\u4E4B\u524D\u662F\u4E0D\u80FD\u5199\u4FE1\u7684\u3002</p><p>\u5468\u5FD7\u521A\u6C14\u5F97\u5C06\u706F\u6253\u5F00\uFF0C\u60F3\u8981\u770B\u7740\u5468\u79C9\u6606\u7684\u8138\u8BF4\u8BDD\uFF0C\u5468\u5FD7\u521A\u8BA4\u4E3A\u6500\u9AD8\u8E29\u5E95\u7684\u4EBA\u624D\u662F\u52BF\u5229\u773C\uFF0C\u800C\u4ED6\u6C38\u8FDC\u90FD\u4E0D\u662F\u8FD9\u79CD\u4EBA\u3002\u5468\u79C9\u6606\u8BA4\u4E3A\u5468\u79C9\u4E49\u548C\u5468\u84C9\u8003\u4E0A\u5317\u5927\u7684\u786E\u4ED6\u8138\u4E0A\u6709\u5149\uFF0C\u4ED6\u4E5F\u7684\u786E\u662F\u60F3\u8D81\u7740\u8FC7\u5E74\u5E26\u7740\u5B69\u5B50\u51FA\u53BB\u663E\u6446\u3002\u4F46\u662F\u4ED6\u505A\u9519\u552F\u4E00\u7684\u4E8B\u60C5\u5C31\u662F\u6CA1\u6709\u987E\u53CA\u5230\u5468\u79C9\u6606\u7684\u611F\u53D7\uFF0C\u8BA9\u5468\u79C9\u6606\u6CA1\u6709\u9762\u5B50\u4E86\u3002</p><p>\u5468\u79C9\u6606\u7A81\u7136\u843D\u6CEA\uFF0C\u8D28\u95EE\u7236\u4EB2\u662F\u5426\u8003\u8651\u8FC7\u4ED6\u7684\u611F\u53D7\uFF1F</p>',17),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","23.html.vue"]]);export{h as default};
