import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as t,c as p,f as a}from"./app.cb44c984.js";const c={},o=a('<h1 id="\u7B2C23\u96C6-\u5362\u51CC\u98CE\u82CF\u65E0\u540D\u5B81\u6E56\u76F8\u805A" tabindex="-1"><a class="header-anchor" href="#\u7B2C23\u96C6-\u5362\u51CC\u98CE\u82CF\u65E0\u540D\u5B81\u6E56\u76F8\u805A" aria-hidden="true">#</a> \u7B2C23\u96C6\uFF1A\u5362\u51CC\u98CE\u82CF\u65E0\u540D\u5B81\u6E56\u76F8\u805A</h1><p>\u82CF\u65E0\u540D\u548C\u88F4\u559C\u541B\u7B49\u4EBA\u529D\u8BF4\u8D39\u9E21\u5E08\u6212\u9152\uFF0C\u53EF\u8D39\u9E21\u5E08\u8FD8\u662F\u57CB\u6028\u82CF\u65E0\u540D\u6CA1\u8BA9\u81EA\u5DF1\u559D\u4E0A\u9F0D\u795E\u9152\u3002\u82CF\u65E0\u540D\u60F3\u8D77\u767D\u65E5\u6CA1\u6709\u95EE\u4ED4\u7EC6\uFF0C\u53C8\u4EB2\u81EA\u6765\u5230\u6797\u5BB6\u5BA2\u6808\uFF0C\u8BE2\u95EE\u5BA2\u6808\u638C\u67DC\u4E00\u4E9B\u8BE6\u60C5\uFF0C\u5F97\u77E5\u5F53\u521D\u638C\u67DC\u662F\u9694\u7740\u5E37\u5E54\u770B\u7684\u9F0D\u795E\uFF0C\u638C\u67DC\u8FD8\u63D0\u9192\u82CF\u65E0\u540D\u5728\u5B81\u6E56\u4E0D\u8981\u6000\u7591\u9F0D\u795E\u3002</p><p>\u6C88\u51B2\u5F97\u5230\u4E00\u90E8\u9F0D\u795E\u793E\u5B9E\u5F55\uFF0C\u662F\u957F\u53F2\u987E\u6587\u5F6C\u4EA4\u4E88\u795E\u793E\u7684\uFF0C\u8BF4\u662F\u5546\u4F1A\u9646\u548F\u6240\u732E\uFF0C\u91CC\u9762\u8BB0\u8F7D\u4E86\u9F0D\u795E\u793E\u7F6A\u884C\u3002\u6C88\u51B2\u62FF\u7740\u5B9E\u5F55\u6765\u5230\u9F0D\u795E\u5C9B\uFF0C\u5411\u9F0D\u795E\u7980\u544A\u6B64\u4E8B\uFF0C\u8868\u793A\u8FD9\u90E8\u5B9E\u5F55\u5E94\u8BE5\u540C\u51FA\u674E\u9E6C\u4E4B\u624B\u3002\u674E\u9E6C\u8FD8\u6709\u4E00\u4E2A\u5973\u513F\uFF0C\u4E07\u4E00\u624B\u4E0A\u8FD8\u6709\u9F0D\u795E\u793E\u5B9E\u5F55\uFF0C\u82E5\u5E26\u51FA\u5B81\u6E56\uFF0C\u540E\u679C\u4E0D\u582A\u8BBE\u60F3\uFF0C\u6B64\u4E8B\u9700\u8981\u5C3D\u5FEB\u5904\u7406\u3002\u6C88\u51B2\u8FD8\u544A\u77E5\u9F0D\u795E\u82CF\u65E0\u540D\u4E0D\u656C\u4E00\u4E8B\uFF0C\u9F0D\u795E\u77E5\u9053\u82CF\u65E0\u540D\u6781\u5176\u64C5\u957F\u67E5\u6848\uFF0C\u8BA9\u6C88\u51B2\u7B49\u4EBA\u8C28\u614E\u63D0\u9632\u3002</p><p>\u674E\u9E6C\u4E4B\u5973\u80ED\u8102\u906D\u9047\u9F0D\u795E\u793E\u523A\u6740\uFF0C\u5E78\u4E8F\u8D3A\u7280\u73B0\u8EAB\u624D\u5C06\u80ED\u8102\u5E26\u8D70\u3002\u8D3A\u7280\u77E5\u9053\u674E\u523A\u53F2\u4E4B\u6B7B\u4E0E\u9F0D\u795E\u793E\u4E0D\u65E0\u5173\u7CFB\uFF0C\u60F3\u901A\u8FC7\u80ED\u8102\u77E5\u9053\u674E\u523A\u53F2\u7684\u6B7B\u56E0\u3002\u53EF\u6A31\u6843\u5E76\u4E0D\u76F8\u4FE1\u8D3A\u7280\uFF0C\u6240\u4EE5\u5E76\u672A\u76F4\u8A00\u3002</p><p>\u957F\u53F2\u8BBE\u5BB4\u6B3E\u5F85\u82CF\u65E0\u540D\uFF0C\u82CF\u65E0\u540D\u8BE2\u95EE\u9F0D\u795E\u793E\u4EE3\u7406\u5DDE\u4E2D\u4E8B\u52A1\u4E00\u4E8B\uFF0C\u4F46\u957F\u53F2\u5E76\u6CA1\u6709\u591A\u52A0\u89E3\u91CA\uFF0C\u82CF\u65E0\u540D\u53EA\u597D\u544A\u8F9E\u79BB\u5F00\u524D\u6765\u7ED9\u674E\u523A\u53F2\u5B88\u7075\u3002\u80ED\u8102\u524D\u6765\u796D\u62DC\u7236\u4EB2\u674E\u9E6C\uFF0C\u53EF\u5374\u7A81\u7136\u88AB\u60CA\u73B0\u7684\u674E\u9E6C\u5413\u6655\uFF0C\u968F\u540E\u82CF\u65E0\u540D\u8D76\u6765\u5B88\u7075\uFF0C\u521A\u597D\u5C06\u80ED\u8102\u6551\u9192\u3002\u82CF\u65E0\u540D\u8868\u660E\u81EA\u5DF1\u5B81\u6E56\u53F8\u9A6C\u7684\u8EAB\u4EFD\uFF0C\u8868\u793A\u4F1A\u7763\u4FC3\u53F8\u6CD5\u53C2\u519B\u5C3D\u5FEB\u63A2\u7834\u6B64\u6848\u3002\u53EF\u80ED\u8102\u77E5\u9053\u5B81\u6E56\u5B98\u5458\u90FD\u548C\u9F0D\u795E\u793E\u6709\u5173\u8054\uFF0C\u5E76\u4E0D\u76F8\u4FE1\u6B64\u8A00\u3002\u4E24\u4EBA\u6B63\u8BF4\u7740\u8BDD\uFF0C\u5C31\u6709\u9F0D\u795E\u793E\u6210\u5458\u523A\u6740\u800C\u6765\uFF0C\u5F88\u5FEB\u80ED\u8102\u5C31\u88AB\u63A7\u5236\u4F4F\uFF0C\u523A\u5BA2\u8BA9\u80ED\u8102\u4EA4\u51FA\u9F0D\u795E\u793E\u5B9E\u5F55\uFF0C\u773C\u89C1\u80ED\u8102\u4E0D\u914D\u5408\u8FD8\u60F3\u4FAE\u8FB1\u5979\u5C31\u8303\uFF0C\u82CF\u65E0\u540D\u8D76\u6765\u963B\u6B62\u9669\u4E9B\u88AB\u6740\uFF0C\u5E78\u4E8F\u5362\u51CC\u98CE\u8D76\u6765\u76F8\u6551\uFF0C\u8FD8\u6210\u529F\u7559\u4E0B\u4E00\u4F4D\u4EBA\u8D28\uFF0C\u82CF\u65E0\u540D\u672C\u60F3\u501F\u4EBA\u8D28\u67E5\u95EE\u7EC6\u8282\uFF0C\u53EF\u80ED\u8102\u8F6C\u773C\u8FF7\u660F\u4E24\u4EBA\uFF0C\u5C06\u4EBA\u8D28\u6740\u5BB3\u3002</p><p>\u6B21\u65E5\u82CF\u65E0\u540D\u6E05\u9192\u540E\u611F\u8C22\u5362\u51CC\u98CE\u7684\u6551\u547D\u4E4B\u6069\uFF0C\u5362\u51CC\u98CE\u5E76\u672A\u8868\u793A\u662F\u56E0\u62C5\u5FC3\u4ED6\u4EEC\u5B89\u5371\u8FFD\u6765\u5B81\u6E56\uFF0C\u53EA\u8868\u793A\u662F\u524D\u6765\u63A2\u671B\u4ED6\u4EEC\u3002\u968F\u540E\u82CF\u65E0\u540D\u67E5\u9A8C\u674E\u523A\u53F2\u5C38\u4F53\uFF0C\u53D1\u73B0\u6B64\u4EBA\u5DE6\u624B\u7EC3\u5200\uFF0C\u4E14\u624B\u6709\u80CE\u8BB0\u3002\u6C88\u51B2\u5F97\u77E5\u884C\u52A8\u5931\u8D25\uFF0C\u975E\u5E38\u751F\u6C14\uFF0C\u547D\u4EBA\u7ACB\u9A6C\u53BB\u5546\u4F1A\u5C06\u9646\u548F\u6293\u6765\u3002</p><p>\u6C88\u51B2\u7ED1\u7740\u9646\u548F\u524D\u6765\u89C1\u987E\u523A\u53F2\uFF0C\u8868\u793A\u6B64\u4EBA\u662F\u523A\u6740\u674E\u523A\u53F2\u7684\u51F6\u624B\uFF0C\u800C\u9646\u548F\u4E5F\u4EB2\u53E3\u627F\u8BA4\u662F\u81EA\u5DF1\u6740\u4E86\u674E\u9E6C\u3002\u8FD8\u8D28\u95EE\u987E\u957F\u53F2\u81EA\u5DF1\u8D60\u4E0E\u7684\u9F0D\u795E\u793E\u5B9E\u5F55\u4F55\u5728\uFF0C\u4EE5\u4E3A\u987E\u957F\u53F2\u548C\u9F0D\u795E\u793E\u662F\u4E00\u4F19\u4EBA\uFF0C\u987E\u6587\u5F6C\u5426\u8BA4\u6B64\u4E8B\u3002\u968F\u540E\u8D3A\u7280\u8D76\u6765\u67E5\u95EE\u6B64\u4E8B\uFF0C\u8BA4\u4E3A\u9646\u548F\u4E0D\u53EF\u80FD\u662F\u6740\u4EBA\u51F6\u624B\uFF0C\u800C\u4E4B\u524D\u81EA\u5DF1\u8C03\u67E5\u6240\u5F97\uFF0C\u674E\u523A\u53F2\u751F\u524D\u5DF2\u7ECF\u52A0\u5165\u9F0D\u795E\u793E\u3002</p><p>\u82CF\u65E0\u540D\u6765\u72F1\u4E2D\u5BA1\u95EE\u9646\u548F\uFF0C\u4ECE\u4ED6\u53E3\u4E2D\u786E\u8BA4\u4E86\u674E\u523A\u53F2\u5C38\u4F53\u4E4B\u4EBA\u662F\u9646\u548F\u6D3E\u6765\u523A\u6740\u674E\u523A\u53F2\u7684\u5DE6\u624B\u5200\u5BA2\uFF0C\u800C\u5E76\u975E\u674E\u523A\u53F2\u672C\u4EBA\u3002\u82CF\u65E0\u540D\u544A\u77E5\u9646\u548F\u8981\u8868\u73B0\u51FA\u8BC9\u6C42\u4E00\u6B7B\u7684\u51B3\u5FC3\uFF0C\u56E0\u4E3A\u81EA\u5DF1\u53EF\u4EE5\u6551\u51FA\u4ED6\u7684\u6027\u547D\u3002\u82CF\u65E0\u540D\u5E26\u7740\u5362\u51CC\u98CE\u8D76\u5F80\u522B\u5885\uFF0C\u4ED6\u8BA4\u4E3A\u674E\u523A\u53F2\u672C\u5C0A\u8FD8\u5728\u6B64\u5730\uFF0C\u90A3\u665A\u80ED\u8102\u66FE\u8A00\u770B\u5230\u7236\u4EB2\uFF0C\u6BD5\u7ADF\u662F\u674E\u523A\u53F2\u672C\u4EBA\u3002\u4E24\u4EBA\u6765\u5230\u522B\u5885\uFF0C\u903C\u95EE\u54D1\u4EC6\u674E\u56DB\uFF0C\u53EF\u8F6C\u773C\u4E00\u5DE8\u9CC4\u5077\u88AD\u800C\u6765\uFF0C\u5362\u51CC\u98CE\u4E5F\u65E0\u6CD5\u5BF9\u6297\u5DE8\u9CC4\uFF0C\u5E78\u4E8F\u82CF\u65E0\u540D\u63D0\u9192\u4E00\u58F0\u97F3\u6270\u4E71\u5DE8\u9CC4\uFF0C\u4E24\u4EBA\u624D\u8D81\u673A\u9003\u8D70\u3002</p><p>\u8D39\u9E21\u5E08\u7ED9\u5362\u51CC\u98CE\u5305\u624E\u597D\u4F24\u53E3\uFF0C\u82CF\u65E0\u540D\u8868\u793A\u674E\u523A\u53F2\u8BA9\u523A\u5BA2\u4F2A\u88C5\u81EA\u5DF1\u53EF\u80FD\u662F\u56E0\u4E3A\u77E5\u9053\u81EA\u5DF1\u5904\u4E8E\u5DE8\u5927\u5371\u9669\u4E2D\uFF0C\u4ED6\u731C\u6D4B\u9F0D\u795E\u793E\u5B9E\u5F55\u5C31\u662F\u674E\u9E6C\u6240\u64B0\u5199\u3002\u5362\u51CC\u98CE\u9003\u907F\u88F4\u559C\u541B\u4E0D\u613F\u5165\u4F4F\u53F8\u9A6C\u5E9C\uFF0C\u82CF\u65E0\u540D\u8BA4\u4E3A\u6B64\u884C\u4E3A\u6781\u4E3A\u61E6\u5F31\uFF0C\u53EF\u5362\u51CC\u98CE\u5374\u89C9\u5F97\u81EA\u5DF1\u65E5\u540E\u6D41\u843D\u6C5F\u6E56\uFF0C\u88F4\u559C\u541B\u4E0D\u8BE5\u8DDF\u7740\u81EA\u5DF1\u53D7\u4E0D\u8BE5\u53D7\u7684\u82E6\u3002\u6B64\u8BDD\u88AB\u95E8\u5916\u7684\u5E0C\u541B\u542C\u5230\uFF0C\u88F4\u559C\u541B\u5F97\u77E5\u5362\u51CC\u98CE\u662F\u4E3A\u81EA\u5DF1\u8003\u8651\uFF0C\u8868\u793A\u81EA\u5DF1\u65E5\u540E\u7EDD\u4E0D\u7EA0\u7F20\u3002\u5362\u51CC\u98CE\u518D\u96BE\u79BB\u5F00\uFF0C\u7D22\u6027\u62E5\u62B1\u559C\u541B\uFF0C\u544A\u77E5\u4F17\u4EBA\u81EA\u5DF1\u786E\u5B9A\u5FC3\u610F\u4E0D\u518D\u9003\u907F\u3002</p>',9),r=[o];function _(n,s){return t(),p("div",null,r)}const h=e(c,[["render",_],["__file","23.html.vue"]]);export{h as default};
