import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as t,f as a}from"./app.dcc3c1cf.js";const c={},o=a('<h1 id="\u7B2C23\u96C6-\u8521\u5143\u57F9\u906D\u8BAD\u8BDD\u8D28\u8BE2-\u5317\u5927\u53D1\u8D77\u7EDD\u5730\u53CD\u51FB" tabindex="-1"><a class="header-anchor" href="#\u7B2C23\u96C6-\u8521\u5143\u57F9\u906D\u8BAD\u8BDD\u8D28\u8BE2-\u5317\u5927\u53D1\u8D77\u7EDD\u5730\u53CD\u51FB" aria-hidden="true">#</a> \u7B2C23\u96C6\uFF1A\u8521\u5143\u57F9\u906D\u8BAD\u8BDD\u8D28\u8BE2 \u5317\u5927\u53D1\u8D77\u7EDD\u5730\u53CD\u51FB</h1><p>\u4F17\u4EBA\u6B63\u5728\u8BA8\u8BBA\uFF0C\u4E00\u4EBA\u6C14\u5598\u5401\u5401\u5730\u51B2\u8FDB\u6765\uFF0C\u8BF4\u662F\u95E8\u5916\u6709\u4E00\u7FA4\u8B66\u5BDF\u6B63\u5728\u627E\u5BFB\u6613\u7FA4\u5148\u3002\u6613\u7FA4\u5148\u4E0D\u613F\u56DE\u5BB6\u8BF7\u6C42\u540C\u5B66\u4EEC\u5E2E\u5FD9\uFF0C\u5EF6\u5E74\u51B7\u9759\u6307\u6325\uFF0C\u8BA9\u4EBA\u5E26\u6613\u7FA4\u5148\u8DF3\u7A97\u51FA\u53BB\u5230\u6CD5\u6587\u8FDB\u4FEE\u9986\uFF0C\u5973\u751F\u56DE\u5BBF\u820D\u63A9\u62A4\uFF0C\u7537\u751F\u968F\u4ED6\u53BB\u5F00\u95E8\u3002\u8B66\u5BDF\u641C\u67E5\u65E0\u679C\u53EA\u597D\u7070\u6E9C\u6E9C\u7684\u79BB\u5F00\uFF0C\u6613\u7FA4\u5148\u6765\u5230\u6CD5\u6587\u8FDB\u4FEE\u9986\u7FFB\u5899\u800C\u5165\uFF0C\u603B\u7B97\u8EB2\u4E86\u8FC7\u53BB\u3002</p><p>\u6B21\u65E5\uFF0C\u540C\u5B66\u4EEC\u4E00\u8D77\u627E\u7740\u90A3\u4E2A\u8BB0\u8005\u8E29\u4EBA\uFF0C\u770B\u7740\u67F3\u7709\u7684\u6307\u8BA4\u90ED\u5FC3\u521A\u8FD9\u624D\u53D1\u73B0\u8FD9\u8BB0\u8005\u5C31\u662F\u5F20\u4E30\u8F7D\u3002\u4F17\u4EBA\u89C1\u673A\u884C\u4E8B\u62E6\u4F4F\u5F20\u4E30\u8F7D\uFF0C\u67F3\u7709\u4F6F\u88C5\u548C\u6613\u7FA4\u5148\u5435\u67B6\uFF0C\u628A\u5F20\u4E30\u8F7D\u7EA6\u51FA\u6765\u8BF4\u7740\u81EA\u5DF1\u5BF9\u9648\u5EF6\u5E74\u5DF2\u6DF1\u6076\u75DB\u7EDD\uFF0C\u5FC5\u8981\u5C06\u9648\u72EC\u79C0\u7236\u5B50\u7684\u4E11\u4E8B\u5168\u90E8\u7206\u6599\u51FA\u6765\u3002\u5F20\u4E30\u8F7D\u542C\u5230\u8FD9\u91CC\u4EE5\u4E3A\u67F3\u7709\u5DF2\u540C\u4E92\u52A9\u793E\u79BB\u5FC3\uFF0C\u9887\u4E3A\u81EA\u8C6A\u7684\u5C06\u4ED6\u4EEC\u8054\u5408\u6613\u5914\u9F99\uFF0C\u627E\u8BB0\u8005\u5199\u8BCB\u6BC1\u9648\u72EC\u79C0\u6587\u7AE0\u7684\u4E8B\u60C5\u4E00\u4E00\u8BF4\u51FA\uFF0C\u604D\u7136\u5927\u609F\u5730\u67F3\u7709\u5185\u5FC3\u5145\u6EE1\u4E86\u6124\u6012\u3002</p><p>\u94B1\u7384\u540C\u53C8\u5C06\u4E00\u5927\u5806\u7684\u7A3F\u5B50\u9001\u6765\u65B0\u9752\u5E74\u7684\u7F16\u8F91\u90E8\uFF0C\u4F17\u7F16\u8F91\u4E0D\u505C\u7684\u5BA1\u6838\u6E90\u6E90\u4E0D\u65AD\u9001\u6765\u7684\u6587\u7AE0\uFF0C\u53EF\u662F\u5FD9\u7684\u4E0D\u53EF\u5F00\u4EA4\u3002\u8FD9\u8FB9\u6559\u80B2\u603B\u957F\u5085\u589E\u6E58\u53C8\u5C06\u8521\u5143\u57F9\u8BF7\u4E86\u8FC7\u53BB\uFF0C\u4ED6\u867D\u7136\u652F\u6301\u65B0\u6587\u5316\u8FD0\u52A8\u4F46\u73B0\u5728\u5404\u65B9\u538B\u529B\u5B9E\u5728\u592A\u5927\uFF0C\u4F46\u8521\u5143\u57F9\u8BA4\u4E3A\u8FD9\u662F\u539F\u5219\u548C\u516C\u7406\uFF0C\u4E0D\u662F\u80FD\u591F\u59A5\u534F\u7684\u4E8B\u60C5\u3002\u5F20\u957F\u793C\u62FF\u7740\u5317\u5927\u7684\u6742\u5FD7\u5230\u5904\u544A\u72B6\uFF0C\u8981\u5F39\u52BE\u5085\u589E\u6E58\u548C\u8521\u5143\u57F9\u3002\u6850\u57CE\u6D3E\u7684\u8BF8\u591A\u5B66\u8005\u5728\u6797\u7EBE\u7684\u5E26\u9886\u4E0B\u7EB7\u7EB7\u5C06\u77DB\u5934\u5BF9\u51C6\u4E86\u8521\u5143\u57F9\uFF0C\u5434\u70B3\u6E58\u53D1\u6765\u7684\u76D1\u63A7\u540D\u5355\u4E2D\u4E5F\u5199\u7740\u8521\u5143\u57F9\u548C\u9648\u72EC\u79C0\u7684\u540D\u5B57\uFF0C\u8521\u516C\u73B0\u5728\u662F\u8179\u80CC\u53D7\u654C\u3002\u4F46\u8521\u5143\u57F9\u6DE1\u7136\u8D77\u8EAB\uFF0C\u5BF9\u5085\u589E\u6E58\u8BF4\u7684\u8FD9\u4E9B\u4ED6\u90FD\u5DF2\u6E05\u695A\uFF0C\u4ED6\u8868\u793A\u81EA\u5DF1\u4E0D\u662F\u86EE\u725B\u4E4B\u4EBA\uFF0C\u80FD\u901A\u878D\u7684\u5730\u65B9\u4ED6\u5B9A\u4F1A\u901A\u878D\uFF0C\u968F\u5373\u544A\u8F9E\u79BB\u5F00\u3002</p><p>\u56DE\u53BB\u7684\u8DEF\u4E0A\u8521\u5143\u57F9\u5FC3\u4E2D\u82E6\u95F7\uFF0C\u4E0B\u4E86\u8F66\u968F\u610F\u4E70\u4E86\u4E00\u4EFD\u300A\u516C\u8A00\u62A5\u300B\uFF0C\u5374\u770B\u5230\u4E86\u6797\u7EBE\u5199\u4E0B\u7684\u81F4\u4E8E\u4ED6\u7684\u516C\u5F00\u4FE1\u3002\u6797\u7EBE\u5C06\u8BF8\u591A\u540C\u4EC1\u53EB\u6765\u5BB6\u4E2D\uFF0C\u5BF9\u81EA\u5DF1\u5199\u7ED9\u8521\u5143\u57F9\u7684\u8FD9\u5C01\u4FE1\u9887\u4E3A\u5F97\u610F\u7684\u8FDB\u884C\u8BB2\u89E3\u3002\u7EA2\u697C\u5185\u674E\u5927\u948A\u542C\u7740\u5BF9\u5DE5\u8BFB\u4E92\u52A9\u4F1A\u4E0D\u5B9E\u62A5\u9053\u7684\u4E8B\u60C5\uFF0C\u5546\u8BA8\u7740\u89E3\u51B3\u529E\u6CD5\u3002\u5EF6\u5E74\u51B3\u5B9A\u5148\u8BF4\u670D\u6613\u5914\u9F99\uFF0C\u5E76\u627E\u5230\u5F20\u4E30\u8F7D\u6076\u610F\u8BCB\u6BC1\u7684\u8BC1\u636E\u518D\u884C\u53CD\u51FB\u3002\u6613\u7FA4\u5148\u8868\u793A\u7AE0\u58EB\u948A\u66FE\u662F\u7236\u4EB2\u6613\u5914\u9F99\u7684\u4E0A\u53F8\uFF0C\u4ED6\u82E5\u80FD\u51FA\u9762\u4FBF\u80FD\u8BF4\u670D\u6210\u529F\u3002\u8FD9\u65F6\uFF0C\u8521\u5143\u57F9\u6D3E\u4EBA\u6765\u5C06\u674E\u5927\u948A\u53EB\u8D70\uFF0C\u5F88\u662F\u7D27\u6025\u3002</p><p>\u8521\u5143\u57F9\u5C06\u6797\u7EBE\u5728\u62A5\u7EB8\u4E0A\u53D1\u8868\u7684\u5C0F\u8BF4\u300A\u5996\u68A6\u300B\u62FF\u7ED9\u4ED6\u4EEC\u770B\uFF0C\u9C81\u8FC5\u5C06\u5C0F\u8BF4\u4E2D\u7684\u4EBA\u7269\u6545\u4E8B\u4E00\u4E00\u89E3\u8BFB\uFF0C\u8FD9\u660E\u6446\u7740\u5C31\u662F\u8BBD\u523A\u8521\u5143\u57F9\u548C\u4ED6\u4EEC\u8FD9\u4E9B\u65B0\u6587\u5316\u7684\u5021\u5BFC\u8005\u662F\u81ED\u4E0D\u53EF\u95FB\u7684\u7CAA\u571F\u3002\u5B89\u798F\u7CFB\u653F\u5BA2\u4E5F\u5251\u6307\u5317\u5927\uFF0C\u5BF9\u9648\u72EC\u79C0\u3001\u94B1\u7384\u540C\u7B49\u4EBA\u8FDB\u884C\u70B9\u540D\u9053\u59D3\u7684\u6307\u8D23\uFF0C\u5E76\u8981\u6C42\u5C06\u4ED6\u4EEC\u9769\u804C\u3002\u6811\u6B32\u9759\u800C\u98CE\u4E0D\u6B62\uFF0C\u8521\u5143\u57F9\u5DF2\u7ECF\u5982\u6B64\u5FCD\u8BA9\u4ED6\u4EEC\u4ECD\u662F\u6B3A\u4EBA\u592A\u751A\uFF0C\u8521\u516C\u51B3\u5FC3\u62AB\u575A\u6267\u9510\uFF0C\u4E0A\u9635\u4E0E\u4ED6\u4EEC\u5C55\u5F00\u6B63\u9762\u53AE\u6740\u3002</p><p>\u9C81\u8FC5\u5728\u5317\u5927\u793C\u5802\u5185\u5BF9\u6797\u7EBE\u5199\u7684\u5C0F\u8BF4\u8FDB\u884C\u516C\u5F00\u7684\u6279\u5224\uFF0C\u5F15\u5F97\u5B66\u751F\u4EEC\u7EB7\u7EB7\u9F13\u638C\u53EB\u597D\u3002\u674E\u5927\u948A\u8D70\u4E0A\u8857\u5934\uFF0C\u5BF9\u7740\u7FA4\u4F17\u8FDB\u884C\u516C\u5F00\u6F14\u8BB2\uFF0C\u75DB\u65A5\u65E7\u52BF\u529B\u7684\u865A\u4F2A\uFF0C\u9AD8\u547C\u65B0\u6587\u5316\u8FD0\u52A8\u4E07\u5C81\u3002\u6BDB\u6CFD\u4E1C\u4E5F\u767B\u4E0A\u6F14\u8BB2\u53F0\uFF0C\u4ED6\u547C\u5401\u7740\u7FA4\u4F17\u4EEC\u8981\u8FFD\u6C42\u81EA\u7531\u548C\u89E3\u653E\uFF0C\u4ED6\u575A\u4FE1\u603B\u6709\u4E00\u5929\u4E2D\u534E\u6C11\u65CF\u7684\u6539\u9769\u4F1A\u6BD4\u4EFB\u4F55\u6C11\u65CF\u90FD\u5F7B\u5E95\uFF0C\u4E2D\u56FD\u5B9A\u4F1A\u8FCE\u6765\u5149\u8F89\u7684\u4E00\u5929\u3002</p><p>\u6797\u7EBE\u5BF9\u5F20\u4E30\u8F7D\u6CA1\u6709\u8FFD\u56DE\u300A\u5996\u68A6\u300B\u4E00\u6587\u7684\u884C\u5F84\u6DF1\u6076\u75DB\u7EDD\uFF0C\u4ED6\u5927\u9A82\u5F20\u4E30\u8F7D\u5C31\u662F\u4E00\u4E2A\u9633\u5949\u9634\u8FDD\u3001\u9053\u8C8C\u5CB8\u7136\u7684\u5C0F\u4EBA\uFF0C\u5F20\u4E30\u8F7D\u88C5\u6A21\u4F5C\u6837\u5730\u8DEA\u5012\u5728\u5730\uFF0C\u5F20\u957F\u793C\u5374\u529D\u8BF4\u6495\u7834\u8138\u4E5F\u597D\uFF0C\u53CD\u6B63\u4E0B\u5468\u4ED6\u4EEC\u5C31\u8981\u5728\u56FD\u4F1A\u4E0A\u516C\u5802\u5BF9\u8D28\u3002\u6797\u7EBE\u7EC8\u4E8E\u660E\u767D\u6587\u4EBA\u548C\u653F\u5BA2\u9053\u4E0D\u540C\u4E0D\u76F8\u4E3A\u8C0B\uFF0C\u4ED6\u8F70\u8D70\u5F20\u957F\u793C\uFF0C\u54ED\u7740\u8BF4\u9053\u518D\u4E5F\u4E0D\u4F1A\u4E0E\u4ED6\u4EEC\u76F8\u89C1\u3002</p><p>\u4E92\u52A9\u4F1A\u7684\u5B66\u751F\u53EC\u5F00\u4F1A\u8BAE\u5B89\u6392\u89E3\u51B3\u8D1F\u9762\u62A5\u9053\u7684\u95EE\u9898\uFF0C\u4ED6\u4EEC\u5175\u5206\u4E09\u8DEF\uFF0C\u5206\u522B\u4ECE\u529D\u89E3\u6613\u5914\u9F99\u3001\u63ED\u9732\u548C\u627E\u5BFB\u65E5\u672C\u8BB0\u8005\u62A5\u9053\u5F20\u4E30\u8F7D\u4E11\u884C\u4EE5\u53CA\u6536\u96C6\u6574\u7406\u8BC1\u636E\u6750\u6599\u4E09\u65B9\u9762\u8FDB\u884C\u53CD\u51FB\u3002\u5F20\u4E30\u8F7D\u5411\u5F20\u957F\u793C\u8BF4\u7740\u81EA\u5DF1\u5185\u5FC3\u7684\u4E0D\u5B89\uFF0C\u4ED6\u5199\u7ED9\u8521\u5143\u57F9\u7684\u4FE1\u662F\u6709\u53BB\u65E0\u56DE\uFF0C\u4ED6\u5C31\u671F\u5F85\u7740\u8521\u5143\u57F9\u53D1\u706B\u597D\u8BA9\u4ED6\u7684\u90A3\u5957\u8A00\u8BBA\u4E0D\u653B\u81EA\u7834\uFF0C\u5374\u6CA1\u80FD\u5F97\u901E\u3002\u5F20\u957F\u793C\u8BA9\u5F20\u4E30\u8F7D\u6BD5\u4E1A\u540E\u53BB\u8B66\u5BDF\u5385\u5DE5\u4F5C\uFF0C\u4F46\u5F20\u4E30\u8F7D\u7ADF\u4ECD\u65E7\u60F3\u8981\u7559\u5728\u5317\u5927\u3002\u5F20\u957F\u793C\u6BEB\u4E0D\u5BA2\u6C14\u5730\u8BF4\u4ED6\u662F\u5047\u9053\u5B66\uFF0C\u6D41\u6C13\u8EAB\u4E0A\u62AB\u7740\u4E2A\u6587\u4EBA\u7684\u5916\u8863\uFF0C\u8FD9\u53EF\u8BA9\u5F20\u4E30\u8F7D\u542C\u7740\u4E0D\u9AD8\u5174\uFF0C\u81EA\u5DF1\u73B0\u5728\u8FD9\u6837\u8FD8\u4E0D\u5168\u662F\u4ED6\u6559\u7684\u3002</p><p>\u5F20\u957F\u793C\u540E\u5929\u5C31\u4F1A\u53BB\u56FD\u4F1A\u8FDB\u884C\u63D0\u6848\u7684\u8FA9\u8BBA\uFF0C\u4ED6\u6253\u7740\u5982\u610F\u7B97\u76D8\u60F3\u62FF\u6613\u5914\u9F99\u5F53\u67AA\u4F7F\uFF0C\u81EA\u5DF1\u8EB2\u5728\u540E\u9762\u505A\u58C1\u4E0A\u89C2\u3002\u4F46\u4ED6\u4E5F\u6DF1\u77E5\u60F3\u8981\u501F\u6B64\u5C06\u8521\u5143\u57F9\u62C9\u4E0B\u53F0\u6839\u672C\u4E0D\u53EF\u80FD\uFF0C\u4ED6\u641E\u8FD9\u4E48\u4E00\u51FA\u95F9\u5267\u5C31\u662F\u4E3A\u4E86\u7ED9\u8521\u5143\u57F9\u7B49\u4EBA\u4E00\u4E2A\u4E0B\u9A6C\u5A01\uFF0C\u5E76\u7ED9\u6C11\u56FD\u653F\u5E9C\u65BD\u52A0\u538B\u529B\u3002\u540C\u65F6\uFF0C\u4ED6\u5B89\u6392\u5F20\u4E30\u8F7D\u4E00\u5B9A\u8981\u53EC\u96C6\u5927\u6279\u8BB0\u8005\uFF0C\u53EA\u6709\u901A\u8FC7\u8206\u8BBA\u4ED6\u4EEC\u624D\u80FD\u9020\u8D77\u66F4\u5927\u7684\u58F0\u52BF\u3002</p>',10),r=[o];function _(n,s){return p(),t("div",null,r)}const h=e(c,[["render",_],["__file","23.html.vue"]]);export{h as default};
