import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as t,f as s}from"./app.ecedbf49.js";const e="/myNotes/assets/img/base/640(11).jpeg",r={},n=s('<h1 id="\u9C9C\u5351\u6155\u5BB9\u4F20\u5947-\u7B2C\u4E8C\u7BC7" tabindex="-1"><a class="header-anchor" href="#\u9C9C\u5351\u6155\u5BB9\u4F20\u5947-\u7B2C\u4E8C\u7BC7" aria-hidden="true">#</a> \u9C9C\u5351\u6155\u5BB9\u4F20\u5947 \u7B2C\u4E8C\u7BC7</h1><blockquote><p>\uFF08\u5347\u5E73\u56DB\u5E74\uFF09\u6625\uFF0C\u6B63\u6708\uFF0C\u7678\u5DF3\uFF0C\u71D5\u4E3B\u5101\u2026\u2026\u4F1A\u75BE\u7B03\uFF0C\u4E43\u53EC\u606A\u3001\u9A9B\u53CA\u53F8\u5F92\u8BC4\u3001\u9886\u519B\u5C06\u519B\u6155\u8206\u6839\u7B49\u53D7\u9057\u8BCF\u8F85\u653F\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274 \u2022 \u5377\u4E00\u96F6\u4E00 \u2022 \u664B\u7EAA\u5EFF\u4E09\u300B</strong></p><p>\u524D\u71D5\u4ECE\u521B\u7ACB\u8D70\u5411\u9F0E\u76DB\uFF0C\u53C8\u7531\u76DB\u8F6C\u8870\uFF0C\u671F\u95F4\u53D1\u751F\u7684\u4E00\u7CFB\u5217\u4E8B\u4EF6\uFF0C\u90FD\u4E0E\u4E00\u4E2A\u4EBA\u7D27\u5BC6\u76F8\u5173\u2014\u2014<code>\u6155\u5BB9\u606A</code>\u3002</p><p>\u5982\u679C\u5BF9\u4E1C\u664B\u5341\u516D\u56FD\u4EE5\u53CA\u5357\u5317\u671D\u65F6\u671F\u7684\u4EBA\u7269\u505A\u4E00\u4E2A\u6392\u5E8F\uFF0C\u90A3\u4E48<strong>\u6155\u5BB9\u606A\u4E00\u5B9A\u5728\u7B2C\u4E00\u6D41\u4EBA\u7269\u4E4B\u5217</strong>\u3002\u5C31\u7B97\u66F4\u8FDB\u4E00\u6B65\uFF0C\u5BF9\u8FD9\u4E9B\u4E00\u6D41\u4EBA\u7269\u518D\u505A\u6392\u5E8F\uFF0C\u6155\u5BB9\u606A\u4E5F\u4F9D\u65E7\u80FD\u6392\u8FDB\u524D\u5217\u3002</p><p>\u6155\u5BB9\u606A\u7684\u80FD\u529B\u4E4B\u5F3A\uFF0C\u9996\u5148\u5728\u4E8E<strong>\u519B\u4E8B\u80FD\u529B</strong>\uFF0C\u5176\u4E00\u751F\u524D\u540E\u5341\u6570\u6B21\u51FA\u5F81\uFF0C\u51E0\u4E4E\u4ECE\u65E0\u8D25\u7EE9\uFF0C\u88AB\u79F0\u4F5C\u201C<strong>\u5341\u516D\u56FD\u65F6\u671F\u7B2C\u4E00\u540D\u5C06</strong>\u201D\u3002</p><p>\u540C\u65F6\uFF0C\u5728<strong>\u56FD\u653F\u6CBB\u7406\u3001\u51DD\u805A\u4EBA\u5FC3\uFF0C\u751A\u81F3\u4E8E\u4EBA\u683C\u4FEE\u517B\u4E0A\uFF0C\u6155\u5BB9\u606A\u4E5F\u5EFA\u6811\u4E0D\u4FD7</strong>\u3002\u4ED6\u5728\u6155\u5BB9\u4FCA\u6B7B\u540E\u53D7\u547D\u8F85\u653F\uFF0C\u5728\u4E16\u671F\u95F4\u56FD\u5185\u653F\u6CBB\u6E05\u660E\uFF0C\u5BF9\u5916\u5C61\u6218\u5C61\u80DC\uFF0C\u6155\u5BB9\u6C0F\u7684\u57FA\u4E1A\u4E00\u65F6\u84B8\u84B8\u65E5\u4E0A\u3002</p><p>\u53EA\u662F\u5929\u4E0D\u5047\u5E74\uFF0C\u6155\u5BB9\u606A\u6700\u5927\u7684\u7F3A\u70B9\u548C\u95EE\u9898\u5728\u4E8E<strong>\u5E74\u5BFF\u4E0D\u6C38</strong>\uFF0C\u53BB\u4E16\u65F6\u5E74\u4EC547\u5C81\u3002\u81EA\u6B64\uFF0C\u6155\u5BB9\u6C0F\u7684\u57FA\u4E1A\u6025\u8F6C\u76F4\u4E0B\uFF0C\u4E0D\u8FC7\u624D\u5230\u4ED6\u53BB\u4E16\u7684\u7B2C\u4E09\u5E74\uFF0C\u53D1\u8F6B\u4E8E\u6155\u5BB9\u5EC6\u3001\u5F00\u521B\u4E8E\u6155\u5BB9\u769D\u7684\u5927\u71D5\u5E1D\u56FD\u5373\u88AB\u6C10\u4EBA\u653F\u6743\u2014\u2014\u79E6\u6240\u6D88\u706D\u3002</p><p><img src="'+e+'" alt="demo" loading="lazy"></p><p>\u5148\u7B80\u8981\u56DE\u987E\u4E00\u4E0B\u6155\u5BB9\u606A\u7684\u751F\u5E73\u3002</p><p>\u524D\u71D5\u5F00\u56FD\u4E4B\u541B<code>\u6155\u5BB9\u5101</code>\u53BB\u4E16\u65F6\uFF0C\u9057\u547D\u6155\u5BB9\u606A\u8F85\u653F\u3002\u6B64\u65F6\u6155\u5BB9\u606A\u53EA\u670940\u5C81\uFF0C\u4F46\u5DF2\u662F\u524D\u71D5\u671D\u91CE\u5185\u5916\u516C\u8BA4\u7684\u8F85\u5F3C\u4E4B\u81E3\u2014\u2014\u4E3B\u8981\uFF0C\u8FD8\u662F\u56E0\u4E3A\u4ED6\u5728\u6218\u573A\u4E0A\u53D6\u5F97\u7684\u6210\u7EE9\u5341\u5206\u8000\u773C\u3002</p><p>\u6155\u5BB9\u606A15\u5C81\u5373\u5DF2\u4ECE\u519B\u300218\u5C81\u65F6\uFF0C\u540E\u8D75\u7687\u5E1D\u4EB2\u81EA\u5E26\u5175\u56F4\u527F\u6155\u5BB9\u90E8\uFF0C\u53CC\u65B9\u76F8\u6301\u5341\u4F59\u65E5\uFF0C\u4E4B\u540E\u540E\u8D75\u519B\u961F\u64A4\u8D70\u3002\u6155\u5BB9\u606A\u5E26\u98862000\u9A91\u5175\u51FA\u57CE\u8FFD\u6740\uFF0C\u5927\u8D25\u8D75\u519B\uFF0C\u4E4B\u540E\u53C8\u4E58\u80DC\u8FFD\u51FB\uFF0C\u65A9\u9996\u4E09\u4E07\u4F59\u4EBA\u3002</p><p>\u540C\u5E74\u5341\u6708\uFF0C\u540E\u8D75\u4E0E\u6155\u5BB9\u90E8\u518D\u6B21\u5F00\u6218\uFF0C\u6155\u5BB9\u606A\u53C8\u4E00\u6B21\u51FB\u6E83\u8D75\u519B\uFF0C\u65A9\u9996\u4E07\u4F59\u4EBA\u3002</p><p>21\u5C81\u65F6\uFF0C\u6155\u5BB9\u606A\u9547\u5B88\u8FBD\u4E1C\uFF0C\u4ECE\u6B64\u9AD8\u53E5\u4E3D\u518D\u4E0D\u6562\u4FB5\u72AF\u524D\u71D5\u8FB9\u5883\u3002</p><p>24\u5C81\uFF0C\u53C2\u4E0E\u6155\u5BB9\u90E8\u4E0E\u5B87\u6587\u90E8\u7684\u51B3\u6218\u3002\u5B87\u6587\u90E8\u5927\u8D25\uFF0C\u4ECE\u6B64\u6563\u4EA1\u3002</p><p>25\u5C81\uFF0C\u4E3A\u5148\u950B\u8FDB\u653B\u9AD8\u53E5\u4E3D\uFF0C\u4FD8\u864F\u5176\u56FD\u738B\u800C\u8FD4\u3002</p><p>\u5728\u8FD9\u4F17\u591A\u7684\u6218\u7EE9\u4E2D\uFF0C<strong>\u771F\u6B63\u8BA9\u6155\u5BB9\u606A\u6210\u540D\u7684\uFF0C\u5219\u662F\u4E0E<code>\u5189\u95F5</code>\u4E4B\u95F4\u7684\u60CA\u5929\u4E00\u6218\u3002</strong></p><p>\u5F53\u65F6\uFF0C\u540E\u8D75\u56E0\u7687\u5E1D\u77F3\u864E\u4E4B\u6B7B\u9677\u5165\u5185\u4E71\u3002\u6700\u7EC8\uFF0C\u6C49\u4EBA\u8840\u7EDF\u7684\u5189\u95F5\u6740\u5C3D\u80E1\u4EBA\u800C\u81EA\u7ACB\uFF0C\u5EFA\u56FD\u53F7\u4E3A\u201C\u9B4F\u201D\uFF0C\u53F2\u79F0\u201C\u5189\u9B4F\u201D\u3002</p><p>\u664B\u7A46\u5E1D\u6C38\u548C\u516B\u5E74\uFF08\u516C\u5143352\u5E74\uFF09\uFF0C\u5189\u95F5\u4EB2\u81EA\u5E26\u5175\u653B\u6253\u6155\u5BB9\u6C0F\u3002\u6155\u5BB9\u6C0F\u4E00\u65B9\u7531\u6155\u5BB9\u606A\u9886\u5175\u9632\u5FA1\uFF0C\u53CC\u65B9\u76F8\u6301\u4E8E\u5B89\u559C\u3002</p><p>\u4E00\u5F00\u59CB\uFF0C\u5189\u95F5\u4EB2\u81EA\u4E0A\u9635\uFF0C\u524D\u540E\u5341\u6B21\u51B2\u51FB\u71D5\u519B\uFF0C\u71D5\u519B\u7686\u8D25\u3002</p><p>\u53EF\u4E4B\u540E\uFF0C\u6155\u5BB9\u606A\u5374\u5229\u7528\u5189\u95F5\u751F\u51FA\u7684\u8F7B\u654C\u4E4B\u5FC3\u7CBE\u5FC3\u8BBE\u8BA1\uFF0C\u5C06\u5189\u95F5\u5F15\u5165\u5708\u5957\uFF0C\u56DB\u65B9\u5939\u51FB\u3002\u6700\u7EC8\uFF0C\u5189\u95F5\u5927\u8D25\u88AB\u64D2\u3002</p><p>\u6155\u5BB9\u606A\u9001\u5189\u95F5\u4E8E\u9F99\u57CE\u3002\u518D\u800C\u540E\uFF0C\u5189\u95F5\u88AB\u6740\uFF0C\u5189\u9B4F\u706D\u4EA1\u3002</p><p>\u4E00\u6218\u800C\u706D\u4E00\u56FD\uFF0C\u6155\u5BB9\u606A\u529F\u4E1A\u4E4B\u9AD8\uFF0C\u65E0\u6709\u6BD4\u8005\u3002</p><p>\u51E0\u5E74\u540E\uFF0C\u524D\u71D5\u5F00\u56FD\u4E4B\u541B\u6155\u5BB9\u5101\u75C5\u901D\u3002\u4E34\u7EC8\u524D\uFF0C\u4ED6\u9057\u547D\u592A\u5B50<code>\u6155\u5BB9\u6690</code>\u7EE7\u4F4D\uFF0C\u4EE5<code>\u6155\u5BB9\u606A</code>\u3001<code>\u6155\u5BB9\u8BC4</code>\u3001<code>\u6155\u8206\u6839</code>\u4E09\u4EBA\u8F85\u653F\u3002<strong>\u5176\u4E2D\uFF0C\u6155\u5BB9\u606A\u201C\u884C\u5468\u516C\u4E8B\u201D</strong>\uFF0C\u4E5F\u5C31\u662F\u8BF4\uFF0C\u6155\u5BB9\u606A\u4EE5\u81E3\u5B50\u4EE3\u884C\u541B\u6743\u3002</p><p>\u636E\u300A\u664B\u4E66\xB7\u6155\u5BB9\u606A\u4F20\u300B\u8BB0\u8F7D\uFF1A</p><blockquote><p>\uFF08\u6155\u5BB9\uFF09\u606A\u865A\u895F\u5F85\u7269\uFF0C\u54A8\u8BE2\u5584\u9053\uFF0C\u91CF\u624D\u5904\u4EFB\uFF0C\u4F7F\u4EBA\u4E0D\u903E\u4F4D\u3002</p><p>\u671D\u5EF7\u8C28\u8083\uFF0C\u8FDB\u6B62\u6709\u5E38\u5EA6\uFF0C\u867D\u6267\u6743\u653F\uFF0C\u6BCF\u4E8B\u5FC5\u54A8\u4E4B\u4E8E\u8BC4\u3002</p><p>\u7F62\u671D\u5F52\u7B2C\uFF0C\u5219\u5C3D\u5FC3\u8272\u517B\uFF0C\u624B\u4E0D\u91CA\u5377\u3002</p><p>\u5176\u767E\u50DA\u6709\u8FC7\uFF0C\u672A\u5C1D\u663E\u4E4B\uFF0C\u81EA\u662F\u5EB6\u50DA\u5316\u5FB7\uFF0C\u7A00\u6709\u72AF\u8005\u3002\u201D</p></blockquote><p>\u6267\u653F\u671F\u95F4\uFF0C\u6155\u5BB9\u606A\u865A\u6000\u5F85\u7269\uFF0C\u77E5\u4EBA\u5584\u4EFB\uFF1B\u6574\u987F\u540F\u6CBB\uFF0C\u4E25\u660E\u8D4F\u7F5A\uFF0C\u8C26\u865A\u8C28\u614E\uFF0C\u4ECE\u4E0D\u4E13\u65AD\uFF1B\u8BDB\u6740\u53DB\u81E3\u6155\u8206\u6839\uFF0C\u7A33\u5B9A\u671D\u653F\u3002\u800C\u4E14\uFF0C\u4E0D\u56E0\u81EA\u5DF1\u53D7\u8F85\u653F\u4E4B\u4EFB\u5C31\u72EC\u65AD\u4E13\u884C\uFF0C\u53CD\u800C\u8C28\u5B88\u81E3\u8282\uFF0C\u4E8B\u4E8B\u62A5\u7687\u5E1D\u6155\u5BB9\u744B\u6279\u51C6\u540E\u65B9\u53EF\u65BD\u884C\u3002\u6B64\u5916\uFF0C\u95F2\u6687\u65F6\u6C42\u77E5\u597D\u5B66\uFF0C\u624B\u4E0D\u91CA\u5377\u3002</p><p>\u4F5C\u4E3A\u6267\u653F\u8005\uFF0C\u6155\u5BB9\u606A\u8868\u73B0\u51FA\u7684\uFF0C\u662F\u9AD8\u5EA6\u4F18\u79C0\u7684\u653F\u6CBB\u7D20\u517B\u3002\u53EF\u4EE5\u8BF4\uFF0C<strong>\u524D\u71D5\u653F\u5C40\u7684\u7A33\u5B9A\uFF0C\u591A\u534A\u662F\u6155\u5BB9\u606A\u7684\u529F\u52B3\u3002</strong></p><p>\u4F46\u53EF\u60DC\u7684\u662F\uFF0C**\u6155\u5BB9\u606A\u6267\u653F\u7684\u65F6\u95F4\uFF0C\u53EA\u6709\u533A\u533A7\u5E74\u3002**\u592A\u548C\u4E8C\u5E74\uFF08\u516C\u5143367\u5E74\uFF09\uFF0C\u6155\u5BB9\u606A\u56E0\u75C5\u53BB\u4E16\uFF0C\u65F6\u5E7447\u5C81\u3002</p><p>\u5230\u4E34\u7EC8\u524D\uFF0C\u6155\u5BB9\u606A\u5BF9\u4E8E\u524D\u71D5\u7684\u653F\u6CBB\u7ED3\u6784\u5DF2\u8868\u73B0\u51FA\u62C5\u5FC3\u3002\u7687\u5E1D\u6155\u5BB9\u6690\u867D\u5DF2\u5728\u4F4D7\u5E74\uFF0C\u4F46\u5E76\u6CA1\u6709\u5B9E\u6743\u3002\u5728\u4ED6\u6B7B\u540E\uFF0C<strong>\u6155\u5BB9\u8BC4\u5FC5\u7136\u6267\u653F\u2014\u2014\u53EF\u662F\uFF0C\u6B64\u4EBA\u5374\u6709\u5AC9\u8D24\u5992\u80FD\u7684\u6BDB\u75C5\u3002</strong></p><p>\u6155\u5BB9\u606A\u6DF1\u77E5\u6155\u5BB9\u8BC4\u7684\u95EE\u9898\uFF0C\u5374\u65E0\u529B\u6539\u53D8\u3002\u9996\u5148\uFF0C\u4E24\u4EBA\u90FD\u662F\u6155\u5BB9\u5101\u4EFB\u547D\u7684\u8F85\u653F\uFF0C\u6155\u5BB9\u606A\u4E0D\u80FD\u56E0\u4E3A\u6155\u5BB9\u8BC4\u7684\u6027\u683C\u95EE\u9898\u5C31\u5C06\u4ED6\u5E9F\u9664\u3002\u5176\u6B21\uFF0C\u6155\u5BB9\u8BC4\u6BD4\u6155\u5BB9\u606A\u8FD8\u8981\u9AD8\u4E00\u8F88\uFF0C\u5728\u8FD9\u79CD\u60C5\u5F62\u4E0B\uFF0C\u6155\u5BB9\u606A\u66F4\u662F\u65E0\u8BA1\u53EF\u65BD\u3002</p><p>\u6155\u5BB9\u606A\u56E0\u6B64\u53EA\u80FD\u53CD\u590D\u5411\u7687\u5E1D\u63A8\u8350\uFF0C\u5E0C\u671B\u80FD\u91CD\u7528<strong>\u5434\u738B<code>\u6155\u5BB9\u5782</code></strong>\u3002</p><p><strong>\u53EF\u4E24\u5E74\u4EE5\u540E\uFF0C\u671D\u653F\u4F9D\u7136\u51FA\u73B0\u53D8\u6545\u3002</strong></p><p>\u5728\u6155\u5BB9\u8BC4\u6267\u653F\u521D\u671F\uFF0C\u5C40\u9762\u5C1A\u597D\uFF0C\u6BD5\u7ADF\u6155\u5BB9\u8BC4\u53EA\u662F\u6743\u529B\u6B32\u65FA\u76DB\uFF0C\u4E0D\u60F3\u4E0E\u4EBA\u5206\u4EAB\u800C\u5DF2\uFF1B\u52A0\u4E4B\u6155\u5BB9\u5782\u53C8\u5B89\u5206\u8001\u5B9E\uFF0C\u4E24\u4EBA\u4E8E\u662F\u76F8\u5B89\u65E0\u4E8B\u3002</p><p>\u4F46\u968F\u7740\u6155\u5BB9\u5782\u51FB\u8D25\u4E1C\u664B\u6853\u6E29\u7684\u5317\u4F10\u90E8\u961F\uFF0C\u53D8\u52A8\u5F00\u59CB\u51FA\u73B0\u3002\u592A\u548C\u56DB\u5E74\uFF08\u516C\u5143369\u5E74\uFF09\uFF0C**\u664B\u5927\u53F8\u9A6C<code>\u6853\u6E29</code>**\u5317\u4F10\uFF0C\u524D\u71D5\u671D\u5EF7\u5927\u9707\u3002\u4E4B\u540E\uFF0C<strong>\u6155\u5BB9\u5782\u8BF7\u7F28\u51FA\u5F81\uFF0C\u51FB\u8D25\u6853\u6E29\u800C\u8FD8\u3002</strong></p><p>\u4F46\u6155\u5BB9\u5782\u56DE\u671D\u540E\uFF0C\u6155\u5BB9\u8BC4\u56E0\u4E3A\u5BB3\u6015\u6155\u5BB9\u5782\u529F\u9AD8\u53D7\u8D4F\uFF0C\u5206\u8D70\u81EA\u5DF1\u624B\u4E2D\u7684\u6743\u529B\uFF0C\u5C45\u7136\u4E0E\u592A\u540E\u53EF\u8DB3\u6D51\u6C0F\u4E00\u8D77\uFF0C\u5BC6\u8C0B\u9664\u6389\u6155\u5BB9\u5782\u3002</p><p>\u660E\u660E\u7ACB\u4E0B\u5927\u529F\uFF0C\u6700\u540E\u5374\u8EAB\u9677\u9669\u5883\uFF0C\u65E0\u6CD5\u81EA\u5BB9\u4E8E\u6BCD\u56FD\uFF0C\u6155\u5BB9\u5782\u4E07\u822C\u65E0\u5948\u4E0B\uFF0C\u53EA\u80FD\u6295\u5954<strong>\u524D\u79E6<code>\u82FB\u575A</code></strong>\u3002</p><p>\u800C\u82FB\u575A\u6B64\u65F6\u6B63\u5728\u5BC6\u8C0B\u7740\u7684\uFF0C\u5C31\u662F\u5982\u4F55\u8FDB\u653B\u524D\u71D5\u3002</p><p>**\u81EA\u4ECE\u6155\u5BB9\u606A\u53BB\u4E16\u540E\uFF0C<code>\u82FB\u575A</code>\u4E0E\u5176\u91CD\u81E3<code>\u738B\u731B</code>\u90FD\u5F00\u59CB\u51C6\u5907\u8FDB\u653B\u524D\u71D5\u3002**\u8FD9\u4E00\u70B9\uFF0C\u5373\u4FBF\u662F\u71D5\u4EBA\u4E5F\u5FC3\u77E5\u809A\u660E\u3002\u6BD5\u7ADF\u5317\u65B9\u7ECF\u8FC7\u6DF7\u4E71\u591A\u5E74\uFF0C\u5982\u4ECA\u53EA\u5269\u4E0B\u4E86\u524D\u79E6\u4E0E\u524D\u71D5\u4E24\u652F\u653F\u6743\uFF0C\u53CC\u65B9\u7684\u5F00\u6218\u5728\u6240\u96BE\u514D\u3002</p><p>\u800C\u5F53\u82FB\u575A\u5F97\u5230\u6155\u5BB9\u5782\u6765\u6295\u7684\u6D88\u606F\u540E\uFF0C\u5927\u559C\u8FC7\u671B\uFF0C\u4E0D\u4E45\u4FBF\u547D\u738B\u731B\u5E26\u5175\u8FDB\u653B\u524D\u71D5\u3002</p><p><strong>\u7EC6\u8BF4\u8D77\u6765\uFF0C\u82FB\u575A\u51FA\u5175\u7684\u7406\u7531\uFF0C\u8FD8\u662F\u524D\u71D5\u81EA\u5DF1\u7ED9\u7684\uFF1A</strong></p><p>\u5F53\u521D\u6853\u6E29\u5317\u4F10\uFF0C\u71D5\u4EBA\u66FE\u5411\u79E6\u4EBA\u6C42\u6551\u3002\u82FB\u575A\u53EC\u96C6\u671D\u81E3\u5546\u8BAE\u6B64\u4E8B\uFF0C\u7EDD\u5927\u90E8\u5206\u4EBA\u7684\u610F\u89C1\u90FD\u662F\u201C\u5E94\u8BE5\u4E0D\u6551\u201D\uFF0C\u53EA\u6709\u738B\u731B\u72EC\u81EA\u5F02\u8BAE\u3002</p><p>\u738B\u731B\u8BA4\u4E3A\uFF0C\u71D5\u56FD\u81EA\u6155\u5BB9\u8BC4\u4E3B\u653F\u540E\uFF0C\u653F\u6CBB\u660F\u6697\uFF0C\u5982\u679C\u4E0D\u6551\uFF0C\u53EF\u80FD\u4F1A\u8BA9\u6853\u6E29\u5F97\u5FD7\u3002\u800C\u7B49\u5230\u6853\u6E29\u51FB\u8D25\u71D5\u56FD\uFF0C\u5360\u636E\u71D5\u56FD\u6545\u5730\uFF0C\u6211\u56FD\u7684\u5F62\u52BF\u5C31\u5C06\u66F4\u4E3A\u5371\u9669\u3002</p><p>\u8FD9\u756A\u8003\u8651\u8BF4\u670D\u4E86\u82FB\u575A\uFF0C\u82FB\u575A\u56E0\u6B64\u540C\u610F\u51FA\u5175\u3002\u5F53\u7136\uFF0C\u71D5\u56FD\u4EBA\u4E5F\u4E0D\u662F\u9760\u7740\u4E00\u5F20\u5634\u5C31\u6765\u9F13\u52A8\u7684\uFF0C\u4ED6\u4EEC\u8BB8\u8BFA\u524D\u79E6\uFF1A\u53EA\u8981\u51FA\u5175\uFF0C\u5C31\u5272\u8BA9\u864E\u7262\u5173\u4EE5\u897F\u7684\u571F\u5730\u3002</p><p>\u53EF\u4E0D\u60F3\u4ED7\u6253\u5B8C\u4E86\uFF0C\u524D\u71D5\u5374\u5F00\u59CB\u62B5\u8D56\uFF0C\u5730\u4E0D\u5272\u4E86\u2014\u2014\u6155\u5BB9\u8BC4\u7684\u77ED\u89C6\uFF0C\u53EF\u89C1\u4E00\u6591\u3002</p><p>\u5982\u4ECA\uFF0C\u524D\u79E6\u6B63\u597D\u501F\u7740\u8FD9\u4E2A\u7531\u5934\uFF0C\u51FA\u5175\u524D\u71D5\u3002</p><p>**\u4E00\u8DEF\u4E0A\uFF0C\u5175\u950B\u6240\u6307\uFF0C\u51E0\u65E0\u62B5\u6297\u3002**\u56E0\u4E3A\u81EA\u4ECE\u524D\u79E6\u51FA\u5175\u540E\uFF0C\u4E24\u56FD\u4E4B\u95F4\u8058\u4F7F\u5F80\u6765\u5BC6\u5207\uFF0C\u6155\u5BB9\u8BC4\u4E8E\u662F\u81EA\u4EE5\u4E3A\u53CC\u65B9\u5173\u7CFB\u8F6C\u6696\uFF0C\u4E0D\u81F3\u4E8E\u5F00\u6218\uFF0C\u56E0\u6B64\u64A4\u8D70\u4E86\u79E6\u71D5\u8FB9\u754C\u7684\u5927\u90E8\u5206\u58EB\u5175\u3002</p><p>\u8FD9\u79CD\u8FF7\u4E4B\u81EA\u4FE1\uFF0C\u6700\u7EC8\u8BA9\u524D\u71D5\u5403\u4E86\u5927\u4E8F\u3002\u592A\u548C\u56DB\u5E74\uFF0C\u4E5F\u5C31\u662F\u6155\u5BB9\u5782\u51FB\u8D25\u6853\u6E29\u7684\u8FD9\u4E00\u5E74\uFF0C\u5341\u4E8C\u6708\uFF0C\u738B\u731B\u5E26\u9886\u4E09\u4E07\u5927\u519B\u51FA\u5F81\uFF0C\u6B21\u5E74\u5341\u6708\u4FBF\u5DF2\u5305\u56F4\u524D\u71D5\u56FD\u90FD\u90BA\u57CE\u3002\u81F3\u4E8E\u524D\u71D5\u7684\u7CBE\u9510\u90E8\u961F\uFF0C\u5219\u5DF2\u5728\u4E4B\u524D\u7684\u664B\u9633\u4E4B\u6218\u4E2D\uFF0C\u88AB\u738B\u731B\u5168\u6B7C\u3002</p><p>\u5341\u4E00\u6708\uFF0C\u82FB\u575A\u7387\u5927\u519B\u63A5\u5E94\uFF0C\u653B\u7834\u90BA\u57CE\u3002\u4E0D\u4E45\u540E\uFF0C\u524D\u71D5\u672B\u5E1D<code>\u6155\u5BB9\u6690</code>\u88AB\u4FD8\u2014\u2014\u6B64\u65F6\u4E0A\u8DDD\u6155\u5BB9\u606A\u53BB\u4E16\uFF0C\u4EC5\u4EC5\u4E09\u5E74\uFF1B\u8DDD\u79BB\u6155\u5BB9\u5782\u51FB\u8D25\u6853\u6E29\u7684\u5317\u4F10\u5927\u519B\uFF0C\u4E5F\u4EC5\u4EC5\u8FC7\u53BB\u4E00\u5E74\u534A\u3002</p><p><strong>\u56DE\u987E\u524D\u71D5\u7684\u5174\u8870\u8D77\u843D\uFF0C\u51E0\u4E4E\u5904\u5904\u90FD\u8131\u4E0D\u5F00\u6155\u5BB9\u606A\u7684\u8EAB\u5F71\u3002</strong></p><blockquote><p><strong>\u9996\u5148\uFF0C\u4ED6\u529F\u52CB\u5353\u8457\uFF0C\u662F\u6155\u5BB9\u6C0F\u6218\u529F\u6700\u5927\u7684\u4EBA\uFF0C\u4E5F\u56E0\u6B64\u88AB\u8BC4\u4E3A\u201C\u5341\u516D\u56FD\u65F6\u671F\u7B2C\u4E00\u540D\u5C06\u201D\u3002</strong></p></blockquote><p>\u9664\u4E86\u4E0A\u6587\u6240\u5217\u4E3E\u7684\u5185\u5BB9\uFF0C\u6155\u5BB9\u606A\u7684\u6218\u529F\u8FD8\u5305\u62EC\uFF1A</p><p>\u5728\u5189\u95F5\u6B7B\u540E\uFF0C\u6E05\u9664\u5189\u9B4F\u7684\u6B8B\u4F59\u62B5\u6297\u52BF\u529B\uFF1B</p><p>\u5E73\u5B9A\u5415\u62A4\u7684\u53DB\u4E71\u2014\u2014\u5415\u62A4\u539F\u662F\u524D\u71D5\u5B88\u5C06\uFF0C\u540E\u6697\u4E2D\u6295\u964D\u4E1C\u664B\uFF0C\u5E76\u8BD5\u56FE\u5F15\u664B\u519B\u5077\u88AD\u524D\u71D5\u56FD\u90FD\u90BA\u57CE\uFF1B</p><p>\u653B\u7834\u81E3\u670D\u4E8E\u4E1C\u664B\u7684\u6BB5\u9F9B\u90E8\uFF0C\u5C06\u524D\u71D5\u7684\u56FD\u571F\u63A8\u79FB\u81F3\u9EC4\u6CB3\u4EE5\u5357\uFF1B</p><p>\u751A\u81F3\u5728\u53D7\u4EFB\u8F85\u653F\u540E\uFF0C\u6155\u5BB9\u606A\u4E5F\u8FD8\u653B\u9677\u4E86\u5F53\u65F6\u5904\u4E8E\u4E1C\u664B\u7EDF\u6CBB\u4E4B\u4E0B\u7684\u6D1B\u9633\u3002</p><p>\u5F53\u521D\u6155\u5BB9\u5101\u53BB\u4E16\uFF0C\u4E1C\u664B\u671D\u5EF7\u5F39\u51A0\u76F8\u5E86\uFF0C\u4EE5\u4E3A\u9664\u4E00\u5927\u654C\u3002\u53EA\u6709\u6853\u6E29\u5FE7\u8651\u4E8E\u5F62\u8272\uFF0C\u8BA4\u4E3A\u5230\u4E86\u6155\u5BB9\u606A\u6267\u653F\uFF0C\u9690\u5FE7\u66F4\u5927\u3002\u800C\u524D\u79E6\u7684\u82FB\u575A\u548C\u738B\u731B\uFF0C\u4E5F\u662F\u5728\u6155\u5BB9\u606A\u6B7B\u540E\uFF0C\u624D\u5BF9\u524D\u71D5\u751F\u51FA\u89CA\u89CE\u4E4B\u5FC3\u3002</p><p>\u53EF\u4EE5\u8BF4\u6155\u5BB9\u606A\u9760\u7740\u81EA\u5DF1\u767E\u6218\u767E\u80DC\u7684\u5A01\u540D\uFF0C\u5728\u4E16\u65F6\u57FA\u672C\u4E0A\u65AD\u7EDD\u4E86\u654C\u56FD\u4E3B\u52A8\u6311\u8845\u7684\u53EF\u80FD\uFF0C<strong>\u51E0\u4E4E\u4EC5\u51ED\u4E00\u5DF1\u4E4B\u529B\uFF0C\u4FBF\u8D77\u5230\u4E86\u9707\u6151\u654C\u4EBA\u7684\u4F5C\u7528\u3002</strong></p><blockquote><p><strong>\u4F46\u540C\u65F6\uFF0C\u8BC4\u4EF7\u6155\u5BB9\u606A\u5982\u679C\u4EC5\u4EC5\u4EE5\u519B\u4E8B\u7EDF\u5E05\u6765\u8BBA\uFF0C\u53C8\u5B9E\u5728\u5931\u4E4B\u504F\u9887\uFF1A</strong></p></blockquote><p><strong>\u4ED6\u66F4\u5927\u7684\u4F5C\u7528\uFF0C\u5728\u4E8E\u7A33\u5B9A\u4E86\u524D\u71D5\u7684\u671D\u653F\u3002</strong></p><p>\u4E0A\u7BC7\u6211\u4EEC\u5728\u4ECB\u7ECD\u524D\u71D5\u7684\u5D1B\u8D77\u65F6\uFF0C\u91CD\u70B9\u8BB2\u4E86\u6155\u5BB9\u6C0F\u8D81\u7740\u540E\u8D75\u5185\u4E71\uFF0C\u8FC5\u901F\u5360\u9886\u540E\u8D75\u9057\u7559\u56FD\u571F\u7684\u597D\u8FD0\u6C14\u3002</p><p>\u4F46\u95EE\u9898\u662F\uFF0C\u8FD9\u79CD\u5E78\u8FD0\u5E76\u975E\u4E00\u52B3\u6C38\u9038\uFF1A\u5373\u4FBF\u6240\u5360\u571F\u5730\u662F\u540E\u8D75\u7684\u818F\u8174\u4E4B\u5730\uFF0C\u654C\u624B\u4E5F\u4F9D\u65E7\u5B58\u5728\uFF1A\u76D8\u8E1E\u4E8E\u5173\u4E2D\u7684\u524D\u79E6\uFF0C\u4EE5\u53CA\u5357\u65B9\u7684\u4E1C\u664B\uFF0C\u90FD\u662F\u6155\u5BB9\u6C0F\u7684\u5FC3\u8179\u5927\u60A3\u3002</p><p>\u800C\u4E14\u6155\u5BB9\u6C0F\u81EA\u5DF1\uFF0C\u4E5F\u4EC5\u4EC5\u662F\u9760\u519B\u4E8B\u5F81\u670D\u5360\u9886\u4E86\u571F\u5730\uFF0C\u5C1A\u672A\u5F62\u6210\u6709\u6548\u7EDF\u6CBB\u3002</p><p>\u6B64\u5916\uFF0C\u671D\u5802\u4E4B\u4E0A\uFF0C\u51FA\u81EA\u90E8\u843D\u7684\u5927\u81E3\u4EEC\u5F7C\u6B64\u5C14\u865E\u6211\u8BC8\uFF0C\u5E76\u6CA1\u6709\u5F62\u6210\u826F\u597D\u7684\u653F\u6CBB\u6587\u660E\uFF0C\u6597\u4E89\u51E0\u4E4E\u6CA1\u6709\u4E0B\u9650\u3002</p><p>\u5728\u8FD9\u6837\u7684\u5185\u5916\u5FE7\u60A3\u4E0B\uFF0C<strong>\u6B63\u662F\u56E0\u4E3A\u6709\u6155\u5BB9\u606A\u5728\uFF0C\u524D\u71D5\u56FD\u5185\u7684\u8FD9\u4E9B\u77DB\u76FE\u548C\u7EB7\u4E89\u624D\u80FD\u80FD\u88AB\u6D88\u5F2D\u4E8E\u65E0\u5F62</strong>\uFF0C\u5373\u4FBF\u6709\u6155\u5BB9\u8BC4\u7684\u8D2A\u5A6A\u548C\u5AC9\u8D24\u5992\u80FD\uFF0C\u5373\u4FBF\u6709\u6155\u5BB9\u6690\u5BF9\u4E8E\u6155\u5BB9\u5782\u7684\u4E0D\u4FE1\u4EFB\uFF0C\u8FD9\u4E9B\u9690\u60A3\u4E5F\u90FD\u4E0D\u4F1A\u7206\u53D1\u3002</p><p>\u56E0\u4E3A\u4E0D\u8BBA\u662F\u529F\u7EE9\u3001\u8840\u7EDF\uFF0C\u8FD8\u662F\u58F0\u671B\u3001\u80FD\u529B\uFF0C\u6155\u5BB9\u606A\u90FD\u8DB3\u4EE5\u80DC\u4EFB\u6267\u653F\u7684\u5DE5\u4F5C\u3002\u5728\u4ED6\u7684\u906E\u62A4\u4E0B\uFF0C\u5404\u65B9\u52BF\u529B\u4E4B\u95F4\u5C31\u7B97\u9F83\u9F89\u4E0D\u65AD\uFF0C\u4E5F\u80FD\u5404\u5B89\u5176\u4F4D\u3002</p><p>\u751A\u81F3\u53EF\u4EE5\u8FD9\u4E48\u8BF4\uFF0C\u6155\u5BB9\u606A\u5982\u679C\u4E00\u76F4\u5B58\u5728\uFF0C\u90A3\u4E48\u524D\u71D5\u53EF\u80FD\u5C31\u4E0D\u7528\u9762\u4E34\u540E\u6765\u7684\u4E00\u7CFB\u5217\u98CE\u9669\u3002\u5F53\u6155\u5BB9\u606A\u6D3B\u7740\uFF0C\u6240\u6709\u7684\u95EE\u9898\u5C31\u90FD\u4E0D\u662F\u95EE\u9898\u3002</p><p>\u53EA\u662F\u53EF\u60DC\uFF0C\u6155\u5BB9\u606A\u4E00\u6B7B\uFF0C\u524D\u71D5\u8FD9\u4E9B\u80FD\u751F\u6210\u91CD\u5927\u5371\u673A\u7684\u5185\u90E8\u77DB\u76FE\u5C31\u8FC5\u901F\u66B4\u9732\u4E86\u51FA\u6765\uFF0C\u5E76\u88AB\u5BF9\u624B\u7ED9\u53CA\u65F6\u5229\u7528\uFF0C\u6700\u7EC8\u62DB\u81F4\u524D\u71D5\u706D\u4EA1\u3002</p><p>\u5982\u679C\u6155\u5BB9\u606A\u5728\u4E16\uFF0C\u90A3\u4E48\u5386\u53F2\u4F1A\u7559\u7ED9\u6155\u5BB9\u6C0F\u89E3\u51B3\u77DB\u76FE\u7684\u65F6\u95F4\u7A97\u53E3\u2014\u2014\u81F3\u4E8E\u80FD\u5426\u6210\u529F\uFF0C\u90A3\u662F\u53E6\u4E00\u4E2A\u8BDD\u9898\u3002\u4F46\u73B0\u5728\uFF0C<strong>\u968F\u7740\u6155\u5BB9\u606A\u7684\u53BB\u4E16\uFF0C\u8FD9\u4E2A\u65F6\u95F4\u7A97\u53E3\u5374\u88AB\u786E\u51FF\u65E0\u7591\u7684\u5173\u95ED\u4E86\u3002</strong></p><p>**\u7B49\u5F85\u6155\u5BB9\u6C0F\u7684\uFF0C\u53EA\u80FD\u662F\u8003\u9A8C\u77DB\u76FE\u7206\u53D1\u4E4B\u540E\u7684\u5E94\u5BF9\u80FD\u529B\u3002**\u4F46\u6CA1\u6709\u60CA\u559C\u4E5F\u6CA1\u6709\u610F\u5916\uFF0C\u6155\u5BB9\u6C0F\u679C\u7136\u6CA1\u80FD\u6491\u8FC7\u8FD9\u6B21\u5371\u673A\u3002\u4E8B\u6001\u56E0\u6853\u6E29\u5317\u4F10\u800C\u5F15\u7206\uFF0C\u4EE5\u6155\u5BB9\u5782\u51FA\u5954\u524D\u79E6\u4E3A\u4E2D\u7EE7\uFF0C\u6700\u7EC8\u7531\u738B\u731B\u706D\u4EA1\u71D5\u56FD\u843D\u4E0B\u7EC8\u7AE0\u3002</p><p>\u6155\u5BB9\u6C0F\u7684\u5E78\u8FD0\u4E0E\u4E0D\u5E78\uFF0C\u5168\u90FD\u7CFB\u4E8E\u6155\u5BB9\u606A\u4E00\u4EBA\uFF1A\u5E78\u8FD0\u8005\uFF0C\u6709\u6155\u5BB9\u606A\u8FD9\u6837\u7684\u5168\u624D\u51FA\u73B0\uFF0C\u4EE4\u6155\u5BB9\u6C0F\u7684\u57FA\u4E1A\u8FBE\u5230\u5168\u76DB\uFF1B\u800C\u4E0D\u5E78\u7684\uFF0C\u5219\u662F\u6155\u5BB9\u606A\u6B7B\u5F97\u592A\u65E9\uFF0C\u5BF9\u624B\u53C8\u5B9E\u5728\u5F3A\u5927\u2014\u2014\u6155\u5BB9\u606A\u8EAB\u540E\u7684\u524D\u71D5\u8981\u9762\u5BF9\u7684\uFF0C\u662F\u201C\u82FB\u575A+\u738B\u731B\u201D\u8FD9\u6837\u7684\u9EC4\u91D1\u7EC4\u5408\u3002</p><p>\u7EC8\u7A76\u96BE\u7EE7\u3002</p>',72),c=[n];function g(d,a){return o(),t("div",null,c)}const i=p(r,[["render",g],["__file","23.html.vue"]]);export{i as default};
