import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.eee000b7.js";const c={},o=a('<h1 id="\u7B2C26\u96C6-\u9648\u6C5F\u6CB3\u5356\u8F66\u8FD8\u503A" tabindex="-1"><a class="header-anchor" href="#\u7B2C26\u96C6-\u9648\u6C5F\u6CB3\u5356\u8F66\u8FD8\u503A" aria-hidden="true">#</a> \u7B2C26\u96C6\uFF1A\u9648\u6C5F\u6CB3\u5356\u8F66\u8FD8\u503A</h1><p>\u9648\u6C5F\u6CB3\u6765\u5230\u5E02\u573A\uFF0C\u53D1\u73B0\u5927\u5BB6\u90FD\u5728\u8EB2\u503A\uFF0C\u521A\u521A\u8D70\u8FDB\u81EA\u5DF1\u7684\u644A\u4F4D\uFF0C\u53D1\u73B0\u5DF2\u7ECF\u88AB\u503A\u4E3B\u5305\u56F4\u4E86\u3002\u5DE7\u59D1\u4E00\u4E2A\u4EBA\u82E6\u82E6\u652F\u6491\u3002\u9648\u6C5F\u6CB3\u65E0\u5948\uFF0C\u53EA\u6709\u8EB2\u8FDB\u4E86\u9694\u58C1\u51AF\u59D0\u7684\u94FA\u5B50\u91CC\u3002</p><p>\u8FD9\u8FB9\uFF0C\u9648\u5BB6\u95E8\u53E3\uFF0C\u4E5F\u6709\u4E00\u7FA4\u4EBA\u6572\u95E8\u6572\u5F97\u5C71\u54CD\u3002 \u738B\u65ED\u9A6C\u4E0A\u5C31\u8981\u8003\u8BD5\uFF0C\u65E0\u6CD5\u5B89\u5FC3\u590D\u4E60\u3002\u9A86\u7389\u73E0\u633A\u7740\u809A\u5B50\u548C\u738B\u65ED\u53EA\u80FD\u4ECE\u540E\u9662\u7FFB\u5899\u79BB\u5F00\u3002</p><p>\u6C5F\u6CB3\u4E00\u5BB6\u4EBA\u65E0\u5904\u53EF\u53BB\uFF0C\u53EA\u80FD\u5546\u91CF\u7740\u5C06\u738B\u65ED\u9001\u5230\u5DE7\u59D1\u5BB6\u91CC\uFF0C\u7136\u540E\u4E24\u4EBA\u5206\u5934\u53BB\u8BA8\u503A\u3002</p><p>\u738B\u65ED\u88AB\u9001\u5230\u5DE7\u59D1\u5BB6\uFF0C\u5DE7\u59D1\u4E34\u65F6\u6709\u4E8B\u8981\u51FA\u95E8\uFF0C\u4FE9\u5B69\u5B50\u4EA4\u7ED9\u9648\u91D1\u6C34\u7167\u770B\uFF0C\u9648\u91D1\u6C34\u4E00\u5F00\u59CB\u9887\u4E3A\u4E0D\u60C5\u613F\u3002\u5012\u662F\u738B\u65ED\u4E3B\u52A8\u63D0\u8D77\u60F3\u548C\u9648\u91D1\u6C34\u8D8A\u505A\u9E21\u6BDB\u6BFD\u5B50\u3002\u8FD8\u63D0\u8D77\u4E86\u5F53\u521D\u9648\u91D1\u6C34\u5728\u5DE7\u59D1\u5A5A\u793C\u4E0A\u8BB2\u8FC7\u7684\u8BDD\u3002\u54C4\u5F97\u9648\u91D1\u6C34\u7B11\u772F\u772F\u3002</p><p>\u9A86\u7389\u73E0\u548C\u9648\u6C5F\u6CB3\u5175\u5206\u4E24\u8DEF\uFF0C\u9A86\u7389\u73E0\u53BB\u4E86\u4E5F\u4E0D\u8BF4\u8BDD\uFF0C\u53EA\u662F\u633A\u7740\u809A\u5B50\u5835\u5728\u4EBA\u5BB6\u95E8\u53E3\uFF0C\u8FDE\u4EBA\u5BB6\u5403\u996D\u4E5F\u8DDF\u5728\u540E\u5934\u3002\u800C\u9648\u6C5F\u6CB3\u8FD9\u8FB9\uFF0C\u5219\u76F4\u63A5\u4E0A\u624B\u5E2E\u4EBA\u5BB6\u5356\u4E1C\u897F\u3002\u5F04\u7684\u8001\u677F\u53C8\u611F\u52A8\u53C8\u4E0D\u597D\u610F\u601D\u3002</p><p>\u53EA\u662F\u8FD9\u6837\u8DD1\u4E86\u51E0\u5929\uFF0C\u4E24\u4E2A\u4EBA\u8981\u56DE\u6765\u7684\u94B1\u4E5F\u53EA\u662F\u676F\u6C34\u8F66\u85AA\u3002</p><p>\u7389\u73E0\u56DE\u6765\uFF0C\u5230\u5DE7\u59D1\u5BB6\u63A5\u5B69\u5B50\uFF0C\u542C\u8BF4\u5DE7\u59D1\u628A\u5B69\u5B50\u4EA4\u7ED9\u9648\u91D1\u6C34\u7167\u987E\uFF0C\u7740\u6025\u4E86\uFF0C\u8FD9\u9648\u91D1\u6C34\u4E00\u5411\u770B\u5979\u4E0D\u987A\u773C\uFF0C\u4ED6\u4F1A\u600E\u4E48\u5BF9\u5F85\u5B69\u5B50\u5462\u3002\u6CA1\u60F3\u5230\u521A\u8D70\u5230\u95E8\u53E3\uFF0C\u5C31\u542C\u5230\u5C4B\u91CC\u4F20\u6765\u4E86\u4E00\u8001\u4E00\u5C0F\u7684\u5406\u559D\u58F0\u3002\u539F\u6765\u9648\u91D1\u6C34\u7ADF\u5C06\u9E21\u6BDB\u6362\u7CD6\u7684\u770B\u5BB6\u672C\u4E8B\u90FD\u4F20\u7ED9\u4E86\u5C0F\u738B\u65ED\u3002\u7389\u73E0\u548C\u5DE7\u59D1\u5728\u95E8\u53E3\u76F8\u89C6\u800C\u7B11\u3002</p><p>\u9648\u6C5F\u6CB3\u5E26\u7740\u738B\u65ED\u5728\u996D\u5E97\u95E8\u53E3\u9047\u5230\u8001\u8463\uFF0C\u9648\u6C5F\u6CB3\u4E0B\u8F66\u8BA8\u503A\u3002\u88AB\u8001\u8463\u63A8\u8131\uFF0C\u738B\u65ED\u770B\u5230\u6B64\u60C5\u666F\uFF0C\u8D81\u4EBA\u4E0D\u5907\uFF0C\u5077\u5077\u6E9C\u8FDB\u9152\u5E97\uFF0C\u62FF\u8D70\u4E86\u8001\u8463\u7684\u94B1\u5305\u3002\u5374\u88AB\u4FDD\u5B89\u6293\u4F4F\uFF0C\u9648\u6C5F\u6CB3\u8D76\u5FD9\u6251\u8FC7\u6765\u62A4\u4F4F\u738B\u65ED\uFF0C\u738B\u65ED\u7740\u6025\u5927\u558A\uFF0C\u8131\u53E3\u800C\u51FA\uFF1A\u522B\u6253\u6211\u7238\u7238\u3002\u9648\u6C5F\u6CB3\u542C\u5230\uFF0C\u7B80\u76F4\u4E0D\u6562\u76F8\u4FE1\u81EA\u5DF1\u7684\u8033\u6735\u3002</p><p>\u56DE\u53BB\u7684\u8DEF\u4E0A\uFF0C\u9648\u6C5F\u6CB3\u4E00\u76F4\u4E50\u5475\u5475\u7684\uFF0C\u867D\u7136\u738B\u65ED\u5634\u786C\u4E0D\u627F\u8BA4\uFF0C\u4F46\u6C5F\u6CB3\u548C\u7389\u73E0\u90FD\u5F88\u5F00\u5FC3\u738B\u65ED\u7EC8\u4E8E\u63A5\u53D7\u4E86\u6C5F\u6CB3\u3002</p><p>\u90B1\u5CA9\u5728\u9152\u5E97\u5076\u7136\u542C\u5230\u5988\u5988\u6253\u7535\u8BDD\uFF0C\u539F\u6765\u5988\u5988\u5728\u7F8E\u56FD\u5DF2\u7ECF\u91CD\u65B0\u7EC4\u5EFA\u4E86\u5BB6\u5EAD\uFF0C\u8FD8\u6709\u4E86\u513F\u5B50\u3002\u800C\u66F4\u8BA9\u5979\u4F24\u5FC3\u7684\u662F\uFF0C\u5988\u5988\u5E76\u4E0D\u6253\u7B97\u5E26\u5979\u56DE\u5BB6\uFF0C\u800C\u662F\u51C6\u5907\u628A\u5979\u9001\u5230\u7F8E\u56FD\u7684\u5BC4\u5BBF\u5B66\u6821\u3002</p><p>\u8FC7\u5E74\u4E86\uFF0C\u9648\u6C5F\u6CB3\u4E00\u5BB6\u4EBA\u5728\u8DEF\u8FB9\u4E0A\u5403\u4E86\u4E00\u987F\u7B80\u5355\u5374\u6E29\u99A8\u7684\u56E2\u5706\u996D\u3002</p><p>\u9648\u6C5F\u6CB3\u5356\u6389\u4E86\u5361\u8F66\uFF0C\u6362\u5230\u4E86\u4E00\u7B14\u73B0\u91D1\u3002\u4ED6\u5E26\u7740\u94B1\u6765\u5230\u8001\u590F\u7684\u5DE5\u5382\uFF0C\u5DE5\u5382\u91CC\u5DF2\u7ECF\u51E0\u4E2A\u6708\u53D1\u4E0D\u4E86\u5DE5\u8D44\u4E86\u3002\u5DE5\u4EBA\u4EEC\u6B63\u56F4\u7740\u8001\u590F\u8981\u5DE5\u8D44\u3002\u8001\u590F\u6101\u7684\u6CA1\u529E\u6CD5\uFF0C\u9648\u6C5F\u6CB3\u7684\u53CA\u65F6\u51FA\u73B0\u7B80\u76F4\u662F\u6551\u4E86\u8001\u590F\u7684\u547D\u3002</p><p>\u9648\u6C5F\u6CB3\u5E26\u7740\u94B1\u56DE\u6765\uFF0C\u77E5\u9053\u503A\u4E3B\u4EEC\u90FD\u88AB\u9648\u91D1\u6C34\u8BF7\u53BB\u559D\u9152\u4E86\uFF0C\u5FD9\u8D76\u5F80\u9648\u5BB6\u3002\u9152\u684C\u4E0A\uFF0C\u5927\u5BB6\u6B63\u5728\u542C\u9648\u91D1\u6C34\u8BB2\u8D77\u5F53\u5E74\u7684\u5F80\u4E8B\u3002\u5927\u5BB6\u90FD\u5F88\u4F69\u670D\u9648\u91D1\u6C34\u3002\u4E34\u8D70\u524D\uFF0C\u8001\u5434\u62C9\u7740\u9648\u6C5F\u6CB3\u76F4\u8BF4\u4ED6\u662F\u503C\u5F97\u4EA4\u7684\u670B\u53CB\u3002</p><p>\u9648\u91D1\u6C34\u544A\u8BC9\u9648\u6C5F\u6CB3\u9E21\u5382\u8981\u62C6\u8FC1\uFF0C\u4ED6\u51C6\u5907\u62FF\u8865\u507F\u7684\u516B\u4E2A\u94FA\u5B50\u5165\u80A1\u9648\u6C5F\u6CB3\u7684\u751F\u610F\u3002\u9A86\u7389\u73E0\u4E00\u53E3\u7B54\u5E94\u3002</p><p>\u90B1\u5CA9\u8981\u8D70\u4E86\uFF0C\u5979\u544A\u8BC9\u738B\u65ED\u5988\u5988\u5BB6\u91CC\u8FD8\u6709\u4E2A\u5F1F\u5F1F\uFF0C\u5979\u5988\u5988\u51C6\u5907\u9001\u5979\u5230\u5BC4\u5BBF\u5B66\u6821\u53BB\u3002\u738B\u65ED\u4E5F\u8BF4\u8D77\u81EA\u5DF1\u7684\u5FC3\u4E8B\uFF0C\u4ED6\u62C5\u5FC3\u9648\u6C5F\u6CB3\u6709\u4E86\u81EA\u5DF1\u7684\u5B69\u5B50\u5C31\u4E0D\u4F1A\u5BF9\u4ED6\u8FD9\u4E48\u597D\u4E86\u3002\u4E24\u4E2A\u5B69\u5B50\u60F3\u8D77\u4E86\u81EA\u5DF1\u7684\u7236\u4EB2\u3002\u90FD\u54ED\u4E86\u8D77\u6765\u3002</p>',16),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","26.html.vue"]]);export{h as default};
