import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.cb44c984.js";const c={},o=a('<h1 id="\u7B2C27\u96C6-\u7389\u73E0\u6167\u773C\u8BC6\u5546\u673A-\u8DEF\u4E0A\u751F\u5B50" tabindex="-1"><a class="header-anchor" href="#\u7B2C27\u96C6-\u7389\u73E0\u6167\u773C\u8BC6\u5546\u673A-\u8DEF\u4E0A\u751F\u5B50" aria-hidden="true">#</a> \u7B2C27\u96C6\uFF1A\u7389\u73E0\u6167\u773C\u8BC6\u5546\u673A \u8DEF\u4E0A\u751F\u5B50</h1><p>\u9A86\u7389\u73E0\u56DE\u5230\u5BB6\u91CC\uFF0C\u5374\u53D1\u73B0\u738B\u65ED\u6CA1\u56DE\u6765\uFF0C\u9648\u6C5F\u6CB3\u544A\u8BC9\u7389\u73E0\uFF0C\u4ED6\u5230\u5B66\u6821\u65F6\uFF0C\u5C31\u542C\u8BF4\u738B\u65ED\u548C\u90B1\u5CA9\u4E00\u8D77\u79BB\u5F00\u4E86\u3002\u6B63\u8BF4\u7740\uFF0C\u90B1\u5CA9\u5988\u5988\u627E\u4E0A\u95E8\u6765\uFF0C\u8BE2\u95EE\u90B1\u5CA9\u7684\u4E0B\u843D\u3002\u8FD9\u624D\u77E5\u9053\u4E24\u4E2A\u5B69\u5B50\u90FD\u6CA1\u56DE\u5BB6\u3002\u5927\u5BB6\u6025\u5FD9\u51FA\u53BB\u5BFB\u627E\u3002\u9A86\u7389\u73E0\u7A81\u7136\u60F3\u5230\u5F53\u5E74\u7684\u4E8B\u60C5\uFF0C\u731C\u6D4B\u738B\u65ED\u4F1A\u4E0D\u4F1A\u5E26\u7740\u90B1\u5CA9\u56DE\u8D63\u5DDE\u53BB\u4E86\u3002\u4E8C\u4EBA\u6CBF\u7740\u94C1\u8DEF\u7EBF\u5BFB\u53BB\u3002\u6574\u6574\u4E00\u5929\uFF0C\u7EC8\u4E8E\u627E\u5230\u4E86\u4E24\u4E2A\u5B69\u5B50\u3002</p><p>\u9648\u6C5F\u6CB3\u53C8\u6C14\u53C8\u6025\u3002\u5FCD\u4E0D\u4F4F\u628A\u738B\u65ED\u62C9\u8FC7\u6765\u8981\u6253\u4ED6\u3002\u738B\u65ED\u77E5\u9053\u9519\u4E86\uFF0C\u5F00\u53E3\u6C42\u9976\uFF0C\u7EC8\u4E8E\u558A\u4E86\u58F0\u7238\u7238\u3002\u9648\u6C5F\u6CB3\u695E\u5728\u5F53\u573A\uFF0C\u54EA\u91CC\u8FD8\u6253\u7684\u4E0B\u53BB\u3002</p><p>\u90B1\u5CA9\u6700\u7EC8\u8FD8\u662F\u8DDF\u7740\u6BCD\u4EB2\u51FA\u56FD\u4E86\u3002\u4E34\u522B\u524D\uFF0C\u4E24\u4E2A\u5B69\u5B50\u4F9D\u4F9D\u60DC\u522B\u3002</p><p>\u9A86\u7389\u73E0\u516B\u4E2A\u6708\u4E86\uFF0C\u5979\u5230\u533B\u9662\u4EA7\u68C0\u65F6\uFF0C\u89C1\u8BC6\u4E86\u53EB\u505A\u5C3F\u4E0D\u6E7F\u7684\u65B0\u9C9C\u73A9\u610F\u3002\u4ECE\u4E2D\u89C1\u5230\u4E86\u5DE8\u5927\u7684\u5546\u673A\u3002\u56DE\u6765\u4FBF\u51B3\u5B9A\u8981\u505A\u5C3F\u4E0D\u6E7F\u7684\u751F\u610F\u3002\u4F46\u5F53\u65F6\uFF0C\u5C3F\u4E0D\u6E7F\u8FD8\u662F\u4E2A\u65B0\u9C9C\u73A9\u610F\uFF0C\u6CA1\u6709\u591A\u5C11\u4EBA\u7528\u3002\u6240\u4EE5\u4E0D\u4EC5\u662F\u9648\u91D1\u6C34\u4E0D\u540C\u610F\uFF0C\u8FDE\u9648\u6C5F\u6CB3\u4E5F\u4E0D\u652F\u6301\u5979\u3002\u9A86\u7389\u73E0\u751F\u6C14\u79BB\u5F00\u3002</p><p>\u56DE\u5230\u5E97\u94FA\uFF0C\u7389\u73E0\u4E00\u76F4\u60F3\u529E\u6CD5\u5230\u5904\u6253\u7535\u8BDD\uFF0C\u5E0C\u671B\u80FD\u8054\u7CFB\u5230\u5382\u5BB6\u3002\u53EF\u662F\u4E00\u76F4\u6CA1\u80FD\u5F97\u5230\u5382\u5BB6\u7684\u8054\u7CFB\u65B9\u5F0F\u3002\u53E6\u5916\u4E00\u8FB9\uFF0C\u6B63\u5728\u8DDF\u4EBA\u8C08\u751F\u610F\u7684\u9648\u6C5F\u6CB3\uFF0C\u7A81\u7136\u6CE8\u610F\u5230\u65C1\u8FB9\u6709\u4EBA\u518D\u7ED9\u5B69\u5B50\u4F7F\u7528\u5C3F\u4E0D\u6E7F\u3002\u5FD9\u51D1\u8FC7\u53BB\u6253\u542C\u3002</p><p>\u7389\u73E0\u7EC8\u4E8E\u6253\u542C\u5230\u6D88\u606F\uFF0C\u4E0D\u987E\u81EA\u5DF1\u7684\u8EAB\u4F53\uFF0C\u4E5F\u8981\u4EB2\u81EA\u53BB\u62FF\u4E0B\u8FD9\u5355\u751F\u610F\u3002\u5DE7\u59D1\u963B\u62E6\u4E0D\u4F4F\uFF0C\u53EA\u6709\u8DDF\u7740\u4E00\u8D77\u53BB\u3002\u4E24\u4EBA\u6765\u5230\u5DE5\u5382\uFF0C\u7389\u73E0\u987A\u5229\u8BF4\u670D\u4E86\u5382\u957F\uFF0C\u5C06\u8D27\u7269\u5168\u90E8\u7ED9\u4ED6\u4EEC\u5206\u9500\uFF0C\u53EA\u662F\u8BF4\u5230\u8981\u5148\u8D4A\u6B20\u65F6\uFF0C\u5382\u957F\u672C\u4E0D\u613F\u610F\uFF0C\u540E\u6765\u542C\u8BF4\u4ED6\u4EEC\u662F\u94F6\u73E0\u4E94\u91D1\u7684\u3002\u5382\u957F\u6B23\u7136\u540C\u610F\u3002\u539F\u6765\uFF0C\u7389\u73E0\u548C\u6C5F\u6CB3\u5728\u5B81\u613F\u5356\u8F66\u4E5F\u8981\u8FD8\u6E05\u6B20\u6B3E\u7684\u4E8B\u60C5\u5DF2\u7ECF\u4F20\u5F00\u4E86\uFF0C\u5927\u5BB6\u90FD\u76F8\u4FE1\u4ED6\u4EEC\u7684\u4EBA\u54C1\u3002</p><p>\u9648\u6C5F\u6CB3\u542C\u8BF4\u9A86\u7389\u73E0\u4EB2\u81EA\u53BB\u8FDB\u8D27\uFF0C\u5413\u4E86\u4E00\u8DF3\uFF0C\u5FD9\u9A91\u8F66\u53BB\u5BFB\u4ED6\u4EEC\uFF0C\u5374\u5728\u9AD8\u901F\u8DEF\u53E3\u88AB\u62E6\u4E0B\u3002\u8FD9\u8FB9\uFF0C\u9A86\u7389\u73E0\u7A81\u7136\u89C9\u5F97\u809A\u5B50\u5267\u75DB\uFF0C\u5C45\u7136\u662F\u8981\u751F\u4E86\u3002\u5DE7\u59D1\u5728\u4E00\u65C1\u614C\u4E86\u795E\uFF0C\u4E0D\u77E5\u5982\u4F55\u662F\u597D\u3002</p><p>\u9648\u6C5F\u6CB3\u53EA\u6709\u6298\u8FD4\uFF0C\u521A\u5230\u5BB6\u5C31\u63A5\u5230\u4E86\u5DE7\u59D1\u7684\u7535\u8BDD\uFF0C\u539F\u6765\u5979\u4EEC\u5DF2\u7ECF\u5230\u533B\u9662\u4E86\u3002\u5B69\u5B50\u5DF2\u7ECF\u51FA\u751F\u4E86\u3002\u9648\u6C5F\u6CB3\u542C\u5230\u5B69\u5B50\u7684\u54ED\u58F0\uFF0C\u534A\u5929\u90FD\u56DE\u4E0D\u8FC7\u795E\u6765\u3002\u8FD9\u4E2A\u5B69\u5B50\uFF0C\u88AB\u9A86\u7389\u73E0\u53D6\u540D\u9648\u8DEF\u3002</p><p>\u8F6C\u773C\u5230\u4E862005\u5E74\uFF0C\u9648\u8DEF\u5C0F\u5C0F\u5E74\u7EAA\u5C31\u5C55\u73B0\u4E86\u8FC7\u4EBA\u7684\u8BA1\u7B97\u673A\u5929\u8D4B\u3002\u800C\u738B\u65ED\u5728\u5927\u5B66\u91CC\u4E5F\u4E0D\u5FD8\u505A\u751F\u610F\uFF0C\u65BD\u5C55\u4ED6\u7684\u751F\u610F\u5934\u8111\u3002\u9648\u6C5F\u6CB3\u7684\u751F\u610F\u4E5F\u8D8A\u505A\u8D8A\u5927\u3002</p><p>\u9A86\u7389\u73E0\u548C\u56DB\u4E2A\u897F\u73ED\u7259\u5546\u4EBA\u8C08\u5224\uFF0C\u4EF7\u683C\u4E00\u76F4\u8C08\u4E0D\u62E2\u3002\u9A86\u7389\u73E0\u8F6C\u8EAB\u79BB\u5F00\uFF0C\u7559\u4E0B\u56DB\u4E2A\u8001\u5916\u9762\u9762\u76F8\u89D1\u3002</p><p>\u800C\u9648\u6C5F\u6CB3\u8FD9\u8FB9\u5374\u4E0D\u987A\u5229\uFF0C\u4ED6\u5230\u6B27\u6D32\u7EA6\u89C1\u4ED6\u7684\u751F\u610F\u4F19\u4F34\u8D39\u5C14\u5357\u5FB7\uFF0C\u4F46\u5BF9\u65B9\u5374\u62D2\u7EDD\u89C1\u9762\u3002\u4ED6\u6000\u7591\u5BF9\u65B9\u53C8\u8D70\u4E86\u65B0\u7684\u5408\u4F5C\u4F19\u4F34\u3002</p><p>\u9A86\u7389\u73E0\u8BF7\u738B\u65ED\u627E\u4E86\u51E0\u4E2A\u897F\u73ED\u7259\u540C\u5B66\u5E2E\u5FD9\u7FFB\u8BD1\u4E00\u6BB5\u5F55\u97F3\u3002\u539F\u6765\u90A3\u56DB\u4E2A\u5546\u4EBA\u65E9\u5C31\u5408\u8C0B\uFF0C\u60F3\u8981\u538B\u4F4E\u4EF7\u683C\u3002\u53EA\u6709\u4E00\u4E2A\u4EBA\u63D0\u51FA\u8FC7\u5F02\u8BAE\u3002\u9A86\u7389\u73E0\u4E8E\u662F\u4EB2\u81EA\u627E\u4E0A\u95E8\u53BB\u3002\u4E0E\u8FD9\u4E2A\u53EB\u83B1\u6602\u7684\u4EBA\u5355\u72EC\u8C08\u5408\u4F5C\u3002</p><p>\u9648\u6C5F\u6CB3\u5077\u5077\u8DDF\u5728\u540E\u9762\uFF0C\u60F3\u8981\u770B\u770B\u662F\u8C01\u5728\u6363\u9B3C\u3002\u6CA1\u60F3\u5230\u5374\u89C1\u5230\u4E00\u4E2A\u719F\u6089\u7684\u4EBA\uFF0C\u6768\u96EA\u3002</p><p>\u9A86\u7389\u73E0\u548C\u83B1\u6602\u8BE6\u8C08\u540E\uFF0C\u83B1\u6602\u5341\u5206\u656C\u4F69\u6C5F\u6CB3\u548C\u7389\u73E0\u3002\u7389\u73E0\u9080\u8BF7\u4ED6\u53C2\u89C2\u4ED6\u4EEC\u7684\u5DE5\u5382\u3002</p><p>\u9648\u6C5F\u6CB3\u8D39\u5C3D\u4E86\u5468\u6298\uFF0C\u7EC8\u4E8E\u89C1\u5230\u4E86\u8D39\u5C14\u5357\u5FB7\uFF0C\u9648\u6C5F\u6CB3\u597D\u8A00\u76F8\u5546\uFF0C\u8D39\u5C14\u5357\u5FB7\u5374\u8DBE\u9AD8\u6C14\u6602\u3002\u8FD8\u8BF4\u4ED6\u613F\u610F\u6309\u7167\u5408\u7EA6\u8D54\u507F\u9648\u6C5F\u6CB3\u4E94\u4E07\u7F8E\u5143\u7684\u635F\u5931\u3002\u4F46\u9648\u6C5F\u6CB3\u7684\u8D27\u7269\u6EDE\u7559\u6E2F\u53E3\u4E00\u5929\u7684\u635F\u5931\uFF0C\u5C31\u662F\u4E94\u4E07\u6B27\u3002</p><p>\u9648\u6C5F\u6CB3\u56DE\u5230\u9152\u5E97\uFF0C\u7EDE\u5C3D\u8111\u6C41\u4E5F\u60F3\u4E0D\u5230\u597D\u529E\u6CD5\uFF0C\u5374\u7A81\u7136\u63A5\u5230\u6768\u96EA\u7684\u7535\u8BDD\u3002\u7EA6\u4ED6\u53BB\u5979\u4F4F\u7684\u9152\u5E97\u89C1\u9762\u3002</p>',17),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","27.html.vue"]]);export{h as default};
