import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as t,f as r}from"./app.8a7d9ef5.js";const n={},s=r('<h1 id="\u738B\u731B" tabindex="-1"><a class="header-anchor" href="#\u738B\u731B" aria-hidden="true">#</a> \u738B\u731B</h1><blockquote><p>\u5317\u6D77\u738B\u731B\uFF0C\u5C11\u597D\u5B66\uFF0C\u501C\u50A5\u6709\u5927\u5FD7\uFF0C\u4E0D\u5C51\u7EC6\u52A1\uFF0C\u4EBA\u7686\u8F7B\u4E4B\u3002\u731B\u60A0\u7136\u81EA\u5F97\uFF0C\u9690\u5C45\u534E\u9634\u3002\u95FB\u6853\u6E29\u5165\u5173\uFF0C\u62AB\u8910\u8BE3\u4E4B\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274 \u2022 \u5377\u4E5D\u5341\u4E5D \u2022 \u664B\u7EAA\u5EFF\u4E00\u300B</strong></p><p><strong>\u5386\u53F2\u4E2D\uFF0C\u5F88\u591A\u4F60\u4EE5\u4E3A\u4E92\u76F8\u4E0D\u6328\u7740\u7684\u4EBA\u7269\uFF0C\u5176\u5B9E\u90FD\u66FE\u53D1\u751F\u8FC7\u4E00\u4E9B\u795E\u5947\u7684\u5173\u7CFB\u3002</strong></p><p>\u5341\u516D\u56FD\u65F6\u671F\uFF0C\u53EF\u4EE5\u88AB\u79F0\u4F5C\u7B2C\u4E00\u6D41\u4EBA\u7269\u7684\uFF0C\u5927\u62B5\u6709\u524D\u79E6\u7684\u738B\u731B\uFF0C\u524D\u71D5\u7684\u6155\u5BB9\u606A\u3001\u6155\u5BB9\u5782\uFF0C\u4E1C\u664B\u7684\u6853\u6E29\u3001\u8C22\u5B89\u7B49\u3002\u8FD9\u5F53\u4E2D\uFF0C<strong><code>\u738B\u731B</code>\u7684\u7ECF\u5386\u6709\u4E00\u70B9\u5F88\u4E0D\u540C\u5BFB\u5E38</strong>\uFF1A\u4ED6\u540C\u65F6\u89C1\u8FC7\u4E24\u5927\u654C\u56FD\u7684\u4E24\u5927\u91CD\u8981\u4EBA\u7269\uFF0C\u4E00\u662F\u71D5\u56FD\u7684<code>\u6155\u5BB9\u5782</code>\uFF1B\u4E8C\u6765\u4E5F\u66F4\u795E\u5947\u7684\uFF0C\u662F\u4ED6\u5C45\u7136\u8FD8\u89C1\u8FC7<code>\u6853\u6E29</code>\u3002</p><p>\u5F53\u65F6\u738B\u731B\u8FD8\u53EA\u662F\u4E00\u4E2A\u666E\u901A\u81F3\u6781\u7684\u8BFB\u4E66\u4EBA\uFF0C\u5728\u6853\u6E29\u7B2C\u4E00\u6B21\u5317\u4F10\u65F6\uFF0C\u4ED6\u4E3B\u52A8\u62DC\u4F1A\u6853\u6E29\uFF0C\u5E76\u4E14\u5F97\u5230\u4E86\u6853\u6E29\u7684\u6781\u5927\u8BA4\u540C\u3002\u4F46\u5F53\u6853\u6E29\u7B2C\u4E00\u6B21\u5317\u4F10\u5931\u8D25\uFF0C\u51C6\u5907\u64A4\u8D70\u65F6\uFF0C\u5C3D\u7BA1\u6853\u6E29\u529B\u9080\u738B\u731B\u540C\u53BB\uFF0C\u738B\u731B\u5374\u9009\u62E9\u4E86\u62D2\u7EDD\uFF0C\u4F9D\u65E7\u7559\u5728\u5173\u4E2D\u3002</p><p>\u5F7C\u65F6\u738B\u731B\u53EA\u6709\u4E09\u5341\u5C81\uFF0C\u4F46\u5176\u5728\u6574\u4F53\u8FC7\u7A0B\u4E2D\u7684\u8003\u8651\uFF0C\u5DF2\u7ECF\u8DB3\u53EF\u7AA5\u89C1\u8D85\u5353\u7684\u667A\u6167\uFF0C\u4EE5\u53CA\u5BF9\u672A\u6765\u7684\u6D1E\u89C1\u3002</p><p>\u6C38\u548C\u5341\u5E74\uFF0C\u6853\u6E29\u7B2C\u4E00\u6B21\u63D0\u5175\u5317\u4E0A\u3002\u6B64\u6B21\u5317\u4F10\u8FDB\u884C\u7684\u9887\u4E3A\u987A\u5229\uFF0C\u4E0D\u4E45\u5C31\u653B\u5165\u4E86\u5173\u4E2D\u3002</p><p>\u80FD\u53D6\u5F97\u5982\u6B64\u6218\u7EE9\uFF0C\u4E0E\u5F53\u65F6\u5317\u65B9\u7684\u65F6\u5C40\u6709\u7740\u83AB\u5927\u5173\u7CFB\uFF1A\u540E\u8D75\u5728\u7687\u5E1D\u77F3\u864E\u6B7B\u540E\u968F\u5373\u5206\u88C2\uFF0C\u5317\u65B9\u91CD\u65B0\u9677\u5165\u5927\u4E71\u3002\u4E4B\u540E\uFF0C\u6709\u4E24\u652F\u52BF\u529B\u9010\u6E10\u5D1B\u8D77\uFF0C\u4EE5\u51FD\u8C37\u5173\u4E3A\u754C\uFF0C<strong>\u5173\u4E2D\u4E4B\u5730\u88AB\u6C10\u65CF\u4EBA\u6240\u5360\u636E\uFF08\u524D\u79E6\uFF09\uFF1B\u51FD\u8C37\u5173\u4EE5\u897F\uFF0C\u5219\u6210\u4E86\u9C9C\u5351\u6155\u5BB9\u6C0F\u7684\u5730\u76D8\uFF08\u524D\u71D5\uFF09\u3002</strong></p><p>\u6853\u6E29\u6B64\u6B21\u5317\u4F10\u7684\u76EE\u6807\uFF0C\u662F\u5DF2\u88AB\u524D\u79E6\u5360\u636E\u7684\u5173\u4E2D\u3002\u5F53\u65F6\u6C10\u65CF\u4EBA\u7ACB\u8DB3\u672A\u7A33\uFF0C\u6853\u6E29\u8D70\u5218\u90A6\u5F53\u5E74\u7684\u8001\u8DEF\uFF0C\u53D6\u9053\u4ECE\u6B66\u5173\u8FDB\u5165\u5173\u4E2D\uFF0C\u5728\u5927\u80DC\u524D\u79E6\u519B\u961F\u540E\uFF0C\u5927\u519B\u8FDB\u81F3\u705E\u4E0A\u3002</p><blockquote><p>\u987A\u4FBF\u4E00\u63D0\uFF0C\u8FD9\u573A\u5927\u6218\u4E2D\uFF0C\u53CC\u65B9\u7684\u7EDF\u5E05\u5206\u522B\u662F\u6853\u6E29\u4E4B\u5F1F<code>\u6853\u51B2</code>\uFF0C\u548C\u524D\u79E6\u4E1E\u76F8\u82FB\u96C4\u2014\u2014\u82FB\u96C4\u5373\u662F\u540E\u6765\u5927\u79E6\u5929\u738B<code>\u82FB\u575A</code>\u7684\u7236\u4EB2\u3002</p></blockquote><p>\u800C\u8FD9\u573A\u5927\u6218\u53D1\u751F\u7684\u5730\u65B9\uFF0C\u5728\u4E00\u5343\u4E03\u767E\u591A\u5E74\u524D\uFF0C\u5C31\u88AB\u53EB\u505A<code>\u767D\u9E7F\u539F</code>\u3002</p><p>\u6853\u6E29\u5927\u519B\u51FB\u8D25\u524D\u79E6\u519B\u961F\u540E\uFF0C\u5173\u4E2D\u5DDE\u53BF\u7EB7\u7EB7\u7EB3\u964D\u3002\u767E\u59D3\u6276\u8001\u643A\u5E7C\u524D\u6765\u56F4\u89C2\uFF0C\u5176\u4E2D\u6709\u8006\u8001\u75DB\u54ED\u4E0D\u5DF2\uFF0C\u201C<strong>\u4E0D\u56FE\u4ECA\u65E5\u590D\u7779\u5B98\u519B</strong>\uFF01\u201D\u2014\u2014\u81EA\u664B\u610D\u5E1D\u4E8E\u5EFA\u5174\u56DB\u5E74\u5728\u957F\u5B89\u88AB\u5308\u5974\u4FD8\u864F\u540E\uFF0C\u664B\u671D\u52BF\u529B\u9000\u51FA\u5173\u4E2D\u5DF2\u7ECF\u5FEB\u56DB\u5341\u5E74\u65F6\u95F4\u3002</p><p><strong>\u800C\u738B\u731B\uFF0C\u5C31\u5728\u8FD9\u4E9B\u56F4\u89C2\u7684\u4EBA\u5F53\u4E2D\u3002</strong></p><p>\u738B\u731B\u662F\u5317\u6D77\u4EBA\uFF0C\u56E0\u5929\u4E0B\u5927\u4E71\u800C\u9690\u5C45\u4E8E\u534E\u9634\u3002\u4F46\u6240\u6709\u80FD\u5728\u5386\u53F2\u4E0A\u7559\u4E0B\u540D\u5B57\u7684\u9690\u58EB\uFF0C\u90FD\u7B97\u4E0D\u4E0A\u771F\u9690\u58EB\uFF0C\u524D\u6709\u8BF8\u845B\u4EAE\uFF0C\u5982\u4ECA\u6709\u738B\u731B\u3002\u5728\u6853\u6E29\u5927\u519B\u8FDB\u5165\u5173\u4E2D\u540E\uFF0C\u738B\u731B\u968F\u5373\u524D\u6765\u62DC\u89C1\u3002</p><p>\u6853\u6E29\u5BF9\u738B\u731B\u7684\u7B2C\u4E00\u5370\u8C61\u662F\u908B\u9062\u3002\u53F2\u8F7D\uFF0C<strong>\u738B\u731B\u201C\u62AB\u8910\u8BE3\u4E4B\uFF0C\u626A\u8671\u800C\u8C08\u201D</strong>\uFF0C\u8EAB\u4E0A\u62AB\u7740\u4E00\u6761\u7834\u9EBB\u888B\uFF0C\u4E00\u8FB9\u6324\u8671\u5B50\u4E00\u8FB9\u5439\u725B\u3002\u6853\u6E29\u51FA\u8EAB\u4E8E\u58EB\u65CF\u4E4B\u5BB6\uFF0C\u738B\u731B\u8FD9\u79CD\u5BF9\u4E2A\u4EBA\u536B\u751F\u7684\u8BB2\u7A76\u7A0B\u5EA6\uFF0C\u5728\u6853\u6E29\u773C\u91CC\u5DF2\u7ECF\u4E0D\u662F\u4E0D\u62D8\u5C0F\u8282\u4E86\uFF0C\u800C\u662F\u4EE4\u4EBA\u4F5C\u5455\u3002</p><p>\u4F46\u6853\u6E29\u8FD8\u662F\u5BF9\u738B\u731B\u53E6\u773C\u76F8\u770B\uFF0C\u56E0\u4E3A\u738B\u731B\u5728\u8C08\u53CA\u5929\u4E0B\u5927\u52BF\u65F6\uFF0C\u6761\u5206\u7F15\u6790\uFF0C\u97AD\u8F9F\u5165\u91CC\uFF0C\u4EE4\u4EBA\u5F88\u662F\u52A8\u5BB9\u3002</p><p>\u8FD9\u91CC\u6709\u4E2A\u80CC\u666F\u9700\u8981\u5927\u5BB6\u7A0D\u5FAE\u6CE8\u610F\u4E00\u4E0B\uFF1A</p><p>\u5982\u4ECA\u7537\u4EBA\u804A\u8D77\u56FD\u9645\u653F\u6CBB\uFF0C\u5C31\u8DDF\u5973\u4EBA\u804A\u516B\u5366\u4E00\u6837\u6B63\u5E38\uFF0C\u4F46\u5728\u5F53\u65F6\u90A3\u4E2A\u5E74\u4EE3\uFF0C\u901A\u8BAF\u6781\u4E0D\u53D1\u8FBE\uFF0C\u5C24\u5176\u50CF\u738B\u731B\u8FD9\u6837\u7684\u9690\u58EB\uFF0C\u63A5\u53D7\u4FE1\u606F\u7684\u6E20\u9053\u4E0D\u4EC5\u6709\u9650\uFF0C\u800C\u4E14\u6700\u540E\u4F20\u5230\u8033\u6735\u91CC\u7684\u6D88\u606F\uFF0C\u66F4\u662F\u4E0D\u77E5\u9053\u5DF2\u7ECF\u8FC7\u4E86\u591A\u5C11\u624B\u4E86\u3002</p><p>\u5728\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0C\u738B\u731B\u4F9D\u7136\u80FD\u4ECE\u6709\u9650\u7684\u4FE1\u606F\u4E2D\u5F97\u51FA\u8BA9\u6853\u6E29\u8D5E\u540C\u7684\u7ED3\u8BBA\uFF0C\u66F4\u53EF\u89C1\u5176\u667A\u6167\u8D85\u7FA4\u3002</p><p>\u5728\u542C\u4E86\u738B\u731B\u7684\u4E00\u756A\u8C08\u8BBA\u540E\uFF0C\u6853\u6E29\u5BF9\u738B\u731B\u5F88\u662F\u6B23\u8D4F\uFF0C\u4E8E\u662F\u4FBF\u5C06\u5FC3\u4E2D\u7684\u7591\u60D1\u5411\u738B\u731B\u8BF4\u51FA\uFF1A\u6211\u5949\u5929\u5B50\u4E4B\u547D\u7387\u9886\u5341\u4E07\u5927\u519B\u8FDB\u5165\u5173\u4E2D\uFF0C\u4E3A\u767E\u59D3\u526A\u9664\u8D3C\u5BB3\uFF0C\u53EF\u4E3A\u4EC0\u4E48\u5173\u4E2D\u7684\u8C6A\u6770\u5374\u6CA1\u6709\u6765\u6295\u5954\u6211\u5462\uFF1F</p><p>\u738B\u731B\u56DE\u7B54\u9053\uFF0C\u60A8\u4E0D\u8FDC\u5343\u91CC\u800C\u6765\uFF0C\u6DF1\u5165\u654C\u5883\u3002**\u53EF\u5982\u4ECA\u957F\u5B89\u8FD1\u5728\u54AB\u5C3A\uFF0C\u60A8\u5374\u5C6F\u5175\u4E8E\u705E\u4E0A\uFF0C\u4E0D\u6E21\u705E\u6C34\u3002**\u5927\u5BB6\u90FD\u4E0D\u77E5\u9053\u60A8\u7A76\u7ADF\u662F\u4EC0\u4E48\u5FC3\u601D\uFF0C\u6240\u4EE5\u624D\u72B9\u8C6B\u89C2\u671B\uFF0C\u4E0D\u6562\u524D\u6765\u6295\u5954\u3002</p><p>\u6853\u6E29\u9ED8\u7136\u4E0D\u5E94\uFF0C\u826F\u4E45\u624D\u8BF4\uFF0C\u60A8\u89C1\u8BC6\u6DF1\u523B\uFF0C\u6C5F\u4E1C\u6CA1\u6709\u50CF\u4F60\u8FD9\u6837\u7684\u4EBA\u624D\u554A\uFF01\u5E76\u968F\u5373\u6388\u4E88\u4E86\u738B\u731B\u4E00\u4E2A\u5B98\u804C\u3002</p><p>\u6853\u6E29\u7684\u8FD9\u756A\u6001\u5EA6\uFF0C\u65E0\u7591\u8BF4\u660E\u4E86\u4ED6\u662F\u8D5E\u540C\u738B\u731B\u7684\u89C2\u70B9\u7684\u3002\u800C\u4E14\u7ED3\u5408\u6853\u6E29\u7684\u884C\u519B\u8DEF\u7EBF\u4EE5\u53CA\u957F\u5B89\u9644\u8FD1\u7684\u5730\u7406\u73AF\u5883\u6765\u770B\uFF0C\u4E8B\u5B9E\u4E5F\u7684\u786E\u5982\u6B64\uFF1A</p><p>\u6853\u6E29\u4ECE\u6C5F\u9675\u51FA\u5175\uFF0C\u4E00\u8DEF\u884C\u81F3\u705E\u4E0A\uFF0C\u8D70\u7684\u90FD\u662F\u9646\u8DEF\u3002\u63A5\u4E0B\u6765\u5982\u679C\u60F3\u653B\u514B\u957F\u5B89\uFF0C\u5C31\u5FC5\u987B\u6E21\u8FC7\u705E\u6C34\uFF0C\u5982\u6B64\u624D\u80FD\u5230\u8FBE\u957F\u5B89\u57CE\u4E0B\u3002</p><p>\u4F46\u5982\u4ECA\u6853\u6E29\u5374\u505C\u5175\u4E8E\u705E\u4E0A\uFF0C\u4E0D\u6E21\u705E\u6C34\u3002<strong>\u5176\u8FDB\u53D6\u4E4B\u610F\u4E0D\u8DB3\uFF0C\u529B\u6C42\u81EA\u4FDD\u4E4B\u52BF\uFF0C\u5DF2\u7ECF\u662D\u7136\u3002</strong></p><p>\u5173\u4E2D\u8C6A\u6770\u6478\u4E0D\u900F\u6853\u6E29\u7684\u4E3B\u610F\uFF0C\u81EA\u7136\u4E0D\u80AF\u8F7B\u6613\u8868\u660E\u7ACB\u573A\u3002</p><p>\u679C\u7136\uFF0C\u6853\u6E29\u5728\u5173\u4E2D\u5E76\u6CA1\u6709\u5F85\u591A\u4E45\u3002\u4E00\u4E2A\u591A\u6708\u4EE5\u540E\uFF0C\u6853\u6E29\u4E0E\u82FB\u96C4\u518D\u6218\u4E8E\u767D\u9E7F\u539F\uFF0C\u6B64\u56DE\u6853\u6E29\u5927\u8D25\u3002\u540C\u65F6\uFF0C\u524D\u79E6\u5175\u5728\u5173\u4E2D\u575A\u58C1\u6E05\u91CE\uFF0C\u4EE4\u6853\u6E29\u5927\u519B\u65E0\u5904\u89C5\u98DF\uFF0C\u53EA\u80FD\u64A4\u8D70\u3002</p><p>\u4E34\u884C\u524D\uFF0C\u6853\u6E29\u9080\u8BF7\u738B\u731B\u4E00\u8D77\u64A4\u9000\uFF0C\u4F46\u738B\u731B\u5374\u8868\u793A\u4E86\u62D2\u7EDD\uFF0C\u4ED6\u51B3\u5B9A\u7EE7\u7EED\u7559\u5728\u5173\u4E2D\uFF0C\u89C2\u671B\u5C40\u52BF\u3002</p><p>\u4E00\u5E74\u540E\uFF0C\u738B\u731B\u624D\u7EC8\u4E8E\u9047\u5230\u4E86\u4ED6\u7684\u771F\u547D\u5929\u5B50\u82FB\u575A\uFF0C\u5E76\u8FDB\u800C\u5728\u5386\u53F2\u4E2D\u8FF8\u53D1\u51FA\u8000\u4EBA\u7684\u5149\u5F69\u3002</p><p>\u4ECE\u53F2\u4E66\u7684\u8BB0\u8F7D\u6765\u770B\uFF0C\u738B\u731B\u7684\u884C\u52A8\u4F3C\u4E4E\u6709<code>\u77DB\u76FE</code>\u4E4B\u5904\uFF1A\u4E3A\u4EC0\u4E48\u4E4B\u524D\u4E3B\u52A8\u53BB\u89C1\u6853\u6E29\u7684\u662F\u4ED6\uFF0C\u4F46\u5728\u6853\u6E29\u8868\u8FBE\u8D4F\u8BC6\u4E4B\u540E\uFF0C\u4E0D\u613F\u610F\u8DDF\u968F\u64A4\u8D70\u4E5F\u662F\u4ED6\uFF1F</p><p>\u8981\u60F3\u641E\u6E05\u695A\u8FD9\u4E2A\u95EE\u9898\uFF0C\u6211\u4EEC\u5C31\u5F97\u5148\u8F6C\u6362\u89C6\u89D2\uFF0C\u4ECE\u6853\u6E29\u7684\u5317\u4F10\u8BF4\u8D77\uFF0C\u53EA\u6709\u89E3\u91CA\u6E05\u695A\u4E86\u8FD9\u4EF6\u4E8B\uFF0C\u624D\u80FD\u5BF9\u738B\u731B\u524D\u540E\u77DB\u76FE\u7684\u9009\u62E9\u5F62\u6210\u4E00\u4E2A\u6E05\u6670\u7684\u8BA4\u77E5\u3002</p><p>**\u6853\u6E29\u7684\u5317\u4F10\uFF0C\u5386\u6765\u5B58\u5728\u4E89\u8BAE\u3002**\u6709\u4EBA\u8BA4\u4E3A\uFF0C\u6853\u6E29\u7684\u519B\u4E8B\u5B9E\u529B\u5728\u5F53\u65F6\u7B97\u5F97\u4E0A\u662F\u4E00\u6D41\u6C34\u5E73\uFF0C\u6700\u8D77\u7801\u6709\u5E73\u5B9A\u8700\u5730\u3001\u706D\u4EA1\u6210\u6C49\u7684\u519B\u529F\u5728\u90A3\u91CC\u6446\u7740\u3002</p><p>\u4F46\u540C\u65F6\uFF0C\u53C8\u6709\u89C2\u70B9\u8BA4\u4E3A\u6853\u6E29\u519B\u4E8B\u5B9E\u529B\u5E73\u5E73\uFF0C\u4E09\u6B21\u5317\u4F10\u51E0\u4E4E\u6CA1\u6709\u53D6\u5F97\u591A\u5C11\u6218\u7EE9\uFF0C\u800C\u4E14\u8D25\u591A\u80DC\u5C11\uFF0C\u5C24\u5176\u662F\u7B2C\u4E09\u6B21\uFF0C\u88AB\u6155\u5BB9\u5782\u5927\u8D25\u4E8E\u678B\u5934\uFF0C\u635F\u5931\u60E8\u91CD\uFF0C\u80FD\u6709\u5982\u6B64\u8D25\u4ED7\uFF0C\u5C31\u6839\u672C\u770B\u4E0D\u51FA\u6853\u6E29\u4F5C\u4E3A\u540D\u5C06\u7684\u98CE\u91C7\u3002</p><p>\u8FD9\u4E9B\u89C2\u70B9\uFF0C<strong>\u5176\u5B9E\u90FD\u662F\u57FA\u4E8E\u7ED3\u679C\u6765\u8BA8\u8BBA\u6853\u6E29\u7684\u519B\u4E8B\u80FD\u529B</strong>\uFF0C\u4F46\u5374\u5FFD\u7565\u4E86\u5F88\u91CD\u8981\u7684\u4E00\u4E2A\u524D\u63D0\uFF1A</p><p><strong>\u6853\u6E29\u7684\u5317\u4F10\uFF0C\u4ECE\u6765\u5C31\u4E0D\u662F\u5355\u7EAF\u7684\u519B\u4E8B\u884C\u52A8\uFF0C\u800C\u662F\u5E26\u6709\u5F88\u5F3A\u7684<code>\u653F\u6CBB\u76EE\u7684</code>\u3002</strong></p><p>\u4EE5\u7B2C\u4E8C\u6B21\u5317\u4F10\u4E3A\u4F8B\uFF0C\u8FD9\u662F\u6853\u6E29\u7684\u4E09\u6B21\u5317\u4F10\u4E2D\uFF0C\u53D6\u5F97\u6210\u679C\u6700\u5927\u7684\u4E00\u6B21\uFF0C\u4E0D\u4EC5\u6536\u590D\u4E86\u6D1B\u9633\uFF0C\u8FD8\u5C06\u4E1C\u664B\u7684\u8FB9\u5883\u4ECE\u6C49\u6C34\u4E00\u5E26\u63A8\u8FDB\u81F3\u9EC4\u6CB3\u6CBF\u7EBF\u3002</p><p>\u6536\u590D\u65E7\u90FD\uFF0C\u8FD9\u672C\u6765\u5E94\u8BE5\u7B97\u5F97\u4E0A\u662F\u5317\u4F10\u7684\u91CD\u5927\u80DC\u5229\uFF0C\u4E0D\u8BBA\u5F80\u524D\u548C\u5F80\u540E\uFF0C\u8FD9\u90FD\u662F\u4E2D\u56FD\u5386\u53F2\u4E0A\u7684\u540D\u81E3\u4EEC\u68A6\u5BD0\u4EE5\u6C42\u7684\u6210\u5C31\uFF1A\u8BF8\u845B\u4EAE\u5FC3\u5FC3\u5FF5\u5FF5\u201C\u8FD8\u4E8E\u65E7\u90FD\u201D\uFF0C\u5374\u59CB\u7EC8\u6CA1\u6709\u8FDB\u5F97\u4E86\u957F\u5B89\uFF0C\u9051\u8BBA\u6D1B\u9633\uFF1B\u518D\u5F80\u540E\u51E0\u767E\u5E74\uFF0C\u5CB3\u98DE\u6B62\u6B65\u4E8E\u5F00\u5C01\u57CE\u5916\u51E0\u5341\u91CC\u7684\u6731\u4ED9\u9547\uFF0C\u4E5F\u5F92\u7559\u5343\u53E4\u9057\u61BE\u3002</p><p>\u4F46\u6853\u6E29\u7684\u8FD9\u6B21\u5317\u4F10\u5462\uFF1F<strong>\u5728\u6536\u590D\u6D1B\u9633\u4EE5\u540E\uFF0C\u671D\u5EF7\u548C\u6853\u6E29\u4E24\u65B9\u7684\u53CD\u5E94\u90FD\u5F88\u5947\u602A\u3002</strong></p><p>\u6853\u6E29\u81EA\u7136\u662F\u4E0A\u4E66\u671D\u5EF7\uFF0C\u8BF4\u5982\u4ECA\u6D1B\u9633\u6536\u590D\uFF0C\u671D\u5EF7\u5E94\u8BE5\u8FC1\u90FD\u56DE\u6D1B\u9633\u2014\u2014\u8FD9\u662F\u9898\u4E2D\u5E94\u6709\u4E4B\u4E49\u3002</p><p>\u4F46\u671D\u5EF7\u5374\u767E\u822C\u63A8\u8F9E\u3002\u4E00\u5219\u6B64\u65F6\u7684\u6D1B\u9633\u867D\u7136\u6536\u590D\uFF0C\u4F46\u5374\u5904\u5728\u524D\u7EBF\uFF0C\u671D\u5EF7\u7ACB\u8EAB\u4E8E\u6B64\uFF0C\u5927\u6709\u5371\u9669\uFF1B\u4E8C\u5219\u4E5F\u66F4\u91CD\u8981\u7684\uFF0C\u662F\u671D\u5EF7\u4E0D\u613F\u610F\u642C\u5230\u6853\u6E29\u7684\u52BF\u529B\u8303\u56F4\u91CC\u53BB\u3002</p><p>\u8BF7\u6CE8\u610F\uFF0C\u8FD9\u5DF2\u7ECF\u662F\u6853\u6E29<code>\u7B2C\u4E8C\u6B21</code>\u4E0A\u4E66\u671D\u5EF7\u4E86\uFF0C\u800C\u4E14\u6B64\u65F6\u8DDD\u79BB\u6536\u590D\u6D1B\u9633\uFF0C\u5DF2\u7ECF\u8FC7\u53BB\u4E86<code>\u516D\u5E74</code>\u65F6\u95F4\u3002</p><p>\u90A3\u4E48\u5728\u8FD9\u516D\u5E74\u91CC\uFF0C\u6853\u6E29\u6709\u6CA1\u6709\u53D6\u5F97\u4EC0\u4E48\u65B0\u6218\u529F\u5462\uFF1F</p><p>\u7B54\u6848\u662F\u4E5F\u6CA1\u6709\u3002</p><p>\u6853\u6E29\u7B2C\u4E8C\u6B21\u5317\u4F10\uFF0C\u51FA\u5175\u4E0D\u4E45\u4FBF\u653B\u5360\u4E86\u6D1B\u9633\uFF0C\u4F46\u81F3\u6B64\uFF0C\u8FD9\u6B21\u5317\u4F10\u5728\u4E8B\u5B9E\u4E0A\u4E5F\u5C31\u5DF2\u7ECF\u7ED3\u675F\u4E86\u3002\u5F53\u65F6\uFF0C\u6853\u6E29\u6D3E\u9A7B\u4E86\u5730\u65B9\u5B98\uFF0C\u7136\u540E\u4FBF\u643A\u5E26\u4E09\u5343\u6237\u767E\u59D3\u8FD4\u56DE\u6C5F\u9675\uFF0C\u6B64\u540E\u518D\u672A\u51FA\u5F81\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C<strong>\u8FD9\u6B21\u5317\u4F10\u5728\u516D\u5E74\u524D\u8FDB\u5165\u6D1B\u9633\u540E\uFF0C\u65CB\u5373\u64A4\u5175\u7ED3\u675F\u3002</strong></p><p><strong>\u5728\u8FD9\u516D\u5E74\u95F4\uFF0C\u6853\u6E29\u5BF9\u4E8E\u6CB3\u6D1B\u4E00\u5E26\uFF0C\u6839\u672C\u8C08\u4E0D\u4E0A\u7ECF\u8425\u3002</strong></p><p>\u4ED6\u65E2\u6CA1\u6709\u9A7B\u519B\u4E8E\u6B64\uFF0C\u4E5F\u6CA1\u6709\u5C6F\u7530\u57A6\u6B96\uFF0C\u4EC5\u4EC5\u662F\u4EFB\u547D\u4E86\u5730\u65B9\u5B98\u3002\u800C\u6D1B\u9633\u5F97\u4EE5\u5B58\u5728\u4E8E\u4E1C\u664B\u7684\u6CBB\u4E0B\uFF0C\u4E3B\u8981\u4E5F\u4E0D\u662F\u8D56\u4E8E\u519B\u4E8B\u5B9E\u529B\u7684\u5F3A\u5927\uFF0C\u800C\u662F\u56E0\u4E3A\u5317\u65B9\u6218\u4E71\uFF0C\u524D\u79E6\u548C\u524D\u71D5\u6DF7\u6218\uFF0C\u6682\u65F6\u6CA1\u6709\u987E\u5F97\u4E0A\u800C\u5DF2\u3002</p><p>\u5982\u679C\u6853\u6E29\u771F\u7684\u610F\u5728\u5317\u4F10\uFF0C\u90A3\u4E48\u6B63\u786E\u7684\u505A\u6CD5\u5E94\u8BE5\u662F\u79FB\u5175\u5317\u4E0A\uFF0C\u7ECF\u8425\u6D1B\u9633\uFF0C\u5DE9\u56FA\u6218\u679C\uFF0C\u4F3A\u673A\u51FA\u51FB\uFF0C\u4F46\u8FD9\u4E9B\u4E3E\u52A8\uFF0C\u6853\u6E29\u7EDF\u7EDF\u90FD\u6CA1\u6709\u3002</p><p>\u4ED6\u53EA\u662F\u9759\u9759\u5730\u5728\u6C5F\u9675\u5F85\u7740\uFF0C\u76F4\u5230\u6D1B\u9633\u5468\u56F4\u7684\u57CE\u9547\u9010\u6E10\u88AB\u524D\u71D5\u4FB5\u5360\uFF0C\u76F4\u5230\u6700\u7EC8\u53EA\u7559\u4E0B\u6D1B\u9633\u4E00\u5EA7\u5B64\u57CE\uFF0C\u76F4\u5230\u8FD9\u5EA7\u5B64\u57CE\u6700\u7EC8\u5931\u9677\u3002</p><p>\u5728\u6B64\u671F\u95F4\uFF0C\u5BF9\u4E8E\u5317\u65B9\u6536\u590D\u7684\u9886\u571F\uFF0C\u6853\u6E29\u51E0\u4E4E\u6CA1\u6709\u505A\u8FC7\u4EFB\u4F55\u519B\u4E8B\u4E0A\u7684\u90E8\u7F72\u6216\u8005\u652F\u63F4\u3002</p><p>\u81F3\u6B64\uFF0C\u518D\u7ED3\u5408\u738B\u731B\u4E0E\u6853\u6E29\u7684\u5BF9\u8BDD\uFF0C\u4FBF\u53EF\u4EE5\u660E\u767D\u65E0\u8BEF\u7684\u770B\u51FA\u6765\u4E86\uFF1A</p><p><strong>\u6853\u6E29\u5317\u4F10\uFF0C\u5E76\u4E0D\u662F\u771F\u7684\u60F3\u6062\u590D\u6CB3\u5C71\uFF0C\u800C\u53EA\u662F\u5728\u635E\u53D6<code>\u653F\u6CBB\u8D44\u672C</code>\u3002</strong></p><p><strong>\u7B2C\u4E00\u6B21\u5317\u4F10</strong>\uFF0C\u5DF2\u7ECF\u5230\u8FBE\u705E\u4E0A\uFF0C\u5374\u4E0D\u613F\u610F\u8FC7\u6CB3\uFF0C\u672C\u8EAB\u5C31\u5B58\u4E86\u64A4\u9000\u7684\u5FC3\u601D\uFF0C\u6015\u4E07\u4E00\u8FC7\u6CB3\uFF0C\u906D\u9047\u4E0D\u6D4B\u2014\u2014\u8FD9\u4E5F\u6B63\u662F\u738B\u731B\u8DDF\u6853\u6E29\u6240\u8BF4\u7684\u3002</p><p>\u800C\u4E5F\u6B63\u662F\u56E0\u4E3A\u6853\u6E29\u7684\u8FDF\u7591\uFF0C\u624D\u8BA9\u524D\u79E6\u6709\u5598\u606F\u4E4B\u673A\uFF0C\u6700\u7EC8\u96C6\u7ED3\u5175\u529B\u51FB\u8D25\u4E86\u6853\u6E29\u3002\u6853\u6E29\u5728\u635F\u5931\u4E0A\u4E07\u4EBA\u540E\uFF0C\u64A4\u56DE\u6C5F\u9675\u3002</p><p><strong>\u7B2C\u4E8C\u6B21\u5317\u4F10</strong>\uFF0C\u6853\u6E29\u5145\u5206\u5438\u53D6\u4E86\u7ECF\u9A8C\uFF0C\u4E0D\u518D\u8EAB\u72AF\u9669\u5883\uFF0C\u4E00\u65E6\u6709\u6240\u6536\u83B7\u4FBF\u9A6C\u4E0A\u64A4\u56DE\uFF0C\u4EE5\u786E\u4FDD\u80DC\u5229\u679C\u5B9E\u3002\u800C\u4E14\uFF0C\u6536\u590D\u6D1B\u9633\u7684\u529F\u52B3\u4E5F\u8DB3\u591F\u5927\uFF0C\u5927\u5230\u4E0D\u9700\u8981\u518D\u6709\u5176\u4ED6\u522B\u7684\u6218\u529F\u3002</p><p>\u5F53\u65F6\uFF0C\u5982\u679C\u6853\u6E29\u771F\u7684\u4E00\u5FC3\u60F3\u8981\u5317\u4F10\uFF0C\u90A3\u4E48\u4EE5\u6536\u590D\u6D1B\u9633\u7684\u5E95\u5B50\uFF0C\u52A0\u4E0A\u8FD9\u4E9B\u5E74\u6765\u7684\u7528\u5FC3\u7ECF\u8425\uFF0C\u518D\u52A0\u4E0A\u6B63\u597D\u9047\u5230\u5317\u65B9\u6DF7\u4E71\uFF0C\u4E0D\u96BE\u83B7\u5F97\u66F4\u5927\u7684\u6218\u529F\u3002</p><p><strong>\u7136\u540E\u662F\u7B2C\u4E09\u6B21\u5317\u4F10</strong>\uFF0C\u4E5F\u5C31\u662F\u88AB\u6155\u5BB9\u5782\u51FB\u9000\u7684\u90A3\u4E00\u6B21\u3002\u5728\u8FD9\u6B21\u5317\u4F10\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u6853\u6E29\u60C5\u654C\u5192\u8FDB\uFF0C\u672C\u8EAB\u5C31\u6709\u5F88\u591A\u5931\u8BEF\u3002\u4F46\u5728\u540E\u64A4\u4EE5\u540E\uFF0C\u4ED6\u5374\u505A\u4E86\u4E24\u4EF6\u4E8B\uFF1A</p><p>\u7B2C\u4E00\uFF0C\u5C06\u6218\u8D25\u7684\u8FC7\u9519\u63A8\u5378\u5230\u8C6B\u5DDE\u523A\u53F2\u8881\u771F\u7684\u8EAB\u4E0A\uFF0C\u5BFC\u81F4\u8881\u771F\u5E26\u5730\u6295\u5411\u524D\u71D5\uFF1B\u7B2C\u4E8C\uFF0C\u5E9F\u9664\u53F8\u9A6C\u5955\uFF0C\u6539\u7ACB\u53F8\u9A6C\u6631\u4E3A\u5E1D\u3002</p><p>\u6853\u6E29\u7684\u5317\u4F10\uFF0C<strong>\u4ECE\u6765\u90FD\u4E0D\u662F\u4E3A\u4E86\u6536\u590D\u5931\u5730\uFF0C\u800C\u662F\u4EE5\u5317\u4F10\u4E3A\u7531\u6765\u6269\u5927\u6743\u5A01\uFF0C\u4EE5\u8FBE\u5230\u5F04\u6743\u7684\u76EE\u7684\uFF0C\u751A\u81F3\u4E8E\uFF0C\u53D6\u4EE3\u664B\u5BA4\u79F0\u5E1D\u3002</strong></p><p>\u5317\u4F10\u4E0D\u662F\u76EE\u7684\uFF0C\u800C\u662F\u624B\u6BB5\u3002</p><p>**\u800C\u65E2\u7136\u7ACB\u5A01\u624D\u662F\u76EE\u7684\uFF0C\u90A3\u4E48\u5C31\u5BB9\u4E0D\u5F97\u4E00\u4E1D\u4E00\u6BEB\u6709\u635F\u5A01\u671B\u7684\u4E8B\u60C5\u53D1\u751F\u4E86\u3002**\u4E5F\u6B63\u56E0\u4E3A\u5982\u6B64\uFF0C\u624D\u4F1A\u6709\u7B2C\u4E00\u6B21\u5317\u4F10\u540E\uFF0C\u6B62\u6B65\u4E8E\u705E\u4E0A\uFF1B\u7B2C\u4E8C\u6B21\u5317\u4F10\uFF0C\u65CB\u8FDB\u65CB\u9000\uFF1B\u7B2C\u4E09\u6B21\u5317\u4F10\uFF0C\u8BFF\u8FC7\u4E8E\u8881\u771F\u7684\u4E8B\u60C5\u53D1\u751F\u3002</p><p>\u6853\u6E29\u771F\u6B63\u60F3\u7684\uFF0C<strong>\u4ECE\u6765\u90FD\u662F\u4E0D\u80FD\u906D\u9047\u5927\u8D25\uFF0C\u4F7F\u5A01\u540D\u53D7\u635F\u3002</strong></p><p>\u53EA\u662F\u504F\u504F\u4E0D\u60F3\u4EC0\u4E48\u5C31\u6765\u4EC0\u4E48\uFF0C\u7B2C\u4E00\u6B21\u5927\u8D25\uFF0C\u7B2C\u4E09\u6B21\u53C8\u662F\u5927\u8D25\u3002</p><p>\u800C\u5728\u7406\u987A\u4E86\u6853\u6E29\u7684\u8FD9\u4E9B\u884C\u4E3A\u548C\u8BC9\u6C42\u4E4B\u540E\uFF0C\u518D\u56DE\u5934\u6765\u7406\u89E3\u738B\u731B\u5F53\u65F6\u7684\u9009\u62E9\uFF0C\u5C31\u76F8\u5BF9\u5BB9\u6613\u4E86\u3002</p><p>\u738B\u731B\u6853\u6E29\u8FDB\u5165\u5173\u4E2D\u540E\uFF0C\u4E3B\u52A8\u53BB\u89C1\u6853\u6E29\uFF0C\u4F46\u5374\u53C8\u4E0D\u613F\u610F\u8DDF\u7740\u6853\u6E29\u64A4\u8D70\uFF0C\u5173\u952E\u4E4B\u5904\u5C31\u5728\u4E8E\uFF0C<strong>\u738B\u731B\u5E94\u8BE5\u5DF2\u7ECF\u53D1\u73B0\u4E86\u6853\u6E29\u6240\u63A9\u85CF\u7740\u7684\u5FC3\u8FF9\u3002</strong></p><p>\u867D\u7136\u6B64\u65F6\u7684\u6853\u6E29\u8FD8\u6CA1\u6709\u505A\u51FA\u90A3\u4E9B\u4E8C\u5341\u5E74\u540E\u51C6\u5907\u7BE1\u4F4D\u7684\u4E3E\u52A8\uFF0C\u4F46\u738B\u731B\u4ECE\u6853\u6E29\u5728\u705E\u4E0A\u7684\u72B9\u8C6B\u4E2D\uFF0C\u5E94\u8BE5\u5DF2\u7ECF\u80FD\u591F\u786E\u8BA4\uFF0C\u8FD9\u4E2A\u4EBA\u548C\u4ED6\u7684\u5317\u4F10\uFF0C\u52A8\u673A\u5E76\u6CA1\u6709\u90A3\u4E48\u5355\u7EAF\u3002</p><p>\u5230\u8FD9\u91CC\uFF0C\u738B\u731B\u81EA\u7136\u4F1A\u6709\u6240\u6743\u8861\u3002</p><p>\u4EBA\u4EEC\u5728\u505A\u4E00\u4EF6\u4E8B\u7684\u65F6\u5019\uFF0C\u90FD\u4F1A\u6709\u81EA\u5DF1\u60F3\u8981\u8FFD\u6C42\u7684\u7ED3\u679C\u3002\u4E00\u822C\u800C\u8A00\uFF0C\u4E00\u4EF6\u4E8B\u60C5\u7684\u5B8C\u6210\uFF0C\u53EF\u4EE5\u8FBE\u6210\u4E00\u4E2A\u7ED3\u679C\u3002\u4F46\u6709\u65F6\u5019\uFF0C\u60C5\u51B5\u4F1A\u66F4\u590D\u6742\u4E00\u4E9B\uFF0C\u53EF\u4EE5\u540C\u65F6\u8FBE\u6210\u4E24\u4E2A\u6216\u662F\u4EE5\u4E0A\u7684\u7ED3\u679C\u3002</p><p>\u6BD4\u5982\uFF0C\u4F60\u53BB\u5F00\u62D3\u5E02\u573A\u3002\u6700\u7EC8\u65E2\u5F97\u5230\u4E86\u5DE5\u4F5C\u4E1A\u7EE9\uFF0C\u53C8\u6536\u83B7\u4E86\u4EBA\u8109\uFF0C\u8FD9\u5C31\u53EF\u4EE5\u7B97\u4F5C\u662F\u4E00\u4E3E\u53CC\u5F97\u3002</p><p>\u4F46\u8FD9\u53EA\u662F\u6700\u597D\u7684\u60C5\u51B5\uFF0C**\u66F4\u591A\u65F6\u5019\uFF0C\u7ED3\u679C\u4E4B\u95F4\u4F1A\u5F62\u6210\u4E3B\u6B21\u4E4B\u522B\u3001\u8FDC\u8FD1\u4E4B\u522B\uFF0C\u751A\u81F3\u4E8E\u5F7C\u6B64\u51B2\u7A81\u3002**\u4E5F\u5C31\u662F\u8BF4\uFF0C\u51B2\u4E86\u5F53\u671F\u4E1A\u7EE9\uFF0C\u53EF\u80FD\u5C31\u4F1A\u4E27\u5931\u8FD9\u6B21\u62D3\u5C55\u4EBA\u9645\u5173\u7CFB\u7684\u673A\u4F1A\uFF1B\u6293\u4F4F\u4E86\u6536\u83B7\u4EBA\u8109\u7684\u673A\u4F1A\uFF0C\u53C8\u4F1A\u5BFC\u81F4\u5E74\u7EC8\u7684\u7EE9\u6548\u65E0\u6CD5\u5B8C\u6210\uFF0C\u8FD9\u79CD\u65F6\u5019\uFF0C\u4F60\u8981\u600E\u4E48\u9009\uFF1F</p><p>\u738B\u731B\u73B0\u5728\u9762\u4E34\u7684\uFF0C\u5C31\u662F\u8FD9\u79CD\u5C40\u9762\u3002</p><p>\u6853\u6E29\u624D\u5177\u8DB3\u591F\uFF0C\u5982\u679C\u4ED6\u80FD\u6709\u8BF8\u845B\u4EAE\u7684\u5FE0\u8D1E\uFF0C\u4E00\u5FC3\u5317\u4F10\uFF0C\u6062\u590D\u5931\u5730\uFF0C\u90A3\u4E48\u81EA\u5DF1\u8DDF\u4ED6\uFF0C\u5E94\u8BE5\u5C31\u80FD\u5B9E\u73B0\u5E73\u751F\u7684\u62B1\u8D1F\u3002</p><p>\u4F46\u4E07\u4E00\u6853\u6E29\u8D70\u4E0A\u4E86\u53E6\u4E00\u6761\u8DEF\uFF0C\u90A3\u81EA\u5DF1\u662F\u4E0D\u662F\u4E5F\u5F97\u628A\u672A\u6765\u642D\u8FDB\u53BB\uFF0C\u8DDF\u7740\u4ED6\u5728\u593A\u6743\u7BE1\u4F4D\u7684\u8DEF\u4E0A\u8D8A\u8D70\u8D8A\u8FDC\uFF1F\u5982\u679C\u67D0\u4E00\u5929\u6853\u6E29\u6B32\u671B\u81A8\u80C0\uFF0C\u771F\u7684\u60F3\u8981\u5F53\u7687\u5E1D\uFF0C\u81EA\u5DF1\u662F\u5E2E\u8FD8\u662F\u4E0D\u5E2E\uFF1F</p><p>\u738B\u731B\u80AF\u5B9A\u4E0D\u613F\u610F\u7275\u6D89\u8FDB\u8FD9\u644A\u70C2\u4E8B\u4E2D\uFF0C\u6240\u4EE5\u624D\u4F1A\u4E00\u5F00\u59CB\u5C31\u9009\u62E9\u653E\u5F03\u6853\u6E29\u3002\u4ED6\u4E58\u5174\u800C\u6765\uFF0C\u8D25\u5174\u800C\u5F52\uFF0C\u867D\u7136\u6853\u6E29\u786E\u5B9E\u770B\u4E2D\u4E86\u4ED6\u7684\u624D\u5E72\uFF0C\u4F46\u4ED6\u5374\u5BF9\u6853\u6E29\u7684\u5FD7\u8DA3\u6DF1\u611F\u5931\u671B\uFF0C\u4E24\u4EBA\u4E0D\u662F\u4E00\u7C7B\u4EBA\uFF0C\u603B\u5F52\u8D70\u4E0D\u5230\u4E00\u8D77\u3002</p><p>\u542C\u4ECE\u4E8E\u81EA\u5DF1\u521D\u5FC3\u7684\u738B\u731B\uFF0C\u6700\u7EC8\u9009\u62E9\u7EE7\u7EED\u7B49\u5F85\uFF0C\u800C\u4E14\u4E5F\u786E\u5B9E\u7B49\u5230\u4E86\u90A3\u4E2A\u80FD\u771F\u6B63\u8BA9\u4ED6\u5B9E\u73B0\u751F\u5E73\u62B1\u8D1F\u7684\u4EBA\u51FA\u73B0\uFF1A</p><p>\u5927\u79E6\u5929\u738B\uFF0C\u82FB\u575A\u3002</p>',77),e=[s];function c(g,d){return o(),t("div",null,e)}const l=p(n,[["render",c],["__file","28.html.vue"]]);export{l as default};
