import{_ as o}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as t,f as e}from"./app.cb44c984.js";const c="/myNotes/assets/img/base/640(23).jpeg",s={},r=e('<h1 id="\u6C49\u671D\u7684\u74E6\u89E3\u4E4B\u59CB" tabindex="-1"><a class="header-anchor" href="#\u6C49\u671D\u7684\u74E6\u89E3\u4E4B\u59CB" aria-hidden="true">#</a> \u6C49\u671D\u7684\u74E6\u89E3\u4E4B\u59CB</h1><blockquote><p>\u592A\u5E38\u6C5F\u590F\u5218\u7109\u89C1\u738B\u5BA4\u591A\u6545\uFF0C\u5EFA\u8BAE\u4EE5\u4E3A\uFF1A\u201C\u56DB\u65B9\u5175\u5BC7\uFF0C\u7531\u523A\u53F2\u5A01\u8F7B\uFF0C\u65E2\u4E0D\u80FD\u7981\uFF0C\u4E14\u7528\u975E\u5176\u4EBA\uFF0C\u4EE5\u81F4\u79BB\u53DB\u3002\u5B9C\u6539\u7F6E\u7267\u4F2F\uFF0C\u9009\u6E05\u540D\u91CD\u81E3\u4EE5\u5C45\u5176\u4EFB\u3002\u201D</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274 \xB7 \u5377\u4E94\u5341\u4E5D \xB7 \u6C49\u7EAA\u4E94\u5341\u4E00\u300B</strong></p><p>\u4E1C\u6C49\u738B\u671D\u5206\u5D29\u79BB\u6790\uFF0C\u6700\u7EC8\u5F62\u6210\u4E09\u56FD\u9F0E\u7ACB\u7684\u5C40\u9762\uFF0C\u5E76\u4E0D\u662F\u56E0\u4E3A\u5F20\u89D2\u7684\u9EC4\u5DFE\u8D77\u4E49\uFF0C\u800C\u662F\u56E0\u4E3A\u4EE5\u4E0A\u5218\u7109\u7684\u8FD9\u6BB5\u5EFA\u8BAE\u3002</p><p>\u6240\u8C13\u7684\u5E73\u5B9A\u9EC4\u5DFE\u4E4B\u4E71\uFF0C\u5176\u5B9E\u662F\u6307\u6D88\u706D\u4E86\u9EC4\u5DFE\u519B\u7684\u4E3B\u529B\uFF0C\u4EE5\u53CA\u9996\u6076\u5F20\u6C0F\u4E09\u5144\u5F1F\u3002\u4F46\u5B9E\u9645\u4E0A\uFF0C\u6B64\u540E\u7684\u51E0\u5341\u5E74\u95F4\uFF0C\u5404\u79CD\u5404\u6837\u7684\u9EC4\u5DFE\u4F59\u515A\u5C42\u51FA\u4E0D\u7A77\uFF0C\u7978\u4E71\u56DB\u65B9\u3002</p><p>\u56E0\u6B64\uFF0C\u65F6\u4EFB\u592A\u5E38\u7684\u5218\u7109\u5411\u671D\u5EF7\u5EFA\u8BAE\uFF1A</p><blockquote><p><strong>\u5C06\u523A\u53F2\u6539\u4E3A\u5DDE\u7267</strong></p></blockquote><p>\u8FD9\u4E2A\u63D0\u8BAE\uFF0C\u76F4\u63A5\u5BFC\u81F4\u4E86\u6C49\u672B\u7684\u519B\u9600\u7EB7\u4E89\u3002\u5177\u4F53\u539F\u56E0\u4F55\u5728\uFF0C\u6211\u4EEC\u4ECA\u5929\u8BD5\u7740\u6765\u89E3\u91CA\u4E00\u4E0B\u3002</p><p><strong>\u9996\u5148\uFF0C\u6211\u4EEC\u8981\u89E3\u91CA\u4E00\u4E0B\u884C\u653F\u533A\u5212\u8FD9\u56DE\u4E8B\u3002</strong></p><p>\u79E6\u59CB\u7687\u7EDF\u4E00\u4E2D\u56FD\u4EE5\u540E\uFF0C\u63A8\u884C\u90E1\u53BF\u5236\uFF0C\u5C06\u5929\u4E0B\u5206\u4E3A\u4E09\u5341\u516D\u90E1\u2014\u2014\u4F60\u53EF\u4EE5\u628A\u5B83\u7406\u89E3\u4E3A\u4E09\u5341\u516D\u4E2A\u7701\u3002</p><p>\u4E09\u5341\u516D\u662F\u4E00\u4E2A\u5F88\u597D\u7684\u6570\u5B57\u3002\u4E3A\u4EC0\u4E48\u5462\uFF1F\u56E0\u4E3A<strong>\u79E6\u6C49\u4E4B\u4EA4\uFF0C\u6570\u5B57\u4EE5\u516D\u4E3A\u5C0A</strong>\uFF0C\u6240\u4EE5\u624D\u4F1A\u6709\u79E6\u59CB\u7687\u5206\u5929\u4E0B\u4E3A\u4E09\u5341\u516D\u90E1\u3001\u94F8\u91D1\u4EBA\u5341\u4E8C\u3001\u5F99\u5929\u4E0B\u8C6A\u5BCC\u65BC\u54B8\u9633\u5341\u4E8C\u4E07\uFF0C\u7B49\u7B49\u3002\u751A\u81F3\u4E8E\u5728\u53F8\u9A6C\u8FC1\u5199\u300A\u53F2\u8BB0\u300B\u7684\u65F6\u5019\uFF0C\u4F5C\u4E3A\u5168\u4E66\u603B\u7EB2\u7684\u672C\u7EAA\u90FD\u662F\u5341\u4E8C\u7BC7\u3002\u6B64\u5916\uFF0C\u4ED6\u8FD8\u6709\u4E00\u7BC7\u8868\uFF0C\u53EB\u300A\u5341\u4E8C\u8BF8\u4FAF\u5E74\u8868\u300B\uFF08\u91CC\u9762\u6709\u5341\u4E09\u56FD\uFF09\u3002</p><p><img src="'+c+'" alt="demo" loading="lazy"></p><p><strong>\u79E6\u90E1\u7684\u5B9E\u9645\u6570\u76EE\uFF0C\u5386\u6765\u4F17\u8BF4\u7EB7\u7EAD\u3002\u4F46\u662F\u201C\u79E6\u4E09\u5341\u516D\u90E1\u201D\u662F\u6700\u521D\u7684\u8BF4\u6CD5\uFF0C\u662F\u90E1\u53BF\u5236\u5728\u5168\u56FD\u83B7\u5F97\u6210\u529F\u63A8\u5E7F\u7684\u6807\u5FD7\uFF0C\u6D41\u4F20\u4E5F\u6700\u5E7F\uFF0C\u6545\u6211\u4EEC\u91C7\u7528\u6B64\u8BF4\u3002</strong></p><p>\u5F53\u7136\uFF0C\u8FD9\u53EA\u662F\u8868\u9762\u7684\u597D\u5904\u3002\u771F\u6B63\u7684\u597D\u5904\u5728\u4E8E\uFF0C\u4E09\u5341\u516D\u8FD9\u4E2A\u6570\u5B57\u4E0D\u591A\u4E0D\u5C11\uFF0C\u4F5C\u4E3A\u4E2D\u592E\u4E0B\u4E00\u7EA7\u7684\u884C\u653F\u533A\u5212\uFF0C\u5F88<strong>\u4FBF\u4E8E\u4E2D\u592E\u7BA1\u7406</strong>\u3002\u591A\u4E86\uFF0C\u7BA1\u7406\u8D77\u6765\u5F88\u56F0\u96BE\uFF1B\u5C11\u4E86\uFF0C\u53C8\u5BB9\u6613\u9020\u6210\u5730\u65B9\u96C6\u6743\u3002</p><p>\u968F\u7740\u65F6\u95F4\u7684\u63A8\u79FB\uFF0C\u5230\u4E1C\u6C49\u65F6\u5019\uFF0C\u90E1\u4EE5\u53CA\u540C\u4E00\u7EA7\u7684\u56FD\uFF0C\u9010\u6E10\u53D1\u5C55\u5230\u4E86\u4E5D\u5341\u516B\u4E2A\u3002\u8FD9\u65F6\u5019\uFF0C\u4E2D\u592E\u76F4\u63A5\u7BA1\u7406\u8D77\u6765\u5C31\u5F88\u662F\u4E0D\u4FBF\u4E86\uFF0C\u4F46\u662F\u53C8\u4E0D\u60F3\u8FC7\u5EA6\u5206\u6743\uFF0C\u600E\u4E48\u529E\u5462\uFF1F\u4E8E\u662F\u4FBF\u4EA7\u751F\u4E86\u4E00\u4E2A\u6298\u4E2D\u7684\u529E\u6CD5\uFF1A<strong>\u8BBE\u7ACB\u523A\u53F2</strong>\u3002</p><p>\u5177\u4F53\u800C\u8A00\uFF0C\u5C31\u662F\u5C06\u5929\u4E0B\u7684\u8FD1\u4E00\u767E\u4E2A\u90E1\uFF0C\u5212\u5206\u4E3A\u5341\u4E8C\u4E2A\u523A\u53F2\u90E8\u53CA\u53F8\u96B6\u6821\u5C09\u90E8\u3002\u5728\u90E1\u4E2D\uFF0C\u884C\u653F\u4F9D\u7136\u7531\u90E1\u592A\u5B88\u8D1F\u8D23\uFF0C\u4F46\u8BF8\u5982\u90E1\u4E2D\u5B98\u5458\u8003\u5BDF\u3001\u5730\u65B9\u98CE\u5316\u3001\u7A3D\u67E5\u6355\u76D7\u7B49\u884C\u4E3A\uFF0C\u5219\u4E0A\u5F52\u7531\u8D1F\u8D23\u8BE5\u90E1\u7684\u523A\u53F2\u8D1F\u8D23\u3002</p><p>\u8FD9\u4E00\u5236\u5EA6\u5728\u6C49\u6B66\u5E1D\u65F6\u671F\u8BBE\u7ACB\uFF0C\u5230\u4E1C\u6C49\u65F6\u804C\u6743\u8BBE\u7F6E\u5DF2\u7ECF\u5341\u5206\u6210\u719F\u3002\u4E00\u76F4\u5230\u9EC4\u5DFE\u4E4B\u4E71\u4EE5\u540E\uFF0C\u5176\u8FD0\u8F6C\u624D\u51FA\u73B0\u95EE\u9898\u3002</p><blockquote><p>\u627F\u5E73\u4E4B\u4E16\uFF0C\u5C3D\u7BA1\u523A\u53F2\u7684\u804C\u8D23\u4E4B\u4E00\u662F\u6355\u76D7\uFF0C\u4F46\u6240\u8C13\u7684\u76D7\u8D3C\u56E2\u4F19\u80FD\u4E0A\u767E\u4EBA\u5C31\u4E0D\u9519\u4E86\uFF0C\u523A\u53F2\u53EA\u8981\u5E26\u70B9\u6B63\u89C4\u519B\uFF0C\u6D88\u706D\u8FD9\u7FA4\u8D2B\u56F0\u4EBA\u53E3\u662F\u5206\u5206\u949F\u7684\u4E8B\u3002</p></blockquote><blockquote><p>**\u53EF\u9EC4\u5DFE\u4E4B\u4E71\u4EE5\u540E\uFF0C\u60C5\u51B5\u5927\u6709\u4E0D\u540C\u3002**\u5730\u65B9\u4E0A\u52A8\u8F84\u5C31\u6709\u51E0\u5341\u4E07\u7684\u9EC4\u5DFE\u4F59\u515A\uFF0C\u523A\u53F2\u624B\u91CC\u90A3\u70B9\u4EBA\uFF0C\u5BF9\u4E0A\u4E86\u7B80\u76F4\u5C31\u662F\u7F8A\u5165\u864E\u53E3\u3002</p></blockquote><blockquote><p>\u4E0B\u9762\u7684\u90E1\u5B88\u5012\u662F\u6709\u5175\uFF0C\u4F46\u662F\u4E00\u6765\u5175\u529B\u4E0D\u8DB3\uFF0C\u4E8C\u6765\u5730\u65B9\u5B98\u867D\u7136\u5B88\u571F\u6709\u8D23\uFF0C\u5374\u6CA1\u6709\u527F\u532A\u7684\u4E49\u52A1\u3002\u6240\u4EE5\u5927\u90E8\u5206\u65F6\u5019\uFF0C\u90E1\u5B88\u53EA\u8981\u628A\u9EC4\u5DFE\u8D3C\u8D76\u51FA\u672C\u90E1\u5730\u754C\uFF0C\u5C31\u5B8C\u4E8B\u4E86\u3002</p></blockquote><p>\u5218\u7109\u7684\u63D0\u8BAE\uFF0C\u6B63\u662F\u9488\u5BF9\u8FD9\u6837\u7684\u73B0\u72B6\u800C\u63D0\u51FA\u3002**\u4ED6\u5EFA\u8BAE\u5C06\u523A\u53F2\u6539\u4E3A\u5DDE\u7267\uFF0C\u6700\u5927\u7684\u533A\u522B\uFF0C\u4E0D\u662F\u5DDE\u7267\u7684\u7235\u4F4D\u63D0\u5347\u4E86\uFF0C\u800C\u662F\u804C\u6743\u53D1\u751F\u4E86\u53D8\u52A8\u3002**\u4EE5\u524D\u90E1\u4E3A\u4E00\u7EA7\uFF0C\u884C\u653F\u76F4\u63A5\u5411\u4E2D\u592E\u8D1F\u8D23\uFF1B\u5982\u4ECA\u5374\u662F\u964D\u4E86\u4E00\u7EA7\uFF0C\u6539\u4E3A\u5411\u5DDE\u7267\u8D1F\u8D23\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C<strong>\u5728\u56FD\u5BB6\u672C\u6765\u7684\u4E24\u7EA7\u884C\u653F\u4F53\u7CFB\u4E2D\uFF0C\u786C\u751F\u751F\u7684\u53C8\u63D2\u5165\u4E86\u4E00\u7EA7\uFF0C\u90E1\u53BF\u5236\u53D8\u6210\u4E86\u7701-\u5E02-\u53BF\u5236\u3002</strong></p><p>\u8FD9\u6837\u505A\u7684\u597D\u5904\u663E\u800C\u6613\u89C1\u3002\u4E00\u5DDE\u4E4B\u5185\uFF0C\u6743\u8D23\u5F97\u5230\u4E86\u7EDF\u4E00\uFF0C\u6709\u5229\u4E8E\u527F\u706D\u9EC4\u5DFE\u4F59\u515A\u3002\u4F46\u95EE\u9898\u5728\u4E8E\uFF0C<strong>\u8FD9\u9879\u5236\u5EA6\uFF0C\u4E5F\u76F4\u63A5\u9020\u6210\u4E86\u519B\u9600\u7684\u5272\u636E\u3002</strong></p><p>\u6700\u521D\u4E2D\u592E\u5728\u5212\u5206\u884C\u653F\u533A\u5212\u65F6\uFF0C\u5C31\u662F\u8003\u8651\u5230\u9632\u6B62\u5730\u65B9\u5750\u5927\uFF0C\u624D\u628A\u90E1\u56FD\u523B\u610F\u5212\u5C0F\u3002\u4E2D\u592E\u5B81\u613F\u9762\u5BF9\u8FD1\u767E\u4E2A\u90E1\u56FD\uFF0C\u4E5F\u4E0D\u613F\u610F\u5C06\u5176\u5408\u5E76\uFF0C\u672C\u6765\u5C31\u662F\u5F3A\u5E72\u5F31\u679D\u4E4B\u8BA1\u3002\u5982\u4ECA\u4E3A\u4E86\u5E73\u53DB\uFF0C\u53CD\u5176\u9053\u800C\u884C\u4E4B\uFF0C\u7ED9\u5927\u6C49\u671D\u7684\u7EDF\u6CBB\u5E26\u6765\u4E86\u6781\u5927\u9690\u60A3\u3002</p><p>\u5F53\u7136\uFF0C\u521A\u5F00\u59CB\u7684\u65F6\u5019\uFF0C\u8FD9\u79CD\u9690\u60A3\u5E76\u4E0D\u660E\u663E\u3002\u6BD5\u7ADF\uFF0C\u5DDE\u7267\u7684\u4EFB\u547D\u8FD8\u662F\u638C\u63E1\u5728\u671D\u5EF7\u624B\u4E2D\u7684\u3002</p><p>\u4F46\u662F\uFF0C\u9A6C\u4E0A\u5C31\u4E0D\u662F\u4E86\u3002</p><p>\u8881\u7ECD\u53EC\u96C6\u8BF8\u4FAF\u53CD\u8463\u5353\uFF0C\u8463\u5353\u4E3A\u4E86\u907F\u5176\u950B\u8292\uFF0C\u4ECE\u6D1B\u9633\u8FC1\u90FD\u957F\u5B89\u3002<strong>\u4EE5\u6B64\u4E8B\u4E3A\u6807\u5FD7\uFF0C\u4E2D\u592E\u4ECE\u6B64\u518D\u65E0\u529B\u53F7\u4EE4\u5730\u65B9\u3002</strong></p><p>\u901A\u4FD7\u7684\u8BF4\u5C31\u662F\uFF1A\u6B64\u524D\u4E2D\u592E\u4EFB\u547D\u7684\u5B98\u804C\uFF0C\u4EFB\u547D\u4E86\u5C31\u662F\u4EFB\u547D\u4E86\u3002\u4F46\u4ECE\u6B64\u4EE5\u540E\uFF0C\u4E2D\u592E\u518D\u505A\u4EFB\u547D\uFF0C\u5730\u65B9\u5C31\u4E0D\u4E00\u5B9A\u63A5\u7EB3\u4E86\uFF0C\u800C\u662F\u770B\u8C01\u7684\u62F3\u5934\u66F4\u786C\u4E00\u70B9\u3002</p><blockquote><p>\u6240\u4EE5\u6211\u4EEC\u624D\u80FD\u5728\u300A\u4E09\u56FD\u6F14\u4E49\u300B\u91CC\u770B\u5230\u9676\u8C26\u4E0E\u5218\u5907\u7684\u79C1\u76F8\u6388\u53D7\u3002\u5F53\u65F6\u7684\u771F\u5B9E\u60C5\u51B5\u662F\uFF0C\u9676\u8C26\u4E00\u6B7B\uFF0C\u5730\u65B9\u5B98\u540F\u5C31\u628A\u5F90\u5DDE\u7267\u7684\u5370\u4FE1\u76F4\u63A5\u9001\u7ED9\u4E86\u5218\u5907\u3002\u5218\u5907\u5462\uFF1F\u7ED9\u671D\u5EF7\u5199\u4E86\u4E00\u5C01\u4FE1\uFF0C\u7136\u540E\u5C31\u5927\u6447\u5927\u6446\u7684\u5F53\u8D77\u4E86\u5F90\u5DDE\u7267\u3002</p></blockquote><blockquote><p>\u800C\u5218\u7109\u4E4B\u6240\u4EE5\u7ED9\u671D\u5EF7\u63D0\u51FA\u201C\u6539\u523A\u53F2\u4E3A\u5DDE\u7267\u201D\u7684\u8FDB\u8A00\uFF0C\u672C\u8EAB\u4E5F\u6CA1\u5B89\u4EC0\u4E48\u597D\u5FC3\u3002\u4ED6\u6253\u7740\u4E3A\u5927\u6C49\u671D\u7740\u60F3\u7684\u540D\u4E49\uFF0C\u5B9E\u9645\u4E0A\u662F\u56E0\u4E3A\u81EA\u5DF1\u770B\u4E2D\u4E86\u4EA4\u5DDE\u3002\u8FD9\u4E2A\u65F6\u5019\uFF0C\u6709\u4EBA\u8DDF\u4ED6\u8BF4\u201C\u76CA\u5DDE\u5206\u91CE\u6709\u5929\u5B50\u6C14\u201D\uFF0C\u4ED6\u4FBF\u53C8\u8F6C\u800C\u6C42\u53D6\u76CA\u5DDE\u7267\u7684\u4F4D\u7F6E\u3002</p></blockquote><blockquote><p>\u540E\u6765\u7684\u5386\u53F2\u6211\u4EEC\u5C31\u90FD\u77E5\u9053\u4E86\uFF1A\u5218\u7109\u6700\u7EC8\u83B7\u4EFB\uFF0C\u5E76\u5728\u6B7B\u540E\u5C06\u8FD9\u4E2A\u4F4D\u7F6E\u4F20\u7ED9\u4E86\u81EA\u5DF1\u7684\u513F\u5B50\u5218\u748B\u3002\u518D\u540E\u6765\uFF0C\u5C31\u8F6C\u5230\u4E86\u5218\u5907\u7684\u624B\u91CC\u3002</p></blockquote><p>\u8463\u5353\u81EA\u5DF1\uFF0C\u5728\u8FDB\u4EAC\u4E4B\u524D\uFF0C\u4FBF\u662F\u5E76\u5DDE\u7267\u3002</p><p>\u90A3\u5176\u4ED6\u7684\u5730\u65B9\u5462\uFF1F\u7B54\u66F0\uFF1A\u8463\u5353\u81EA\u5DF1\u9001\u4EBA\u4E86\u3002</p><p>\u5728\u5E9F\u7ACB\u4E86\u7687\u5E1D\u4EE5\u540E\uFF0C<strong>\u4E3A\u4E86\u6362\u53D6\u5B98\u50DA\u96C6\u56E2\u5BF9\u81EA\u5DF1\u7684\u652F\u6301\uFF0C\u8463\u5353\u4EFB\u547D\u4E86\u4E00\u5927\u7968\u7684\u58EB\u4EBA\u4E3A\u5730\u65B9\u5B98\u3002</strong></p><p>\u7ED3\u679C\uFF0C\u8FD9\u4E9B\u4EBA\u4E00\u5230\u5730\u65B9\u4E0A\uFF0C\u5C31\u9A6C\u4E0A\u548C\u8881\u7ECD\u4E32\u901A\u8D77\u6765\uFF0C\u8054\u5408\u53CD\u8463\u3002\u201C\u521D\uFF0C\u5353\u4FE1\u4EFB\u5C1A\u4E66\u5468\u6BD6\uFF0C\u57CE\u95E8\u6821\u5C09\u4F0D\u743C\u7B49\uFF0C\u7528\u5176\u6240\u4E3E\u97E9\u99A5\u3001\u5218\u5CB1\u3001\u5B54\u4F37\u3001\u5F20\u54A8\u3001\u5F20\u9088\u7B49\u51FA\u5BB0\u5DDE\u90E1\u3002\u800C\u99A5\u7B49\u81F3\u5B98\uFF0C\u7686\u5408\u5175\u5C06\u4EE5\u8BA8\u5353\u3002\u5353\u95FB\u4E4B\uFF0C\u4EE5\u4E3A\u6BD6\u3001\u743C\u7B49\u901A\u60C5\u5356\u5DF1\uFF0C\u7686\u65A9\u4E4B\u3002\uFF08\u300A\u4E09\u56FD\u5FD7\xB7\u9B4F\u4E66\xB7\u8463\u5353\u8881\u7ECD\u8881\u672F\u5218\u8868\u4F20\u300B\uFF09\u201D</p><p>\u201C<code>\u571F\u5D29</code>\u201D\u548C\u201C<code>\u74E6\u89E3</code>\u201D\u4E4B\u95F4\u7684\u533A\u522B\u3002</p><p>\u8FD9\u4E24\u4E2A\u6982\u5FF5\uFF0C\u7531\u300A\u6C49\u4E66\u300B\u91CC\u4E00\u4E2A\u53EB<code>\u5F90\u4E50</code>\u7684\u4EBA\u63D0\u51FA\u3002\u6B64\u4EBA\u65E0\u751F\u5352\u5E74\u6708\uFF0C\u65E0\u751F\u5E73\u4E8B\u8FF9\uFF0C\u53EA\u7559\u4E0B\u4E86\u4E00\u5C01\u594F\u6298\u3002\u4F46\u56E0\u4E3A\u8FD9\u5C01\u594F\u6298\u5199\u5F97\u5B9E\u5728\u662F\u592A\u597D\u592A\u597D\uFF0C\u56E0\u6B64\u7ECF\u73ED\u56FA\u4E4B\u624B\u5F97\u4EE5\u6D41\u4F20\u81F3\u4ECA\u3002</p><p>\u594F\u6298\u91CC\uFF0C\u5F90\u4E50\u63D0\u51FA\u4E86\u4E24\u4E2A\u6982\u5FF5\uFF1A\u4E00\u4E2A\u53EB\u201C\u571F\u5D29\u201D\uFF0C\u53E6\u4E00\u4E2A\u53EB\u201C\u74E6\u89E3\u201D\u3002\u571F\u5D29\u74E6\u89E3\uFF0C\u4E8E\u4ECA\u4EBA\u800C\u8A00\uFF0C\u65E0\u751A\u533A\u522B\uFF0C\u4F46\u5728\u5F90\u4E50\u7684\u594F\u758F\u4E2D\uFF0C\u5DEE\u522B\u5DE8\u5927\u3002</p><blockquote><p>\u571F\u5D29</p></blockquote><p>\u5F90\u4E50\u4E3E\u4E86\u79E6\u672B\u7684\u4F8B\u5B50\u3002\u4ED6\u8BA4\u4E3A\u5F53\u65F6\u767E\u59D3\u6D41\u79BB\u5931\u6240\u3001\u56F0\u82E6\u4E0D\u582A\uFF0C\u5BF9\u7EDF\u6CBB\u8005\u6028\u58F0\u8F7D\u9053\u800C\u4E0A\u5C42\u5374\u4E0D\u77E5\uFF1B\u5730\u65B9\u4E0A\uFF0C\u4E5F\u662F\u884C\u653F\u6DF7\u4E71\uFF0C\u9053\u5FB7\u6CA6\u4E27\u3002\u6B63\u662F\u56E0\u6B64\u79CD\u79CD\uFF0C\u624D\u4F1A\u6709\u9648\u80DC\u7684\u63ED\u7AFF\u800C\u8D77\uFF0C\u7136\u540E\u904D\u5730\u661F\u706B\u71CE\u539F\u3002\uFF08\u201C\u79E6\u4E4B\u672B\u4E16\u662F\u4E5F\u3002\u2026\u2026\u7531\u6C11\u56F0\u800C\u4E3B\u4E0D\u6064\uFF0C\u4E0B\u6028\u800C\u4E0A\u4E0D\u77E5\uFF0C\u4FD7\u5DF2\u4E71\u800C\u653F\u4E0D\u4FEE\uFF0C\u6B64\u4E09\u8005\u9648\u6D89\u4E4B\u6240\u4EE5\u4E3A\u8D44\u4E5F\u3002\u6B64\u4E4B\u8C13\u571F\u5D29\u3002\u201D\uFF09</p><blockquote><p>\u74E6\u89E3</p></blockquote><p>\u5F90\u4E50\u4E3E\u4E86\u4E03\u56FD\u4E4B\u4E71\u7684\u4F8B\u5B50\u3002\u4E03\u56FD\u867D\u7136\u5175\u591A\u5C06\u5E7F\uFF0C\u7CAE\u8349\u7CBE\u8DB3\uFF0C\u4F46\u662F\uFF0C\u4ED6\u4EEC\u4E0A\u5F97\u4E0D\u5230\u5176\u4ED6\u8BF8\u4FAF\u7684\u652F\u6301\uFF0C\u4E0B\u6CA1\u6709\u666E\u901A\u767E\u59D3\u7684\u54CD\u5E94\u3002\u9762\u5BF9\u671D\u5EF7\u7684\u5927\u519B\uFF0C\u81EA\u7136\u5BB9\u6613\u8D25\u4EA1\u3002\uFF08\u201C\u4E03\u56FD\u8C0B\u4E3A\u5927\u9006\uFF0C\u53F7\u7686\u79F0\u4E07\u4E58\u4E4B\u541B\uFF0C\u5E26\u7532\u6570\u5341\u4E07\uFF0C\u5A01\u8DB3\u4EE5\u4E25\u5176\u5883\u5185\uFF0C\u8D22\u8DB3\u4EE5\u529D\u5176\u58EB\u6C11\uFF0C\u7136\u4E0D\u80FD\u897F\u6518\u5C3A\u5BF8\u4E4B\u5730\uFF0C\u800C\u8EAB\u4E3A\u79BD\u4E8E\u4E2D\u539F\u8005\uFF0C\u6B64\u5176\u6545\u4F55\u4E5F\uFF1F\u975E\u6743\u8F7B\u4E8E\u5339\u592B\u800C\u5175\u5F31\u4E8E\u9648\u6D89\u4E5F\u3002\u5F53\u662F\u4E4B\u65F6\uFF0C\u5148\u5E1D\u4E4B\u5FB7\u672A\u8870\uFF0C\u800C\u5B89\u571F\u4E50\u4FD7\u4E4B\u6C11\u4F17\uFF0C\u6545\u8BF8\u4FAF\u65E0\u7ADF\u5916\u4E4B\u52A9\u3002\u6B64\u4E4B\u8C13\u74E6\u89E3\u3002\u201D\uFF09</p><p>\u6982\u62EC\u4E00\u4E0B\u5C31\u662F\uFF1A</p><blockquote><p>\u201C\u571F\u5D29\u201D\uFF0C\u662F\u4E2D\u592E\u5BF9\u57FA\u5C42\u7EC4\u7EC7\u80FD\u529B\u7684\u4E27\u5931\uFF1B</p></blockquote><blockquote><p>\u201C\u74E6\u89E3\u201D\uFF0C\u5219\u662F\u5BF9\u4E2D\u5C42\u63A7\u5236\u80FD\u529B\u7684\u4E27\u5931\u3002</p></blockquote><p>\u90A3\u4E48\u4E1C\u6C49\u672B\u5E74\uFF0C\u5C5E\u4E8E\u54EA\u4E00\u79CD\u5462\uFF1F</p><p>\u6211\u4EEC\u63D0\u53CA\u4E86\u9EC4\u5DFE\u4E4B\u4E71\uFF0C\u770B\u4E0A\u53BB\u5F88\u50CF\u662F\u201C\u571F\u5D29\u201D\u3002\u5F20\u89D2\u62AB\u7740\u5B97\u6559\u7684\u5916\u8863\uFF0C\u5728\u5168\u56FD\u8303\u56F4\u5185\u90FD\u5F62\u6210\u4E86\u5DE8\u5927\u7684\u7EC4\u7EC7\u80FD\u529B\u3002</p><p>\u4F46\u5B9E\u9645\u4E0A\uFF0C<strong>\u4E1C\u6C49\u7684\u706D\u4EA1\uFF0C\u662F\u201C\u74E6\u89E3\u201D\u3002</strong></p><blockquote><p>\u8463\u5353\u4EE5\u540E\uFF0C\u519B\u9600\u5272\u636E\u8981\u6D25\uFF0C\u62DB\u5175\u4E70\u9A6C\uFF0C\u62E5\u5175\u81EA\u91CD\u3002</p></blockquote><blockquote><p>\u8FD0\u6C14\u5DEE\u7684 \u5728\u6DF7\u6218\u4E2D\u8EAB\u6B7B\u65CF\u706D\uFF0C\u5982\u8881\u6C0F\u5144\u5F1F\uFF1B</p></blockquote><blockquote><p>\u8FD0\u6C14\u4E00\u822C\u7684\uFF0C \u56DB\u5904\u5F81\u4F10\uFF0C\u4E09\u5206\u5929\u4E0B\u6709\u5176\u4E8C\uFF0C\u8FD9\u662F\u66F9\u64CD\uFF1B</p></blockquote><blockquote><p>\u8FD0\u6C14\u597D\u7684\uFF0C \u5219\u5B88\u7740\u4E00\u5757\u5730\u4ECE\u59CB\u5230\u7EC8\uFF0C\u8B6C\u5982\u5B59\u6743\uFF1B</p></blockquote><blockquote><p>\u5F53\u7136\uFF0C\u8FD8\u6709\u8FD0\u6C14\u597D\u5230\u7206\u7684\uFF0C \u4E94\u5341\u591A\u5C81\u4E86\uFF0C\u4EBA\u751F\u5C45\u7136\u8FD8\u80FD\u7115\u53D1\u7B2C\u4E8C\u6625\uFF0C\u4ECE\u4E00\u65E0\u6240\u6709\u5230\u7EDD\u5730\u5927\u53CD\u51FB\uFF0C\u8FD9\u5C31\u662F\u5218\u5907\u4E86\u3002</p></blockquote><p>\u7EB5\u89C2\u4E09\u56FD\u7684\u519B\u9600\u6DF7\u6218\uFF0C\u9664\u4E86\u5218\u5907\u7B49\u5BE5\u5BE5\u51E0\u4EBA\uFF0C\u4F60\u4F1A\u53D1\u73B0\uFF0C<strong>\u5176\u4F59\u90FD\u662F\u4F53\u5236\u5185\u7684\u5B98\u50DA</strong>\uFF0C\u5982\u66F9\u64CD\u3001\u8881\u7ECD\u7B49\uFF0C\u4ED6\u4EEC\u5F88\u65E9\u5C31\u5728\u671D\u5EF7\u5185\u4EFB\u804C\u3002\u8BF4\u767D\u4E86\uFF0C\u4ED6\u4EEC\u7528\u4EE5\u4E89\u593A\u5929\u4E0B\u7684\u8D44\u672C\uFF0C\u57FA\u672C\u4E0A\u90FD\u662F\u516C\u6B3E\u516C\u7269\u3002</p><p>\u8FD9\u4E5F\u662F\u4E09\u56FD\u6DF7\u6218\u8FD1\u767E\u5E74\u65B9\u80FD\u7EDF\u4E00\u7684\u539F\u56E0\u4E4B\u4E00\u3002\u5176\u65F6\uFF0C\u6BCF\u4E2A\u4EBA\u90FD\u6709\u81EA\u5DF1\u7684\u6839\u636E\u5730\uFF0C\u671F\u95F4\u5927\u90E8\u5206\u7684\u6218\u4E89\u90FD\u53EA\u662F\u5C40\u90E8\u6218\u4E89\uFF0C<strong>\u80DC\u8D25\u4E0E\u5426\uFF0C\u65E0\u4F24\u5168\u5C40\u3002</strong></p><p>\u8FD9\u4E0E\u79E6\u672B\u76F8\u6BD4\uFF0C\u5DF2\u7ECF\u4E0D\u540C\u3002\u9648\u80DC\u8D77\u4E8B\u540E\uFF0C\u5929\u4E0B\u54CD\u5E94\uFF0C\u5404\u5730\u5747\u9677\u5165\u6743\u529B\u771F\u7A7A\u7684\u72B6\u6001\u3002\u4F60\u53EA\u8981\u6709\u4EBA\u6709\u67AA\uFF0C\u5C31\u80FD\u79F0\u738B\u79F0\u9738\uFF0C\u4F46\u662F\u7EDF\u6CBB\u5374\u5E76\u4E0D\u7A33\u56FA\uFF0C\u7ECF\u5E38\u662F\u4E00\u4ED7\u6218\u8D25\uFF0C\u5C5E\u4E0B\u5373\u6563\u3002</p><p><strong>\u517C\u6709\u201C\u571F\u5D29\u201D\u4E0E\u201C\u74E6\u89E3\u201D\u7684\uFF0C\u5219\u662F\u660E\u672B\u3002</strong></p><p>\u660E\u672B\u5317\u65B9\u8FDE\u5E74\u5E72\u65F1\uFF0C\u674E\u81EA\u6210\u8F6C\u6218\u5343\u91CC\u540E\uFF0C\u5317\u65B9\u5DF2\u6210\u201C\u571F\u5D29\u201D\u4E4B\u52BF\uFF1B\u4F46\u5357\u65B9\u5374\u4E0D\u540C\uFF0C\u5D07\u796F\u6B7B\u540E\uFF0C\u51E0\u5E74\u95F4\uFF0C\u6CBF\u6C5F\u6709\u56DB\u5927\u603B\u5175\uFF0C\u57FA\u672C\u4E0A\u5DF2\u7ECF\u662F\u72EC\u7ACB\u738B\u56FD\uFF0C\u5357\u660E\u671D\u5EF7\u6839\u672C\u65E0\u53EF\u5948\u4F55\u3002</p>',58),n=[r];function l(u,b){return p(),t("div",null,n)}const q=o(s,[["render",l],["__file","3.html.vue"]]);export{q as default};
