import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.cd90e563.js";const c={},o=a('<h1 id="\u7B2C3\u96C6-\u6C5F\u9633\u8C03\u53D6\u5377\u5B97\u9891\u906D\u963B\u62E6-\u9648\u660E\u7AE0\u7206\u6599\u4FAF\u8D35\u5E73\u6B7B\u56E0" tabindex="-1"><a class="header-anchor" href="#\u7B2C3\u96C6-\u6C5F\u9633\u8C03\u53D6\u5377\u5B97\u9891\u906D\u963B\u62E6-\u9648\u660E\u7AE0\u7206\u6599\u4FAF\u8D35\u5E73\u6B7B\u56E0" aria-hidden="true">#</a> \u7B2C3\u96C6\uFF1A\u6C5F\u9633\u8C03\u53D6\u5377\u5B97\u9891\u906D\u963B\u62E6 \u9648\u660E\u7AE0\u7206\u6599\u4FAF\u8D35\u5E73\u6B7B\u56E0</h1><p>\u6CD5\u533B\u8001\u9A6C\u5BF9\u4E8E\u6C5F\u9633\u6B7B\u56E0\u4ECD\u6000\u8FDF\u7591\u6001\u5EA6\uFF0C\u5C24\u5176\u51F6\u6848\u73B0\u573A\u7559\u4E0B\u7684\u7535\u89C6\u4FE1\u53F7\u7EBF\uFF0C\u4E00\u9053\u4E0D\u8D77\u773C\u7684\u5212\u75D5\u4EE4\u4ED6\u767E\u601D\u4E0D\u5F97\u5176\u89E3\u3002\u7531\u4E8E\u4FAF\u8D35\u5E73\u5377\u5B97\u7F3A\u5C11\u5C38\u68C0\u62A5\u544A\uFF0C\u4E25\u826F\u5429\u5490\u987E\u4E00\u9E23\u524D\u5F80\u6863\u6848\u5BA4\u8C03\u53D6\uFF0C\u8D77\u521D\u987E\u4E00\u9E23\u788D\u4E8E\u4ED6\u62A2\u8D70\u81EA\u5DF1\u98CE\u5934\uFF0C\u6781\u4E0D\u60C5\u613F\uFF0C\u4F46\u770B\u5728\u4EFB\u73A5\u5A77\u9762\u5B50\u4E0A\uFF0C\u53EA\u80FD\u5E94\u4E0B\u6B64\u4E8B\u3002</p><p>\u6C7D\u8F66\u9A76\u8FC7\u7E41\u534E\u8857\u9053\uFF0C\u505C\u5728\u5E73\u5EB7\u53BF\u516C\u5B89\u5C40\u95E8\u524D\uFF0C\u76F8\u6BD4\u4E03\u5E74\u524D\u7684\u7B80\u6734\uFF0C\u8FD9\u91CC\u5E76\u975E\u6BEB\u65E0\u6539\u53D8\uFF0C\u81F3\u5C11\u8B66\u5458\u6001\u5EA6\u4E0D\u518D\u5F3A\u786C\uFF0C\u6863\u6848\u5BA4\u53EF\u4EE5\u76F4\u63A5\u95EF\u5165\uFF0C\u800C\u4E0D\u50CF\u5F53\u521D\u6C5F\u9633\u8F9B\u82E6\u8D76\u6765\uFF0C\u7B49\u5019\u8BB8\u4E45\uFF0C\u6700\u7EC8\u5403\u4E2A\u95ED\u95E8\u7FB9\u3002</p><p>\u6C5F\u9633\u8981\u6C42\u8C03\u53D6\u6848\u5377\uFF0C\u91CD\u65B0\u590D\u67E5\uFF0C\u5211\u8B66\u961F\u957F\u674E\u5EFA\u56FD\u4EE5\u9500\u6848\u5C01\u5B58\u4E3A\u7531\uFF0C\u62D2\u7EDD\u4EA4\u51FA\u4FAF\u8D35\u5E73\u7684\u6848\u5377\u6750\u6599\u3002\u6C5F\u9633\u5C61\u6B21\u78B0\u58C1\uFF0C\u53C8\u5728\u624B\u4E0B\u9762\u524D\u4E22\u8138\uFF0C\u4E0D\u7981\u5927\u4E3A\u607C\u706B\uFF0C\u5F53\u4ED6\u542C\u95FB\u5E73\u5EB7\u516C\u5B89\u5C40\u6240\u6709\u5C38\u68C0\u62A5\u544A\u5747\u51FA\u81EA\u6CD5\u533B\u9648\u660E\u7AE0\u4E4B\u624B\uFF0C\u4E8E\u662F\u4EB2\u81EA\u6765\u5230\u6280\u4FA6\u4E2D\u5FC3\u6CD5\u533B\u5BA4\u3002</p><p>\u9648\u660E\u7AE0\u4E0E\u6C5F\u9633\u521D\u6B21\u89C1\u9762\uFF0C\u56E0\u4ED6\u5E26\u7740\u773C\u955C\uFF0C\u957F\u76F8\u65AF\u6587\uFF0C\u8BA9\u65C1\u4EBA\u4EE5\u4E3A\u6781\u597D\u8BF4\u8BDD\u3002\u7ED3\u679C\u9648\u660E\u7AE0\u4E0D\u50CF\u6CD5\u533B\uFF0C\u66F4\u50CF\u5546\u4EBA\uFF0C\u4ED6\u5341\u5206\u4E16\u6545\u53CA\u72E1\u733E\uFF0C\u5F97\u77E5\u6C5F\u9633\u4E0E\u4FAF\u8D35\u5E73\u5C5E\u4E8E\u540C\u5B66\uFF0C\u4FBF\u4EE5\u4E00\u5343\u5143\u7684\u9AD8\u4EF7\u628A\u62A5\u544A\u5356\u7ED9\u6C5F\u9633\u3002\u867D\u7136\u9648\u660E\u7AE0\u7684\u8981\u94B1\u884C\u4E3A\u8BA9\u6C5F\u9633\u8DCC\u7834\u773C\u775B\uFF0C\u53EF\u662F\u60F3\u5230\u4FAF\u8D35\u5E73\u6848\u4EF6\u7684\u91CD\u5927\u9690\u60C5\uFF0C\u5904\u4E8E\u804C\u4E1A\u9075\u5B88\u4E0E\u826F\u77E5\uFF0C\u6700\u7EC8\u6C5F\u9633\u8DDF\u9648\u660E\u7AE0\u7EA6\u5728\u9910\u5385\u89C1\u9762\uFF0C\u4E00\u624B\u4EA4\u94B1\u4E00\u624B\u4EA4\u8D44\u6599\u3002</p><p>\u5F53\u5929\u665A\u4E0A\uFF0C\u5434\u7231\u53EF\u966A\u540C\u6C5F\u9633\u5750\u5728\u9910\u5385\u5305\u53A2\u91CC\uFF0C\u9648\u660E\u7AE0\u7F13\u7F13\u800C\u6765\uFF0C\u53CD\u5BA2\u4E3A\u4E3B\u5730\u70B9\u597D\u83DC\u5355\uFF0C\u968F\u4E4B\u62FF\u51FA\u4FAF\u8D35\u5E73\u5C38\u68C0\u62A5\u544A\uFF0C\u751A\u81F3\u7206\u6599\u6709\u4EBA\u7BE1\u6539\u7ED3\u8BBA\u9020\u6210\u4FAF\u8D35\u5E73\u6EBA\u6B7B\u5047\u8C61\uFF0C\u800C\u6709\u5982\u6B64\u5B9E\u6743\u4E4B\u4EBA\u7EDD\u5BF9\u8EAB\u5C45\u9AD8\u4F4D\u3002</p><p>\u9648\u660E\u7AE0\u7684\u8BDD\u4EE4\u6C5F\u9633\u52A8\u6447\uFF0C\u4ED6\u77E5\u9053\u6B64\u4E8B\u5C06\u4F1A\u7275\u6D89\u5F88\u591A\uFF0C\u4F5C\u4E3A\u4E00\u540D\u5E74\u8F7B\u68C0\u5BDF\u5B98\uFF0C\u6CA1\u6709\u5E9E\u5927\u5173\u7CFB\u7F51\uFF0C\u60F3\u8981\u4E0E\u5176\u5468\u65CB\u7B49\u540C\u81EA\u5BFB\u6B7B\u8DEF\u3002\u6C5F\u9633\u4E0D\u65AD\u5411\u62A5\u544A\u6863\u6848\u6295\u53BB\u76EE\u5149\uFF0C\u4EFF\u4F5B\u6700\u540E\u4E00\u6839\u7A3B\u8349\u5C31\u5728\u91CC\u9762\uFF0C\u5982\u82E5\u4E3A\u524D\u9014\u59A5\u534F\uFF0C\u6050\u6015\u4EE5\u540E\u4FBF\u6C38\u8FDC\u65E0\u6CD5\u6253\u5F00\uFF0C\u4FAF\u8D35\u5E73\u7684\u6B7B\u6210\u4E3A\u79D8\u5BC6\uFF0C\u6210\u4E3A\u9057\u61BE\uFF0C\u6210\u4E3A\u6CB3\u5E95\u7684\u6C89\u9ED8\uFF0C\u4E0D\u89C1\u5929\u65E5\u3002</p><p>\u987E\u4E00\u9E23\u4ECE\u6863\u6848\u5BA4\u91CC\u51FA\u6765\uFF0C\u6BEB\u65E0\u6240\u83B7\uFF0C\u5176\u540E\u53C8\u53BB\u4E00\u8D9F\u68C0\u5BDF\u9662\uFF0C\u56E0\u6B64\u786E\u5B9A\u6C5F\u9633\u751F\u524D\u66FE\u4E3A\u4ED6\u7ACB\u6848\u8C03\u67E5\uFF0C\u53EA\u53EF\u60DC\u5230\u5934\u6765\u6CA1\u6709\u4EFB\u4F55\u7ED3\u679C\uFF0C\u5C31\u8FDE\u5C38\u68C0\u62A5\u544A\u4E5F\u90FD\u65E0\u6545\u6D88\u5931\u3002</p><p>\u6C5F\u9633\u5728\u5C38\u68C0\u62A5\u544A\u4E0A\u770B\u5230\u674E\u5EFA\u56FD\u7684\u540D\u5B57\uFF0C\u6545\u800C\u731C\u6D4B\u5E73\u6F6D\u53BF\u516C\u5B89\u5C40\u6709\u610F\u9690\u7792\u5B9E\u60C5\uFF0C\u4E8E\u662F\u53BB\u627E\u4FAF\u8D35\u5E73\u5BB6\u5C5E\u4E86\u89E3\u60C5\u51B5\uFF0C\u5E76\u8BA9\u4ED6\u4EEC\u5728\u7533\u8FF0\u4E66\u4E0A\u7B7E\u5B57\u3002\u4FAF\u8D35\u5E73\u8205\u8205\u60F3\u8981\u7FFB\u6848\uFF0C\u4E3B\u52A8\u8BB2\u8D77\u5916\u7525\u7684\u6545\u4E8B\uFF0C\u65E0\u8BBA\u4ECE\u4ED6\u5B66\u4E60\u5230\u4EBA\u54C1\u90FD\u662F\u65E0\u53EF\u6311\u5254\uFF0C\u8C01\u4E5F\u672A\u66FE\u6599\u5230\u7ADF\u4F1A\u53D1\u751F\u8FD9\u822C\u53D8\u6545\uFF0C\u5E76\u4E14\u81EA\u4ECE\u4FAF\u8D35\u5E73\u53BB\u4E16\u540E\uFF0C\u4FAF\u7236\u5F53\u573A\u660F\u5012\uFF0C\u8FDE\u7EED\u51E0\u5929\u4E0D\u5403\u4E0D\u559D\uFF0C\u4FAF\u6BCD\u4E5F\u56E0\u5927\u53D7\u523A\u6FC0\uFF0C\u7EE7\u800C\u75AF\u766B\uFF0C\u6574\u65E5\u5230\u5904\u6E38\u8361\u3002</p><p>\u7B7E\u5B57\u6388\u6743\u5230\u624B\u4EE5\u540E\uFF0C\u6C5F\u9633\u9A6C\u4E0A\u4EE5\u76D1\u7763\u4FA6\u67E5\u79D1\u540D\u4E49\uFF0C\u8981\u6C42\u516C\u5B89\u590D\u67E5\u3002\u600E\u6599\u7ACB\u6848\u51B3\u5B9A\u4E66\u9001\u8FBE\u516C\u5B89\u5C40\uFF0C\u674E\u5EFA\u56FD\u5374\u4EB2\u81EA\u5C06\u6587\u4E66\u9001\u56DE\u6765\uFF0C\u8868\u793A\u5C38\u4F53\u706B\u5316\u73B0\u573A\u7834\u574F\uFF0C\u4E24\u5E74\u524D\u6848\u5B50\u5DF2\u6709\u5B9A\u8BBA\uFF0C\u52D2\u4EE4\u4ED6\u8D76\u7D27\u64A4\u9500\u8C03\u67E5\u3002</p><p>\u9762\u5BF9\u5982\u6B64\u6001\u5EA6\uFF0C\u6C5F\u9633\u76F4\u63A5\u53BB\u627E\u5434\u68C0\u8BF4\u660E\u539F\u59D4\u3002\u4F5C\u4E3A\u5434\u7231\u53EF\u7684\u7236\u4EB2\uFF0C\u5434\u68C0\u672C\u8EAB\u683C\u5916\u5668\u91CD\u6C5F\u9633\uFF0C\u5C24\u5176\u542C\u5B8C\u4E8B\u60C5\u7ECF\u8FC7\uFF0C\u4E0D\u7981\u6709\u4E9B\u52A8\u5BB9\uFF0C\u7D22\u6027\u6253\u7535\u8BDD\u7ED9\u6C5F\u9633\u6240\u5728\u68C0\u5BDF\u9662\uFF0C\u63D0\u8BAE\u8BA9\u4E01\u68C0\u5F00\u4F1A\u7814\u7A76\uFF0C\u91CD\u65B0\u5546\u69B7\u3002</p><p>\u5F20\u6653\u5029\u6536\u5230\u7B2C\u4E8C\u5C01\u4FE1\u4EF6\uFF0C\u7167\u7247\u80CC\u666F\u51FA\u81EA\u4E3D\u666F\u9152\u5E97\u3002\u8B66\u65B9\u6839\u636E\u90AE\u5C40\u65B9\u63D0\u4F9B\u4FE1\u606F\uFF0C\u53D1\u73B0\u533F\u540D\u4FE1\u6295\u9012\u5728\u897F\u57CE\u533A\u4E1C\u7F57\u5C0F\u5DF7\u7684\u90AE\u7B52\u5185\uFF0C\u7686\u4E0E\u4E0A\u6B21\u76F8\u4F3C\uFF0C\u5948\u4F55\u897F\u57CE\u533A\u5C0F\u5DF7\u8001\u65E7\u4E0D\u582A\uFF0C\u65E9\u5DF2\u5217\u5165\u8001\u57CE\u6539\u9020\u62C6\u8FC1\u8303\u56F4\u4E4B\u5185\uFF0C\u6240\u4EE5\u6839\u672C\u6CA1\u6709\u5B89\u88C5\u76D1\u63A7\u3002</p><p>\u4EFB\u73A5\u5A77\u4E0E\u987E\u4E00\u9E23\u53EA\u80FD\u5B88\u5728\u9644\u8FD1\u76EF\u68A2\uFF0C\u7ED3\u679C\u63A5\u8FDE\u6570\u65E5\u6BEB\u65E0\u52A8\u9759\uFF0C\u6B63\u5F53\u5927\u5BB6\u8010\u5FC3\u51E0\u4E4E\u8017\u5149\uFF0C\u6CA1\u60F3\u5230\u5ACC\u7591\u76EE\u6807\u7A81\u7136\u51FA\u73B0\uFF0C\u72EC\u81EA\u5F98\u5F8A\u5728\u90AE\u7B52\u65C1\u8FB9\u3002\u6B64\u4EBA\u8863\u886B\u7834\u70C2\uFF0C\u884C\u52A8\u6709\u4E9B\u8FDF\u7F13\uFF0C\u5230\u5934\u6765\u4EC5\u4EC5\u6361\u8D70\u5730\u4E0A\u7684\u6613\u62C9\u7F50\uFF0C\u5E76\u65E0\u5176\u4ED6\u884C\u4E3A\u3002</p><p>\u4E25\u826F\u5728\u5F20\u6653\u5029\u7684\u56DE\u5FC6\u91CC\u83B7\u6089\u6C5F\u9633\u9047\u5BB3\u524D\u4F3C\u4E4E\u5DF2\u6709\u9884\u611F\uFF0C\u6BCF\u53E5\u8BDD\u90FD\u900F\u9732\u51FA\u7EDD\u671B\u548C\u8BC0\u522B\u3002\u5F53\u65F6\u5F20\u6653\u5029\u7A0D\u6709\u56F0\u60D1\uFF0C\u4F46\u662F\u6CA1\u6709\u6DF1\u7A76\uFF0C\u53EF\u5728\u5982\u4ECA\u60F3\u6765\uFF0C\u5374\u662F\u4E0E\u4ED6\u7684\u6700\u540E\u4E00\u9762\u3002\u4E13\u6848\u7EC4\u65E0\u529F\u800C\u8FD4\uFF0C\u4E25\u826F\u5374\u8BA4\u4E3A\u6361\u62FE\u7834\u70C2\u7684\u6D41\u6D6A\u6C49\u6781\u5176\u53EF\u7591\u3002</p>',14),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","3.html.vue"]]);export{h as default};
