import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.e2de568c.js";const c={},o=a('<h1 id="\u7B2C32\u96C6-\u4E1C\u65B9\u9752\u82CD\u88AB\u56F0\u68A6\u5883" tabindex="-1"><a class="header-anchor" href="#\u7B2C32\u96C6-\u4E1C\u65B9\u9752\u82CD\u88AB\u56F0\u68A6\u5883" aria-hidden="true">#</a> \u7B2C32\u96C6\uFF1A\u4E1C\u65B9\u9752\u82CD\u88AB\u56F0\u68A6\u5883</h1><p>\u660F\u8FF7\u7684\u4E1C\u65B9\u9752\u82CD\u5C06\u81EA\u5DF1\u56F0\u5728\u4E00\u4E2A\u68A6\u5883\u4E4B\u4E2D\u3002\u8FD9\u4E2A\u68A6\u5883\u662F\u7528\u4ED6\u5168\u90E8\u7684\u5143\u795E\u4E4B\u529B\u6240\u9020\uFF0C\u5E76\u4E14\u4E3A\u4E86\u7EF4\u6301\u8FD9\u4E2A\u68A6\u5883\uFF0C\u4ED6\u4E0D\u60DC\u71C3\u70E7\u5FC3\u706B\uFF0C\u8017\u5C3D\u5143\u795E\u3002\u5018\u82E5\u518D\u8FD9\u6837\u4E0B\u53BB\uFF0C\u4E1C\u65B9\u9752\u82CD\u4E5F\u4F1A\u5143\u795E\u7C89\u788E\uFF0C\u5F62\u795E\u4FF1\u706D\u3002</p><p>\u68A6\u4E2D\u7684\u4E1C\u65B9\u9752\u82CD\u548C\u5C0F\u5170\u82B1\u8FC7\u7740\u65E0\u5FE7\u65E0\u8651\u7684\u751F\u6D3B\uFF0C\u6CA1\u6709\u65C1\u4EBA\u6253\u6405\uFF0C\u66F4\u6CA1\u6709\u6B8B\u9177\u7684\u6218\u4E89\u3002\u53EF\u662F\u4ED6\u4E0D\u660E\u767D\uFF0C\u4E3A\u4F55\u547D\u7C3F\u4E4B\u4E0A\u7684\u6709\u60C5\u4EBA\u90FD\u6CA1\u6709\u597D\u7ED3\u679C\u3002\u5C0F\u5170\u82B1\u6709\u4E9B\u611F\u6168\uFF0C\u5979\u548C\u4E1C\u65B9\u9752\u82CD\u7EA6\u5B9A\u597D\uFF0C\u6C38\u751F\u6C38\u4E16\u90FD\u4E0D\u8981\u5206\u5F00\u3002</p><p>\u68A6\u7684\u5916\u9762\uFF0C\u6B87\u9619\u51E0\u4EBA\u6B63\u5728\u4E3A\u4E1C\u65B9\u9752\u82CD\u6CE8\u5165\u5143\u795E\u4E4B\u529B\u3002\u867D\u7136\u5927\u5BB6\u90FD\u77E5\u9053\uFF0C\u5916\u529B\u4E0D\u4EC5\u4E0D\u80FD\u8D77\u5230\u4EFB\u4F55\u5E2E\u52A9\uFF0C\u8FD8\u53EF\u4EE5\u5E26\u6765\u53CD\u566C\u3002\u53EF\u54EA\u6015\u6709\u4E00\u4E1D\u4E1D\u5E0C\u671B\uFF0C\u4ED6\u4EEC\u4E5F\u8981\u8BD5\u4E00\u8BD5\u3002\u5371\u6025\u65F6\u523B\uFF0C\u957F\u73E9\u53CA\u65F6\u51FA\u73B0\u3002\u4ED6\u71C3\u8D77\u4E00\u70B7\u9999\uFF0C\u51B3\u5B9A\u8FDB\u5165\u4E1C\u65B9\u9752\u82CD\u7684\u68A6\u5883\u4E2D\u5524\u9192\u4ED6\u3002</p><p>\u6B64\u65F6\u68A6\u4E2D\u7684\u4E1C\u65B9\u9752\u82CD\u770B\u5230\u4E86\u5929\u6781\u955C\u4E2D\u5173\u4E8E\u957F\u73E9\u548C\u606F\u5C71\u795E\u5973\u7684\u5927\u5A5A\uFF0C\u5FC3\u4E2D\u987F\u65F6\u6C14\u6025\u3002\u8C01\u6599\u957F\u73E9\u7A81\u7136\u95EA\u51FA\uFF0C\u544A\u8BC9\u4ED6\u8FD9\u4E00\u5207\u90FD\u662F\u68A6\u5883\u3002\u5C0F\u5170\u82B1\u4F3C\u4E4E\u542C\u5230\u4E86\u4ED6\u4EEC\u7684\u5BF9\u8BDD\uFF0C\u8D76\u5FD9\u51FA\u6765\u963B\u6B62\u3002\u4E1C\u65B9\u9752\u82CD\u5DE6\u53F3\u4E3A\u96BE\uFF0C\u59CB\u7EC8\u4E0B\u4E0D\u4E86\u51B3\u5B9A\u3002\u5916\u9762\u7684\u9999\u5FEB\u8981\u71C3\u5C3D\uFF0C\u4E1C\u65B9\u9752\u82CD\u7A81\u7136\u5C06\u957F\u73E9\u63A8\u51FA\u68A6\u5883\u3002\u4F17\u4EBA\u4ECD\u65E7\u4E0D\u80AF\u653E\u5F03\uFF0C\u7EE7\u7EED\u5411\u4E1C\u65B9\u9752\u82CD\u6CE8\u5165\u5143\u795E\u4E4B\u529B\uFF0C\u5E76\u62FC\u547D\u5524\u9192\u4ED6\u3002</p><p>\u6B64\u65F6\uFF0C\u68A6\u91CC\u7684\u53F8\u547D\u6BBF\u77AC\u95F4\u71C3\u6210\u7070\u70EC\uFF0C\u5411\u7A7A\u4E2D\u98D8\u98D8\u7136\u800C\u53BB\u3002\u5176\u5B9E\u4E1C\u65B9\u9752\u82CD\u4ECE\u4E00\u5F00\u59CB\u5C31\u77E5\u9053\uFF0C\u773C\u524D\u7684\u5C0F\u5170\u82B1\u4E0D\u662F\u771F\u7684\uFF0C\u6B64\u95F4\u7ECF\u5386\u7684\u79CD\u79CD\u4E0D\u8FC7\u662F\u4E00\u573A\u68A6\u3002\u843D\u53F6\u7FE9\u7FE9\uFF0C\u5C06\u76F8\u7231\u7684\u4E24\u4EBA\u56E2\u56E2\u56F4\u4F4F\u3002\u4E1C\u65B9\u9752\u82CD\u6258\u4F4F\u5C0F\u5170\u82B1\u7684\u8138\uFF0C\u4E0E\u5979\u4F9D\u4F9D\u60DC\u522B\u3002\u5C0F\u5170\u82B1\u54ED\u7740\u7D27\u63E1\u4E1C\u65B9\u9752\u82CD\u7684\u624B\uFF0C\u751F\u6015\u81EA\u5DF1\u4E0D\u7559\u795E\u4ED6\u5C31\u6D88\u5931\u4E0D\u89C1\u3002</p><p>\u4ED6\u4EEC\u660E\u660E\u8BF4\u597D\u4E86\u6C38\u751F\u6C38\u4E16\u90FD\u8981\u5728\u4E00\u8D77\uFF0C\u5C0F\u5170\u82B1\u600E\u4E48\u53EF\u80FD\u8BA9\u4E1C\u65B9\u9752\u82CD\u79BB\u81EA\u5DF1\u800C\u53BB\u3002\u5BF9\u4E8E\u4E1C\u65B9\u9752\u82CD\u800C\u8A00\uFF0C\u5982\u679C\u5C0F\u5170\u82B1\u6D3B\u7740\uFF0C\u5C31\u8981\u5AC1\u7ED9\u522B\u4EBA\uFF0C\u4ED6\u5F53\u7136\u4E0D\u613F\u610F\u3002\u53EF\u662F\u4ED6\u66F4\u60F3\u8BA9\u5C0F\u5170\u82B1\u6D3B\u7740\u3002\u5C0F\u5170\u82B1\u7D27\u7D27\u62B1\u7740\u4E1C\u65B9\u9752\u82CD\uFF0C\u54C0\u6C42\u4ED6\u4E0D\u8981\u79BB\u5F00\u3002\u53EF\u662F\u4E1C\u65B9\u9752\u82CD\u4E0D\u7518\u5FC3\u5C31\u8FD9\u4E48\u9677\u4E8E\u68A6\u5883\u4E4B\u4E2D\uFF0C\u4ED6\u8981\u51FA\u53BB\uFF0C\u65E0\u8BBA\u5982\u4F55\uFF0C\u4ED6\u8981\u590D\u6D3B\u5C0F\u5170\u82B1\u3002</p><p>\u8D64\u5730\u5973\u5B50\u4E0E\u5BB9\u660A\u5728\u7AF9\u6797\u4E2D\u7EC3\u5251\uFF0C\u53EF\u662F\u5979\u5374\u9690\u9690\u89C9\u5F97\uFF0C\u5BB9\u660A\u7684\u5251\u6CD5\u4E0D\u540C\u4EE5\u5F80\u3002\u8FC7\u53BB\uFF0C\u4ED6\u7684\u5251\u610F\u4E4B\u4E2D\u59CB\u7EC8\u6709\u7740\u4E00\u9897\u51E1\u5FC3\uFF0C\u53EF\u73B0\u5728\u5374\u591A\u4E86\u4E00\u4EFD\u6740\u4F10\u4E4B\u6C14\u3002\u5979\u544A\u8BC9\u5BB9\u660A\uFF0C\u8EAB\u4E3A\u6218\u58EB\uFF0C\u4E0D\u7BA1\u66FE\u7ECF\u5931\u53BB\u8FC7\u4EC0\u4E48\uFF0C\u5343\u4E07\u4E0D\u80FD\u88AB\u4EC7\u6068\u8499\u853D\u53CC\u773C\uFF0C\u5426\u5219\u6218\u4E89\u6C38\u8FDC\u4E0D\u4F1A\u505C\u6B62\u3002</p><p>\u8FD9\u51E0\u65E5\u7684\u5E73\u9759\u8BA9\u8D64\u5730\u5973\u5B50\u5FC3\u4E2D\u600E\u4E48\u4E5F\u5E73\u9759\u4E0D\u4E86\uFF0C\u5979\u603B\u89C9\u5F97\u54EA\u91CC\u602A\u602A\u7684\u3002\u8FD9\u5929\uFF0C\u5979\u7EC8\u4E8E\u5FCD\u4E0D\u4F4F\u5288\u5F00\u8FD9\u9053\u7ED3\u754C\uFF0C\u624D\u5F97\u77E5\u81EA\u5DF1\u6D88\u5931\u4E86\u4E09\u4E07\u5E74\u3002\u53EF\u662F\u4E00\u60F3\u8D77\u8FD9\u4E09\u4E07\u5E74\u7684\u4E8B\u60C5\uFF0C\u5979\u4FBF\u89C9\u5F97\u5934\u660F\u76EE\u7729\u3002\u5979\u7EC8\u4E8E\u60F3\u8D77\u4E00\u5207\uFF0C\u4E8E\u662F\u5E26\u7740\u5BB9\u660A\u5230\u4E91\u4E2D\u541B\u9762\u524D\u81EA\u9996\u3002</p><p>\u51B7\u9759\u4E0B\u6765\u4E4B\u540E\uFF0C\u6B87\u9619\u7EC8\u4E8E\u60F3\u660E\u767D\u4E86\uFF0C\u539F\u6765\u7ED3\u9ECE\u6CA1\u6709\u5E26\u8D70\u5C0F\u5170\u82B1\uFF0C\u800C\u4ED6\u81EA\u5DF1\u4E5F\u4E2D\u4E86\u8776\u6BD2\u3002\u53E6\u4E00\u8FB9\uFF0C\u7ED3\u9ECE\u6491\u7740\u6700\u540E\u4E00\u53E3\u6C14\u7ED9\u81EA\u5DF1\u6316\u4E86\u4E00\u5757\u575F\u5730\u3002\u53EF\u8C01\u77E5\u8776\u8863\u7A81\u7136\u51FA\u73B0\uFF0C\u5C06\u89E3\u836F\u5582\u7ED9\u5979\u3002\u8776\u8863\u5C06\u7ED3\u9ECE\u5E26\u5230\u6FA7\u6C85\u9762\u524D\uFF0C\u8BA9\u4ED6\u6551\u51FA\u5BB9\u660A\u3002</p><p>\u5343\u5E74\u4E4B\u524D\uFF0C\u53F8\u547D\u79C1\u81EA\u653E\u8D70\u7F6A\u9F99\u957F\u6E0A\uFF0C\u4E0E\u4ED6\u76F8\u7231\u5E76\u7ED3\u4E3A\u592B\u59BB\uFF0C\u89E6\u72AF\u5929\u89C4\u3002\u4E91\u4E2D\u541B\u5C06\u4ED6\u4EEC\u4E8C\u4EBA\u56DA\u5728\u4E07\u5929\u4E4B\u589F\u4E2D\uFF0C\u4ED6\u4EEC\u4E5F\u5728\u91CC\u9762\u5EFA\u4E86\u4E00\u5EA7\u53F8\u547D\u6BBF\u3002\u4E07\u5929\u4E4B\u589F\u4E43\u4E09\u754C\u4E4B\u5916\uFF0C\u56E0\u7F18\u5BC2\u706D\u4E4B\u5904\uFF0C\u751F\u7075\u6709\u8FDB\u51FA\uFF0C\u552F\u6709\u661F\u6CB3\u6C38\u5728\u3002\u957F\u73E9\u4E0D\u613F\u5C0F\u5170\u82B1\u4F24\u5FC3\uFF0C\u4FBF\u6CA1\u6709\u5C06\u6B64\u4E8B\u544A\u8BC9\u5979\u3002\u53EF\u662F\u4E1C\u65B9\u9752\u82CD\u5374\u6E05\u695A\uFF0C\u5176\u5B9E\u5C0F\u5170\u82B1\u4E00\u76F4\u90FD\u77E5\u9053\u3002</p><p>\u89C1\u5230\u4E86\u53F8\u547D\u548C\u957F\u6E0A\uFF0C\u4E1C\u65B9\u9752\u82CD\u8FD9\u624D\u660E\u767D\uFF0C\u539F\u6765\u5F53\u521D\u5728\u660A\u5929\u5854\u521D\u9047\uFF0C\u5C0F\u5170\u82B1\u5C31\u5DF2\u7ECF\u5F00\u59CB\u5728\u6551\u4ED6\u3002\u53F8\u547D\u544A\u8BC9\u4ED6\uFF0C\u5C0F\u5170\u82B1\u5C1A\u5B58\u6709\u4E00\u7F15\u5143\u795E\u5728\u4ED6\u7684\u4F53\u5185\uFF0C\u82E5\u8981\u590D\u6D3B\u5C0F\u5170\u82B1\uFF0C\u9700\u8981\u5C06\u5979\u7684\u90A3\u7F15\u5143\u795E\u5265\u79BB\u5F00\uFF0C\u653E\u5728\u606F\u5C71\u5723\u5883\u91CC\u5B89\u517B\uFF0C\u4EE5\u5F85\u5316\u5F62\u3002</p><p>\u957F\u73E9\u5FC3\u4E2D\u56F0\u60D1\uFF0C\u5929\u6781\u955C\u4E2D\u662F\u5426\u771F\u7684\u4E3A\u9884\u8A00\u3002\u53F8\u547D\u544A\u8BC9\u4ED6\u4EEC\uFF0C\u955C\u4E2D\u6240\u793A\uFF0C\u7686\u4E3A\u9884\u8A00\uFF0C\u80FD\u591F\u770B\u89C1\u9884\u8A00\u7684\uFF0C\u5B9A\u662F\u53EF\u4EE5\u4FC3\u6210\u8FD9\u6BB5\u59FB\u7F18\u7684\u5E94\u8BB8\u4E4B\u4EBA\uFF0C\u800C\u5929\u6781\u955C\u4E2D\u7684\u672A\u6765\u65E0\u6CD5\u66F4\u6539\u3002\u4E34\u8D70\uFF0C\u53F8\u547D\u5C06\u5C0F\u5170\u82B1\u7684\u547D\u7C3F\u4EA4\u7ED9\u4E1C\u65B9\u9752\u82CD\u3002\u6216\u8BB8\u662F\u56E0\u4E3A\u4ED6\u548C\u81EA\u5DF1\u4E00\u6837\uFF0C\u80FD\u591F\u4ECE\u5341\u4E07\u68F5\u6C34\u5170\u82B1\u4E2D\u5C06\u5979\u627E\u5230\u3002</p>',13),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","32.html.vue"]]);export{h as default};
