import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as t,f as a}from"./app.1218f0f6.js";const c={},o=a('<h1 id="\u7B2C34\u96C6-\u4E1C\u65B9\u9752\u82CD\u6B63\u9762\u6C42\u8BC1\u606F\u82B8" tabindex="-1"><a class="header-anchor" href="#\u7B2C34\u96C6-\u4E1C\u65B9\u9752\u82CD\u6B63\u9762\u6C42\u8BC1\u606F\u82B8" aria-hidden="true">#</a> \u7B2C34\u96C6\uFF1A\u4E1C\u65B9\u9752\u82CD\u6B63\u9762\u6C42\u8BC1\u606F\u82B8</h1><p>\u4E39\u97F3\u548C\u8D64\u5730\u5973\u5B50\u9762\u5BF9\u9762\u5750\u7740\uFF0C\u5174\u594B\u7684\u548C\u5979\u8BF4\u7740\u81EA\u5DF1\u7684\u9AD8\u5174\u3002\u8C01\u6599\u8D64\u5730\u5973\u5B50\u773C\u4E2D\u7A81\u7136\u5347\u817E\u8D77\u6740\u6C14\uFF0C\u624B\u8D77\u5200\u843D\uFF0C\u7A97\u68C2\u4E0A\u88AB\u55B7\u6E85\u4E86\u4E00\u62B9\u7329\u7EA2\u3002\u5E78\u800C\u8FD9\u53EA\u662F\u5979\u7684\u5E7B\u89C9\uFF0C\u4E39\u97F3\u4ECD\u65E7\u5728\u9762\u524D\u8BF4\u7740\u8BDD\u3002\u8D64\u5730\u5973\u5B50\u8D8A\u53D1\u70E6\u8E81\uFF0C\u6025\u5FD9\u8D77\u8EAB\u8EB2\u907F\u4E39\u97F3\uFF0C\u5E76\u50AC\u4FC3\u5979\u8D76\u7D27\u79BB\u5F00\u3002</p><p>\u9759\u6084\u6084\u7684\u591C\uFF0C\u660F\u6C89\u6C89\u7684\u5929\uFF0C\u90A3\u4E9B\u6742\u5FF5\u4F3C\u4E4E\u6700\u559C\u6B22\u8FD9\u6837\u7684\u65F6\u5019\u3002\u8D64\u5730\u5973\u5B50\u6342\u7740\u8033\u6735\u8737\u7F29\u5728\u89D2\u843D\uFF0C\u53EF\u90A3\u4E9B\u8BE1\u5F02\u7684\u58F0\u97F3\u4ECD\u65E7\u6325\u4E4B\u4E0D\u53BB\u3002\u4F8D\u536B\u542C\u5230\u52A8\u9759\uFF0C\u8D76\u5FD9\u524D\u6765\u67E5\u770B\u3002\u53EF\u6B64\u65F6\u7684\u8D64\u5730\u5973\u5B50\u5DF2\u7ECF\u5931\u4E86\u795E\u5FD7\uFF0C\u5F85\u5979\u51B7\u9759\u4E0B\u6765\uFF0C\u624B\u91CC\u7684\u5251\u5DF2\u7ECF\u6CBE\u6EE1\u4E86\u9C9C\u8840\uFF0C\u90A3\u4E9B\u4F8D\u536B\u4EEC\u6A2A\u4E03\u7AD6\u516B\u8EBA\u5728\u5730\u4E0A\u4E00\u52A8\u4E0D\u52A8\u3002</p><p>\u8D64\u5730\u5973\u5B50\u88AB\u795F\u6C14\u4FB5\u8680\u5143\u795E\u4E00\u4E8B\u4F20\u5230\u4E91\u4E2D\u541B\u548C\u957F\u73E9\u90A3\u91CC\u3002\u4E91\u4E2D\u541B\u89C9\u5F97\u8FD9\u4E0D\u8FC7\u662F\u8D64\u5730\u5973\u5B50\u7684\u75AF\u8BDD\uFF0C\u53EF\u957F\u73E9\u5374\u8BA4\u4E3A\u8FD9\u4E8B\u4E0E\u51F6\u795E\u592A\u5C81\u6709\u5173\u3002\u51E0\u756A\u5BF9\u8BDD\u4E0B\u6765\uFF0C\u957F\u73E9\u7EC8\u4E8E\u660E\u767D\uFF0C\u4E91\u4E2D\u541B\u7684\u773C\u4E2D\u53EA\u6709\u4E24\u65CF\u4E4B\u6218\u3002\u8C01\u6599\u4E91\u4E2D\u541B\u4E5F\u4E0D\u906E\u63A9\uFF0C\u751A\u81F3\u8B66\u544A\u957F\u73E9\u4E0D\u8981\u8282\u5916\u751F\u679D\u3002</p><p>\u4E91\u4E2D\u541B\u4E0B\u4EE4\u5C06\u8D64\u5730\u5973\u5B50\u5904\u51B3\uFF0C\u8776\u8863\u8D76\u5FD9\u5C06\u6B64\u4E8B\u544A\u77E5\u5BB9\u660A\u3002\u6DF1\u591C\uFF0C\u6FA7\u6C85\u6765\u5230\u4E39\u97F3\u623F\u4E2D\uFF0C\u66FF\u5979\u6396\u597D\u88AB\u5B50\u3002\u4E39\u97F3\u7F13\u7F13\u9192\u6765\uFF0C\u4E0D\u77E5\u7236\u5C0A\u6DF1\u591C\u524D\u6765\u6240\u4E3A\u4F55\u4E8B\u3002\u6FA7\u6C85\u53EA\u53EE\u5631\u4E39\u97F3\uFF0C\u82E5\u81EA\u5DF1\u4E0D\u5728\uFF0C\u4E00\u5B9A\u7167\u770B\u597D\u7ED3\u9ECE\u3002\u4E39\u97F3\u89C9\u5F97\u7236\u5C0A\u5F88\u662F\u5947\u602A\uFF0C\u5F85\u4ED6\u79BB\u5F00\u540E\uFF0C\u4FBF\u8D76\u5FD9\u53BB\u627E\u7ED3\u9ECE\u6253\u63A2\u60C5\u51B5\u3002\u53EF\u95EE\u6765\u95EE\u53BB\uFF0C\u4E5F\u95EE\u4E0D\u51FA\u4E2A\u6240\u4EE5\u7136\uFF0C\u53EA\u597D\u6084\u6084\u8DDF\u8E2A\u7236\u5C0A\u3002</p><p>\u6FA7\u6C85\u6765\u5230\u6E21\u4E1A\u6E0A\uFF0C\u51C6\u5907\u6253\u5F00\u5929\u7262\u653E\u8D70\u5BB9\u660A\u3002\u8C01\u6599\u4E39\u97F3\u7A81\u7136\u51FA\u73B0\uFF0C\u963B\u6B62\u4E86\u6FA7\u6C85\u3002\u8776\u8863\u8D81\u7236\u5973\u4E24\u4EBA\u4E89\u6267\uFF0C\u6361\u8D77\u7389\u724C\u6253\u5F00\u4E86\u5929\u7262\u3002\u5F85\u7236\u5973\u4E24\u4EBA\u53CD\u5E94\u8FC7\u6765\uFF0C\u6FA7\u6C85\u5DF2\u7ECF\u8EAB\u4E2D\u4E00\u638C\u88AB\u51FB\u5012\u5728\u5730\u3002\u4E39\u97F3\u53C8\u6C14\u53C8\u6025\uFF0C\u60F3\u8981\u62E6\u4F4F\u5BB9\u660A\u3002\u53EF\u8776\u8863\u5374\u631F\u6301\u4E86\u6FA7\u6C85\uFF0C\u60F3\u8981\u5C06\u7236\u5973\u4E24\u4EBA\u706D\u53E3\u3002\u5371\u6025\u65F6\u523B\uFF0C\u7ED3\u9ECE\u7A81\u7136\u51FA\u73B0\uFF0C\u5047\u88C5\u4ECD\u65E7\u662F\u6D77\u5E02\u7684\u7EC6\u4F5C\u3002\u5F85\u8776\u8863\u653E\u677E\u8B66\u60D5\u65F6\uFF0C\u7ED3\u9ECE\u5FFD\u7136\u626D\u8F6C\u5251\u67C4\u76F4\u6307\u8776\u8863\u3002</p><p>\u6FA7\u6C85\u5C06\u4E24\u4E2A\u5973\u513F\u5B89\u987F\u5728\u623F\u4E2D\uFF0C\u6253\u7B97\u72EC\u81EA\u53BB\u5411\u4E91\u4E2D\u541B\u81EA\u9996\u3002\u53EF\u4E39\u97F3\u8BF4\u4EC0\u4E48\u4E5F\u4E0D\u80AF\u6FA7\u6C85\u4E00\u4E2A\u4EBA\u53BB\uFF0C\u7ED3\u9ECE\u65E0\u5948\u53EA\u597D\u5C06\u5979\u6253\u6655\u3002\u4E39\u97F3\u9192\u6765\u5DF2\u7ECF\u4E8E\u4E8B\u65E0\u8865\uFF0C\u4E0D\u7531\u5F97\u8FC1\u6012\u7ED3\u9ECE\u3002\u7ED3\u9ECE\u4E5F\u4E0D\u751F\u6C14\uFF0C\u56E0\u4E3A\u5979\u5E76\u4E0D\u540E\u6094\u505A\u8FD9\u4E9B\u4E8B\u60C5\u3002\u53EF\u8776\u8863\u4E00\u6B7B\uFF0C\u5979\u4FBF\u6CA1\u4E86\u89E3\u836F\u3002</p><p>\u89C1\u795E\u5973\u8D70\u6765\uFF0C\u4E1C\u65B9\u9752\u82CD\u8D76\u5FD9\u8FCE\u4E0A\u53BB\u3002\u795E\u5973\u544A\u8BC9\u4ED6\uFF0C\u81EA\u5DF1\u5DF2\u7ECF\u77E5\u6653\uFF0C\u4ED6\u548C\u5C0F\u5170\u82B1\u4E4B\u95F4\u66FE\u6709\u4E00\u6BB5\u60C5\u7F18\uFF0C\u8FD8\u5DEE\u70B9\u7ED3\u4E3A\u592B\u59BB\uFF0C\u8FD9\u4E00\u5207\u90FD\u662F\u957F\u73E9\u544A\u8BC9\u81EA\u5DF1\u7684\u3002\u53EF\u662F\u795E\u5973\u7EC8\u7A76\u4E0D\u662F\u5C0F\u5170\u82B1\uFF0C\u5C0F\u5170\u82B1\u4E5F\u6C38\u8FDC\u90FD\u4E0D\u4F1A\u56DE\u6765\u4E86\u3002\u53EF\u5BF9\u4E8E\u4E1C\u65B9\u9752\u82CD\u800C\u8A00\uFF0C\u773C\u524D\u7684\u795E\u5973\u5C31\u662F\u5C0F\u5170\u82B1\u3002</p><p>\u795E\u5973\u53EA\u597D\u5766\u8A00\uFF0C\u5F53\u5E74\u53F8\u547D\u4E3A\u4E86\u85CF\u5979\uFF0C\u4F2A\u9020\u4E86\u4E00\u68F5\u666E\u901A\u5170\u8349\u7684\u5047\u8C61\uFF0C\u906E\u76D6\u606F\u5170\u5723\u8349\u7684\u771F\u8EAB\uFF0C\u800C\u5C0F\u5170\u82B1\u53EA\u662F\u9644\u7740\u5728\u8FD9\u4E2A\u5047\u8C61\u4E4B\u4E0A\u7684\u4E00\u7F15\u5FAE\u5F31\u7684\u8BB0\u5FC6\u3002\u5982\u4ECA\uFF0C\u5047\u8C61\u88AB\u63ED\u7A7F\uFF0C\u5C0F\u5170\u82B1\u4E5F\u5C31\u968F\u4E4B\u70DF\u6D88\u4E91\u6563\u3002\u4E1C\u65B9\u9752\u82CD\u4F4E\u4E0B\u5934\uFF0C\u4E0D\u80AF\u63A5\u53D7\u795E\u5973\u6240\u8A00\u3002\u53EF\u65E0\u8BBA\u4ED6\u600E\u4E48\u89E3\u91CA\uFF0C\u90FD\u65E0\u6CD5\u8BF4\u670D\u795E\u5973\uFF0C\u751A\u81F3\u8BF4\u670D\u4E0D\u4E86\u81EA\u5DF1\u3002</p><p>\u4E39\u97F3\u80CC\u7740\u6BD2\u53D1\u7684\u7ED3\u9ECE\u524D\u5F80\u606F\u5C71\u6C42\u89C1\u795E\u5973\uFF0C\u8DEF\u4E0A\uFF0C\u7ED3\u9ECE\u7EC8\u4E8E\u5524\u4E86\u5979\u4E00\u58F0\u201C\u963F\u59D0\u201D\u3002\u795E\u5973\u7B54\u5E94\u533B\u6CBB\u7ED3\u9ECE\uFF0C\u5E76\u4ECE\u4E39\u97F3\u90A3\u91CC\u5F97\u77E5\u4E86\u6C34\u4E91\u5929\u7684\u4E8B\u60C5\u3002\u5979\u8D76\u5FD9\u548C\u957F\u73E9\u524D\u5F80\u5C71\u6D1E\uFF0C\u53EF\u51F6\u795E\u592A\u5C81\u5DF2\u7ECF\u4E0D\u89C1\u8E2A\u5F71\u3002</p><p>\u6B87\u9619\u514B\u5236\u7740\u81EA\u5DF1\u7684\u60C5\u7EEA\uFF0C\u53EF\u662F\u65E0\u8BBA\u5982\u4F55\u4E5F\u65E0\u6CD5\u514B\u5236\u5BF9\u7ED3\u9ECE\u7684\u5173\u5FC3\u3002\u7136\u800C\u7ED3\u9ECE\u5374\u4E0D\u60F3\u5F97\u5230\u4ED6\u7684\u539F\u8C05\uFF0C\u4E5F\u4E0D\u60F3\u548C\u4ED6\u7EE7\u7EED\u7EA0\u7F20\u3002\u56E0\u4E3A\u5979\u89C9\u5F97\uFF0C\u81EA\u5DF1\u4E0D\u914D\u62E5\u6709\u6B87\u9619\u7F8E\u597D\u7684\u7231\u3002\u4E39\u97F3\u60F3\u8981\u529D\u6170\u7ED3\u9ECE\uFF0C\u53EF\u662F\u5979\u53C8\u4F55\u5C1D\u4E0D\u662F\u5728\u7231\u4E2D\u81EA\u5351\u3002\u5979\u77E5\u9053\u957F\u73E9\u559C\u6B22\u5C0F\u5170\u82B1\u800C\u975E\u795E\u5973\uFF0C\u53EF\u5979\u4E0D\u660E\u767D\uFF0C\u957F\u73E9\u4E3A\u4F55\u4ECD\u65E7\u8981\u548C\u795E\u5973\u5B8C\u5A5A\u3002\u957F\u73E9\u4E0D\u613F\u591A\u8A00\uFF0C\u53EA\u9053\u81EA\u5DF1\u613F\u610F\u627F\u62C5\u4E00\u5207\u540E\u679C\u3002</p>',11),r=[o];function _(n,s){return p(),t("div",null,r)}const h=e(c,[["render",_],["__file","34.html.vue"]]);export{h as default};
