import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as e,f as t}from"./app.eee000b7.js";const c={},r=t('<h1 id="\u540E\u71D5\u7684\u8870\u843D" tabindex="-1"><a class="header-anchor" href="#\u540E\u71D5\u7684\u8870\u843D" aria-hidden="true">#</a> \u540E\u71D5\u7684\u8870\u843D</h1><blockquote><p>\u5F15\uFF08\u6155\u5BB9\u5B9D\uFF09\u5B9D\u5165\u9F99\u57CE\u5916\u90B8\uFF0C\u5F11\u4E4B\uFF0C\u8C25\u5B9D\u66F0\u7075\u5E1D\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u4E00\u96F6\xB7\u664B\u7EAA\u4E09\u5341\u4E8C\u300B</strong></p><p><code>\u540E\u71D5</code>\u7684\u8870\u843D\uFF0C\u5728<code>\u6155\u5BB9\u5782</code>\u75C5\u901D\u7684\u90A3\u4E00\u523B\uFF0C\u5373\u5DF2\u6CE8\u5B9A\u3002</p><p><strong>\u5173\u952E\u5728\u4E8E<code>\u6155\u5BB9\u5B9D</code></strong>\u3002</p><p>\u6155\u5BB9\u5B9D\u5728\u6155\u5BB9\u5782\u53BB\u4E16\u540E\uFF0C\u4EE5\u592A\u5B50\u7EE7\u4F4D\uFF0C\u76F4\u5230\u4E24\u5E74\u540E\u88AB\u6740\u3002\u5728\u4ED6\u5728\u4F4D\u7684\u8FD9\u4E24\u5E74\u95F4\uFF0C<strong>\u540E\u71D5\u7684\u8870\u843D\u5B8C\u5168\u5C31\u662F\u65AD\u5D16\u5F0F\u7684\u81EA\u7531\u843D\u4F53</strong>\u3002\u8FD9\u5176\u4E2D\u65E2\u6709\u6155\u5BB9\u5B9D\u4E2A\u4EBA\u80FD\u529B\u4E0D\u8DB3\u7684\u56E0\u7D20\uFF0C\u4E5F\u662F\u4E00\u4E2A\u7EC4\u7EC7\u8FDB\u5165\u4E0B\u964D\u901A\u9053\u540E\u7684\u5FC5\u7136\u3002</p><p>\u524D\u7BC7\u6211\u4EEC\u66FE\u7ECF\u63D0\u53CA\uFF0C\u6155\u5BB9\u5782\u662F\u5728\u73ED\u5E08\u56DE\u671D\u7684\u9014\u4E2D\u75C5\u901D\u7684\u3002\u56E0\u6B64\uFF0C\u4E0D\u59A8\u5C31\u4ECE\u8FD9\u6B21\u5BF9\u540E\u71D5\u56FD\u8FD0\u6709\u7740\u91CD\u5927\u5F71\u54CD\u7684\u51FA\u5F81\u5F00\u59CB\u8BF4\u8D77\u3002</p><p>\u5F53\u65F6\uFF0C\u4E2D\u56FD\u5317\u65B9\u4E09\u96C4\u5E76\u7ACB\uFF0C\u5206\u522B\u662F\uFF1A\u897F\u65B9\u7684<code>\u540E\u79E6</code>\u3001\u4E1C\u65B9\u7684<code>\u540E\u71D5</code>\uFF0C\u4EE5\u53CA\u5317\u90E8\u7684<code>\u4EE3\u56FD</code>\uFF08\u540E\u6539\u56FD\u53F7\u4E3A\u9B4F\uFF0C\u662F\u4E3A<code>\u5317\u9B4F</code>\uFF09\u3002</p><p>\u6700\u521D\uFF0C\u62D3\u8DCB\u6C0F\u4EE3\u56FD\uFF08\u5317\u9B4F\uFF09\u4E0E\u6155\u5BB9\u6C0F\u540E\u71D5\u7684\u5173\u7CFB\u5C1A\u53EF\u3002\u4E24\u56FD\u5747\u4E3A\u9C9C\u5351\u65CF\uFF0C\u867D\u7136\u5206\u5C5E\u4E8E\u4E0D\u540C\u90E8\u843D\uFF0C\u4F46\u76F8\u4E92\u4E4B\u95F4\u6570\u4EE3\u8054\u59FB\uFF0C\u4EA4\u60C5\u751A\u7B03\u3002</p><p>\u76F4\u5230\u5982\u4ECA\uFF0C\u968F\u7740\u6155\u5BB9\u5782\u8001\u53BB\uFF0C\u4EE5\u53CA\u5317\u9B4F\u6269\u5F20\u7684\u91CE\u5FC3\u4E0D\u65AD\u53D8\u5927\uFF0C\u53CC\u65B9\u53D1\u751F\u6218\u4E89\u5DF2\u7ECF\u5728\u6240\u96BE\u514D\u3002</p><p>\u516C\u5143395\u5E74\uFF0C\u540E\u71D5\u4EE5\u5317\u9B4F\u53CD\u53DB\u4E3A\u7531\uFF0C\u51FA\u5175\u653B\u6253\u3002\u6B64\u65F6\u71D5\u5F3A\u9B4F\u5F31\uFF0C\u540E\u71D5\u9886\u5175\u51FA\u5F81\u7684\u6B63\u662F\u592A\u5B50\u6155\u5BB9\u5B9D\u3002</p><p><strong>\u4EE5\u592A\u5B50\u8EAB\u4EFD\u9886\u5175\u51FA\u5F81\uFF0C\u5176\u5B9E\u6B8A\u6709\u4E0D\u4FBF</strong>\uFF1A\u80DC\u4E86\uFF0C\u8D4F\u65E0\u53EF\u8D4F\uFF0C\u6155\u5BB9\u5782\u603B\u4E0D\u80FD\u63D0\u524D\u9000\u4F4D\u53BB\u5F53\u592A\u4E0A\u7687\u5427\uFF1F\u8D25\u4E86\uFF0C\u65E0\u6CD5\u8D23\u7F5A\uFF0C\u96BE\u4E0D\u6210\u771F\u7684\u628A\u7687\u592A\u5B50\u7ED9\u5E9F\u4E86\uFF1F</p><p>\u4F46\u6155\u5BB9\u5B9D\u53C8\u5B9E\u5728\u6709\u4E0D\u5F97\u4E0D\u51FA\u5F81\u7684\u7406\u7531\uFF1A\u5F53\u65F6\u5929\u4E0B\u5927\u4E71\uFF0C\u5404\u56FD\u95F4\u5F81\u6218\u9891\u7E41\uFF0C\u738B\u516C\u8D35\u65CF\u65E0\u4E0D\u4EB2\u4E0A\u6218\u9635\uFF0C\u535A\u53D6\u529F\u540D\u3002\u5C24\u5176\u540E\u71D5\u4E43\u4E00\u519B\u4E8B\u5F3A\u56FD\uFF0C\u592A\u5B50\u5982\u679C\u6CA1\u6709\u519B\u529F\uFF0C\u90A3\u4E48\u5B8C\u5168\u65E0\u6CD5\u7EDF\u5FA1\u519B\u961F\uFF0C\u5730\u4F4D\u4E5F\u4F1A\u5C8C\u5C8C\u53EF\u5371\u3002\u56E0\u6B64\uFF0C\u6155\u5BB9\u5B9D\u65E2\u6025\u9700\u6218\u529F\uFF0C\u4E5F\u6025\u9700\u4ECE\u6218\u573A\u4E0A\u83B7\u5F97\u5BF9\u519B\u961F\u7684\u5F71\u54CD\u529B\u3002</p><p>\u60F3\u6765\u60F3\u53BB\uFF0C\u5317\u9B4F\u662F\u4E2A\u5F88\u597D\u7684\u9009\u62E9\uFF0C\u73AF\u4F3A\u5468\u56F4\uFF0C\u53EA\u6709\u5B83\u5B9E\u529B\u6700\u5F31\uFF0C\u6B63\u597D\u53EF\u4EE5\u634F\u4E2A\u8F6F\u67FF\u5B50\u3002</p><p>\u4F46\u672C\u4EE5\u4E3A\u5B83\u662F\u4E2A\u9752\u94DC\uFF0C\u6CA1\u60F3\u5230\u5374\u662F\u4E2A\u738B\u8005\u3002<strong>\u4E00\u6218\u4E0B\u6765\uFF0C\u6155\u5BB9\u5B9D\u5927\u8D25\u4E8F\u8F93</strong>\u3002</p><p>\u800C\u4E14\u5317\u9B4F<code>\u62D3\u8DCB\u73EA</code>\u4E00\u65B9\u5728\u80DC\u4E86\u4E4B\u540E\uFF0C\u8FD8\u505A\u4E86\u4E00\u4EF6\u66F4\u4E3A\u6B8B\u5FCD\u7684\u4E8B\uFF1A<strong>\u5C06\u4FD8\u864F\u7684\u56DB\u4E07\u71D5\u519B\u5C06\u58EB\uFF0C\u5168\u90E8\u5751\u6740</strong>\u2014\u2014\u62D3\u8DCB\u73EA\u6709\u4ED6\u81EA\u5DF1\u7684\u8003\u8651\uFF0C\u8FD9\u56DB\u4E07\u4EBA\u5982\u679C\u653E\u56DE\u53BB\u4E86\uFF0C\u6765\u5E74\u80AF\u5B9A\u8FD8\u4F1A\u518D\u6765\u3002\u65E2\u7136\u5DF2\u7ECF\u8DDF\u71D5\u56FD\u6495\u7834\u4E86\u8138\uFF0C\u90A3\u4E48\u8FD8\u4E0D\u5982\u65A9\u8349\u9664\u6839\u3002</p><p>\u6155\u5BB9\u5B9D\u51E0\u8FD1\u4E8E\u53EA\u8EAB\u9003\u56DE\u3002</p><p>\u627F\u53D7\u5982\u6B64\u5927\u8D25\uFF0C\u540E\u71D5\u81EA\u7136\u4E0D\u53EF\u80FD\u5584\u7F62\u7518\u4F11\u3002\u63A5\u4E0B\u6765\uFF0C\u4FBF\u662F\u6155\u5BB9\u5782\u4EB2\u81EA\u9886\u5175\u51FA\u5F81\u3002\u6B64\u6218\u71D5\u519B\u83B7\u80DC\uFF0C\u4F46\u867D\u7136\u653B\u5360\u4E86\u5317\u9B4F\u7684\u9996\u90FD\u5E73\u57CE\uFF0C\u5374\u5E76\u6CA1\u6709\u627E\u5230\u9B4F\u519B\u4E3B\u529B\u3002\u6155\u5BB9\u5782\u6B64\u65F6\u5DF2\u7ECF\u91CD\u75C5\u7F20\u8EAB\uFF0C\u53EA\u80FD\u5148\u884C\u56DE\u5E08\u3002</p><p>\u5728\u56DE\u5E08\u7684\u8DEF\u4E0A\uFF0C\u6155\u5BB9\u5782\u75C5\u6545\uFF0C\u7EC8\u5E7471\u5C81\u3002</p><p>\u63A5\u4E0B\u6765\uFF0C\u6155\u5BB9\u5B9D\u7684\u60B2\u60E8\u5C81\u6708\u624D\u771F\u6B63\u5F00\u542F\u3002</p><p>\u5728\u6155\u5BB9\u5782\u53BB\u4E16\u51E0\u4E2A\u6708\u540E\uFF0C<code>\u62D3\u8DCB\u73EA</code>\u5373\u4FBF\u51FA\u5175\u3002\u51E0\u4E4E\u6CA1\u6709\u8D39\u591A\u5C11\u529B\u6C14\uFF0C\u5927\u519B\u4FBF\u5360\u636E\u4E86\u539F\u672C\u5C5E\u4E8E\u540E\u71D5\u7684\u5E76\u5DDE\uFF0C\u5E76\u968F\u5373\u5F15\u5175\u8FDB\u5165\u4E86\u540E\u71D5\u7684\u7EDF\u6CBB\u4E2D\u5FC3\u5730\u5E26\u2014\u2014\u6CB3\u5317\u5E73\u539F\u3002</p><p>\u9762\u5BF9\u6765\u88AD\u7684\u5927\u519B\uFF0C\u540E\u71D5\u671D\u5EF7\u7EC4\u7EC7\u8FDB\u884C\u4E86\u8BA4\u771F\u8BA8\u8BBA\uFF0C\u8BA4\u4E3A\u654C\u4EBA\u5F88\u5F3A\u5927\uFF0C\u65E0\u6CD5\u6B63\u9762\u5BF9\u6297\uFF0C\u53EA\u80FD\u662F\u575A\u58C1\u6E05\u91CE\uFF0C\u4EE5\u5B88\u4E3A\u653B\u3002</p><p>\u4F46\u8FD9\u4E0D\u8FC7\u662F\u540E\u71D5\u671D\u5EF7\u7684\u4E00\u53A2\u60C5\u613F\u3002\u5728\u6CA1\u6709\u671D\u5EF7\u5927\u519B\u652F\u6301\u7684\u60C5\u51B5\u4E0B\uFF0C\u968F\u7740\u62D3\u8DCB\u73EA\u4E0D\u65AD\u6DF1\u5165\uFF0C\u5730\u65B9\u5B88\u540F\u4E0D\u613F\u610F\u6B63\u9762\u62B5\u6297\uFF0C\u7EB7\u7EB7\u6295\u964D\u3002</p><p>\u4EC5\u4EC5\u51E0\u4E2A\u6708\u7684\u65F6\u95F4\uFF0C\u540E\u71D5\u4FBF\u5931\u53BB\u4E86\u5927\u7247\u9886\u571F\uFF0C\u6155\u5BB9\u5B9D\u56F0\u5B88\u4E8E\u90FD\u57CE\u4E2D\u5C71\u3002</p><p>\u4F46\u968F\u5373\u4E2D\u5C71\u4E5F\u4E22\u4E86\u3002</p><p>\u6700\u5F00\u59CB\uFF0C\u5317\u9B4F\u56F4\u653B\u4FE1\u90FD\uFF0C\u6155\u5BB9\u5B9D\u9886\u5175\u6551\u63F4\uFF0C\u4F46\u5728\u56DE\u4E2D\u5C71\u7684\u8DEF\u4E0A\u5374\u906D\u9047\u622A\u6740\uFF0C\u5927\u8D25\u800C\u5F52\u3002\u4E3A\u4E86\u963B\u65AD\u8FFD\u519B\uFF0C\u6155\u5BB9\u5B9D\u751A\u81F3\u547D\u5168\u4F53\u5C06\u58EB\u4E22\u76D4\u5378\u7532\u4EE5\u963B\u585E\u9053\u8DEF\u3002</p><p>\u56DE\u5230\u4E2D\u5C71\u57CE\u5185\u540E\u4E0D\u4E45\uFF0C\u57CE\u4E2D\u4FBF\u53D1\u751F\u4E86\u53DB\u4E71\uFF0C\u867D\u7136\u6700\u7EC8\u88AB\u5E73\u5B9A\uFF0C\u4F46\u4EBA\u5FC3\u60F6\u60F6\u7684\u5C40\u9762\u5DF2\u7ECF\u65E0\u6CD5\u633D\u56DE\u3002</p><p>\u7136\u540E\uFF0C\u6155\u5BB9\u5B9D\u7684\u513F\u5B50<code>\u6155\u5BB9\u4F1A</code>\u4E5F\u88AB\u7275\u6D89\u4E86\u8FDB\u6765\u3002</p><p>\u5728\u6155\u5BB9\u5B9D\u7684\u513F\u5B50\u4E2D\uFF0C\u6155\u5BB9\u4F1A\u662F\u80FD\u529B\u6BD4\u8F83\u51FA\u4F17\u7684\u4E00\u4E2A\uFF0C\u5F53\u5E74\u6155\u5BB9\u5782\u8FD8\u5728\u4E16\u7684\u65F6\u5019\u5C31\u5F88\u559C\u6B22\u4ED6\u3002\u4F46\u95EE\u9898\u662F\uFF0C\u7237\u7237\u559C\u6B22\uFF0C\u4E0D\u4EE3\u8868\u7238\u7238\u4E5F\u559C\u6B22\uFF0C\u6155\u5BB9\u5B9D\u5728\u5373\u4F4D\u4EE5\u540E\uFF0C\u5E76\u6CA1\u6709\u7ACB\u6155\u5BB9\u4F1A\u4E3A\u592A\u5B50\u3002</p><p>\u7531\u6B64\uFF0C\u6155\u5BB9\u4F1A\u8BB0\u6068\u5728\u5FC3\u3002</p><p>\u5F53\u6155\u5BB9\u5B9D\u88AB\u9B4F\u519B\u56F4\u5728\u4E2D\u5C71\uFF0C\u8EAB\u5728\u5317\u65B9\u9547\u5B88\u7684\u6155\u5BB9\u4F1A\u4FBF\u4E0A\u4E66\u8BF7\u547D\u6551\u63F4\uFF0C\u6155\u5BB9\u5B9D\u4E5F\u540C\u610F\u4E86\u3002\u4F46\u6155\u5BB9\u4F1A\u51FA\u53D1\u4EE5\u540E\uFF0C\u5374\u5728\u8DEF\u4E0A\u78E8\u78E8\u8E6D\u8E6D\uFF0C\u751A\u81F3\u4E8E\u9A7B\u624E\u5728\u9014\u4E2D\u4E00\u52A8\u4E0D\u52A8\uFF0C\u4EFB\u51ED\u6155\u5BB9\u5B9D\u53CD\u590D\u50AC\u4FC3\uFF0C\u6155\u5BB9\u4F1A\u90FD\u7F6E\u82E5\u7F54\u95FB\u3002</p><p>\u8FD9\u79CD\u884C\u4E3A\uFF0C\u5728\u6155\u5BB9\u5B9D\u770B\u6765\u5C31\u7279\u522B\u6076\u52A3\u4E86\uFF1A\u6155\u5BB9\u4F1A\u5206\u660E\u5C31\u662F\u8981\u501F\u5200\u6740\u4EBA\uFF0C\u4F60\u60F3\u8BA9\u9B4F\u519B\u653B\u7834\u4E2D\u5C71\u6740\u6389\u8001\u5B50\uFF0C\u7136\u540E\u518D\u81EA\u5DF1\u51FA\u9762\u633D\u6551\u5C40\u9762\uFF0C\u987A\u5229\u79F0\u5E1D\uFF01</p><p>\u7236\u5B50\u5ACC\u9699\u5DF2\u6210\u3002</p><p>\u5728\u57CE\u5185\u7684\u6218\u5B88\u95EE\u9898\u4E0A\uFF0C\u6155\u5BB9\u5B9D\u4E5F\u4E3E\u68CB\u4E0D\u5B9A\u3002</p><p>\u9AD8\u9633\u738B<code>\u6155\u5BB9\u9686</code>\uFF08\u4EE5\u4E0B\u7B80\u79F0\u9AD8\u9633\u738B\u9686\uFF0C\u540E\u540C\uFF09\u5E0C\u671B\u51FA\u57CE\u5E94\u6218\uFF0C\u4EE5\u653B\u4E3A\u5B88\uFF1B\u4F46\u8D75\u738B<code>\u6155\u5BB9\u9E9F</code>\u5374\u8868\u793A\u53CD\u5BF9\uFF0C\u4EE5\u81F3\u4E8E\u9AD8\u9633\u738B\u9686\u7684\u53CD\u653B\u8BA1\u591A\u6B21\u53D7\u963B\u3002\u67D0\u6B21\uFF0C\u9AD8\u9633\u738B\u9686\u90FD\u5DF2\u7ECF\u5E26\u5175\u8D70\u5230\u4E86\u57CE\u95E8\u53E3\uFF0C\u5374\u8FD8\u662F\u88AB\u8D75\u738B\u9E9F\u7ED9\u62E6\u4E86\u56DE\u6765\u3002</p><p>\u8FD9\u662F\u56E0\u4E3A\u8D75\u738B\u9E9F\u6301\u91CD\u5417\uFF1F\u5E76\u4E0D\u662F\uFF0C\u800C\u662F\u56E0\u4E3A\u4ED6\u6709\u5F02\u5FC3\u3002</p><p>\u4E0D\u4E45\uFF0C\u8D75\u738B\u9E9F\u4FBF\u9F13\u52A8\u5317\u5730\u738B<code>\u6155\u5BB9\u7CBE</code>\u53CD\u53DB\uFF0C\u6740\u6389\u71D5\u5E1D\u6155\u5BB9\u5B9D\uFF0C\u4F46\u5374\u88AB\u5317\u5730\u738B\u7CBE\u62D2\u7EDD\u3002\u60C5\u6025\u4E4B\u4E0B\uFF0C\u8D75\u738B\u9E9F\u6740\u6B7B\u5317\u5730\u738B\u7CBE\uFF0C\u9003\u51FA\u4E2D\u5C71\u3002</p><p>\u8FD9\u6B21\u672A\u9042\u7684\u653F\u53D8\uFF0C\u7ED9\u6155\u5BB9\u5B9D\u5E26\u6765\u4E86\u6781\u5927\u60F6\u6050\u3002\u8D75\u738B\u9E9F\u4E00\u4E2A\u4EBA\u4E0D\u53EF\u6015\uFF0C\u4F46\u5982\u679C\u4ED6\u51FA\u8D70\u4EE5\u540E\u8DDF\u6155\u5BB9\u4F1A\u52FE\u7ED3\u5462\uFF1F\u5728\u4EE5\u524D\uFF0C\u8FD9\u4E0D\u662F\u95EE\u9898\u3002\u4F46\u73B0\u5982\u4ECA\uFF0C\u4E24\u4E2A\u4EBA\u6709\u4E86\u5171\u540C\u7684\u76EE\u6807\uFF0C\u800C\u4E14\u8FD9\u4E2A\u76EE\u6807\u8FD8\u5C31\u662F\u88AB\u56F4\u5728\u4E2D\u5C71\u57CE\u5185\u7684\u81EA\u5DF1\uFF0C\u90A3\u4E48\u5C40\u9762\u5C31\u4E0D\u597D\u8BF4\u4E86\u3002</p><p>\u56E0\u6B64\uFF0C\u6155\u5BB9\u5B9D\u505A\u4E86\u4E00\u4E2A\u51B3\u5B9A\uFF1A<strong>\u5E26\u5175\u64A4\u51FA\u4E2D\u5C71\uFF0C\u62A2\u5148\u4E0E\u6155\u5BB9\u4F1A\u90E8\u56DE\u5408\uFF0C\u593A\u53D6\u519B\u961F\u63A7\u5236\u6743</strong>\u3002</p><p>\u5802\u5802\u4E00\u56FD\u56FD\u541B\uFF0C\u7ADF\u7136\u4EE5\u8FD9\u6837\u7684\u7406\u7531\uFF0C\u9003\u79BB\u4E86\u90FD\u57CE\u3002</p><p>\u800C\u4E14\u5728\u5175\u5408\u4E00\u5904\u540E\uFF0C\u6155\u5BB9\u5B9D\u5E76\u6CA1\u6709\u53CD\u653B\uFF0C\u800C\u662F\u7EE7\u7EED\u64A4\u9000\u3002\u5148\u5230\u4E86\u84DF\u57CE\uFF0C\u89C9\u5F97\u8FD8\u4E0D\u5B89\u5168\uFF0C\u53C8\u7EE7\u7EED\u540E\u64A4\uFF0C\u51C6\u5907\u64A4\u5230\u9F99\u57CE\u3002</p><p>\u8001\u7239\u8868\u73B0\u5F97\u5982\u6B64\u9F20\u80C6\uFF0C\u6155\u5BB9\u4F1A\u5F88\u9119\u89C6\u3002\u4ED6\u8BF7\u6C42\u9886\u5175\u8FCE\u51FB\u8FFD\u519B\uFF0C\u6155\u5BB9\u5B9D\u4E5F\u540C\u610F\u4E86\u3002\u7ED3\u679C\uFF0C\u6155\u5BB9\u4F1A\u5927\u8D25\u8FFD\u519B\uFF0C\u8FD9\u7B80\u76F4\u5C31\u662F\u5F53\u4F17\u62BD\u6155\u5BB9\u5B9D\u7684\u8033\u5149\u3002</p><p>\u56E0\u6B64\u4E00\u4E8B\uFF0C\u6155\u5BB9\u4F1A\u5F00\u59CB\u6709\u4E86\u53D6\u800C\u4EE3\u4E4B\u7684\u5FC3\u601D\u3002\u800C\u6155\u5BB9\u5B9D\u4E5F\u5BDF\u89C9\u5230\u4E86\u513F\u5B50\u7684\u5F02\u5FD7\uFF0C\u51C6\u5907\u5148\u4E0B\u624B\u4E3A\u5F3A\u3002</p><p>\u4E0D\u4E45\uFF0C\u7236\u5B50\u53CD\u76EE\u3002\u706B\u5E76\u4E4B\u4E0B\uFF0C\u53CC\u65B9\u4E92\u6709\u635F\u4F24\uFF0C\u6155\u5BB9\u4F1A\u53D7\u4F24\uFF0C\u6155\u5BB9\u5B9D\u9003\u56DE\u9F99\u57CE\u3002</p><p>\u6B64\u65F6\uFF0C\u540E\u71D5\u7684\u56FD\u571F\u5DF2\u7ECF\u88AB\u5317\u9B4F\u751F\u751F\u622A\u65AD\uFF0C\u5357\u90E8\u57FA\u672C\u4E0A\u88AB**\u8303\u9633\u738B<code>\u6155\u5BB9\u5FB7</code>**\u63A7\u5236\uFF0C\u5317\u90E8\u5219\u6709\u4E24\u80A1\u52BF\u529B\uFF1A\u6155\u5BB9\u5B9D\u4E0E\u6155\u5BB9\u4F1A\u3002</p><p>\u53CC\u65B9\u5F88\u5FEB\u4FBF\u518D\u6B21\u4EA4\u6218\u3002<strong>\u8FD9\u6B21\uFF0C\u6155\u5BB9\u5B9D\u83B7\u5F97\u4E86\u80DC\u5229\uFF0C\u6155\u5BB9\u4F1A\u88AB\u6740</strong>\u3002</p><p>\u6B64\u540E\u4E0D\u4E45\uFF0C\u8303\u9633\u738B\u5FB7\u4E0E\u6155\u5BB9\u5B9D\u4E4B\u95F4\u6062\u590D\u4E86\u8054\u7CFB\uFF0C\u4ED6\u6D3E\u4EBA\u529D\u8BF4\u6155\u5BB9\u5B9D\u5357\u5F81\uFF0C\u593A\u56DE\u4E2D\u5C71\u3002\u8FD9\u4E2A\u65F6\u5019\u7684\u540E\u71D5\u5B9E\u529B\u5927\u635F\uFF0C\u4E8B\u5B9E\u4E0A\u5DF2\u7ECF\u6CA1\u6709\u8DDF\u5317\u9B4F\u4E00\u8F83\u9AD8\u4E0B\u7684\u80FD\u529B\uFF0C\u4F46\u6155\u5BB9\u5B9D\u8FD8\u662F\u4E0D\u987E\u529D\u963B\uFF0C\u5174\u5175\u5357\u4E0B\uFF0C\u51C6\u5907\u593A\u56DE\u4E2D\u5C71\u3002</p><p>\u5C40\u9762\u518D\u8D77\u6CE2\u6F9C\u3002\u9014\u4E2D\uFF0C\u56E0\u4E3A\u90E8\u4E0B\u53DB\u4E71\uFF0C\u6155\u5BB9\u5B9D\u4E0D\u5F97\u4EE5\u800C\u9003\u8D70\u3002\u71D5\u519B\u5185\u90E8\u5F00\u59CB\u706B\u5E76\uFF0C\u58EB\u5352\u5F88\u5FEB\u79BB\u6563\u3002<strong>\u6155\u5BB9\u5B9D\u5E26\u4E86\u4E09\u4E07\u4EBA\u51FA\u5F81\uFF0C\u672A\u7ECF\u4E00\u6218\uFF0C\u519B\u58EB\u4FBF\u6D88\u5931\u6B86\u5C3D</strong>\u3002</p><p>\u6155\u5BB9\u5B9D\u53EA\u5F97\u518D\u56DE\u9F99\u57CE\uFF0C\u4F46\u9F99\u57CE\u6B64\u65F6\u5DF2\u7ECF\u88AB\u53E6\u4E00\u62E8\u90E8\u4E0B\u5360\u636E\u3002\u4E0D\u77E5\u60C5\u7684\u6155\u5BB9\u5B9D\u5728\u8FDB\u57CE\u540E\uFF0C\u6700\u7EC8\u88AB\u6740\u3002</p><p>\u81F3\u4E8E\u4ED6\u8EAB\u540E\u7684\u540E\u71D5\uFF0C\u5219\u4F9D\u65E7\u5728\u5185\u4E71\u4E2D\u7EB7\u4E89\u4E0D\u5DF2\u3002\u6CA1\u8FC7\u591A\u4E45\uFF0C\u5373\u88AB<code>\u5317\u71D5</code>\u53D6\u4EE3\u3002</p><p>\u4EE5\u6155\u5BB9\u5782\u4E4B\u6B7B\u4E3A\u754C\uFF0C\u540E\u71D5\u7684\u8870\u843D\u51E0\u4E4E\u662F\u65AD\u5D16\u5F0F\u7684\u3002\u51E0\u5E74\u4E4B\u95F4\uFF0C\u8FD9\u4E2A\u66FE\u7ECF\u96C4\u9738\u4E2D\u56FD\u4E1C\u90E8\u7684\u5E1D\u56FD\u4FBF\u5206\u5D29\u79BB\u6790\u3002</p><p>\u4F20\u7EDF\u4E0A\uFF0C<strong>\u8BA4\u4E3A\u540E\u71D5\u7684\u8870\u843D\u662F\u56E0\u4E3A\u6155\u5BB9\u5B9D</strong>\u3002\u6155\u5BB9\u5B9D\u65E0\u96C4\u624D\u5927\u7565\uFF0C\u884C\u4E8B\u884C\u4E3A\u4E00\u518D\u5931\u8BEF\uFF0C\u6700\u7EC8\u56E0\u6B64\u8EAB\u6B7B\u56FD\u706D\uFF0C\u4E5F\u53EF\u7B97\u505A\u662F\u548E\u7531\u81EA\u53D6\u3002</p><p>\u5BF9\u6B64\uFF0C\u6211\u4EEC\u5E76\u4E0D\u6253\u7B97\u7FFB\u6848\uFF0C\u800C\u662F\u51C6\u5907\u4ECE\u53E6\u4E00\u4E2A\u89D2\u5EA6\u6765\u7406\u89E3\u540E\u71D5\u7684\u8870\u4EA1\u3002</p><p>\u4ECE<code>\u7EC4\u7EC7\u8870\u8D25</code>\u7684\u89D2\u5EA6\uFF1A\u900F\u8FC7\u4E00\u4E2A\u8D70\u4E0B\u5761\u8DEF\u7684\u7EC4\u7EC7\uFF0C\u6765\u770B\u770B\u7EC4\u7EC7\u7684\u5D29\u6E83\u7A76\u7ADF\u662F\u4EC0\u4E48\u6837\u5B50\u7684\u3002</p><p>\u8D70\u4E0B\u5761\u8DEF\u7684\u7EC4\u7EC7\uFF0C\u4E00\u822C\u90FD\u4F1A\u4F34\u968F\u8FD9\u6837\u4E00\u4E9B\u7279\u5F81\uFF1A\u6D3B\u529B\u4E0B\u964D\uFF0C\u4EBA\u6D6E\u4E8E\u4E8B\uFF0C\u8D44\u6E90\u4E0D\u8DB3\uFF0C\u5185\u8017\u9891\u53D1\uFF0C\u7B49\u7B49\u7B49\u7B49\u2014\u2014\u8BF7\u6CE8\u610F\uFF0C\u8FD9\u4E9B\u7279\u5F81\u51E0\u4E4E\u5728\u6240\u6709\u8D70\u4E0B\u5761\u8DEF\u7684\u7EC4\u7EC7\u4E2D\u90FD\u4F1A\u5B58\u5728\uFF0C\u800C\u4E14\u4F1A\u5F62\u6210\u6076\u6027\u5FAA\u73AF\u3002</p><p>\u4E3A\u4EC0\u4E48\uFF1F</p><p>\u9996\u5148\uFF0C\u662F\u7EC4\u7EC7\u6D3B\u529B\u7684\u4E0B\u964D\u4F1A\u5BFC\u81F4\u8D44\u6E90\u83B7\u53D6\u80FD\u529B\u7684\u4E0D\u8DB3\uFF1B\u8FD9\u6837\u4F1A\u628A\u7EC4\u7EC7\u4E2D\u6709\u80FD\u529B\u6709\u60F3\u6CD5\u7684\u4EBA\u6324\u51FA\u53BB\u2014\u2014\u8FD9\u4E9B\u4EBA\u603B\u662F\u80FD\u627E\u5F97\u5230\u66F4\u597D\u7684\u53BB\u5904\uFF1B\u800C\u8FD9\u4E9B\u4EBA\u8D70\u4E86\u4EE5\u540E\uFF0C\u53C8\u52A0\u5267\u4E86\u7EC4\u7EC7\u80FD\u529B\u7684\u8870\u843D\u3002</p><p>\u5982\u6B64\u53CD\u590D\u518D\u4E09\u3002</p><p>\u7136\u540E\u63A5\u4E0B\u6765\u4F1A\u600E\u6837\u5462\uFF1F\u7EC4\u7EC7\u6210\u5458\u5E61\u7136\u9192\u609F\uFF0C\u5F7C\u6B64\u76F8\u6FE1\u4EE5\u6CAB\u5171\u514B\u65F6\u8270\u5417\uFF1F</p><p>\u4E0D\u662F\u6CA1\u6709\u8FD9\u79CD\u53EF\u80FD\uFF0C\u4F46\u5F88\u5C11\u3002\u7EDD\u5927\u591A\u6570\u60C5\u5F62\uFF0C\u90FD\u662F\u8D70\u5411\u53CD\u9762\u7684\uFF0C<strong>\u4E5F\u5C31\u662F\u7EC4\u7EC7\u5185\u90E8\u7684\u6597\u4E89\u4F1A\u8D8A\u6765\u8D8A\u6FC0\u70C8</strong>\u2014\u2014\u65E2\u7136\u4ECE\u5916\u90E8\u5DF2\u7ECF\u96BE\u4EE5\u83B7\u53D6\u8D44\u6E90\uFF0C\u90A3\u4E48\u5C31\u53EA\u80FD\u662F\u5185\u90E8\u7684\u5404\u4E2A\u90E8\u95E8\u95F4\u76F8\u4E92\u4E89\u62A2\u6495\u54AC\u4E86\u3002</p><p>\u5F53\u6BCF\u4E00\u4E2A\u90E8\u95E8\u5982\u662F\uFF0C\u90A3\u4E48\u6BCF\u4E00\u4E2A\u4EBA\u4E5F\u90FD\u4F1A\u5982\u6B64\uFF0C<strong>\u8EAB\u5904\u5176\u4E2D\uFF0C\u4EBA\u4EBA\u90FD\u4F1A\u88AB\u4EBA\u6027\u4E4B\u6076\u6240\u9A71\u4F7F</strong>\u3002\u4ED6\u4EEC\u9F20\u76EE\u5BF8\u5149\uFF0C\u53EA\u770B\u5F97\u5230\u7CFB\u7EDF\u5185\u7684\u7ED3\u6784\uFF1B\u4ED6\u4EEC\u7259\u5C16\u9F7F\u5229\uFF0C\u503E\u5FC3\u4E8E\u8DDF\u8EAB\u8FB9\u7684\u4EBA\u62A2\u8D44\u6E90\uFF1B\u4ED6\u4EEC\u4E13\u6CE8\u4E8E\u548C\u5176\u4ED6\u90E8\u95E8\u6076\u6597\uFF0C\u800C\u4E14\u5728\u7F20\u6597\u4E4B\u95F4\uFF0C\u8D8A\u6765\u8D8A\u5F80\u4E0B\u63A2\u89E6\u9053\u5FB7\u7684\u5E95\u7EBF\u3002</p><p>\u4ECE\u8FD9\u4E2A\u89D2\u5EA6\u770B\u8FC7\u53BB\uFF0C\u4F60\u5C31\u80FD\u5F88\u5BB9\u6613\u7406\u89E3\u540E\u71D5\u672B\u5E74\u6155\u5BB9\u4EEC\u4E4B\u95F4\u7684\u4E92\u76F8\u6B8B\u6740\u4E86\u3002</p><p>\u4E3A\u4EC0\u4E48\u5728\u7956\u8F88\u521B\u4E1A\u4E4B\u65F6\uFF0C\u56E2\u7ED3\u662F\u4E3B\u6D41\uFF1B\u4F46\u5230\u4E86\u672B\u5E74\uFF0C\u5728\u66F4\u9700\u8981\u6155\u5BB9\u4EEC\u5408\u4F5C\u7684\u65F6\u5019\uFF0C\u5374\u5904\u5904\u90FD\u5145\u65A5\u7740\u81EA\u76F8\u6B8B\u6740\uFF1F\u5C31\u56E0\u4E3A\u8FD9\u662F\u4E00\u4E2A\u7EC4\u7EC7\u5D29\u574F\u4EE5\u540E\u7684\u5FC5\u7136\uFF1A<strong>\u65E2\u7136\u4ECE\u5916\u754C\u5DF2\u7ECF\u96BE\u4EE5\u83B7\u5F97\u8D44\u6E90\uFF0C\u90A3\u4E48\u8DDF\u5468\u56F4\u7684\u4EBA\u62A2\uFF0C\u5C31\u662F\u96BE\u5EA6\u66F4\u5C0F\u7684\u65B9\u6CD5\u3002</strong></p><p>\u800C\u5728\u8FD9\u79CD\u5185\u90E8\u7684\u62A2\u593A\u4E2D\uFF0C<strong>\u7A81\u7834\u4E0B\u9650\u662F\u4E00\u4EF6\u5F88\u5BB9\u6613\u7684\u4E8B</strong>\u3002\u6700\u7EC8\u5C55\u73B0\u51FA\u6765\u7684\uFF0C\u5C31\u662F\u6CEF\u706D\u4EBA\u6027\u7684\u6B8B\u6740\uFF0C\u4EE5\u53CA\u7EC4\u7EC7\u529B\u91CF\u4EE5\u66F4\u5FEB\u7684\u901F\u5EA6\u98CE\u6D88\u4E91\u6563\u3002</p><p>\u5982\u4ECA\u6155\u5BB9\u5B9D\u9762\u5BF9\u7684\u5C31\u662F\u8FD9\u79CD\u5C40\u9762\u3002\u4EE5\u6155\u5BB9\u5782\u4E4B\u6B7B\u4E3A\u754C\uFF0C\u540E\u71D5\u51E0\u4E4E\u5728\u4E00\u591C\u4E4B\u95F4\u4ECE\u84B8\u84B8\u65E5\u4E0A\u53D8\u6210\u4E86\u65E5\u8584\u897F\u5C71\u3002\u8FD9\u80CC\u540E\uFF0C\u6155\u5BB9\u5B9D\u81EA\u5DF1\u7684\u80FD\u529B\u4E0D\u8DB3\u5F53\u7136\u662F\u539F\u56E0\u4E4B\u4E00\uFF0C\u4F46\u66F4\u91CD\u8981\u7684\u56E0\u7D20\uFF0C\u5374\u662F\u540E\u71D5\u6574\u4E2A\u56FD\u5BB6\u7684\u8870\u8D25\uFF1A</p><p>\u81EA\u4ECE\u88AB\u62D3\u8DCB\u73EA\u51FB\u8D25\u4EE5\u540E\uFF0C\u540E\u71D5\u4FBF\u4E27\u5931\u4E86\u6D3B\u529B\uFF0C\u6574\u4E2A\u7EC4\u7EC7\u7684\u751F\u5B58\u903B\u8F91\u88AB\u5B8C\u5168\u9006\u8F6C\uFF0C\u800C\u6155\u5BB9\u5B9D\u53C8\u6CA1\u6709\u80FD\u529B\u6539\u53D8\u8FD9\u4E00\u60C5\u5F62\u3002</p><p>\u5982\u6B64\u4EE5\u540E\uFF0C\u624D\u662F\u4E00\u5E55\u5E55\u7684\u602A\u8BDE\u63A5\u8FDE\u4E0A\u6F14\u3002\u6211\u4EEC\u56E0\u6B64\u624D\u4F1A\u770B\u5230\u6155\u5BB9\u6C0F\u7684\u5386\u53F2\u7AE0\u8282\uFF0C\u4ECE\u5D1B\u8D77\u4E4B\u65F6\u7684\u5144\u5F1F\u9F50\u5FC3\u4EBA\u624D\u8F88\u51FA\uFF0C\u5FEB\u901F\u6ED1\u5165\u8870\u843D\u4E4B\u65F6\u7684\u9AA8\u8089\u76F8\u6B8B\u6740\u622E\u4E0D\u65AD\u3002\u8FD9\u4E2A\u5DF2\u7ECF\u65E0\u6CD5\u4ECE\u5916\u754C\u83B7\u53D6\u8D44\u6E90\u7684\u7EC4\u7EC7\uFF0C\u5185\u90E8\u77DB\u76FE\u7A81\u7136\u95F4\u88AB\u6FC0\u5316\u3002\u4ECE\u6B64\uFF0C\u88AB\u62D6\u5165\u5185\u4E71\u4E2D\u4E0D\u65AD\u6D88\u8017\uFF0C\u76F4\u5230\u706D\u4EA1\u3002</p><p><strong>\u6076\u6027\u5FAA\u73AF\u4E00\u65E6\u5F62\u6210\uFF0C\u4FBF\u4F1A\u5982\u540C\u6EDA\u4E0B\u5C71\u9876\u7684\u96EA\u7403</strong>\uFF0C\u8D8A\u6765\u8D8A\u5927\uFF0C\u8D8A\u6765\u8D8A\u5FEB\uFF0C\u76F4\u5230\u89E6\u5E95\u4EE5\u540E\uFF0C\u5728\u5DE8\u5927\u7684\u649E\u51FB\u4E2D\uFF0C\u70DF\u6D88\u4E91\u6563\u3002</p><p>\u90A3\u4E48\uFF0C\u8FD9\u6837\u7684\u7EC4\u7EC7\u662F\u4E0D\u662F\u4ECE\u6B64\u5C31\u65E0\u836F\u53EF\u6551\u4E86\uFF1F</p><p>\u5982\u679C\u80FD\u540C\u65F6\u6EE1\u8DB3\u4E24\u4E2A\u6761\u4EF6\uFF0C\u90A3\u4E48\u53EF\u80FD\u8FD8\u662F\u56DE\u5929\u6709\u529B\u7684\uFF1A</p><ul><li><p><strong>\u9996\u5148\uFF0C\u5916\u90E8\u7684\u538B\u529B\u6682\u65F6\u6D88\u5931\uFF0C\u80FD\u591F\u91CD\u65B0\u4ECE\u5916\u90E8\u83B7\u53D6\u8D44\u6E90\u3002</strong></p></li><li><p><strong>\u7136\u540E\uFF0C\u5728\u6B64\u57FA\u7840\u4E0A\uFF0C\u6709\u4E00\u4E2A\u5F3A\u6709\u529B\u7684\u529B\u91CF\u51FA\u73B0\uFF0C\u80FD\u591F\u9547\u538B\u5185\u90E8\u7684\u6597\u4E89\u3002</strong></p></li></ul><p>\u5178\u578B\u4F8B\u5B50\uFF0C\u4FBF\u662F<code>\u5218\u88D5</code>\u5D1B\u8D77\u7684\u8FC7\u7A0B\u3002</p>',72),n=[r];function s(d,g){return o(),e("div",null,n)}const i=p(c,[["render",s],["__file","39.html.vue"]]);export{i as default};
