import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.f4897be6.js";const c={},o=a('<h1 id="\u7B2C4\u96C6-\u9648\u6C5F\u6CB3\u7389\u73E0\u804A\u751F\u610F\u7ECF" tabindex="-1"><a class="header-anchor" href="#\u7B2C4\u96C6-\u9648\u6C5F\u6CB3\u7389\u73E0\u804A\u751F\u610F\u7ECF" aria-hidden="true">#</a> \u7B2C4\u96C6\uFF1A\u9648\u6C5F\u6CB3\u7389\u73E0\u804A\u751F\u610F\u7ECF</h1><p>\u62D6\u628A\u5356\u51FA\u53BB\u4E86\uFF0C\u9648\u5BB6\u6751\u7684\u6751\u6C11\u4EEC\u9AD8\u9AD8\u5174\u5174\u7684\u7B49\u7740\u9648\u6C5F\u6CB3\u5206\u94B1\u3002\u9A86\u7389\u73E0\u5077\u5077\u8EB2\u5728\u4E00\u65C1\u770B\uFF0C\u5FC3\u91CC\u5341\u5206\u9AD8\u5174\u3002\u8F6C\u5934\u5374\u770B\u5230\u4E86\u8D70\u8FDB\u6765\u7684\u9648\u91D1\u6C34\u3002\u8138\u8272\u53D8\u5F97\u5341\u5206\u96BE\u770B\u3002</p><p>\u9648\u91D1\u6C34\u8FD9\u624D\u53D1\u73B0\uFF0C\u539F\u6765\u4E49\u4E4C\u5E02\u573A\u4E0A\u5356\u7684\u706B\u7206\u7684\u62D6\u628A\uFF0C\u771F\u662F\u8FD9\u7FA4\u81EA\u5DF1\u770B\u7740\u957F\u5927\u7684\u5B69\u5B50\u4EEC\u7684\u6770\u4F5C\u3002\u5927\u5BB6\u770B\u7740\u91D1\u6C34\u53D4\u8D70\u8FD1\uFF0C\u90FD\u4E0D\u6562\u51FA\u58F0\u3002\u6CA1\u60F3\u5230\uFF0C\u9648\u91D1\u6C34\u4EC0\u4E48\u8BDD\u4E5F\u6CA1\u8BF4\uFF0C\u8F6C\u8EAB\u79BB\u5F00\u3002</p><p>\u90B1\u82F1\u6770\u5C06\u9648\u6C5F\u6CB3\u63A5\u5230\u4E86\u81EA\u5DF1\u5728\u53BF\u59D4\u529E\u516C\u5BA4\u7684\u4E34\u65F6\u5BBF\u820D\u3002\u5144\u5F1F\u76F8\u89C1\uFF0C\u603B\u6709\u8BF4\u4E0D\u5B8C\u7684\u8BDD\u3002\u90B1\u82F1\u6770\u672C\u51C6\u5907\u7559\u9648\u6C5F\u6CB3\u4F4F\u4E0B\uFF0C\u5F7B\u591C\u957F\u8C08\uFF0C\u4F46\u9648\u6C5F\u6CB3\u5374\u541E\u541E\u5410\u5410\u7684\u544A\u8BC9\u4ED6\uFF0C\u4ECA\u5929\u665A\u4E0A\u7EA6\u4E86\u4EBA\u3002</p><p>\u90B1\u82F1\u6770\u4E86\u7136\u3002\u77E5\u9053\u9648\u6C5F\u6CB3\u7EA6\u5F97\u6B63\u662F\u9A86\u7389\u73E0\u3002</p><p>\u9A86\u7389\u73E0\u7684\u5BB6\u6B63\u662F\u5F53\u5E74\u9648\u6C5F\u6CB3\u79DF\u4E0B\u7684\u5C0F\u9662\u3002\u9A86\u7389\u73E0\u6B63\u5728\u5BB6\u91CC\u5BF9\u7740\u955C\u5B50\u4ED4\u4ED4\u7EC6\u7EC6\u7684\u6536\u62FE\u81EA\u5DF1\u3002\u6253\u5F00\u95E8\uFF0C\u679C\u7136\u89C1\u9648\u6C5F\u6CB3\u5DF2\u7ECF\u7B49\u5728\u95E8\u5916\u3002</p><p>\u7389\u73E0\u505A\u4E86\u4E00\u684C\u5B50\u83DC\uFF0C\u4E24\u4EBA\u804A\u8D77\u4E86\u751F\u610F\u7ECF\uFF0C\u5C31\u50CF\u6253\u5F00\u4E86\u8BDD\u5323\u5B50\uFF0C\u505C\u4E0D\u4E0B\u6765\u3002</p><p>\u9648\u5BB6\u6751\u8FD9\u8FB9\uFF0C\u9648\u91D1\u6C34\u5728\u5BB6\u575A\u6301\u7B49\u9648\u6C5F\u6CB3\u56DE\u6765\u5403\u996D\uFF0C\u8C01\u529D\u4E5F\u4E0D\u542C\u3002\u5374\u7A81\u7136\u53D1\u73B0\u5077\u5077\u6E9C\u8FDB\u5DE7\u59D1\u623F\u95F4\u7684\u9648\u5927\u5149\uFF0C\u539F\u6765\u8FD9\u4E24\u4E2A\u5B69\u5B50\u4ECE\u5C0F\u9752\u6885\u7AF9\u9A6C\uFF0C\u65E9\u5C31\u6697\u751F\u60C5\u612B\u3002</p><p>\u9648\u91D1\u6C34\u53EB\u51FA\u4E86\u9648\u5927\u5149\uFF0C\u77E5\u9053\u9648\u6C5F\u6CB3\u53BB\u4E86\u9A86\u7389\u73E0\u5BB6\uFF0C\u6C14\u4E0D\u6253\u4E00\u51FA\u6765\u3002\u4E00\u5B9A\u8981\u9648\u5927\u5149\u53BB\u7389\u73E0\u5BB6\u628A\u9648\u6C5F\u6CB3\u53EB\u56DE\u6765\u3002\u8FD8\u8BF4\u81EA\u5DF1\u751F\u75C5\u4E86\u3002</p><p>\u9648\u6C5F\u6CB3\u548C\u9A86\u7389\u73E0\u804A\u8D77\u4E86\u5F53\u5E74\u7684\u5F80\u4E8B\uFF0C\u60F3\u8D77\u5206\u79BB\u7684\u4E09\u5E74\uFF0C\u601D\u5FF5\u7684\u4E09\u5E74\uFF0C\u4E0D\u7981\u60B2\u4ECE\u4E2D\u6765\u3002\u6C5F\u6CB3\u9F13\u8D77\u52C7\u6C14\u60F3\u8981\u63E1\u4F4F\u7389\u73E0\u7684\u624B\u7ED9\u5979\u5B89\u6170\u3002\u5374\u504F\u504F\u88AB\u9648\u5927\u5149\u7684\u6572\u95E8\u58F0\u6253\u65AD\u3002\u6C5F\u6CB3\u542C\u8BF4\u91D1\u6C34\u53D4\u75C5\u4E86\uFF0C\u5FD9\u4E22\u4E0B\u7389\u73E0\uFF0C\u8D76\u4E86\u56DE\u53BB\u3002</p><p>\u6C5F\u6CB3\u56DE\u53BB\uFF0C\u770B\u5230\u91D1\u6C34\u53D4\u8EBA\u5728\u5E8A\u4E0A\uFF0C\u624D\u77E5\u9053\u5F53\u65F6\u4ED6\u9003\u8D70\u4EE5\u540E\uFF0C\u9648\u91D1\u6C34\u56E0\u4E3A\u4E1C\u9633\u653E\u706B\u7684\u4E8B\u60C5\u88AB\u5E26\u5230\u4E86\u5B66\u4E60\u73ED\u3002\u7531\u4E8E\u6761\u4EF6\u8270\u82E6\uFF0C\u53C8\u65E0\u4EBA\u7167\u6599\u3002\u624D\u843D\u4E0B\u4E86\u75C5\u6839\u3002\u6C5F\u6CB3\u4E00\u542C\uFF0C\u5341\u5206\u5185\u759A\uFF0C\u53D1\u8A93\u8981\u5B5D\u656C\u91D1\u6C34\u3002\u8C01\u77E5\u9053\u9648\u91D1\u6C34\u8BDD\u950B\u4E00\u8F6C\uFF0C\u7ADF\u662F\u8981\u5C06\u81EA\u5DF1\u7684\u5973\u513F\u5DE7\u59D1\u5AC1\u7ED9\u9648\u6C5F\u6CB3\u3002\u9648\u6C5F\u6CB3\u60CA\u8BB6\u7684\u8BF4\u4E0D\u51FA\u8BDD\u6765\uFF0C\u5DE7\u59D1\u542C\u8BF4\uFF0C\u8138\u8272\u5341\u5206\u96BE\u770B\uFF0C\u539F\u6765\u5DE7\u59D1\u559C\u6B22\u7684\u4EBA\u662F\u9648\u5927\u5149\uFF0C\u540C\u6837\u5E76\u4E0D\u613F\u610F\u5AC1\u7ED9\u81EA\u5DF1\u5F53\u54E5\u54E5\u770B\u7684\u9648\u6C5F\u6CB3\u3002\u53EF\u662F\u9648\u91D1\u6C34\u5374\u5E76\u4E0D\u7406\u89E3\u5C0F\u8F88\u513F\u4EEC\u7684\u5FC3\u601D\u3002</p><p>\u7B2C\u4E8C\u5929\u4E00\u5927\u65E9\uFF0C\u9A86\u7389\u73E0\u7B49\u5728\u6865\u5934\uFF0C\u77E5\u9053\u6C5F\u6CB3\u8981\u53BB\u53BF\u59D4\u7684\u62DB\u5F85\u6240\uFF0C\u6084\u6084\u585E\u7ED9\u6C5F\u6CB3\u4E00\u53CC\u978B\uFF0C\u8F6C\u5934\u5C31\u8DD1\u3002\u6C5F\u6CB3\u770B\u7740\u90A3\u53CC\u978B\uFF0C\u53C8\u60F3\u8D77\u6628\u5929\u665A\u4E0A\u7684\u4E8B\u60C5\u3002\u5FC3\u91CC\u4E0D\u77E5\u662F\u4EC0\u4E48\u6ECB\u5473\u513F\u3002</p><p>\u9A86\u7389\u73E0\u4E0A\u8D27\u7684\u8DEF\u4E0A\uFF0C\u5403\u4E86\u4E00\u53E3\u5C0F\u59D0\u59B9\u4E70\u6765\u7684\u997C\uFF0C\u89C9\u5F97\u5341\u5206\u597D\u5403\uFF0C\u60F3\u8981\u7ED9\u9648\u6C5F\u6CB3\u5C1D\u4E00\u5C1D\uFF0C\u5728\u5C0F\u59D0\u59B9\u4EEC\u7684\u8C03\u7B11\u58F0\u4E2D\uFF0C\u987E\u4E0D\u4E0A\u81EA\u5DF1\u672C\u6765\u8981\u53BB\u505A\u751F\u610F\uFF0C\u8F6C\u8EAB\u4E0B\u8F66\u53EA\u4E3A\u4E70\u997C\u7ED9\u9648\u6C5F\u6CB3\u9001\u53BB\u3002</p><p>\u9A86\u7389\u73E0\u7279\u610F\u9001\u6765\u4E86\u997C\uFF0C\u6CA1\u60F3\u5230\u9648\u6C5F\u6CB3\u6CA1\u6709\u6536\uFF0C\u8FD8\u9000\u56DE\u4E86\u5979\u521A\u9001\u7684\u978B\uFF0C\u4E89\u6267\u8FC7\u7A0B\u4E2D\uFF0C\u997C\u6389\u5230\u4E86\u5730\u4E0A\u3002\u4E24\u4EBA\u6123\u4F4F\u4E86\u3002\u5C31\u5728\u8FD9\u65F6\uFF0C\u5DE7\u59D1\u7ED9\u9648\u6C5F\u6CB3\u9001\u6765\u4E86\u5403\u7684\u3002\u7389\u73E0\u8F6C\u5934\u5C31\u8D70\u3002\u5176\u5B9E\uFF0C\u5DE7\u59D1\u4E5F\u662F\u88AB\u7236\u4EB2\u903C\u7740\u6765\u7684\u3002</p><p>\u9648\u6C5F\u6CB3\u548C\u90B1\u82F1\u6770\u8C08\u8D77\u8FD9\u4EF6\u4E8B\uFF0C\u90B1\u82F1\u6770\u529D\u544A\u4ED6\uFF0C\u4E0D\u80FD\u628A\u62A5\u6069\u548C\u5A5A\u59FB\u81EA\u7531\u6DF7\u4E3A\u4E00\u8C08\u3002\u9648\u6C5F\u6CB3\u542C\u540E\uFF0C\u82E5\u6709\u6240\u601D\u3002</p><p>\u6E05\u65E9\uFF0C\u9A86\u7389\u73E0\u6B63\u5728\u5FD9\u7740\u81EA\u5DF1\u7684\u751F\u610F\uFF0C\u6CA1\u60F3\u5230\u9648\u91D1\u6C34\u7ADF\u4E3B\u52A8\u627E\u5230\u5979\uFF0C\u5BF9\u4EE5\u524D\u7684\u8FC7\u8282\u5411\u7389\u73E0\u9053\u6B49\u3002\u9A86\u7389\u73E0\u6B63\u8BE7\u5F02\u9648\u91D1\u6C34\u7684\u8F6C\u53D8\u3002\u6CA1\u60F3\u5230\u9648\u91D1\u6C34\u7ADF\u662F\u6765\u529D\u544A\u9A86\u7389\u73E0\u4E0D\u8981\u518D\u7EA0\u7F20\u9648\u6C5F\u6CB3\uFF0C\u8FD8\u8BF4\u6C5F\u6CB3\u548C\u5DE7\u59D1\u4E0B\u4E2A\u6708\u5C31\u8981\u5B9A\u5A5A\u3002\u9A86\u7389\u73E0\u5FC3\u4E2D\u60B2\u75DB\uFF0C\u5374\u5F3A\u6491\u7740\u4E0D\u8BA9\u9648\u91D1\u6C34\u770B\u51FA\u6765\u3002</p><p>\u9648\u6C5F\u6CB3\u51E0\u6B21\u8BD5\u56FE\u53BB\u627E\u7389\u73E0\u8C08\u4E00\u8C08\uFF0C\u4F46\u662F\u7389\u73E0\u4E0D\u7406\u4ED6\u3002\u8FD9\u65F6\u3002\u4E61\u91CC\u7684\u517B\u6B96\u573A\u51FA\u4E86\u95EE\u9898\uFF0C\u6C5F\u6CB3\u4E0D\u5F97\u4E0D\u8DDF\u7740\u90B1\u82F1\u6770\u79BB\u5F00\u3002\u9A86\u7389\u73E0\u5C06\u5168\u90E8\u7684\u7CBE\u529B\u90FD\u653E\u5728\u4E86\u751F\u610F\u4E0A\uFF0C\u4EFF\u4F5B\u8FD9\u6837\uFF0C\u5979\u624D\u80FD\u4E0D\u8BA9\u81EA\u5DF1\u60F3\u8D77\u9648\u6C5F\u6CB3\u3002</p><p>\u539F\u6765\u662F\u4E61\u91CC\u7684\u517B\u6B96\u573A\u6269\u5927\u89C4\u6A21\u540E\uFF0C\u9972\u6599\u7684\u6765\u6E90\u6210\u4E86\u5927\u95EE\u9898\u3002\u9648\u6C5F\u6CB3\u4ECE\u4E61\u91CC\u56DE\u6765\u540E\uFF0C\u53C8\u53BB\u627E\u7389\u73E0\uFF0C\u624D\u53D1\u73B0\u7389\u73E0\u75AF\u4E86\u4E00\u6837\u7684\u8FDB\u8D27\uFF0C\u7ED3\u679C\u4E22\u4E86\u4E24\u5305\u7EBD\u6263\u518D\u62D6\u62C9\u673A\u4E0A\u3002\u7389\u73E0\u548C\u6C5F\u6CB3\u4E00\u8DEF\u627E\u53BB\uFF0C\u624D\u53D1\u73B0\u539F\u6765\u88AB\u62D6\u8FDB\u4E86\u5927\u9EA6\u52A0\u5DE5\u5382\u3002\u9648\u6C5F\u6CB3\u770B\u5230\u52A0\u5DE5\u5382\u90A3\u4E48\u591A\u6CA4\u80A5\u4E86\u7684\u5927\u9EA6\u3002\u7075\u673A\u4E00\u52A8\uFF0C\u4E70\u4E0B\u4E86\u6240\u6709\u7684\u5927\u9EA6\u3002\u987A\u5229\u7684\u89E3\u51B3\u4E86\u732A\u9972\u6599\u7684\u95EE\u9898\u3002</p>',18),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","4.html.vue"]]);export{h as default};
