import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as t,f as r}from"./app.1218f0f6.js";const n={},s=r('<h1 id="\u6C49\u671D\u4E2D\u67A2\u4E4B\u4E71" tabindex="-1"><a class="header-anchor" href="#\u6C49\u671D\u4E2D\u67A2\u4E4B\u4E71" aria-hidden="true">#</a> \u6C49\u671D\u4E2D\u67A2\u4E4B\u4E71</h1><blockquote><p>\u521D\uFF0C\u5E1D\u6570\u5931\u7687\u5B50\uFF0C\u4F55\u7687\u540E\u751F\u5B50\u8FA9\uFF0C\u517B\u4E8E\u9053\u4EBA\u53F2\u5B50\u7707\u5BB6\uFF0C\u53F7\u66F0\u201C\u53F2\u4FAF\u201D\u3002\u738B\u7F8E\u4EBA\u751F\u5B50\u534F\uFF0C\u8463\u592A\u540E\u81EA\u517B\u4E4B\uFF0C\u53F7\u66F0\u201C\u8463\u4FAF\u201D\u3002\u7FA4\u81E3\u8BF7\u7ACB\u592A\u5B50\u3002\u5E1D\u4EE5\u8FA9\u8F7B\u4F7B\u65E0\u5A01\u4EEA\uFF0C\u6B32\u7ACB\u534F\uFF0C\u72B9\u8C6B\u672A\u51B3\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274 \xB7 \u5377\u4E94\u5341\u4E5D \xB7 \u6C49\u7EAA\u4E94\u5341\u4E00\u300B</strong></p><p>\u7075\u5E1D\u6709\u5F88\u591A\u5149\u8363\u52A3\u8FF9\uFF0C\u6BD4\u5982\uFF1A\u4FE1\u4EFB\u5BA6\u5B98\u3001\u9020\u6210\u4E24\u6B21\u515A\u9522\u4E4B\u7978\u3001\u5356\u5B98\u6512\u94B1\u3001\u79BB\u5FC3\u7231\u56FD\u58EB\u5927\u592B\u7B49\u3002\u4ECA\u5929\uFF0C\u6211\u4EEC\u5C31\u6765\u8BF4\u8BF4\u7075\u5E1D\u751F\u5E73\u5E72\u5F97\u6700\u540E\u4E00\u4EF6\u574F\u4E8B\u2014\u2014\u4ED6\u5728\u4E34\u6B7B\u524D\u4E00\u756A\u6298\u817E\uFF0C\u6700\u540E\u5BB3\u6B7B\u4E86\u81EA\u5DF1\u7684\u8001\u5988\u3001\u8001\u5A46\u548C\u513F\u5B50\u3002</p><p><strong>\u4E8B\u60C5\u7684\u8D77\u56E0\u662F\u9009\u7ACB\u592A\u5B50\u3002</strong></p><p>\u7075\u5E1D\u4E34\u6B7B\u524D\uFF0C\u6D3B\u7740\u7684\u513F\u5B50\u53EA\u6709\u4E24\u4E2A\uFF1A\u5927\u513F\u5B50\u5218\u8FA9\uFF0C\u662F\u4F55\u7687\u540E\u751F\u7684\uFF0C\u751F\u4E86\u4EE5\u540E\u517B\u5728\u6C11\u95F4\uFF1B\u4E8C\u513F\u5B50\u5218\u534F\uFF0C\u662F\u738B\u7F8E\u4EBA\u751F\u7684\uFF0C\u7531\u7075\u5E1D\u7684\u6BCD\u4EB2\u8463\u592A\u540E\u4EB2\u81EA\u629A\u517B\u3002</p><p>\u7FA4\u81E3\u5F53\u65F6\u53EF\u80FD\u5DF2\u7ECF\u770B\u51FA\u4E86\u7075\u5E1D\u547D\u4E0D\u4E45\u77E3\uFF0C\u7EB7\u7EB7\u4E0A\u4E66\u529D\u7075\u5E1D\u7ACB\u592A\u5B50\u3002\u8BF4\u5F97\u66F4\u51C6\u786E\u4E00\u70B9\uFF0C\u5927\u5BB6\u662F\u7EB7\u7EB7\u4E0A\u4E66\uFF0C\u8FDB\u8A00\u7075\u5E1D\u7ACB\u5218\u8FA9\u4E3A\u592A\u5B50\u3002\u56E0\u4E3A\u5218\u8FA9\u662F\u5AE1\u957F\u5B50\uFF0C\u5929\u7136\u5C31\u5E94\u8BE5\u505A\u592A\u5B50\u3002</p><p>\u4F46\u7075\u5E1D\u81EA\u5DF1\u6709\u60F3\u6CD5\uFF0C\u4ED6\u89C9\u5F97\u5218\u8FA9\u201C\u8F7B\u4F7B\u65E0\u5A01\u4EEA\u201D\u3002</p><p>\u8FD9\u4E2A\u4E8B\u5C31\u76F8\u5F53\u7684\u5947\u602A\u4E86\uFF0C\u7075\u5E1D\u81EA\u5DF1\u5C31\u662F\u4E2A\u8F7B\u4F7B\u65E0\u72B6\u7684\u4E3B\uFF0C\u771F\u4E0D\u77E5\u4ED6\u662F\u600E\u4E48\u770B\u51FA\u6765\u5218\u8FA9\u6709\u8FD9\u4E2A\u7F3A\u70B9\u7684\u3002\u8FD9\u5C31\u597D\u6BD4\u8D3E\u8D66\u9A82\u8D3E\u740F\u8D2A\u6DEB\u660F\u66B4\u4E00\u6837\uFF0C\u771F\u662F\u8BA9\u6211\u7B49\u65C1\u4EBA\u5567\u5567\u5567......\u4F46\u7075\u5E1D\u8FD8\u633A\u56FA\u6267\uFF0C\u7FA4\u81E3\u5982\u6B64\u4E0A\u4E66\uFF0C\u4ED6\u5374\u59CB\u7EC8\u78E8\u78E8\u5527\u5527\u7684\uFF0C\u5C31\u662F\u62D6\u7740\u4E0D\u529E\u3002</p><p>\u8981\u662F\u4ED6\u6625\u79CB\u6B63\u76DB\uFF0C\u62D6\u4E00\u62D6\u4E5F\u5C31\u7B97\u4E86\u3002\u53EF\u95EE\u9898\u662F\uFF0C\u5F53\u65F6\u4ED6\u7684\u75C5\u5DF2\u7ECF\u76F8\u5F53\u91CD\u4E86\uFF0C\u4ED6\u81EA\u5DF1\u4E5F\u81EA\u77E5\u5C06\u4E0D\u4E45\u4E8E\u4EBA\u4E16\u4E86\u3002</p><p>\u4E8E\u662F\u6700\u540E\uFF0C\u4ED6\u628A\u8FD9\u4E2A\u4E8B\u6258\u4ED8\u7ED9\u4E86\u8E47\u7855\u3002</p><p>\u8E47\u7855\u662F\u4E2A\u4EC0\u4E48\u4EBA\uFF1F\u4ED6\u662F\u4E2A\u592A\u76D1\u3002\u4F46\u662F\uFF0C\u662F\u4E00\u4E2A\u6709\u5175\u6743\u7684\u592A\u76D1\u3002</p><p>\u8E47\u7855\u4E3A\u4EC0\u4E48\u80FD\u6709\u5175\u6743\uFF0C\u8FD9\u4E2A\u5C31\u8BF4\u6765\u8BDD\u957F\u4E86\uFF0C\u6211\u4EEC\u5728\u8FD9\u91CC\u7A0D\u5FAE\u89E3\u91CA\u4E00\u4E0B\uFF1A</p><p>\u9EC4\u5DFE\u4E4B\u4E71\u521D\u8D77\u65F6\uFF0C<strong>\u7687\u540E\u7684\u54E5\u54E5\u4F55\u8FDB</strong>\u88AB\u4EFB\u547D\u4E3A\u5927\u5C06\u519B\uFF0C\u8D1F\u8D23\u5F81\u8BA8\u5F20\u89D2\u3002</p><p>\u5F20\u89D2\u540E\u6765\u5F88\u5FEB\u5C31\u8D25\u4EA1\uFF0C\u7075\u5E1D\u4E8E\u662F\u5C31\u60F3\u6536\u56DE\u4F55\u8FDB\u7684\u5175\u6743\u3002\u53EF\u5916\u621A\u4EFB\u5927\u5C06\u519B\u4E43\u662F\u4E1C\u6C49\u4E00\u671D\u7684\u6807\u914D\uFF0C\u800C\u4E14\u4F55\u8FDB\u672C\u8EAB\u4E5F\u6CA1\u6709\u4EC0\u4E48\u8FC7\u9519\uFF0C\u4E0D\u597D\u8F7B\u6613\u514D\u804C\u3002</p><p>\u600E\u4E48\u529E\uFF1F\u7075\u5E1D\u5145\u5206\u53D1\u6325\u81EA\u5DF1\u7684\u806A\u660E\u624D\u667A\uFF0C\u5728\u6D1B\u9633\u8BBE\u7F6E\u4E86\u897F\u56ED\u516B\u6821\u5C09\u638C\u5175\uFF08\u8FD9\u4E9B\u6821\u5C09\u4E2D\uFF0C\u4E5F\u5305\u62EC\u8881\u7ECD\u548C\u66F9\u64CD\uFF09\uFF0C\u4EFB\u547D\u5C0F\u9EC4\u95E8\u8E47\u7855\u4E3A\u4E0A\u519B\u6821\u5C09\uFF0C\u603B\u7BA1\u5176\u4E8B\u3002\u8FD9\u6837\u4E00\u6765\uFF0C\u5927\u5C06\u519B\u4F55\u8FDB\u53CD\u5012\u662F\u53D7\u8E47\u7855\u6307\u6325\u3002</p><p>\u6B64\u5916\uFF0C\u4E3A\u4E86\u8FDB\u4E00\u6B65\u5206\u4F55\u8FDB\u7684\u6743\u529B\uFF0C\u7075\u5E1D\u8FD8\u4EFB\u547D<strong>\u8205\u8205\u8463\u91CD</strong>\u4E3A\u9AA0\u9A91\u5C06\u519B\u3002</p><p>\u6240\u4EE5\u7075\u5E1D\u5728\u5C06\u6B7B\u4E4B\u65F6\uFF0C\u628A\u5C0F\u513F\u5B50\u5218\u534F\u4EA4\u7ED9\u8E47\u7855\u3002\u8A00\u4E0B\u4E4B\u610F\uFF0C\u5C31\u662F\u8BA9\u4ED6\u5E2E\u52A9\u5218\u534F\u5373\u4F4D\u3002</p><p>\u4F46\u4ED6\u4E00\u6B7B\uFF0C\u7ACB\u8C01\u4E3A\u7687\u5E1D\uFF0C\u5C31\u662F\u5927\u81E3\u4EEC\u7684\u4E8B\u4E86\uFF0C\u8E47\u7855\u538B\u6839\u63D2\u4E0D\u4E0A\u8BDD\u3002\u65E0\u5948\u4E4B\u4E0B\uFF0C\u8E47\u7855\u53EA\u80FD\u5175\u884C\u9669\u62DB\u3002</p><p>\u8E47\u7855\u7684\u8BA1\u5212\uFF0C\u662F\u628A\u4F55\u8FDB\u9A97\u8FDB\u5BAB\u4E2D\u8FDB\u884C\u8BDB\u6740\u3002\u4F46\u65E0\u5948\uFF0C\u6D88\u606F\u6CC4\u9732\uFF0C\u529F\u8D25\u5782\u6210\u3002\u5218\u8FA9\u88AB\u63A8\u4E0A\u4E86\u7687\u4F4D\uFF0C\u4F55\u8FDB\u4F5C\u4E3A\u8205\u8205\u83B7\u5F97\u4E86\u6743\u529B\uFF0C\u8E47\u7855\u968F\u5373\u88AB\u6740\u3002</p><p>\u4E8B\u60C5\u5230\u6B64\u5E76\u672A\u6D88\u505C\uFF0C\u56E0\u4E3A\u4F55\u8FDB\u7684\u80C3\u53E3\u5DF2\u7ECF\u4E0D\u6B62\u4E8E\u6B64\uFF0C\u4ED6\u53C8\u76EF\u4E0A\u4E86\u5176\u4ED6\u7684\u5BA6\u5B98\u3002\u5BA6\u5B98\u96C6\u56E2\u5927\u4E3A\u60CA\u6050\uFF0C\u8D76\u7D27\u8F6C\u800C\u652F\u6301\u8463\u91CD\uFF0C\u4E0E\u4F55\u8FDB\u6297\u8861\u3002</p><p><strong>\u8463\u91CD\u8DDF\u4F55\u8FDB\u4E4B\u95F4\uFF0C\u77DB\u76FE\u4E5F\u4E0D\u5C0F\u3002</strong></p><p><strong>\u9996\u5148</strong>\uFF0C\u5C11\u5E1D\u5373\u4F4D\u4EE5\u540E\uFF0C\u4F55\u8FDB\u5F55\u4E86\u5C1A\u4E66\u4E8B\u3002\u8463\u91CD\u4E00\u6837\u662F\u5916\u621A\uFF0C\u4F46\u5374\u6CA1\u6709\u4ED6\u7684\u4EFD\u3002</p><p><strong>\u5176\u6B21</strong>\uFF0C\u8463\u592A\u7687\u592A\u540E\u548C\u4F55\u592A\u540E\u5728\u5BAB\u91CC\u76F8\u5904\u5F97\u4E5F\u4E0D\u5B89\u5B81\uFF0C\u5F53\u7136\u8FD9\u4E5F\u53F8\u7A7A\u89C1\u60EF\u4E86\uFF0C\u54EA\u6709\u5A46\u5A46\u548C\u5AB3\u5987\u80FD\u641E\u597D\u5173\u7CFB\u7684\u3002</p><p><strong>\u518D\u6B21</strong>\uFF0C\u7687\u4F4D\u4E4B\u4E89\u3002\u5218\u8FA9\u548C\u5218\u534F\u5206\u5C5E\u4E8E\u4E24\u65B9\u9635\u8425\uFF0C\u8463\u6C0F\u81EA\u7136\u662F\u503E\u5411\u4E8E\u5218\u534F\u7684\u3002\u6240\u4EE5\u8463\u91CD\u548C\u4F55\u8FDB\u641E\u4E0D\u597D\u5173\u7CFB\uFF0C\u548C\u5BA6\u5B98\u4EEC\u5374\u5229\u76CA\u4E00\u81F4\uFF0C\u641E\u5728\u4E00\u8D77\u5C31\u518D\u6B63\u5E38\u4E0D\u8FC7\u4E86\u3002</p><p><strong>\u4F46\u8FD9\u6B21\u53C8\u88AB\u4F55\u8FDB\u5360\u4E86\u5148\u624B\u3002</strong></p><p>\u4F55\u8FDB\u5148\u53D1\u5236\u4EBA\uFF0C\u8463\u91CD\u88AB\u903C\u81EA\u6740\u3002\u7136\u540E\u4F55\u8FDB\u4E0A\u4E66\uFF0C\u5C06\u8463\u592A\u7687\u592A\u540E\u8D76\u51FA\u4E86\u5BAB\u4E2D\u3002\u4E0D\u4E45\uFF0C\u8463\u592A\u7687\u592A\u540E\u5373\u53BB\u4E16\uFF0C\u6B7B\u56E0\u4E0D\u660E\u3002</p><p>\u4F55\u8FDB\u4E00\u6B65\u6B65\u8D70\u6765\uFF0C\u6709\u6761\u4E0D\u7D0A\uFF0C\u628A\u5BA6\u5B98\u7684\u5BAB\u5916\u52BF\u529B\u57FA\u672C\u4E0A\u7ED9\u94F2\u9664\u5F97\u4E00\u5E72\u4E8C\u51C0\u3002</p><p>\u4F46\u63A5\u4E0B\u6765\uFF0C\u4E8B\u60C5\u5C31\u6CA1\u529E\u6CD5\u518D\u8FDB\u4E00\u6B65\u4E86\u3002</p><p>\u5BA6\u5B98\u5728\u5BAB\u5916\u7684\u52BF\u529B\u88AB\u94F2\u9664\u4E86\uFF0C\u4F46\u5BAB\u91CC\u5374\u4F9D\u65E7\u662F\u5BA6\u5B98\u7684\u5929\u4E0B\u3002\u800C\u4E14\uFF0C\u4F55\u8FDB\u78B0\u5230\u4E86\u548C\u524D\u4EFB\u7AA6\u6B66\u4E00\u6837\u7684\u95EE\u9898\uFF1A\u4ED6\u60F3\u5C06\u5BA6\u5B98\u6740\u5149\uFF0C\u4F46\u81EA\u5DF1\u7684\u59B9\u59B9\u4F55\u592A\u540E\u4E0D\u540C\u610F\u3002</p><p>\u8FD9\u4E0B\u5C31\u5F88\u68D8\u624B\u4E86\u3002\u4E00\u65B9\u9762\uFF0C\u4F55\u8FDB\u80CC\u4E86\u76F8\u5F53\u7684\u6076\u540D\uFF0C\u62C9\u4E86\u76F8\u5F53\u7684\u4EC7\u6068\uFF1B\u53E6\u4E00\u65B9\u9762\uFF0C\u5728\u94F2\u9664\u5BA6\u5B98\u8FD9\u4EF6\u4E8B\u4E0A\uFF0C\u4ED6\u53C8\u5F88\u4E0D\u5F97\u8981\u9886\u3002**\u5BAB\u91CC\u4ED6\u4E0D\u6562\u53BB\uFF0C\u53EF\u592A\u540E\u548C\u7687\u5E1D\u53C8\u5168\u5728\u5BAB\u91CC\u3002**\u4ED6\u8FD9\u91CC\u53EA\u8981\u4E00\u52A8\u624B\uFF0C\u5BA6\u5B98\u5C31\u53EF\u4EE5\u52AB\u6301\u5E1D\u540E\uFF0C\u7136\u540E\u5360\u636E\u9053\u4E49\u4E0A\u7684\u5236\u9AD8\u70B9\u5BF9\u4ED6\u8FDB\u884C\u7F34\u706D\u2014\u2014\u4E4B\u524D\u7AA6\u6B66\u548C\u9648\u8543\u5C31\u662F\u8FD9\u4E48\u5931\u8D25\u7684\u3002</p><p>\u6240\u4EE5\u6B64\u65F6\u4F55\u8FDB\u9887\u4E3A\u7EA0\u7ED3\uFF0C\u5BA6\u5B98\u4EEC\u53CD\u5012\u662F\u4E0D\u6025\u4E0D\u7F13\u3002\u4ED6\u4EEC\u5F53\u7136\u4E5F\u6E05\u695A\u81EA\u5DF1\u7684\u5148\u8F88\u4EEC\u8BDB\u6740\u7AA6\u6B66\u9648\u8543\u4E4B\u6D41\u7684\u5149\u8363\u4E8B\u8FF9\uFF0C\u800C\u4E14\u8FD9\u5176\u4E2D\u50CF\u8D75\u5FE0\u3001\u5F20\u8BA9\u7B49\u4EBA\uFF0C\u8FD8\u90FD\u662F\u4EB2\u5386\u8FC7\u7684\u3002</p><p>**\u4F55\u8FDB\u4E8E\u662F\u95EE\u8BA1\u4E8E\u8881\u7ECD\u3002**\u518D\u540E\u6765\u7684\u4E8B\u60C5\u5927\u5BB6\u5C31\u90FD\u6BD4\u8F83\u719F\u6089\u4E86\uFF1A\u8881\u7ECD\u5EFA\u8BAE\u53EC\u5916\u5C06\u5165\u4EAC\u3002\u6240\u53EC\u4E4B\u4EBA\uFF0C\u4FBF\u662F\u8463\u5353\u3002\u672A\u7B49\u8463\u5353\u8FDB\u4EAC\uFF0C\u4F55\u8FDB\u4FBF\u5DF2\u88AB\u5BA6\u5B98\u6740\u5BB3\u3002</p><p>\u540E\u6765\u8463\u5353\u79C9\u653F\uFF0C\u5E9F\u5218\u8FA9\u4E3A\u5F18\u519C\u738B\uFF0C\u7ACB\u5218\u534F\u4E3A\u5E1D\uFF0C\u662F\u4E3A\u6C49\u732E\u5E1D\u3002\u518D\u540E\u6765\uFF0C\u5218\u8FA9\u4E0E\u4F55\u592A\u540E\u88AB\u6BD2\u6740\u3002</p><p>\u867D\u8BF4\u5C11\u5E1D\u5218\u8FA9\u4E0E\u732E\u5E1D\u5218\u534F\u7684\u5730\u4F4D\u66F4\u66FF\uFF0C\u662F\u5728\u8463\u5353\u624B\u4E2D\u5B8C\u6210\u7684\u3002\u4F46\u5176\u5B9E\uFF0C<code>\u4E00\u5207\u7684\u6E90\u5934\u8087\u59CB\u4E8E\u7075\u5E1D</code>\u3002\u800C\u4E14\uFF0C\u8463\u5353\u4E5F\u662F\u95F4\u63A5\u56E0\u4E3A\u5E9F\u7ACB\u4E00\u4E8B\uFF0C\u624D\u7275\u6D89\u8FDB\u6765\u7684\u3002</p><blockquote><p>\u8FFD\u6839\u6EAF\u6E90\uFF0C\u7075\u5E1D\u60F3\u5E9F\u957F\u7ACB\u5E7C\uFF0C\u5F00\u542F\u4E86\u4E4B\u540E\u7684\u65E0\u7A77\u7978\u7AEF\uFF1A \u4ED6\u4E8B\u6709\u4E0D\u6210\uFF0C\u6258\u4ED8\u4E8E\u8E47\u7855\uFF0C\u52A0\u5267\u4E86\u5BA6\u5B98\u548C\u5916\u621A\u7684\u77DB\u76FE\uFF1B \u4ED6\u4EFB\u547D\u8463\u91CD\u4E3A\u9AA0\u9A91\u5C06\u519B\uFF0C\u4EE5\u671F\u5206\u4F55\u8FDB\u7684\u6743\uFF0C\u7ED3\u679C\u9020\u6210\u4E86\u8463\u91CD\u548C\u8463\u592A\u540E\u7684\u6B7B\uFF1B \u800C\u6700\u7EC8\uFF0C\u8463\u5353\u53C8\u4EE5\u6B64\u4E3A\u501F\u53E3\uFF0C\u5E9F\u957F\u7ACB\u5E7C\uFF0C\u5BB3\u6B7B\u4E86\u5C11\u5E1D\u5218\u8FA9\u4EE5\u53CA\u4F55\u592A\u540E\u3002</p></blockquote><p>\u5982\u679C\u8BF4\u5751\u5BB3\u5916\u4EBA\u5C31\u7B97\u574F\uFF0C\u90A3\u5BB3\u6B7B\u81EA\u5DF1\u7684\u5BB6\u4EBA\uFF0C\u5E94\u8BE5\u7B97\u662F\u574F\u5F97\u4E27\u5C3D\u5929\u826F\u4E86\u5427\u3002</p><p>\u9664\u4E86\u5BB3\u6B7B\u4E86\u81EA\u5DF1\u7684\u5BB6\u4EBA\uFF0C**\u7075\u5E1D\u7531\u5E9F\u592A\u5B50\u5F15\u53D1\u7684\u671D\u653F\u4E89\u6267\uFF0C\u6700\u7EC8\u53D8\u6210\u4E86\u4E0D\u53EF\u63A7\u5236\u7684\u6D41\u8840\u653F\u53D8\u3002**\u6B64\u540E\uFF0C\u5927\u6C49\u738B\u671D\u5206\u5D29\u79BB\u6790\u3002</p><p>\u7ACB\u50A8\u4ECE\u6765\u90FD\u662F\u738B\u671D\u7684\u5927\u4E8B\u3002\u5982\u4F55\u7ACB\uFF0C\u662F\u6709\u56FA\u5B9A\u6807\u51C6\u7684\uFF0C\u8FD9\u4E2A\u6807\u51C6\u5C31\u662F\u7ACB\u5AE1\u7ACB\u957F\u3002\u4EE5\u8FD9\u4E2A\u6807\u51C6\u800C\u8BBA\uFF0C\u65E0\u8BBA\u5982\u4F55\uFF0C\u90FD\u8BE5\u662F\u5218\u8FA9\u5F53\u9009\u3002\u4F46\u7075\u5E1D\u975E\u8981\u89C9\u5F97\u5218\u534F\u66F4\u5408\u4ED6\u7684\u610F\uFF0C\u8FD9\u5FC5\u7136\u906D\u81F3\u5927\u81E3\u4EEC\u7684\u53CD\u5BF9\u3002</p><p>\u5927\u81E3\u53CD\u5BF9\u7684\u7406\u7531\uFF0C\u4E00\u662F\u56E0\u4E3A\u793C\u6CD5\u5982\u6B64\u3002\u82E5\u8F7B\u6613\u8FDD\u53CD\uFF0C\u8EAB\u8D1F\u536B\u9053\u4E4B\u804C\u7684\u58EB\u5927\u592B\u5FC5\u7136\u8981\u963B\u6B62\u3002\u6240\u4EE5\u6211\u4EEC\u770B\u5230\u4E86\u4F55\u8FDB\u4EE5\u53CA\u80CC\u540E\u7684\u58EB\u5927\u592B\u96C6\u56E2\uFF0C\u5BF9\u5218\u8FA9\u5F3A\u529B\u652F\u6301\u3002\u4E8C\u6765\u66F4\u4E3A\u91CD\u8981\u7684\u662F\uFF0C\u8FD9\u79CD\u52A8\u8BAE\u4E00\u65E6\u63D0\u51FA\uFF0C\u5F88\u5FEB\u5C31\u4F1A\u6709\u4EBA\u5F00\u59CB\u94BB\u8FD9\u4E2A\u7A7A\u5B50\uFF0C\u652F\u6301\u5E9F\u7ACB\uFF0C\u6295\u673A\u94BB\u8425\u3002\u8463\u91CD\u548C\u5BA6\u5B98\u4EEC\u4E5F\u6B63\u662F\u4EE5\u6B64\u4E3A\u501F\u53E3\uFF0C\u63D2\u624B\u671D\u653F\uFF0C\u6700\u7EC8\u917F\u6210\u5927\u7978\u3002</p><p>\u4ECE\u6B64\u4EE5\u540E\uFF0C\u671D\u653F\u7684\u4E2D\u5FC3\u5C31\u53D8\u4E86\uFF0C\u4EC0\u4E48\u5E73\u53DB\u5B89\u6C11\u3001\u8FB9\u7586\u7A33\u56FA\uFF0C\u8FD9\u4E9B\u4E8B\u90FD\u53D8\u5F97\u518D\u4E5F\u4E0D\u91CD\u8981\u4E86\u3002\u671D\u5EF7\u91CC\u552F\u4E00\u91CD\u8981\u7684\u4E8B\u60C5\uFF0C\u5C31\u662F\u8C01\u8981\u6765\u5F53\u592A\u5B50\u3002</p><p>\u800C\u6C49\u7075\u5E1D\uFF0C\u4FBF\u662F\u5236\u9020\u4E86\u8FD9\u6837\u7684\u4E00\u79CD\u7EB7\u4E89\u3002\u800C\u4E14\u7406\u7531\uFF0C\u8FD8\u4EC5\u4EC5\u662F\u56E0\u4E3A\u4E2A\u4EBA\u7684\u559C\u597D\u3002</p><p>**\u5386\u53F2\u4E0A\uFF0C\u6B64\u7C7B\u7EB7\u4E89\uFF0C\u53F2\u4E0D\u7EDD\u4E66\u3002**\u5982\u5B8B\u671D\u7684\u201C\u749E\u8BAE\u201D\u3001\u660E\u671D\u7684\u201C\u5927\u793C\u4EEA\u201D\u4E4B\u4E89\uFF0C\u90FD\u662F\u5C06\u671D\u653F\u5F3A\u884C\u8F6C\u5411\u3002\u66F4\u4E3A\u4E25\u91CD\u7684\u5219\u662F\u738B\u5B89\u77F3\u53D8\u6CD5\uFF0C\u65B0\u65E7\u515A\u4E89\u6700\u7EC8\u5BF9\u5927\u5B8B\u671D\u9020\u6210\u4E86\u6BC1\u706D\u6027\u7684\u6253\u51FB\u3002\u5230\u4E86\u6700\u540E\uFF0C\u8C01\u5BF9\u8C01\u9519\u5176\u5B9E\u5173\u7CFB\u5DF2\u7ECF\u4E0D\u5927\uFF0C\u5173\u952E\u7684\u95EE\u9898\u5728\u4E8E\uFF0C\u5929\u4E0B\u5F62\u6210\u4E86\u6C34\u706B\u4E0D\u5BB9\u7684\u4E24\u6D3E\uFF0C\u7FA4\u81E3\u5404\u6267\u7ACB\u573A\uFF0C\u662F\u975E\u53D8\u5F97\u518D\u4E0D\u91CD\u8981\u3002</p>',43),e=[s];function c(a,g){return o(),t("div",null,e)}const l=p(n,[["render",c],["__file","4.html.vue"]]);export{l as default};
