import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as t,f as a}from"./app.cb44c984.js";const c={},o=a('<h1 id="\u7B2C4\u96C6-\u4E1C\u65B9\u9752\u82CD\u53D8-\u5927\u5F3A" tabindex="-1"><a class="header-anchor" href="#\u7B2C4\u96C6-\u4E1C\u65B9\u9752\u82CD\u53D8-\u5927\u5F3A" aria-hidden="true">#</a> \u7B2C4\u96C6\uFF1A\u4E1C\u65B9\u9752\u82CD\u53D8\u201C\u5927\u5F3A\u201D</h1><p>\u7D2F\u4E86\u4E00\u5929\u7684\u5C0F\u5170\u82B1\u521A\u4E00\u8EBA\u4E0B\uFF0C\u5C31\u8FDB\u5165\u4E86\u68A6\u4E61\u3002\u68A6\u4E2D\uFF0C\u5979\u5728\u7761\u68A6\u4E2D\u88AB\u4EBA\u53EB\u9192\uFF0C\u968F\u540E\u88AB\u7ED1\u7740\u62C9\u5230\u5916\u9762\u3002\u4E00\u8F6C\u5934\u5C31\u770B\u5230\u540C\u6837\u88AB\u7ED1\u7740\u7684\u4E1C\u65B9\u9752\u82CD\uFF0C\u524D\u9762\u8FD8\u7AD9\u7740\u4E00\u4F4D\u4E0A\u4ED9\u3002\u5F85\u90A3\u4E0A\u4ED9\u8F6C\u8FC7\u8EAB\u6765\uFF0C\u5C0F\u5170\u82B1\u8FD9\u624D\u770B\u6E05\u4ED6\u7684\u9762\u8C8C\uFF0C\u90A3\u4EBA\u7ADF\u662F\u957F\u73E9\u3002\u53EA\u89C1\u957F\u73E9\u624B\u8D77\u5200\u843D\uFF0C\u5C06\u4E1C\u65B9\u9752\u82CD\u6740\u4E86\u3002\u5C0F\u5170\u82B1\u5BB3\u6015\u6781\u4E86\uFF0C\u53EF\u5374\u53EA\u80FD\u770B\u7740\u90A3\u950B\u5229\u7684\u5251\u76F4\u76F4\u780D\u4E86\u4E0B\u6765\u3002\u5C0F\u5170\u82B1\u4ECE\u5669\u68A6\u4E2D\u60CA\u9192\uFF0C\u4E0E\u6B64\u540C\u65F6\uFF0C\u4E1C\u65B9\u9752\u82CD\u4E5F\u8DF3\u4E86\u8D77\u6765\u3002</p><p>\u4E1C\u65B9\u9752\u82CD\u6765\u5230\u5C0F\u5170\u82B1\u623F\u95F4\uFF0C\u5374\u770B\u5230\u5979\u7761\u5F97\u6B63\u9163\u3002\u5C0F\u5170\u82B1\u600E\u4E48\u90FD\u4E0D\u80AF\u9192\uFF0C\u4E1C\u65B9\u9752\u82CD\u53EA\u597D\u4E0A\u624B\u53BB\u63EA\u5979\u7684\u88AB\u5B50\u3002\u53EF\u5FFD\u7136\u5934\u4E0A\u7684\u5723\u5370\u53D1\u51FA\u5149\u8292\uFF0C\u4ED6\u7ADF\u4E0D\u53D7\u63A7\u5236\u5730\u6765\u5230\u6797\u95F4\u82B1\u623F\uFF0C\u4E0D\u53D7\u63A7\u5236\u5730\u4E3E\u624B\u6570\u82B1\u3002\u4E1C\u65B9\u9752\u82CD\u540E\u77E5\u540E\u89C9\uFF0C\u4F3C\u4E4E\u5C0F\u5170\u82B1\u80FD\u591F\u63A7\u5236\u4ED6\u7684\u884C\u4E3A\u3002</p><p>\u4E1C\u65B9\u9752\u82CD\u53C8\u6C14\u53C8\u6025\uFF0C\u6025\u5FD9\u5524\u6765\u6B87\u9619\uFF0C\u547D\u4ED6\u5C06\u8BB0\u8F7D\u606F\u5170\u6CD5\u672F\u7684\u4E66\u7C4D\u627E\u6765\u3002\u8FD9\u5377\u4E66\u5B9E\u5728\u592A\u957F\uFF0C\u627E\u4E86\u8BB8\u4E45\uFF0C\u4E1C\u65B9\u9752\u82CD\u8FD9\u624D\u627E\u5230\u5173\u952E\u6240\u5728\u3002\u539F\u6765\u4ED6\u4E2D\u7684\u662F\u540C\u5FC3\u5492\uFF0C\u4E0E\u65BD\u5492\u8005\u540C\u559C\u540C\u60B2\uFF0C\u540C\u6B7B\u540C\u4F24\uFF0C\u8EAB\u5FC3\u76F8\u8FDE\uFF0C\u5982\u540C\u4E00\u4F53\u3002\u53EF\u4E0D\u7BA1\u4E1C\u65B9\u9752\u82CD\u5982\u4F55\uFF0C\u5C0F\u5170\u82B1\u90FD\u4E0D\u4F1A\u53D7\u5F71\u54CD\uFF0C\u53CD\u5012\u662F\u4ED6\u4F1A\u56E0\u7740\u5C0F\u5170\u82B1\u7684\u6240\u53D7\u6240\u611F\u800C\u53D1\u751F\u53CD\u5E94\u3002</p><p>\u5F97\u77E5\u4E1C\u65B9\u9752\u82CD\u7ADF\u4E3A\u4E86\u81EA\u5DF1\u968F\u53E3\u7684\u4E00\u53E5\u8BDD\u5C31\u8DD1\u53BB\u82B1\u623F\u6570\u82B1\uFF0C\u5C0F\u5170\u82B1\u7B11\u5F97\u524D\u4EF0\u540E\u5408\u3002\u4E1C\u65B9\u9752\u82CD\u50AC\u4FC3\u5C0F\u5170\u82B1\u8D76\u7D27\u53BB\u4FEE\u590D\u547D\u7C3F\uFF0C\u53EF\u5BF9\u65B9\u5374\u60F3\u8981\u53BB\u5403\u65E9\u996D\u3002\u4E1C\u65B9\u9752\u82CD\u54EA\u91CC\u80AF\u7B54\u5E94\uFF0C\u53EF\u65E0\u5948\u4ED6\u7684\u809A\u5B50\u5341\u5206\u4E0D\u4E89\u6C14\u7684\u548C\u5C0F\u5170\u82B1\u4E00\u6837\u5495\u5495\u4E71\u53EB\u3002\u65E0\u5948\uFF0C\u4E1C\u65B9\u9752\u82CD\u53EA\u597D\u548C\u5C0F\u5170\u82B1\u4E00\u540C\u8FDB\u98DF\u3002</p><p>\u76F8\u5904\u4E86\u8FD9\u4E48\u4E45\uFF0C\u5C0F\u5170\u82B1\u8FD8\u4E0D\u77E5\u9053\u4E1C\u65B9\u9752\u82CD\u7684\u8EAB\u4EFD\u3002\u53EF\u5C31\u5728\u4E1C\u65B9\u9752\u82CD\u62A5\u51FA\u540D\u8BB3\u7684\u65F6\u5019\uFF0C\u5C0F\u5170\u82B1\u5F88\u4E0D\u51D1\u5DE7\u5730\u6253\u4E86\u4E00\u4E2A\u55B7\u568F\uFF0C\u9519\u4EE5\u4E3A\u4E1C\u65B9\u9752\u82CD\u540D\u53EB\u4E1C\u65B9\u5F3A\u3002\u4E1C\u65B9\u9752\u82CD\u4E0D\u613F\u548C\u5979\u8BA1\u8F83\uFF0C\u53EA\u80FD\u4EFB\u7531\u5979\u5524\u81EA\u5DF1\u5927\u5F3A\u3002\u5C0F\u5170\u82B1\u6709\u4E9B\u4E3A\u5927\u5F3A\u62C5\u5FC3\uFF0C\u56E0\u4E3A\u4ED6\u548C\u5927\u9B54\u5934\u4E1C\u65B9\u9752\u82CD\u4E00\u4E2A\u59D3\u3002\u5728\u5C0F\u5170\u82B1\u773C\u4E2D\uFF0C\u5927\u9B54\u5934\u4E1C\u65B9\u9752\u82CD\u53EF\u662F\u4E00\u5934\u957F\u76F8\u602A\u5F02\u7684\u6076\u5FC3\u602A\u7269\u3002\u542C\u5230\u8FD9\u8BDD\uFF0C\u4E1C\u65B9\u9752\u82CD\u6C14\u4E0D\u6253\u4E00\u5904\u6765\uFF0C\u5FCD\u4E0D\u4F4F\u5413\u552C\u5C0F\u5170\u82B1\u3002</p><p>\u8FD9\u5929\uFF0C\u5C0F\u5170\u82B1\u6B63\u548C\u82B1\u623F\u91CC\u7684\u5C0F\u82B1\u7CBE\u4EEC\u8BF4\u8D77\u4E09\u4E07\u5E74\u524D\u7684\u5927\u6218\uFF0C\u628A\u4E1C\u65B9\u9752\u82CD\u8BF4\u7684\u4E00\u6587\u4E0D\u503C\u3001\u5341\u5206\u72FC\u72C8\u3002\u4E00\u65C1\u7684\u4E1C\u65B9\u9752\u82CD\u5B9E\u5728\u542C\u4E0D\u4E0B\u53BB\uFF0C\u51FA\u9762\u6253\u65AD\u4E86\u5C0F\u5170\u82B1\u7684\u8BDD\u3002\u542C\u95FB\u90A3\u4E9B\u8BDD\u90FD\u662F\u5C0F\u5170\u82B1\u81EA\u5DF1\u7F16\u7684\uFF0C\u4E1C\u65B9\u9752\u82CD\u53C8\u6C14\u53C8\u65E0\u5948\uFF0C\u53EA\u597D\u8BA9\u5979\u6362\u4E00\u672C\u4E66\u6765\u8BFB\u3002</p><p>\u4ECA\u5929\u521A\u597D\u5C31\u662F\u5E86\u795D\u4E24\u65CF\u5927\u6218\u3001\u4ED9\u65CF\u80DC\u5229\u7684\u65E5\u5B50\uFF0C\u6C34\u4E91\u5929\u6BCF\u9022\u6B64\u65E5\u90FD\u4F1A\u5E86\u795D\u5927\u6218\u7684\u80DC\u5229\u3001\u5C01\u5370\u6708\u5C0A\u7684\u8282\u65E5\u3002\u5C0F\u5170\u82B1\u5174\u81F4\u51B2\u51B2\u5730\u51C6\u5907\u8282\u65E5\u7684\u4E1C\u897F\uFF0C\u53EF\u4E00\u65C1\u7684\u4E1C\u65B9\u9752\u82CD\u8138\u4E0A\u9752\u7B4B\u66B4\u8D77\uFF0C\u731B\u5730\u5C06\u7897\u6454\u788E\u3002\u5C0F\u5170\u82B1\u4EE5\u4E3A\u4E1C\u65B9\u9752\u82CD\u505A\u7CD5\u70B9\u592A\u8FC7\u7528\u529B\uFF0C\u53EA\u597D\u8F6C\u8FC7\u8EAB\u53BB\u4EB2\u81EA\u6559\u5BFC\u4ED6\u3002\u671B\u7740\u5C0F\u5170\u82B1\u8BA4\u771F\u53C8\u611A\u8822\u7684\u6A21\u6837\uFF0C\u4E1C\u65B9\u9752\u82CD\u65E0\u5948\u7684\u504F\u8FC7\u5934\u53BB\u3002\u53EF\u8C01\u77E5\u5C0F\u5170\u82B1\u8FD8\u5728\u5371\u9669\u7684\u8FB9\u7F18\u75AF\u72C2\u8BD5\u63A2\uFF0C\u628A\u4E1C\u65B9\u9752\u82CD\u7684\u9762\u56E2\u634F\u5F97\u5947\u5F62\u602A\u72B6\u3002\u4E1C\u65B9\u9752\u82CD\u7EC8\u4E8E\u5FCD\u4E0D\u4F4F\uFF0C\u773C\u4E2D\u95EA\u51FA\u7EA2\u5149\u3002\u4F3C\u4E4E\u662F\u60F3\u5230\u4E86\u606F\u5170\u547D\u5492\uFF0C\u4ED6\u6781\u529B\u538B\u6291\u4F4F\u81EA\u5DF1\u7684\u6012\u706B\u3002</p><p>\u65E5\u590D\u4E00\u65E5\uFF0C\u5C0F\u5170\u82B1\u5728\u4E1C\u65B9\u9752\u82CD\u7684\u76D1\u89C6\u4E0B\u52AA\u529B\u4FEE\u590D\u547D\u7C3F\u3002\u53EF\u5230\u6700\u540E\uFF0C\u4E0D\u4EC5\u6CA1\u6709\u4EFB\u4F55\u6210\u6548\uFF0C\u5979\u81EA\u5DF1\u4E5F\u7D2F\u5F97\u660F\u7761\u8FC7\u53BB\u3002\u53E6\u4E00\u8FB9\uFF0C\u957F\u73E9\u4F3C\u4E4E\u77E5\u6653\u5C0F\u5170\u82B1\u7684\u72B6\u51B5\uFF0C\u6B63\u5728\u6BBF\u4E2D\u4FEE\u70BC\u7075\u4E39\u3002\u671B\u7740\u8EAB\u65C1\u7684\u6C34\u5170\u82B1\uFF0C\u5C0F\u5170\u82B1\u90A3\u6D3B\u6CFC\u53EF\u7231\u7684\u8138\u5E9E\u4E0D\u7531\u5F97\u8DF3\u8FDB\u4E86\u4ED6\u7684\u8111\u4E2D\u3002</p><p>\u67E5\u4E86\u5377\u4E66\uFF0C\u4E1C\u65B9\u9752\u82CD\u8FD9\u624D\u5BFB\u5230\u517B\u62A4\u5C0F\u5170\u82B1\u7684\u65B9\u6CD5\u3002\u53EF\u8FD9\u65B9\u6CD5\u7740\u5B9E\u7E41\u590D\uFF0C\u4E1C\u65B9\u9752\u82CD\u600E\u4E48\u4E5F\u4E0D\u613F\u610F\u505A\u3002\u53EF\u4E3A\u4E86\u547D\u7C3F\uFF0C\u4ED6\u4E5F\u53EA\u80FD\u8D77\u65E9\u8D2A\u9ED1\u4E3A\u5C0F\u5170\u82B1\u51C6\u5907\u4FEE\u590D\u4ED9\u6839\u7684\u7075\u836F\u3002\u957F\u73E9\u5982\u4ECA\u5DF2\u7ECF\u70BC\u5236\u597D\u4E86\u7075\u836F\uFF0C\u51C6\u5907\u9001\u7ED9\u5C0F\u5170\u82B1\u3002\u53EF\u4E91\u4E2D\u541B\u5374\u5FFD\u7136\u5C06\u4ED6\u53EB\u53BB\uFF0C\u4E8B\u6001\u7D27\u6025\uFF0C\u4ED6\u4E5F\u53EA\u80FD\u79BB\u5F00\u3002</p>',10),r=[o];function _(n,s){return p(),t("div",null,r)}const h=e(c,[["render",_],["__file","4.html.vue"]]);export{h as default};
