import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.8a7d9ef5.js";const c={},o=a('<h1 id="\u7B2C4\u96C6-\u4FAF\u8D35\u5E73\u652F\u6559\u5F80\u4E8B\u88AB\u63ED\u9732-\u4E25\u826F\u6C5F\u9633-\u8054\u624B-\u62E8\u5F00\u8FF7\u96FE" tabindex="-1"><a class="header-anchor" href="#\u7B2C4\u96C6-\u4FAF\u8D35\u5E73\u652F\u6559\u5F80\u4E8B\u88AB\u63ED\u9732-\u4E25\u826F\u6C5F\u9633-\u8054\u624B-\u62E8\u5F00\u8FF7\u96FE" aria-hidden="true">#</a> \u7B2C4\u96C6\uFF1A\u4FAF\u8D35\u5E73\u652F\u6559\u5F80\u4E8B\u88AB\u63ED\u9732 \u4E25\u826F\u6C5F\u9633\u201C\u8054\u624B\u201D\u62E8\u5F00\u8FF7\u96FE</h1><p>\u7ECF\u8FC7\u68C0\u5BDF\u9662\u9886\u5BFC\u5408\u8BAE\uFF0C\u4E01\u68C0\u8BA4\u4E3A\u6CA1\u6709\u8DB3\u591F\u8BC1\u636E\u4E0D\u8DB3\u4EE5\u63A8\u7FFB\u539F\u6848\uFF0C\u56E0\u6B64\u901A\u77E5\u6C5F\u9633\u505C\u6B62\u8C03\u67E5\u3002\u7136\u800C\u6C5F\u9633\u65E9\u5DF2\u6253\u5B9A\u4E3B\u610F\u66FF\u4FAF\u8D35\u5E73\u4F38\u51A4\uFF0C\u5373\u4FBF\u65E0\u6CD5\u660E\u5BDF\u4E5F\u8981\u6697\u8BBF\uFF0C\u52BF\u5FC5\u5C06\u6B63\u4E49\u7EF4\u62A4\u5230\u5E95\uFF0C\u4E0D\u8BA9\u771F\u76F8\u57CB\u6CA1\u3002</p><p>\u4E25\u826F\u548C\u4EFB\u73A5\u5A77\u53BB\u627E\u674E\u9759\u4E86\u89E3\u60C5\u51B5\uFF0C\u4ECE\u5979\u53E3\u4E2D\u83B7\u6089\u4FAF\u8D35\u5E73\u5728\u7814\u4E00\u65F6\u671F\u62A5\u540D\u516C\u76CA\u9879\u76EE\uFF0C\u4E3B\u52A8\u524D\u5F80\u82D7\u9AD8\u4E61\u53C2\u4E0E\u652F\u6559\uFF0C\u90A3\u4E00\u5E74\u6B63\u662F\u5343\u79A7\u5E74\uFF0C\u5374\u5F7B\u5E95\u6210\u4E3A\u4ED6\u7684\u4E27\u793C\u5E74\u3002</p><p>\u4FAF\u8D35\u5E73\u521D\u5230\u82D7\u9AD8\u4E61\uFF0C\u56E0\u6027\u683C\u6E29\u548C\uFF0C\u683C\u5916\u53D7\u5176\u4ED6\u8001\u5E08\u6B22\u8FCE\uFF0C\u7EB7\u7EB7\u7F6E\u529E\u9152\u83DC\uFF0C\u63A5\u98CE\u6D17\u5C18\u3002\u56E0\u4E3A\u82D7\u9AD8\u4E61\u5C5E\u4E8E\u504F\u8FDC\u8D2B\u56F0\u5730\u533A\uFF0C\u57FA\u672C\u6CA1\u6709\u8001\u5E08\u613F\u5728\u6B64\u5904\u4E45\u5C45\uFF0C\u5C31\u8FDE\u8FD9\u6240\u4E2D\u5B66\u4E5F\u662F\u5361\u6069\u96C6\u56E2\u6350\u52A9\u5EFA\u6210\uFF0C\u5728\u8BFB\u5B66\u751F\u5E74\u9F84\u6B8B\u5DEE\u4E0D\u9F50\uFF0C\u5B66\u6821\u53EA\u6709\u4E24\u540D\u8001\u5E08\u5E73\u644A\u6388\u8BFE\uFF0C\u597D\u5728\u4FAF\u8D35\u5E73\u7684\u52A0\u5165\uFF0C\u53EF\u4EE5\u589E\u5F3A\u6587\u79D1\u7C7B\u77E5\u8BC6\u3002</p><p>\u5361\u6069\u96C6\u56E2\u8D44\u52A9\u5B66\u6821\u4FEE\u7F2E\u65B0\u6559\u5BA4\uFF0C\u8D60\u9001\u8BFE\u5916\u8BFB\u7269\u548C\u5B66\u4E60\u7528\u54C1\uFF0C\u533A\u6559\u80B2\u5C40\u79E6\u5C40\u957F\u51FA\u9762\u81F4\u8C22\uFF0C\u4EB2\u81EA\u4E3A\u4ED6\u6388\u4E88\u8363\u8A89\u6821\u957F\u79F0\u53F7\u3002\u5B66\u6821\u5168\u4F53\u5E08\u751F\u805A\u96C6\u5E7F\u573A\uFF0C\u4FAF\u8D35\u5E73\u521D\u6B21\u89C1\u5230\u96C6\u56E2\u8463\u4E8B\u957F\u5B59\u4F20\u798F\uFF0C\u53EA\u89C9\u4ED6\u76F8\u8C8C\u548C\u853C\uFF0C\u5E94\u662F\u62C5\u5F97\u8D77\u201C\u6148\u5584\u5BB6\u201D\u7684\u5934\u8854\u3002</p><p>\u6C5F\u9633\u7ECF\u8FC7\u591A\u65B9\u67E5\u8BE2\uFF0C\u7EC8\u5728\u82D7\u9AD8\u4E61\u6D3E\u51FA\u6240\u8C03\u53D6\u5230\u4E00\u4EFD\u6765\u81EA2000\u5E7411\u67082\u53F7\u7684\u51FA\u8B66\u8BB0\u5F55\uFF0C\u62A5\u8B66\u4EBA\u662F\u82D7\u9AD8\u4E61\u6751\u6C11\u4E01\u6625\u59B9\uFF0C\u800C\u5F53\u665A\u53D7\u7406\u7684\u6C11\u8B66\u5206\u522B\u662F\u9AD8\u5FD7\u8FDC\u548C\u6768\u5FB7\u65B0\u3002\u7531\u4E8E\u4FE9\u4EBA\u5DF2\u572801\u5E74\u6625\u8282\u524D\u8C03\u8D70\uFF0C\u6240\u4EE5\u6C5F\u9633\u7D22\u8981\u4FAF\u8D35\u5E73\u6B7B\u4EA1\u6863\u6848\uFF0C\u7ED3\u679C\u7ADF\u7136\u770B\u89C1\u51FA\u8B66\u4EBA\u8FD8\u662F\u9AD8\u5FD7\u8FDC\u548C\u6768\u5FB7\u65B0\u3002</p><p>\u4F9D\u7167\u51FA\u8B66\u6863\u6848\u5185\u5BB9\uFF0C\u6C5F\u9633\u627E\u5230\u62A5\u8B66\u4EBA\u5B59\u5B87\u6CFD\uFF0C\u4ED6\u548C\u59BB\u5B50\u5916\u51FA\u65F6\uFF0C\u610F\u5916\u76EE\u51FB\u4FAF\u8D35\u5E73\u6C89\u5C38\u6C34\u5E93\uFF0C\u5C38\u9996\u5C5E\u4E8E\u80CC\u671D\u4E0A\u72B6\u6001\uFF0C\u5F85\u5B59\u5B87\u6CFD\u7528\u94C1\u94A9\u6253\u635E\uFF0C\u53D1\u73B0\u80CC\u4E0A\u7F20\u7ED5\u8BB8\u591A\u6C34\u8349\u3002\u6C5F\u9633\u79BB\u5F00\u6C34\u5E93\u540E\uFF0C\u518D\u53BB\u82D7\u9AD8\u4E2D\u5B66\u5BFB\u4EBA\u6253\u63A2\uFF0C\u5B66\u6821\u5E08\u751F\u7686\u6362\uFF0C\u552F\u72EC\u9A6C\u8001\u5E08\u5B89\u5BB6\u5728\u6B64\uFF0C\u6545\u672A\u66FE\u79BB\u5F00\u3002</p><p>\u5728\u9A6C\u8001\u5E08\u7684\u5370\u8C61\u4E2D\uFF0C\u4FAF\u8D35\u5E73\u5341\u5206\u70ED\u5FF1\uFF0C\u5BF9\u5B66\u751F\u4EEC\u683C\u5916\u5173\u6CE8\uFF0C\u5E76\u4E14\u5F97\u77E5\u5F88\u591A\u5B69\u5B50\u6240\u5B66\u53D7\u9650\uFF0C\u6700\u7EC8\u9519\u5931\u9AD8\u8003\u673A\u4F1A\u3002\u7FC1\u7F8E\u9999\u548C\u845B\u4E3D\u662F\u73ED\u7EA7\u91CC\u5B66\u4E60\u6700\u597D\u7684\u5973\u5B69\uFF0C\u5948\u4F55\u7F3A\u4E4F\u6587\u79D1\u77E5\u8BC6\uFF0C\u65E0\u6CD5\u987A\u5229\u8003\u4E0A\u5927\u5B66\uFF0C\u53EA\u80FD\u5728\u9644\u8FD1\u7EBA\u7EC7\u5382\u5DE5\u4F5C\u3002</p><p>\u4FAF\u8D35\u5E73\u4E3A\u4E24\u4E2A\u5973\u5B69\u60CB\u60DC\uFF0C\u4E8E\u662F\u63D0\u51FA\u5F00\u529E\u590D\u8BFB\u73ED\uFF0C\u8981\u4E3A\u5979\u4EEC\u8865\u4E60\u5E94\u8003\u3002\u7FC1\u7F8E\u9999\u88AB\u4FAF\u8D35\u5E73\u771F\u8BDA\u6253\u52A8\uFF0C\u4E5F\u5E0C\u671B\u901A\u8FC7\u77E5\u8BC6\u6539\u53D8\u547D\u8FD0\uFF0C\u5979\u53F7\u53EC\u5341\u51E0\u540D\u5DF2\u7ECF\u4F11\u5B66\u7684\u5E74\u8F7B\u7537\u5973\uFF0C\u5927\u5BB6\u767D\u5929\u5E72\u6D3B\uFF0C\u665A\u4E0A\u5219\u5728\u4FAF\u8D35\u5E73\u7684\u76D1\u7763\u4E0B\uFF0C\u52AA\u529B\u590D\u4E60\u529F\u8BFE\u3002\u9AD8\u8003\u5012\u8BA1\u65F6\u5206\u79D2\u6D41\u5931\uFF0C\u5B66\u751F\u4EEC\u867D\u5FD9\u788C\u5374\u5FEB\u4E50\uFF0C\u4FAF\u8D35\u5E73\u5982\u540C\u4E00\u7F15\u9633\u5149\u7167\u8FDB\u4F17\u4EBA\u5185\u5FC3\uFF0C\u7FC1\u7F8E\u9999\u4E5F\u56E0\u4ED6\u7684\u5230\u6765\uFF0C\u9010\u6E10\u5FD8\u5374\u6F5C\u85CF\u4E8E\u9ED1\u6697\u91CC\u7684\u5371\u673A\uFF0C\u5F85\u771F\u6B63\u56DE\u8FC7\u795E\uFF0C\u624D\u77E5\u591C\u662F\u65E0\u9650\u6F2B\u957F\u3002</p><p>\u9EC4\u6BDB\u662F\u9547\u4E0A\u7684\u6D41\u6C13\uFF0C\u8FD1\u6765\u65F6\u5E38\u5728\u7EBA\u7EC7\u5382\u9644\u8FD1\u9A9A\u6270\u5973\u5DE5\uFF0C\u751A\u81F3\u76EF\u4E0A\u845B\u4E3D\u548C\u7FC1\u7F8E\u9999\u3002\u845B\u4E3D\u4E0D\u582A\u5176\u6270\uFF0C\u76F4\u63A5\u8EB2\u56DE\u5BB6\u91CC\uFF0C\u7FC1\u7F8E\u9999\u72EC\u5C45\u4E0D\u6562\u5916\u51FA\uFF0C\u539F\u672C\u60F3\u8981\u6C42\u52A9\u4FAF\u8D35\u5E73\uFF0C\u600E\u6599\u9EC4\u6BDB\u5F00\u8F66\u5835\u5728\u534A\u8DEF\uFF0C\u81EA\u79F0\u7FC1\u7F8E\u9999\u8868\u54E5\uFF0C\u968F\u5373\u5C06\u5979\u5E26\u8D70\u3002</p><p>\u4E8B\u540E\u7FC1\u7F8E\u9999\u670D\u7528\u519C\u836F\u81EA\u6740\uFF0C\u7ECF\u53BF\u57CE\u533B\u9662\u62A2\u6551\uFF0C\u6700\u7EC8\u533B\u751F\u5BA3\u544A\u6B7B\u4EA1\u3002\u4FAF\u8D35\u5E73\u4ECE\u53E6\u5916\u4E24\u540D\u5B66\u751F\u53E3\u4E2D\u542C\u95FB\u4E8B\u60C5\u771F\u76F8\uFF0C\u7D27\u63A5\u53C8\u5728\u7FC1\u7F8E\u9999\u65E5\u8BB0\u91CC\u770B\u5230\u5979\u5BF9\u9EC4\u6BDB\u7684\u63A7\u8BC9\uFF0C\u4EE5\u53CA\u6050\u60E7\uFF0C\u987F\u65F6\u9677\u5165\u6781\u5EA6\u61CA\u6094\uFF0C\u4ED6\u6C38\u8FDC\u65E0\u6CD5\u5FD8\u8BB0\u7FC1\u7F8E\u9999\u4E0A\u8F66\u540E\u7684\u773C\u795E\uFF0C\u54EA\u6015\u6C7D\u8F66\u9A76\u8FDC\u3002</p><p>\u4FAF\u8D35\u5E73\u5E26\u4E0A\u65E5\u8BB0\u672C\uFF0C\u642D\u4E58\u8D27\u8F66\u8D76\u5F80\u5E73\u5EB7\u53BF\u516C\u5B89\u5C40\u62A5\u6848\uFF0C\u8981\u6C42\u5BF9\u7FC1\u7F8E\u9999\u8FDB\u884C\u5C38\u68C0\uFF0C\u4E3E\u62A5\u9EC4\u6BDB\u6027\u4FB5\u5973\u5DE5\u3002\u8B66\u65B9\u53D7\u7406\u540E\u7ACB\u6848\u6293\u4EBA\uFF0C\u5C31\u5728\u5927\u5BB6\u51C6\u5907\u7B49\u5F85\u5B9A\u7F6A\u7ED3\u679C\uFF0C\u6CA1\u60F3\u5230\u9EC4\u6BDB\u7A81\u7136\u51B2\u8FDB\u4FAF\u8D35\u5E73\u5BBF\u820D\u5C06\u4ED6\u6253\u4F24\uFF0C\u751A\u81F3\u60F3\u8981\u4FAE\u8FB1\u674E\u9759\uFF0C\u5E78\u597D\u9A6C\u8001\u5E08\u53CA\u65F6\u51FA\u73B0\u3002\u7ECF\u8FC7\u8FD9\u756A\u53D8\u6545\uFF0C\u4FAF\u8D35\u5E73\u51B3\u5B9A\u79C1\u4E0B\u8C03\u67E5\uFF0C\u8FD8\u5728\u65E5\u5386\u4E0A\u5708\u51FA\u201C10\u670825\u53F7\u201D\u3002</p><p>\u6BCF\u5F53\u674E\u9759\u601D\u53CA\u6B64\u5904\uFF0C\u4EFF\u4F5B\u4F24\u75A4\u518D\u88AB\u63ED\u5F00\uFF0C\u9732\u51FA\u8840\u6DCB\u6DCB\u7684\u4E8B\u5B9E\uFF0C\u5305\u62EC\u7559\u7ED9\u4FAF\u8D35\u5E73\u7684\u50BB\u74DC\u80F6\u7247\u76F8\u673A\u3002\u4E25\u826F\u95EE\u8D77\u76F8\u673A\u5185\u5BB9\uFF0C\u674E\u9759\u5C24\u663E\u7D27\u5F20\uFF0C\u7D22\u6027\u8F6C\u79FB\u8BDD\u9898\uFF0C\u8868\u793A\u4FAF\u8D35\u5E73\u4E34\u7EC8\u524D\u5BC4\u7ED9\u5979\u4E00\u5C01\u4FE1\uFF0C\u800C\u5979\u5374\u5C06\u6B64\u4FE1\u4EA4\u7ED9\u6C5F\u9633\uFF0C\u5982\u4ECA\u5E94\u8BE5\u8FD8\u5728\u9057\u7269\u91CC\u3002</p><p>\u91CD\u6848\u7EC4\u53D1\u73B0\u7B2C\u4E09\u5C01\u4FE1\u4ECE\u4E1C\u57CE\u5BC4\u51FA\uFF0C\u5E76\u4ECE\u6C5F\u9633\u9057\u7269\u4E2D\u627E\u5230\u7167\u76F8\u9986\u6536\u636E\uFF0C\u4E0A\u9762\u6807\u6CE82000\u5E7410\u670826\u65E5\u3002\u4E25\u826F\u60F3\u8D77\u6848\u53D1\u73B0\u573A\u7684\u4F38\u7F29\u8863\u67B6\u6216\u8BB8\u53EF\u4EE5\u63D0\u4F9B\u7EBF\u7D22\uFF0C\u4E8E\u662F\u5429\u5490\u8B66\u5458\u5C0F\u9A6C\u5230\u5404\u5927\u5546\u573A\u5E97\u94FA\u7B49\u5730\u8D2D\u4E70\u540C\u6B3E\uFF0C\u968F\u540E\u6839\u636E\u6536\u636E\u5730\u5740\u627E\u5230\u91D1\u5C71\u7167\u76F8\u9986\uFF0C\u53EF\u60DC\u7167\u76F8\u9986\u65E9\u5728\u591A\u5E74\u524D\u5DF2\u88AB\u70E7\u6BC1\u3002</p><p>\u4E25\u826F\u7FFB\u67E5\u5931\u706B\u51FA\u8B66\u8BB0\u5F55\uFF0C\u51FA\u8B66\u4EBA\u5458\u8C03\u53D6\u7167\u76F8\u9986\u9986\u4E3B\u674E\u5927\u5317\u6863\u6848\uFF0C\u6B64\u4EBA\u6B7B\u4E8E2003\u5E749\u670820\u65E5\uFF0C\u4E5F\u5C31\u662F\u7167\u76F8\u9986\u5927\u706B\u90A3\u5929\u3002\u674E\u5927\u5317\u8D4C\u8FD0\u5931\u5229\uFF0C\u4FBF\u53BB\u627E\u9053\u54E5\u501F\u94B1\uFF0C\u56E0\u4E3A\u5148\u524D\u65E7\u8D26\u672A\u8FD8\uFF0C\u9053\u54E5\u607C\u7F9E\u6210\u6012\uFF0C\u5A01\u80C1\u4ED6\u4E03\u5929\u5185\u51D1\u8DB3\u672C\u91D1\u3002</p><p>\u591C\u8272\u5DF2\u6DF1\uFF0C\u6C5F\u9633\u5BBF\u820D\u7A97\u6237\u88AB\u4EBA\u6253\u788E\uFF0C\u5F85\u4ED6\u593A\u95E8\u8FFD\u53BB\uFF0C\u65E9\u5DF2\u4E0D\u89C1\u8E2A\u8FF9\u3002\u4E00\u5219\u964C\u751F\u6765\u7535\u54CD\u8D77\uFF0C\u624B\u673A\u91CC\u4F20\u51FA\u795E\u79D8\u7537\u5B50\u58F0\u97F3\uFF0C\u4ED6\u5A01\u80C1\u6C5F\u9633\u505C\u67E5\u6B64\u6848\uFF0C\u5426\u5219\u6027\u547D\u4E0D\u4FDD\u30022000\u5E74\u7684\u4FAF\u8D35\u5E73\uFF1B2010\u5E74\u7684\u4E25\u826F\uFF0C\u4ED6\u4EEC\u4E0E\u6C5F\u9633\u4E3A\u6B63\u4E49\u800C\u6218\uFF0C\u4E0D\u60DC\u5BF9\u6297\u6076\u52BF\u529B\uFF0C\u800C\u6B64\u65F6\u7AD9\u5728\u9ED1\u6697\u4E0B\u7684\u6076\u52BF\u529B\uFF0C\u6B63\u88AB\u624B\u4E0B\u544A\u77E5\u5C0F\u9A6C\u8FFD\u67E5\u4F38\u7F29\u8863\u67B6\u3002</p>',16),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","4.html.vue"]]);export{h as default};
