import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as p,c as t,f as a}from"./app.cd90e563.js";const c={},o=a('<h1 id="\u7B2C40\u96C6-\u516D\u541B\u5B50\u805A\u4F1A\u4E0D\u6B22\u800C\u6563" tabindex="-1"><a class="header-anchor" href="#\u7B2C40\u96C6-\u516D\u541B\u5B50\u805A\u4F1A\u4E0D\u6B22\u800C\u6563" aria-hidden="true">#</a> \u7B2C40\u96C6\uFF1A\u516D\u541B\u5B50\u805A\u4F1A\u4E0D\u6B22\u800C\u6563</h1><p>1992\u5E74\u5409\u6625\uFF0C\u5B59\u5C0F\u5B81\u56DE\u6765\u8FC7\u5E74\uFF0C\u542C\u8BF4\u4E86\u73B0\u5728\u5468\u5BB6\u7684\u4E8B\u60C5\uFF0C\u5B59\u5C0F\u5B81\u7279\u610F\u6765\u63A2\u671B\u8FD8\u62FF\u51FA\u4E86\u4E00\u4E9B\u94B1\uFF0C\u5E0C\u671B\u80FD\u4EE3\u66FF\u54E5\u54E5\u548C\u5AC2\u5B50\u611F\u8C22\u5468\u79C9\u6606\u548C\u90D1\u5A1F\u7684\u5E2E\u52A9\uFF0C\u540C\u65F6\u4E5F\u662F\u5BF9\u8FC7\u4E16\u8001\u4EBA\u7684\u4E00\u70B9\u5FC3\u610F\u3002\u90D1\u5A1F\u672C\u60F3\u62D2\u7EDD\uFF0C\u4F46\u662F\u5B59\u5C0F\u5B81\u5DF2\u7ECF\u8DD1\u5F00\u4E86\u3002\u5468\u79C9\u6606\u56DE\u6765\u540E\u5F97\u77E5\u60C5\u51B5\uFF0C\u770B\u5230\u5F88\u591A\u7684\u94B1\u4E5F\u89C9\u5F97\u8FC7\u610F\u4E0D\u53BB\uFF0C\u5E72\u8106\u62FF\u7740\u94B1\u6765\u627E\u8D76\u8D85\uFF0C\u8BA9\u8D76\u8D85\u8FD8\u7ED9\u5B59\u5C0F\u5B81\u3002</p><p>\u8D76\u8D85\u8BA9\u5468\u79C9\u6606\u5C06\u94B1\u6536\u8D77\u6765\uFF0C\u56E0\u4E3A\u8C01\u8FD8\u7ED9\u5B59\u5C0F\u5B81\u5979\u90FD\u4E0D\u4F1A\u8981\uFF0C\u73B0\u5728\u5B59\u5C0F\u5B81\u5728\u6DF1\u5733\u505A\u697C\u76D8\u9500\u552E\uFF0C\u8D5A\u7684\u94B1\u5F88\u591A\uFF0C\u5468\u79C9\u6606\u8FD9\u624D\u62FF\u8D70\u4E86\u94B1\u3002</p><p>\u4E00\u5E74\u4E00\u6B21\u7684\u76F8\u805A\uFF0C\u5927\u5BB6\u518D\u6B21\u805A\u5230\u4E86\u4E00\u8D77\uFF0C\u552F\u72EC\u7F3A\u4E86\u5415\u5DDD\u548C\u5411\u9633\uFF0C\u4E54\u6625\u71D5\u5C31\u529D\u8BF4\u5927\u5BB6\u5FD8\u8BB0\u4E24\u4EBA\uFF0C\u56E0\u4E3A\u6709\u4E86\u65B0\u670B\u53CB\u90FD\u4E0D\u4F1A\u8BB0\u5F97\u8001\u670B\u53CB\u3002\u8BDD\u97F3\u521A\u843D\u5415\u5DDD\u548C\u5411\u9633\u5C31\u6765\u5230\u4E86\u8EAB\u540E\uFF0C\u4E54\u6625\u71D5\u8C03\u4F83\u4E8C\u4EBA\u5C31\u597D\u50CF\u662F\u5317\u4EAC\u6765\u7684\u5927\u9886\u5BFC\u6765\u63A2\u671B\u5927\u5BB6\u4E00\u6837\u3002\u5415\u5DDD\u5374\u7B11\u8A00\u4ED6\u5C31\u50CF\u662F\u79BB\u5F00\u5BB6\u591A\u5E74\u7684\u8001\u72D7\uFF0C\u751F\u6015\u56DE\u6765\u8001\u5BB6\u4EBA\u4E0D\u5F85\u89C1\u4E86\uFF0C\u4E54\u6625\u71D5\u95FB\u8A00\u4E0A\u524D\u5C31\u6765\u4E86\u4E00\u4E2A\u5927\u62E5\u62B1\uFF0C\u4E00\u4E2A\u543B\u9001\u5230\u4E86\u8138\u988A\u4E0A\uFF0C\u6C14\u6C1B\u4E5F\u88AB\u70D8\u6258\u8D77\u6765\u3002</p><p>\u5415\u5DDD\u8BB2\u8FF0\u4E86\u5728\u65F6\u4EE3\u53D1\u5C55\u4E2D\u5FC5\u7136\u4F1A\u9047\u5230\u5404\u79CD\u9635\u75DB\u7684\u4E8B\u60C5\uFF0C\u4F46\u90FD\u4F1A\u8FCE\u6765\u597D\u7684\u7ED3\u679C\uFF0C\u8FD9\u5BF9\u4E8E\u4E54\u6625\u71D5\u6765\u8BF4\u6839\u672C\u65E0\u6CD5\u7406\u89E3\uFF0C\u5979\u73B0\u5728\u7684\u6FA1\u5802\u5B50\u4E5F\u65E9\u5C31\u53D8\u6210\u4E86\u6C11\u8425\u4E86\u3002\u4E8E\u8679\u5E0C\u671B\u5415\u5DDD\u80FD\u8BB2\u4E00\u4E0B\u4EE5\u540E\u7684\u53D1\u5C55\u65B9\u5411\u8FD8\u6709\u4ED6\u4EEC\u4EE5\u540E\u7684\u9053\u8DEF\uFF0C\u53EF\u662F\u56FD\u5E86\u548C\u8D76\u8D85\u5374\u6253\u65AD\u4E86\u5415\u5DDD\u7684\u8BDD\uFF0C\u8FD8\u7279\u610F\u8BBD\u523A\u4E24\u4EBA\u548C\u4ED6\u4EEC\u5DF2\u7ECF\u4E0D\u5728\u4E00\u4E2A\u9AD8\u5EA6\u4E86\uFF0C\u5415\u5DDD\u8BB2\u7684\u8BDD\u4ED6\u4EEC\u4E5F\u542C\u4E0D\u61C2\u3002</p><p>\u56FD\u5E86\u8BA4\u4E3A\u5149\u5B57\u7247\u7684\u5730\u65B9\u5F97\u4E0D\u5230\u89E3\u51B3\u5C31\u8BF4\u660E\u4E0A\u9762\u7684\u4EBA\u6CA1\u6709\u4F5C\u4E3A\uFF0C\u518D\u591A\u7684\u5927\u9053\u7406\u4E5F\u53EA\u662F\u9053\u7406\uFF0C\u4ED6\u7236\u4EB2\u5C31\u662F\u6B7B\u5728\u8FD9\u79CD\u7A77\u56F0\u4E4B\u4E0B\uFF0C\u56FD\u5E86\u63D0\u51FA\u8BA9\u5415\u5DDD\u5411\u4E0A\u9762\u7684\u4EBA\u53CD\u5E94\u4E00\u4E0B\uFF0C\u4E0D\u8981\u603B\u8BF4\u5927\u9053\u7406\u3002\u56FD\u5E86\u4E00\u6012\u4E4B\u4E0B\u6380\u7FFB\u4E86\u684C\u5B50\uFF0C\u5468\u79C9\u6606\u5FCD\u65E0\u53EF\u5FCD\u7684\u8BAD\u65A5\u4E86\u56FD\u5E86\uFF0C\u5176\u5B9E\u5415\u5DDD\u4ECE\u6765\u4EC0\u4E48\u4E5F\u6CA1\u6709\u8BF4\uFF0C\u6BCF\u5929\u90FD\u6709\u4EBA\u7684\u7236\u6BCD\u6B7B\u4EA1\uFF0C\u5415\u5DDD\u4E5F\u65E0\u53EF\u5948\u4F55\u3002\u5468\u79C9\u6606\u4E3B\u52A8\u63D0\u51FA\u9001\u5415\u5DDD\u56DE\u53BB\uFF0C\u5415\u5DDD\u5374\u628A\u81EA\u5DF1\u5173\u5728\u5395\u6240\u91CC\uFF0C\u8D76\u8D70\u4E86\u5468\u79C9\u6606\u548C\u5411\u9633\u3002</p><p>\u5468\u79C9\u6606\u56DE\u6765\u7684\u65F6\u5019\uFF0C\u56FD\u5E86\u5DF2\u7ECF\u5728\u5BB6\u91CC\u7B49\u4E86\u5F88\u957F\u65F6\u95F4\uFF0C\u56FD\u5E86\u610F\u8BC6\u5230\u81EA\u5DF1\u7684\u9519\u8BEF\uFF0C\u5E0C\u671B\u80FD\u8BA9\u5468\u79C9\u6606\u5E26\u7740\u53BB\u9053\u6B49\uFF0C\u5468\u79C9\u6606\u53EA\u597D\u53C8\u7A7F\u4E0A\u8131\u4E0B\u7684\u8863\u670D\u5E26\u7740\u56FD\u5E86\u51FA\u95E8\uFF0C\u53EF\u662F\u5E76\u672A\u89C1\u5230\u5415\u5DDD\u3002</p><p>\u4E54\u6625\u71D5\u548C\u66F9\u5FB7\u5B9D\u6765\u627E\u5468\u79C9\u6606\u5E2E\u5FD9\uFF0C\u5E0C\u671B\u5468\u79C9\u6606\u80FD\u5E2E\u5FD9\u53BB\u627E\u5468\u79C9\u4E49\u7ED9\u66F9\u5FB7\u5B9D\u627E\u4E2A\u5DE5\u4F5C\uFF0C\u8FD9\u6837\u4E24\u4EBA\u6536\u5165\u4E5F\u6BD4\u8F83\u9AD8\uFF0C\u8FD8\u80FD\u51FA\u53BB\u79DF\u623F\u5B50\u3002\u5468\u79C9\u6606\u5EFA\u8BAE\u8BA9\u627E\u8D76\u8D85\u60F3\u529E\u6CD5\uFF0C\u8D76\u8D85\u73B0\u5728\u6253\u96F6\u5DE5\uFF0C\u6BCF\u5929\u4E5F\u6536\u5165\u5F88\u591A\u94B1\u3002\u4E54\u6625\u71D5\u8D23\u602A\u5468\u79C9\u6606\u4E0D\u80AF\u5E2E\u5FD9\uFF0C\u5468\u79C9\u6606\u53D1\u813E\u6C14\u8BA9\u4E54\u6625\u71D5\u81EA\u5DF1\u53BB\u627E\u5468\u79C9\u4E49\uFF0C\u4E5F\u607C\u6012\u4ECA\u5929\u56FD\u5E86\u6380\u684C\u5B50\u7684\u4E8B\u60C5\uFF0C\u6240\u6709\u4E8B\u60C5\u52A0\u4E00\u8D77\u53CC\u65B9\u95F9\u4E86\u4E2A\u4E0D\u6B22\u800C\u6563\u3002</p><p>\u8D76\u8D85\u5728\u80F6\u978B\u5382\u5356\u978B\uFF0C\u773C\u7785\u7740\u80F6\u978B\u5356\u4E0D\u51FA\u53BB\uFF0C\u5DE5\u4EBA\u5DE5\u8D44\u53D1\u4E0D\u51FA\u6765\u3002\u8D76\u8D85\u7279\u610F\u8054\u7CFB\u4E86\u4E00\u4E2A\u5357\u65B9\u7684\u5546\u4EBA\u613F\u610F\u63A5\u624B\uFF0C\u53EF\u662F\u5BF9\u65B9\u53EA\u7B54\u5E94\u9884\u4ED8\u56DB\u5206\u4E4B\u4E00\u7684\u8D27\u6B3E\uFF0C\u800C\u5DE5\u5382\u7684\u4EBA\u5374\u8981\u6C42\u9884\u4ED8\u4E09\u5206\u4E4B\u4E00\uFF0C\u8D76\u8D85\u60F3\u8981\u4FC3\u6210\u8FD9\u4EF6\u4E8B\uFF0C\u5C31\u6253\u7B97\u81EA\u5DF1\u57AB\u4ED8\u5176\u4E2D\u7684\u4E00\u5343\u591A\u5143\u3002\u5468\u79C9\u6606\u4E8C\u8BDD\u4E0D\u8BF4\u5C31\u5C06\u94B1\u501F\u7ED9\u4E86\u8D76\u8D85\uFF0C\u53EF\u662F\u6CA1\u60F3\u5230\u534A\u5E74\u4E4B\u540E\u51FA\u4E8B\u4E86\uFF0C\u8D76\u8D85\u90A3\u8FB9\u544A\u8BC9\u5468\u79C9\u6606\u81EA\u5DF1\u88AB\u9A97\u4E86\u3002</p><p>\u73B0\u5728\u60F3\u8981\u8054\u7CFB\u5B59\u5C0F\u5B81\u53BB\u7ED9\u770B\u770B\uFF0C\u5B59\u5C0F\u5B81\u8FD8\u8F9E\u804C\u4E86\uFF0C\u73B0\u5728\u978B\u4ED8\u51FA\u53BB\u4E86\uFF0C\u8D27\u6B3E\u8981\u4E0D\u56DE\u6765\uFF0C\u5BF9\u65B9\u8BF4\u8D27\u4E0D\u597D\u5356\u3002\u8D76\u8D85\u63D0\u51FA\u8981\u5C06\u8D27\u7ED9\u8981\u56DE\u6765\uFF0C\u53EF\u662F\u5BF9\u65B9\u975E\u8981\u518D\u5356\u5356\u770B\uFF0C\u4F46\u662F\u4ECE\u6B64\u5C31\u6CA1\u6709\u97F3\u4FE1\u4E86\u3002\u5982\u679C\u8FD9\u4E2A\u4E8B\u60C5\u5904\u7406\u4E0D\u597D\uFF0C\u53EF\u80FD\u5C31\u4F1A\u88AB\u8BA4\u5B9A\u4E3A\u8BC8\u9A97\u72AF\u7F6A\uFF0C\u6709\u53EF\u80FD\u4F1A\u88AB\u5173\u5165\u5927\u7262\u3002\u5468\u79C9\u6606\u63D0\u51FA\u53BB\u627E\u5468\u79C9\u4E49\u60F3\u529E\u6CD5\uFF0C\u8D76\u8D85\u611F\u6FC0\u4E0D\u5C3D\u3002</p><p>\u5F53\u5468\u79C9\u6606\u6765\u627E\u5468\u79C9\u4E49\u7684\u65F6\u5019\uFF0C\u5F97\u77E5\u6709\u5DE5\u4EBA\u95F9\u4E8B\u53BB\u516C\u5B89\u5C40\u4E86\uFF0C\u5468\u79C9\u4E49\u6B63\u5E26\u4EBA\u5F00\u8F66\u8D76\u8FC7\u53BB\u516C\u5B89\u5C40\u3002\u7531\u4E8E\u6C7D\u914D\u5382\u88AB\u5C71\u897F\u5546\u4EBA\u9A97\u4E86\uFF0C\u5DE5\u4EBA\u5DE5\u8D44\u53D1\u4E0D\u51FA\u6765\uFF0C\u6240\u4EE5\u5DE5\u4EBA\u5C31\u53BB\u95F9\u4E8B\u4E86\u3002\u6C7D\u914D\u5382\u7684\u5382\u957F\u627E\u5468\u79C9\u4E49\uFF0C\u5E0C\u671B\u80FD\u8D76\u7D27\u7834\u6848\uFF0C\u5728\u4ED6\u770B\u6765\u4E5F\u90FD\u662F\u516C\u5B89\u5C40\u672A\u80FD\u7834\u6848\u5BFC\u81F4\u7684\u3002\u5468\u79C9\u4E49\u544A\u8BC9\u5382\u957F\uFF0C\u516C\u5B89\u5C40\u4E5F\u6709\u516C\u5B89\u5C40\u7684\u96BE\u5904\uFF0C\u73B0\u5728\u662F\u8DE8\u7701\u6293\u4EBA\uFF0C\u9762\u5BF9\u7684\u56F0\u96BE\u4E5F\u662F\u7A7A\u524D\u7684\u96BE\u3002</p><p>\u5468\u79C9\u4E49\u5B89\u629A\u4E86\u5927\u5BB6\u7684\u60C5\u7EEA\uFF0C\u4FDD\u8BC1\u4E86\u89E3\u51B3\u95EE\u9898\u7684\u524D\u63D0\u4E0D\u80FD\u8BA9\u6C7D\u914D\u5382\u5DE5\u4EBA\u7684\u5229\u76CA\u53D7\u635F\u3002\u5728\u516C\u5B89\u5C40\u5FD9\u4E86\u4E00\u5929\u4E4B\u540E\uFF0C\u5468\u79C9\u4E49\u6253\u7B97\u56DE\u5BB6\uFF0C\u53F8\u673A\u544A\u77E5\u5468\u79C9\u6606\u6765\u8FC7\u4E86\uFF0C\u5468\u79C9\u4E49\u4E34\u65F6\u51B3\u5B9A\u53BB\u4F20\u8FBE\u5BA4\u6253\u7535\u8BDD\u3002</p>',12),r=[o];function _(n,s){return p(),t("div",null,r)}const h=e(c,[["render",_],["__file","40.html.vue"]]);export{h as default};
