import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.8a7d9ef5.js";const c={},o=a('<h1 id="\u7B2C41\u96C6-\u73A5\u73A5\u5982\u613F\u4EE5\u507F\u8003\u4E0A\u6E05\u534E" tabindex="-1"><a class="header-anchor" href="#\u7B2C41\u96C6-\u73A5\u73A5\u5982\u613F\u4EE5\u507F\u8003\u4E0A\u6E05\u534E" aria-hidden="true">#</a> \u7B2C41\u96C6\uFF1A\u73A5\u73A5\u5982\u613F\u4EE5\u507F\u8003\u4E0A\u6E05\u534E</h1><p>\u5468\u79C9\u4E49\u770B\u4F20\u8FBE\u5BA4\u5DF2\u7ECF\u4E0B\u73ED\u4E86\uFF0C\u76F4\u63A5\u53BB\u5BB6\u91CC\u627E\u5468\u79C9\u6606\uFF0C\u5468\u79C9\u4E49\u5FD9\u4E86\u4E00\u5929\u90FD\u6CA1\u987E\u4E0A\u5403\u996D\uFF0C\u90D1\u5A1F\u60F3\u8981\u7ED9\u505A\u70B9\uFF0C\u4F46\u5468\u79C9\u4E49\u4E0D\u60F3\u592A\u9EBB\u70E6\uFF0C\u5C31\u7740\u51B7\u996D\u5403\u4E86\u51E0\u53E3\u3002\u5468\u79C9\u6606\u672C\u4E0D\u60F3\u518D\u53BB\u9EBB\u70E6\u54E5\u54E5\uFF0C\u4F46\u662F\u5468\u79C9\u4E49\u77E5\u9053\u4E00\u5B9A\u662F\u51FA\u4E86\u5927\u4E8B\uFF0C\u8981\u4E0D\u7136\u5468\u79C9\u6606\u4E5F\u4E0D\u4F1A\u4E13\u95E8\u53BB\u673A\u5173\u627E\u4ED6\u3002\u5468\u79C9\u6606\u8FD9\u624D\u8BF4\u4E86\u8D76\u8D85\u7684\u4E8B\u60C5\uFF0C\u56DE\u53BB\u4E4B\u540E\u5468\u79C9\u4E49\u5C31\u7ED9\u59DA\u7ACB\u677E\u6253\u4E86\u7535\u8BDD\u3002</p><p>\u9A86\u58EB\u5BBE\u77E5\u9053\u8FD9\u4EF6\u4E8B\u4E4B\u540E\u7ACB\u523B\u8868\u793A\u4ED6\u6765\u529E\uFF0C\u4ED6\u8981\u627E\u5230\u90A3\u4E2A\u6DF7\u86CB\u7136\u540E\u4EB2\u81EA\u5E26\u7740\u94B1\u53BB\u5409\u6625\u767B\u95E8\u9053\u6B49\uFF0C\u59DA\u7ACB\u677E\u7B11\u9A86\u58EB\u5BBE\u662F\u60F3\u8BA9\u6960\u6960\u770B\u770B\u4ED6\u7684\u672C\u4E8B\u3002\u9A86\u58EB\u5BBE\u5374\u5E76\u6CA1\u6709\u8BF4\u660E\u539F\u56E0\uFF0C\u4ED6\u4E0D\u6307\u671B\u5468\u79C9\u6606\u79F0\u8D5E\uFF0C\u4E5F\u4E0D\u5E0C\u671B\u6960\u6960\u53E6\u773C\u76F8\u770B\uFF0C\u53EA\u5E0C\u671B\u5468\u79C9\u6606\u80FD\u4E0D\u5728\u6960\u6960\u9762\u524D\u9A82\u4ED6\u5C31\u884C\uFF0C\u867D\u7136\u662F\u73A9\u7B11\u8BDD\u59DA\u7ACB\u677E\u4E5F\u770B\u51FA\u9A86\u58EB\u5BBE\u6CA1\u6709\u6076\u610F\u3002\u5F88\u5FEB\uFF0C\u6D82\u603B\u548C\u6C34\u81EA\u6D41\u4E00\u8D77\u6765\u627E\u8D76\u8D85\uFF0C\u8FDE\u672C\u5E26\u606F\u7684\u90FD\u7ED9\u5E26\u6765\u4E86\uFF0C\u5468\u79C9\u6606\u53EA\u597D\u5BF9\u6C34\u81EA\u6D41\u8868\u793A\u4E86\u8C22\u610F\u3002</p><p>\u4E54\u6625\u71D5\u8BF7\u90B5\u4E3B\u4EFB\u5403\u996D\u5E76\u4E14\u51C6\u5907\u4E86\u7EA2\u5305\uFF0C\u60F3\u8981\u8BF7\u5979\u5E2E\u5FD9\u4E0D\u8981\u817E\u623F\uFF0C\u4F46\u662F\u90B5\u4E3B\u4EFB\u5E76\u672A\u6536\u4E0B\uFF0C\u4E5F\u8868\u793A\u65E0\u6CD5\u5E2E\u5FD9\u3002\u4E24\u4EBA\u7EC8\u4E8E\u6495\u7834\u8138\uFF0C\u4E54\u6625\u71D5\u8BBD\u523A\u90B5\u4E3B\u4EFB\u5F53\u5E74\u89C1\u98CE\u4F7F\u8235\uFF0C\u90B5\u4E3B\u4EFB\u5374\u544A\u8BC9\u4E54\u6625\u71D5\u5979\u662F\u4E00\u6B65\u4E00\u4E2A\u811A\u5370\u5E72\u51FA\u6765\u7684\uFF0C\u5F53\u5E74\u5979\u559C\u6B22\u7684\u5C31\u662F\u4E54\u6625\u71D5\u4E0D\u670D\u8F93\u7684\u6027\u683C\uFF0C\u53EF\u662F\u73B0\u5728\u8FD9\u79CD\u6027\u683C\u6CA1\u4E86\u3002\u90B5\u4E3B\u4EFB\u79BB\u5F00\u7684\u65F6\u5019\u4E0B\u8FBE\u4E86\u6700\u540E\u901A\u7252\uFF0C\u4E0B\u4E2A\u6708\u5341\u53F7\u5FC5\u987B\u817E\u623F\uFF0C\u5426\u5219\u5F3A\u5236\u6267\u884C\u3002</p><p>\u8D76\u8D85\u559D\u4E86\u5F88\u591A\u9152\uFF0C\u56DE\u53BB\u4E4B\u540E\u62FF\u4E86\u5F88\u591A\u94B1\u7ED9\u4E8E\u8679\uFF0C\u516D\u5343\u53CC\u978B\u7684\u63D0\u6210\uFF0C\u8FD8\u6709\u4E4B\u524D\u4E00\u5343\u5757\u94B1\u7684\u5229\u606F\uFF0C\u4E8E\u8679\u5F00\u5FC3\u4E0D\u5DF2\u3002</p><p>\u4E54\u6625\u71D5\u6765\u627E\u5468\u79C9\u6606\u8BF4\u4E86\u5DF2\u7ECF\u53EB\u4E86\u54E5\u4EEC\u548C\u59D0\u4EEC\u642C\u5BB6\u7684\u4E8B\u60C5\uFF0C\u5468\u79C9\u6606\u7ACB\u523B\u8868\u793A\u4E0B\u5468\u4E5F\u53BB\u5E2E\u5FD9\u642C\u5BB6\uFF0C\u4F46\u4E54\u6625\u71D5\u5374\u53CD\u95EE\u5468\u79C9\u6606\u662F\u5979\u54E5\u4EEC\u5417\uFF1F\u5468\u79C9\u6606\u77E5\u9053\u4E54\u6625\u71D5\u662F\u8D23\u602A\u4ED6\u6CA1\u80FD\u5E2E\u5FD9\u3002</p><p>\u6C34\u81EA\u6D41\u627E\u90D1\u5A1F\uFF0C\u5E0C\u671B\u6691\u5047\u7684\u65F6\u5019\u80FD\u8BA9\u6960\u6960\u53BB\u6DF1\u5733\u8FC7\uFF0C\u90D1\u5A1F\u5BF9\u4E8E\u6960\u6960\u7684\u4E8B\u60C5\u5DF2\u7ECF\u770B\u5F00\u4E86\uFF0C\u4E5F\u4E0D\u6253\u7B97\u7BA1\u4E86\uFF0C\u53EA\u8981\u6960\u6960\u81EA\u5DF1\u540C\u610F\u5979\u6CA1\u6709\u610F\u89C1\uFF0C\u6C34\u81EA\u6D41\u8FD8\u662F\u5E0C\u671B\u90D1\u5A1F\u80FD\u548C\u6960\u6960\u8BF4\u4E00\u58F0\u3002\u6070\u5728\u8FD9\u4E2A\u65F6\u5019\u5468\u79C9\u6606\u56DE\u6765\u4E86\uFF0C\u5468\u79C9\u6606\u8BF4\u4E86\u4E00\u4E0B\u56DE\u6765\u4E4B\u524D\u78B0\u89C1\u4E54\u6625\u71D5\u7684\u4E8B\u60C5\u803D\u6401\u4E86\u3002\u6C34\u81EA\u6D41\u4E3B\u52A8\u8BE2\u95EE\u770B\u662F\u5426\u80FD\u5E2E\u5FD9\uFF0C\u4F46\u662F\u5468\u79C9\u6606\u5374\u8BA4\u4E3A\u4E0D\u80FD\u5E2E\u5FD9\u4E54\u6625\u71D5\u4E5F\u4E0D\u8BE5\u751F\u6C14\uFF0C\u4E5F\u4E0D\u8BA9\u6C34\u81EA\u6D41\u591A\u7BA1\u3002\u5468\u79C9\u6606\u5BF9\u4E8E\u6960\u6960\u7684\u4E8B\u60C5\u4E5F\u4E0D\u53CD\u5BF9\uFF0C\u6BD5\u7ADF\u5B69\u5B50\u5927\u4E86\uFF0C\u81EA\u5DF1\u505A\u4E3B\u3002</p><p>\u79E6\u5B88\u5FB7\u5E0C\u671B\u80FD\u901A\u8FC7\u4E54\u6625\u71D5\u8BA4\u8BC6\u4E00\u4E0B\u5468\u79C9\u4E49\uFF0C\u8FD8\u8BB8\u8BFA\u7ED9\u4E54\u6625\u71D5\u79DF\u4E86\u4E00\u5957\u597D\u623F\u5B50\uFF0C\u58F0\u79F0\u8D70\u516C\u53F8\u7684\u8D26\u3002\u4E54\u6625\u71D5\u56DE\u53BB\u548C\u4E54\u5927\u5A76\u5546\u8BAE\u4E86\u4E00\u4E0B\uFF0C\u6253\u7B97\u8D81\u7740\u642C\u5BB6\u7684\u65F6\u5019\u628A\u6240\u6709\u7684\u670B\u53CB\u90FD\u53EB\u8FC7\u6765\uFF0C\u800C\u5468\u79C9\u6606\u4E5F\u4E00\u5B9A\u6765\u3002\u5230\u65F6\u5019\u5C31\u62C9\u5927\u65D7\u505A\u4F5C\u864E\u76AE\uFF0C\u867D\u7136\u4E0D\u80FD\u5C06\u5468\u79C9\u4E49\u8BF7\u8FC7\u6765\uFF0C\u4F46\u662F\u5468\u79C9\u6606\u5374\u662F\u5468\u79C9\u4E49\u7684\u4EB2\u5F1F\u5F1F\u3002\u4E54\u6625\u71D5\u5411\u66F9\u5FB7\u5B9D\u8BF4\u4E86\u81EA\u5DF1\u7684\u6253\u7B97\uFF0C\u8FD8\u6253\u7B97\u8D81\u6B64\u673A\u4F1A\u8BA9\u79E6\u5B88\u5FB7\u7ED9\u89E3\u51B3\u4E86\u66F9\u5FB7\u5B9D\u5DE5\u4F5C\u7684\u95EE\u9898\uFF0C\u8FD8\u5FC5\u987B\u662F\u597D\u5DE5\u4F5C\u4F53\u9762\u8D5A\u94B1\u624D\u884C\u3002</p><p>\u5728\u805A\u4F1A\u8FD9\u5929\uFF0C\u5468\u79C9\u6606\u5E26\u7740\u90D1\u5A1F\u8FC7\u53BB\uFF0C\u4E54\u6625\u71D5\u6545\u4F5C\u4EB2\u5BC6\u7684\u53EB\u7740\u5468\u79C9\u6606\u54E5\u54E5\uFF0C\u5468\u79C9\u6606\u548C\u4E54\u6625\u71D5\u672C\u5C31\u50CF\u662F\u5144\u5F1F\u4E00\u822C\u76F8\u5904\uFF0C\u81EA\u7136\u6CA1\u6709\u60F3\u5230\u4F1A\u88AB\u4E54\u6625\u71D5\u5229\u7528\u3002\u79E6\u5B88\u5FB7\u6BD5\u606D\u6BD5\u656C\u7684\u9012\u51FA\u4E86\u540D\u7247\uFF0C\u77E5\u9053\u5468\u79C9\u6606\u6CA1\u6709\u540D\u7247\u8FD8\u7279\u610F\u7ED9\u4ED6\u4E5F\u5370\u5236\u4E86\u4E00\u76D2\u9001\u5230\u996D\u5E97\u3002</p><p>\u79E6\u5B88\u5FB7\u5E26\u7740\u5BA2\u6237\u53BB\u5403\u996D\uFF0C\u4E3A\u4E86\u7ED9\u4E54\u6625\u71D5\u9762\u5B50\uFF0C\u5468\u79C9\u6606\u7279\u610F\u524D\u53BB\u6253\u62DB\u547C\uFF0C\u79E6\u5B88\u5FB7\u4E5F\u5C06\u5468\u79C9\u6606\u548C\u5468\u79C9\u4E49\u7684\u5173\u7CFB\u70AB\u8000\u4E86\u4E00\u756A\uFF0C\u5F15\u5F97\u5927\u5BB6\u90FD\u975E\u5E38\u7FA1\u6155\u3002\u66F9\u5FB7\u5B9D\u4E5F\u5982\u613F\u4EE5\u507F\u5F53\u4E0A\u4E86\u5E93\u7BA1\uFF0C\u5DE5\u8D44\u9AD8\u6D3B\u8FD8\u8F7B\u5DE7\u3002\u66F9\u5FB7\u5B9D\u540C\u65F6\u4E5F\u8DDF\u4E54\u6625\u71D5\u5766\u767D\uFF0C\u6709\u4E2A\u5973\u7684\u559C\u6B22\u4ED6\uFF0C\u4ED6\u56E0\u4E3A\u90A3\u5973\u7684\u6027\u5B50\u6709\u70B9\u5FC3\u52A8\uFF0C\u4F46\u6CA1\u6709\u505A\u8FC7\u5206\u7684\u4E8B\u60C5\uFF0C\u4E54\u6625\u71D5\u4E5F\u610F\u8BC6\u5230\u81EA\u5DF1\u5E73\u65F6\u6027\u5B50\u592A\u6025\u4E86\uFF0C\u66F9\u5FB7\u5B9D\u4E5F\u8868\u793A\u7406\u89E3\uFF0C\u4E24\u4E2A\u4EBA\u60FA\u60FA\u76F8\u60DC\u3002</p><p>\u90D1\u5A1F\u63A5\u5230\u4E86\u6960\u6960\u7684\u6765\u4FE1\uFF0C\u77E5\u9053\u6960\u6960\u540C\u610F\u53BB\u6DF1\u5733\u4E86\uFF0C\u90D1\u5A1F\u5FC3\u91CC\u8FD8\u6709\u4E9B\u96BE\u8FC7\uFF0C\u5468\u79C9\u6606\u529D\u8BF4\u90D1\u5A1F\u8981\u653E\u5BBD\u5FC3\uFF0C\u6960\u6960\u4EBA\u5728\u6DF1\u5733\uFF0C\u5FC3\u5728\u5409\u6625\u3002\u6960\u6960\u6765\u5230\u6DF1\u5733\u627E\u9A86\u58EB\u5BBE\uFF0C\u9A86\u58EB\u5BBE\u7684\u59BB\u5B50\u66F9\u73CA\u5FC3\u91CC\u867D\u7136\u4E0D\u6EE1\uFF0C\u53EF\u662F\u8138\u4E0A\u5374\u4E0D\u5F97\u4E0D\u88C5\u51FA\u4E00\u526F\u6B22\u8FCE\u7684\u6837\u5B50\u3002</p><p>\u5468\u84C9\u6B63\u5728\u5FD9\u7740\u505A\u996D\uFF0C\u73A5\u73A5\u62FF\u7740\u6E05\u534E\u7684\u901A\u77E5\u4E66\u653E\u5728\u4E86\u5468\u84C9\u7684\u773C\u524D\uFF0C\u5468\u84C9\u6FC0\u52A8\u548C\u73A5\u73A5\u62E5\u62B1\u5927\u558A\uFF0C\u4E24\u4EBA\u7684\u53EB\u58F0\u5F15\u6765\u4E86\u8521\u6653\u5149\u3002\u8521\u6653\u5149\u770B\u5230\u901A\u77E5\u4E66\u4E5F\u5FCD\u4E0D\u4F4F\u554A\u554A\u5927\u53EB\uFF0C\u6FC0\u52A8\u4E4B\u60C5\u6EA2\u4E8E\u8A00\u8868\u3002</p><p>\u73A5\u73A5\u8003\u4E0A\u4E86\u6E05\u534E\u5927\u5B66\uFF0C\u800C\u4E54\u6625\u71D5\u7684\u513F\u5B50\u725B\u725B\u5374\u4EC0\u4E48\u90FD\u4E0D\u662F\uFF0C\u5929\u5929\u4E0D\u7740\u5BB6\uFF0C\u4E54\u6625\u71D5\u6254\u7ED9\u66F9\u5FB7\u5B9D\u4E00\u6C93\u5B50\u94B1\uFF0C\u8FD9\u662F\u5979\u53D1\u7684\u4E24\u500D\u5956\u91D1\uFF0C\u800C\u4E14\u9886\u5BFC\u8FD8\u8BF4\u8981\u7ED9\u5F00\u4E00\u4E2A\u5206\u5E97\u3002\u66F9\u5FB7\u5B9D\u5FD9\u966A\u7740\u5C0F\u5FC3\u54C4\u7740\u4E54\u6625\u71D5\uFF0C\u4E54\u6625\u71D5\u53D1\u73B0\u725B\u725B\u56E0\u4E3A\u6CA1\u6709\u8003\u597D\uFF0C\u7559\u4E0B\u4E66\u4FE1\u79BB\u5BB6\u51FA\u8D70\uFF0C\u7740\u6025\u7684\u8DD1\u53BB\u516C\u5B89\u5C40\u62A5\u6848\u3002\u516C\u5B89\u5C40\u8BA4\u4E3A\u6CA1\u6709\u6EE1\u56DB\u5341\u516B\u5C0F\u65F6\u4E0D\u7ACB\u6848\uFF0C\u4E54\u6625\u71D5\u60C5\u6025\u4E4B\u4E0B\u6655\u5012\u4E86\u3002</p><p>\u4E54\u6625\u71D5\u9AD8\u70E7\u4E0D\u9000\u8EBA\u5728\u5E8A\u4E0A\uFF0C\u5468\u79C9\u6606\u6765\u770B\u4E54\u6625\u71D5\uFF0C\u5B89\u6170\u4E54\u6625\u71D5\u591A\u770B\u770B\u5B59\u5C0F\u5B81\uFF0C\u4E00\u4E2A\u5973\u5B69\u51FA\u95E8\u5728\u5916\u6DF7\u5F97\u6BD4\u7537\u4EBA\u90FD\u597D\uFF0C\u73B0\u5728\u5B69\u5B50\u957F\u5927\u4E86\u5C31\u4E0D\u7528\u592A\u62C5\u5FC3\uFF0C\u53EF\u80FD\u4ED6\u662F\u60F3\u6DF7\u51FA\u4E00\u4E2A\u4EBA\u6837\u518D\u56DE\u6765\u3002\u4E54\u6625\u71D5\u81EA\u8D23\u81EA\u5DF1\u90FD\u6CA1\u6709\u8003\u4E0A\u5927\u5B66\uFF0C\u5929\u5929\u7740\u513F\u5B50\u4E0A\u5927\u5B66\u3002</p>',14),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","41.html.vue"]]);export{h as default};
