import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as t,f as e}from"./app.b5163196.js";const r={},s=e('<h1 id="\u4E1C\u664B\u7684\u706D\u4EA1" tabindex="-1"><a class="header-anchor" href="#\u4E1C\u664B\u7684\u706D\u4EA1" aria-hidden="true">#</a> \u4E1C\u664B\u7684\u706D\u4EA1</h1><blockquote><p>\uFF08\u664B\u5B89\u5E1D\u5174\u5143\u4E8C\u5E74\u5341\u4E00\u6708\uFF09\uFF0C\u5E9A\u8FB0\uFF0C\u5E1D\u4E34\u8F69\uFF0C\u9063\u517C\u592A\u4FDD\u3001\u9886\u53F8\u5F92\u738B\u8C27\u5949\u73BA\u7EF6\uFF0C\u7985\u4F4D\u4E8E\u695A\u3002</p></blockquote><p><strong>\u300A\u8D44\u6CBB\u901A\u9274\xB7\u5377\u4E00\u4E00\u4E09\xB7\u664B\u7EAA\u4E09\u5341\u4E94\u300B</strong></p><p><strong>\u5F88\u591A\u65F6\u5019\uFF0C\u4ECE\u5DC5\u5CF0\u8DCC\u843D\u4E0D\u8FC7\u662F\u4E00\u77AC\u95F4\u7684\u4E8B</strong>\u3002</p><p>\u664B\u592A\u5143\u516B\u5E74\uFF08\u516C\u5143383\u5E74\uFF09\uFF0C<code>\u4E1C\u664B</code>\u5728\u6DDD\u6C34\u4E4B\u7554\u5BF9\u9635<code>\u524D\u79E6</code>\uFF0C\u53D6\u5F97\u65F7\u53E4\u7EDD\u4ECA\u7684\u5927\u80DC\u3002\u4F46\u8BE1\u5F02\u7684\u662F\uFF0C\u5927\u6218\u53CC\u65B9\u7684\u7CBE\u795E\u6C14\u8D28\u4E0E\u80DC\u8D25\u7ED3\u5C40\u5374\u622A\u7136\u76F8\u53CD\uFF1A\u8D25\u4E86\u7684\u4E00\u65B9\u84B8\u84B8\u65E5\u4E0A\uFF0C\u80DC\u4E86\u7684\u4E00\u65B9\u66AE\u6C14\u6C89\u6C89\u3002</p><p>\u5DE8\u5927\u7684\u6218\u679C\uFF0C\u5E76\u6CA1\u6709\u7ED9\u4E1C\u664B\u5E26\u6765\u4EC0\u4E48\u5E0C\u671B\u7684\u66D9\u5149\uFF0C\u56E0\u4E3A\u5927\u80DC\u800C\u593A\u5F97\u7684\u5317\u65B9\u9886\u571F\uFF0C\u5728\u4E0D\u4E45\u4E4B\u540E\u4FBF\u9010\u6E10\u5931\u53BB\uFF1B\u671D\u5EF7\u5728\u9010\u6E10\u7684\u6C89\u6CA6\u4E2D\uFF0C\u4E5F\u7EC8\u81F3\u65E0\u836F\u53EF\u6551\u3002</p><p>\u6DDD\u6C34\u4E4B\u6218\u540E\u7684\u7B2C\u4E8C\u5341\u5E74\uFF0C<code>\u6853\u7384</code>\u903C\u664B\u5B89\u5E1D\u9000\u4F4D\uFF0C\u81EA\u7ACB\u4E3A\u5E1D\u3002\u540C\u5E74\uFF0C<code>\u5218\u88D5</code>\u5728\u4EAC\u53E3\u8D77\u5175\uFF0C\u51FB\u8D25\u6853\u7384\u3002\u4E00\u5E74\u540E\uFF0C\u6853\u7384\u88AB\u6740\uFF0C\u5218\u88D5\u8FCE\u56DE\u664B\u5B89\u5E1D\u3002\u4F46\u6B64\u65F6\uFF0C\u4E1C\u664B\u738B\u671D\u4E8B\u5B9E\u4E0A\u5DF2\u7ECF\u53EA\u662F\u5218\u88D5\u7684\u53F0\u524D\u6728\u5076\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C<strong>\u4EC5\u4EC5\u53EA\u7528\u4E86\u4E8C\u5341\u5E74\u7684\u65F6\u95F4\uFF0C\u4E1C\u664B\u5C31\u4ECE\u6DDD\u6C34\u4E4B\u6218\u7684\u5927\u80DC\u8D70\u5411\u4E86\u4E8B\u5B9E\u4E0A\u7684\u706D\u4EA1</strong>\u3002</p><p>\u6DDD\u6C34\u4E4B\u6218\u540E\uFF0C<code>\u8C22\u5B89</code>\u56E0\u529F\u52B3\u8FC7\u5927\uFF0C\u4E3A\u671D\u5EF7\u4E0D\u5BB9\uFF0C\u9009\u62E9\u4E86\u6DE1\u51FA\u671D\u653F\u3002**\u5B5D\u6B66\u5E1D<code>\u53F8\u9A6C\u66DC</code><strong>\u4E0E\u5144\u5F1F</strong>\u7405\u740A\u738B<code>\u53F8\u9A6C\u9053\u5B50</code>**\u4E8E\u662F\u8D81\u52BF\u593A\u56DE\u6743\u67C4\u2014\u2014<strong>\u6B64\u4E3E\u4E00\u6539\u4E1C\u664B\u7ACB\u56FD\u4EE5\u6765\u58EB\u65CF\u6267\u653F\u7684\u5C40\u9762\uFF0C\u91CD\u65B0\u5C06\u671D\u653F\u5927\u6743\u56DE\u5F52\u5230\u53F8\u9A6C\u6C0F\u7687\u65CF\u624B\u4E2D</strong>\u3002</p><p>\u5BF9\u4E8E\u5B5D\u6B66\u5E1D\u6765\u8BF4\uFF0C\u8FD9\u5DF2\u7ECF\u662F\u4ED6\u5728\u7687\u5E1D\u7684\u4F4D\u7F6E\u4E0A\u6240\u53D6\u5F97\u7684\u6700\u5927\u6210\u5C31\u4E86\u3002\u671F\u95F4\uFF0C\u4ED6\u4E0E\u53F8\u9A6C\u9053\u5B50\u867D\u7136\u5144\u5F1F\u95F4\u4E5F\u6709\u9F83\u9F89\uFF0C\u4F46\u9762\u5BF9\u7740\u201C\u7687\u65CFvs\u58EB\u65CF\u201D\u8FD9\u4E2A\u6839\u672C\u6027\u7684\u95EE\u9898\uFF0C\u4E24\u4EBA\u5012\u662F\u5229\u76CA\u4E00\u81F4\u3002</p><p>\u53EA\u662F\u65E0\u5948\u8FD9\u5BF9\u5144\u5F1F\u5B9E\u5728\u592A\u6781\u54C1\uFF0C\u4ED6\u4EEC\u52AA\u529B\u9760\u7740\u81EA\u5DF1\u7684\u4F5C\u5929\u4F5C\u5730\uFF0C\u6210\u529F\u5C06\u4E1C\u664B\u7684\u653F\u6CBB\u98CE\u6C14\u7834\u574F\u6B86\u5C3D\uFF0C\u6574\u4E2A\u671D\u5EF7\u5E76\u6CA1\u6709\u56E0\u4E3A\u4ED6\u4EEC\u7684\u638C\u6743\u53D8\u5F97\u66F4\u597D\uFF0C\u800C\u662F\u66F4\u7CDF\u4E86\u3002</p><p><strong>\u8FD9\u91CC\u4E0D\u59A8\u5148\u4E3E\u4E2A\u5C0F\u4F8B\u5B50</strong></p><p>\u6DDD\u6C34\u4E4B\u6218\u540E\uFF0C\u6709\u4E00\u4E2A\u53EB\u8D75\u7259\u7684\uFF0C\u662F\u4F36\u4EBA\u51FA\u8EAB\uFF1B\u53E6\u4E00\u4E2A\u53EB\u8339\u5343\u79CB\u7684\uFF0C\u539F\u6765\u662F\u4E2A\u6355\u5FEB\u3002\u4E24\u4EBA\u90FD\u60F3\u529E\u6CD5\u6253\u901A\u95E8\u8DEF\uFF0C\u642D\u4E0A\u4E86\u53F8\u9A6C\u9053\u5B50\uFF0C\u83B7\u5F97\u9AD8\u5B98\u4E4B\u4F4D\u3002</p><p>\u8D75\u7259\u5728\u5EFA\u5EB7\u57CE\u7684\u5E9C\u90B8\u4FEE\u7B51\u5F97\u5341\u5206\u5BCC\u4E3D\u5802\u7687\uFF0C\u5C71\u6C34\u4EAD\u697C\u65E0\u4E0D\u5C3D\u6709\u3002\u67D0\u6B21\uFF0C\u5B5D\u6B66\u5E1D\u53BB\u4ED6\u5BB6\u505A\u5BA2\uFF0C\u770B\u7F62\u90FD\u5BF9\u966A\u540C\u7684\u53F8\u9A6C\u9053\u5B50\u8BF4\uFF1A\u7F8E\u5219\u7F8E\u77E3\uFF0C\u5C31\u662F\u8FC7\u5206\u5962\u4F88\u4E86\u3002</p><p>\u53F8\u9A6C\u9053\u5B50\u65E0\u8A00\u4EE5\u5BF9\u3002\u5F85\u5B5D\u6B66\u5E1D\u8D70\u540E\uFF0C\u53F8\u9A6C\u9053\u5B50\u624D\u8DDF\u8D75\u7259\u8BF4\uFF0C\u5E78\u4E8F\u7687\u5E1D\u4E0D\u77E5\u9053\u4F60\u8FD9\u5C71\u662F\u7528\u4EBA\u529B\u5806\u51FA\u6765\u7684\uFF0C\u5426\u5219\u4F60\u6015\u662F\u6B7B\u671F\u5C06\u81F3\u4E86\u3002\u8D75\u7259\u5B09\u76AE\u7B11\u8138\u7684\u56DE\u7B54\u9053\uFF0C\u6709\u60A8\u5728\uFF0C\u6211\u54EA\u91CC\u6562\u6B7B\u5462\u3002</p><p>\u53F8\u9A6C\u9053\u5B50\u7528\u7684\uFF0C\u5C31\u662F\u8FD9\u4E48\u4E9B\u8D27\u8272\u3002</p><p>\u4E4B\u540E\uFF0C\u53F8\u9A6C\u9053\u5B50\u56E0\u4E3A\u559C\u6B22\u63FD\u6743\uFF0C\u9010\u6E10\u88AB\u5B5D\u6B66\u5E1D\u4E0D\u559C\u3002\u5B5D\u6B66\u5E1D\u5F00\u59CB\u91CD\u7528\u58EB\u4EBA<code>\u738B\u606D</code>\u7B49\u9632\u5907\u53F8\u9A6C\u9053\u5B50\u3002\u4F46\u53F8\u9A6C\u9053\u5B50\u5374\u4E0D\u77E5\u6536\u655B\uFF0C\u53CD\u800C\u9009\u62E9\u501A\u91CD<code>\u738B\u56FD\u5B9D</code>\u7B49\u8FDB\u884C\u5BF9\u6297\u3002</p><p><strong>\u81EA\u6B64\uFF0C\u671D\u5EF7\u91CC\u515A\u4E89\u4E0D\u65AD\uFF0C\u4E4C\u70DF\u7634\u6C14</strong>\u3002\u540C\u65F6\uFF0C\u738B\u606D\u548C\u738B\u56FD\u5B9D\u7684\u5BF9\u7ACB\uFF0C\u4E5F\u4E3A\u4EE5\u540E\u7684\u4E71\u5C40\u57CB\u4E0B\u4E86\u91CD\u5927\u4F0F\u7B14\u3002</p><p><strong>\u4E0D\u4E45\u4E4B\u540E\uFF0C\u5B5D\u6B66\u5E1D\u7A81\u7136\u6B7B\u4E86</strong>\u3002</p><p>\u67D0\u6B21\uFF0C\u5B5D\u6B66\u5E1D\u559D\u591A\u4E86\uFF0C\u8DDF\u5BA0\u7231\u7684\u5F20\u8D35\u5983\u8BF4\uFF0C\u4F60\u5E74\u7EAA\u8FD9\u4E48\u5927\u4E86\uFF0C\u6211\u5C31\u5E94\u8BE5\u5E9F\u4E86\u4F60\uFF0C\u518D\u6362\u4E2A\u5E74\u8F7B\u6F02\u4EAE\u7684\u6765\u73A9\u73A9\u3002</p><p>\u8FD9\u79CD\u8BDD\uFF0C\u5728\u4EFB\u4F55\u65F6\u4EE3\u8DDF\u4EFB\u4F55\u5973\u4EBA\u8BF4\u90FD\u662F\u7981\u5FCC\u3002\u679C\u7136\uFF0C<strong>\u6C14\u4E0D\u8FC7\u7684\u5F20\u8D35\u5983\u5C31\u5728\u5B5D\u6B66\u5E1D\u559D\u9189\u4EE5\u540E\uFF0C\u6D3E\u4E86\u4E2A\u5A62\u5973\u628A\u7687\u5E1D\u7ED9\u6342\u6B7B\u4E86</strong>\u3002</p><p>\u4E4B\u540E\u5F20\u8D35\u5983\u7528\u94B1\u7ED9\u5DE6\u53F3\u4E4B\u4EBA\u5C01\u4E86\u53E3\uFF0C\u53EA\u8BF4\u5B5D\u6B66\u5E1D\u662F\u88AB\u68A6\u9B47\u4F4F\u4E86\uFF0C\u7A81\u7136\u6B7B\u7684\u3002\u53F8\u9A6C\u9053\u5B50\u6B63\u5DF4\u4E0D\u5F97\u5B5D\u6B66\u5E1D\u6B7B\u5462\uFF0C\u4E5F\u5C31\u4E50\u5F97\u76F8\u4FE1\u4E86\u3002\u592A\u5B50<code>\u53F8\u9A6C\u5FB7\u5B97</code>\u4E8E\u662F\u5373\u4F4D\uFF0C\u662F\u4E3A\u664B\u5B89\u5E1D\u2014\u2014<strong>\u8FD9\u51E0\u4E4E\u662F\u4E2A\u667A\u969C</strong>\u3002</p><p>\u5B5D\u6B66\u5E1D\u4F5C\u4E3A\u5802\u5802\u4E00\u56FD\u4E4B\u541B\uFF0C\u5C31\u8FD9\u4E48\u4E0D\u660E\u4E0D\u767D\u7684\u88AB\u4EBA\u6740\u4E86\uFF0C\u5230\u6700\u540E\u8FDE\u4E2A\u8FFD\u7A76\u7684\u4EBA\u90FD\u6CA1\u6709\u3002</p><p>\u800C\u968F\u7740\u5B5D\u6B66\u5E1D\u53BB\u4E16\uFF0C\u738B\u606D\u4E0E\u738B\u56FD\u5B9D\u7684\u77DB\u76FE\u7EC8\u4E8E\u6FC0\u5316\u3002</p><p>\u5B5D\u6B66\u5E1D\u5728\u4E16\u65F6\uFF0C\u5F88\u6B23\u8D4F\u738B\u606D\u7684\u6B63\u76F4\u4E0E\u62C5\u5F53\uFF0C\u66FE\u7ECF\u79C1\u4E0B\u91CC\u8DDF\u4ED6\u8BF4\uFF0C\u5C06\u6765\u8981\u4EFB\u547D\u4ED6\u4E3A\u8F85\u653F\u5927\u81E3\u3002\u800C\u4E3A\u4E86\u7ED9\u738B\u606D\u5386\u7EC3\uFF0C\u5B5D\u6B66\u5E1D\u66FE\u547D\u738B\u606D\u51FA\u9547\u5730\u65B9\uFF0C\u201C\u90FD\u7763\u5156\u3001\u9752\u3001\u5180\u3001\u5E7D\u3001\u5E76\u3001\u5F90\u3001\u626C\u5DDE\u4E4B\u664B\u9675\u8BF8\u519B\u4E8B\uFF0C\u524D\u5C06\u519B\uFF0C\u5156\u3001\u9752\u4E8C\u5DDE\u523A\u53F2\u201D\u3002</p><p>\u4F46\u5982\u4ECA\u5B5D\u6B66\u5E1D\u6B7B\u5F97\u7A81\u7136\uFF0C\u738B\u606D\u6CA1\u6709\u53D7\u5230\u4EFB\u547D\u3002\u800C\u540E\uFF0C\u53F8\u9A6C\u9053\u5B50\u5927\u6743\u5728\u63E1\uFF0C\u738B\u56FD\u5B9D\u8D81\u673A\u5F97\u52BF\uFF0C\u4E24\u4EBA\u98DE\u626C\u8DCB\u6248\uFF0C\u7978\u4E71\u671D\u653F\uFF0C\u5F15\u8D77\u4E86\u738B\u606D\u7684\u6781\u5927\u4E0D\u6EE1\u3002</p><p>\u738B\u606D\u4E0E\u738B\u56FD\u5B9D\u867D\u7136\u90FD\u51FA\u81EA\u592A\u539F\u738B\u6C0F\uFF0C\u4F46\u4E92\u4E0D\u6295\u5951\uFF0C\u738B\u606D\u521A\u76F4\uFF0C\u738B\u56FD\u5B9D\u8C04\u5A9A\u3002\u4E24\u4EBA\u7684\u5BF9\u7ACB\uFF0C\u65E2\u6709\u653F\u89C1\u4E0A\u7684\u4E0D\u540C\uFF0C\u4E5F\u56E0\u4E3A\u5206\u5C5E\u4E8E\u4E0D\u540C\u9635\u8425\u7ACB\u573A\u4E0A\u7684\u539F\u56E0\u3002</p><p>\u738B\u606D\u66FE\u7ECF\u76F4\u8C0F\u53F8\u9A6C\u9053\u5B50\u738B\u56FD\u5B9D\u7684\u79CD\u79CD\u52A3\u8FF9\uFF0C\u5F15\u8D77\u4E86\u738B\u56FD\u5B9D\u7684\u5AC9\u6068\uFF0C\u79C1\u4E0B\u91CC\u51C6\u5907\u9664\u6389\u738B\u606D\u3002<strong>\u800C\u738B\u606D\u5219\u5148\u53D1\u5236\u4EBA\uFF0C\u4EE5\u8BDB\u6740\u738B\u56FD\u5B9D\u7684\u540D\u4E49\u76F4\u63A5\u8D77\u5175</strong>\u3002</p><p>\u53F8\u9A6C\u9053\u5B50\u548C\u738B\u56FD\u5B9D\u663E\u7136\u6CA1\u6709\u6599\u5230\u738B\u606D\u4F1A\u8FD9\u4E48\u751F\u731B\uFF0C\u76F4\u63A5\u5C31\u625B\u5200\u8BF4\u8BDD\u3002\u4E24\u4EBA\u867D\u7136\u5185\u5FC3\u4E0D\u6EE1\uFF0C\u53EF\u82E6\u4E8E\u6CA1\u6709\u5B9E\u529B\u5BF9\u6297\uFF0C\u53EA\u80FD\u662F\u4E00\u4E2A\u7EC8\u65E5\u60F6\u60F6\uFF0C\u4E00\u4E2A\u7126\u5934\u70C2\u989D\u3002<strong>\u6700\u7EC8\uFF0C\u53F8\u9A6C\u9053\u5B50\u51B3\u5B9A\u4E22\u5352\u4FDD\u5E05\uFF0C\u7528\u738B\u56FD\u5B9D\u7684\u4EBA\u5934\u6765\u6362\u738B\u606D\u7684\u7F62\u5175</strong>\u3002</p><p>\u738B\u606D\u5F88\u6109\u5FEB\u5730\u540C\u610F\u4E86\u8FD9\u6869\u751F\u610F\u3002</p><blockquote><p>\u8FD9\u4E5F\u53EF\u4EE5\u7B97\u662F\u4E1C\u664B\u81EA\u738B\u6566\u4EE5\u6765\u7684\u6076\u4E60\u4E86\uFF1A \u5730\u65B9\u5B9E\u529B\u6D3E\u4E00\u8A00\u4E0D\u5408\u5C31\u8D77\u5175\u5BF9\u6297\u671D\u5EF7\uFF0C\u800C\u671D\u5EF7\u5F80\u5F80\u4E5F\u6CA1\u6709\u4EC0\u4E48\u529E\u6CD5\uFF0C\u53EA\u8FFD\u6C42\u606F\u4E8B\u5B81\u4EBA\uFF0C\u5E76\u4E0D\u8FFD\u7A76\u3002</p></blockquote><p>\u4E0D\u8FC7\u53F8\u9A6C\u9053\u5B50\u548C\u738B\u606D\u90FD\u4E0D\u662F\u4E00\u822C\u7684boy\uFF0C\u53CC\u65B9\u6469\u62F3\u64E6\u638C\uFF0C\u51C6\u5907\u518D\u6218\u3002<strong>\u738B\u606D\u8054\u5408\u548C<code>\u6853\u7384</code>\u3001<code>\u6BB7\u4EF2\u582A</code>\u7B49\u4EBA\uFF0C\u53CD\u5BF9\u53F8\u9A6C\u9053\u5B50\uFF0C\u56E0\u6B64\u518D\u4E00\u6B21\u8D77\u5175</strong>\u3002\u800C\u53F8\u9A6C\u9053\u5B50\u4E5F\u60F3\u786C\u6C14\u4E00\u56DE\uFF0C\u60F3\u8981\u5F7B\u5E95\u89E3\u51B3\u6389\u738B\u606D\u8FD9\u4E2A\u5A01\u80C1\u3002</p><p>\u8FD9\u6B21\uFF0C\u53F8\u9A6C\u9053\u5B50\u51C6\u5907\u597D\u4E86\u5BF9\u7B56\uFF1A\u738B\u606D\u6240\u4EF0\u4ED7\u7684\uFF0C\u65E0\u975E\u5C31\u662F<code>\u5218\u7262\u4E4B</code>\u7684\u5317\u5E9C\u5175\u3002\u53F8\u9A6C\u9053\u5B50\u4E8E\u662F\u6D3E\u4EBA\u7B56\u53CD\u5218\u7262\u4E4B\uFF0C\u7B54\u5E94\u5728\u4E8B\u6210\u4E4B\u540E\uFF0C\u5C06\u738B\u606D\u7684\u804C\u4F4D\u5168\u90E8\u8D60\u4E0E\u5218\u7262\u4E4B\u3002</p><p>\u5218\u7262\u4E4B\u5BF9\u4E8E\u738B\u606D\u770B\u4E0D\u8D77\u4ED6\u7684\u6001\u5EA6\u4E00\u76F4\u803F\u803F\u4E8E\u6000\uFF0C\u53CC\u65B9\u56E0\u6B64\u4E00\u62CD\u5373\u5408\u3002<strong>\u4E4B\u540E\uFF0C\u5218\u7262\u4E4B\u53CD\u6C34\uFF0C\u738B\u606D\u5175\u8D25\u88AB\u6740</strong></p><p>\u6309\u4E0B\u846B\u82A6\u6D6E\u8D77\u74E2\uFF0C\u738B\u606D\u7684\u95EE\u9898\u521A\u89E3\u51B3\uFF0C<code>\u6853\u7384</code>\u53C8\u53D8\u6210\u4E86\u65B0\u95EE\u9898\u3002</p><p>\u5F53\u5E74\uFF0C<code>\u6853\u6E29</code>\u7BE1\u4F4D\u800C\u672A\u679C\uFF0C\u8FD9\u662F\u671D\u91CE\u5171\u77E5\u7684\u4E8B\u3002\u4F46\u56E0\u4E3A\u6853\u6E29\u6700\u7EC8\u6CA1\u6709\u8D70\u6210\u90A3\u4E00\u6B65\uFF0C\u7EE7\u4EFB\u7684\u8C22\u5B89\u4E3A\u4E86\u9632\u6B62\u53EF\u80FD\u5F15\u8D77\u7684\u5185\u4E71\uFF0C\u4E5F\u5C31\u6CA1\u6709\u6DF1\u7A76\u6B64\u4E8B\u3002</p><p>\u4E0D\u8FC7\u671D\u5EF7\u5BF9\u6853\u6C0F\u5BB6\u65CF\u7684\u731C\u5FCC\uFF0C\u5374\u4E00\u76F4\u672A\u51CF\u3002</p><p>\u6853\u6E29\u7684\u5AE1\u5B50\u6853\u7384\u5728\u7EE7\u627F\u6853\u6E29\u7684\u7235\u4F4D\u2014\u2014\u5357\u90E1\u516C\u65F6\uFF0C\u53EA\u6709\u516D\u5C81\u3002\u53EF\u6709\u7235\u4F4D\u5E76\u4E0D\u610F\u5473\u7740\u6709\u804C\u4F4D\uFF0C\u76F4\u5230\u4E8C\u5341\u4E09\u5C81\u65F6\uFF0C\u671D\u5EF7\u624D\u5141\u8BB8\u6853\u7384\u51FA\u4ED5\u4EFB\u804C\u3002</p><p>\u67D0\u6B21\u9152\u5BB4\u4E4B\u4E0A\uFF0C\u53F8\u9A6C\u9053\u5B50\u559D\u591A\u4E86\uFF0C\u5F53\u4F17\u95EE\u6853\u7384\uFF1A\u542C\u8BF4\u6853\u6E29\u665A\u5E74\u60F3\u8981\u505A\u8D3C\uFF0C\u8FD9\u4E8B\u4F60\u77E5\u9053\u5417\uFF1F</p><p>\u6853\u7384\u542C\u4E86\u5413\u5F97\u8DB4\u5728\u5730\u4E0A\uFF0C\u6D51\u8EAB\u5927\u6C57\u3002\u53F8\u9A6C\u9053\u5B50\u773C\u770B\u7740\u6853\u7384\u5982\u6B64\u6050\u60E7\uFF0C\u5FC3\u60C5\u8212\u7545\u3002\u6853\u7384\u5219\u4ECE\u6B64\u5F00\u59CB\u5FCC\u6068\u53F8\u9A6C\u9053\u5B50\uFF0C\u5E76\u6124\u6124\u4E8E\u81EA\u5DF1\u7684\u4E0D\u5F97\u5FD7\u3002</p><p>\u4E0D\u4E45\u540E\uFF0C<strong>\u6853\u7384\u8F9E\u5B98\u56DE\u5230\u8346\u5DDE\uFF0C\u5F00\u59CB\u79EF\u6512\u52BF\u529B</strong>\u3002\u5F53\u65F6\uFF0C\u524D\u4EFB\u8346\u5DDE\u523A\u53F2\u6070\u597D\u75C5\u6545\uFF0C\u5B5D\u6B66\u5E1D\u51C6\u5907\u6D3E\u738B\u606D\u63A5\u4EFB\u3002\u6853\u7384\u60E7\u6015\u738B\u606D\uFF0C\u4FBF\u79EF\u6781\u60F3\u529E\u6CD5\u8BA9\u5B5D\u6B66\u5E1D\u6539\u53D8\u4EFB\u547D\u3002</p><p>\u6700\u540E\u7528\u5230\u7684\u529E\u6CD5\u5F88\u7B80\u5355\uFF1A\u8D3F\u8D42\uFF0C\u8D3F\u8D42\u5B5D\u6B66\u5E1D\u5BA0\u4FE1\u7684\u5C3C\u59D1\uFF0C<strong>\u5C3C\u59D1</strong>\uFF01</p><p>\u6CA1\u8FC7\u591A\u4E45\uFF0C\u88AB\u8BA4\u4E3A\u662F\u201C\u5F31\u624D\u201D\u7684<code>\u6BB7\u4EF2\u582A</code>\u4E0A\u4EFB\uFF0C\u6210\u4E3A\u65B0\u7684\u8346\u5DDE\u523A\u53F2\u3002</p><p>\u6853\u6C0F\u5728\u8346\u5DDE\u5DF2\u7ECF\u76D8\u8E1E\u591A\u5E74\uFF0C\u8F6F\u5F31\u7684\u6BB7\u4EF2\u582A\u6839\u672C\u5C31\u65E0\u529B\u538B\u5236\u6853\u7384\uFF0C\u53CD\u800C\u8FD8\u53D7\u5230\u6853\u7384\u9A71\u4F7F\u2014\u2014\u5305\u62EC\u4E0A\u6587\u63D0\u5230\u7684\u4E0E\u738B\u606D\u8054\u5408\u8D77\u5175\uFF0C\u53CD\u5BF9\u53F8\u9A6C\u9053\u5B50\u4E00\u4E8B\uFF0C\u6BB7\u4EF2\u582A\u4E5F\u662F\u5728\u6853\u7384\u7684\u7AED\u529B\u6002\u607F\u4E4B\u4E0B\u624D\u52C9\u5F3A\u540C\u610F\u7684\u3002</p><p>\u800C\u738B\u606D\u7684\u7B2C\u4E8C\u6B21\u8D77\u5175\u867D\u7136\u4EE5\u5931\u8D25\u544A\u7EC8\uFF0C\u6853\u7384\u5728\u5176\u4E2D\u5374\u635E\u4E86\u4E0D\u5C11\u597D\u5904\u3002\u4ED6\u501F\u7740\u671D\u5EF7\u7684\u4EFB\u547D\uFF0C\u4E00\u756A\u5185\u6597\u4E0B\u6765\uFF0C\u6210\u529F\u5730\u9664\u6389\u4E86\u6BB7\u4EF2\u582A\uFF0C\u9738\u5360\u8346\u5DDE\u3002</p><p><strong>\u81F3\u4E8E\u671D\u5EF7\uFF0C\u9762\u5BF9\u7740\u5982\u6B64\u6FC0\u70C8\u7684\u5730\u65B9\u8BF8\u4FAF\u6DF7\u6218\uFF0C\u7ADF\u7136\u6CA1\u6709\u8FFD\u7A76\uFF0C\u53CD\u800C\u662F\u63A5\u53D7\u4E86\u8FD9\u4E2A\u4E8B\u5B9E</strong>\u3002</p><p>\u5230\u8FD9\u91CC\uFF0C\u4E00\u4E2A\u5F88\u5947\u602A\u7684\u903B\u8F91\u602A\u5708\u5C31\u51FA\u73B0\u4E86\uFF1A\u5982\u679C\u671D\u5EF7\u771F\u7684\u8BA4\u4E3A\u6853\u7384\u662F\u4E2A\u5A01\u80C1\uFF0C\u90A3\u4E48\u5C31\u5E94\u8BE5\u5BF9\u4ED6\u7684\u884C\u52A8\u91CD\u70B9\u76D1\u63A7\uFF0C\u4E25\u9632\u6B7B\u5B88\uFF1B\u5982\u679C\u671D\u5EF7\u8FD8\u613F\u610F\u63A5\u7EB3\u6853\u7384\uFF0C\u5219\u5E94\u8BE5\u6350\u5F03\u6210\u89C1\uFF0C\u9762\u5411\u672A\u6765\u3002</p><p><strong>\u4F46\u6211\u4EEC\u7EB5\u89C2\u53F8\u9A6C\u9053\u5B50\u7684\u9A9A\u64CD\u4F5C\uFF0C\u5374\u4F1A\u53D1\u73B0\u4ED6\u7684\u903B\u8F91\u662F\u98D8\u5FFD\u7684</strong>\uFF1A\u4E00\u65B9\u9762\uFF0C\u4ED6\u523A\u6FC0\u6853\u7384\uFF0C\u52A0\u6DF1\u4EC7\u6068\uFF1B\u53E6\u4E00\u65B9\u9762\uFF0C\u5374\u53C8\u5BF9\u6853\u7384\u80CC\u5730\u91CC\u57F9\u690D\u52BF\u529B\u7684\u884C\u4E3A\u4E0D\u7BA1\u4E0D\u95EE\uFF0C\u653E\u4EFB\u81EA\u5982\uFF0C\u751A\u81F3\u662F\u521B\u9020\u6761\u4EF6\u7ED9\u6853\u7384\u5236\u9020\u673A\u4F1A\u3002</p><p>\u7136\u540E\uFF0C\u53F8\u9A6C\u9053\u5B50\u53C8\u6709\u4E86\u65B0\u7684\u9EBB\u70E6\uFF1A<strong>\u4ED6\u88AB\u4EBA\u593A\u6743\u4E86</strong>\u3002</p><p>\u800C\u4E14\u8FD9\u4E2A\u4EBA\u8FD8\u5C31\u662F\u4ED6\u81EA\u5DF1\u7684\u513F\u5B50\uFF0C<code>\u53F8\u9A6C\u5143\u663E</code>\u3002</p><p>\u65E9\u4E9B\u5E74\uFF0C\u8FD8\u5728\u738B\u606D\u6CA1\u6709\u8D77\u5175\u7684\u65F6\u5019\uFF0C\u53F8\u9A6C\u5143\u663E\u5C31\u8DDF\u53F8\u9A6C\u9053\u5B50\u8BF4\uFF1A\u738B\u606D\u548C\u6BB7\u4EF2\u582A\u9760\u4E0D\u4F4F\uFF0C\u5E94\u8BE5\u6709\u6240\u9632\u5907\u3002\u53F8\u9A6C\u9053\u5B50\u5BF9\u513F\u5B50\u7684\u89C1\u8BC6\u5F88\u662F\u6B23\u8D4F\uFF0C\u4E13\u95E8\u7ED9\u4ED6\u52A0\u4E86\u5B98\uFF0C\u914D\u5907\u4E86\u5B98\u5458\u8F85\u4F50\uFF0C\u4E00\u526F\u8981\u57F9\u517B\u793E\u4F1A\u4E3B\u4E49\u63A5\u73ED\u4EBA\u7684\u67B6\u52BF\u3002</p><p>\u53EF\u8C01\u77E5\u9053\uFF0C\u8FD9\u4F4D\u63A5\u73ED\u4EBA\u4E0D\u60F3\u7B49\u90A3\u4E48\u4E45\u3002</p><p>\u738B\u606D\u4E24\u6B21\u8D77\u5175\uFF0C\u53F8\u9A6C\u5143\u663E\u90FD\u4EB2\u8D74\u524D\u7EBF\uFF0C\u6781\u6709\u80C6\u9B44\u3002\u8FD9\u8BA9\u53F8\u9A6C\u9053\u5B50\u8D8A\u770B\u8D8A\u559C\u6B22\uFF0C\u4FBF\u9010\u6E10\u8BA9\u53F8\u9A6C\u5143\u663E\u638C\u63E1\u4E86\u5EFA\u5EB7\u57CE\u7684\u519B\u4E8B\u3002\u7ED3\u679C\u513F\u5B50\u7FC5\u8180\u786C\u4E86\u4E4B\u540E\uFF0C\u5148\u627E\u8001\u5B50\u5F00\u5200\u3002</p><p>\u53F8\u9A6C\u9053\u5B50\u559C\u6B22\u559D\u9152\uFF0C\u65E0\u65E5\u4E0D\u9189\u3002\u67D0\u6B21\uFF0C\u8D81\u7740\u4ED6\u53C8\u9189\u9152\uFF0C<strong>\u53F8\u9A6C\u5143\u663E\u64CD\u7EB5\u671D\u5EF7\u5927\u81E3\uFF0C\u628A\u53F8\u9A6C\u9053\u5B50\u7ED9\u514D\u4E86\u804C\uFF0C\u7136\u540E\u628A\u8FD9\u4E9B\u804C\u4F4D\u52A0\u5728\u81EA\u5DF1\u8EAB\u4E0A</strong>\u3002\u53F8\u9A6C\u9053\u5B50\u9192\u4E86\u4EE5\u540E\uFF0C\u52C3\u7136\u5927\u6012\uFF0C\u4F46\u6728\u5DF2\u6210\u821F\uFF0C\u65E0\u53EF\u5948\u4F55\u3002</p><p>\u5F53\u65F6\uFF0C\u4E24\u4EBA\u540C\u65F6\u90FD\u4EFB\u804C\u201C\u5F55\u5C1A\u4E66\u4E8B\u201D\uFF0C\u65F6\u4EBA\u628A\u53F8\u9A6C\u9053\u5B50\u7684\u5E9C\u90B8\u79F0\u4F5C\u201C\u4E1C\u5F55\u201D\uFF0C\u53F8\u9A6C\u5143\u663E\u7684\u662F\u201C\u897F\u5F55\u201D\u3002\u4E00\u65F6\u4E4B\u95F4\uFF0C\u4E1C\u5F55\u95E8\u53EF\u7F57\u96C0\uFF0C\u897F\u5F55\u8F66\u6C34\u9A6C\u9F99\u3002</p><p>\u53F8\u9A6C\u5143\u663E\u672C\u5C31\u5E74\u8F7B\uFF0C\u53C8\u6CA1\u6709\u4EC0\u4E48\u826F\u5E08\u76CA\u53CB\u8F85\u4F50\uFF0C\u8EAB\u8FB9\u591A\u662F\u5C0F\u4EBA\u8FDB\u8C17\uFF0C\u52A0\u4E4B\u81EA\u5DF1\u53C8\u7231\u8D22\uFF0C\u671D\u653F\u88AB\u641E\u5F97\u66F4\u52A0\u4E4C\u70DF\u7634\u6C14\u3002</p><p><strong>\u800C\u6240\u8C13\u7684\u671D\u5EF7\uFF0C\u6B64\u65F6\u5176\u5B9E\u5DF2\u7ECF\u76F8\u5F53\u7A98\u8FEB</strong>\u3002\u81EA\u738B\u606D\u8D77\u5175\u4EE5\u6765\uFF0C\u626C\u5DDE\u4E0A\u6E38\u7684\u8346\u5DDE\u548C\u6C5F\u5DDE\u5DF2\u7ECF\u4E0D\u518D\u542C\u4ECE\u671D\u5EF7\u53F7\u4EE4\u3002\u800C\u626C\u5DDE\u4EE5\u5317\uFF0C\u5219\u662F\u5218\u7262\u4E4B\u7684\u5730\u76D8\u3002</p><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u6B64\u65F6\u671D\u5EF7\u771F\u6B63\u80FD\u63A7\u5236\u7684\uFF0C\u53EA\u6709\u5EFA\u5EB7\u548C\u4E09\u5434\u5730\u533A\u4E86\u3002\u53EF\u7531\u4E8E\u5B98\u573A\u98CE\u6C14\u5947\u5DEE\uFF0C\u5730\u65B9\u6A2A\u5F81\u66B4\u655B\uFF0C\u767E\u59D3\u6C11\u4E0D\u804A\u751F\uFF0C\u7EC8\u4E8E\u7206\u53D1\u4E86\u5B59\u6069\u7684\u53DB\u4E71\u3002</p><p><strong>\u800C\u5F53\u671D\u5EF7\u5185\u5916\u706B\u8B66\u56DB\u8D77\uFF0C\u53EA\u6709\u5BF9\u6853\u7384\u6765\u8BF4\uFF0C\u8FD9\u4E9B\u610F\u5473\u7740\u5929\u8D50\u826F\u673A</strong>\u3002</p><p>\u5982\u4ECA\u7684\u6853\u7384\uFF0C\u6240\u5360\u4F18\u52BF\u5DF2\u7ECF\u8FDC\u8FDC\u8D85\u8FC7\u4ED6\u7236\u4EB2\u5F53\u5E74\u7684\u60C5\u5F62\uFF1A\u671D\u653F\u4E0A\uFF0C\u53F8\u9A6C\u9053\u5B50\u4E0E\u53F8\u9A6C\u5143\u663E\u591A\u5E74\u5012\u884C\u9006\u65BD\uFF0C\u671D\u653F\u5DF2\u7ECF\u7CDC\u70C2\u4E0D\u582A\uFF1B\u66FE\u7ECF\u7275\u5236\u6853\u6E29\u7684\u58EB\u65CF\u529B\u91CF\uFF0C\u8FD9\u4E9B\u5E74\u4E5F\u88AB\u53F8\u9A6C\u9053\u5B50\u7236\u5B50\u7FE6\u9664\u6B86\u5C3D\uFF1B\u671D\u5EF7\u672C\u8EAB\u5B9E\u529B\u7FB8\u5F31\uFF0C\u5218\u7262\u4E4B\u53C8\u5DF2\u88AB\u6853\u7384\u6536\u4E70\uFF0C\u518D\u52A0\u4E0A\u5B59\u6069\u7684\u53DB\u4E71\u6A2A\u626B\u4E09\u5434\uFF0C\u671D\u5EF7\u7684\u8D22\u8D4B\u518D\u65E0\u4F9D\u4ED7\u3002</p><p>\u6853\u7384\u4E8E\u662F\u4EE5\u5E73\u5B9A\u5B59\u6069\u53DB\u4E71\u4E3A\u540D\u51FA\u5175\u3002\u5728\u7ECF\u8FC7\u4E86\u51E0\u573A\u89C4\u6A21\u4E0D\u5927\u7684\u6218\u4E89\u4EE5\u540E\uFF0C\u6853\u7384\u987A\u5229\u8FDB\u5165\u5EFA\u5EB7\u57CE\uFF0C\u6740\u6389\u4E86\u53F8\u9A6C\u9053\u5B50\u7236\u5B50\u3002</p><p>\u4E0D\u4E45\u540E\uFF0C<strong>\u6853\u7384\u903C\u8FEB\u664B\u5B89\u5E1D\uFF0C\u903C\u664B\u5B89\u5E1D\u7985\u4F4D\u4E8E\u4ED6\uFF0C\u7ACB\u56FD\u53F7\u4E3A<code>\u695A</code></strong>\u3002</p>',62),n=[s];function c(d,g){return o(),t("div",null,n)}const l=p(r,[["render",c],["__file","41.html.vue"]]);export{l as default};
