import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as e,c as t,f as a}from"./app.1218f0f6.js";const c={},o=a('<h1 id="\u7B2C42\u96C6-\u6960\u6960\u5BF9\u9A86\u58EB\u5BBE\u6539\u79F0\u547C" tabindex="-1"><a class="header-anchor" href="#\u7B2C42\u96C6-\u6960\u6960\u5BF9\u9A86\u58EB\u5BBE\u6539\u79F0\u547C" aria-hidden="true">#</a> \u7B2C42\u96C6\uFF1A\u6960\u6960\u5BF9\u9A86\u58EB\u5BBE\u6539\u79F0\u547C</h1><p>\u5468\u84C9\u548C\u8521\u6653\u5149\u53BB\u9001\u73A5\u73A5\u5750\u8F66\uFF0C\u73A5\u73A5\u6325\u624B\u5411\u4E8C\u4EBA\u9053\u522B\uFF0C\u5468\u84C9\u4E0D\u6EE1\u73A5\u73A5\u5BF9\u8521\u6653\u5149\u7684\u79F0\u547C\uFF0C\u4ED6\u4EEC\u5DF2\u7ECF\u7ED3\u5A5A\uFF0C\u73A5\u73A5\u8FD8\u662F\u53EB\u53D4\u53D4\uFF0C\u4F46\u8521\u6653\u5149\u5374\u662F\u6EE1\u8138\u7684\u5BA0\u6EBA\uFF0C\u4ED6\u4E1D\u6BEB\u4E0D\u5728\u4E4E\u79F0\u547C\uFF0C\u4E5F\u7406\u89E3\u5B69\u5B50\u7684\u5FC3\u7406\u3002</p><p>\u73A5\u73A5\u521A\u6765\u6E05\u534E\uFF0C\u6960\u6960\u5E26\u7740\u5979\u56DB\u5904\u770B\u4E86\u4E00\u4E0B\uFF0C\u4E5F\u544A\u8BC9\u73A5\u73A5\u4ED6\u5DF2\u7ECF\u8003\u4E0A\u4E86\u7F8E\u56FD\u7684\u5B66\u6821\uFF0C\u8FD8\u662F\u5168\u90E8\u5956\u5B66\u91D1\u3002\u73A5\u73A5\u5F88\u96BE\u8FC7\u81EA\u5DF1\u521A\u6765\u6960\u6960\u5C31\u8981\u79BB\u5F00\uFF0C\u6960\u6960\u5374\u544A\u8BC9\u73A5\u73A5\u4ED6\u5E0C\u671B\u80FD\u65E9\u70B9\u53BB\u7F8E\u56FD\uFF0C\u65E9\u70B9\u5B66\u6210\u5F52\u6765\uFF0C\u4ED6\u5E0C\u671B\u80FD\u5C3D\u5FEB\u5C06\u90D1\u5A1F\u548C\u5468\u79C9\u6606\u5E26\u51FA\u5149\u5B57\u7247\u3002\u73A5\u73A5\u5F88\u8D5E\u540C\uFF0C\u53EA\u8981\u80FD\u79BB\u5F00\u5149\u5B57\u7247\uFF0C\u4E00\u5207\u7684\u969C\u788D\u90FD\u4E0D\u662F\u969C\u788D\u3002\u6960\u6960\u51FA\u56FD\u4E4B\u524D\u8FD8\u7279\u610F\u56DE\u53BB\u770B\u4E86\u90D1\u5A1F\u548C\u5468\u79C9\u6606\uFF0C\u90D1\u5A1F\u60F3\u8981\u7ED9\u6960\u6960\u5305\u997A\u5B50\uFF0C\u5468\u79C9\u6606\u5E26\u7740\u6960\u6960\u53BB\u996D\u5E97\u5E86\u795D\u3002</p><p>\u5B59\u5C0F\u5B81\u8DDF\u4E86\u4E00\u4E2A\u5927\u6B3E\uFF0C\u4F46\u662F\u5979\u4E0D\u77E5\u9053\u5927\u6B3E\u662F\u7ED3\u5A5A\u4E86\u7684\u4EBA\uFF0C\u5927\u6B3E\u4E0D\u5728\u5BB6\u7684\u65F6\u5019\u4ED6\u8001\u5A46\u5E26\u7740\u4EBA\u6253\u4E0A\u95E8\uFF0C\u5C06\u5B59\u5C0F\u5B81\u4E22\u51FA\u53BB\uFF0C\u4E00\u4EF6\u6362\u6D17\u8863\u670D\u90FD\u6CA1\u6709\u7ED9\u62FF\u4E0A\u3002\u5B59\u5C0F\u5B81\u5199\u4FE1\u7ED9\u4E8E\u8679\u8BF4\u4E86\u524D\u56E0\u540E\u679C\uFF0C\u4E8E\u8679\u62C5\u5FC3\u5B59\u5C0F\u5B81\u51FA\u4E8B\u8D76\u7D27\u627E\u4E86\u8D76\u8D85\uFF0C\u800C\u4E54\u6625\u71D5\u4E5F\u770B\u5230\u8FD9\u5C01\u4FE1\uFF0C\u89C9\u5F97\u8FD9\u662F\u5B59\u5C0F\u5B81\u8981\u7559\u4E0B\u9057\u8A00\uFF0C\u5426\u5219\u4E5F\u4E0D\u4F1A\u8FDE\u5730\u5740\u90FD\u6CA1\u6709\u3002\u4E54\u6625\u71D5\u5BB3\u6015\u725B\u725B\u4E5F\u51FA\u4E8B\uFF0C\u8DD1\u53BB\u627E\u9F9A\u7EF4\u5219\u5E2E\u5FD9\u5BFB\u627E\uFF0C\u9F9A\u7EF4\u5219\u8868\u793A\u6709\u4E9B\u96BE\u5EA6\uFF0C\u6BD5\u7ADF\u8FD8\u4E0D\u77E5\u9053\u725B\u725B\u53BB\u4E86\u54EA\u91CC\u3002\u4E54\u6625\u71D5\u903C\u95EE\u9F9A\u7EF4\u5219\u7A76\u7ADF\u8981\u591A\u5C11\u94B1\u624D\u80AF\u5E2E\u5FD9\uFF0C\u9F9A\u7EF4\u5219\u607C\u6012\u4E54\u6625\u71D5\u7684\u53E3\u4E0D\u62E9\u8A00\u3002</p><p>\u4E54\u6625\u71D5\u75C5\u6025\u4E71\u6295\u533B\u7684\u8DD1\u53BB\u627E\u5468\u79C9\u4E49\uFF0C\u6070\u597D\u9F9A\u7EF4\u5219\u5728\u90A3\u91CC\uFF0C\u4ED6\u529D\u8BF4\u4E54\u6625\u71D5\u56DE\u53BB\uFF0C\u4E54\u6625\u71D5\u975E\u4F46\u4E0D\u56DE\u53BB\uFF0C\u53CD\u800C\u63D0\u9192\u9F9A\u7EF4\u5219\u4E0D\u8981\u5FD8\u4E86\u4ED6\u8FD9\u4E2A\u5C40\u957F\u662F\u600E\u4E48\u5F53\u4E0A\u7684\u3002\u9F9A\u7EF4\u5219\u751F\u6C14\u547D\u4EBA\u5C06\u4E54\u6625\u71D5\u5E26\u53BB\u4E86\u516C\u5B89\u5C40\u5173\u8D77\u6765\u3002\u4E54\u6625\u71D5\u88AB\u5173\u8D77\u6765\u4E4B\u540E\u4E5F\u51B7\u9759\u4E0B\u6765\uFF0C\u4E3B\u52A8\u5411\u9F9A\u7EF4\u5219\u6C42\u9976\uFF0C\u8BF7\u6C42\u5B88\u536B\u7684\u4EBA\u544A\u8BC9\u9F9A\u7EF4\u5219\u5979\u77E5\u9053\u9519\u4E86\u3002\u9F9A\u7EF4\u5219\u4E0D\u77E5\u9053\u5982\u4F55\u5904\u7406\u59A5\u5F53\uFF0C\u53EA\u597D\u6253\u7535\u8BDD\u7ED9\u4E86\u5468\u79C9\u6606\uFF0C\u8BE2\u95EE\u5468\u79C9\u4E49\u4E8B\u60C5\u662F\u5426\u8C08\u5B8C\u4E86\u3002</p><p>\u6B64\u65F6\uFF0C\u9A86\u58EB\u5BBE\u6B63\u5728\u5305\u95F4\u91CC\u548C\u5468\u79C9\u4E49\u8C08\u4E8B\u60C5\uFF0C\u4ED6\u60F3\u8981\u56DE\u6765\u6295\u8D44\uFF0C\u76EE\u7684\u5F88\u660E\u786E\u5C31\u662F\u60F3\u8981\u8863\u9526\u8FD8\u4E61\uFF0C\u4F46\u662F\u4ED6\u7684\u94B1\u4E5F\u4E0D\u662F\u5927\u98CE\u522E\u6765\u7684\u3002\u9A86\u58EB\u5BBE\u5BB3\u6015\u54EA\u4E00\u5929\u5468\u79C9\u4E49\u8C03\u8D70\u4E86\u4E8B\u60C5\u6CA1\u6709\u4EBA\u8D1F\u8D23\u3002\u5468\u79C9\u4E49\u4FDD\u8BC1\u4ED6\u4E00\u5B9A\u4F1A\u5BF9\u5316\u5DE5\u5382\u8D1F\u8D23\u5230\u5E95\uFF0C\u800C\u4FDD\u8BC1\u7684\u8D44\u672C\u5C31\u662F\u6765\u81EA\u4E8E\u5CB3\u6BCD\u5BF9\u5DE5\u4F5C\u7684\u652F\u6301\uFF0C\u8FD9\u8BA9\u9A86\u58EB\u5BBE\u4E5F\u589E\u52A0\u4E86\u4FE1\u5FC3\u3002</p><p>\u6B21\u65E5\u4E00\u65E9\uFF0C\u4E54\u6625\u71D5\u624D\u88AB\u653E\u56DE\u53BB\uFF0C\u66F9\u5FB7\u5B9D\u770B\u89C1\u53D7\u4E86\u59D4\u5C48\u7684\u4E54\u6625\u71D5\u5F88\u5FC3\u75BC\uFF0C\u7ACB\u523B\u5C31\u8981\u51B2\u51FA\u53BB\u627E\u9F9A\u7EF4\u5219\uFF0C\u4F46\u88AB\u4E54\u6625\u71D5\u6B7B\u6B7B\u62B1\u7740\u3002\u7ECF\u8FC7\u4E86\u4E00\u591C\u7262\u623F\u4E4B\u540E\uFF0C\u4E54\u6625\u71D5\u5FC3\u751F\u60E7\u610F\u3002</p><p>\u8D76\u8D85\u7ECF\u8FC7\u4E86\u4E00\u591C\u4E0D\u7720\u4E4B\u540E\uFF0C\u4ED6\u91CD\u65B0\u5C06\u6495\u6BC1\u7684\u4FE1\u7C98\u8D34\u597D\uFF0C\u5B59\u5C0F\u5B81\u4E0D\u540E\u6094\u901A\u8FC7\u5979\u7684\u52AA\u529B\u6539\u5584\u4E86\u54E5\u54E5\u548C\u7236\u6BCD\u7684\u751F\u6D3B\uFF0C\u4F46\u662F\u5374\u540E\u6094\u8D70\u9519\u4E86\u8DEF\u3002\u4E8E\u8679\u63D0\u51FA\u53BB\u627E\u5B59\u5C0F\u5B81\uFF0C\u8D76\u8D85\u8BA4\u4E3A\u5B59\u5C0F\u5B81\u4E0D\u7559\u4E0B\u5730\u5740\u5C31\u662F\u4E0D\u60F3\u8BA9\u4ED6\u4EEC\u627E\u5230\uFF0C\u773C\u4E0B\u4ED6\u4EEC\u53EA\u6709\u8FC7\u597D\u81EA\u5DF1\u7684\u65E5\u5B50\uFF0C\u7B49\u7740\u5B59\u5C0F\u5B81\u56DE\u4FE1\u3002</p><p>\u4E54\u6625\u71D5\u5FC3\u91CC\u4E0D\u5B89\u5230\u5904\u8D34\u5BFB\u4EBA\u542F\u4E8B\u627E\u725B\u725B\uFF0C\u9F9A\u7EF4\u5219\u7279\u610F\u6765\u6D17\u6D74\u4E2D\u5FC3\u627E\u4E54\u6625\u71D5\uFF0C\u4F46\u662F\u5E76\u672A\u627E\u5230\u5979\u3002\u4E54\u6625\u71D5\u63A5\u5230\u516C\u5B89\u5C40\u7535\u8BDD\u725B\u725B\u53BB\u4E86\u5317\u4EAC\uFF0C\u7740\u6025\u7684\u5728\u5BB6\u91CC\u7B49\u7740\uFF0C\u770B\u5230\u725B\u725B\u88AB\u9001\u56DE\u6765\u7070\u5934\u571F\u8138\u7684\u6837\u5B50\uFF0C\u4E54\u6625\u71D5\u5FC3\u75BC\u62B1\u7740\u725B\u725B\u54ED\u3002</p><p>\u770B\u7740\u725B\u725B\u72FC\u541E\u864E\u54BD\u5403\u996D\u7684\u6837\u5B50\uFF0C\u66F9\u5FB7\u5B9D\u548C\u4E54\u6625\u71D5\u90FD\u5F88\u5FC3\u75BC\uFF0C\u4E54\u6625\u71D5\u4FDD\u8BC1\u4EE5\u540E\u518D\u4E5F\u4E0D\u8BF4\u4ED6\u4E86\uFF0C\u725B\u725B\u60F3\u8981\u5728\u5DE5\u5730\u5E72\u6D3B\u51FA\u4EBA\u5934\u5730\uFF0C\u4F46\u662F\u6BCF\u5929\u4F4F\u5728\u51B0\u51B7\u7684\u5730\u65B9\uFF0C\u800C\u4E14\u5403\u996D\u90FD\u6CA1\u6709\u8089\uFF0C\u4E54\u6625\u71D5\u66F4\u5FC3\u75BC\u3002\u4E54\u6625\u71D5\u4E3A\u4E86\u7ED9\u725B\u725B\u627E\u4E00\u4E2A\u597D\u5DE5\u4F5C\u53C8\u6765\u627E\u79E6\u5B88\u5FB7\uFF0C\u5979\u4E5F\u4F1A\u53BB\u8D1F\u8D23\u5206\u516C\u53F8\uFF0C\u4F46\u4E54\u6625\u71D5\u5E0C\u671B\u725B\u725B\u80FD\u53BB\u4E00\u4E2A\u56FD\u8425\u5355\u4F4D\u3002</p><p>\u5728\u90A3\u4E2A\u53D8\u9769\u7684\u5E74\u4EE3\uFF0C\u5468\u79C9\u4E49\u548C\u9A86\u58EB\u5BBE\u7B7E\u4E0B\u5408\u540C\u662F\u88AB\u5DE5\u4EBA\u4E0D\u7406\u89E3\u7684\uFF0C\u90FD\u8BA4\u4E3A\u5468\u79C9\u4E49\u662F\u52FE\u7ED3\u8D44\u672C\u5BB6\uFF0C\u4E3A\u4E86\u5F80\u4E0A\u722C\u4E0D\u62E9\u624B\u6BB5\u3002\u5382\u533A\u91CC\u90FD\u8D34\u6EE1\u4E86\u5BF9\u5468\u79C9\u4E49\u7684\u653B\u51FB\uFF0C\u9A86\u58EB\u5BBE\u5F97\u77E5\u540E\u4F69\u670D\u5468\u79C9\u4E49\u662F\u4E2A\u597D\u9886\u5BFC\uFF0C\u5728\u5409\u6625\u90A3\u4E2A\u5C0F\u5730\u65B9\u65BD\u5C55\u4E0D\u51FA\u6765\u3002\u9A86\u58EB\u5BBE\u5C31\u51B2\u7740\u5468\u79C9\u4E49\u8FD9\u4E2A\u4EBA\u4E5F\u51B3\u5B9A\u6295\u8D44\uFF0C\u800C\u4E14\u8BA9\u5C5E\u4E0B\u8D54\u70B9\u94B1\u4E5F\u4E0D\u8981\u7D27\uFF0C\u5408\u540C\u4E00\u5B9A\u8981\u4FC3\u6210\u3002</p><p>\u9A86\u58EB\u5BBE\u7279\u610F\u8D76\u5230\u5317\u4EAC\u9001\u6960\u6960\uFF0C\u8FD8\u7ED9\u6960\u6960\u51C6\u5907\u4E86\u4E00\u4E9B\u73B0\u91D1\uFF0C\u8BA9\u6960\u6960\u53BB\u4E86\u7F8E\u56FD\u5148\u8003\u9A7E\u8BC1\u7136\u540E\u4E70\u8F66\uFF0C\u56E0\u4E3A\u7F8E\u56FD\u6CA1\u6709\u8F66\u5BF8\u6B65\u96BE\u884C\u3002\u9A86\u58EB\u5BBE\u558B\u558B\u4E0D\u4F11\u8BB2\u8FF0\u4E86\u5F88\u591A\u7F8E\u56FD\u9700\u8981\u6CE8\u610F\u7684\u4E8B\u9879\uFF0C\u7F8E\u56FD\u8D85\u5E02\u90FD\u53EF\u4EE5\u4E70Q\uFF0C\u9047\u5230\u4E8B\u60C5\u4E0D\u8981\u901E\u5F3A\uFF0C\u56E0\u4E3A\u4E0D\u786E\u5B9A\u5BF9\u65B9\u624B\u91CC\u662F\u5426\u6709\u67AA\u3002\u9A86\u58EB\u5BBE\u4E8B\u65E0\u5DE8\u7EC6\u7684\u4EA4\u4EE3\u7740\uFF0C\u6960\u6960\u5FC3\u751F\u611F\u52A8\uFF0C\u8FDB\u5165\u5B89\u68C0\u7684\u65F6\u5019\u56DE\u5934\u53EB\u4E86\u4E00\u58F0\u7238\u7238\uFF0C\u9A86\u58EB\u5BBE\u60CA\u8BB6\u4E14\u611F\u52A8\u3002</p><p>1993\u5E74\uFF0C\u6960\u6960\u5199\u4E86\u4FE1\u7ED9\u5468\u79C9\u6606\uFF0C\u8BB2\u8FF0\u81EA\u5DF1\u5728\u7F8E\u56FD\u7684\u751F\u6D3B\u8D77\u5C45\uFF0C\u5468\u79C9\u6606\u7684\u5634\u89D2\u4E0A\u626C\u3002</p><p>\u8521\u6653\u5149\u5FD9\u7740\u505A\u996D\uFF0C\u5468\u84C9\u60E6\u8BB0\u7740\u73A5\u73A5\u662F\u5426\u5FEB\u56DE\u6765\u4E86\uFF0C\u6253\u7B97\u51FA\u95E8\u8FCE\u8FCE\u3002</p>',14),r=[o];function _(n,s){return e(),t("div",null,r)}const h=p(c,[["render",_],["__file","42.html.vue"]]);export{h as default};
